let lives = [{"author": {"avatar": {"large": "", "normal": "", "small": ""}, "introduction": "", "nickname": "Ranjit Devraj", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "Ranjit Devraj", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [], "content": "<p><span style=\"font-size:14px\"><span style=\"color:#808080\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<h2><strong>\u592a\u957f\u4e0d\u770b\uff1a</strong></h2>\r\n\r\n<ol>\r\n\t<li>\u81f4\u75c5\u5fae\u751f\u7269\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u4e0d\u65ad\u589e\u5f3a\uff0c\u964d\u4f4e\u4e86\u91cd\u8981\u836f\u7269\u7684\u7597\u6548\u3002</li>\r\n\t<li>\u5168\u7403\u7814\u7a76\u8868\u660e\uff0c\u4e9a\u6d32\u5728\u8fc7\u53bb20\u5e74\u95f4\uff0c\u8089\u7c7b\u6d88\u8d39\u589e\u957f\u4e8668%\u3002</li>\r\n\t<li>\u4f5c\u8005\u6307\u51fa\uff0c\u755c\u7267\u4e1a\u5fc5\u987b\u7981\u7528\u901f\u6548\u6297\u751f\u7d20\u3002</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>\u8010\u836f\u5371\u673a\u6301\u7eed\u52a0\u91cd</strong></h2>\r\n\r\n<p>2019\u5e749\u670820\u65e5\uff0c\u300a\u79d1\u5b66\u300b\u6742\u5fd7\u4e0a\u53d1\u8868\u4e86\u4e00\u9879\u7814\u7a76\uff0c\u8bc4\u4f30\u4e86\u53d1\u5c55\u4e2d\u56fd\u5bb6\u4e2d\u8010\u836f\u75c5\u539f\u4f53\u7684\u53d1\u5c55\u5386\u7a0b\u3002\u7814\u7a76\u53d1\u73b0\uff0c\u5728<strong>\u4e2d\u56fd\u548c\u5370\u5ea6\u7684\u90e8\u5206\u5730\u533a\uff0c\u52a8\u7269\u7684\u591a\u91cd\u8010\u836f\u6027\u5c45\u9ad8\u4e0d\u4e0b</strong>\u3002\u80af\u5c3c\u4e9a\u548c\u5df4\u897f\u7684\u90e8\u5206\u5730\u533a\u4e5f\u6709\u540e\u6765\u5c45\u4e0a\u7684\u8d8b\u52bf\u3002</p>\r\n\r\n<p>\u8fd9\u9879\u7814\u7a76\u7684\u5171\u540c\u4f5c\u8005\u62c9\u66fc&middot;\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\uff08Ramanan Laxminarayan\uff09\uff0c\u662f\u666e\u6797\u65af\u987f\u5927\u5b66\u7684\u9ad8\u7ea7\u7814\u7a76\u5458\uff0c\u4e5f\u662f\u534e\u76db\u987f\u75be\u75c5\u52a8\u6001\u3001\u7ecf\u6d4e\u548c\u653f\u7b56\u4e2d\u5fc3\u7684\u521b\u529e\u8005\u548c\u4e3b\u7ba1\u3002\u4ed6\u8868\u793a\uff1a&ldquo;\u4f5c\u4e3a\u5730\u7403\u5171\u540c\u4f53\u7684\u4e00\u90e8\u5206\uff0c\u4e2d\u56fd\u548c\u5370\u5ea6\u5fc5\u987b\u8fdb\u4e00\u6b65\u91c7\u53d6\u63aa\u65bd\uff0c\u51cf\u7f13\u8010\u836f\u6027\u5bf9\u56fd\u5185\u52a8\u7269\u548c\u5c45\u6c11\u7684\u5f71\u54cd\u3002&rdquo;</p>\r\n\r\n<p>\u7814\u7a76\u8868\u660e\uff1a\u7531\u4e8e<strong>\u5168\u7403\u5e02\u573a\u4e2d73%\u7684\u6297\u83cc\u7d20\u7528\u4e8e\u5bb6\u79bd\u548c\u5bb6\u755c\u517b\u6b96\uff0c\u81f4\u75c5\u5fae\u751f\u7269\u7684\u8010\u836f\u6027\u95ee\u9898\u65e5\u76ca\u4e25\u5cfb\uff0c\u591a\u79cd\u91cd\u8981\u6297\u751f\u7d20\u836f\u7269\u7684\u6709\u6548\u6027\u964d\u4f4e</strong>\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/dd852q2hH9F_zZiEwhfspykSoVs9-qG7p0uNoUaZnfg4BAAA0wIAAEpQ.jpg\" style=\"height:428px; width:640px\" />\u5168\u7403\u5e02\u573a\u4e2d73%\u7684\u6297\u83cc\u7d20\u7528\u4e8e\u5bb6\u79bd\u548c\u5bb6\u755c\u517b\u6b96\uff0c\u52a8\u7269\u548c\u4eba\u7c7b\u7684\u8010\u836f\u6027\u95ee\u9898\u65e5\u76ca\u4e25\u5cfb\uff5cPixabay</p>\r\n\r\n<p>\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\uff08WHO\uff09\u5c06\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u5b9a\u4e49\u4e3a\uff1a\u80fd\u591f\u81f4\u75c5\u7684\u5fae\u751f\u7269\u4fdd\u62a4\u81ea\u5df1\u514d\u53d7\u6297\u751f\u836f\u7269\u653b\u51fb\u7684\u80fd\u529b\u3002\u8fd9\u4e9b\u5fae\u751f\u7269\u5305\u62ec\u4e86\u7ec6\u83cc\u3001\u75c5\u6bd2\u3001\u771f\u83cc\u548c\u4e00\u4e9b\u5bc4\u751f\u866b\u3002\u56e0\u6b64\uff0c\u666e\u901a\u533b\u7597\u624b\u6bb5\u5bf9\u8fd9\u4e9b\u5fae\u751f\u7269\u65e0\u6548\uff0c\u90a3\u4e9b\u5177\u6709\u4f20\u67d3\u6027\u7684\u75be\u75c5\u5c06\u987d\u56fa\u4e0d\u706d\uff0c\u5e76\u4e14\u4f20\u64ad\u7ed9\u5176\u4ed6\u52a8\u7269\u6216\u4eba\u7c7b\u3002</p>\r\n\r\n<p>\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8868\u793a\uff0c<strong>\u8fc7\u53bb\u7684\u4e8c\u5341\u5e74\u5185\uff0c\u52a8\u7269\u4e2d\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u6b63\u5728\u6025\u901f\u4e0a\u5347</strong>\u3002\u4ed6\u8bf4\uff1a&ldquo;2000\u5e74\u52302018\u5e74\u5185\uff0c\u79bd\u7c7b\u4e2d\u5bf9\u6297\u751f\u7d20\u5177\u670950%\u4ee5\u4e0a\u8010\u836f\u6027\u7684\u6bd4\u4f8b\u4ece15%\u4e0a\u5347\u5230\u4e8641%\uff0c\u800c\u5728\u5bb6\u732a\u4e0a\uff0c\u540c\u6837\u7684\u6570\u636e\u5219\u4ece13%\u4e0a\u5347\u5230\u4e8634%\u3002&rdquo;</p>\r\n\r\n<p>\u6839\u636e\u6700\u65b0\u7684\u8fd9\u9879\u7814\u7a76\uff0c<strong>\u4f4e\u4e2d\u6536\u5165\u56fd\u5bb6\u901a\u8fc7\u9ad8\u5f3a\u5ea6\u7684\u52a8\u7269\u751f\u4ea7\u7cfb\u7edf\u5b9e\u73b0\u9ad8\u86cb\u767d\u996e\u98df\uff0c\u5e76\u901a\u8fc7\u9891\u7e41\u4f7f\u7528\u6297\u751f\u7d20\u6765\u7ef4\u6301\u52a8\u7269\u7684\u5065\u5eb7\u548c\u751f\u4ea7\u529b</strong>\u3002</p>\r\n\r\n<h2><strong>\u8010\u836f\u6027\u53d1\u5c55\u8d8b\u52bf\u7684\u5168\u7403\u5feb\u7167</strong></h2>\r\n\r\n<p>\u4e3a\u4e86\u5448\u73b0\u4f4e\u4e2d\u6536\u5165\u56fd\u5bb6\u5185\u98df\u7528\u52a8\u7269\u4e2d\u7684\u8010\u836f\u6027\u53d1\u5c55\u8d8b\u52bf\uff0c\u7814\u7a76\u8005\u4eec\u5229\u75282008\u5e74\u81f32018\u5e74\u73b0\u60a3\u7387\uff08point-prevalence\uff09\u8c03\u67e5\u6570\u636e\uff0c\u7814\u53d1\u4e86\u4e00\u4e2a\u5730\u7406\u7a7a\u95f4\u6a21\u578b\u3002\u8fd9\u4e2a\u6a21\u578b\u80fd\u591f\u4e3a\u52a8\u7269\u548c\u98df\u7269\u751f\u4ea7\u4e2d\u7684\u8010\u836f\u6027\u6bd4\u7387\u63d0\u4f9b\u5168\u7403\u6027\u5feb\u7167\u3002</p>\r\n\r\n<p>\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8bf4\uff1a&ldquo;\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u5f15\u8d77\u5927\u5bb6\u5173\u6ce8\u4ee5\u4e0b\u4e8b\u5b9e\uff0c\u5373\u8010\u836f\u6027\u4e0d\u53ea\u56f0\u6270\u7740\u4eba\u7c7b\uff0c\u5b83\u4e5f\u5f71\u54cd\u4e86\u60a3\u75c5\u7684\u52a8\u7269\u3002\u6211\u4eec\u7684\u7814\u7a76\u4e3a\u653f\u7b56\u5236\u5b9a\u8005\u548c\u79d1\u5b66\u5bb6\u63d0\u4f9b\u4e86\u4f4e\u4e2d\u6536\u5165\u56fd\u5bb6\u5185\u52a8\u7269\u5bf9\u6297\u751f\u7d20\u8010\u836f\u6027\u7684\u7efc\u5408\u63cf\u8ff0\uff0c\u8fd9\u662f\u524d\u6240\u672a\u6709\u7684\u3002&rdquo;</p>\r\n\r\n<p>\u62a5\u544a\u8868\u793a\uff1a\u5728\u8fc7\u53bb\u768420\u5e74\u5185\uff0c\u8089\u7c7b\u6d88\u8d39\u5728\u9ad8\u6536\u5165\u56fd\u5bb6\u9010\u6e10\u5e73\u7f13\uff0c\u4f46\u5728\u4e9a\u6d32\u4e0a\u5347\u4e8668%\uff0c\u975e\u6d32\u4e0a\u5347\u4e8664%\uff0c\u5357\u7f8e\u4e0a\u5347\u4e8640%\u3002\u636e\u8c03\u67e5\uff0c\u5168\u740356%\u7684\u5bb6\u732a\u548c54%\u7684\u5bb6\u79bd\u90fd\u751f\u6d3b\u5728\u4e9a\u6d32\u3002</p>\r\n\r\n<p>\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8865\u5145\u9053\uff1a&ldquo;<strong>\u4e0d\u65ad\u63d0\u9ad8\u7684\u8010\u836f\u6027\u5f88\u53ef\u80fd\u5bf9\u519c\u6c11\u4ea7\u751f\u4e0d\u826f\u7684\u7ecf\u6d4e\u5f71\u54cd\uff0c\u5e76\u4e14\u4ee5\u98df\u7269\u4ef7\u683c\u6ce2\u52a8\u7684\u65b9\u5f0f\u95f4\u63a5\u5f71\u54cd\u5230\u6d88\u8d39\u8005\u3002\u8fd9\u5c06\u635f\u5bb3\u4e9a\u6d32\u7684\u98df\u54c1\u4f9b\u7ed9\u80fd\u529b\uff0c\u4f7f\u5176\u4e0d\u65ad\u589e\u957f\u7684\u4eba\u53e3\u95ee\u9898\u96ea\u4e0a\u52a0\u971c</strong>\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/XljehImX4d7D3GS29JAcFcvurlaLm-4aNjZRDCmxJGCsAwAAcgIAAEpQ.jpg\" style=\"height:426px; width:640px\" />\u4e0d\u65ad\u63d0\u9ad8\u7684\u8010\u836f\u6027\u4f1a\u635f\u5bb3\u4e9a\u6d32\u7684\u98df\u54c1\u4f9b\u7ed9\u80fd\u529b\uff5cPixabay</p>\r\n\r\n<p>\u5370\u5ea6\u6700\u8fd1\u7981\u6b62\u4e86\u755c\u7267\u4e1a\u4f7f\u7528\u7c98\u6746\u83cc\u7d20\uff0c\u8fd9\u79cd\u6297\u751f\u7d20\u88abWHO\u89c6\u4e3a\u4eba\u4f53\u6cbb\u7597\u7684\u6700\u540e\u4fdd\u9669\u548c\u4e0b\u4e0b\u7b56\u3002\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u5c06\u8be5\u884c\u52a8\u89c6\u4e3a\u4e00\u4e2a\u5f00\u59cb\uff0c\u4ed6\u8865\u5145\u9053\uff1a&ldquo;\u6619\u82b1\u4e00\u73b0\u7684\u653f\u7b56\u548c\u53e3\u53f7\u5e76\u4e0d\u80fd\u89e3\u51b3\u95ee\u9898\u3002<strong>\u6211\u4eec\u9700\u8981\u6559\u80b2\u519c\u6c11\u5982\u4f55\u4e0d\u4f7f\u7528\u6297\u751f\u7d20\u6765\u517b\u80b2\u5bb6\u79bd\u548c\u5bb6\u755c\uff0c\u5e76\u4e14\u4f7f\u4ed6\u4eec\u65e5\u76ca\u51cf\u5c11\u5bf9\u6297\u751f\u7d20\u7684\u4f9d\u8d56</strong>\u3002&rdquo;</p>\r\n\r\n<h2><strong>\u8010\u836f\u83cc\u4e0d\u65ad\u8513\u5ef6\uff0c\u98df\u54c1\u4e5f\u96be\u5e78\u514d</strong></h2>\r\n\r\n<p>\u98df\u54c1\u751f\u4ea7\u4e2d\u5e38\u4f7f\u7528\u7684\u6297\u751f\u7d20\uff0c\u4ea7\u751f\u7684\u8010\u836f\u6027\u662f\u6700\u5f3a\u7684\uff0c\u8fd9\u4e9b\u6297\u751f\u7d20\u5305\u62ec\u56db\u73af\u7d20\u3001\u78fa\u80fa\u7c7b\u836f\u548c\u9752\u9709\u7d20\u3002\u5bf9\u533b\u5b66\u6cbb\u7597\u4e0a\u6700\u91cd\u8981\u7684\u5404\u79cd\u6297\u751f\u7d20\uff0c\u8010\u836f\u6027\u7a0b\u5ea6\u968f\u7740\u533b\u7597\u4f7f\u7528\u6297\u751f\u7d20\u79cd\u7c7b\u4e0d\u540c\u800c\u53d8\u5316\u3002\u5404\u75c5\u539f\u4f53\u4e2d\u670920%-60%\u5bf9\u73af\u4e19\u6c99\u661f\u548c\u7ea2\u9709\u7d20\u4ea7\u751f\u8010\u836f\u6027\uff0c\u670910%-40%\u5bf9\u7b2c\u4e09\u4ee3\u548c\u7b2c\u56db\u4ee3\u5934\u5b62\u83cc\u7d20\u4ea7\u751f\u8010\u836f\u6027\u3002</p>\r\n\r\n<p>\u901a\u8fc7\u57282000\u5e74\u81f32018\u5e74\u671f\u95f4\u8fdb\u884c901\u4f8b\u73b0\u60a3\u7387\u8c03\u67e5\uff0c\u7814\u7a76\u5c0f\u7ec4\u5c06\u91cd\u70b9\u805a\u7126\u5728\u5448\u73b0\u51fa\u5bf9\u6297\u751f\u7d20\u5177\u6709\u8010\u836f\u6027\u7684\u5e38\u89c1\u98df\u6e90\u6027\u75c5\u539f\u4f53\u4e0a\uff0c\u8fd9\u5305\u62ec\u5927\u80a0\u6746\u83cc\u3001\u5f2f\u66f2\u6746\u83cc\u5c5e\u3001\u975e\u4f24\u5bd2\u6c99\u95e8\u83cc\u548c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u3002</p>\r\n\r\n<p>\u6839\u636eWHO\u7684\u4e00\u4efd\u62a5\u544a\uff0c<strong>\u8fd9\u4e9b\u7531\u52a8\u7269\u643a\u5e26\u3001\u5177\u6709\u8010\u836f\u6027\u7684\u7ec6\u83cc\u4e5f\u53ef\u4ee5\u4f7f\u4eba\u7c7b\u5f97\u75c5\uff0c\u5e76\u4e14\u901a\u8fc7\u5c60\u5bb0\u548c\u52a0\u5de5\u8fc7\u7a0b\u6c61\u67d3\u4ece\u519c\u573a\u5230\u57ce\u5e02\u7684\u6574\u4e2a\u98df\u7269\u4f9b\u7ed9\u94fe</strong>\u3002\u8fd9\u4efd\u62a5\u544a\u6307\u51fa\uff1a&ldquo;\u6c34\u679c\u548c\u852c\u83dc\u4e5f\u53ef\u80fd\u5728\u519c\u573a\u88ab\u6c61\u67d3\uff0c\u6216\u8005\u5728\u8fd0\u8f93\u8fc7\u7a0b\u4e2d\u88ab\u4ea4\u53c9\u6c61\u67d3\u3002&rdquo;</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/bswUKMb0Ibc9-K5WF9yy03kEgIE9EZ8APmxTgO4S26y0AwAAgQIAAEpQ.jpg\" style=\"height:433px; width:640px\" />\u5177\u6709\u8010\u836f\u6027\u7684\u7ec6\u83cc\u53ef\u80fd\u5728\u8fd0\u8f93\u8fc7\u7a0b\u4e2d\u4ea4\u53c9\u6c61\u67d3\u6c34\u679c\u548c\u852c\u83dc\uff5cPixabay</p>\r\n\r\n<p>\u5728\u5df4\u57fa\u65af\u5766\u5317\u90e8\u3001\u4f0a\u6717\u3001\u571f\u8033\u5176\u4e1c\u90e8\u3001\u57c3\u53ca\u7684\u5c3c\u7f57\u6cb3\u4e09\u89d2\u533a\u5730\u533a\u3001\u8d8a\u5357\u7684\u7ea2\u6cb3\u4e09\u89d2\u533a\u5730\u533a\u3001\u58a8\u897f\u54e5\u57ce\u5e02\u4ee5\u53ca\u5357\u975e\u7ea6\u7ff0\u5185\u65af\u5821\u5468\u8fb9\u5730\u533a\u90fd\u88ab\u8bc1\u5b9e\u662f\u8010\u836f\u6027\u7684\u9ad8\u53d1\u533a\u3002\u7c7b\u4f3c\u7684\u60c5\u51b5\u4e5f\u53d1\u751f\u5728\u5370\u5ea6\u4e1c\u5317\u90e8\u548c\u4e2d\u56fd\u4e1c\u5317\u90e8\u3002</p>\r\n\r\n<p>\u83b1\u739b&middot;\u67e5\u52aa&middot;\u6c99\u514b\u8482&middot;\u4e9a\u59c6\u8d1d\u59c6\uff08Leima Chanu Shakti Yambem\uff09\u662f\u4e00\u540d\u533b\u751f\uff0c\u6765\u81ea\u5370\u5ea6\u4e1c\u5317\u90e8\u66fc\u5c3c\u666e\u5c14\u5dde\u9996\u90fd\u82f1\u5e15\u5c14\u5e02\u3002\u5979\u8ba4\u4e3a\u8fd9\u9879\u7814\u7a76\u80fd\u591f\u5bf9\u5370\u5ea6\u7684\u4e1c\u5317\u90e8\u5730\u533a\u4ea7\u751f\u653f\u7b56\u5f71\u54cd\uff0c\u56e0\u4e3a\u4e1c\u5317\u90e8\u6070\u6070\u662f\u5370\u5ea6\u8089\u7c7b\u6d88\u8d39\u6700\u9ad8\u7684\u5730\u533a\u3002</p>\r\n\r\n<p>\u4e9a\u59c6\u8d1d\u59c6\u544a\u8bc9SciDev.Net\uff1a&ldquo;\u4eba\u4eec\u5df2\u7ecf\u610f\u8bc6\u5230\u4eba\u4f53\u4ea7\u751f\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u7684\u5371\u5bb3\uff0c\u800c\u8fd9\u9879\u7814\u7a76\u5c06\u8fdb\u4e00\u6b65\u6fc0\u53d1\u519c\u6c11\u548c\u6d88\u8d39\u8005\u5bf9\u8fd9\u4e2a\u95ee\u9898\u7684\u610f\u8bc6\uff0c\u5e2e\u52a9\u4ed6\u4eec\u8ba4\u8bc6\u5230\uff1a\u5728\u755c\u7267\u4e1a\u4e2d\u4f7f\u7528\u901f\u6548\u6297\u751f\u7d20\u6216\u8bb8\u6709\u77ed\u671f\u6548\u76ca\uff0c\u4f46\u5b83\u9020\u6210\u7684\u5371\u5bb3\u6700\u7ec8\u5c06\u5f71\u54cd\u6240\u6709\u4eba\u3002\u65e9\u6709\u7814\u7a76\u8bc1\u5b9e\uff0c<strong>\u4eba\u4f53\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u4f1a\u9020\u6210\u66f4\u957f\u7684\u60a3\u75c5\u5468\u671f\u3001\u66f4\u9ad8\u7684\u4f4f\u9662\u7387\u548c\u81f4\u547d\u7684\u533b\u7597\u5931\u8d25</strong>\u3002&rdquo;</p>\r\n\r\n<h2><strong>\u9632\u6b62\u8010\u836f\u5371\u673a\uff0c\u9700\u8981\u5168\u7403\u5171\u540c\u52aa\u529b</strong></h2>\r\n\r\n<p>\u867d\u7136\u8fd9\u9879\u7814\u7a76\u4e3b\u8981\u9488\u5bf9\u4f4e\u4e2d\u6536\u5165\u56fd\u5bb6\u4e2d\u8010\u836f\u6027\u7a0b\u5ea6\u7684\u5206\u5e03\uff0c\u4f46\u79d1\u5b66\u5bb6\u540c\u6837\u547c\u5401\u9ad8\u6536\u5165\u56fd\u5bb6\u53c2\u4e0e\u3002\u6bd5\u7adf\uff0c\u9ad8\u6536\u5165\u56fd\u5bb6\u81ea\u4ece1950\u5e74\u8d77\u5c31\u5f00\u59cb\u5728\u519c\u4e1a\u4e2d\u4f7f\u7528\u6297\u751f\u7d20\u3002\u8fd9\u9879\u7814\u7a76\u5e0c\u671b\u9ad8\u6536\u5165\u56fd\u5bb6\u80fd\u591f\u5e2e\u52a9\u4f4e\u6536\u5165\u56fd\u5bb6\u5b9e\u73b0\u5411\u5b89\u5168\u548c\u53ef\u6301\u7eed\u7684\u52a8\u7269\u751f\u4ea7\u65b9\u5f0f\u8f6c\u53d8\uff0c\u56e0\u4e3a\u53d1\u5c55\u4e2d\u56fd\u5bb6\u7684\u8089\u7c7b\u6d88\u8d39\u5df2\u51fa\u73b0\u5feb\u901f\u589e\u957f\u3002</p>\r\n\r\n<p>\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8bf4\uff1a&ldquo;<strong>\u5f52\u6839\u5230\u5e95\uff0c\u6297\u751f\u7d20\u8010\u836f\u6027\u95ee\u9898\u662f\u4e00\u4e2a\u5168\u7403\u95ee</strong><strong>\u9898\u3002\u5982\u679c\u6211\u4eec\u53ea\u662f\u7247\u9762\u5730\u964d\u4f4e\u5c40\u90e8\u5730\u533a\u7684\u8010\u836f\u6027\uff0c\u800c\u5141\u8bb8\u8fd9\u79cd\u73b0\u8c61\u5728\u5176\u4ed6\u5730\u533a\u53d1\u751f\uff0c\u90a3\u4e48\u6240\u6709\u7684\u52aa\u529b\u5c31\u662f\u5f92\u52b3\u7684</strong>\u3002&rdquo;</p>\r\n\r\n<p>\u4f46\u662f\uff0c\u8fd9\u79cd\u5bf9\u6297\u751f\u7d20\u8010\u836f\u6027\u7684\u7efc\u5408\u8003\u91cf\uff0c\u4e0d\u4ec5\u5e94\u8be5\u662f\u5730\u7406\u8303\u56f4\u4e0a\u7684\u5168\u7403\u5316\uff0c\u4e5f\u5e94\u8be5<strong>\u9488\u5bf9\u5177\u4f53\u56fd\u5bb6\u91c7\u53d6\u4e0d\u540c\u7b56\u7565</strong>\u3002\u6211\u4eec\u4e0d\u5f97\u4e0d<strong>\u5145\u5206\u8003\u8651\u5f53\u5730\u7684\u7ecf\u6d4e\u6a21\u5f0f\u548c\u4eba\u6c11\u7684\u57fa\u7840\u751f\u5b58\u6761\u4ef6</strong>\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/prvtQctoIufAMwWWaf1nIPp-rNtrN4VYNTRhyVEMFeGUAwAAYQIAAEpQ.jpg\" style=\"height:426px; width:640px\" />\u6212\u7528\u6297\u751f\u7d20\u5e94\u8be5\u9488\u5bf9\u5177\u4f53\u56fd\u5bb6\u91c7\u7528\u4e0d\u540c\u7b56\u7565\uff5cPixabay</p>\r\n\r\n<p>\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u544a\u8beb\u9053\uff0c<strong>\u867d\u7136\u6211\u4eec\u6709\u5fc5\u8981\u4f7f\u4e2d\u56fd\u3001\u5370\u5ea6\u548c\u4e9a\u592a\u5730\u533a\u5176\u4ed6\u56fd\u5bb6\u4e2d\u755c\u7267\u4e1a\u7981\u7528\u6297\u751f\u7d20\uff0c\u4f46\u6211\u4eec\u4e5f\u5fc5\u987b\u6ce8\u610f\u4e0d\u80fd\u5f71\u54cd\u5f53\u5730\u519c\u6c11\u7684\u751f\u8ba1</strong>\u3002</p>\r\n\r\n<p>\u76ee\u524d\uff0c\u975e\u6d32\u56fd\u5bb6\u7684\u9996\u8981\u95ee\u9898\u4ecd\u7136\u662f\u7ecf\u6d4e\u53d1\u5c55\u3002\u56e0\u6b64\uff0c\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8ba4\u4e3a\u4efb\u4f55\u9488\u5bf9\u6297\u751f\u7d20\u8010\u836f\u6027\u7684\u56fd\u9645\u6027\u7b56\u7565\u90fd\u5e94\u8be5\u8c41\u514d\u975e\u6d32\u56fd\u5bb6\uff0c\u4f7f\u4ed6\u4eec\u65e0\u9700\u91c7\u53d6\u5f3a\u5236\u63aa\u65bd\u9650\u5236\u517d\u836f\uff0c\u56e0\u4e3a\u8fd9\u79cd\u5f3a\u5236\u63aa\u65bd\u6216\u8bb8\u4f1a\u635f\u5bb3\u5f53\u5730\u4ee5\u755c\u7267\u4e1a\u4e3a\u57fa\u7840\u7684\u7ecf\u6d4e\u53d1\u5c55\u3002\uff08\u8bd1\u8005\uff1a\u72d7\u86cb\uff0c\u7f16\u8f91\uff1a\u60b2\u50ac\u7684\u94ca\u5b9d\u5b9d\uff0c\u5ba1\u7a3f\uff1aYuki\uff09</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#808080\">\u9898\u56fe\u6765\u6e90\uff1aPixabay</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#808080\">\u539f\u6587\u94fe\u63a5\uff1ahttps://www.scidev.net/global/agriculture/news/india-and-china-top-hot-spots-of-antimicrobial-resistance-in-animals.html\uff08\u672c\u6587\u7531storythings\u4f9b\u7a3f\uff09</span></span></p>", "date_created": "2019-10-31T15:57:05.446322+08:00", "date_modified": "2019-10-31T16:38:22.140109+08:00", "date_published": "2019-11-01T16:39:13.262022+08:00", "id": 454954, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": null, "replies_count": 9, "small_image": "https://1-im.guokr.com/8jNWltkvmPuUe2RBvVtIKVPoAYTcBwkzDhTbTq71ryZIBAAAAAMAAEpQ.jpg", "subject": {"articles_count": 87, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "agronomy", "minisite_key": "None", "name": "\u519c\u5b66", "sort_score": 15, "subject_type": "normal"}, "subject_key": "agronomy", "summary": "\u4e9a\u6d32\u755c\u7267\u4e1a\u7684\u5371\u201c\u9e21\u201d\u91cd\u91cd\u3002", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u592a\u957f\u4e0d\u770b\uff1a\u81f4\u75c5\u5fae\u751f\u7269\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u4e0d\u65ad\u589e\u5f3a\uff0c\u964d\u4f4e\u4e86\u91cd\u8981\u836f\u7269\u7684\u7597\u6548\u3002\u5168\u7403\u7814\u7a76\u8868\u660e\uff0c\u4e9a\u6d32\u5728\u8fc7\u53bb20\u5e74\u95f4\uff0c\u8089\u7c7b\u6d88\u8d39\u589e\u957f\u4e8668%\u3002\u4f5c\u8005\u6307\u51fa\uff0c\u755c\u7267\u4e1a\u5fc5\u987b\u7981\u7528\u901f\u6548\u6297\u751f\u7d20\u3002\u00a0\u8010\u836f\u5371\u673a\u6301\u7eed\u52a0\u91cd2019\u5e749\u670820\u65e5\uff0c\u300a\u79d1\u5b66\u300b\u6742\u5fd7\u4e0a\u53d1\u8868\u4e86\u4e00\u9879\u7814\u7a76\uff0c\u8bc4\u4f30\u4e86\u53d1\u5c55\u4e2d\u56fd\u5bb6\u4e2d\u8010\u836f\u75c5\u539f\u4f53\u7684\u53d1\u5c55\u5386\u7a0b\u3002\u7814\u7a76\u53d1\u73b0\uff0c\u5728\u4e2d\u56fd\u548c\u5370\u5ea6\u7684\u90e8\u5206\u5730\u533a\uff0c\u52a8\u7269\u7684\u591a\u91cd\u8010\u836f\u6027\u5c45\u9ad8\u4e0d\u4e0b\u3002\u80af\u5c3c\u4e9a\u548c\u5df4\u897f\u7684\u90e8\u5206\u5730\u533a\u4e5f\u6709\u540e\u6765\u5c45\u4e0a\u7684\u8d8b\u52bf\u3002\u8fd9\u9879\u7814\u7a76\u7684\u5171\u540c\u4f5c\u8005\u62c9\u66fc\u00b7\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\uff08Ramanan Laxminarayan\uff09\uff0c\u662f\u666e\u6797\u65af\u987f\u5927\u5b66\u7684\u9ad8\u7ea7\u7814\u7a76\u5458\uff0c\u4e5f\u662f\u534e\u76db\u987f\u75be\u75c5\u52a8\u6001\u3001\u7ecf\u6d4e\u548c\u653f\u7b56\u4e2d\u5fc3\u7684\u521b\u529e\u8005\u548c\u4e3b\u7ba1\u3002\u4ed6\u8868\u793a\uff1a\u201c\u4f5c\u4e3a\u5730\u7403\u5171\u540c\u4f53\u7684\u4e00\u90e8\u5206\uff0c\u4e2d\u56fd\u548c\u5370\u5ea6\u5fc5\u987b\u8fdb\u4e00\u6b65\u91c7\u53d6\u63aa\u65bd\uff0c\u51cf\u7f13\u8010\u836f\u6027\u5bf9\u56fd\u5185\u52a8\u7269\u548c\u5c45\u6c11\u7684\u5f71\u54cd\u3002\u201d\u7814\u7a76\u8868\u660e\uff1a\u7531\u4e8e\u5168\u7403\u5e02\u573a\u4e2d73%\u7684\u6297\u83cc\u7d20\u7528\u4e8e\u5bb6\u79bd\u548c\u5bb6\u755c\u517b\u6b96\uff0c\u81f4\u75c5\u5fae\u751f\u7269\u7684\u8010\u836f\u6027\u95ee\u9898\u65e5\u76ca\u4e25\u5cfb\uff0c\u591a\u79cd\u91cd\u8981\u6297\u751f\u7d20\u836f\u7269\u7684\u6709\u6548\u6027\u964d\u4f4e\u3002\u5168\u7403\u5e02\u573a\u4e2d73%\u7684\u6297\u83cc\u7d20\u7528\u4e8e\u5bb6\u79bd\u548c\u5bb6\u755c\u517b\u6b96\uff0c\u52a8\u7269\u548c\u4eba\u7c7b\u7684\u8010\u836f\u6027\u95ee\u9898\u65e5\u76ca\u4e25\u5cfb\uff5cPixabay\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\uff08WHO\uff09\u5c06\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u5b9a\u4e49\u4e3a\uff1a\u80fd\u591f\u81f4\u75c5\u7684\u5fae\u751f\u7269\u4fdd\u62a4\u81ea\u5df1\u514d\u53d7\u6297\u751f\u836f\u7269\u653b\u51fb\u7684\u80fd\u529b\u3002\u8fd9\u4e9b\u5fae\u751f\u7269\u5305\u62ec\u4e86\u7ec6\u83cc\u3001\u75c5\u6bd2\u3001\u771f\u83cc\u548c\u4e00\u4e9b\u5bc4\u751f\u866b\u3002\u56e0\u6b64\uff0c\u666e\u901a\u533b\u7597\u624b\u6bb5\u5bf9\u8fd9\u4e9b\u5fae\u751f\u7269\u65e0\u6548\uff0c\u90a3\u4e9b\u5177\u6709\u4f20\u67d3\u6027\u7684\u75be\u75c5\u5c06\u987d\u56fa\u4e0d\u706d\uff0c\u5e76\u4e14\u4f20\u64ad\u7ed9\u5176\u4ed6\u52a8\u7269\u6216\u4eba\u7c7b\u3002\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8868\u793a\uff0c\u8fc7\u53bb\u7684\u4e8c\u5341\u5e74\u5185\uff0c\u52a8\u7269\u4e2d\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u6b63\u5728\u6025\u901f\u4e0a\u5347\u3002\u4ed6\u8bf4\uff1a\u201c2000\u5e74\u52302018\u5e74\u5185\uff0c\u79bd\u7c7b\u4e2d\u5bf9\u6297\u751f\u7d20\u5177\u670950%\u4ee5\u4e0a\u8010\u836f\u6027\u7684\u6bd4\u4f8b\u4ece15%\u4e0a\u5347\u5230\u4e8641%\uff0c\u800c\u5728\u5bb6\u732a\u4e0a\uff0c\u540c\u6837\u7684\u6570\u636e\u5219\u4ece13%\u4e0a\u5347\u5230\u4e8634%\u3002\u201d\u6839\u636e\u6700\u65b0\u7684\u8fd9\u9879\u7814\u7a76\uff0c\u4f4e\u4e2d\u6536\u5165\u56fd\u5bb6\u901a\u8fc7\u9ad8\u5f3a\u5ea6\u7684\u52a8\u7269\u751f\u4ea7\u7cfb\u7edf\u5b9e\u73b0\u9ad8\u86cb\u767d\u996e\u98df\uff0c\u5e76\u901a\u8fc7\u9891\u7e41\u4f7f\u7528\u6297\u751f\u7d20\u6765\u7ef4\u6301\u52a8\u7269\u7684\u5065\u5eb7\u548c\u751f\u4ea7\u529b\u3002\u8010\u836f\u6027\u53d1\u5c55\u8d8b\u52bf\u7684\u5168\u7403\u5feb\u7167\u4e3a\u4e86\u5448\u73b0\u4f4e\u4e2d\u6536\u5165\u56fd\u5bb6\u5185\u98df\u7528\u52a8\u7269\u4e2d\u7684\u8010\u836f\u6027\u53d1\u5c55\u8d8b\u52bf\uff0c\u7814\u7a76\u8005\u4eec\u5229\u75282008\u5e74\u81f32018\u5e74\u73b0\u60a3\u7387\uff08point-prevalence\uff09\u8c03\u67e5\u6570\u636e\uff0c\u7814\u53d1\u4e86\u4e00\u4e2a\u5730\u7406\u7a7a\u95f4\u6a21\u578b\u3002\u8fd9\u4e2a\u6a21\u578b\u80fd\u591f\u4e3a\u52a8\u7269\u548c\u98df\u7269\u751f\u4ea7\u4e2d\u7684\u8010\u836f\u6027\u6bd4\u7387\u63d0\u4f9b\u5168\u7403\u6027\u5feb\u7167\u3002\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8bf4\uff1a\u201c\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u5f15\u8d77\u5927\u5bb6\u5173\u6ce8\u4ee5\u4e0b\u4e8b\u5b9e\uff0c\u5373\u8010\u836f\u6027\u4e0d\u53ea\u56f0\u6270\u7740\u4eba\u7c7b\uff0c\u5b83\u4e5f\u5f71\u54cd\u4e86\u60a3\u75c5\u7684\u52a8\u7269\u3002\u6211\u4eec\u7684\u7814\u7a76\u4e3a\u653f\u7b56\u5236\u5b9a\u8005\u548c\u79d1\u5b66\u5bb6\u63d0\u4f9b\u4e86\u4f4e\u4e2d\u6536\u5165\u56fd\u5bb6\u5185\u52a8\u7269\u5bf9\u6297\u751f\u7d20\u8010\u836f\u6027\u7684\u7efc\u5408\u63cf\u8ff0\uff0c\u8fd9\u662f\u524d\u6240\u672a\u6709\u7684\u3002\u201d\u62a5\u544a\u8868\u793a\uff1a\u5728\u8fc7\u53bb\u768420\u5e74\u5185\uff0c\u8089\u7c7b\u6d88\u8d39\u5728\u9ad8\u6536\u5165\u56fd\u5bb6\u9010\u6e10\u5e73\u7f13\uff0c\u4f46\u5728\u4e9a\u6d32\u4e0a\u5347\u4e8668%\uff0c\u975e\u6d32\u4e0a\u5347\u4e8664%\uff0c\u5357\u7f8e\u4e0a\u5347\u4e8640%\u3002\u636e\u8c03\u67e5\uff0c\u5168\u740356%\u7684\u5bb6\u732a\u548c54%\u7684\u5bb6\u79bd\u90fd\u751f\u6d3b\u5728\u4e9a\u6d32\u3002\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8865\u5145\u9053\uff1a\u201c\u4e0d\u65ad\u63d0\u9ad8\u7684\u8010\u836f\u6027\u5f88\u53ef\u80fd\u5bf9\u519c\u6c11\u4ea7\u751f\u4e0d\u826f\u7684\u7ecf\u6d4e\u5f71\u54cd\uff0c\u5e76\u4e14\u4ee5\u98df\u7269\u4ef7\u683c\u6ce2\u52a8\u7684\u65b9\u5f0f\u95f4\u63a5\u5f71\u54cd\u5230\u6d88\u8d39\u8005\u3002\u8fd9\u5c06\u635f\u5bb3\u4e9a\u6d32\u7684\u98df\u54c1\u4f9b\u7ed9\u80fd\u529b\uff0c\u4f7f\u5176\u4e0d\u65ad\u589e\u957f\u7684\u4eba\u53e3\u95ee\u9898\u96ea\u4e0a\u52a0\u971c\u3002\u4e0d\u65ad\u63d0\u9ad8\u7684\u8010\u836f\u6027\u4f1a\u635f\u5bb3\u4e9a\u6d32\u7684\u98df\u54c1\u4f9b\u7ed9\u80fd\u529b\uff5cPixabay\u5370\u5ea6\u6700\u8fd1\u7981\u6b62\u4e86\u755c\u7267\u4e1a\u4f7f\u7528\u7c98\u6746\u83cc\u7d20\uff0c\u8fd9\u79cd\u6297\u751f\u7d20\u88abWHO\u89c6\u4e3a\u4eba\u4f53\u6cbb\u7597\u7684\u6700\u540e\u4fdd\u9669\u548c\u4e0b\u4e0b\u7b56\u3002\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u5c06\u8be5\u884c\u52a8\u89c6\u4e3a\u4e00\u4e2a\u5f00\u59cb\uff0c\u4ed6\u8865\u5145\u9053\uff1a\u201c\u6619\u82b1\u4e00\u73b0\u7684\u653f\u7b56\u548c\u53e3\u53f7\u5e76\u4e0d\u80fd\u89e3\u51b3\u95ee\u9898\u3002\u6211\u4eec\u9700\u8981\u6559\u80b2\u519c\u6c11\u5982\u4f55\u4e0d\u4f7f\u7528\u6297\u751f\u7d20\u6765\u517b\u80b2\u5bb6\u79bd\u548c\u5bb6\u755c\uff0c\u5e76\u4e14\u4f7f\u4ed6\u4eec\u65e5\u76ca\u51cf\u5c11\u5bf9\u6297\u751f\u7d20\u7684\u4f9d\u8d56\u3002\u201d\u8010\u836f\u83cc\u4e0d\u65ad\u8513\u5ef6\uff0c\u98df\u54c1\u4e5f\u96be\u5e78\u514d\u98df\u54c1\u751f\u4ea7\u4e2d\u5e38\u4f7f\u7528\u7684\u6297\u751f\u7d20\uff0c\u4ea7\u751f\u7684\u8010\u836f\u6027\u662f\u6700\u5f3a\u7684\uff0c\u8fd9\u4e9b\u6297\u751f\u7d20\u5305\u62ec\u56db\u73af\u7d20\u3001\u78fa\u80fa\u7c7b\u836f\u548c\u9752\u9709\u7d20\u3002\u5bf9\u533b\u5b66\u6cbb\u7597\u4e0a\u6700\u91cd\u8981\u7684\u5404\u79cd\u6297\u751f\u7d20\uff0c\u8010\u836f\u6027\u7a0b\u5ea6\u968f\u7740\u533b\u7597\u4f7f\u7528\u6297\u751f\u7d20\u79cd\u7c7b\u4e0d\u540c\u800c\u53d8\u5316\u3002\u5404\u75c5\u539f\u4f53\u4e2d\u670920%-60%\u5bf9\u73af\u4e19\u6c99\u661f\u548c\u7ea2\u9709\u7d20\u4ea7\u751f\u8010\u836f\u6027\uff0c\u670910%-40%\u5bf9\u7b2c\u4e09\u4ee3\u548c\u7b2c\u56db\u4ee3\u5934\u5b62\u83cc\u7d20\u4ea7\u751f\u8010\u836f\u6027\u3002\u901a\u8fc7\u57282000\u5e74\u81f32018\u5e74\u671f\u95f4\u8fdb\u884c901\u4f8b\u73b0\u60a3\u7387\u8c03\u67e5\uff0c\u7814\u7a76\u5c0f\u7ec4\u5c06\u91cd\u70b9\u805a\u7126\u5728\u5448\u73b0\u51fa\u5bf9\u6297\u751f\u7d20\u5177\u6709\u8010\u836f\u6027\u7684\u5e38\u89c1\u98df\u6e90\u6027\u75c5\u539f\u4f53\u4e0a\uff0c\u8fd9\u5305\u62ec\u5927\u80a0\u6746\u83cc\u3001\u5f2f\u66f2\u6746\u83cc\u5c5e\u3001\u975e\u4f24\u5bd2\u6c99\u95e8\u83cc\u548c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u3002\u6839\u636eWHO\u7684\u4e00\u4efd\u62a5\u544a\uff0c\u8fd9\u4e9b\u7531\u52a8\u7269\u643a\u5e26\u3001\u5177\u6709\u8010\u836f\u6027\u7684\u7ec6\u83cc\u4e5f\u53ef\u4ee5\u4f7f\u4eba\u7c7b\u5f97\u75c5\uff0c\u5e76\u4e14\u901a\u8fc7\u5c60\u5bb0\u548c\u52a0\u5de5\u8fc7\u7a0b\u6c61\u67d3\u4ece\u519c\u573a\u5230\u57ce\u5e02\u7684\u6574\u4e2a\u98df\u7269\u4f9b\u7ed9\u94fe\u3002\u8fd9\u4efd\u62a5\u544a\u6307\u51fa\uff1a\u201c\u6c34\u679c\u548c\u852c\u83dc\u4e5f\u53ef\u80fd\u5728\u519c\u573a\u88ab\u6c61\u67d3\uff0c\u6216\u8005\u5728\u8fd0\u8f93\u8fc7\u7a0b\u4e2d\u88ab\u4ea4\u53c9\u6c61\u67d3\u3002\u201d\u5177\u6709\u8010\u836f\u6027\u7684\u7ec6\u83cc\u53ef\u80fd\u5728\u8fd0\u8f93\u8fc7\u7a0b\u4e2d\u4ea4\u53c9\u6c61\u67d3\u6c34\u679c\u548c\u852c\u83dc\uff5cPixabay\u5728\u5df4\u57fa\u65af\u5766\u5317\u90e8\u3001\u4f0a\u6717\u3001\u571f\u8033\u5176\u4e1c\u90e8\u3001\u57c3\u53ca\u7684\u5c3c\u7f57\u6cb3\u4e09\u89d2\u533a\u5730\u533a\u3001\u8d8a\u5357\u7684\u7ea2\u6cb3\u4e09\u89d2\u533a\u5730\u533a\u3001\u58a8\u897f\u54e5\u57ce\u5e02\u4ee5\u53ca\u5357\u975e\u7ea6\u7ff0\u5185\u65af\u5821\u5468\u8fb9\u5730\u533a\u90fd\u88ab\u8bc1\u5b9e\u662f\u8010\u836f\u6027\u7684\u9ad8\u53d1\u533a\u3002\u7c7b\u4f3c\u7684\u60c5\u51b5\u4e5f\u53d1\u751f\u5728\u5370\u5ea6\u4e1c\u5317\u90e8\u548c\u4e2d\u56fd\u4e1c\u5317\u90e8\u3002\u83b1\u739b\u00b7\u67e5\u52aa\u00b7\u6c99\u514b\u8482\u00b7\u4e9a\u59c6\u8d1d\u59c6\uff08Leima Chanu Shakti Yambem\uff09\u662f\u4e00\u540d\u533b\u751f\uff0c\u6765\u81ea\u5370\u5ea6\u4e1c\u5317\u90e8\u66fc\u5c3c\u666e\u5c14\u5dde\u9996\u90fd\u82f1\u5e15\u5c14\u5e02\u3002\u5979\u8ba4\u4e3a\u8fd9\u9879\u7814\u7a76\u80fd\u591f\u5bf9\u5370\u5ea6\u7684\u4e1c\u5317\u90e8\u5730\u533a\u4ea7\u751f\u653f\u7b56\u5f71\u54cd\uff0c\u56e0\u4e3a\u4e1c\u5317\u90e8\u6070\u6070\u662f\u5370\u5ea6\u8089\u7c7b\u6d88\u8d39\u6700\u9ad8\u7684\u5730\u533a\u3002\u4e9a\u59c6\u8d1d\u59c6\u544a\u8bc9SciDev.Net\uff1a\u201c\u4eba\u4eec\u5df2\u7ecf\u610f\u8bc6\u5230\u4eba\u4f53\u4ea7\u751f\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u7684\u5371\u5bb3\uff0c\u800c\u8fd9\u9879\u7814\u7a76\u5c06\u8fdb\u4e00\u6b65\u6fc0\u53d1\u519c\u6c11\u548c\u6d88\u8d39\u8005\u5bf9\u8fd9\u4e2a\u95ee\u9898\u7684\u610f\u8bc6\uff0c\u5e2e\u52a9\u4ed6\u4eec\u8ba4\u8bc6\u5230\uff1a\u5728\u755c\u7267\u4e1a\u4e2d\u4f7f\u7528\u901f\u6548\u6297\u751f\u7d20\u6216\u8bb8\u6709\u77ed\u671f\u6548\u76ca\uff0c\u4f46\u5b83\u9020\u6210\u7684\u5371\u5bb3\u6700\u7ec8\u5c06\u5f71\u54cd\u6240\u6709\u4eba\u3002\u65e9\u6709\u7814\u7a76\u8bc1\u5b9e\uff0c\u4eba\u4f53\u5bf9\u6297\u751f\u7d20\u7684\u8010\u836f\u6027\u4f1a\u9020\u6210\u66f4\u957f\u7684\u60a3\u75c5\u5468\u671f\u3001\u66f4\u9ad8\u7684\u4f4f\u9662\u7387\u548c\u81f4\u547d\u7684\u533b\u7597\u5931\u8d25\u3002\u201d\u9632\u6b62\u8010\u836f\u5371\u673a\uff0c\u9700\u8981\u5168\u7403\u5171\u540c\u52aa\u529b\u867d\u7136\u8fd9\u9879\u7814\u7a76\u4e3b\u8981\u9488\u5bf9\u4f4e\u4e2d\u6536\u5165\u56fd\u5bb6\u4e2d\u8010\u836f\u6027\u7a0b\u5ea6\u7684\u5206\u5e03\uff0c\u4f46\u79d1\u5b66\u5bb6\u540c\u6837\u547c\u5401\u9ad8\u6536\u5165\u56fd\u5bb6\u53c2\u4e0e\u3002\u6bd5\u7adf\uff0c\u9ad8\u6536\u5165\u56fd\u5bb6\u81ea\u4ece1950\u5e74\u8d77\u5c31\u5f00\u59cb\u5728\u519c\u4e1a\u4e2d\u4f7f\u7528\u6297\u751f\u7d20\u3002\u8fd9\u9879\u7814\u7a76\u5e0c\u671b\u9ad8\u6536\u5165\u56fd\u5bb6\u80fd\u591f\u5e2e\u52a9\u4f4e\u6536\u5165\u56fd\u5bb6\u5b9e\u73b0\u5411\u5b89\u5168\u548c\u53ef\u6301\u7eed\u7684\u52a8\u7269\u751f\u4ea7\u65b9\u5f0f\u8f6c\u53d8\uff0c\u56e0\u4e3a\u53d1\u5c55\u4e2d\u56fd\u5bb6\u7684\u8089\u7c7b\u6d88\u8d39\u5df2\u51fa\u73b0\u5feb\u901f\u589e\u957f\u3002\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8bf4\uff1a\u201c\u5f52\u6839\u5230\u5e95\uff0c\u6297\u751f\u7d20\u8010\u836f\u6027\u95ee\u9898\u662f\u4e00\u4e2a\u5168\u7403\u95ee\u9898\u3002\u5982\u679c\u6211\u4eec\u53ea\u662f\u7247\u9762\u5730\u964d\u4f4e\u5c40\u90e8\u5730\u533a\u7684\u8010\u836f\u6027\uff0c\u800c\u5141\u8bb8\u8fd9\u79cd\u73b0\u8c61\u5728\u5176\u4ed6\u5730\u533a\u53d1\u751f\uff0c\u90a3\u4e48\u6240\u6709\u7684\u52aa\u529b\u5c31\u662f\u5f92\u52b3\u7684\u3002\u201d\u4f46\u662f\uff0c\u8fd9\u79cd\u5bf9\u6297\u751f\u7d20\u8010\u836f\u6027\u7684\u7efc\u5408\u8003\u91cf\uff0c\u4e0d\u4ec5\u5e94\u8be5\u662f\u5730\u7406\u8303\u56f4\u4e0a\u7684\u5168\u7403\u5316\uff0c\u4e5f\u5e94\u8be5\u9488\u5bf9\u5177\u4f53\u56fd\u5bb6\u91c7\u53d6\u4e0d\u540c\u7b56\u7565\u3002\u6211\u4eec\u4e0d\u5f97\u4e0d\u5145\u5206\u8003\u8651\u5f53\u5730\u7684\u7ecf\u6d4e\u6a21\u5f0f\u548c\u4eba\u6c11\u7684\u57fa\u7840\u751f\u5b58\u6761\u4ef6\u3002\u6212\u7528\u6297\u751f\u7d20\u5e94\u8be5\u9488\u5bf9\u5177\u4f53\u56fd\u5bb6\u91c7\u7528\u4e0d\u540c\u7b56\u7565\uff5cPixabay\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u544a\u8beb\u9053\uff0c\u867d\u7136\u6211\u4eec\u6709\u5fc5\u8981\u4f7f\u4e2d\u56fd\u3001\u5370\u5ea6\u548c\u4e9a\u592a\u5730\u533a\u5176\u4ed6\u56fd\u5bb6\u4e2d\u755c\u7267\u4e1a\u7981\u7528\u6297\u751f\u7d20\uff0c\u4f46\u6211\u4eec\u4e5f\u5fc5\u987b\u6ce8\u610f\u4e0d\u80fd\u5f71\u54cd\u5f53\u5730\u519c\u6c11\u7684\u751f\u8ba1\u3002\u76ee\u524d\uff0c\u975e\u6d32\u56fd\u5bb6\u7684\u9996\u8981\u95ee\u9898\u4ecd\u7136\u662f\u7ecf\u6d4e\u53d1\u5c55\u3002\u56e0\u6b64\uff0c\u62c9\u514b\u65af\u7c73\u7eb3\u62c9\u626c\u8ba4\u4e3a\u4efb\u4f55\u9488\u5bf9\u6297\u751f\u7d20\u8010\u836f\u6027\u7684\u56fd\u9645\u6027\u7b56\u7565\u90fd\u5e94\u8be5\u8c41\u514d\u975e\u6d32\u56fd\u5bb6\uff0c\u4f7f\u4ed6\u4eec\u65e0\u9700\u91c7\u53d6\u5f3a\u5236\u63aa\u65bd\u9650\u5236\u517d\u836f\uff0c\u56e0\u4e3a\u8fd9\u79cd\u5f3a\u5236\u63aa\u65bd\u6216\u8bb8\u4f1a\u635f\u5bb3\u5f53\u5730\u4ee5\u755c\u7267\u4e1a\u4e3a\u57fa\u7840\u7684\u7ecf\u6d4e\u53d1\u5c55\u3002\uff08\u8bd1\u8005\uff1a\u72d7\u86cb\uff0c\u7f16\u8f91\uff1a\u60b2\u50ac\u7684\u94ca\u5b9d\u5b9d\uff0c\u5ba1\u7a3f\uff1aYuki\uff09\u9898\u56fe\u6765\u6e90\uff1aPixabay\u539f\u6587\u94fe\u63a5\uff1ahttps://www.scidev.net/global/agriculture/news/india-and-china-top-hot-spots-of-antimicrobial-resistance-in-animals.html\uff08\u672c\u6587\u7531storythings\u4f9b\u7a3f\uff09", "title": "\u4f60\u76d8\u5b50\u91cc\u90a3\u53ea\u9e21\uff0c\u597d\u50cf\u5403\u8fc7\u6297\u751f\u7d20", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "", "normal": "", "small": ""}, "introduction": "", "nickname": "Crispin Maslog", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "Crispin Maslog", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<p><strong>\u592a\u957f\u4e0d\u770b\uff1a</strong></p>\r\n\r\n<ol>\r\n\t<li>\u4e9a\u6d32\u57ce\u5e02\u843d\u540e\u7684\u53d1\u5c55\u89c4\u5212\u6781\u5927\u5730\u5f71\u54cd\u7740\u7ecf\u6d4e\u53d1\u5c55\u548c\u73af\u5883\u95ee\u9898\u3002</li>\r\n\t<li>\u516c\u5171\u7a7a\u95f4\u3001\u4ea4\u901a\u3001\u5783\u573e\u5904\u7406\u548c\u7a7a\u6c14\u6c61\u67d3\u7b49\u65b9\u9762\u7684\u538b\u529b\u65e5\u76ca\u589e\u5927\u3002</li>\r\n\t<li>\u89e3\u51b3\u8fd9\u79cd\u6df7\u4e71\u7684\u57ce\u5e02\u53d1\u5c55\u72b6\u51b5\uff0c\u9700\u8981\u5efa\u7acb\u8bbe\u65bd\u9f50\u5168\u3001\u80fd\u591f\u81ea\u7ed9\u81ea\u8db3\u7684\u57ce\u5e02\u793e\u533a\u3002</li>\r\n</ol>\r\n\r\n<h2>\u8d85\u7ea7\u57ce\u5e02\u6709\u591a\u5927</h2>\r\n\r\n<p>\u5728\u5168\u4e16\u754c77\u4ebf\u4eba\u53e3\u4e2d\uff0c\u6709\u5c06\u8fd160%\u7684\u4eba\u5c45\u4f4f\u5728\u57ce\u5e02\u91cc\uff0c\u800c\u57ce\u5e02\u4eba\u53e3\u6570\u91cf\uff0c\u5c24\u5176\u662f\u4e9a\u592a\u5730\u533a\u7684\u57ce\u5e02\u5c45\u6c11\u6570\u91cf\u4f9d\u7136\u5728\u8fc5\u901f\u589e\u52a0\u3002<strong>\u4e0d\u65ad\u589e\u957f\u7684\u57ce\u5e02\u4eba\u53e3\u6570\u91cf\u5c06\u5bf9\u57ce\u5e02\u7684\u7ecf\u6d4e\u3001\u793e\u4f1a\u548c\u73af\u5883\u4ea7\u751f\u6df1\u8fdc\u7684\u5f71\u54cd</strong>\u3002<br />\r\n<br />\r\n\u5728\u4e9a\u592a\u5730\u533a\uff0c\u4f4f\u5728\u57ce\u5e02\u4e2d\u7684\u4eba\u8d85\u8fc723\u4ebf\u3002\u4e16\u754c\u4e0a\u6700\u5927\u768410\u5ea7\u57ce\u5e02\u5f53\u4e2d\u5c31\u67098\u5ea7\u5728\u4e9a\u6d32\u3002\u6839\u636e2018\u5e7411\u67082\u65e5\u7684\u4e16\u754c\u5730\u56fe\u96c6\u7edf\u8ba1\uff0c\u8fd98\u5ea7\u57ce\u5e02\u6309\u4eba\u53e3\u6570\u91cf\u4ece\u591a\u5230\u5c11\u6392\u5e8f\u5206\u522b\u662f\uff1a\u4e0a\u6d77\uff082410\u4e07\uff09\uff0c\u5317\u4eac\uff081850\u4e07\uff09\uff0c\u5df4\u57fa\u65af\u5766\u5361\u62c9\u5947\uff081800\u4e07\uff09\uff0c\u5b5f\u52a0\u62c9\u56fd\u8fbe\u5361\uff081450\u4e07\uff09\uff0c\u65e5\u672c\u4e1c\u4eac\uff081360\u4e07\uff09\uff0c\u83f2\u5f8b\u5bbe\u9a6c\u5c3c\u62c9\uff081280\u4e07\uff09\uff0c\u5929\u6d25\uff081270\u4e07\uff09\u548c\u5370\u5ea6\u5b5f\u4e70\uff081240\u4e07\uff09\u3002</p>\r\n\r\n<p>\u8fd9\u91cc\u7edf\u8ba1\u7684\u4eba\u53e3\u6570\u91cf\u8fd8\u4e0d\u5305\u62ec\u4e3b\u57ce\u533a\u4ee5\u5916\u7684\u90ca\u533a\u4eba\u53e3\u3002\u6bd4\u5982\u83f2\u5f8b\u5bbe\u9996\u90fd\u9a6c\u5c3c\u62c9\uff0c\u5982\u679c\u5c06\u5b83\u5468\u56f4\u4eba\u53e3\u8f83\u5c11\u3001\u89c4\u6a21\u8f83\u5c0f\uff0c\u4e0e\u4e2d\u5fc3\u57ce\u5e02\u529f\u80fd\u8054\u7cfb\u7d27\u5bc6\u7684&ldquo;\u536b\u661f\u57ce\u5e02&rdquo;\u6db5\u76d6\u5728\u5185\uff0c\u90a3\u4e48\u4e00\u5171\u5927\u6982\u4f1a\u67092400\u4e07\u4eba\u53e3\u3002\u968f\u7740\u57ce\u5e02\u5316\u8fdb\u7a0b\u7684\u5feb\u901f\u8fdb\u884c\uff0c\u4ec5\u572810\u591a\u5e74\u540e\u76842030\u5e74\uff0c\u4e16\u754c\u4e0a\u5c06\u6709\u8d85\u8fc76\u6210\u4eba\u53e3\u5c45\u4f4f\u5728\u57ce\u5e02\u5f53\u4e2d\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/SPhH9I7o0zysB-hn7UCxKmaoifWUrtZqHZQLC4eYaGbwAgAA8wEAAEpQ.jpg\" style=\"height:425px; width:640px\" />\u4e0d\u65ad\u589e\u957f\u7684\u57ce\u5e02\u4eba\u53e3\u6570\u91cf\u5c06\u5bf9\u57ce\u5e02\u7684\u7ecf\u6d4e\u3001\u793e\u4f1a\u548c\u73af\u5883\u4ea7\u751f\u6df1\u8fdc\u7684\u5f71\u54cd\uff5cPixabay</p>\r\n\r\n<h2>\u8d85\u7ea7\u57ce\u5e02\u810f\u4e71\u5dee</h2>\r\n\r\n<p>\u8fc7\u53bb\uff0c\u4eba\u4eec\u968f\u610f\u5730\u6269\u5f20\u57ce\u5e02\uff0c\u5e76\u6ca1\u6709\u7ecf\u8fc7\u4ec0\u4e48\u89c4\u5212\u3002\u73b0\u5728\u662f\u65f6\u5019\u8ba9\u57ce\u533a\u5728\u89c4\u5212\u548c\u7ba1\u7406\u4e2d\u6210\u957f\u4e86\u3002<br />\r\n<br />\r\n\u4e0d\u7136\uff0c\u6211\u4eec\u5c06\u627f\u62c5<strong>\u57ce\u5e02\u80e1\u4e71\u6269\u5f20\u5e26\u6765\u7684\u540e\u679c</strong>\u3002\u57ce\u5e02\u89c4\u5212\u8005\u5f53\u524d\u5e94\u8be5\u62c5\u5fc3\u7684\u95ee\u9898\u975e\u5e38\u591a\uff0c\u5305\u62ec\u4eba\u53e3\u5bc6\u5ea6\u3001\u975e\u6b63\u5f0f\u5c45\u6c11\u3001\u57ce\u5e02\u8870\u9000\u3001\u516c\u5171\u7a7a\u95f4\u3001\u516c\u5171\u4ea4\u901a\u3001\u5783\u573e\u5904\u7406\u3001\u7a7a\u6c14\u6c61\u67d3\u3001\u81ea\u7136\u8d44\u6e90\u7ba1\u7406\u3001\u6c34\u8d44\u6e90\u4fdd\u62a4\u3001\u9002\u5e94\u548c\u7f13\u89e3\u6c14\u5019\u53d8\u5316\u3001\u707e\u5bb3\u98ce\u9669\u7ba1\u7406\u548c\u65e5\u76ca\u52a0\u5267\u7684\u4e0d\u5e73\u7b49\u95ee\u9898\u7b49\u3002</p>\r\n\r\n<p>&nbsp;&ldquo;\u6211\u5de5\u4f5c\u751f\u6d3b\u5728\u4e9a\u6d32\u8d85\u5927\u57ce\u5e02\u83f2\u5f8b\u5bbe\u9a6c\u5c3c\u62c9\u7684\u8fb9\uff0c\u56e0\u4e3a\u4fee\u8def\uff0c\u8fd9\u5ea7\u57ce\u5e02\u5728\u8fc7\u53bb\u7684\u51e0\u4e2a\u661f\u671f\u91cc\u4ece\u65e9\u5230\u665a\u5168\u5929\u90fd\u5728\u5835\u8f66\u3002\u73b0\u5728\u6211\u4ece\u6211\u5bb6\u6240\u5728\u7684\u5c0f\u9547\u5f00\u8f66\u5230\u9a6c\u5c3c\u62c9\uff0c\u53ea\u670965\u516c\u91cc\u8def\uff0c\u8981\u5f00\u534a\u5929\u65f6\u95f4\u3002&rdquo;\u9a6c\u5c3c\u62c9\u73af\u4fdd\u8bb0\u8005\u514b\u91cc\u65af\u5bbe&middot;\u9a6c\u65af\u6d1b\uff08Crispin Maslog\uff09\u8bf4\u3002<br />\r\n<br />\r\n&ldquo;\u6211\u7ecf\u5e38\u53bb\u5468\u56f4\u7684\u57ce\u5e02\uff0c\u6bd4\u5982\u6cf0\u56fd\u66fc\u8c37\u3001\u5370\u5ea6\u6d77\u5fb7\u62c9\u5df4\u3001\u5370\u5c3c\u96c5\u52a0\u8fbe\u548c\u67ec\u57d4\u5be8\u91d1\u8fb9\uff0c\u6211\u4e86\u89e3\u4ed6\u4eec\u5982\u4f55\u5e94\u5bf9\u57ce\u5e02\u6269\u5f20\u95ee\u9898\u3001\u4e25\u91cd\u7684\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\u548c\u6392\u6c34\u7cfb\u7edf\u95ee\u9898\uff0c\u56e0\u4e3a\u57ce\u5e02\u5783\u573e\u592a\u591a\u5835\u585e\u4e86\u4e0b\u6c34\u9053\uff0c\u4e00\u70b9\u70b9\u5c0f\u96e8\u5c31\u4f1a\u5f15\u53d1\u4e25\u91cd\u7684\u79ef\u6c34\u3002\u5370\u5c3c\u7684\u68ee\u6797\u706b\u707e\u4e5f\u5728\u5e2e\u5012\u5fd9\uff0c\u6d53\u70df\u6e10\u6e10\u8513\u5ef6\u5230\u90bb\u56fd\uff0c\u5f71\u54cd\u5230\u9a6c\u6765\u897f\u4e9a\u3001\u83f2\u5f8b\u5bbe\u3001\u65b0\u52a0\u5761\u548c\u6cf0\u56fd\u3002&rdquo;</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/RraIT6Eb5kazhnal__Hf4FGoGY8Oi8wSnLbUY_bGY1b8AgAAswEAAEpQ.jpg\" style=\"height:364px; width:640px\" />\u4ea4\u901a\u95ee\u9898\u53ea\u662f\u4e9a\u6d32\u57ce\u5e02\u9700\u8981\u9762\u5bf9\u7684\u8bf8\u591a\u95ee\u9898\u4e4b\u4e00\uff5cPatrick Roque, CC BY-SA 4.0</p>\r\n\r\n<p>&ldquo;\u6211\u4e5f\u4f1a\u95ee\u81ea\u5df1\uff0c\u5982\u679c\u57ce\u5e02\u771f\u7684\u53d8\u5f97\u8d8a\u6765\u8d8a\u4e0d\u5b9c\u5c45\uff0c\u73b0\u5728\u8981\u642c\u5230\u4e61\u6751\u91cc\uff0c\u4f8d\u5f04\u82b1\u8349\uff0c\u953b\u70bc\u8eab\u4f53\uff0c\u547c\u5438\u65b0\u9c9c\u7a7a\u6c14\uff0c\u8fc7\u4e0a\u8fdc\u79bb\u4ea4\u901a\u62e5\u5835\u3001\u566a\u97f3\u548c\u7a7a\u6c14\u6c61\u67d3\u7684\u751f\u6d3b\u5417\uff1f&rdquo;</p>\r\n\r\n<h2>\u57ce\u5e02\u95ee\u9898\u89e3\u51b3\u6cd5</h2>\r\n\r\n<p>\u968f\u7740\u4eba\u53e3\u9010\u6e10\u5411\u57ce\u5e02\u805a\u96c6\uff0c\u57ce\u5e02\u9010\u6e10\u6269\u5f20\uff0c\u8ba9\u57ce\u5e02\u53ef\u6301\u7eed\u5730\u6309\u89c4\u5212\u6210\u957f\u8feb\u5728\u7709\u776b\u3002\u653f\u5e9c\u5fc5\u987b\u63d0\u4f9b\u50cf\u516c\u56ed\u4e00\u6837\u7684\u516c\u5171\u7a7a\u95f4\uff0c\u73b0\u5728\u5df2\u6709\u7684\u516c\u5171\u7a7a\u95f4\u5fc5\u987b\u5f97\u5230\u4fdd\u62a4\uff0c\u4e0d\u80fd\u88ab\u5546\u4e1a\u53d1\u5c55\u5360\u7528\u3002<strong>\u7f8e\u56fd\u548c\u6b27\u6d32\u7684\u57ce\u5e02\u516c\u56ed\u4f53\u7cfb\u662f\u503c\u5f97\u4e9a\u6d32\u653f\u5e9c\u5b66\u4e60\u7684\u597d\u699c\u6837</strong>\u3002<br />\r\n<br />\r\n\u540c\u65f6\uff0c\u6211\u4eec\u5fc5\u987b<strong>\u63d0\u9ad8\u5e9f\u7269\u56de\u6536\u548c\u5faa\u73af\u5229\u7528\u7684\u6548\u7387</strong>\uff1b\u653f\u5e9c\u5fc5\u987b<strong>\u63d0\u4f9b\u66f4\u5bb9\u6613\u8d1f\u62c5\u5f97\u8d77\u7684\u4f4f\u623f</strong><strong>\uff1b\u5fc5\u987b\u7ec4\u7ec7\u548c\u652f\u6301\u516c\u5171\u4ea4\u901a\u7684\u53d1\u5c55</strong>\uff0c\u8fd9\u6837\u5c45\u6c11\u5c31\u53ef\u4ee5\u628a\u79c1\u5bb6\u8f66\u505c\u5728\u5bb6\u91cc\uff0c\u4e58\u5750\u516c\u5171\u4ea4\u901a\u5de5\u5177\u4e0a\u73ed\uff0c\u5728\u8fd9\u65b9\u9762\u6211\u4eec\u5df2\u7ecf\u53ef\u4ee5\u5411\u9999\u6e2f\u548c\u65b0\u52a0\u5761\u5b66\u4e60\u3002<br />\r\n<br />\r\n\u8054\u5408\u56fd\u4eba\u7c7b\u5c45\u4f4f\u89c4\u5212\u7f72\uff08UN Habitat\uff09\u5df2\u7ecf\u652f\u6301\u4e86\u5f88\u591a\u4e9a\u6d32\u7684\u9879\u76ee\uff0c\u4e3a\u57ce\u5e02\u793e\u533a\u89c4\u5212\u8bbe\u8ba1\u516c\u5171\u7a7a\u95f4\u3002\u4f8b\u5982\u8054\u5408\u56fd\u4eba\u7c7b\u5c45\u4f4f\u89c4\u5212\u7f72\u4e0e\u83f2\u5f8b\u5bbe\u57ce\u5e02\u8054\u76df\uff08League of Cities of the Philippines\uff09\u3001\u8d2b\u6c11\u7a9f\u5c45\u6c11\u56fd\u9645\uff08SDI\uff09\u548c\u4e00\u4e9b\u5927\u5b66\u3001\u975e\u653f\u5e9c\u7ec4\u7ec7\u7b49\u5f53\u5730\u7ec4\u7ec7\u5408\u4f5c\u7684\u53d1\u5c55\u9879\u76ee\u3002\u5728\u793e\u533a\u91cc\u89c4\u5212\u516c\u5171\u7a7a\u95f4\uff0c\u8ba9\u793e\u533a\u5bf9\u516c\u5171\u7a7a\u95f4\u66f4\u6709\u5f52\u5c5e\u611f\uff0c\u8fd9\u4e9b\u516c\u5171\u7a7a\u95f4\u4e5f\u5f97\u5230\u4e86\u79ef\u6781\u7684\u7ba1\u7406\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/tipl2Q4cyoETaO2SI6XJYtqxo_3njchUTBI7I2f7sJ0UAwAADgIAAEpQ.jpg\" style=\"height:427px; width:640px\" />\u89c4\u5212\u72ec\u7acb\u7684\u81ea\u7ed9\u81ea\u8db3\u7684\u57ce\u5e02\u793e\u533a\uff0c\u53ef\u4ee5\u4f7f\u4ea4\u901a\u9700\u6c42\u6700\u5c0f\u5316\uff0c\u4ece\u800c\u6d88\u9664\u4ea4\u901a\u95ee\u9898\uff5cPixabay</p>\r\n\r\n<p>\u73b0\u5728\uff0c\u6211\u4eec\u5e94\u8be5\u89c4\u5212\u4e00\u4e2a<strong>\u72ec\u7acb\u7684\u81ea\u7ed9\u81ea\u8db3\u7684\u57ce\u5e02\u793e\u533a</strong>\u3002\u8fd9\u79cd\u793e\u533a\u6a21\u5f0f\u8ba9\u5c45\u6c11\u5c45\u4f4f\u5728\u5de5\u4f5c\u533a\uff08\u529e\u516c\u5ba4\uff09\u3001\u5546\u4e1a\u533a\uff08\u5546\u573a\uff09\u3001\u5a31\u4e50\u8bbe\u65bd\uff08\u7535\u5f71\u9662\u3001\u4f53\u80b2\u9986\u7b49\uff09\u548c\u57fa\u7840\u8bbe\u65bd\uff08\u5b66\u6821\u548c\u533b\u9662\u7b49\uff09\u9644\u8fd1\uff0c<strong>\u4f7f\u4ea4\u901a\u9700\u6c42\u6700\u5c0f\u5316\uff0c\u4ece\u800c\u6d88\u9664\u4ea4\u901a\u95ee\u9898</strong>\u3002</p>\r\n\r\n<p>\u8fd8\u6709\u4e00\u79cd\u6a21\u5f0f\u662f<strong>\u91cd\u65b0\u5efa\u7acb\u4e00\u4e2a\u57ce\u5e02</strong>\u3002\u4f8b\u5982\u9a6c\u6765\u897f\u4e9a\u65b0\u7684\u8054\u90a6\u884c\u653f\u9996\u90fd\u5e03\u7279\u62c9\u518d\u4e5f\uff08\u592a\u5b50\u57ce\uff09\uff0c\u5b83\u662f\u9a6c\u6765\u897f\u4e9a\u7b2c\u4e09\u5927\u8054\u90a6\u76f4\u8f96\u533a\uff0c\u5efa\u7acb\u5728\u4e00\u57575000\u516c\u9877\u7684\u6cbc\u6cfd\u5730\u548c\u6cb9\u68d5\u56ed\u533a\u4e0a\u3002\u518d\u6bd4\u5982\uff0c\u83f2\u5f8b\u5bbe\u5219\u6b63\u5728\u89c4\u5212\u65b0\u5efa\u514b\u62c9\u514b\u57ce\u3002\u5b83\u539f\u672c\u662f\u8ddd\u79bb\u9a6c\u5c3c\u62c998\u516c\u91cc\u7684\u4e00\u4e2a\u524d\u7f8e\u56fd\u519b\u4e8b\u57fa\u5730\uff0c\u83f2\u5f8b\u5bbe\u8ba1\u5212\u5c06\u5176\u53d1\u5c55\u4e3a\u56fd\u5bb6\u672a\u6765\u7684\u7ecf\u6d4e\u4e2d\u5fc3\u3002<br />\r\n<br />\r\n\u4e3a\u4e86\u8ba9\u4e9a\u592a\u5730\u533a\u7684\u57ce\u5e02\u66f4\u9002\u5b9c\u4eba\u7c7b\u5c45\u4f4f\uff0c\u7b2c\u4e03\u5c4a\u4e9a\u592a\u57ce\u5e02\u8bba\u575b\uff08APUF-7\uff09\u4e8e2019\u5e7410\u670815-17\u65e5\u5728\u9a6c\u6765\u897f\u4e9a\u69df\u57ce\u4e3e\u529e\uff0c\u8bba\u575b\u4e3b\u9898\u4e3a&ldquo;\u4e9a\u592a\u57ce\u5e02\u7684\u672a\u6765\uff1a\u4e3a\u5b9e\u73b02030\u53ef\u6301\u7eed\u53d1\u5c55\u7684\u53d8\u9769&rdquo;\u3002<br />\r\n<br />\r\n\u4f1a\u8bae\u65e8\u5728\u63d0\u4f9b&ldquo;\u9762\u5411\u672a\u6765\u7684\u57ce\u5e02\u5316\u65b9\u6848&rdquo;\uff0c\u5728\u56fd\u5bb6\u548c\u5730\u65b9\u653f\u5e9c\u9886\u5bfc\u3001\u653f\u7b56\u5236\u5b9a\u8005\u3001\u79c1\u4eba\u673a\u6784\u3001\u5b66\u8005\u548c\u6c11\u95f4\u56e2\u4f53\u7684\u5e2e\u52a9\u4e0b\uff0c\u5bfb\u627e\u57ce\u5e02\u6240\u9762\u4e34\u7684\u751f\u5b58\u95ee\u9898\u7684\u7b54\u6848\u3002</p>\r\n\r\n<p>\u7136\u800c\u4e0d\u7ba1\u613f\u666f\u6709\u591a\u7f8e\u597d\uff0c\u4e9a\u592a\u57ce\u5e02\u8bba\u575b\u4e5f\u65e0\u6cd5\u5728\u4e00\u591c\u4e4b\u95f4\u89e3\u51b3\u4e9a\u6d32\u57ce\u5e02\u4e0d\u5b9c\u5c45\u7684\u95ee\u9898\u3002\u4e0d\u8fc7\uff0c\u8fd9\u6b21\u4f1a\u8bae\u81f3\u5c11\u4f1a\u5e26\u6765\u4e00\u79cd\u7d27\u8feb\u611f\uff0c<strong>\u8981\u6c42\u57ce\u5e02\u5728\u89c4\u5212\u4e2d\u751f\u957f\uff0c\u9632\u6b62\u5b83\u4eec\u80e1\u4e71\u6269\u5f20</strong>\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/xk__NxcIB9PPwID-s8Xtgd7wGP3hKVf0PUV9UFl2FnusAwAAcgIAAEpQ.jpg\" style=\"height:426px; width:640px\" />\u57ce\u5e02\u8981\u5728\u89c4\u5212\u4e2d\u751f\u957f\uff0c\u907f\u514d\u80e1\u4e71\u6269\u5f20\uff5cPixabay</p>\r\n\r\n<p>\u540c\u65f6\uff0c\u8054\u5408\u56fd\u7684\u89c4\u5212\u8005\u4eec\u8fd8\u5e94\u5f53\u63a5\u53d7\u4e00\u4e2a\u4e8b\u5b9e\uff1a\u5730\u7403\u5c31\u8fd9\u4e48\u5927\uff0c\u4eba\u53e3\u6570\u91cf\u5374\u5728\u4e0d\u52a0\u9650\u5236\u5730\u589e\u957f\u3002\u4e0e\u5176\u5355\u5355\u8fdb\u884c\u57ce\u5e02\u89c4\u5212\uff0c\u4e0d\u5982\u5c06\u63a7\u5236\u4eba\u53e3\u6570\u91cf\u4e5f\u7eb3\u5165\u8054\u5408\u56fd\u653f\u7b56\uff1f\uff08\u8bd1\u8005\uff1a\u793e\u4f1a\u5c0f\u7070\u74f6\uff0c\u7f16\u8f91\uff1a\u60b2\u50ac\u7684\u94ca\u5b9d\u5b9d\uff0c\u5ba1\u7a3f\uff1aYuki\uff09</p>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">\u9898\u56fe\u6765\u6e90\uff1aPixabay</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">\u539f\u6587\u94fe\u63a5\uff1a</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">https://www.scidev.net/global/cities/columns/asia-pacific-analysis-living-in-asia-s-unliveable-cities-1.html\uff08\u672c\u6587\u7531storythings\u4f9b\u7a3f\uff09</span></span></div>", "date_created": "2019-10-30T11:12:06.489058+08:00", "date_modified": "2019-10-30T11:48:03.735596+08:00", "date_published": "2019-10-31T11:20:59.090526+08:00", "id": 454945, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": null, "replies_count": 4, "small_image": "https://3-im.guokr.com/QhDhGa8l2Flfr5sLw9fRJUiKPh6zDQx2ua6rku7ap_OPAwAAWwIAAEpQ.jpg", "subject": {"articles_count": 423, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "society", "minisite_key": "None", "name": "\u793e\u4f1a", "sort_score": 21, "subject_type": "normal"}, "subject_key": "society", "summary": "\u57ce\u5e02\u8981\u5728\u89c4\u5212\u4e2d\u751f\u957f\uff0c\u907f\u514d\u80e1\u4e71\u6269\u5f20\u3002", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u592a\u957f\u4e0d\u770b\uff1a\u4e9a\u6d32\u57ce\u5e02\u843d\u540e\u7684\u53d1\u5c55\u89c4\u5212\u6781\u5927\u5730\u5f71\u54cd\u7740\u7ecf\u6d4e\u53d1\u5c55\u548c\u73af\u5883\u95ee\u9898\u3002\u516c\u5171\u7a7a\u95f4\u3001\u4ea4\u901a\u3001\u5783\u573e\u5904\u7406\u548c\u7a7a\u6c14\u6c61\u67d3\u7b49\u65b9\u9762\u7684\u538b\u529b\u65e5\u76ca\u589e\u5927\u3002\u89e3\u51b3\u8fd9\u79cd\u6df7\u4e71\u7684\u57ce\u5e02\u53d1\u5c55\u72b6\u51b5\uff0c\u9700\u8981\u5efa\u7acb\u8bbe\u65bd\u9f50\u5168\u3001\u80fd\u591f\u81ea\u7ed9\u81ea\u8db3\u7684\u57ce\u5e02\u793e\u533a\u3002\u8d85\u7ea7\u57ce\u5e02\u6709\u591a\u5927\u5728\u5168\u4e16\u754c77\u4ebf\u4eba\u53e3\u4e2d\uff0c\u6709\u5c06\u8fd160%\u7684\u4eba\u5c45\u4f4f\u5728\u57ce\u5e02\u91cc\uff0c\u800c\u57ce\u5e02\u4eba\u53e3\u6570\u91cf\uff0c\u5c24\u5176\u662f\u4e9a\u592a\u5730\u533a\u7684\u57ce\u5e02\u5c45\u6c11\u6570\u91cf\u4f9d\u7136\u5728\u8fc5\u901f\u589e\u52a0\u3002\u4e0d\u65ad\u589e\u957f\u7684\u57ce\u5e02\u4eba\u53e3\u6570\u91cf\u5c06\u5bf9\u57ce\u5e02\u7684\u7ecf\u6d4e\u3001\u793e\u4f1a\u548c\u73af\u5883\u4ea7\u751f\u6df1\u8fdc\u7684\u5f71\u54cd\u3002\r\u5728\u4e9a\u592a\u5730\u533a\uff0c\u4f4f\u5728\u57ce\u5e02\u4e2d\u7684\u4eba\u8d85\u8fc723\u4ebf\u3002\u4e16\u754c\u4e0a\u6700\u5927\u768410\u5ea7\u57ce\u5e02\u5f53\u4e2d\u5c31\u67098\u5ea7\u5728\u4e9a\u6d32\u3002\u6839\u636e2018\u5e7411\u67082\u65e5\u7684\u4e16\u754c\u5730\u56fe\u96c6\u7edf\u8ba1\uff0c\u8fd98\u5ea7\u57ce\u5e02\u6309\u4eba\u53e3\u6570\u91cf\u4ece\u591a\u5230\u5c11\u6392\u5e8f\u5206\u522b\u662f\uff1a\u4e0a\u6d77\uff082410\u4e07\uff09\uff0c\u5317\u4eac\uff081850\u4e07\uff09\uff0c\u5df4\u57fa\u65af\u5766\u5361\u62c9\u5947\uff081800\u4e07\uff09\uff0c\u5b5f\u52a0\u62c9\u56fd\u8fbe\u5361\uff081450\u4e07\uff09\uff0c\u65e5\u672c\u4e1c\u4eac\uff081360\u4e07\uff09\uff0c\u83f2\u5f8b\u5bbe\u9a6c\u5c3c\u62c9\uff081280\u4e07\uff09\uff0c\u5929\u6d25\uff081270\u4e07\uff09\u548c\u5370\u5ea6\u5b5f\u4e70\uff081240\u4e07\uff09\u3002\u8fd9\u91cc\u7edf\u8ba1\u7684\u4eba\u53e3\u6570\u91cf\u8fd8\u4e0d\u5305\u62ec\u4e3b\u57ce\u533a\u4ee5\u5916\u7684\u90ca\u533a\u4eba\u53e3\u3002\u6bd4\u5982\u83f2\u5f8b\u5bbe\u9996\u90fd\u9a6c\u5c3c\u62c9\uff0c\u5982\u679c\u5c06\u5b83\u5468\u56f4\u4eba\u53e3\u8f83\u5c11\u3001\u89c4\u6a21\u8f83\u5c0f\uff0c\u4e0e\u4e2d\u5fc3\u57ce\u5e02\u529f\u80fd\u8054\u7cfb\u7d27\u5bc6\u7684\u201c\u536b\u661f\u57ce\u5e02\u201d\u6db5\u76d6\u5728\u5185\uff0c\u90a3\u4e48\u4e00\u5171\u5927\u6982\u4f1a\u67092400\u4e07\u4eba\u53e3\u3002\u968f\u7740\u57ce\u5e02\u5316\u8fdb\u7a0b\u7684\u5feb\u901f\u8fdb\u884c\uff0c\u4ec5\u572810\u591a\u5e74\u540e\u76842030\u5e74\uff0c\u4e16\u754c\u4e0a\u5c06\u6709\u8d85\u8fc76\u6210\u4eba\u53e3\u5c45\u4f4f\u5728\u57ce\u5e02\u5f53\u4e2d\u3002\u4e0d\u65ad\u589e\u957f\u7684\u57ce\u5e02\u4eba\u53e3\u6570\u91cf\u5c06\u5bf9\u57ce\u5e02\u7684\u7ecf\u6d4e\u3001\u793e\u4f1a\u548c\u73af\u5883\u4ea7\u751f\u6df1\u8fdc\u7684\u5f71\u54cd\uff5cPixabay\u8d85\u7ea7\u57ce\u5e02\u810f\u4e71\u5dee\u8fc7\u53bb\uff0c\u4eba\u4eec\u968f\u610f\u5730\u6269\u5f20\u57ce\u5e02\uff0c\u5e76\u6ca1\u6709\u7ecf\u8fc7\u4ec0\u4e48\u89c4\u5212\u3002\u73b0\u5728\u662f\u65f6\u5019\u8ba9\u57ce\u533a\u5728\u89c4\u5212\u548c\u7ba1\u7406\u4e2d\u6210\u957f\u4e86\u3002\r\u4e0d\u7136\uff0c\u6211\u4eec\u5c06\u627f\u62c5\u57ce\u5e02\u80e1\u4e71\u6269\u5f20\u5e26\u6765\u7684\u540e\u679c\u3002\u57ce\u5e02\u89c4\u5212\u8005\u5f53\u524d\u5e94\u8be5\u62c5\u5fc3\u7684\u95ee\u9898\u975e\u5e38\u591a\uff0c\u5305\u62ec\u4eba\u53e3\u5bc6\u5ea6\u3001\u975e\u6b63\u5f0f\u5c45\u6c11\u3001\u57ce\u5e02\u8870\u9000\u3001\u516c\u5171\u7a7a\u95f4\u3001\u516c\u5171\u4ea4\u901a\u3001\u5783\u573e\u5904\u7406\u3001\u7a7a\u6c14\u6c61\u67d3\u3001\u81ea\u7136\u8d44\u6e90\u7ba1\u7406\u3001\u6c34\u8d44\u6e90\u4fdd\u62a4\u3001\u9002\u5e94\u548c\u7f13\u89e3\u6c14\u5019\u53d8\u5316\u3001\u707e\u5bb3\u98ce\u9669\u7ba1\u7406\u548c\u65e5\u76ca\u52a0\u5267\u7684\u4e0d\u5e73\u7b49\u95ee\u9898\u7b49\u3002\u00a0\u201c\u6211\u5de5\u4f5c\u751f\u6d3b\u5728\u4e9a\u6d32\u8d85\u5927\u57ce\u5e02\u83f2\u5f8b\u5bbe\u9a6c\u5c3c\u62c9\u7684\u8fb9\uff0c\u56e0\u4e3a\u4fee\u8def\uff0c\u8fd9\u5ea7\u57ce\u5e02\u5728\u8fc7\u53bb\u7684\u51e0\u4e2a\u661f\u671f\u91cc\u4ece\u65e9\u5230\u665a\u5168\u5929\u90fd\u5728\u5835\u8f66\u3002\u73b0\u5728\u6211\u4ece\u6211\u5bb6\u6240\u5728\u7684\u5c0f\u9547\u5f00\u8f66\u5230\u9a6c\u5c3c\u62c9\uff0c\u53ea\u670965\u516c\u91cc\u8def\uff0c\u8981\u5f00\u534a\u5929\u65f6\u95f4\u3002\u201d\u9a6c\u5c3c\u62c9\u73af\u4fdd\u8bb0\u8005\u514b\u91cc\u65af\u5bbe\u00b7\u9a6c\u65af\u6d1b\uff08Crispin Maslog\uff09\u8bf4\u3002\r\u201c\u6211\u7ecf\u5e38\u53bb\u5468\u56f4\u7684\u57ce\u5e02\uff0c\u6bd4\u5982\u6cf0\u56fd\u66fc\u8c37\u3001\u5370\u5ea6\u6d77\u5fb7\u62c9\u5df4\u3001\u5370\u5c3c\u96c5\u52a0\u8fbe\u548c\u67ec\u57d4\u5be8\u91d1\u8fb9\uff0c\u6211\u4e86\u89e3\u4ed6\u4eec\u5982\u4f55\u5e94\u5bf9\u57ce\u5e02\u6269\u5f20\u95ee\u9898\u3001\u4e25\u91cd\u7684\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\u548c\u6392\u6c34\u7cfb\u7edf\u95ee\u9898\uff0c\u56e0\u4e3a\u57ce\u5e02\u5783\u573e\u592a\u591a\u5835\u585e\u4e86\u4e0b\u6c34\u9053\uff0c\u4e00\u70b9\u70b9\u5c0f\u96e8\u5c31\u4f1a\u5f15\u53d1\u4e25\u91cd\u7684\u79ef\u6c34\u3002\u5370\u5c3c\u7684\u68ee\u6797\u706b\u707e\u4e5f\u5728\u5e2e\u5012\u5fd9\uff0c\u6d53\u70df\u6e10\u6e10\u8513\u5ef6\u5230\u90bb\u56fd\uff0c\u5f71\u54cd\u5230\u9a6c\u6765\u897f\u4e9a\u3001\u83f2\u5f8b\u5bbe\u3001\u65b0\u52a0\u5761\u548c\u6cf0\u56fd\u3002\u201d\u4ea4\u901a\u95ee\u9898\u53ea\u662f\u4e9a\u6d32\u57ce\u5e02\u9700\u8981\u9762\u5bf9\u7684\u8bf8\u591a\u95ee\u9898\u4e4b\u4e00\uff5cPatrick Roque, CC BY-SA 4.0\u201c\u6211\u4e5f\u4f1a\u95ee\u81ea\u5df1\uff0c\u5982\u679c\u57ce\u5e02\u771f\u7684\u53d8\u5f97\u8d8a\u6765\u8d8a\u4e0d\u5b9c\u5c45\uff0c\u73b0\u5728\u8981\u642c\u5230\u4e61\u6751\u91cc\uff0c\u4f8d\u5f04\u82b1\u8349\uff0c\u953b\u70bc\u8eab\u4f53\uff0c\u547c\u5438\u65b0\u9c9c\u7a7a\u6c14\uff0c\u8fc7\u4e0a\u8fdc\u79bb\u4ea4\u901a\u62e5\u5835\u3001\u566a\u97f3\u548c\u7a7a\u6c14\u6c61\u67d3\u7684\u751f\u6d3b\u5417\uff1f\u201d\u57ce\u5e02\u95ee\u9898\u89e3\u51b3\u6cd5\u968f\u7740\u4eba\u53e3\u9010\u6e10\u5411\u57ce\u5e02\u805a\u96c6\uff0c\u57ce\u5e02\u9010\u6e10\u6269\u5f20\uff0c\u8ba9\u57ce\u5e02\u53ef\u6301\u7eed\u5730\u6309\u89c4\u5212\u6210\u957f\u8feb\u5728\u7709\u776b\u3002\u653f\u5e9c\u5fc5\u987b\u63d0\u4f9b\u50cf\u516c\u56ed\u4e00\u6837\u7684\u516c\u5171\u7a7a\u95f4\uff0c\u73b0\u5728\u5df2\u6709\u7684\u516c\u5171\u7a7a\u95f4\u5fc5\u987b\u5f97\u5230\u4fdd\u62a4\uff0c\u4e0d\u80fd\u88ab\u5546\u4e1a\u53d1\u5c55\u5360\u7528\u3002\u7f8e\u56fd\u548c\u6b27\u6d32\u7684\u57ce\u5e02\u516c\u56ed\u4f53\u7cfb\u662f\u503c\u5f97\u4e9a\u6d32\u653f\u5e9c\u5b66\u4e60\u7684\u597d\u699c\u6837\u3002\r\u540c\u65f6\uff0c\u6211\u4eec\u5fc5\u987b\u63d0\u9ad8\u5e9f\u7269\u56de\u6536\u548c\u5faa\u73af\u5229\u7528\u7684\u6548\u7387\uff1b\u653f\u5e9c\u5fc5\u987b\u63d0\u4f9b\u66f4\u5bb9\u6613\u8d1f\u62c5\u5f97\u8d77\u7684\u4f4f\u623f\uff1b\u5fc5\u987b\u7ec4\u7ec7\u548c\u652f\u6301\u516c\u5171\u4ea4\u901a\u7684\u53d1\u5c55\uff0c\u8fd9\u6837\u5c45\u6c11\u5c31\u53ef\u4ee5\u628a\u79c1\u5bb6\u8f66\u505c\u5728\u5bb6\u91cc\uff0c\u4e58\u5750\u516c\u5171\u4ea4\u901a\u5de5\u5177\u4e0a\u73ed\uff0c\u5728\u8fd9\u65b9\u9762\u6211\u4eec\u5df2\u7ecf\u53ef\u4ee5\u5411\u9999\u6e2f\u548c\u65b0\u52a0\u5761\u5b66\u4e60\u3002\r\u8054\u5408\u56fd\u4eba\u7c7b\u5c45\u4f4f\u89c4\u5212\u7f72\uff08UN Habitat\uff09\u5df2\u7ecf\u652f\u6301\u4e86\u5f88\u591a\u4e9a\u6d32\u7684\u9879\u76ee\uff0c\u4e3a\u57ce\u5e02\u793e\u533a\u89c4\u5212\u8bbe\u8ba1\u516c\u5171\u7a7a\u95f4\u3002\u4f8b\u5982\u8054\u5408\u56fd\u4eba\u7c7b\u5c45\u4f4f\u89c4\u5212\u7f72\u4e0e\u83f2\u5f8b\u5bbe\u57ce\u5e02\u8054\u76df\uff08League of Cities of the Philippines\uff09\u3001\u8d2b\u6c11\u7a9f\u5c45\u6c11\u56fd\u9645\uff08SDI\uff09\u548c\u4e00\u4e9b\u5927\u5b66\u3001\u975e\u653f\u5e9c\u7ec4\u7ec7\u7b49\u5f53\u5730\u7ec4\u7ec7\u5408\u4f5c\u7684\u53d1\u5c55\u9879\u76ee\u3002\u5728\u793e\u533a\u91cc\u89c4\u5212\u516c\u5171\u7a7a\u95f4\uff0c\u8ba9\u793e\u533a\u5bf9\u516c\u5171\u7a7a\u95f4\u66f4\u6709\u5f52\u5c5e\u611f\uff0c\u8fd9\u4e9b\u516c\u5171\u7a7a\u95f4\u4e5f\u5f97\u5230\u4e86\u79ef\u6781\u7684\u7ba1\u7406\u3002\u89c4\u5212\u72ec\u7acb\u7684\u81ea\u7ed9\u81ea\u8db3\u7684\u57ce\u5e02\u793e\u533a\uff0c\u53ef\u4ee5\u4f7f\u4ea4\u901a\u9700\u6c42\u6700\u5c0f\u5316\uff0c\u4ece\u800c\u6d88\u9664\u4ea4\u901a\u95ee\u9898\uff5cPixabay\u73b0\u5728\uff0c\u6211\u4eec\u5e94\u8be5\u89c4\u5212\u4e00\u4e2a\u72ec\u7acb\u7684\u81ea\u7ed9\u81ea\u8db3\u7684\u57ce\u5e02\u793e\u533a\u3002\u8fd9\u79cd\u793e\u533a\u6a21\u5f0f\u8ba9\u5c45\u6c11\u5c45\u4f4f\u5728\u5de5\u4f5c\u533a\uff08\u529e\u516c\u5ba4\uff09\u3001\u5546\u4e1a\u533a\uff08\u5546\u573a\uff09\u3001\u5a31\u4e50\u8bbe\u65bd\uff08\u7535\u5f71\u9662\u3001\u4f53\u80b2\u9986\u7b49\uff09\u548c\u57fa\u7840\u8bbe\u65bd\uff08\u5b66\u6821\u548c\u533b\u9662\u7b49\uff09\u9644\u8fd1\uff0c\u4f7f\u4ea4\u901a\u9700\u6c42\u6700\u5c0f\u5316\uff0c\u4ece\u800c\u6d88\u9664\u4ea4\u901a\u95ee\u9898\u3002\u8fd8\u6709\u4e00\u79cd\u6a21\u5f0f\u662f\u91cd\u65b0\u5efa\u7acb\u4e00\u4e2a\u57ce\u5e02\u3002\u4f8b\u5982\u9a6c\u6765\u897f\u4e9a\u65b0\u7684\u8054\u90a6\u884c\u653f\u9996\u90fd\u5e03\u7279\u62c9\u518d\u4e5f\uff08\u592a\u5b50\u57ce\uff09\uff0c\u5b83\u662f\u9a6c\u6765\u897f\u4e9a\u7b2c\u4e09\u5927\u8054\u90a6\u76f4\u8f96\u533a\uff0c\u5efa\u7acb\u5728\u4e00\u57575000\u516c\u9877\u7684\u6cbc\u6cfd\u5730\u548c\u6cb9\u68d5\u56ed\u533a\u4e0a\u3002\u518d\u6bd4\u5982\uff0c\u83f2\u5f8b\u5bbe\u5219\u6b63\u5728\u89c4\u5212\u65b0\u5efa\u514b\u62c9\u514b\u57ce\u3002\u5b83\u539f\u672c\u662f\u8ddd\u79bb\u9a6c\u5c3c\u62c998\u516c\u91cc\u7684\u4e00\u4e2a\u524d\u7f8e\u56fd\u519b\u4e8b\u57fa\u5730\uff0c\u83f2\u5f8b\u5bbe\u8ba1\u5212\u5c06\u5176\u53d1\u5c55\u4e3a\u56fd\u5bb6\u672a\u6765\u7684\u7ecf\u6d4e\u4e2d\u5fc3\u3002\r\u4e3a\u4e86\u8ba9\u4e9a\u592a\u5730\u533a\u7684\u57ce\u5e02\u66f4\u9002\u5b9c\u4eba\u7c7b\u5c45\u4f4f\uff0c\u7b2c\u4e03\u5c4a\u4e9a\u592a\u57ce\u5e02\u8bba\u575b\uff08APUF-7\uff09\u4e8e2019\u5e7410\u670815-17\u65e5\u5728\u9a6c\u6765\u897f\u4e9a\u69df\u57ce\u4e3e\u529e\uff0c\u8bba\u575b\u4e3b\u9898\u4e3a\u201c\u4e9a\u592a\u57ce\u5e02\u7684\u672a\u6765\uff1a\u4e3a\u5b9e\u73b02030\u53ef\u6301\u7eed\u53d1\u5c55\u7684\u53d8\u9769\u201d\u3002\r\u4f1a\u8bae\u65e8\u5728\u63d0\u4f9b\u201c\u9762\u5411\u672a\u6765\u7684\u57ce\u5e02\u5316\u65b9\u6848\u201d\uff0c\u5728\u56fd\u5bb6\u548c\u5730\u65b9\u653f\u5e9c\u9886\u5bfc\u3001\u653f\u7b56\u5236\u5b9a\u8005\u3001\u79c1\u4eba\u673a\u6784\u3001\u5b66\u8005\u548c\u6c11\u95f4\u56e2\u4f53\u7684\u5e2e\u52a9\u4e0b\uff0c\u5bfb\u627e\u57ce\u5e02\u6240\u9762\u4e34\u7684\u751f\u5b58\u95ee\u9898\u7684\u7b54\u6848\u3002\u7136\u800c\u4e0d\u7ba1\u613f\u666f\u6709\u591a\u7f8e\u597d\uff0c\u4e9a\u592a\u57ce\u5e02\u8bba\u575b\u4e5f\u65e0\u6cd5\u5728\u4e00\u591c\u4e4b\u95f4\u89e3\u51b3\u4e9a\u6d32\u57ce\u5e02\u4e0d\u5b9c\u5c45\u7684\u95ee\u9898\u3002\u4e0d\u8fc7\uff0c\u8fd9\u6b21\u4f1a\u8bae\u81f3\u5c11\u4f1a\u5e26\u6765\u4e00\u79cd\u7d27\u8feb\u611f\uff0c\u8981\u6c42\u57ce\u5e02\u5728\u89c4\u5212\u4e2d\u751f\u957f\uff0c\u9632\u6b62\u5b83\u4eec\u80e1\u4e71\u6269\u5f20\u3002\u57ce\u5e02\u8981\u5728\u89c4\u5212\u4e2d\u751f\u957f\uff0c\u907f\u514d\u80e1\u4e71\u6269\u5f20\uff5cPixabay\u540c\u65f6\uff0c\u8054\u5408\u56fd\u7684\u89c4\u5212\u8005\u4eec\u8fd8\u5e94\u5f53\u63a5\u53d7\u4e00\u4e2a\u4e8b\u5b9e\uff1a\u5730\u7403\u5c31\u8fd9\u4e48\u5927\uff0c\u4eba\u53e3\u6570\u91cf\u5374\u5728\u4e0d\u52a0\u9650\u5236\u5730\u589e\u957f\u3002\u4e0e\u5176\u5355\u5355\u8fdb\u884c\u57ce\u5e02\u89c4\u5212\uff0c\u4e0d\u5982\u5c06\u63a7\u5236\u4eba\u53e3\u6570\u91cf\u4e5f\u7eb3\u5165\u8054\u5408\u56fd\u653f\u7b56\uff1f\uff08\u8bd1\u8005\uff1a\u793e\u4f1a\u5c0f\u7070\u74f6\uff0c\u7f16\u8f91\uff1a\u60b2\u50ac\u7684\u94ca\u5b9d\u5b9d\uff0c\u5ba1\u7a3f\uff1aYuki\uff09\u9898\u56fe\u6765\u6e90\uff1aPixabay\u539f\u6587\u94fe\u63a5\uff1ahttps://www.scidev.net/global/cities/columns/asia-pacific-analysis-living-in-asia-s-unliveable-cities-1.html\uff08\u672c\u6587\u7531storythings\u4f9b\u7a3f\uff09", "title": "\u810f\u3001\u4e71\u3001\u5835\uff0c\u4e9a\u6d32\u8d85\u7ea7\u57ce\u5e02\u7684\u51fa\u8def\u5728\u54ea\u91cc\uff1f", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "", "normal": "", "small": ""}, "introduction": "", "nickname": "Inga Vesper", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "Inga Vesper", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [], "content": "<p><span style=\"font-size:14px\"><span style=\"color:#808080\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<p>\u5e74\u521d\u5f00\u64ad\u4e86\u4e00\u90e8\u533b\u7597\u7eaa\u5f55\u7247\u300a\u4eba\u95f4\u4e16\u7b2c\u4e8c\u5b63\u300b\uff0c\u6709\u4e00\u96c6\u7684\u540d\u5b57\u5f88\u7f8e&mdash;&mdash;\u70df\u82b1\u3002\u955c\u5934\u5bf9\u51c6\u4e86\u51e0\u4f4d\u60a3\u6709\u6076\u6027\u9aa8\u80bf\u7624\u7684\u5c0f\u670b\u53cb\u548c\u4ed6\u4eec\u7684\u5bb6\u5ead\uff0c\u4ed6\u4eec\u7fbd\u7ffc\u672a\u4e30\u5374\u75c5\u9b54\u7f20\u8eab\uff0c\u4ed6\u4eec\u7684\u751f\u547d\u72b9\u5982\u70df\u82b1\u822c\u7eda\u70c2\u5374\u77ed\u6682\uff0c\u8ba9\u4eba\u4e0d\u7981\u6f78\u7136\u6cea\u4e0b\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/S18TgY3jCBICJON3UFw7G_nV3mfFUoVDfWJ4e1Q_3S2AAQAA2AAAAEdJ.gif\" style=\"height:216px; width:384px\" />\u7ed9\u81ea\u5df1\u653e\u4e00\u4e2a\u70df\u82b1\uff5c\u300a\u4eba\u95f4\u4e16\u7b2c\u4e8c\u5b63\u300b</p>\r\n\r\n<p>\u513f\u7ae5\u764c\u75c7\u662f\u4e00\u7c7b\u76f8\u5bf9\u7f55\u89c1\u7684\u75be\u75c5\uff0c\u76ee\u524d\u5168\u4e16\u754c\u8303\u56f4\u5185\u53d1\u75c5\u7387\u5728\u6bcf\u767e\u4e07\u4eba50~200\u4f8b\u3002\u5176\u4e2d\u6700\u4e3a\u5e38\u89c1\u7684\u764c\u75c7\u7c7b\u578b\u662f\u8840\u764c\u3001\u8111\u764c\u548c\u6dcb\u5df4\u7624\u3002\u7531\u4e8e\u513f\u7ae5\u4ecd\u5904\u4e8e\u751f\u957f\u53d1\u80b2\u9636\u6bb5\uff0c<strong>\u6210\u5e74\u4eba\u764c\u75c7\u4e2d\u5e38\u89c4\u7684\u5316\u7597\u3001\u653e\u7597\u7b49\u6cbb\u7597\u624b\u6bb5\uff0c\u4f1a\u7ed9\u5b69\u5b50\u4eec\u5e26\u6765\u4e00\u4e9b\u6210\u5e74\u4eba\u4e0d\u4f1a\u51fa\u73b0\u7684\u4e0d\u826f\u53cd\u5e94</strong>\u3002\u5e76\u4e14\u7531\u4e8e\u513f\u7ae5\u764c\u75c7\u60a3\u513f\u5e74\u9f84\u5c0f\uff0c\u6cbb\u7597\u5bf9\u4ed6\u4eec\u8eab\u4f53\u5e26\u6765\u7684\u5f71\u54cd\u8fdc\u6bd4\u5bf9\u6210\u5e74\u60a3\u8005\u8981\u6df1\u8fdc\u5f97\u591a\uff0c\u6bd4\u5982\uff1a\u4e0d\u5b55\u3001\u53d1\u80b2\u4e0d\u5168\u3001\u5fc3\u810f\u635f\u5bb3\u3001\u51fa\u73b0\u7b2c\u4e8c\u79cd\u764c\u75c7\uff0c\u4ee5\u53ca\u793e\u4f1a\u5fc3\u7406\u5c42\u9762\u7684\u5f71\u54cd\u3002</p>\r\n\r\n<p>\u6b63\u662f\u7531\u4e8e\u764c\u75c7\u5bf9\u513f\u7ae5\u60a3\u8005\u5e26\u6765\u5f71\u54cd\u5177\u6709\u957f\u671f\u6027\uff0c\u6700\u8fd1\u4e00\u9879\u53d1\u8868\u5728\u300a\u67f3\u53f6\u5200&middot;\u80bf\u7624\u5b66\u300b\u7684\u7814\u7a76\u8bba\u6587\u4ee5<strong>\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74</strong>\uff08disability-adjusted life years \uff0cDALYs\uff09\u4e3a\u6307\u6807\uff0c\u6765\u8bc4\u4ef7\u5168\u7403\u513f\u7ae5\u764c\u75c7\u8d1f\u62c5\u3002\u8389\u8428&middot;\u798f\u65af\uff08Lisa Force\uff09\u662f\u6765\u81ea\u7f8e\u56fd\u5b5f\u83f2\u65af\u5723\u6731\u8fea\u513f\u7ae5\u7814\u7a76\u533b\u9662\u7684\u4e34\u5e8a\u5b66\u8005\uff0c\u4e5f\u662f\u8fd9\u9879\u7814\u7a76\u7684\u5171\u540c\u4f5c\u8005\uff0c\u5979\u6307\u51fa\uff0c\u8fd9\u4e00\u65b9\u6cd5\u5c06\u5e2e\u52a9\u4eba\u4eec\u5168\u65b9\u4f4d\u5730\u4e86\u89e3\u764c\u75c7\u7ed9\u5168\u7403\u513f\u7ae5\u5e26\u6765\u7684\u60ca\u4eba\u5f71\u54cd\u3002</p>\r\n\r\n<p>\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u662f\u8ba1\u7b97\u75be\u75c5\u8d1f\u62c5\u7684\u4e00\u4e2a\u6307\u6807\uff0c\u7528\u6765\u4f30\u7b97\u513f\u7ae5\u548c\u9752\u5c11\u5e74\u764c\u75c7\u60a3\u8005\u56e0\u4e3a\u75be\u75c5\u3001\u6b8b\u75be\u548c\u8fc7\u65e9\u6b7b\u4ea1\u800c\u5931\u53bb\u7684\u5065\u5eb7\u5bff\u547d\u5e74\u9650\u3002</p>\r\n\r\n<h2><strong>\u8d2b\u56f0\u56fd\u5bb6\u7684\u5b69\u5b50\u4eec\u627f\u53d7\u66f4\u591a\u75db\u82e6</strong></h2>\r\n\r\n<p>\u7814\u7a76\u7ed3\u679c\u663e\u793a\uff0c2017\u5e74\u5168\u7403\u8303\u56f4\u5185\uff0c\u764c\u75c7\u593a\u53bb\u4e86142300\u540d\u513f\u7ae5\u7684\u751f\u547d\uff0c\u9020\u6210\u7684\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u8fbe1150\u4e07\u5e74\uff0c\u8fd9\u4e00\u6570\u636e\u4f7f\u5f97\u513f\u7ae5\u764c\u75c7\u5728\u5168\u7403\u6240\u6709\u764c\u75c7\u8d1f\u62c5\u4e2d\u6392\u540d\u7b2c\u516d\uff0c\u5728\u6240\u6709\u513f\u7ae5\u75be\u75c5\u8d1f\u62c5\u4e2d\u6392\u540d\u7b2c\u4e5d\u3002\u800c\u53d1\u5c55\u4e2d\u56fd\u5bb6\u513f\u7ae5\u764c\u75c7\u7684\u75be\u75c5\u8d1f\u62c5\u66f4\u662f\u51fa\u5947\u7684\u9ad8\u3002\u7531\u4e8e\u7f3a\u4e4f\u65e9\u671f\u68c0\u6d4b\u624b\u6bb5\uff0c\u4e14\u6cbb\u7597\u65b9\u6cd5\u975e\u5e38\u6709\u9650\uff0c\u4f4e\u6536\u5165\u548c\u4e2d\u4f4e\u6536\u5165\u56fd\u5bb6\u513f\u7ae5\u764c\u75c7\u8d1f\u62c5\u5360\u5230\u4e86\u5168\u7403\u8d1f\u62c5\u768482%\uff0c<strong>\u7b49\u540c\u4e8e\u5931\u53bb\u4e86700\u4e07\u5e74\u7684\u5065\u5eb7\u5bff\u547d\u5e74</strong>\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/JCC1ZYYeFdn8a4BR_GNue8X4fPD9trqTtWboqcTOkW-AAwAA6AUAAEpQ.jpg\" style=\"height:1080px; width:640px\" />\u4f4e\u6536\u5165\u548c\u4e2d\u4f4e\u6536\u5165\u56fd\u5bb6\u627f\u62c5\u7740\u5168\u7403\u513f\u7ae5\u764c\u75c7\u8d1f\u62c5\u768482%\uff0c\u8fd9\u7b49\u4ef7\u4e8e\u5931\u53bb\u4e86700\u4e07\u5e74\u7684\u5065\u5eb7\u5bff\u547d\u5e74\uff5c\u300a\u4eba\u95f4\u4e16\u7b2c\u4e8c\u5b63\u300b</p>\r\n\r\n<p>\u6b64\u5916\uff0c\u6765\u81ea\u4f4e\u6536\u5165\u6216\u4e2d\u4f4e\u6536\u5165\u56fd\u5bb6\u7684\u513f\u7ae5\u764c\u75c7\u60a3\u8005\u7684\u751f\u5b58\u7387\u663e\u8457\u4f4e\u4e8e\u53d1\u8fbe\u56fd\u5bb6\uff0c\u8bca\u65ad\u540e\u4e94\u5e74\u751f\u5b58\u7387\u4f4e\u4e8e40%\u3002\u800c\u5728\u5bcc\u88d5\u56fd\u5bb6\uff0c\u513f\u7ae5\u5728\u53d1\u73b0\u764c\u75c7\u540e\u4e94\u5e74\u751f\u5b58\u7387\u53ef\u8fbe80%\u3002\u5355\u770b2017\u5e74\u4e00\u5e74\u7684\u6570\u636e\uff0c\u5177\u6709\u4f4e\u6216\u4f4e\u5230\u4e2d\u7b49\u793e\u4f1a\u4eba\u53e3\u5b66\u6307\u6570\u7684\u56fd\u5bb6\u4e2d\uff0c\u5171\u6709159600\u4f8b\u513f\u7ae5\u764c\u75c7\u65b0\u8bca\u65ad\u75c5\u4f8b\uff0c\u5360\u5168\u7403\u768438%\uff0c\u4f46\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u5374\u5360\u4e86\u5168\u7403\u768460%\u3002</p>\r\n\r\n<p>\u5728\u5168\u7403\u4e94\u5341\u4e2a\u4eba\u53e3\u6700\u591a\u7684\u56fd\u5bb6\u4e2d\uff0c\u5370\u5ea6\u3001\u4e2d\u56fd\u3001\u5c3c\u65e5\u5229\u4e9a\u3001\u5df4\u57fa\u65af\u5766\u548c\u5370\u5ea6\u5c3c\u897f\u4e9a\u7684\u513f\u7ae5\u764c\u75c7\u8d1f\u62c5\u5c24\u5176\u9ad8\u3002\u6492\u54c8\u62c9\u4ee5\u5357\u7684\u975e\u6d32\u5730\u533a\u5728\u7814\u7a76\u6d89\u53ca\u7684\u5341\u79cd\u7c7b\u578b\u764c\u75c7\u4e2d\uff0c\u6709\u516d\u79cd\u90fd\u6392\u540d\u7b2c\u4e00\u3002</p>\r\n\r\n<h2><strong>\u53d1\u5c55\u4e2d\u56fd\u5bb6\u9762\u4e34\u91cd\u91cd\u969c\u788d</strong></h2>\r\n\r\n<p>\u90a3\u4e48\u4e3a\u4f55\u53d1\u5c55\u4e2d\u56fd\u5bb6\u4f1a\u51fa\u73b0\u8fd9\u79cd\u513f\u7ae5\u764c\u75c7\u9ad8\u8d1f\u62c5\u3001\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u663e\u8457\u9ad8\u4e8e\u53d1\u8fbe\u56fd\u5bb6\u7684\u73b0\u8c61\u5462\uff1f\u6765\u81ea\u6fb3\u5927\u5229\u4e9a\u6089\u5c3c\u5927\u5b66\u533b\u5b66\u9662\u52a9\u7406\u6559\u6388\u4e9a\u5386\u5c71\u5fb7\u62c9&middot;\u9a6c\u4e01\u7ebd\u514b\uff08Alexandra Martiniuk\uff09\u89e3\u91ca\u9053\uff0c\u5728\u9488\u5bf9\u513f\u7ae5\u764c\u75c7\u7684\u533b\u7597\u4e2d\uff0c\u53d1\u5c55\u4e2d\u56fd\u5bb6\u9762\u4e34\u7740\u591a\u91cd\u963b\u788d\uff0c\u5176\u4e2d\u5305\u62ec\u4e13\u4e1a\u4eba\u5458\u8bad\u7ec3\u4e0d\u8db3\u4ee5\u53ca\u8bca\u65ad\u82b1\u8d39\u8fc7\u4e8e\u9ad8\u6602\u3002\u4e0e\u6b64\u540c\u65f6\uff0c<strong>\u9488\u5bf9\u7279\u5b9a\u75c7\u72b6\u662f\u5426\u613f\u610f\u9009\u62e9\u5bfb\u6c42\u533b\u7597\u5e2e\u52a9\uff0c\u4ee5\u53ca\u662f\u5426\u5177\u6709\u8db3\u591f\u8d22\u529b\u5bfb\u6c42\u533b\u7597\u5e2e\u52a9\u4e5f\u540c\u6837\u662f\u53d1\u5c55\u4e2d\u56fd\u5bb6\u9762\u4e34\u7684\u96be\u9898</strong>\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/bg6HY54SKfAl-QIqI-KJ-l_8CMFLCEKR-F2a2-0DnOKAAwAA-AEAAEpQ.jpg\" style=\"height:360px; width:640px\" />\u60a3\u513f\u624b\u81c2\u4e0a\u5199\u7684&ldquo;Yes!&rdquo; \uff5c\u300a\u4eba\u95f4\u4e16\u7b2c\u4e8c\u5b63\u300b</p>\r\n\r\n<p>\u6765\u81ea\u5362\u65fa\u8fbe\u5e03\u5854\u6d1b\u533b\u9662\u7684\u8d1f\u8d23\u4eba\u6492\u897f\u65af&middot;\u739b\u9e4f\u52a0\uff08Tharcisse Mpunga\uff09\u8ba4\u4e3a\uff0c\u4e0d\u80fd\u7ed9\u51fa\u53ca\u65f6\u3001\u51c6\u786e\u7684\u8bca\u65ad\u662f\u5176\u4e2d\u6700\u5927\u7684\u95ee\u9898\u3002\u8fd9\u79cd\u6ede\u540e\u65e2\u53d1\u751f\u5728\u793e\u4f1a\u5c42\u9762\uff0c\u4e5f\u53d1\u751f\u5728\u533b\u7597\u7cfb\u7edf\u4e2d\u3002<strong>\u5305\u62ec\u7531\u7ecf\u6d4e\u5236\u7ea6\u5bfc\u81f4\u7684\u7f3a\u4e4f\u968f\u8bbf\u6216\u653e\u5f03\u6cbb\u7597\uff0c\u8fd8\u6709\u56e0\u836f\u7269\u6bd2\u6027\u5bfc\u81f4\u7684\u9ad8\u590d\u53d1\u7387\u548c\u6b7b\u4ea1\u7387</strong>\u3002</p>\r\n\r\n<p>\u7c7b\u4f3c\u7684\u95ee\u9898\u5728\u65b0\u53d1\u8868\u7684\u7814\u7a76\u4e2d\u4e5f\u6709\u6240\u4f53\u73b0\uff0c\u5168\u7403\u62a5\u9053\u7684\u513f\u7ae5\u764c\u75c7\u4e2d\uff0c<strong>26%\u88ab\u8ba4\u5b9a\u4e3a&ldquo;\u672a\u5206\u7c7b&rdquo;</strong>\u3002\u8fd9\u662f\u7531\u4e8e\u76ee\u524d\u7edd\u5927\u591a\u6570\u9488\u5bf9\u513f\u7ae5\u764c\u75c7\u7684\u4fe1\u606f\u6536\u96c6\uff0c\u91c7\u7528\u7684\u662f\u9488\u5bf9\u6210\u4eba\u764c\u75c7\u5f00\u53d1\u7684\u62a5\u544a\u7cfb\u7edf\u3002\u51c6\u786e\u3001\u5b8c\u6574\u4fe1\u606f\u7684\u7f3a\u4e4f\uff0c\u4e3a\u5f00\u5c55\u9002\u5f53\u7684\u533b\u7597\u548c\u653f\u7b56\u4ecb\u5165\u5e26\u6765\u4e86\u56f0\u96be\u3002\u8fd9\u4e5f\u5c31\u5bfc\u81f4\u4e86\u76f8\u6bd4\u4efb\u4f55\u7c7b\u578b\u7684\u6210\u4eba\u764c\u75c7\u6765\u8bb2\uff0c\u513f\u7ae5\u764c\u75c7\u90fd\u5177\u6709\u6700\u5927\u7684\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u3002</p>\r\n\r\n<p>\u6765\u81ea\u6089\u5c3c\u5927\u5b66\u7684\u533b\u5b66\u751f\u5185\u54c8&middot;\u6cd5\u9c81\u57fa\uff08Neha Faruqui\uff09\u5bf9\u5370\u5ea6\u65b0\u5fb7\u91cc\u513f\u7ae5\u764c\u75c7\u7684\u6cbb\u7597\u8fdb\u884c\u4e86\u7814\u7a76\u3002\u5979\u53d1\u73b0\u8bb8\u591a\u4f4e\u6536\u5165\u3001\u4e2d\u4f4e\u6536\u5165\u56fd\u5bb6\u7684\u653f\u5e9c\u5728\u9762\u5bf9\u7a76\u7adf\u628a\u82b1\u94b1\u5728\u7f55\u89c1\u75be\u75c5\uff08\u6bd4\u5982\u513f\u7ae5\u764c\u75c7\uff09\u7684\u8bca\u65ad\u548c\u6cbb\u7597\u8fd8\u662f\u66f4\u52a0\u5e38\u89c1\u7684\u75be\u75c5\u4e0a\uff0c\u9762\u4e34\u7740\u8270\u96be\u7684\u6289\u62e9\u3002</p>\r\n\r\n<p>\u5927\u591a\u533a\u57df\u6027\u533b\u9662\u65e0\u6cd5\u83b7\u5f97\u8db3\u591f\u7684\u8d22\u653f\u652f\u6301\uff0c\u56e0\u6b64\u51fa\u73b0\u4e86\u5e8a\u4f4d\u77ed\u7f3a\uff0c\u65e0\u6cd5\u63d0\u4f9b\u8425\u517b\u54a8\u8be2\u7b49\u95ee\u9898\uff0c\u751a\u81f3\u8fde\u836f\u7269\u7684\u7a33\u5b9a\u4f9b\u5e94\u90fd\u65e0\u6cd5\u4fdd\u8bc1\u3002\u533b\u7597\u8d44\u6e90\u5f80\u5f80\u53ea\u96c6\u4e2d\u5728\u5c11\u6570\u5927\u57ce\u5e02\uff0c\u8fd9\u79cd\u5730\u7406\u4e0a\u7684\u4e0d\u5e73\u8861\u540c\u6837\u662f\u5b69\u5b50\u4eec\u65e0\u6cd5\u83b7\u5f97\u6cbb\u7597\u7684\u969c\u788d\u4e4b\u4e00\u3002</p>\r\n\r\n<p>\u5b69\u5b50\uff0c\u662f\u6211\u4eec\u7684\u672a\u6765\u3002\u53ea\u6709\u5145\u8db3\u7684\u793e\u4f1a\u548c\u533b\u7597\u8d44\u6e90\u7684\u652f\u6301\uff0c\u4e43\u81f3\u6574\u4e2a\u56fd\u5bb6\u7ecf\u6d4e\u7684\u53d1\u5c55\u624d\u80fd\u591f\u8ba9\u4ed6\u4eec\u83b7\u5f97\u53ca\u65f6\u6709\u6548\u5730\u6cbb\u7597\u3002\u613f\u6bcf\u4e2a\u5c0f\u5929\u4f7f\u90fd\u53ef\u4ee5\u8fdc\u79bb\u75c5\u75db\uff0c\u5065\u5eb7\u6210\u957f\u3002\uff08\u7f16\u8bd1\uff1a\u90ed\u603f\u6684\uff0c\u7f16\u8f91\uff1a\u60b2\u50ac\u7684\u94ca\u5b9d\u5b9d\uff0c\u5ba1\u7a3f\uff1aYuki\uff09</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#808080\">\u9898\u56fe\u6765\u6e90\uff1aPixabay</span></span></p>\r\n\r\n<h2 class=\"references\">\u53c2\u8003\u8d44\u6599</h2>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">1.https://www.who.int/cancer/media/news/Childhood_cancer_day/en/</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">2.https://www.merckmanuals.com/professional/pediatrics/pediatric-cancers/overview-of-pediatric-cancer</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">\u6587\u7ae0\u6765\u6e90\uff1ahttps://www.scidev.net/global/children/news/childhood-cancer-claims-7-million-years-of-life-in-poorer-nations.html\uff08\u672c\u6587\u7531storythings\u4f9b\u7a3f\uff09</span></span></div>", "date_created": "2019-10-30T11:04:11.965951+08:00", "date_modified": "2019-10-30T11:48:38.963018+08:00", "date_published": "2019-10-30T16:11:12.042582+08:00", "id": 454943, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": null, "replies_count": 2, "small_image": "https://3-im.guokr.com/-C7Ih3qOKRrYvU9lVRvwGN6h0MdA_Gan0ZC4zxPxyMsABQAAfQMAAEpQ.jpg", "subject": {"articles_count": 423, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "society", "minisite_key": "None", "name": "\u793e\u4f1a", "sort_score": 21, "subject_type": "normal"}, "subject_key": "society", "summary": "\u613f\u6bcf\u4e2a\u5c0f\u5929\u4f7f\u90fd\u53ef\u4ee5\u8fdc\u79bb\u75c5\u75db\uff0c\u5065\u5eb7\u6210\u957f\u3002", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u5e74\u521d\u5f00\u64ad\u4e86\u4e00\u90e8\u533b\u7597\u7eaa\u5f55\u7247\u300a\u4eba\u95f4\u4e16\u7b2c\u4e8c\u5b63\u300b\uff0c\u6709\u4e00\u96c6\u7684\u540d\u5b57\u5f88\u7f8e\u2014\u2014\u70df\u82b1\u3002\u955c\u5934\u5bf9\u51c6\u4e86\u51e0\u4f4d\u60a3\u6709\u6076\u6027\u9aa8\u80bf\u7624\u7684\u5c0f\u670b\u53cb\u548c\u4ed6\u4eec\u7684\u5bb6\u5ead\uff0c\u4ed6\u4eec\u7fbd\u7ffc\u672a\u4e30\u5374\u75c5\u9b54\u7f20\u8eab\uff0c\u4ed6\u4eec\u7684\u751f\u547d\u72b9\u5982\u70df\u82b1\u822c\u7eda\u70c2\u5374\u77ed\u6682\uff0c\u8ba9\u4eba\u4e0d\u7981\u6f78\u7136\u6cea\u4e0b\u3002\u7ed9\u81ea\u5df1\u653e\u4e00\u4e2a\u70df\u82b1\uff5c\u300a\u4eba\u95f4\u4e16\u7b2c\u4e8c\u5b63\u300b\u513f\u7ae5\u764c\u75c7\u662f\u4e00\u7c7b\u76f8\u5bf9\u7f55\u89c1\u7684\u75be\u75c5\uff0c\u76ee\u524d\u5168\u4e16\u754c\u8303\u56f4\u5185\u53d1\u75c5\u7387\u5728\u6bcf\u767e\u4e07\u4eba50~200\u4f8b\u3002\u5176\u4e2d\u6700\u4e3a\u5e38\u89c1\u7684\u764c\u75c7\u7c7b\u578b\u662f\u8840\u764c\u3001\u8111\u764c\u548c\u6dcb\u5df4\u7624\u3002\u7531\u4e8e\u513f\u7ae5\u4ecd\u5904\u4e8e\u751f\u957f\u53d1\u80b2\u9636\u6bb5\uff0c\u6210\u5e74\u4eba\u764c\u75c7\u4e2d\u5e38\u89c4\u7684\u5316\u7597\u3001\u653e\u7597\u7b49\u6cbb\u7597\u624b\u6bb5\uff0c\u4f1a\u7ed9\u5b69\u5b50\u4eec\u5e26\u6765\u4e00\u4e9b\u6210\u5e74\u4eba\u4e0d\u4f1a\u51fa\u73b0\u7684\u4e0d\u826f\u53cd\u5e94\u3002\u5e76\u4e14\u7531\u4e8e\u513f\u7ae5\u764c\u75c7\u60a3\u513f\u5e74\u9f84\u5c0f\uff0c\u6cbb\u7597\u5bf9\u4ed6\u4eec\u8eab\u4f53\u5e26\u6765\u7684\u5f71\u54cd\u8fdc\u6bd4\u5bf9\u6210\u5e74\u60a3\u8005\u8981\u6df1\u8fdc\u5f97\u591a\uff0c\u6bd4\u5982\uff1a\u4e0d\u5b55\u3001\u53d1\u80b2\u4e0d\u5168\u3001\u5fc3\u810f\u635f\u5bb3\u3001\u51fa\u73b0\u7b2c\u4e8c\u79cd\u764c\u75c7\uff0c\u4ee5\u53ca\u793e\u4f1a\u5fc3\u7406\u5c42\u9762\u7684\u5f71\u54cd\u3002\u6b63\u662f\u7531\u4e8e\u764c\u75c7\u5bf9\u513f\u7ae5\u60a3\u8005\u5e26\u6765\u5f71\u54cd\u5177\u6709\u957f\u671f\u6027\uff0c\u6700\u8fd1\u4e00\u9879\u53d1\u8868\u5728\u300a\u67f3\u53f6\u5200\u00b7\u80bf\u7624\u5b66\u300b\u7684\u7814\u7a76\u8bba\u6587\u4ee5\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\uff08disability-adjusted life years \uff0cDALYs\uff09\u4e3a\u6307\u6807\uff0c\u6765\u8bc4\u4ef7\u5168\u7403\u513f\u7ae5\u764c\u75c7\u8d1f\u62c5\u3002\u8389\u8428\u00b7\u798f\u65af\uff08Lisa Force\uff09\u662f\u6765\u81ea\u7f8e\u56fd\u5b5f\u83f2\u65af\u5723\u6731\u8fea\u513f\u7ae5\u7814\u7a76\u533b\u9662\u7684\u4e34\u5e8a\u5b66\u8005\uff0c\u4e5f\u662f\u8fd9\u9879\u7814\u7a76\u7684\u5171\u540c\u4f5c\u8005\uff0c\u5979\u6307\u51fa\uff0c\u8fd9\u4e00\u65b9\u6cd5\u5c06\u5e2e\u52a9\u4eba\u4eec\u5168\u65b9\u4f4d\u5730\u4e86\u89e3\u764c\u75c7\u7ed9\u5168\u7403\u513f\u7ae5\u5e26\u6765\u7684\u60ca\u4eba\u5f71\u54cd\u3002\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u662f\u8ba1\u7b97\u75be\u75c5\u8d1f\u62c5\u7684\u4e00\u4e2a\u6307\u6807\uff0c\u7528\u6765\u4f30\u7b97\u513f\u7ae5\u548c\u9752\u5c11\u5e74\u764c\u75c7\u60a3\u8005\u56e0\u4e3a\u75be\u75c5\u3001\u6b8b\u75be\u548c\u8fc7\u65e9\u6b7b\u4ea1\u800c\u5931\u53bb\u7684\u5065\u5eb7\u5bff\u547d\u5e74\u9650\u3002\u8d2b\u56f0\u56fd\u5bb6\u7684\u5b69\u5b50\u4eec\u627f\u53d7\u66f4\u591a\u75db\u82e6\u7814\u7a76\u7ed3\u679c\u663e\u793a\uff0c2017\u5e74\u5168\u7403\u8303\u56f4\u5185\uff0c\u764c\u75c7\u593a\u53bb\u4e86142300\u540d\u513f\u7ae5\u7684\u751f\u547d\uff0c\u9020\u6210\u7684\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u8fbe1150\u4e07\u5e74\uff0c\u8fd9\u4e00\u6570\u636e\u4f7f\u5f97\u513f\u7ae5\u764c\u75c7\u5728\u5168\u7403\u6240\u6709\u764c\u75c7\u8d1f\u62c5\u4e2d\u6392\u540d\u7b2c\u516d\uff0c\u5728\u6240\u6709\u513f\u7ae5\u75be\u75c5\u8d1f\u62c5\u4e2d\u6392\u540d\u7b2c\u4e5d\u3002\u800c\u53d1\u5c55\u4e2d\u56fd\u5bb6\u513f\u7ae5\u764c\u75c7\u7684\u75be\u75c5\u8d1f\u62c5\u66f4\u662f\u51fa\u5947\u7684\u9ad8\u3002\u7531\u4e8e\u7f3a\u4e4f\u65e9\u671f\u68c0\u6d4b\u624b\u6bb5\uff0c\u4e14\u6cbb\u7597\u65b9\u6cd5\u975e\u5e38\u6709\u9650\uff0c\u4f4e\u6536\u5165\u548c\u4e2d\u4f4e\u6536\u5165\u56fd\u5bb6\u513f\u7ae5\u764c\u75c7\u8d1f\u62c5\u5360\u5230\u4e86\u5168\u7403\u8d1f\u62c5\u768482%\uff0c\u7b49\u540c\u4e8e\u5931\u53bb\u4e86700\u4e07\u5e74\u7684\u5065\u5eb7\u5bff\u547d\u5e74\u3002\u4f4e\u6536\u5165\u548c\u4e2d\u4f4e\u6536\u5165\u56fd\u5bb6\u627f\u62c5\u7740\u5168\u7403\u513f\u7ae5\u764c\u75c7\u8d1f\u62c5\u768482%\uff0c\u8fd9\u7b49\u4ef7\u4e8e\u5931\u53bb\u4e86700\u4e07\u5e74\u7684\u5065\u5eb7\u5bff\u547d\u5e74\uff5c\u300a\u4eba\u95f4\u4e16\u7b2c\u4e8c\u5b63\u300b\u6b64\u5916\uff0c\u6765\u81ea\u4f4e\u6536\u5165\u6216\u4e2d\u4f4e\u6536\u5165\u56fd\u5bb6\u7684\u513f\u7ae5\u764c\u75c7\u60a3\u8005\u7684\u751f\u5b58\u7387\u663e\u8457\u4f4e\u4e8e\u53d1\u8fbe\u56fd\u5bb6\uff0c\u8bca\u65ad\u540e\u4e94\u5e74\u751f\u5b58\u7387\u4f4e\u4e8e40%\u3002\u800c\u5728\u5bcc\u88d5\u56fd\u5bb6\uff0c\u513f\u7ae5\u5728\u53d1\u73b0\u764c\u75c7\u540e\u4e94\u5e74\u751f\u5b58\u7387\u53ef\u8fbe80%\u3002\u5355\u770b2017\u5e74\u4e00\u5e74\u7684\u6570\u636e\uff0c\u5177\u6709\u4f4e\u6216\u4f4e\u5230\u4e2d\u7b49\u793e\u4f1a\u4eba\u53e3\u5b66\u6307\u6570\u7684\u56fd\u5bb6\u4e2d\uff0c\u5171\u6709159600\u4f8b\u513f\u7ae5\u764c\u75c7\u65b0\u8bca\u65ad\u75c5\u4f8b\uff0c\u5360\u5168\u7403\u768438%\uff0c\u4f46\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u5374\u5360\u4e86\u5168\u7403\u768460%\u3002\u5728\u5168\u7403\u4e94\u5341\u4e2a\u4eba\u53e3\u6700\u591a\u7684\u56fd\u5bb6\u4e2d\uff0c\u5370\u5ea6\u3001\u4e2d\u56fd\u3001\u5c3c\u65e5\u5229\u4e9a\u3001\u5df4\u57fa\u65af\u5766\u548c\u5370\u5ea6\u5c3c\u897f\u4e9a\u7684\u513f\u7ae5\u764c\u75c7\u8d1f\u62c5\u5c24\u5176\u9ad8\u3002\u6492\u54c8\u62c9\u4ee5\u5357\u7684\u975e\u6d32\u5730\u533a\u5728\u7814\u7a76\u6d89\u53ca\u7684\u5341\u79cd\u7c7b\u578b\u764c\u75c7\u4e2d\uff0c\u6709\u516d\u79cd\u90fd\u6392\u540d\u7b2c\u4e00\u3002\u53d1\u5c55\u4e2d\u56fd\u5bb6\u9762\u4e34\u91cd\u91cd\u969c\u788d\u90a3\u4e48\u4e3a\u4f55\u53d1\u5c55\u4e2d\u56fd\u5bb6\u4f1a\u51fa\u73b0\u8fd9\u79cd\u513f\u7ae5\u764c\u75c7\u9ad8\u8d1f\u62c5\u3001\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u663e\u8457\u9ad8\u4e8e\u53d1\u8fbe\u56fd\u5bb6\u7684\u73b0\u8c61\u5462\uff1f\u6765\u81ea\u6fb3\u5927\u5229\u4e9a\u6089\u5c3c\u5927\u5b66\u533b\u5b66\u9662\u52a9\u7406\u6559\u6388\u4e9a\u5386\u5c71\u5fb7\u62c9\u00b7\u9a6c\u4e01\u7ebd\u514b\uff08Alexandra Martiniuk\uff09\u89e3\u91ca\u9053\uff0c\u5728\u9488\u5bf9\u513f\u7ae5\u764c\u75c7\u7684\u533b\u7597\u4e2d\uff0c\u53d1\u5c55\u4e2d\u56fd\u5bb6\u9762\u4e34\u7740\u591a\u91cd\u963b\u788d\uff0c\u5176\u4e2d\u5305\u62ec\u4e13\u4e1a\u4eba\u5458\u8bad\u7ec3\u4e0d\u8db3\u4ee5\u53ca\u8bca\u65ad\u82b1\u8d39\u8fc7\u4e8e\u9ad8\u6602\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u9488\u5bf9\u7279\u5b9a\u75c7\u72b6\u662f\u5426\u613f\u610f\u9009\u62e9\u5bfb\u6c42\u533b\u7597\u5e2e\u52a9\uff0c\u4ee5\u53ca\u662f\u5426\u5177\u6709\u8db3\u591f\u8d22\u529b\u5bfb\u6c42\u533b\u7597\u5e2e\u52a9\u4e5f\u540c\u6837\u662f\u53d1\u5c55\u4e2d\u56fd\u5bb6\u9762\u4e34\u7684\u96be\u9898\u3002\u60a3\u513f\u624b\u81c2\u4e0a\u5199\u7684\u201cYes!\u201d \uff5c\u300a\u4eba\u95f4\u4e16\u7b2c\u4e8c\u5b63\u300b\u6765\u81ea\u5362\u65fa\u8fbe\u5e03\u5854\u6d1b\u533b\u9662\u7684\u8d1f\u8d23\u4eba\u6492\u897f\u65af\u00b7\u739b\u9e4f\u52a0\uff08Tharcisse Mpunga\uff09\u8ba4\u4e3a\uff0c\u4e0d\u80fd\u7ed9\u51fa\u53ca\u65f6\u3001\u51c6\u786e\u7684\u8bca\u65ad\u662f\u5176\u4e2d\u6700\u5927\u7684\u95ee\u9898\u3002\u8fd9\u79cd\u6ede\u540e\u65e2\u53d1\u751f\u5728\u793e\u4f1a\u5c42\u9762\uff0c\u4e5f\u53d1\u751f\u5728\u533b\u7597\u7cfb\u7edf\u4e2d\u3002\u5305\u62ec\u7531\u7ecf\u6d4e\u5236\u7ea6\u5bfc\u81f4\u7684\u7f3a\u4e4f\u968f\u8bbf\u6216\u653e\u5f03\u6cbb\u7597\uff0c\u8fd8\u6709\u56e0\u836f\u7269\u6bd2\u6027\u5bfc\u81f4\u7684\u9ad8\u590d\u53d1\u7387\u548c\u6b7b\u4ea1\u7387\u3002\u7c7b\u4f3c\u7684\u95ee\u9898\u5728\u65b0\u53d1\u8868\u7684\u7814\u7a76\u4e2d\u4e5f\u6709\u6240\u4f53\u73b0\uff0c\u5168\u7403\u62a5\u9053\u7684\u513f\u7ae5\u764c\u75c7\u4e2d\uff0c26%\u88ab\u8ba4\u5b9a\u4e3a\u201c\u672a\u5206\u7c7b\u201d\u3002\u8fd9\u662f\u7531\u4e8e\u76ee\u524d\u7edd\u5927\u591a\u6570\u9488\u5bf9\u513f\u7ae5\u764c\u75c7\u7684\u4fe1\u606f\u6536\u96c6\uff0c\u91c7\u7528\u7684\u662f\u9488\u5bf9\u6210\u4eba\u764c\u75c7\u5f00\u53d1\u7684\u62a5\u544a\u7cfb\u7edf\u3002\u51c6\u786e\u3001\u5b8c\u6574\u4fe1\u606f\u7684\u7f3a\u4e4f\uff0c\u4e3a\u5f00\u5c55\u9002\u5f53\u7684\u533b\u7597\u548c\u653f\u7b56\u4ecb\u5165\u5e26\u6765\u4e86\u56f0\u96be\u3002\u8fd9\u4e5f\u5c31\u5bfc\u81f4\u4e86\u76f8\u6bd4\u4efb\u4f55\u7c7b\u578b\u7684\u6210\u4eba\u764c\u75c7\u6765\u8bb2\uff0c\u513f\u7ae5\u764c\u75c7\u90fd\u5177\u6709\u6700\u5927\u7684\u4f24\u6b8b\u8c03\u6574\u5bff\u547d\u5e74\u3002\u6765\u81ea\u6089\u5c3c\u5927\u5b66\u7684\u533b\u5b66\u751f\u5185\u54c8\u00b7\u6cd5\u9c81\u57fa\uff08Neha Faruqui\uff09\u5bf9\u5370\u5ea6\u65b0\u5fb7\u91cc\u513f\u7ae5\u764c\u75c7\u7684\u6cbb\u7597\u8fdb\u884c\u4e86\u7814\u7a76\u3002\u5979\u53d1\u73b0\u8bb8\u591a\u4f4e\u6536\u5165\u3001\u4e2d\u4f4e\u6536\u5165\u56fd\u5bb6\u7684\u653f\u5e9c\u5728\u9762\u5bf9\u7a76\u7adf\u628a\u82b1\u94b1\u5728\u7f55\u89c1\u75be\u75c5\uff08\u6bd4\u5982\u513f\u7ae5\u764c\u75c7\uff09\u7684\u8bca\u65ad\u548c\u6cbb\u7597\u8fd8\u662f\u66f4\u52a0\u5e38\u89c1\u7684\u75be\u75c5\u4e0a\uff0c\u9762\u4e34\u7740\u8270\u96be\u7684\u6289\u62e9\u3002\u5927\u591a\u533a\u57df\u6027\u533b\u9662\u65e0\u6cd5\u83b7\u5f97\u8db3\u591f\u7684\u8d22\u653f\u652f\u6301\uff0c\u56e0\u6b64\u51fa\u73b0\u4e86\u5e8a\u4f4d\u77ed\u7f3a\uff0c\u65e0\u6cd5\u63d0\u4f9b\u8425\u517b\u54a8\u8be2\u7b49\u95ee\u9898\uff0c\u751a\u81f3\u8fde\u836f\u7269\u7684\u7a33\u5b9a\u4f9b\u5e94\u90fd\u65e0\u6cd5\u4fdd\u8bc1\u3002\u533b\u7597\u8d44\u6e90\u5f80\u5f80\u53ea\u96c6\u4e2d\u5728\u5c11\u6570\u5927\u57ce\u5e02\uff0c\u8fd9\u79cd\u5730\u7406\u4e0a\u7684\u4e0d\u5e73\u8861\u540c\u6837\u662f\u5b69\u5b50\u4eec\u65e0\u6cd5\u83b7\u5f97\u6cbb\u7597\u7684\u969c\u788d\u4e4b\u4e00\u3002\u5b69\u5b50\uff0c\u662f\u6211\u4eec\u7684\u672a\u6765\u3002\u53ea\u6709\u5145\u8db3\u7684\u793e\u4f1a\u548c\u533b\u7597\u8d44\u6e90\u7684\u652f\u6301\uff0c\u4e43\u81f3\u6574\u4e2a\u56fd\u5bb6\u7ecf\u6d4e\u7684\u53d1\u5c55\u624d\u80fd\u591f\u8ba9\u4ed6\u4eec\u83b7\u5f97\u53ca\u65f6\u6709\u6548\u5730\u6cbb\u7597\u3002\u613f\u6bcf\u4e2a\u5c0f\u5929\u4f7f\u90fd\u53ef\u4ee5\u8fdc\u79bb\u75c5\u75db\uff0c\u5065\u5eb7\u6210\u957f\u3002\uff08\u7f16\u8bd1\uff1a\u90ed\u603f\u6684\uff0c\u7f16\u8f91\uff1a\u60b2\u50ac\u7684\u94ca\u5b9d\u5b9d\uff0c\u5ba1\u7a3f\uff1aYuki\uff09\u9898\u56fe\u6765\u6e90\uff1aPixabay\u53c2\u8003\u8d44\u65991.https://www.who.int/cancer/media/news/Childhood_cancer_day/en/2.https://www.merckmanuals.com/professional/pediatrics/pediatric-cancers/overview-of-pediatric-cancer\u6587\u7ae0\u6765\u6e90\uff1ahttps://www.scidev.net/global/children/news/childhood-cancer-claims-7-million-years-of-life-in-poorer-nations.html\uff08\u672c\u6587\u7531storythings\u4f9b\u7a3f\uff09", "title": "\u90a3\u4e9b\u5f97\u4e86\u764c\u75c7\u7684\u5c0f\u670b\u53cb\uff0c\u540e\u6765\u90fd\u600e\u4e48\u6837\u4e86\uff1f", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160/format/PNG", "normal": "https://1-im.guokr.com/X1xkPOsjddD8FotCpDX_OajYPhmVgAO5CzGxxwP6LqKgAAAAeAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://1-im.guokr.com/X1xkPOsjddD8FotCpDX_OajYPhmVgAO5CzGxxwP6LqKgAAAAeAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "\u5d14\u51ef", "title": "", "ukey": "bl1jig"}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160/format/PNG", "normal": "https://1-im.guokr.com/X1xkPOsjddD8FotCpDX_OajYPhmVgAO5CzGxxwP6LqKgAAAAeAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://1-im.guokr.com/X1xkPOsjddD8FotCpDX_OajYPhmVgAO5CzGxxwP6LqKgAAAAeAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "\u5d14\u51ef", "title": "", "ukey": "bl1jig"}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<p>1994\u5e74\uff0c\u7f8e\u56fd\u5b66\u8005\u5e03\u6717\u5199\u4e86\u4e00\u7bc7\u6587\u7ae0\uff0c\u9898\u76ee\u4e3a\u300a\u8c01\u6765\u517b\u6d3b\u4e2d\u56fd\u300b\uff0c\u5728\u897f\u65b9\u4e16\u754c\u5f15\u8d77\u5e7f\u6cdb\u5173\u6ce8\u300225\u5e74\u8fc7\u53bb\uff0c\u4e2d\u56fd\u7528\u5168\u4e16\u754c8%\u7684\u8015\u5730\u517b\u6d3b\u4e8618%\u7684\u4eba\u53e3\uff0c\u4f46\u662f\u5e03\u6717\u63d0\u51fa\u7684\u95ee\u9898\uff0c\u4f9d\u65e7\u503c\u5f97\u6211\u4eec\u8b66\u60d5\u30022019\u5e7410\u670814\u65e5\uff0c\u56fd\u52a1\u9662\u65b0\u95fb\u529e\u65f6\u969423\u5e74\u518d\u6b21\u53d1\u8868\u4e86\u300a\u4e2d\u56fd\u7684\u7cae\u98df\u5b89\u5168\u300b\u767d\u76ae\u4e66\uff0c\u660e\u786e\uff1a<strong>\u4ece\u4e2d\u957f\u671f\u770b\uff0c\u4e2d\u56fd\u7684\u7cae\u98df\u4ea7\u9700\u4ecd\u5c06\u7ef4\u6301\u7d27\u5e73\u8861\u6001\u52bf</strong>\u3002</p>\r\n\r\n<h2><strong>\u4e2d\u56fd\u7684\u8d44\u6e90\u7980\u8d4b</strong></h2>\r\n\r\n<p>\u6b4c\u624b\u674e\u5065\u6f14\u5531\u8fc7\u7684\u4e00\u9996\u6b4c\uff1a\u300a\u7236\u4eb2\u5199\u7684\u6563\u6587\u8bd7\u300b\uff0c\u5176\u4e2d\u6709\u4e00\u6bb5\u6734\u5b9e\u65e0\u534e\u7684\u6b4c\u8bcd\uff1a&ldquo;\u4e00\u4e5d\u516b\u56db\u5e74\uff0c\u5e84\u7a3c\u8fd8\u6ca1\u6536\u5272\u5b8c\uff0c\u513f\u5b50\u8eba\u5728\u6211\u6000\u91cc\uff0c\u7761\u5f97\u90a3\u4e48\u751c\u3002\u660e\u5929\u6211\u8981\u53bb\u90bb\u5c45\u5bb6\uff0c\u518d\u501f\u70b9\u94b1\uff0c\u5b69\u5b50\u54ed\u4e86\u4e00\u6574\u5929\u4e86\uff0c\u95f9\u7740\u8981\u5403\u997c\u5e72\u3002\u8fd9\u662f\u6211\u7236\u4eb2\u65e5\u8bb0\u91cc\u7684\u6587\u5b57\uff0c\u8fd9\u662f\u4ed6\u7684\u9752\u6625\u7559\u4e0b\u6765\u7684\u6563\u6587\u8bd7\u3002<strong>\u8fd9\u7247\u571f\u5730\u66fe\u8ba9\u6211\u6cea\u6d41\u4e0d\u6b62\uff0c\u5b83\u57cb\u846c\u4e86\u591a\u5c11\u4eba\u5fc3\u9178\u7684\u5f80\u4e8b</strong>\u3002&rdquo;</p>\r\n\r\n<p>\u6211\u4eec\u628a\u65f6\u95f4\u5750\u6807\u5c31\u653e\u5728\u6b4c\u4e2d\u5531\u5230\u76841984\u5e74\u3002\u90a3\u4e00\u5e74\u6536\u6210\u4e0d\u9519\uff0c\u4e2d\u56fd\u7cae\u98df\u4ea7\u91cf\u9996\u6b21\u7a81\u78344\u4ebf\u5428\uff0c\u4eba\u5747\u7cae\u98df390\u516c\u65a4\uff0c\u53ea\u76f8\u5f53\u4e8e\u4eca\u5929\u768470%\u3002\u5728\u5f53\u65f6\uff0c\u8fd9\u4e9b\u7cae\u98df\u80fd\u591f\u7ef4\u6301\u8001\u767e\u59d3\u7684\u57fa\u672c\u6e29\u9971\uff0c\u5374\u5206\u4e0d\u51fa\u66f4\u591a\u7684\u7cae\u98df\u53bb\u5582\u732a\u517b\u9e21\u30021984\u5e74\uff0c\u4e2d\u56fd\u4eba\u5747\u8089\u7c7b\u53ea\u670915\u516c\u65a4\uff0c\u76f8\u5f53\u4e8e\u4eca\u5929\u76841/4\u3002\u5f53\u65f6\uff0c\u56fd\u5bb6\u5b9e\u884c\u7cae\u98df\u5b9a\u91cf\u4f9b\u5e94\uff0c<strong>\u8d2d\u4e70\u7cae\u98df\u9700\u8981\u7cae\u7968</strong>\u3002\u76f4\u52301993\u5e74\uff0c\u7cae\u7968\u624d\u9000\u51fa\u5386\u53f2\u821e\u53f0\u3002</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>1980\u5e74\uff0c\u6211\u8fd8\u5728\u8bfb\u5c0f\u5b66\uff0c\u957f\u5f97\u5f88\u5355\u8584\u3002\u90a3\u5e74\u5934\uff0c\u5976\u7c89\u5bf9\u4e8e\u5bfb\u5e38\u767e\u59d3\u5bb6\u8fd8\u662f\u5962\u4f88\u54c1\u3002\u4e3a\u4e86\u5e2e\u6211\u8865\u5145\u8425\u517b\uff0c\u7236\u4eb2\u4ece\u6751\u91cc\u7684\u69a8\u6cb9\u574a\u4e70\u6765\u5bcc\u542b\u86cb\u767d\u8d28\u7684\u8c46\u7c95\uff0c\u653e\u5728\u767d\u8272\u7684\u5e03\u888b\u91cc\uff0c\u8ba9\u6211\u62ff\u7740\u6d17\u8863\u68d2\u69cc\u7528\u529b\u62cd\u6253\u3002\u8c46\u7c95\u88ab\u6363\u788e\u540e\uff0c\u518d\u63ba\u5230\u7389\u7c73\u9762\u4e2d\uff0c\u84b8\u51fa\u7684\u7389\u7c73\u7a9d\u5934\u6709\u4e00\u79cd\u72ec\u7279\u7684\u8c46\u9999\u3002\u4eca\u5929\u56de\u60f3\u8d77\u6765\uff0c<strong>\u5c11\u5e74\u65f6\u5403\u7684&ldquo;\u7389\u7c73+\u8c46\u7c95&rdquo;\u5c31\u662f\u4eca\u5929\u7684\u9972\u6599\u914d\u65b9</strong>&mdash;&mdash;\u8fd9\u4e9b\u4e8b\u5728\u4eca\u5929\u7684\u5e74\u8f7b\u670b\u53cb\u773c\u4e2d\uff0c\u5df2\u7ecf\u662f\u53e4\u8001\u7684\u4f20\u8bf4\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/llHw93OQE8ZuSQAzLA9Z7y2RLd8O9aUvjD34HqYfMovUAgAAZgIAAEpQ.jpg\" style=\"height:543px; width:640px\" />2018\u5e74\u4e2d\u56fd\u7cae\u98df\u7684\u4ea7\u91cf\u7ed3\u6784 | \u6570\u636e\u6765\u6e90\uff1a\u56fd\u5bb6\u7edf\u8ba1\u5c40</p>\r\n\r\n<p>\u89c6\u7ebf\u8f6c\u56de\u5230\u4eca\u5929\u30022015\u5e74\u4ee5\u6765\uff0c\u4e2d\u56fd\u7cae\u98df\u4ea7\u91cf\u8fde\u7eed\u56db\u5e74\u7a33\u5b9a\u57286.5\u4ebf\u5428\uff0c\u518d\u52a0\u4e0a\u6bcf\u5e74\u8fdb\u53e3\u76841.2\u4ebf\u5428\u7cae\u98df\uff0c\u5408\u8ba1\u6d88\u8d397.7\u4ebf\u5428\u3002\u5982\u679c\u6309\u7167\u91cd\u91cf\u6298\u7b97\uff0c1.2\u4ebf\u5428\u7684\u8fdb\u53e3\u91cf\u4ec5\u76f8\u5f53\u4e8e6.6\u4ebf\u5428\u56fd\u5185\u603b\u4ea7\u91cf\u76841/6\uff0c\u770b\u4f3c\u95ee\u9898\u4e0d\u5927\uff0c\u5176\u5b9e\u4e0d\u7136\u3002\u628a\u8fd91.2\u4ebf\u5428\u8fdb\u53e3\u7cae\u98df\u6298\u7b97\u6210\u8015\u5730\uff0c\u76f8\u5f53\u4e8e6\u4ebf\u4ea9\u7684\u8015\u5730\u3002\u5982\u679c\u518d\u52a0\u4e0a\u8fdb\u53e3\u7684\u68c9\u82b1\u3001\u767d\u7cd6\u3001\u6c34\u679c\u3001\u8089\u7c7b\u3001\u5976\u7c89\u7b49\uff0c\u5219\u8d85\u8fc77\u4ebf\u4ea9\u3002\u6309\u7167\u5168\u56fd20\u4ebf\u4ea9\u8015\u5730\u9762\u79ef\u8ba1\u7b97\uff0c<strong>\u6211\u4eec\u7684\u8015\u5730&ldquo;\u5bf9\u5916\u4f9d\u5b58\u5ea6&rdquo;\u8fbe\u523035%</strong>\u3002\u7cae\u98df\u751f\u4ea7\u9700\u8981\u6d88\u8017\u8015\u5730\u3001\u5316\u80a5\u3001\u519c\u836f\u548c\u6de1\u6c34\uff0c\u8fdb\u53e3\u7cae\u98df\u672c\u8d28\u4e0a\u5c31\u662f\u5728\u8fdb\u53e3\u8d44\u6e90\u3002</p>\r\n\r\n<p>\u8bf7\u5927\u5bb6\u770b\u770b\u4e0b\u9762\u8fd9\u5f20\u8868\u683c\u3002\u5c31\u56fd\u571f\u9762\u79ef\u800c\u8a00\uff0c960\u4e07\u5e73\u65b9\u516c\u91cc\u7684\u4e2d\u56fd\u6392\u5728\u4e16\u754c\u7b2c3\uff0c\u4f46\u662f\u5f88\u591a\u5730\u65b9\u6216\u662f\u5e72\u65f1\u5c11\u96e8\u7684\u6c99\u6f20\u6208\u58c1\uff0c\u6216\u662f\u5e38\u5e74\u4f4e\u6e29\u7684\u9ad8\u539f\u5bd2\u5730\uff0c\u6211\u4eec\u7684\u8015\u5730\u5360\u56fd\u571f\u9762\u79ef\u6bd4\u91cd\u4ec5\u670914%\u3002<strong>\u4eba\u591a\u5730\u5c11\uff0c\u4eba\u5747\u8015\u5730\u9762\u79ef\u4ec5\u4e3a0.1\u516c\u9877\uff0c\u8fdc\u4f4e\u4e8e\u4fc4\u7f57\u65af\u3001\u7f8e\u56fd\u3001\u5df4\u897f\u548c\u963f\u6839\u5ef7\u7b49\u519c\u4e1a\u5927\u56fd</strong>\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/olA2zKyNij2_PGOc6SFEj5IAHr49Vt_mbi6IxN0vxZk4BAAAzQEAAEpQ.jpg\" style=\"height:273px; width:640px\" /></p>\r\n\r\n<p>\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u80fd\u591f\u8ba914\u4ebf\u4eba\u5403\u9971\uff0c\u5c31\u662f\u4e2d\u56fd\u5bf9\u4e16\u754c\u6700\u5927\u7684\u8d21\u732e\u3002\u7136\u800c\u6211\u4eec\u4e3a\u6b64\u4e5f\u4ed8\u51fa\u4e86\u6c89\u91cd\u7684\u4ee3\u4ef7&mdash;&mdash;\u4e3a\u4e86\u63a7\u5236\u75c5\u866b\u5bb3\uff0c\u519c\u7530\u91cc\u5927\u91cf\u55b7\u6d12\u519c\u836f\uff0c\u4e25\u91cd<strong>\u6c61\u67d3\u6c34\u571f\u73af\u5883\uff0c\u7834\u574f\u751f\u6001\u5e73\u8861</strong>\u3002\u5728\u6211\u7684\u5c11\u5e74\u8bb0\u5fc6\u4e2d\uff0c\u665a\u971e\u4e2d\u873b\u8713\u98de\u821e\uff0c\u590f\u591c\u91cc\u86d9\u58f0\u4e00\u7247\u3002\u4eca\u5929\u518d\u53bb\u770b\u770b\uff0c\u8fd9\u79cd\u753b\u9762\u5df2\u7ecf\u6210\u4e3a\u56de\u5fc6\u3002</p>\r\n\r\n<p>\u4e3a\u4e86\u589e\u52a0\u7cae\u98df\u4ea7\u91cf\uff0c\u6211\u4eec\u4f7f\u7528\u4e86\u5168\u4e16\u754c1/3\u7684\u5316\u80a5\u3002\u4e2d\u56fd\u5316\u80a5\u7528\u91cf\u81ea1984 \u5e74\u5df2\u7ecf\u8fde\u7eed30\u591a\u5e74\u6210\u4e3a\u4e16\u754c\u7b2c\u4e00\u3002\u7136\u800c\u6211\u4eec\u7684\u5316\u80a5\u5229\u7528\u7387\u4ec5\u4e3a38%\uff0c\u8fdc\u4f4e\u4e8e\u53d1\u8fbe\u56fd\u5bb650-60%\u7684\u6c34\u5e73\u3002\u7cae\u98df\u4f5c\u7269\u7684\u5316\u80a5\u7528\u91cf\u5360\u519c\u4f5c\u7269\uff08\u8fd8\u5305\u62ec\u852c\u83dc\u3001\u6c34\u679c\u3001\u68c9\u82b1\u7b49\uff09\u603b\u91cf\u76841/2\uff0c\u4eca\u5929\u5e73\u5747\u6bcf100\u65a4\u7cae\u98df\u5c31\u9700\u8981\u65bd\u80a56\u65a4\u3002\u8fc7\u91cf\u65bd\u80a5\u7834\u574f\u4e86\u8015\u5730\u7684\u571f\u58e4\u7ed3\u6784\uff0c\u9020\u6210\u571f\u58e4\u677f\u7ed3\u548c\u76d0\u78b1\u5316\u3002\u8fc7\u5269\u7684\u5316\u80a5\u53c8\u968f\u7740\u96e8\u6c34\u6d41\u5165\u6c5f\u6e56\u6e56\u6cca\uff0c\u5bfc\u81f4\u6c34\u4f53\u5bcc\u8425\u517b\u5316\uff0c\u85fb\u7c7b\u690d\u7269\u75af\u957f\uff0c\u6c34\u4f53\u53d1\u81ed\u3002\u5927\u91cf\u85fb\u7c7b\u8986\u76d6\u6c34\u9762\uff0c\u906e\u5149\u8017\u6c27\uff0c\u5f71\u54cd\u6c34\u5e95\u751f\u7269\u751f\u957f\uff0c<strong>\u6574\u4e2a\u6c34\u751f\u751f\u6001\u7cfb\u7edf\u906d\u53d7\u7834\u574f</strong>\uff0c\u592a\u6e56\u84dd\u85fb\u7206\u53d1\u5c31\u662f\u6700\u5178\u578b\u7684\u4e8b\u4f8b\u4e4b\u4e00\u3002</p>\r\n\r\n<p>\u8015\u5730\u8d28\u91cf\u76f4\u63a5\u5173\u7cfb\u5230\u56fd\u5bb6\u7cae\u98df\u5b89\u5168\u3002\u9ed1\u571f\u5730\u662f\u4e16\u754c\u516c\u8ba4\u6700\u80a5\u6c83\u7684\u571f\u58e4\uff0c&ldquo;\u63d2\u6839\u7b77\u5b50\u4e5f\u53d1\u82bd&rdquo;\u3002\u4e1c\u5317\u662f\u5168\u7403\u4e09\u5927\u9ed1\u571f\u533a\u4e4b\u4e00\uff0c\u7cae\u98df\u4ea7\u91cf\u5360\u5168\u56fd\u76841/4\uff0c\u8c03\u51fa\u91cf\u5360\u5168\u56fd\u76841/3\uff0c\u662f\u4e2d\u56fd\u91cd\u8981\u7684\u7cae\u4ed3\u3002\u8fd9\u4e9b\u5e74\uff0c\u5728\u7cae\u98df\u589e\u4ea7\u7684\u540c\u65f6\uff0c\u9ed1\u571f\u5730\u4e5f\u88ab\u8fc7\u5ea6\u5f00\u53d1\u5229\u7528\uff0c\u571f\u58e4\u660e\u663e\u9000\u5316\uff0c\u75b2\u60eb\u4e0d\u582a\u3002\u9ed1\u571f\u5c42\u5df2\u7531\u4e0a\u4e16\u7eaa50\u5e74\u4ee3\u768460-70\u5398\u7c73\uff0c\u4e0b\u964d\u5230\u4eca\u5929\u768420-30\u5398\u7c73&mdash;&mdash;\u9ed1\u571f\u662f\u4e0d\u53ef\u518d\u751f\u7684\u5b9d\u8d35\u8d44\u6e90\uff0c\u5f62\u62101\u5398\u7c73\u9ed1\u571f\u5c42\u9700\u8981\u51e0\u767e\u5e74\u7684\u65f6\u95f4\u3002\u8fc7\u53bb\u9ed1\u571f\u5c42\u4e30\u539a\uff0c\u811a\u8e29\u4e0a\u53bb\u5c31\u50cf\u8e29\u5728\u6d77\u7ef5\u4e0a\u4e00\u6837\u3002\u73b0\u5728\u571f\u5c42\u53d8\u8584\uff0c\u8425\u517b\u4e0d\u8db3\uff0c\u4e0d\u5f97\u4e0d\u591a\u65bd\u5316\u80a5\u3002\u5316\u80a5\u8d8a\u7528\u8d8a\u591a\uff0c\u53c8\u5bfc\u81f4\u571f\u58e4\u677f\u7ed3\uff0c\u8e29\u4e0a\u53bb\u751a\u81f3\u770b\u4e0d\u5230\u811a\u5370\u3002&ldquo;<strong>\u4e07\u7269\u571f\u4e2d\u751f\uff0c\u6709\u571f\u65af\u6709\u7cae</strong>&rdquo;\uff0c\u9ed1\u571f\u5730\u7684\u53ef\u6301\u7eed\u53d1\u5c55\u95ee\u9898\u4e9f\u5f85\u89e3\u51b3\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/I_8-t-mLGQoE4i7p58ErDrlkdH9lWDf4hQsOUJNgQ6DoAwAAWAIAAEpQ.jpg\" style=\"height:384px; width:640px\" />\u4e1c\u5317\u9ed1\u571f\u5730 &nbsp;| \u6444\uff1a\u5b9b\u51cc\u8fc5</p>\r\n\r\n<p>\u4e2d\u56fd\u7684\u4eba\u5747\u6c34\u8d44\u6e90\u4ec5\u4e3a2050\u7acb\u65b9\u7c73\uff0c\u4ec5\u4ec5\u76f8\u5f53\u4e8e\u4e16\u754c\u5e73\u5747\u6c34\u5e73\u76841/4\u3002\u519c\u4e1a\u704c\u6e89\u6d88\u8017\u4e86\u4e2d\u56fd60%\u7684\u53ef\u5229\u7528\u6c34\u8d44\u6e90\uff0c\u7528\u6c34\u6548\u7387\u5374\u4ec5\u4e3a30-40%\uff0c\u8fdc\u4f4e\u4e8e\u53d1\u8fbe\u56fd\u5bb670-80%\u7684\u6c34\u5e73\u3002\u5728\u5317\u65b9\u65f1\u533a\uff0c\u7531\u4e8e\u6c34\u8d44\u6e90\u77ed\u7f3a\uff0c\u6709\u4e9b\u5730\u65b9<strong>\u8fc7\u5ea6\u5f00\u91c7\u5730\u4e0b\u6c34\u8d44\u6e90</strong>\u7528\u4e8e\u519c\u4e1a\u704c\u6e89\u3002\u534e\u5317\u4e95\u704c\u533a\u662f\u4e2d\u56fd\u7684\u7cae\u98df\u4e3b\u4ea7\u533a\u4e4b\u4e00\u3002\u7531\u4e8e\u8fc7\u53bb30\u51e0\u5e74\u7684\u5f00\u91c7\uff0c\u8fd9\u91cc\u7684\u5730\u4e0b\u6c34\u4f4d\u4e0b\u964d\u5e45\u5ea6\u751a\u81f3\u8d85\u8fc720\u7c73\uff0c\u6210\u4e3a<strong>\u4e16\u754c\u4e0a\u6700\u5927\u7684\u5730\u4e0b\u6c34\u6f0f\u6597\u533a</strong>\u3002</p>\r\n\r\n<p>\u653e\u773c\u672a\u6765\uff0c\u4e2d\u56fd\u7684\u4eba\u53e3\u5cf0\u503c\u9884\u8ba1\u5c06\u51fa\u73b0\u57282028 \u5e74\u524d\u540e\uff0c\u603b\u4eba\u53e3\u7ea6\u4e3a14.2 \u4ebf\u3002\u867d\u7136\u6b64\u540e\u7684\u4eba\u53e3\u6570\u91cf\u5f00\u59cb\u4e0b\u964d\uff0c\u4f462050 \u5e74\u524d\u4ecd\u4e0d\u4f1a\u5c11\u4e8e12 \u4ebf\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c<strong>\u672a\u676530\u5e74\u4e2d\u56fd\u5c06\u4f9d\u7136\u9762\u5bf9\u7cae\u98df\u5b89\u5168\u538b\u529b</strong>\u3002</p>\r\n\r\n<p>\u653e\u773c\u4e16\u754c\uff0c\u4eca\u5929\u5730\u7403\u4e0a\u5df2\u7ecf\u670976\u4ebf\u4eba\uff0c\u5176\u4e2d<strong>10%\u5904\u4e8e\u9965\u997f\u72b6\u6001</strong>\uff0c\u5305\u62ec1.5\u4ebf\u53d1\u80b2\u4e0d\u826f\u7684\u513f\u7ae5\u3002\u9884\u8ba1\u52302050\u5e74\uff0c\u4e16\u754c\u4eba\u53e3\u4f1a\u589e\u52a0\u523095\u4ebf\uff0c2100\u5e74\u4f1a\u8fbe\u5230110\u4ebf\u3002\u8ba9\u5730\u7403\u517b\u6d3b\u8fd9\u6837\u591a\u7684\u4eba\u53e3\uff0c\u5f88\u4e0d\u8f7b\u677e\u3002</p>\r\n\r\n<h2><strong>&ldquo;\u727a\u7272&rdquo;\u5927\u8c46\uff0c\u786e\u4fdd&ldquo;\u996d\u7897&rdquo;</strong></h2>\r\n\r\n<p>\u4e2d\u56fd\u662f\u5927\u8c46\u7684\u6545\u4e61\uff0c5000\u5e74\u524d\uff0c\u534e\u590f\u5148\u6c11\u5f00\u542f\u5927\u8c46\u79cd\u690d\uff0c\u4ece\u6b64&ldquo;\u4e2d\u539f\u6709\u83fd\uff0c\u5eb6\u6c11\u91c7\u4e4b&rdquo;\u3002\u8c46\u8150\u91cc\u6709\u534e\u590f\u6c11\u65cf\u5343\u5e74\u966a\u4f34\u7684\u82b3\u9999\uff0c\u6709&ldquo;\u716e\u8c46\u71c3\u8c46\u8401&rdquo;\u7684\u8bd7\u53e5\u3002\u516c\u51438\u4e16\u7eaa\u7684\u5510\u671d\uff0c\u9274\u771f\u4e1c\u6e21\uff0c\u5c06\u5927\u8c46\u4f20\u5230\u65e5\u672c\u3002\u5230\u4e8618\u4e16\u7eaa\u7684\u6e05\u671d\uff0c\u5927\u8c46\u53c8\u4ece\u4e2d\u56fd\u4f20\u5165\u6b27\u7f8e\u3002\u4eca\u5929\uff0c\u5927\u8c46\u5728\u5730\u7403\u4e0a\u7684\u79cd\u690d\u9762\u79ef\u8fbe\u52301.2\u4ebf\u516c\u9877\uff0c\u7ea6\u5360\u5168\u4e16\u754c\u8015\u5730\u9762\u79ef\u76848%\uff0c\u53ef\u4ee5\u8bf4\uff0c<strong>\u5927\u8c46\u662f\u4e2d\u56fd\u9001\u7ed9\u4e16\u754c\u7684\u6700\u597d\u793c\u7269</strong>\u3002</p>\r\n\r\n<p>\u5728\u4e0a\u4e2a\u4e16\u7eaa\u4e2d\u671f\uff0c\u662f\u4e2d\u56fd\u5927\u8c46\u7684\u8f89\u714c\u65f6\u671f\u3002\u5927\u8c46\u548c\u8336\u3001\u4e1d\u662f\u4e2d\u56fd\u51fa\u53e3\u7684\u4e09\u5927\u540d\u4ea7\uff0c\u5728\u4e16\u754c\u4e0a\u4eab\u6709\u76db\u8a89\u3002\u5f53\u65f6\uff0c\u4e2d\u56fd\u5927\u8c46\u79cd\u690d\u9762\u79ef\u5360\u5168\u4e16\u754c\u768440%\uff0c\u7136\u800c\u572880\u5e74\u4ee3\u4e0b\u964d\u523020%\uff0c\u73b0\u5728\u53ea\u67097%\u30021995\u5e74\uff0c\u4e2d\u56fd\u5f00\u59cb\u53d8\u6210\u5927\u8c46\u8fdb\u53e3\u56fd\uff0c\u8fdb\u53e3\u91cf\u4e00\u8def\u98d9\u5347\u52302018\u5e74\u76849000\u4e07\u5428\uff0c<strong>\u5360\u5168\u7403\u5927\u8c46\u8d38\u6613\u76842/3</strong>\u3002</p>\r\n\r\n<p><strong>\u4e3a\u4ec0\u4e48\u8981\u8fdb\u53e3\u8fd9\u4e48\u591a\u5927\u8c46\uff1f</strong>\u5f88\u591a\u516c\u4f17\u5bf9\u6b64\u5e76\u4e0d\u7406\u89e3\u3002\u6b64\u95f4\u6709\u884c\u4e1a\u6ca6\u9677\u7684\u60b2\u60c5\uff0c\u6709\u7cae\u98df\u5b89\u5168\u7684\u5fe7\u8651\uff0c\u6709\u8f6c\u57fa\u56e0\u7684\u4e89\u8bba\uff0c\u6709\u8d38\u6613\u6218\u7684\u7231\u56fd\u60c5\u6000\u3002\u76d8\u6839\u9519\u8282\uff0c\u526a\u4e0d\u65ad\uff0c\u7406\u8fd8\u4e71\u3002</p>\r\n\r\n<p>\u5927\u8c46\u662f\u5bcc\u542b\u86cb\u767d\u8d28\u7684\u8c37\u7269\uff0c\u8fd9\u4e00\u7279\u70b9\u4e5f\u51b3\u5b9a\u4e86\u5b83\u7684\u5bbf\u547d&mdash;&mdash;<strong>\u4e0d\u53ef\u6216\u7f3a\u7684\u9972\u6599\u914d\u6599</strong>\u3002\u6211\u4eec\u9910\u684c\u4e0a\u7684\u8089\u3001\u86cb\u3001\u5976\u90fd\u662f\u9972\u6599\u8f6c\u5316\u800c\u6765\u3002\u5728\u9972\u6599\u4e2d\uff0c\u7389\u7c73\u536065%\uff0c\u69a8\u6cb9\u540e\u7684\u8c46\u7c95\u536020%\u3002\u732a\u6bcf\u54032.7\u65a4\u9972\u6599\u53ef\u4ee5\u957f\u51fa1\u65a4\u8089\uff0c\u800c\u9e21\u6bcf\u54032\u65a4\u9972\u6599\u5c31\u80fd\u957f\u51fa1\u65a4\u8089\u6216\u4ea7\u51fa1\u65a4\u86cb\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/iu25WWk3_oja3qHp732PU4WwZYQXtU5G6MyRZyZzjdakAwAAKAIAAEpQ.jpg\" style=\"height:379px; width:640px\" />\u4e1c\u5317\u5927\u8c46 | \u6444\u4e8e\u5409\u6797\u519c\u4e1a\u5927\u5b66\u5b9e\u9a8c\u519c\u573a</p>\r\n\r\n<p>\u8fdb\u516590\u5e74\u4ee3\uff0c\u767e\u59d3\u751f\u6d3b\u4ece&ldquo;\u5403\u9971&rdquo;\u8fdb\u5165&ldquo;\u5403\u597d&rdquo;\uff0c\u8089\u86cb\u5976\u6d88\u8d39\u91cf\u8fc5\u901f\u4e0a\u5347\uff0c\u8089\u7c7b\u4ea7\u91cf\u4ece1990\u5e74\u76842800\u4e07\u5428\u589e\u957f\u52302018\u5e74\u76848500\u4e07\u5428\uff0c\u63d0\u9ad8\u4e86\u6574\u65743\u500d\u3002\u4f5c\u4e3a\u9972\u6599\u6838\u5fc3\u914d\u6599\u7684\u8c46\u7c95\u9700\u6c42\u91cf\u4e5f\u540c\u6bd4\u589e\u52a0\u3002\u4eca\u5929\uff0c\u56fd\u4ea7\u5927\u8c46\u4ea7\u91cf\u4ec5\u4e3a1500\u4e07\u5428\uff0c\u8fdb\u53e3\u91cf\u8fbe\u52309000\u4e07\u5428\u3002\u6362\u8a00\u4e4b\uff0c\u5927\u8c46\u7684\u8fdb\u53e3\u4f9d\u5b58\u5ea6\u8d85\u8fc780%\u3002</p>\r\n\r\n<p>\u66fe\u6709\u516c\u4f17\u8d28\u7591\uff1a<strong>\u4e3a\u4ec0\u4e48\u4e0d\u6269\u5927\u56fd\u4ea7\u5927\u8c46\u7684\u79cd\u690d\u9762\u79ef\uff1f</strong>\u4e2d\u56fd\u662f\u5168\u4e16\u754c\u7b2c\u4e00\u4eba\u53e3\u5927\u56fd\uff0c\u7cae\u98df\u5b89\u5168\u91cd\u4e8e\u6cf0\u5c71\u3002\u4e00\u9762\u662f\u6301\u7eed\u589e\u957f\u7684\u4eba\u53e3\u548c\u8089\u86cb\u5976\u6d88\u8d39\uff0c\u4e00\u9762\u662f\u6349\u895f\u89c1\u8098\u768420\u4ebf\u4ea9\u8015\u5730\u9762\u79ef\u3002\u4e3a\u4e86\u4fdd\u969c\u996d\u7897\u7262\u7262\u7aef\u5728\u81ea\u5df1\u624b\u91cc\uff0c\u4e2d\u56fd\u53ea\u80fd\u9009\u62e9&ldquo;\u727a\u7272&rdquo;\u5927\u8c46\uff0c\u4f18\u5148\u4fdd\u969c\u6c34\u7a3b\u548c\u5c0f\u9ea6\u7684\u79cd\u690d\u9762\u79ef\u3002\u7528\u8fdb\u53e3\u5927\u8c46\u8282\u7ea6\u51fa\u6765\u7684\u8015\u5730\u6765\u79cd\u690d\u76f8\u5bf9\u9ad8\u4ea7\u7684\u4e3b\u7cae\u4f5c\u7269\uff0c\u5f53\u7136\u662f\u6700\u5408\u7406\u7684\u9009\u62e9\u3002</p>\r\n\r\n<p>\u5176\u5b9e\u519c\u6c11\u4e5f\u7f3a\u5c11\u79cd\u690d\u5927\u8c46\u7684\u613f\u610f\u3002\u5927\u8c46\u4ea7\u91cf\u4f4e\uff0c\u79cd\u690d\u6536\u76ca\u53ea\u6709\u7389\u7c73\u548c\u5c0f\u9ea6\u76841/2\u3002\u4e1c\u5317\u662f\u5927\u8c46\u4e3b\u4ea7\u533a\uff0c\u5728\u6b4c\u66f2\u300a\u5bb6\u5728\u4e1c\u5317\u300b\u4e2d\u6709\u4e00\u6bb5\u6b4c\u8bcd\uff1a&ldquo;\u6211\u7684\u5bb6\u5728\u4e1c\u5317\u677e\u82b1\u6c5f\u4e0a\uff0c\u90a3\u91cc\u6709\u6f2b\u5c71\u904d\u91ce\u7684\u5927\u8c46\u9ad8\u7cb1\u3002&rdquo;\u4eca\u5929\uff0c\u4f60\u82e5\u771f\u7684\u53bb\u4e1c\u5317\u770b\u770b\uff0c\u5927\u9762\u79ef\u7684\u5927\u8c46\u3001\u9ad8\u7cb1\u65e9\u5df2\u88ab\u7389\u7c73\u53d6\u800c\u4ee3\u4e4b\u3002</p>\r\n\r\n<p>\u4e5f\u6709\u516c\u4f17\u63d0\u51fa\uff1a<strong>\u4e3a\u4ec0\u4e48\u8981\u8fdb\u53e3\u7f8e\u56fd\u5927\u8c46\uff1f</strong>\u5168\u7403\u5927\u8c46\u5e74\u4ea7\u91cf\u7ea6\u4e3a3.5\u4ebf\u5428\uff0c\u51e0\u4e2a\u4e3b\u4ea7\u56fd\u6263\u9664\u672c\u571f\u6d88\u8d39\uff0c\u80fd\u591f\u62ff\u51fa\u6765\u8fdb\u884c\u56fd\u9645\u8d38\u6613\u7684\u53ea\u67091.5\u4ebf\u5428\uff0c\u5176\u4e2d\u7f8e\u56fd\u4e3a6000\u4e07\u5428\u3002\u4e2d\u56fd\u8fdb\u53e39000\u4e07\u5428\u5927\u8c46\uff0c\u5982\u679c\u5168\u9762\u5c01\u6740\u7f8e\u56fd\u5927\u8c46\uff0c\u5219\u610f\u5473\u7740\u6211\u4eec\u8981\u4e70\u5149\u7f8e\u56fd\u4ee5\u5916\u7684\u5168\u7403\u7684\u8d38\u6613\u5927\u8c46\uff0c\u8fd9\u662f\u4e0d\u73b0\u5b9e\u7684\u3002</p>\r\n\r\n<p>2018\u5e74\u4e2d\u7f8e\u8d38\u6613\u6218\u785d\u70df\u5f25\u6f2b\uff1a\u7f8e\u56fd\u4ee5\u82af\u7247\u4e3a\u77db\uff0c\u4e2d\u56fd\u7528\u5927\u8c46\u505a\u76fe\u3002\u695a\u6cb3\u6c49\u754c\uff0c\u4e92\u4e0d\u76f8\u8ba9\uff0c\u6700\u7ec8\u5750\u6536\u6e14\u5229\u7684\u662f\u5df4\u897f\u3002\u6b64\u8bdd\u600e\u8bb2\uff1f\u5df4\u897f\u548c\u7f8e\u56fd\u662f\u5168\u7403\u5927\u8c46\u7684\u4e24\u5927\u51fa\u53e3\u56fd\u30022017\u5e74\uff0c\u4e2d\u56fd\u7684\u8fdb\u53e3\u5927\u8c46\u4e2d\uff0c\u5df4\u897f\u548c\u7f8e\u56fd\u7684\u4efd\u989d\u5206\u522b\u662f53%\u548c34%\u3002\u800c\u5230\u4e862018\u5e74\uff0c\u8fd9\u4e00\u6bd4\u4f8b\u53d8\u4e3a75%\u548c19%\u3002\u5728\u6fc0\u589e\u7684\u5df4\u897f\u5927\u8c46\u51fa\u53e3\u91cf\u4e2d\uff0c\u6709\u591a\u5c11\u662f\u5c06\u7f8e\u56fd\u5927\u8c46\u901a\u8fc7\u8f6c\u53e3\u8d38\u6613\u7a7f\u4e0a\u5df4\u897f\u7684\u9a6c\u7532\u518d\u5356\u7ed9\u4e2d\u56fd\uff1f\u4e0d\u5f97\u800c\u77e5\u3002\u4e9a\u9a6c\u900a\u96e8\u6797\u88ab\u79f0\u4e3a\u5730\u7403\u4e4b\u80ba\uff0c\u800c60%\u7684\u4e9a\u9a6c\u900a\u5728\u5df4\u897f\u5883\u5185\u3002\u4fdd\u62a4\u96e8\u6797\u662f\u8d1f\u62c5\uff0c\u53d1\u5c55\u519c\u4e1a\u5219\u662f\u6548\u76ca\u3002\u9762\u5bf9\u7ecf\u6d4e\u840e\u9761\u548c\u8d2b\u5bcc\u5dee\u8ddd\uff0c\u5df4\u897f\u8fd1\u5e74\u6765<strong>\u5927\u9762\u79ef\u780d\u4f10\u70ed\u5e26\u96e8\u6797</strong>\uff0c\u7528\u6765\u79cd\u690d\u5927\u8c46\u548c\u653e\u7267\u8089\u725b\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/s40FQZiEllZltekwoe1YHu-uhuo0LIFklVmgHDwYXumgAwAAWgIAAEpQ.jpg\" style=\"height:415px; width:640px\" />\u6536\u83b7\u7684\u5927\u8c46\u690d\u682a | \u6444\u4e8e\u5409\u6797\u519c\u4e1a\u5927\u5b66\u5b9e\u9a8c\u519c\u573a</p>\r\n\r\n<p>\u8fd8\u6709\u516c\u4f17\u8d28\u7591\uff1a<strong>\u4e3a\u4ec0\u4e48\u8981\u8fdb\u53e3\u8f6c\u57fa\u56e0\u5927\u8c46\uff1f</strong>\u4e3a\u4ec0\u4e48\u4e0d\u4ece\u4fc4\u7f57\u65af\u7b49\u56fd\u5bb6\u5bfb\u6c42\u66ff\u4ee3\u8fdb\u53e3\uff1f\u8fd9\u91cc\u6211\u8981\u544a\u8bc9\u5927\u5bb6\uff0c\u7f8e\u56fd\u3001\u5df4\u897f\u548c\u963f\u6839\u5ef7\u4e09\u4e2a\u56fd\u5bb6\u7684\u5927\u8c46\u8d38\u6613\u91cf\u5c31\u5360\u5230\u5168\u740390%\u7684\u6bd4\u91cd\uff0c\u5176\u4ed6\u56fd\u5bb6\uff08\u4e4c\u62c9\u572d\u3001\u4fc4\u7f57\u65af\u3001\u5370\u5ea6\u7b49\uff09\u51fa\u53e3\u7684\u57fa\u6570\u53ea\u6709100\u4e07\u5428\u7684\u6570\u91cf\u7ea7\uff0c\u76f8\u5bf9\u4e8e\u4e2d\u56fd9000\u4e07\u5428\u7684\u5e9e\u5927\u8fdb\u53e3\u9700\u6c42\uff0c\u53ea\u662f\u676f\u6c34\u8f66\u85aa\u3002\u7136\u800c\u7f8e\u56fd\u3001\u5df4\u897f\u548c\u963f\u6839\u5ef7\u7684\u8f6c\u57fa\u56e0\u79cd\u690d\u6bd4\u4f8b\u90fd\u8fbe\u523095%\u3002\u5982\u679c\u4e2d\u56fd\u7981\u6b62\u8fdb\u53e3\u8f6c\u57fa\u56e0\u5927\u8c46\uff0c\u52bf\u5fc5\u9762\u4e34\u9972\u6599\u77ed\u7f3a\uff0c\u9020\u6210\u8089\u86cb\u5976\u6570\u91cf\u51cf\u5c11\u3001\u4ef7\u683c\u98d9\u5347\uff0c\u8001\u767e\u59d3\u53ea\u80fd\u6e05\u6c64\u5be1\u6c34\u7684\u5929\u5929\u5403\u7d20&mdash;&mdash;\u8fd9\u76f8\u5f53\u4e8e\u8981\u8f70\u8f70\u70c8\u70c8\u7684\u641e\u4e00\u573a\u5168\u6c11\u51cf\u80a5\u8fd0\u52a8\uff0c\u516c\u4f17\u80fd\u5426\u5403\u5f97\u6d88\uff1f</p>\r\n\r\n<p>\u7f8e\u56fd\u662f\u8f6c\u57fa\u56e0\u4f5c\u7269\u5168\u7403\u7b2c\u4e00\u79cd\u690d\u548c\u6d88\u8d39\u8005\u5927\u56fd\u3002\u57282\u4ebf\u516c\u9877\u7684\u8015\u5730\u603b\u9762\u79ef\u4e2d\uff0c40%\u79cd\u690d\u7684\u8f6c\u57fa\u56e0\u4f5c\u7269\uff0c\u5176\u4e2d\u7389\u7c73\u3001\u5927\u8c46\u3001\u6cb9\u83dc\u7684\u8f6c\u57fa\u56e0\u79cd\u690d\u6bd4\u4f8b\u5747\u8d85\u8fc790%\u3002\u4ee5\u5927\u8c46\u4e3a\u4f8b\uff1a\u7f8e\u56fd\u603b\u4ea7\u91cf1.2\u4ebf\u5428\uff0c\u51fa\u53e3\u91cf\u5219\u662f0.6\u4ebf\u5428\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u7f8e\u56fd\u4e00\u534a\u7684\u8f6c\u57fa\u56e0\u5927\u8c46\u7528\u4e8e\u672c\u571f\u6d88\u8d39\u3002\u5173\u4e8e\u8f6c\u57fa\u56e0\u7684\u5b89\u5168\u95ee\u9898\uff0c\u8fd9\u91cc\u4e0d\u505a\u63a2\u8ba8\u3002\u4f46\u7f51\u7edc\u4e0a\u6709\u4e00\u79cd\u4f20\u8a00\uff1a<strong>\u7f8e\u56fd\u4eba\u81ea\u5df1\u4e0d\u5403\u8f6c\u57fa\u56e0\u98df\u54c1&mdash;&mdash;\u8fd9\u4e0d\u7b26\u5408\u4e8b\u5b9e</strong>\uff01</p>\r\n\r\n<p>\u56fd\u9645\u8d38\u6613\uff0c\u4ef7\u683c\u662f\u738b\u9053\u3002\u7f8e\u6d32\uff08\u5df4\u897f\u3001\u7f8e\u56fd\u548c\u963f\u6839\u5ef7\uff09\u7684\u5927\u8c46\u51fa\u53e3\u5730\u4ef7\u683c\u662f2600\u5143/\u5428\uff0c\u4ece\u5730\u7403\u53e6\u4e00\u7aef\u6f02\u6d0b\u8fc7\u6d77\u8fd0\u5230\u4e2d\u56fd\uff0c\u52a0\u4e0a25%\u7684\u8fd0\u8d39\u548c\u5173\u7a0e\uff0c\u5230\u5cb8\u4ef73300\u5143/\u5428\uff0c\u6bd4\u56fd\u4ea7\u5927\u8c46\uff083700\u5143/\u5428\uff09\u8fd8\u4fbf\u5b9c15%\u3002\u5f52\u6839\u7ed3\u5e95\uff0c\u662f<strong>\u6211\u4eec\u7684\u519c\u4e1a&ldquo;\u751f\u4ea7\u529b&rdquo;\u843d\u540e\u4e8e\u522b\u4eba</strong>\u3002\u4e2d\u56fd\u5927\u8c46\u6bcf\u516c\u9877\u7684\u5e73\u5747\u4ea7\u91cf\u4e0d\u52302000\u516c\u65a4\uff0c\u53ea\u76f8\u5f53\u4e8e\u7f8e\u56fd\u76843300\u516c\u65a4\u768460%\u3002\u5de8\u5927\u7684\u4ea7\u91cf\u5dee\u8ddd\u4e00\u65b9\u9762\u662f\u571f\u58e4\u548c\u7530\u95f4\u7ba1\u7406\uff0c\u53e6\u4e00\u65b9\u9762\u5219\u662f\u54c1\u79cd\u4f18\u52bf\u3002\u79cd\u5b50\u662f\u519c\u4e1a\u7684&ldquo;\u82af\u7247&rdquo;\uff0c\u4e2d\u56fd\u9700\u8981\u53d1\u5c55\u519c\u4e1a\u751f\u7269\u6280\u672f\uff0c\u4ee5\u5e94\u5bf9\u65b0\u4e00\u4ee3\u80b2\u79cd\u56fd\u9645\u7ade\u4e89\u3002</p>\r\n\r\n<div class=\"editors-note\" style=\"text-align: center;\"><strong>\u5ef6\u4f38\u8bdd\u9898\uff1a\u5168\u7403\u89c6\u91ce\u4e0b\u7684\u5927\u8c46\u535a\u5f08</strong></div>\r\n\r\n<div class=\"editors-note\"><span style=\"color:#696969\">\u5c3d\u7ba1\u4e2d\u56fd\u5927\u8c46\u91c7\u8d2d\u91cf\u5360\u5168\u7403\u5927\u8c46\u8d38\u6613\u76842/3\uff0c\u4f46\u5927\u8c46\u5b9a\u4ef7\u6743\u5e76\u6ca1\u6709\u638c\u63e1\u5728\u4e2d\u56fd\u4eba\u624b\u91cc\u3002\u7a76\u5176\u539f\u56e0\uff0c\u8fd9\u7275\u6d89\u5230\u4e8c\u6218\u4ee5\u540e\u7684\u56fd\u9645\u79e9\u5e8f\u3001\u4ee5\u7f8e\u5143\u4e3a\u4e2d\u5fc3\u7684\u56fd\u9645\u8d27\u5e01\u4f53\u7cfb\u3001WTO\u89c4\u5219\u548c\u5168\u7403\u519c\u4ea7\u54c1\u671f\u8d27\u5e02\u573a\u7b49\u590d\u6742\u95ee\u9898\u3002\u800c\u5728\u56fd\u5185\u5e02\u573a\uff0c\u5916\u8d44\u3001\u56fd\u8d44\u548c\u6c11\u8d44\u4e4b\u95f4\u7684\u5229\u76ca\u535a\u5f08\u4e5f\u662f\u76d8\u6839\u9519\u8282\u3002\u4e0d\u8c0b\u5168\u5c40\u8005\u4e0d\u8db3\u8c0b\u4e00\u57df\uff0c\u4eca\u5929\u7684\u8d38\u6613\u6218\u80cc\u540e\u662f\u4e00\u573a\u56fd\u9645\u79e9\u5e8f\u7684\u535a\u5f08\uff0c\u800c\u5927\u8c46\u5219\u662f\u8fd9\u573a\u535a\u5f08\u4e2d\u7684\u4e00\u9897\u5173\u952e\u68cb\u5b50\u3002</span></div>\r\n\r\n<div class=\"editors-note\">&nbsp;</div>\r\n\r\n<div class=\"editors-note\"><span style=\"color:#696969\">\u6216\u8bb8\u6709\u670b\u53cb\u4f1a\u95ee\uff1a\u5927\u8c46\u56f0\u5c40\u96be\u9053\u5c31\u65e0\u89e3\u4e86\u5417\uff1f\u8fd8\u662f\u7535\u5f71\u300a\u54ea\u5412\u4e4b\u9b54\u7ae5\u964d\u4e16\u300b\u4e2d\u7684\u90a3\u53e5\u53f0\u8bcd\uff1a&ldquo;\u6211\u547d\u7531\u6211\u4e0d\u7531\u5929\u3002&rdquo;\u65e2\u7136\u56fd\u5185\u8015\u5730\u8d44\u6e90\u6709\u9650\uff0c\u6211\u4eec\u5c31\u5fc5\u987b\u5b66\u4f1a\u6574\u5408\u5168\u7403\u7684\u519c\u4e1a\u8d44\u6e90\u3002\u7f8e\u56fd\u524d\u56fd\u52a1\u537f\u57fa\u8f9b\u683c\u66fe\u7ecf\u8bf4\u8fc7\uff1a&ldquo;\u8c01\u63a7\u5236\u4e86\u77f3\u6cb9\uff0c\u5c31\u63a7\u5236\u4e86\u6240\u6709\u56fd\u5bb6\uff1b\u8c01\u63a7\u5236\u4e86\u7cae\u98df\uff0c\u5c31\u63a7\u5236\u4e86\u4eba\u7c7b;\u8c01\u63a7\u5236\u4e86\u8d27\u5e01,\u5c31\u63a7\u5236\u4e86\u5168\u7403\u7ecf\u6d4e\u3002\u8fd9\u53e5\u8bdd\u9053\u51fa\u4e86\u535a\u5f08\u5173\u952e\u6240\u5728\uff0c\u4e5f\u7ed9\u51fa\u4e86\u89e3\u5c40\u7684\u601d\u8def\u3002</span></div>\r\n\r\n<div class=\"editors-note\">&nbsp;</div>\r\n\r\n<div class=\"editors-note\"><span style=\"color:#696969\">\u4ed6\u5c71\u4e4b\u77f3\uff0c\u53ef\u4ee5\u653b\u7389\u3002\u5927\u8c46\u6709\u4e00\u4e2a\u5f88\u597d\u7684\u524d\u8f66\u4e4b\u9274&mdash;&mdash;\u4e8b\u5173\u80fd\u6e90\u5b89\u5168\u7684\u77f3\u6cb9\u3002\u548c\u5927\u8c46\u4e00\u6837\uff0c\u77f3\u6cb9\u4e5f\u662f\u8d44\u6e90\u578b\u4ea7\u54c1\u30021993\u5e74\u4ee5\u524d\uff0c\u4e2d\u56fd\u77f3\u6cb9\u4e5f\u662f\u81ea\u7ed9\u81ea\u8db3\uff0c\u6b64\u540e\u4f34\u968f\u7740\u5de5\u4e1a\u5316\u8fdb\u7a0b\uff0c\u8fdb\u53e3\u91cf\u9010\u5e74\u6500\u5347\uff0c\u6f02\u6d0b\u8fc7\u6d77\u7684\u8fdb\u53e3\u539f\u6cb9\u6210\u672c\u4e5f\u4f4e\u4e8e\u56fd\u5185\u751f\u4ea7\u7684\u539f\u6cb9\u3002\u4eca\u5929\uff0c\u4e2d\u56fd\u662f\u4e16\u754c\u7b2c\u4e00\u5927\u77f3\u6cb9\u8fdb\u53e3\u56fd\uff0c\u77f3\u6cb9\u5bf9\u5916\u4f9d\u5b58\u5ea6\u8d85\u8fc770%\u3002\u5728\u5f88\u957f\u7684\u65f6\u95f4\u91cc\uff0c\u4e2d\u56fd\u539f\u6cb9\u8fdb\u53e3\u7684\u4e3b\u8981\u6765\u6e90\u4e8e\u5c40\u52bf\u4e0d\u7a33\u5b9a\u7684\u4e2d\u4e1c\u5730\u533a\uff0c\u6d77\u8def\u8fd0\u8f93\u8fd8\u8981\u901a\u8fc7\u5bb9\u6613\u53d7\u5230\u904f\u5236\u7684\u9a6c\u516d\u7532\u6d77\u5ce1\u3002\u600e\u4e48\u6837\uff0c\u77f3\u6cb9\u548c\u5927\u8c46\u7684\u53d1\u5c55\u8f68\u8ff9\u662f\u4e0d\u662f\u5f88\u50cf\u5b6a\u751f\u5144\u5f1f\uff1f</span></div>\r\n\r\n<div class=\"editors-note\">&nbsp;</div>\r\n\r\n<div class=\"editors-note\"><span style=\"color:#696969\">\u7136\u800c\u7ecf\u8fc7\u5341\u51e0\u5e74\u7684\u52aa\u529b\uff0c\u4e2d\u56fd\u77f3\u6cb9\u8fdb\u53e3\u5df2\u7ecf\u6e10\u6e10&ldquo;\u7a81\u56f4&rdquo;\u3002\u4eca\u5929\uff0c\u4e2d\u56fd\u53ef\u4ee5\u4ece\u7ea650\u4e2a\u56fd\u5bb6\u6216\u5730\u533a\u8fdb\u53e3\u539f\u6cb9\uff0c\u904d\u5e03\u4e2d\u4e1c\u3001\u975e\u6d32\u3001\u4fc4\u7f57\u65af\u548c\u5357\u7f8e\u6d32\uff0c\u6784\u5efa\u4e86\u591a\u5143\u5316\u7684\u77f3\u6cb9\u8fdb\u53e3\u683c\u5c40\u3002\u5176\u4e2d\u4fc4\u7f57\u65af\u5df2\u7ecf\u8fde\u7eed\u56db\u5e74\u6210\u4e3a\u4e2d\u56fd\u6700\u5927\u77f3\u6cb9\u4f9b\u5e94\u56fd\uff0c\u5360\u6bd4\u8fbe\u523015%\uff0c\u800c\u4e2d\u4e1c\u7684\u4f9b\u6cb9\u6bd4\u4f8b\u5219\u63a7\u5236\u572845%\u5de6\u53f3\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u4e2d\u56fd\u6cb9\u4f01\u4e5f\u5728\u4e16\u754c\u5404\u5730\u5165\u80a1\u65b0\u6cb9\u7530\u3001\u5efa\u8bbe\u9646\u8def\u77f3\u6cb9\u7ba1\u9053\u3001\u7b7e\u7f72\u8fd0\u8f93\u534f\u8bae\u3001\u91c7\u7528\u4eba\u6c11\u5e01\u7ed3\u7b97\uff0c\u540c\u65f6\u5728\u56fd\u5185\u5927\u529b\u63d0\u5347\u539f\u6cb9\u50a8\u5907\u548c\u70bc\u6cb9\u80fd\u529b\uff0c\u4e3a\u56fd\u5bb6\u80fd\u6e90\u5b89\u5168\u63d0\u4f9b\u4fdd\u969c\u3002</span></div>\r\n\r\n<div class=\"editors-note\">&nbsp;</div>\r\n\r\n<div class=\"editors-note\"><span style=\"color:#696969\">\u4ece\u6218\u7565\u4e0a\uff0c\u4e2d\u56fd\u7684\u5927\u8c46\u91c7\u8d2d\u5b8c\u5168\u53ef\u4ee5\u501f\u9274\u77f3\u6cb9\u6218\u7565\uff0c\u8fdb\u884c\u5168\u7403\u5316\u5e03\u5c40\uff0c\u5efa\u7acb\u591a\u5143\u5316\u8fdb\u53e3\u683c\u5c40\uff0c\u4e0d\u80fd\u540a\u5728\u7f8e\u6d32\uff08\u7f8e\u56fd\u3001\u5df4\u897f\u548c\u963f\u6839\u5ef7\uff09\u4e00\u68f5\u6811\u4e0a\u3002\u4fc4\u7f57\u65af\u3001\u4e4c\u514b\u5170\u7b49\u4e2d\u4e9a\u548c\u4e1c\u6b27\u5730\u533a\u6709\u5927\u9762\u79ef\u672a\u5f00\u57a6\u7684\u80a5\u6c83\u571f\u5730\uff0c\u8fd9\u4e5f\u662f&ldquo;\u4e00\u8def\u4e00\u5e26&rdquo;\u6218\u7565\u5408\u4f5c\u7684\u9898\u4e2d\u4e4b\u4e49\u3002\u5982\u679c\u8fd9\u4e00\u5730\u533a\u6bcf\u5e74\u80fd\u591f\u4e3a\u63d0\u4f9b\u4e2d\u56fd2000\u4e07\u5428\u7684\u5927\u8c46\uff0c\u5176&ldquo;\u6760\u6746\u6548\u5e94&rdquo;\u5c06\u5bf9\u5168\u7403\u5927\u8c46\u8d38\u6613\u683c\u5c40\u4ea7\u751f\u6df1\u8fdc\u5f71\u54cd\u3002</span></div>\r\n\r\n<div class=\"editors-note\">&nbsp;</div>\r\n\r\n<div class=\"editors-note\"><span style=\"color:#696969\">\u540c\u6837\u501f\u9274\u80fd\u6e90\u5b89\u5168\u7684\u7ecf\u9a8c\uff0c\u4e2d\u56fd\u7684\u7cae\u98df\u5b89\u5168\u4e0d\u80fd\u4ec5\u4ec5\u505c\u7559\u5728\u4ea7\u91cf\u4e0a\uff0c\u63d0\u5347\u7cae\u98df\u5e93\u5b58\u3001\u7269\u6d41\u548c\u5168\u7403\u8d38\u6613\u80fd\u529b\u4e5f\u662f\u6211\u4eec\u7684\u5fc5\u4fee\u8bfe\u3002\u503c\u5f97\u6ce8\u610f\u7684\u662f\uff0c\u6d77\u5916\u6536\u8d2d\u519c\u4e1a\u7528\u5730\u975e\u5e38&ldquo;\u654f\u611f&rdquo;\uff0c\u9700\u8981\u501f\u52a9\u751f\u4ea7\u673a\u68b0\u3001\u4ed3\u50a8\u3001\u8fd0\u8f93\u7b49\u67d4\u6027\u65b9\u5f0f\u63a7\u5236\u7cae\u98df\u8d44\u6e90\u3002\u6700\u7ec8\u7684\u751f\u4ea7\u6210\u672c\u53ef\u80fd\u4f1a\u9ad8\u4e8e\u7f8e\u6d32\u5927\u8c46\uff0c\u7136\u800c\u653e\u773c\u4eca\u5929\u7684\u4e16\u754c\u683c\u5c40\uff0c\u6211\u4eec\u8981\u7b97\u7ecf\u6d4e\u8d26\uff0c\u66f4\u8981\u8003\u8651\u6218\u7565\u7a7a\u95f4\u3002\u8ffd\u6839\u6eaf\u6e90\uff0c\u8fd9\u7c92\u5706\u5706\u7684\u5927\u8c46\u6240\u8574\u542b\u7684\u662f\u5168\u7403\u519c\u4e1a\u8d44\u6e90\u7684\u4e00\u573a\u518d\u914d\u5236\u548c\u518d\u5e73\u8861\u3002</span></div>\r\n\r\n<h2><strong>\u4e16\u754c\u672b\u65e5\u548c\u79cd\u5b50\u5e93</strong></h2>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/cWKvjnSYbJY6r5ZXI3QaDvkm2Uul29gso5NSU9e1lhpYAgAAkAEAAEpQ.jpg\" style=\"height:400px; width:600px\" />\u7535\u5f71\u300a\u661f\u9645\u7a7f\u8d8a\u300b\u5267\u7167</p>\r\n\r\n<p>\u5927\u5bb6\u60f3\u60f3\u79d1\u5e7b\u7535\u5f71\u300a\u661f\u9645\u7a7f\u8d8a\u300b\u4e2d\u7684\u5267\u60c5\uff1a\u5e72\u65f1\u548c\u67af\u840e\u75c5\u5148\u540e\u706d\u7edd\u4e86\u5c0f\u9ea6\u3001\u79cb\u8475\u7b49\u4f5c\u7269\uff0c\u4f5c\u4e3a&ldquo;\u672b\u65e5\u4f5c\u7269&rdquo;\u7684\u7389\u7c73\u4e5f\u5944\u5944\u4e00\u606f\uff0c\u4eba\u7c7b\u8d70\u6295\u65e0\u8def\u3002\u5176\u5b9e\uff0c\u8fd9\u79cd\u5267\u60c5\u5e76\u975e\u5f02\u60f3\u5929\u5f00\u3002</p>\r\n\r\n<p>1954\u5e74\uff0c\u5b62\u56ca\u7ebf\u866b\u75c5\u4f7f\u7f8e\u56fd\u5927\u8c46\u79cd\u690d\u906d\u9047\u6bc1\u706d\u6027\u6253\u51fb\uff0c\u80b2\u79cd\u5b66\u5bb6\u4ece3000\u591a\u4efd\u79cd\u8d28\u8d44\u6e90\u4e2d&ldquo;\u7ffb\u7bb1\u5012\u67dc&rdquo;\uff0c\u6700\u7ec8\u627e\u5230\u4e8620\u4e16\u7eaa\u521d\u7f8e\u56fd\u4f20\u6559\u58eb\u4ece\u4e2d\u56fd\u6536\u96c6\u7684\u72ec\u7279\u5730\u65b9\u54c1\u79cd&mdash;&mdash;\u5317\u4eac\u5c0f\u9ed1\u8c46\u3002\u5229\u7528\u8fd9\u4e00\u54c1\u79cd\u4e2d\u53d1\u73b0\u4e86\u7279\u6709\u7684\u6297\u75c5\u57fa\u56e0\uff0c\u6700\u7ec8\u57f9\u80b2\u51fa\u7684\u65b0\u7684\u5927\u8c46\u54c1\u79cd\uff0c\u4f7f\u5f97\u7f8e\u56fd\u5927\u8c46\u4ea7\u4e1a\u5f97\u4ee5\u590d\u82cf\u3002\u5f53\u65f6\uff0c\u8fd9\u4efd\u6765\u81ea\u4e2d\u56fd\u7684\u79cd\u8d28\u8d44\u6e90\uff0c\u5df2\u5728\u7f8e\u56fd\u4fdd\u5b58\u4e8647\u5e74\u3002</p>\r\n\r\n<p>\u4ee5\u524d\u7684\u7c73\u996d\u716e\u719f\u540e\uff0c\u4f1a\u6709\u4e00\u5c42\u6cb9\u5149\uff1b\u4e00\u6ef4\u829d\u9ebb\u9999\u6cb9\u6ef4\u51fa\uff0c\u80fd\u8ba9\u6574\u4e2a\u5c4b\u5b50\u91cc\u98d8\u9999\u3002\u4eca\u5929\u7684\u8c37\u7269\u54c1\u79cd\u4ea7\u91cf\u8d8a\u6765\u8d8a\u9ad8\uff0c\u53e3\u611f\u5374\u5c11\u4e86\u66fe\u7ecf\u7684\u9187\u539a\u548c\u9999\u6c14\u3002\u5176\u5b9e\u635f\u5931\u7684\u4e0d\u53ea\u662f\u53e3\u611f\uff0c\u8fd8\u6709\u5927\u81ea\u7136\u4e2d\u7684\u751f\u7269\u591a\u6837\u6027\uff0c\u5c24\u5176\u662f\u79cd\u8d28\u8d44\u6e90\u3002\u5bf9\u4e8e\u4f5c\u7269\u80b2\u79cd\u800c\u8a00\uff0c<strong>\u79cd\u8d28\u8d44\u6e90\u5e93\u76f8\u5f53\u4e8e\u5e9e\u5927\u7684&ldquo;\u57fa\u56e0\u94f6\u884c&rdquo;</strong>\uff0c\u4e30\u5bcc\u7684\u751f\u7269\u591a\u6837\u6027\u662f\u5927\u81ea\u7136\u7ed9\u4eba\u7c7b\u7684\u9988\u8d60\u3002\u7136\u800c\uff0c\u4eca\u5929\u7684\u9ad8\u4ea7\u4f5c\u7269\u4e00\u65e6\u63a8\u800c\u5e7f\u4e4b\uff0c\u5c31\u5f00\u59cb&ldquo;\u8d62\u8005\u901a\u5403&rdquo;\uff0c\u4fb5\u5360\u5c0f\u4f17\u8c37\u7269\u54c1\u7cfb\u7684\u79cd\u690d\u7a7a\u95f4\u3002\u7edf\u8ba1\u6570\u636e\u663e\u793a\uff0c\u6211\u56fd\u5730\u65b9\u8c37\u7269\u54c1\u79cd\u7684\u6570\u76ee\uff0c\u5df2\u7ecf\u4ece1956\u5e74\u768411590\u4e2a\u4e0b\u964d\u52302014\u5e74\u76843271\u4e2a\uff0c\u4e27\u5931\u6bd4\u4f8b\u9ad8\u8fbe71.8%\u3002\u6839\u636e\u4e16\u754c\u7cae\u519c\u7ec4\u7ec7\u516c\u5e03\u7684\u8d44\u6599\uff0c<strong>\u5728\u8fc7\u53bb\u7684100\u5e74\u91cc\uff0c90%\u4ee5\u4e0a\u7684\u519c\u4f5c\u7269\u54c1\u79cd\u5df2\u7ecf\u4ece\u519c\u7530\u4e2d\u6d88\u5931</strong>\u3002</p>\r\n\r\n<p>\u4eca\u5929\uff0c\u5e72\u65f1\u3001\u9ad8\u6e29\u3001\u75c5\u866b\u5bb3\u5bf9\u5168\u7403\u8c37\u7269\u751f\u4ea7\u6b63\u5e26\u6765\u8d8a\u6765\u8d8a\u5927\u7684\u538b\u529b\uff0c\u672a\u6765\u5730\u7403\u73af\u5883\u4f1a\u8d70\u5411\u4f55\u65b9\uff1f\u5c06\u6765\u4f1a\u4e0d\u4f1a\u6709\u4e00\u5929\uff0c\u5f53\u4eba\u7c7b\u5f00\u59cb\u53bb\u5bfb\u627e\u80fd\u6297\u65f1\u3001\u6297\u75c5\u7684\u6027\u72b6\u65f6\uff0c\u5374\u5ffd\u7136\u53d1\u73b0\u5177\u5907\u8fd9\u4e9b\u57fa\u56e0\u7684\u690d\u7269\u5df2\u7ecf\u5728\u5730\u7403\u4e0a\u6d88\u5931\u4e86\uff1f\u4eba\u7c7b\u4e0d\u80fd\u53ea\u6709\u5bf9\u81ea\u5df1\u7684&ldquo;\u79c1\u5fc3&rdquo;\uff0c\u4e5f\u9700\u8981\u6709\u5bf9\u5927\u81ea\u7136\u7684&ldquo;\u516c\u5fc3&rdquo;\u3002\u65f6\u81f3\u4eca\u65e5\uff0c<strong>\u5f88\u5c11\u4eba\u8ba4\u8bc6\u5230\u751f\u7269\u591a\u6837\u6027\u5bf9\u4eba\u7c7b\u548c\u81ea\u7136\u7684\u91cd\u8981\u6027</strong>\u30022000\u5e74\uff0c\u8054\u5408\u56fd\u5927\u4f1a\u5ba3\u5e03\u5c06\u6bcf\u5e74\u76845\u670822\u65e5\u4e3a\u56fd\u9645\u751f\u7269\u591a\u6837\u6027\u65e5\u3002</p>\r\n\r\n<p>\u73b0\u5b9e\u793e\u4f1a\u4e2d\uff0c\u7684\u786e\u6709\u4eba\u5728\u4e3a&ldquo;\u4e16\u754c\u672b\u65e5&rdquo;\u672a\u96e8\u7ef8\u7f2a\u30022008\u5e74\uff0c\u632a\u5a01\u5728\u8ddd\u79bb\u5317\u6781\u5708\u6781\u70b91000\u591a\u516c\u91cc\u7684\u5c71\u4f53\u4e2d\u5efa\u6210\u4e86\u4e00\u5ea7\u603b\u957f120\u7c73\u7684&ldquo;\u5168\u7403\u79cd\u5b50\u5e93&rdquo;\u3002\u632a\u5a01\u4eba\u7ed9\u5b83\u53d6\u4e86\u4e2a\u54cd\u4eae\u7684\u540d\u5b57&mdash;&mdash;&ldquo;\u672b\u65e5\u7a79\u9876&rdquo;\u3002\u6d1e\u5185\u9762\u79ef\u7ea61\u5343\u5e73\u7c73\uff0c\u6bcf\u4e2a\u6837\u672c\u4fdd\u5b58\u7ea65\u767e\u7c92\u79cd\u5b50\u30022018\u5e74\uff0c<strong>&ldquo;\u5168\u7403\u79cd\u5b50\u5e93&rdquo;\u50a8\u5b58\u7684\u79cd\u5b50\u6837\u672c\u5df2\u8d85\u8fc7100\u4e07\u4efd</strong>\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/NnspIl6xE2iABB9APnnXM5oW7dUd3PGT4F8nuWBBXic4BAAAAQMAAEpQ.jpg\" style=\"height:456px; width:640px\" />\u5168\u7403\u79cd\u5b50\u5e93 | Wikimedia Commons</p>\r\n\r\n<p>\u79cd\u5b50\u5e93\u4f4d\u4e8e\u6781\u5bd2\u4e4b\u5730\uff0c\u53ef\u4ee5\u62b5\u5fa1\u70ed\u6d6a\u51b2\u51fb\u3002\u6d1e\u7a74\u9ad8\u4e8e\u6d77\u5e73\u9762130\u7c73\uff0c\u53ef\u4ee5\u62b5\u5fa1\u51b0\u5ddd\u878d\u5316\u5bfc\u81f4\u7684\u6d77\u9762\u5347\u9ad8\u3002\u5982\u679c\u4eba\u7c7b\u5728\u9047\u5230\u6838\u6218\u4e89\u3001\u5c0f\u884c\u661f\u649e\u51fb\u3001\u6c14\u5019\u5267\u53d8\u3001\u6d77\u5e73\u9762\u4e0a\u5347\u7b49\u672b\u65e5\u5371\u673a\uff0c\u5728\u6ca1\u6709\u5176\u4ed6\u79cd\u5b50\u53ef\u7528\u7684\u60c5\u51b5\u4e0b\uff0c\u53ef\u4ee5\u6253\u5f00&ldquo;\u672b\u65e5\u7a79\u9876&rdquo;\uff0c\u5728\u5730\u7403\u4e0a\u91cd\u65b0\u5efa\u7acb\u8c37\u7269\u751f\u4ea7\u7cfb\u7edf\u3002</p>\r\n\r\n<p>\u4eca\u5929\uff0c\u5f88\u591a\u670b\u53cb\u611f\u53d7\u7684\u662f\u5c81\u6708\u9759\u597d\u3001\u8863\u98df\u65e0\u5fe7\uff0c\u4f24\u8111\u7b4b\u7684\u662f\u8425\u517b\u8fc7\u5269\u548c\u80a5\u80d6\u75c7\u3002\u5176\u5b9e\u4e2d\u56fd\u80fd\u5403\u9971\u996d\uff0c\u4e0d\u8fc7\u662f\u6700\u8fd130\u5e74\u7684\u4e8b\u60c5\u3002\u4eca\u5929\uff0c\u6211\u4eec\u9762\u4e34\u7740\u4eba\u591a\u5730\u5c11\u3001\u5316\u5b66\u519c\u4e1a\u6c61\u67d3\u3001\u8015\u5730\u8d28\u91cf\u9000\u5316\u3001\u5730\u4e0b\u6c34\u4f4d\u4e0b\u964d\u3001\u8c37\u7269\u54c1\u79cd\u591a\u6837\u6027\u4e27\u5931\u7b49\u590d\u6742\u95ee\u9898\uff0c<strong>\u5982\u4f55\u5b9e\u73b0\u7cae\u98df\u5b89\u5168\u548c\u8d44\u6e90\u3001\u73af\u5883\u548c\u53ef\u6301\u7eed\u519c\u4e1a\u4e4b\u95f4\u7684\u5e73\u8861\uff0c\u662f\u6446\u5728\u4e2d\u56fd\u9762\u524d\u7684\u4e00\u9879\u523b\u4e0d\u5bb9\u7f13\u7684\u6218\u7565\u8bae\u9898</strong>\u3002</p>\r\n\r\n<p>\u5e0c\u671b\u5927\u5bb6\u5bf9\u7cae\u98df\u5b89\u5168\u80fd\u6709\u66f4\u4e3a\u5168\u9762\u7684\u8ba4\u8bc6\uff0c<strong>\u7231\u62a4\u73af\u5883\uff0c\u73cd\u60dc\u7cae\u98df</strong>\uff01\uff08\u7f16\u8f91\uff1aYuki\uff09</p>\r\n\r\n<div>\u5d14\u51ef\u300a\u8c37\u7269\u7684\u6545\u4e8b\u300b\u7cfb\u5217:</div>\r\n\r\n<div><strong>\u8c37\u7269\u7684\u8d77\u6e90\uff1a</strong><a href=\"https://www.guokr.com/article/454812/\" target=\"_blank\">\u6211\u4eec\u6bcf\u5929\u5403\u7684\u7c73\u996d\uff0c\u6700\u65e9\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f</a></div>\r\n\r\n<div><strong>\u8c37\u7269\u7684\u4e00\u751f\uff1a</strong><a href=\"https://www.guokr.com/article/454871/\" target=\"_blank\">\u6ca1\u7a7a\u604b\u7231\uff0c\u53ea\u80fd\u548c\u65b9\u5706\u767e\u7c73\u7684\u5f02\u6027\u95ea\u5a5a\uff0c\u5b83\u4eec\u5b9e\u5728\u82e6\u547d\uff01</a></div>\r\n\r\n<h2 class=\"references\"><span style=\"color:#808080\"><span style=\"font-size:14px\">\u53c2\u8003\u6587\u732e\uff1a</span></span></h2>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[1].Kai Cui etc\uff0cA look at food security in China\uff0cnpj Science of Food (2018) 2:4-5\u9875;</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[2].\u4e54\u91d1\u4eae\uff0c\u9ed1\u571f\u5730\u53d8\u8584\u4ee4\u4eba\u62c5\u5fe7 \u300a\u7ecf\u6d4e\u65e5\u62a5\u300b2015\u5e747\u670828\u65e5</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[3].\u5218\u6d77\u82e5\u7b49\uff0c\u534e\u5317\u4e95\u704c\u533a\u5730\u4e0b\u6c34\u6c34\u4f4d\u53d8\u5316\u73b0\u72b6\u53ca\u5e94\u5bf9\u63aa\u65bd\u5efa\u8bae\uff0c\u6c34\u8d44\u6e90\u7ba1\u7406\uff0c2016\u5e74\u7b2c9\u671f\uff1a25-28</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[4].\u5d14\u51ef\uff0c\u7528\u5927\u8c46\u53cd\u5236\u7f8e\u56fd\u8d38\u6613\u6218\uff1f\u6ca1\u90a3\u4e48\u7b80\u5355\uff01\u77e5\u8bc6\u5206\u5b50&rdquo;\u516c\u4f17\u53f7\uff0c2018\u5e743\u670827\u65e5</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[5].\u5d14\u51ef\uff0c\u300a\u5168\u56fd\u8f6c\u57fa\u56e0\u79d1\u666e\u5de1\u8bb2\u300b\u8bb2\u4e49\uff0c\u4e2d\u56fd\u519c\u5b66\u4f1a\uff0c2019\u5e749\u670826\u65e5</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[6].\u4faf\u840c\u7476\u7b49\uff0c\u4e2d\u56fd\u4e3b\u8981\u519c\u4f5c\u7269\u5316\u80a5\u7528\u91cf\u4f30\u7b97\uff0c\u519c\u4e1a\u8d44\u6e90\u4e0e\u73af\u5883\u5b66\u62a5\uff0c2017 \u5e74\u7b2c34 \u5377\uff1a360-367</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[7].\u738b\u7433\u7433\uff0c\u751f\u7269\u591a\u6837\u6027\u662f\u7cae\u98df\u5b89\u5168\u548c\u4eba\u7c7b\u5065\u5eb7\u7684\u57fa\u7840\uff0c\u4e2d\u56fd\u73af\u5883\u62a5\uff0c2019\u5e745\u670822\u65e5</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[8].\u6768\u8212\uff0c\u5b88\u62a4\u597d\u6211\u4eec\u7684\u79cd\u8d28\u8d44\u6e90\uff0c\u5149\u660e\u65e5\u62a5 2019\u5e745\u670822\u65e5</span></span></div>", "date_created": "2019-10-30T10:50:29.454228+08:00", "date_modified": "2019-10-30T11:48:56.414604+08:00", "date_published": "2019-10-30T14:00:57.806282+08:00", "id": 454942, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": null, "replies_count": 12, "small_image": "https://1-im.guokr.com/trgTkU55YoLToj568x0yaxNaXNQ6Qb8W0PECT8ZWcDbAAwAA0AIAAEpQ.jpg", "subject": {"articles_count": 87, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "agronomy", "minisite_key": "None", "name": "\u519c\u5b66", "sort_score": 15, "subject_type": "normal"}, "subject_key": "agronomy", "summary": "\u300a\u8c37\u7269\u7684\u6545\u4e8b\u300b3\uff1a\u4e2d\u56fd\u7684\u7cae\u98df\u5b89\u5168", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|1994\u5e74\uff0c\u7f8e\u56fd\u5b66\u8005\u5e03\u6717\u5199\u4e86\u4e00\u7bc7\u6587\u7ae0\uff0c\u9898\u76ee\u4e3a\u300a\u8c01\u6765\u517b\u6d3b\u4e2d\u56fd\u300b\uff0c\u5728\u897f\u65b9\u4e16\u754c\u5f15\u8d77\u5e7f\u6cdb\u5173\u6ce8\u300225\u5e74\u8fc7\u53bb\uff0c\u4e2d\u56fd\u7528\u5168\u4e16\u754c8%\u7684\u8015\u5730\u517b\u6d3b\u4e8618%\u7684\u4eba\u53e3\uff0c\u4f46\u662f\u5e03\u6717\u63d0\u51fa\u7684\u95ee\u9898\uff0c\u4f9d\u65e7\u503c\u5f97\u6211\u4eec\u8b66\u60d5\u30022019\u5e7410\u670814\u65e5\uff0c\u56fd\u52a1\u9662\u65b0\u95fb\u529e\u65f6\u969423\u5e74\u518d\u6b21\u53d1\u8868\u4e86\u300a\u4e2d\u56fd\u7684\u7cae\u98df\u5b89\u5168\u300b\u767d\u76ae\u4e66\uff0c\u660e\u786e\uff1a\u4ece\u4e2d\u957f\u671f\u770b\uff0c\u4e2d\u56fd\u7684\u7cae\u98df\u4ea7\u9700\u4ecd\u5c06\u7ef4\u6301\u7d27\u5e73\u8861\u6001\u52bf\u3002\u4e2d\u56fd\u7684\u8d44\u6e90\u7980\u8d4b\u6b4c\u624b\u674e\u5065\u6f14\u5531\u8fc7\u7684\u4e00\u9996\u6b4c\uff1a\u300a\u7236\u4eb2\u5199\u7684\u6563\u6587\u8bd7\u300b\uff0c\u5176\u4e2d\u6709\u4e00\u6bb5\u6734\u5b9e\u65e0\u534e\u7684\u6b4c\u8bcd\uff1a\u201c\u4e00\u4e5d\u516b\u56db\u5e74\uff0c\u5e84\u7a3c\u8fd8\u6ca1\u6536\u5272\u5b8c\uff0c\u513f\u5b50\u8eba\u5728\u6211\u6000\u91cc\uff0c\u7761\u5f97\u90a3\u4e48\u751c\u3002\u660e\u5929\u6211\u8981\u53bb\u90bb\u5c45\u5bb6\uff0c\u518d\u501f\u70b9\u94b1\uff0c\u5b69\u5b50\u54ed\u4e86\u4e00\u6574\u5929\u4e86\uff0c\u95f9\u7740\u8981\u5403\u997c\u5e72\u3002\u8fd9\u662f\u6211\u7236\u4eb2\u65e5\u8bb0\u91cc\u7684\u6587\u5b57\uff0c\u8fd9\u662f\u4ed6\u7684\u9752\u6625\u7559\u4e0b\u6765\u7684\u6563\u6587\u8bd7\u3002\u8fd9\u7247\u571f\u5730\u66fe\u8ba9\u6211\u6cea\u6d41\u4e0d\u6b62\uff0c\u5b83\u57cb\u846c\u4e86\u591a\u5c11\u4eba\u5fc3\u9178\u7684\u5f80\u4e8b\u3002\u201d\u6211\u4eec\u628a\u65f6\u95f4\u5750\u6807\u5c31\u653e\u5728\u6b4c\u4e2d\u5531\u5230\u76841984\u5e74\u3002\u90a3\u4e00\u5e74\u6536\u6210\u4e0d\u9519\uff0c\u4e2d\u56fd\u7cae\u98df\u4ea7\u91cf\u9996\u6b21\u7a81\u78344\u4ebf\u5428\uff0c\u4eba\u5747\u7cae\u98df390\u516c\u65a4\uff0c\u53ea\u76f8\u5f53\u4e8e\u4eca\u5929\u768470%\u3002\u5728\u5f53\u65f6\uff0c\u8fd9\u4e9b\u7cae\u98df\u80fd\u591f\u7ef4\u6301\u8001\u767e\u59d3\u7684\u57fa\u672c\u6e29\u9971\uff0c\u5374\u5206\u4e0d\u51fa\u66f4\u591a\u7684\u7cae\u98df\u53bb\u5582\u732a\u517b\u9e21\u30021984\u5e74\uff0c\u4e2d\u56fd\u4eba\u5747\u8089\u7c7b\u53ea\u670915\u516c\u65a4\uff0c\u76f8\u5f53\u4e8e\u4eca\u5929\u76841/4\u3002\u5f53\u65f6\uff0c\u56fd\u5bb6\u5b9e\u884c\u7cae\u98df\u5b9a\u91cf\u4f9b\u5e94\uff0c\u8d2d\u4e70\u7cae\u98df\u9700\u8981\u7cae\u7968\u3002\u76f4\u52301993\u5e74\uff0c\u7cae\u7968\u624d\u9000\u51fa\u5386\u53f2\u821e\u53f0\u3002\u00a01980\u5e74\uff0c\u6211\u8fd8\u5728\u8bfb\u5c0f\u5b66\uff0c\u957f\u5f97\u5f88\u5355\u8584\u3002\u90a3\u5e74\u5934\uff0c\u5976\u7c89\u5bf9\u4e8e\u5bfb\u5e38\u767e\u59d3\u5bb6\u8fd8\u662f\u5962\u4f88\u54c1\u3002\u4e3a\u4e86\u5e2e\u6211\u8865\u5145\u8425\u517b\uff0c\u7236\u4eb2\u4ece\u6751\u91cc\u7684\u69a8\u6cb9\u574a\u4e70\u6765\u5bcc\u542b\u86cb\u767d\u8d28\u7684\u8c46\u7c95\uff0c\u653e\u5728\u767d\u8272\u7684\u5e03\u888b\u91cc\uff0c\u8ba9\u6211\u62ff\u7740\u6d17\u8863\u68d2\u69cc\u7528\u529b\u62cd\u6253\u3002\u8c46\u7c95\u88ab\u6363\u788e\u540e\uff0c\u518d\u63ba\u5230\u7389\u7c73\u9762\u4e2d\uff0c\u84b8\u51fa\u7684\u7389\u7c73\u7a9d\u5934\u6709\u4e00\u79cd\u72ec\u7279\u7684\u8c46\u9999\u3002\u4eca\u5929\u56de\u60f3\u8d77\u6765\uff0c\u5c11\u5e74\u65f6\u5403\u7684\u201c\u7389\u7c73+\u8c46\u7c95\u201d\u5c31\u662f\u4eca\u5929\u7684\u9972\u6599\u914d\u65b9\u2014\u2014\u8fd9\u4e9b\u4e8b\u5728\u4eca\u5929\u7684\u5e74\u8f7b\u670b\u53cb\u773c\u4e2d\uff0c\u5df2\u7ecf\u662f\u53e4\u8001\u7684\u4f20\u8bf4\u30022018\u5e74\u4e2d\u56fd\u7cae\u98df\u7684\u4ea7\u91cf\u7ed3\u6784 | \u6570\u636e\u6765\u6e90\uff1a\u56fd\u5bb6\u7edf\u8ba1\u5c40\u89c6\u7ebf\u8f6c\u56de\u5230\u4eca\u5929\u30022015\u5e74\u4ee5\u6765\uff0c\u4e2d\u56fd\u7cae\u98df\u4ea7\u91cf\u8fde\u7eed\u56db\u5e74\u7a33\u5b9a\u57286.5\u4ebf\u5428\uff0c\u518d\u52a0\u4e0a\u6bcf\u5e74\u8fdb\u53e3\u76841.2\u4ebf\u5428\u7cae\u98df\uff0c\u5408\u8ba1\u6d88\u8d397.7\u4ebf\u5428\u3002\u5982\u679c\u6309\u7167\u91cd\u91cf\u6298\u7b97\uff0c1.2\u4ebf\u5428\u7684\u8fdb\u53e3\u91cf\u4ec5\u76f8\u5f53\u4e8e6.6\u4ebf\u5428\u56fd\u5185\u603b\u4ea7\u91cf\u76841/6\uff0c\u770b\u4f3c\u95ee\u9898\u4e0d\u5927\uff0c\u5176\u5b9e\u4e0d\u7136\u3002\u628a\u8fd91.2\u4ebf\u5428\u8fdb\u53e3\u7cae\u98df\u6298\u7b97\u6210\u8015\u5730\uff0c\u76f8\u5f53\u4e8e6\u4ebf\u4ea9\u7684\u8015\u5730\u3002\u5982\u679c\u518d\u52a0\u4e0a\u8fdb\u53e3\u7684\u68c9\u82b1\u3001\u767d\u7cd6\u3001\u6c34\u679c\u3001\u8089\u7c7b\u3001\u5976\u7c89\u7b49\uff0c\u5219\u8d85\u8fc77\u4ebf\u4ea9\u3002\u6309\u7167\u5168\u56fd20\u4ebf\u4ea9\u8015\u5730\u9762\u79ef\u8ba1\u7b97\uff0c\u6211\u4eec\u7684\u8015\u5730\u201c\u5bf9\u5916\u4f9d\u5b58\u5ea6\u201d\u8fbe\u523035%\u3002\u7cae\u98df\u751f\u4ea7\u9700\u8981\u6d88\u8017\u8015\u5730\u3001\u5316\u80a5\u3001\u519c\u836f\u548c\u6de1\u6c34\uff0c\u8fdb\u53e3\u7cae\u98df\u672c\u8d28\u4e0a\u5c31\u662f\u5728\u8fdb\u53e3\u8d44\u6e90\u3002\u8bf7\u5927\u5bb6\u770b\u770b\u4e0b\u9762\u8fd9\u5f20\u8868\u683c\u3002\u5c31\u56fd\u571f\u9762\u79ef\u800c\u8a00\uff0c960\u4e07\u5e73\u65b9\u516c\u91cc\u7684\u4e2d\u56fd\u6392\u5728\u4e16\u754c\u7b2c3\uff0c\u4f46\u662f\u5f88\u591a\u5730\u65b9\u6216\u662f\u5e72\u65f1\u5c11\u96e8\u7684\u6c99\u6f20\u6208\u58c1\uff0c\u6216\u662f\u5e38\u5e74\u4f4e\u6e29\u7684\u9ad8\u539f\u5bd2\u5730\uff0c\u6211\u4eec\u7684\u8015\u5730\u5360\u56fd\u571f\u9762\u79ef\u6bd4\u91cd\u4ec5\u670914%\u3002\u4eba\u591a\u5730\u5c11\uff0c\u4eba\u5747\u8015\u5730\u9762\u79ef\u4ec5\u4e3a0.1\u516c\u9877\uff0c\u8fdc\u4f4e\u4e8e\u4fc4\u7f57\u65af\u3001\u7f8e\u56fd\u3001\u5df4\u897f\u548c\u963f\u6839\u5ef7\u7b49\u519c\u4e1a\u5927\u56fd\u3002\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u80fd\u591f\u8ba914\u4ebf\u4eba\u5403\u9971\uff0c\u5c31\u662f\u4e2d\u56fd\u5bf9\u4e16\u754c\u6700\u5927\u7684\u8d21\u732e\u3002\u7136\u800c\u6211\u4eec\u4e3a\u6b64\u4e5f\u4ed8\u51fa\u4e86\u6c89\u91cd\u7684\u4ee3\u4ef7\u2014\u2014\u4e3a\u4e86\u63a7\u5236\u75c5\u866b\u5bb3\uff0c\u519c\u7530\u91cc\u5927\u91cf\u55b7\u6d12\u519c\u836f\uff0c\u4e25\u91cd\u6c61\u67d3\u6c34\u571f\u73af\u5883\uff0c\u7834\u574f\u751f\u6001\u5e73\u8861\u3002\u5728\u6211\u7684\u5c11\u5e74\u8bb0\u5fc6\u4e2d\uff0c\u665a\u971e\u4e2d\u873b\u8713\u98de\u821e\uff0c\u590f\u591c\u91cc\u86d9\u58f0\u4e00\u7247\u3002\u4eca\u5929\u518d\u53bb\u770b\u770b\uff0c\u8fd9\u79cd\u753b\u9762\u5df2\u7ecf\u6210\u4e3a\u56de\u5fc6\u3002\u4e3a\u4e86\u589e\u52a0\u7cae\u98df\u4ea7\u91cf\uff0c\u6211\u4eec\u4f7f\u7528\u4e86\u5168\u4e16\u754c1/3\u7684\u5316\u80a5\u3002\u4e2d\u56fd\u5316\u80a5\u7528\u91cf\u81ea1984 \u5e74\u5df2\u7ecf\u8fde\u7eed30\u591a\u5e74\u6210\u4e3a\u4e16\u754c\u7b2c\u4e00\u3002\u7136\u800c\u6211\u4eec\u7684\u5316\u80a5\u5229\u7528\u7387\u4ec5\u4e3a38%\uff0c\u8fdc\u4f4e\u4e8e\u53d1\u8fbe\u56fd\u5bb650-60%\u7684\u6c34\u5e73\u3002\u7cae\u98df\u4f5c\u7269\u7684\u5316\u80a5\u7528\u91cf\u5360\u519c\u4f5c\u7269\uff08\u8fd8\u5305\u62ec\u852c\u83dc\u3001\u6c34\u679c\u3001\u68c9\u82b1\u7b49\uff09\u603b\u91cf\u76841/2\uff0c\u4eca\u5929\u5e73\u5747\u6bcf100\u65a4\u7cae\u98df\u5c31\u9700\u8981\u65bd\u80a56\u65a4\u3002\u8fc7\u91cf\u65bd\u80a5\u7834\u574f\u4e86\u8015\u5730\u7684\u571f\u58e4\u7ed3\u6784\uff0c\u9020\u6210\u571f\u58e4\u677f\u7ed3\u548c\u76d0\u78b1\u5316\u3002\u8fc7\u5269\u7684\u5316\u80a5\u53c8\u968f\u7740\u96e8\u6c34\u6d41\u5165\u6c5f\u6e56\u6e56\u6cca\uff0c\u5bfc\u81f4\u6c34\u4f53\u5bcc\u8425\u517b\u5316\uff0c\u85fb\u7c7b\u690d\u7269\u75af\u957f\uff0c\u6c34\u4f53\u53d1\u81ed\u3002\u5927\u91cf\u85fb\u7c7b\u8986\u76d6\u6c34\u9762\uff0c\u906e\u5149\u8017\u6c27\uff0c\u5f71\u54cd\u6c34\u5e95\u751f\u7269\u751f\u957f\uff0c\u6574\u4e2a\u6c34\u751f\u751f\u6001\u7cfb\u7edf\u906d\u53d7\u7834\u574f\uff0c\u592a\u6e56\u84dd\u85fb\u7206\u53d1\u5c31\u662f\u6700\u5178\u578b\u7684\u4e8b\u4f8b\u4e4b\u4e00\u3002\u8015\u5730\u8d28\u91cf\u76f4\u63a5\u5173\u7cfb\u5230\u56fd\u5bb6\u7cae\u98df\u5b89\u5168\u3002\u9ed1\u571f\u5730\u662f\u4e16\u754c\u516c\u8ba4\u6700\u80a5\u6c83\u7684\u571f\u58e4\uff0c\u201c\u63d2\u6839\u7b77\u5b50\u4e5f\u53d1\u82bd\u201d\u3002\u4e1c\u5317\u662f\u5168\u7403\u4e09\u5927\u9ed1\u571f\u533a\u4e4b\u4e00\uff0c\u7cae\u98df\u4ea7\u91cf\u5360\u5168\u56fd\u76841/4\uff0c\u8c03\u51fa\u91cf\u5360\u5168\u56fd\u76841/3\uff0c\u662f\u4e2d\u56fd\u91cd\u8981\u7684\u7cae\u4ed3\u3002\u8fd9\u4e9b\u5e74\uff0c\u5728\u7cae\u98df\u589e\u4ea7\u7684\u540c\u65f6\uff0c\u9ed1\u571f\u5730\u4e5f\u88ab\u8fc7\u5ea6\u5f00\u53d1\u5229\u7528\uff0c\u571f\u58e4\u660e\u663e\u9000\u5316\uff0c\u75b2\u60eb\u4e0d\u582a\u3002\u9ed1\u571f\u5c42\u5df2\u7531\u4e0a\u4e16\u7eaa50\u5e74\u4ee3\u768460-70\u5398\u7c73\uff0c\u4e0b\u964d\u5230\u4eca\u5929\u768420-30\u5398\u7c73\u2014\u2014\u9ed1\u571f\u662f\u4e0d\u53ef\u518d\u751f\u7684\u5b9d\u8d35\u8d44\u6e90\uff0c\u5f62\u62101\u5398\u7c73\u9ed1\u571f\u5c42\u9700\u8981\u51e0\u767e\u5e74\u7684\u65f6\u95f4\u3002\u8fc7\u53bb\u9ed1\u571f\u5c42\u4e30\u539a\uff0c\u811a\u8e29\u4e0a\u53bb\u5c31\u50cf\u8e29\u5728\u6d77\u7ef5\u4e0a\u4e00\u6837\u3002\u73b0\u5728\u571f\u5c42\u53d8\u8584\uff0c\u8425\u517b\u4e0d\u8db3\uff0c\u4e0d\u5f97\u4e0d\u591a\u65bd\u5316\u80a5\u3002\u5316\u80a5\u8d8a\u7528\u8d8a\u591a\uff0c\u53c8\u5bfc\u81f4\u571f\u58e4\u677f\u7ed3\uff0c\u8e29\u4e0a\u53bb\u751a\u81f3\u770b\u4e0d\u5230\u811a\u5370\u3002\u201c\u4e07\u7269\u571f\u4e2d\u751f\uff0c\u6709\u571f\u65af\u6709\u7cae\u201d\uff0c\u9ed1\u571f\u5730\u7684\u53ef\u6301\u7eed\u53d1\u5c55\u95ee\u9898\u4e9f\u5f85\u89e3\u51b3\u3002\u4e1c\u5317\u9ed1\u571f\u5730 \u00a0| \u6444\uff1a\u5b9b\u51cc\u8fc5\u4e2d\u56fd\u7684\u4eba\u5747\u6c34\u8d44\u6e90\u4ec5\u4e3a2050\u7acb\u65b9\u7c73\uff0c\u4ec5\u4ec5\u76f8\u5f53\u4e8e\u4e16\u754c\u5e73\u5747\u6c34\u5e73\u76841/4\u3002\u519c\u4e1a\u704c\u6e89\u6d88\u8017\u4e86\u4e2d\u56fd60%\u7684\u53ef\u5229\u7528\u6c34\u8d44\u6e90\uff0c\u7528\u6c34\u6548\u7387\u5374\u4ec5\u4e3a30-40%\uff0c\u8fdc\u4f4e\u4e8e\u53d1\u8fbe\u56fd\u5bb670-80%\u7684\u6c34\u5e73\u3002\u5728\u5317\u65b9\u65f1\u533a\uff0c\u7531\u4e8e\u6c34\u8d44\u6e90\u77ed\u7f3a\uff0c\u6709\u4e9b\u5730\u65b9\u8fc7\u5ea6\u5f00\u91c7\u5730\u4e0b\u6c34\u8d44\u6e90\u7528\u4e8e\u519c\u4e1a\u704c\u6e89\u3002\u534e\u5317\u4e95\u704c\u533a\u662f\u4e2d\u56fd\u7684\u7cae\u98df\u4e3b\u4ea7\u533a\u4e4b\u4e00\u3002\u7531\u4e8e\u8fc7\u53bb30\u51e0\u5e74\u7684\u5f00\u91c7\uff0c\u8fd9\u91cc\u7684\u5730\u4e0b\u6c34\u4f4d\u4e0b\u964d\u5e45\u5ea6\u751a\u81f3\u8d85\u8fc720\u7c73\uff0c\u6210\u4e3a\u4e16\u754c\u4e0a\u6700\u5927\u7684\u5730\u4e0b\u6c34\u6f0f\u6597\u533a\u3002\u653e\u773c\u672a\u6765\uff0c\u4e2d\u56fd\u7684\u4eba\u53e3\u5cf0\u503c\u9884\u8ba1\u5c06\u51fa\u73b0\u57282028 \u5e74\u524d\u540e\uff0c\u603b\u4eba\u53e3\u7ea6\u4e3a14.2 \u4ebf\u3002\u867d\u7136\u6b64\u540e\u7684\u4eba\u53e3\u6570\u91cf\u5f00\u59cb\u4e0b\u964d\uff0c\u4f462050 \u5e74\u524d\u4ecd\u4e0d\u4f1a\u5c11\u4e8e12 \u4ebf\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u672a\u676530\u5e74\u4e2d\u56fd\u5c06\u4f9d\u7136\u9762\u5bf9\u7cae\u98df\u5b89\u5168\u538b\u529b\u3002\u653e\u773c\u4e16\u754c\uff0c\u4eca\u5929\u5730\u7403\u4e0a\u5df2\u7ecf\u670976\u4ebf\u4eba\uff0c\u5176\u4e2d10%\u5904\u4e8e\u9965\u997f\u72b6\u6001\uff0c\u5305\u62ec1.5\u4ebf\u53d1\u80b2\u4e0d\u826f\u7684\u513f\u7ae5\u3002\u9884\u8ba1\u52302050\u5e74\uff0c\u4e16\u754c\u4eba\u53e3\u4f1a\u589e\u52a0\u523095\u4ebf\uff0c2100\u5e74\u4f1a\u8fbe\u5230110\u4ebf\u3002\u8ba9\u5730\u7403\u517b\u6d3b\u8fd9\u6837\u591a\u7684\u4eba\u53e3\uff0c\u5f88\u4e0d\u8f7b\u677e\u3002\u201c\u727a\u7272\u201d\u5927\u8c46\uff0c\u786e\u4fdd\u201c\u996d\u7897\u201d\u4e2d\u56fd\u662f\u5927\u8c46\u7684\u6545\u4e61\uff0c5000\u5e74\u524d\uff0c\u534e\u590f\u5148\u6c11\u5f00\u542f\u5927\u8c46\u79cd\u690d\uff0c\u4ece\u6b64\u201c\u4e2d\u539f\u6709\u83fd\uff0c\u5eb6\u6c11\u91c7\u4e4b\u201d\u3002\u8c46\u8150\u91cc\u6709\u534e\u590f\u6c11\u65cf\u5343\u5e74\u966a\u4f34\u7684\u82b3\u9999\uff0c\u6709\u201c\u716e\u8c46\u71c3\u8c46\u8401\u201d\u7684\u8bd7\u53e5\u3002\u516c\u51438\u4e16\u7eaa\u7684\u5510\u671d\uff0c\u9274\u771f\u4e1c\u6e21\uff0c\u5c06\u5927\u8c46\u4f20\u5230\u65e5\u672c\u3002\u5230\u4e8618\u4e16\u7eaa\u7684\u6e05\u671d\uff0c\u5927\u8c46\u53c8\u4ece\u4e2d\u56fd\u4f20\u5165\u6b27\u7f8e\u3002\u4eca\u5929\uff0c\u5927\u8c46\u5728\u5730\u7403\u4e0a\u7684\u79cd\u690d\u9762\u79ef\u8fbe\u52301.2\u4ebf\u516c\u9877\uff0c\u7ea6\u5360\u5168\u4e16\u754c\u8015\u5730\u9762\u79ef\u76848%\uff0c\u53ef\u4ee5\u8bf4\uff0c\u5927\u8c46\u662f\u4e2d\u56fd\u9001\u7ed9\u4e16\u754c\u7684\u6700\u597d\u793c\u7269\u3002\u5728\u4e0a\u4e2a\u4e16\u7eaa\u4e2d\u671f\uff0c\u662f\u4e2d\u56fd\u5927\u8c46\u7684\u8f89\u714c\u65f6\u671f\u3002\u5927\u8c46\u548c\u8336\u3001\u4e1d\u662f\u4e2d\u56fd\u51fa\u53e3\u7684\u4e09\u5927\u540d\u4ea7\uff0c\u5728\u4e16\u754c\u4e0a\u4eab\u6709\u76db\u8a89\u3002\u5f53\u65f6\uff0c\u4e2d\u56fd\u5927\u8c46\u79cd\u690d\u9762\u79ef\u5360\u5168\u4e16\u754c\u768440%\uff0c\u7136\u800c\u572880\u5e74\u4ee3\u4e0b\u964d\u523020%\uff0c\u73b0\u5728\u53ea\u67097%\u30021995\u5e74\uff0c\u4e2d\u56fd\u5f00\u59cb\u53d8\u6210\u5927\u8c46\u8fdb\u53e3\u56fd\uff0c\u8fdb\u53e3\u91cf\u4e00\u8def\u98d9\u5347\u52302018\u5e74\u76849000\u4e07\u5428\uff0c\u5360\u5168\u7403\u5927\u8c46\u8d38\u6613\u76842/3\u3002\u4e3a\u4ec0\u4e48\u8981\u8fdb\u53e3\u8fd9\u4e48\u591a\u5927\u8c46\uff1f\u5f88\u591a\u516c\u4f17\u5bf9\u6b64\u5e76\u4e0d\u7406\u89e3\u3002\u6b64\u95f4\u6709\u884c\u4e1a\u6ca6\u9677\u7684\u60b2\u60c5\uff0c\u6709\u7cae\u98df\u5b89\u5168\u7684\u5fe7\u8651\uff0c\u6709\u8f6c\u57fa\u56e0\u7684\u4e89\u8bba\uff0c\u6709\u8d38\u6613\u6218\u7684\u7231\u56fd\u60c5\u6000\u3002\u76d8\u6839\u9519\u8282\uff0c\u526a\u4e0d\u65ad\uff0c\u7406\u8fd8\u4e71\u3002\u5927\u8c46\u662f\u5bcc\u542b\u86cb\u767d\u8d28\u7684\u8c37\u7269\uff0c\u8fd9\u4e00\u7279\u70b9\u4e5f\u51b3\u5b9a\u4e86\u5b83\u7684\u5bbf\u547d\u2014\u2014\u4e0d\u53ef\u6216\u7f3a\u7684\u9972\u6599\u914d\u6599\u3002\u6211\u4eec\u9910\u684c\u4e0a\u7684\u8089\u3001\u86cb\u3001\u5976\u90fd\u662f\u9972\u6599\u8f6c\u5316\u800c\u6765\u3002\u5728\u9972\u6599\u4e2d\uff0c\u7389\u7c73\u536065%\uff0c\u69a8\u6cb9\u540e\u7684\u8c46\u7c95\u536020%\u3002\u732a\u6bcf\u54032.7\u65a4\u9972\u6599\u53ef\u4ee5\u957f\u51fa1\u65a4\u8089\uff0c\u800c\u9e21\u6bcf\u54032\u65a4\u9972\u6599\u5c31\u80fd\u957f\u51fa1\u65a4\u8089\u6216\u4ea7\u51fa1\u65a4\u86cb\u3002\u4e1c\u5317\u5927\u8c46 | \u6444\u4e8e\u5409\u6797\u519c\u4e1a\u5927\u5b66\u5b9e\u9a8c\u519c\u573a\u8fdb\u516590\u5e74\u4ee3\uff0c\u767e\u59d3\u751f\u6d3b\u4ece\u201c\u5403\u9971\u201d\u8fdb\u5165\u201c\u5403\u597d\u201d\uff0c\u8089\u86cb\u5976\u6d88\u8d39\u91cf\u8fc5\u901f\u4e0a\u5347\uff0c\u8089\u7c7b\u4ea7\u91cf\u4ece1990\u5e74\u76842800\u4e07\u5428\u589e\u957f\u52302018\u5e74\u76848500\u4e07\u5428\uff0c\u63d0\u9ad8\u4e86\u6574\u65743\u500d\u3002\u4f5c\u4e3a\u9972\u6599\u6838\u5fc3\u914d\u6599\u7684\u8c46\u7c95\u9700\u6c42\u91cf\u4e5f\u540c\u6bd4\u589e\u52a0\u3002\u4eca\u5929\uff0c\u56fd\u4ea7\u5927\u8c46\u4ea7\u91cf\u4ec5\u4e3a1500\u4e07\u5428\uff0c\u8fdb\u53e3\u91cf\u8fbe\u52309000\u4e07\u5428\u3002\u6362\u8a00\u4e4b\uff0c\u5927\u8c46\u7684\u8fdb\u53e3\u4f9d\u5b58\u5ea6\u8d85\u8fc780%\u3002\u66fe\u6709\u516c\u4f17\u8d28\u7591\uff1a\u4e3a\u4ec0\u4e48\u4e0d\u6269\u5927\u56fd\u4ea7\u5927\u8c46\u7684\u79cd\u690d\u9762\u79ef\uff1f\u4e2d\u56fd\u662f\u5168\u4e16\u754c\u7b2c\u4e00\u4eba\u53e3\u5927\u56fd\uff0c\u7cae\u98df\u5b89\u5168\u91cd\u4e8e\u6cf0\u5c71\u3002\u4e00\u9762\u662f\u6301\u7eed\u589e\u957f\u7684\u4eba\u53e3\u548c\u8089\u86cb\u5976\u6d88\u8d39\uff0c\u4e00\u9762\u662f\u6349\u895f\u89c1\u8098\u768420\u4ebf\u4ea9\u8015\u5730\u9762\u79ef\u3002\u4e3a\u4e86\u4fdd\u969c\u996d\u7897\u7262\u7262\u7aef\u5728\u81ea\u5df1\u624b\u91cc\uff0c\u4e2d\u56fd\u53ea\u80fd\u9009\u62e9\u201c\u727a\u7272\u201d\u5927\u8c46\uff0c\u4f18\u5148\u4fdd\u969c\u6c34\u7a3b\u548c\u5c0f\u9ea6\u7684\u79cd\u690d\u9762\u79ef\u3002\u7528\u8fdb\u53e3\u5927\u8c46\u8282\u7ea6\u51fa\u6765\u7684\u8015\u5730\u6765\u79cd\u690d\u76f8\u5bf9\u9ad8\u4ea7\u7684\u4e3b\u7cae\u4f5c\u7269\uff0c\u5f53\u7136\u662f\u6700\u5408\u7406\u7684\u9009\u62e9\u3002\u5176\u5b9e\u519c\u6c11\u4e5f\u7f3a\u5c11\u79cd\u690d\u5927\u8c46\u7684\u613f\u610f\u3002\u5927\u8c46\u4ea7\u91cf\u4f4e\uff0c\u79cd\u690d\u6536\u76ca\u53ea\u6709\u7389\u7c73\u548c\u5c0f\u9ea6\u76841/2\u3002\u4e1c\u5317\u662f\u5927\u8c46\u4e3b\u4ea7\u533a\uff0c\u5728\u6b4c\u66f2\u300a\u5bb6\u5728\u4e1c\u5317\u300b\u4e2d\u6709\u4e00\u6bb5\u6b4c\u8bcd\uff1a\u201c\u6211\u7684\u5bb6\u5728\u4e1c\u5317\u677e\u82b1\u6c5f\u4e0a\uff0c\u90a3\u91cc\u6709\u6f2b\u5c71\u904d\u91ce\u7684\u5927\u8c46\u9ad8\u7cb1\u3002\u201d\u4eca\u5929\uff0c\u4f60\u82e5\u771f\u7684\u53bb\u4e1c\u5317\u770b\u770b\uff0c\u5927\u9762\u79ef\u7684\u5927\u8c46\u3001\u9ad8\u7cb1\u65e9\u5df2\u88ab\u7389\u7c73\u53d6\u800c\u4ee3\u4e4b\u3002\u4e5f\u6709\u516c\u4f17\u63d0\u51fa\uff1a\u4e3a\u4ec0\u4e48\u8981\u8fdb\u53e3\u7f8e\u56fd\u5927\u8c46\uff1f\u5168\u7403\u5927\u8c46\u5e74\u4ea7\u91cf\u7ea6\u4e3a3.5\u4ebf\u5428\uff0c\u51e0\u4e2a\u4e3b\u4ea7\u56fd\u6263\u9664\u672c\u571f\u6d88\u8d39\uff0c\u80fd\u591f\u62ff\u51fa\u6765\u8fdb\u884c\u56fd\u9645\u8d38\u6613\u7684\u53ea\u67091.5\u4ebf\u5428\uff0c\u5176\u4e2d\u7f8e\u56fd\u4e3a6000\u4e07\u5428\u3002\u4e2d\u56fd\u8fdb\u53e39000\u4e07\u5428\u5927\u8c46\uff0c\u5982\u679c\u5168\u9762\u5c01\u6740\u7f8e\u56fd\u5927\u8c46\uff0c\u5219\u610f\u5473\u7740\u6211\u4eec\u8981\u4e70\u5149\u7f8e\u56fd\u4ee5\u5916\u7684\u5168\u7403\u7684\u8d38\u6613\u5927\u8c46\uff0c\u8fd9\u662f\u4e0d\u73b0\u5b9e\u7684\u30022018\u5e74\u4e2d\u7f8e\u8d38\u6613\u6218\u785d\u70df\u5f25\u6f2b\uff1a\u7f8e\u56fd\u4ee5\u82af\u7247\u4e3a\u77db\uff0c\u4e2d\u56fd\u7528\u5927\u8c46\u505a\u76fe\u3002\u695a\u6cb3\u6c49\u754c\uff0c\u4e92\u4e0d\u76f8\u8ba9\uff0c\u6700\u7ec8\u5750\u6536\u6e14\u5229\u7684\u662f\u5df4\u897f\u3002\u6b64\u8bdd\u600e\u8bb2\uff1f\u5df4\u897f\u548c\u7f8e\u56fd\u662f\u5168\u7403\u5927\u8c46\u7684\u4e24\u5927\u51fa\u53e3\u56fd\u30022017\u5e74\uff0c\u4e2d\u56fd\u7684\u8fdb\u53e3\u5927\u8c46\u4e2d\uff0c\u5df4\u897f\u548c\u7f8e\u56fd\u7684\u4efd\u989d\u5206\u522b\u662f53%\u548c34%\u3002\u800c\u5230\u4e862018\u5e74\uff0c\u8fd9\u4e00\u6bd4\u4f8b\u53d8\u4e3a75%\u548c19%\u3002\u5728\u6fc0\u589e\u7684\u5df4\u897f\u5927\u8c46\u51fa\u53e3\u91cf\u4e2d\uff0c\u6709\u591a\u5c11\u662f\u5c06\u7f8e\u56fd\u5927\u8c46\u901a\u8fc7\u8f6c\u53e3\u8d38\u6613\u7a7f\u4e0a\u5df4\u897f\u7684\u9a6c\u7532\u518d\u5356\u7ed9\u4e2d\u56fd\uff1f\u4e0d\u5f97\u800c\u77e5\u3002\u4e9a\u9a6c\u900a\u96e8\u6797\u88ab\u79f0\u4e3a\u5730\u7403\u4e4b\u80ba\uff0c\u800c60%\u7684\u4e9a\u9a6c\u900a\u5728\u5df4\u897f\u5883\u5185\u3002\u4fdd\u62a4\u96e8\u6797\u662f\u8d1f\u62c5\uff0c\u53d1\u5c55\u519c\u4e1a\u5219\u662f\u6548\u76ca\u3002\u9762\u5bf9\u7ecf\u6d4e\u840e\u9761\u548c\u8d2b\u5bcc\u5dee\u8ddd\uff0c\u5df4\u897f\u8fd1\u5e74\u6765\u5927\u9762\u79ef\u780d\u4f10\u70ed\u5e26\u96e8\u6797\uff0c\u7528\u6765\u79cd\u690d\u5927\u8c46\u548c\u653e\u7267\u8089\u725b\u3002\u6536\u83b7\u7684\u5927\u8c46\u690d\u682a | \u6444\u4e8e\u5409\u6797\u519c\u4e1a\u5927\u5b66\u5b9e\u9a8c\u519c\u573a\u8fd8\u6709\u516c\u4f17\u8d28\u7591\uff1a\u4e3a\u4ec0\u4e48\u8981\u8fdb\u53e3\u8f6c\u57fa\u56e0\u5927\u8c46\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u4ece\u4fc4\u7f57\u65af\u7b49\u56fd\u5bb6\u5bfb\u6c42\u66ff\u4ee3\u8fdb\u53e3\uff1f\u8fd9\u91cc\u6211\u8981\u544a\u8bc9\u5927\u5bb6\uff0c\u7f8e\u56fd\u3001\u5df4\u897f\u548c\u963f\u6839\u5ef7\u4e09\u4e2a\u56fd\u5bb6\u7684\u5927\u8c46\u8d38\u6613\u91cf\u5c31\u5360\u5230\u5168\u740390%\u7684\u6bd4\u91cd\uff0c\u5176\u4ed6\u56fd\u5bb6\uff08\u4e4c\u62c9\u572d\u3001\u4fc4\u7f57\u65af\u3001\u5370\u5ea6\u7b49\uff09\u51fa\u53e3\u7684\u57fa\u6570\u53ea\u6709100\u4e07\u5428\u7684\u6570\u91cf\u7ea7\uff0c\u76f8\u5bf9\u4e8e\u4e2d\u56fd9000\u4e07\u5428\u7684\u5e9e\u5927\u8fdb\u53e3\u9700\u6c42\uff0c\u53ea\u662f\u676f\u6c34\u8f66\u85aa\u3002\u7136\u800c\u7f8e\u56fd\u3001\u5df4\u897f\u548c\u963f\u6839\u5ef7\u7684\u8f6c\u57fa\u56e0\u79cd\u690d\u6bd4\u4f8b\u90fd\u8fbe\u523095%\u3002\u5982\u679c\u4e2d\u56fd\u7981\u6b62\u8fdb\u53e3\u8f6c\u57fa\u56e0\u5927\u8c46\uff0c\u52bf\u5fc5\u9762\u4e34\u9972\u6599\u77ed\u7f3a\uff0c\u9020\u6210\u8089\u86cb\u5976\u6570\u91cf\u51cf\u5c11\u3001\u4ef7\u683c\u98d9\u5347\uff0c\u8001\u767e\u59d3\u53ea\u80fd\u6e05\u6c64\u5be1\u6c34\u7684\u5929\u5929\u5403\u7d20\u2014\u2014\u8fd9\u76f8\u5f53\u4e8e\u8981\u8f70\u8f70\u70c8\u70c8\u7684\u641e\u4e00\u573a\u5168\u6c11\u51cf\u80a5\u8fd0\u52a8\uff0c\u516c\u4f17\u80fd\u5426\u5403\u5f97\u6d88\uff1f\u7f8e\u56fd\u662f\u8f6c\u57fa\u56e0\u4f5c\u7269\u5168\u7403\u7b2c\u4e00\u79cd\u690d\u548c\u6d88\u8d39\u8005\u5927\u56fd\u3002\u57282\u4ebf\u516c\u9877\u7684\u8015\u5730\u603b\u9762\u79ef\u4e2d\uff0c40%\u79cd\u690d\u7684\u8f6c\u57fa\u56e0\u4f5c\u7269\uff0c\u5176\u4e2d\u7389\u7c73\u3001\u5927\u8c46\u3001\u6cb9\u83dc\u7684\u8f6c\u57fa\u56e0\u79cd\u690d\u6bd4\u4f8b\u5747\u8d85\u8fc790%\u3002\u4ee5\u5927\u8c46\u4e3a\u4f8b\uff1a\u7f8e\u56fd\u603b\u4ea7\u91cf1.2\u4ebf\u5428\uff0c\u51fa\u53e3\u91cf\u5219\u662f0.6\u4ebf\u5428\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u7f8e\u56fd\u4e00\u534a\u7684\u8f6c\u57fa\u56e0\u5927\u8c46\u7528\u4e8e\u672c\u571f\u6d88\u8d39\u3002\u5173\u4e8e\u8f6c\u57fa\u56e0\u7684\u5b89\u5168\u95ee\u9898\uff0c\u8fd9\u91cc\u4e0d\u505a\u63a2\u8ba8\u3002\u4f46\u7f51\u7edc\u4e0a\u6709\u4e00\u79cd\u4f20\u8a00\uff1a\u7f8e\u56fd\u4eba\u81ea\u5df1\u4e0d\u5403\u8f6c\u57fa\u56e0\u98df\u54c1\u2014\u2014\u8fd9\u4e0d\u7b26\u5408\u4e8b\u5b9e\uff01\u56fd\u9645\u8d38\u6613\uff0c\u4ef7\u683c\u662f\u738b\u9053\u3002\u7f8e\u6d32\uff08\u5df4\u897f\u3001\u7f8e\u56fd\u548c\u963f\u6839\u5ef7\uff09\u7684\u5927\u8c46\u51fa\u53e3\u5730\u4ef7\u683c\u662f2600\u5143/\u5428\uff0c\u4ece\u5730\u7403\u53e6\u4e00\u7aef\u6f02\u6d0b\u8fc7\u6d77\u8fd0\u5230\u4e2d\u56fd\uff0c\u52a0\u4e0a25%\u7684\u8fd0\u8d39\u548c\u5173\u7a0e\uff0c\u5230\u5cb8\u4ef73300\u5143/\u5428\uff0c\u6bd4\u56fd\u4ea7\u5927\u8c46\uff083700\u5143/\u5428\uff09\u8fd8\u4fbf\u5b9c15%\u3002\u5f52\u6839\u7ed3\u5e95\uff0c\u662f\u6211\u4eec\u7684\u519c\u4e1a\u201c\u751f\u4ea7\u529b\u201d\u843d\u540e\u4e8e\u522b\u4eba\u3002\u4e2d\u56fd\u5927\u8c46\u6bcf\u516c\u9877\u7684\u5e73\u5747\u4ea7\u91cf\u4e0d\u52302000\u516c\u65a4\uff0c\u53ea\u76f8\u5f53\u4e8e\u7f8e\u56fd\u76843300\u516c\u65a4\u768460%\u3002\u5de8\u5927\u7684\u4ea7\u91cf\u5dee\u8ddd\u4e00\u65b9\u9762\u662f\u571f\u58e4\u548c\u7530\u95f4\u7ba1\u7406\uff0c\u53e6\u4e00\u65b9\u9762\u5219\u662f\u54c1\u79cd\u4f18\u52bf\u3002\u79cd\u5b50\u662f\u519c\u4e1a\u7684\u201c\u82af\u7247\u201d\uff0c\u4e2d\u56fd\u9700\u8981\u53d1\u5c55\u519c\u4e1a\u751f\u7269\u6280\u672f\uff0c\u4ee5\u5e94\u5bf9\u65b0\u4e00\u4ee3\u80b2\u79cd\u56fd\u9645\u7ade\u4e89\u3002\u5ef6\u4f38\u8bdd\u9898\uff1a\u5168\u7403\u89c6\u91ce\u4e0b\u7684\u5927\u8c46\u535a\u5f08\u5c3d\u7ba1\u4e2d\u56fd\u5927\u8c46\u91c7\u8d2d\u91cf\u5360\u5168\u7403\u5927\u8c46\u8d38\u6613\u76842/3\uff0c\u4f46\u5927\u8c46\u5b9a\u4ef7\u6743\u5e76\u6ca1\u6709\u638c\u63e1\u5728\u4e2d\u56fd\u4eba\u624b\u91cc\u3002\u7a76\u5176\u539f\u56e0\uff0c\u8fd9\u7275\u6d89\u5230\u4e8c\u6218\u4ee5\u540e\u7684\u56fd\u9645\u79e9\u5e8f\u3001\u4ee5\u7f8e\u5143\u4e3a\u4e2d\u5fc3\u7684\u56fd\u9645\u8d27\u5e01\u4f53\u7cfb\u3001WTO\u89c4\u5219\u548c\u5168\u7403\u519c\u4ea7\u54c1\u671f\u8d27\u5e02\u573a\u7b49\u590d\u6742\u95ee\u9898\u3002\u800c\u5728\u56fd\u5185\u5e02\u573a\uff0c\u5916\u8d44\u3001\u56fd\u8d44\u548c\u6c11\u8d44\u4e4b\u95f4\u7684\u5229\u76ca\u535a\u5f08\u4e5f\u662f\u76d8\u6839\u9519\u8282\u3002\u4e0d\u8c0b\u5168\u5c40\u8005\u4e0d\u8db3\u8c0b\u4e00\u57df\uff0c\u4eca\u5929\u7684\u8d38\u6613\u6218\u80cc\u540e\u662f\u4e00\u573a\u56fd\u9645\u79e9\u5e8f\u7684\u535a\u5f08\uff0c\u800c\u5927\u8c46\u5219\u662f\u8fd9\u573a\u535a\u5f08\u4e2d\u7684\u4e00\u9897\u5173\u952e\u68cb\u5b50\u3002\u00a0\u6216\u8bb8\u6709\u670b\u53cb\u4f1a\u95ee\uff1a\u5927\u8c46\u56f0\u5c40\u96be\u9053\u5c31\u65e0\u89e3\u4e86\u5417\uff1f\u8fd8\u662f\u7535\u5f71\u300a\u54ea\u5412\u4e4b\u9b54\u7ae5\u964d\u4e16\u300b\u4e2d\u7684\u90a3\u53e5\u53f0\u8bcd\uff1a\u201c\u6211\u547d\u7531\u6211\u4e0d\u7531\u5929\u3002\u201d\u65e2\u7136\u56fd\u5185\u8015\u5730\u8d44\u6e90\u6709\u9650\uff0c\u6211\u4eec\u5c31\u5fc5\u987b\u5b66\u4f1a\u6574\u5408\u5168\u7403\u7684\u519c\u4e1a\u8d44\u6e90\u3002\u7f8e\u56fd\u524d\u56fd\u52a1\u537f\u57fa\u8f9b\u683c\u66fe\u7ecf\u8bf4\u8fc7\uff1a\u201c\u8c01\u63a7\u5236\u4e86\u77f3\u6cb9\uff0c\u5c31\u63a7\u5236\u4e86\u6240\u6709\u56fd\u5bb6\uff1b\u8c01\u63a7\u5236\u4e86\u7cae\u98df\uff0c\u5c31\u63a7\u5236\u4e86\u4eba\u7c7b;\u8c01\u63a7\u5236\u4e86\u8d27\u5e01,\u5c31\u63a7\u5236\u4e86\u5168\u7403\u7ecf\u6d4e\u3002\u8fd9\u53e5\u8bdd\u9053\u51fa\u4e86\u535a\u5f08\u5173\u952e\u6240\u5728\uff0c\u4e5f\u7ed9\u51fa\u4e86\u89e3\u5c40\u7684\u601d\u8def\u3002\u00a0\u4ed6\u5c71\u4e4b\u77f3\uff0c\u53ef\u4ee5\u653b\u7389\u3002\u5927\u8c46\u6709\u4e00\u4e2a\u5f88\u597d\u7684\u524d\u8f66\u4e4b\u9274\u2014\u2014\u4e8b\u5173\u80fd\u6e90\u5b89\u5168\u7684\u77f3\u6cb9\u3002\u548c\u5927\u8c46\u4e00\u6837\uff0c\u77f3\u6cb9\u4e5f\u662f\u8d44\u6e90\u578b\u4ea7\u54c1\u30021993\u5e74\u4ee5\u524d\uff0c\u4e2d\u56fd\u77f3\u6cb9\u4e5f\u662f\u81ea\u7ed9\u81ea\u8db3\uff0c\u6b64\u540e\u4f34\u968f\u7740\u5de5\u4e1a\u5316\u8fdb\u7a0b\uff0c\u8fdb\u53e3\u91cf\u9010\u5e74\u6500\u5347\uff0c\u6f02\u6d0b\u8fc7\u6d77\u7684\u8fdb\u53e3\u539f\u6cb9\u6210\u672c\u4e5f\u4f4e\u4e8e\u56fd\u5185\u751f\u4ea7\u7684\u539f\u6cb9\u3002\u4eca\u5929\uff0c\u4e2d\u56fd\u662f\u4e16\u754c\u7b2c\u4e00\u5927\u77f3\u6cb9\u8fdb\u53e3\u56fd\uff0c\u77f3\u6cb9\u5bf9\u5916\u4f9d\u5b58\u5ea6\u8d85\u8fc770%\u3002\u5728\u5f88\u957f\u7684\u65f6\u95f4\u91cc\uff0c\u4e2d\u56fd\u539f\u6cb9\u8fdb\u53e3\u7684\u4e3b\u8981\u6765\u6e90\u4e8e\u5c40\u52bf\u4e0d\u7a33\u5b9a\u7684\u4e2d\u4e1c\u5730\u533a\uff0c\u6d77\u8def\u8fd0\u8f93\u8fd8\u8981\u901a\u8fc7\u5bb9\u6613\u53d7\u5230\u904f\u5236\u7684\u9a6c\u516d\u7532\u6d77\u5ce1\u3002\u600e\u4e48\u6837\uff0c\u77f3\u6cb9\u548c\u5927\u8c46\u7684\u53d1\u5c55\u8f68\u8ff9\u662f\u4e0d\u662f\u5f88\u50cf\u5b6a\u751f\u5144\u5f1f\uff1f\u00a0\u7136\u800c\u7ecf\u8fc7\u5341\u51e0\u5e74\u7684\u52aa\u529b\uff0c\u4e2d\u56fd\u77f3\u6cb9\u8fdb\u53e3\u5df2\u7ecf\u6e10\u6e10\u201c\u7a81\u56f4\u201d\u3002\u4eca\u5929\uff0c\u4e2d\u56fd\u53ef\u4ee5\u4ece\u7ea650\u4e2a\u56fd\u5bb6\u6216\u5730\u533a\u8fdb\u53e3\u539f\u6cb9\uff0c\u904d\u5e03\u4e2d\u4e1c\u3001\u975e\u6d32\u3001\u4fc4\u7f57\u65af\u548c\u5357\u7f8e\u6d32\uff0c\u6784\u5efa\u4e86\u591a\u5143\u5316\u7684\u77f3\u6cb9\u8fdb\u53e3\u683c\u5c40\u3002\u5176\u4e2d\u4fc4\u7f57\u65af\u5df2\u7ecf\u8fde\u7eed\u56db\u5e74\u6210\u4e3a\u4e2d\u56fd\u6700\u5927\u77f3\u6cb9\u4f9b\u5e94\u56fd\uff0c\u5360\u6bd4\u8fbe\u523015%\uff0c\u800c\u4e2d\u4e1c\u7684\u4f9b\u6cb9\u6bd4\u4f8b\u5219\u63a7\u5236\u572845%\u5de6\u53f3\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u4e2d\u56fd\u6cb9\u4f01\u4e5f\u5728\u4e16\u754c\u5404\u5730\u5165\u80a1\u65b0\u6cb9\u7530\u3001\u5efa\u8bbe\u9646\u8def\u77f3\u6cb9\u7ba1\u9053\u3001\u7b7e\u7f72\u8fd0\u8f93\u534f\u8bae\u3001\u91c7\u7528\u4eba\u6c11\u5e01\u7ed3\u7b97\uff0c\u540c\u65f6\u5728\u56fd\u5185\u5927\u529b\u63d0\u5347\u539f\u6cb9\u50a8\u5907\u548c\u70bc\u6cb9\u80fd\u529b\uff0c\u4e3a\u56fd\u5bb6\u80fd\u6e90\u5b89\u5168\u63d0\u4f9b\u4fdd\u969c\u3002\u00a0\u4ece\u6218\u7565\u4e0a\uff0c\u4e2d\u56fd\u7684\u5927\u8c46\u91c7\u8d2d\u5b8c\u5168\u53ef\u4ee5\u501f\u9274\u77f3\u6cb9\u6218\u7565\uff0c\u8fdb\u884c\u5168\u7403\u5316\u5e03\u5c40\uff0c\u5efa\u7acb\u591a\u5143\u5316\u8fdb\u53e3\u683c\u5c40\uff0c\u4e0d\u80fd\u540a\u5728\u7f8e\u6d32\uff08\u7f8e\u56fd\u3001\u5df4\u897f\u548c\u963f\u6839\u5ef7\uff09\u4e00\u68f5\u6811\u4e0a\u3002\u4fc4\u7f57\u65af\u3001\u4e4c\u514b\u5170\u7b49\u4e2d\u4e9a\u548c\u4e1c\u6b27\u5730\u533a\u6709\u5927\u9762\u79ef\u672a\u5f00\u57a6\u7684\u80a5\u6c83\u571f\u5730\uff0c\u8fd9\u4e5f\u662f\u201c\u4e00\u8def\u4e00\u5e26\u201d\u6218\u7565\u5408\u4f5c\u7684\u9898\u4e2d\u4e4b\u4e49\u3002\u5982\u679c\u8fd9\u4e00\u5730\u533a\u6bcf\u5e74\u80fd\u591f\u4e3a\u63d0\u4f9b\u4e2d\u56fd2000\u4e07\u5428\u7684\u5927\u8c46\uff0c\u5176\u201c\u6760\u6746\u6548\u5e94\u201d\u5c06\u5bf9\u5168\u7403\u5927\u8c46\u8d38\u6613\u683c\u5c40\u4ea7\u751f\u6df1\u8fdc\u5f71\u54cd\u3002\u00a0\u540c\u6837\u501f\u9274\u80fd\u6e90\u5b89\u5168\u7684\u7ecf\u9a8c\uff0c\u4e2d\u56fd\u7684\u7cae\u98df\u5b89\u5168\u4e0d\u80fd\u4ec5\u4ec5\u505c\u7559\u5728\u4ea7\u91cf\u4e0a\uff0c\u63d0\u5347\u7cae\u98df\u5e93\u5b58\u3001\u7269\u6d41\u548c\u5168\u7403\u8d38\u6613\u80fd\u529b\u4e5f\u662f\u6211\u4eec\u7684\u5fc5\u4fee\u8bfe\u3002\u503c\u5f97\u6ce8\u610f\u7684\u662f\uff0c\u6d77\u5916\u6536\u8d2d\u519c\u4e1a\u7528\u5730\u975e\u5e38\u201c\u654f\u611f\u201d\uff0c\u9700\u8981\u501f\u52a9\u751f\u4ea7\u673a\u68b0\u3001\u4ed3\u50a8\u3001\u8fd0\u8f93\u7b49\u67d4\u6027\u65b9\u5f0f\u63a7\u5236\u7cae\u98df\u8d44\u6e90\u3002\u6700\u7ec8\u7684\u751f\u4ea7\u6210\u672c\u53ef\u80fd\u4f1a\u9ad8\u4e8e\u7f8e\u6d32\u5927\u8c46\uff0c\u7136\u800c\u653e\u773c\u4eca\u5929\u7684\u4e16\u754c\u683c\u5c40\uff0c\u6211\u4eec\u8981\u7b97\u7ecf\u6d4e\u8d26\uff0c\u66f4\u8981\u8003\u8651\u6218\u7565\u7a7a\u95f4\u3002\u8ffd\u6839\u6eaf\u6e90\uff0c\u8fd9\u7c92\u5706\u5706\u7684\u5927\u8c46\u6240\u8574\u542b\u7684\u662f\u5168\u7403\u519c\u4e1a\u8d44\u6e90\u7684\u4e00\u573a\u518d\u914d\u5236\u548c\u518d\u5e73\u8861\u3002\u4e16\u754c\u672b\u65e5\u548c\u79cd\u5b50\u5e93\u7535\u5f71\u300a\u661f\u9645\u7a7f\u8d8a\u300b\u5267\u7167\u5927\u5bb6\u60f3\u60f3\u79d1\u5e7b\u7535\u5f71\u300a\u661f\u9645\u7a7f\u8d8a\u300b\u4e2d\u7684\u5267\u60c5\uff1a\u5e72\u65f1\u548c\u67af\u840e\u75c5\u5148\u540e\u706d\u7edd\u4e86\u5c0f\u9ea6\u3001\u79cb\u8475\u7b49\u4f5c\u7269\uff0c\u4f5c\u4e3a\u201c\u672b\u65e5\u4f5c\u7269\u201d\u7684\u7389\u7c73\u4e5f\u5944\u5944\u4e00\u606f\uff0c\u4eba\u7c7b\u8d70\u6295\u65e0\u8def\u3002\u5176\u5b9e\uff0c\u8fd9\u79cd\u5267\u60c5\u5e76\u975e\u5f02\u60f3\u5929\u5f00\u30021954\u5e74\uff0c\u5b62\u56ca\u7ebf\u866b\u75c5\u4f7f\u7f8e\u56fd\u5927\u8c46\u79cd\u690d\u906d\u9047\u6bc1\u706d\u6027\u6253\u51fb\uff0c\u80b2\u79cd\u5b66\u5bb6\u4ece3000\u591a\u4efd\u79cd\u8d28\u8d44\u6e90\u4e2d\u201c\u7ffb\u7bb1\u5012\u67dc\u201d\uff0c\u6700\u7ec8\u627e\u5230\u4e8620\u4e16\u7eaa\u521d\u7f8e\u56fd\u4f20\u6559\u58eb\u4ece\u4e2d\u56fd\u6536\u96c6\u7684\u72ec\u7279\u5730\u65b9\u54c1\u79cd\u2014\u2014\u5317\u4eac\u5c0f\u9ed1\u8c46\u3002\u5229\u7528\u8fd9\u4e00\u54c1\u79cd\u4e2d\u53d1\u73b0\u4e86\u7279\u6709\u7684\u6297\u75c5\u57fa\u56e0\uff0c\u6700\u7ec8\u57f9\u80b2\u51fa\u7684\u65b0\u7684\u5927\u8c46\u54c1\u79cd\uff0c\u4f7f\u5f97\u7f8e\u56fd\u5927\u8c46\u4ea7\u4e1a\u5f97\u4ee5\u590d\u82cf\u3002\u5f53\u65f6\uff0c\u8fd9\u4efd\u6765\u81ea\u4e2d\u56fd\u7684\u79cd\u8d28\u8d44\u6e90\uff0c\u5df2\u5728\u7f8e\u56fd\u4fdd\u5b58\u4e8647\u5e74\u3002\u4ee5\u524d\u7684\u7c73\u996d\u716e\u719f\u540e\uff0c\u4f1a\u6709\u4e00\u5c42\u6cb9\u5149\uff1b\u4e00\u6ef4\u829d\u9ebb\u9999\u6cb9\u6ef4\u51fa\uff0c\u80fd\u8ba9\u6574\u4e2a\u5c4b\u5b50\u91cc\u98d8\u9999\u3002\u4eca\u5929\u7684\u8c37\u7269\u54c1\u79cd\u4ea7\u91cf\u8d8a\u6765\u8d8a\u9ad8\uff0c\u53e3\u611f\u5374\u5c11\u4e86\u66fe\u7ecf\u7684\u9187\u539a\u548c\u9999\u6c14\u3002\u5176\u5b9e\u635f\u5931\u7684\u4e0d\u53ea\u662f\u53e3\u611f\uff0c\u8fd8\u6709\u5927\u81ea\u7136\u4e2d\u7684\u751f\u7269\u591a\u6837\u6027\uff0c\u5c24\u5176\u662f\u79cd\u8d28\u8d44\u6e90\u3002\u5bf9\u4e8e\u4f5c\u7269\u80b2\u79cd\u800c\u8a00\uff0c\u79cd\u8d28\u8d44\u6e90\u5e93\u76f8\u5f53\u4e8e\u5e9e\u5927\u7684\u201c\u57fa\u56e0\u94f6\u884c\u201d\uff0c\u4e30\u5bcc\u7684\u751f\u7269\u591a\u6837\u6027\u662f\u5927\u81ea\u7136\u7ed9\u4eba\u7c7b\u7684\u9988\u8d60\u3002\u7136\u800c\uff0c\u4eca\u5929\u7684\u9ad8\u4ea7\u4f5c\u7269\u4e00\u65e6\u63a8\u800c\u5e7f\u4e4b\uff0c\u5c31\u5f00\u59cb\u201c\u8d62\u8005\u901a\u5403\u201d\uff0c\u4fb5\u5360\u5c0f\u4f17\u8c37\u7269\u54c1\u7cfb\u7684\u79cd\u690d\u7a7a\u95f4\u3002\u7edf\u8ba1\u6570\u636e\u663e\u793a\uff0c\u6211\u56fd\u5730\u65b9\u8c37\u7269\u54c1\u79cd\u7684\u6570\u76ee\uff0c\u5df2\u7ecf\u4ece1956\u5e74\u768411590\u4e2a\u4e0b\u964d\u52302014\u5e74\u76843271\u4e2a\uff0c\u4e27\u5931\u6bd4\u4f8b\u9ad8\u8fbe71.8%\u3002\u6839\u636e\u4e16\u754c\u7cae\u519c\u7ec4\u7ec7\u516c\u5e03\u7684\u8d44\u6599\uff0c\u5728\u8fc7\u53bb\u7684100\u5e74\u91cc\uff0c90%\u4ee5\u4e0a\u7684\u519c\u4f5c\u7269\u54c1\u79cd\u5df2\u7ecf\u4ece\u519c\u7530\u4e2d\u6d88\u5931\u3002\u4eca\u5929\uff0c\u5e72\u65f1\u3001\u9ad8\u6e29\u3001\u75c5\u866b\u5bb3\u5bf9\u5168\u7403\u8c37\u7269\u751f\u4ea7\u6b63\u5e26\u6765\u8d8a\u6765\u8d8a\u5927\u7684\u538b\u529b\uff0c\u672a\u6765\u5730\u7403\u73af\u5883\u4f1a\u8d70\u5411\u4f55\u65b9\uff1f\u5c06\u6765\u4f1a\u4e0d\u4f1a\u6709\u4e00\u5929\uff0c\u5f53\u4eba\u7c7b\u5f00\u59cb\u53bb\u5bfb\u627e\u80fd\u6297\u65f1\u3001\u6297\u75c5\u7684\u6027\u72b6\u65f6\uff0c\u5374\u5ffd\u7136\u53d1\u73b0\u5177\u5907\u8fd9\u4e9b\u57fa\u56e0\u7684\u690d\u7269\u5df2\u7ecf\u5728\u5730\u7403\u4e0a\u6d88\u5931\u4e86\uff1f\u4eba\u7c7b\u4e0d\u80fd\u53ea\u6709\u5bf9\u81ea\u5df1\u7684\u201c\u79c1\u5fc3\u201d\uff0c\u4e5f\u9700\u8981\u6709\u5bf9\u5927\u81ea\u7136\u7684\u201c\u516c\u5fc3\u201d\u3002\u65f6\u81f3\u4eca\u65e5\uff0c\u5f88\u5c11\u4eba\u8ba4\u8bc6\u5230\u751f\u7269\u591a\u6837\u6027\u5bf9\u4eba\u7c7b\u548c\u81ea\u7136\u7684\u91cd\u8981\u6027\u30022000\u5e74\uff0c\u8054\u5408\u56fd\u5927\u4f1a\u5ba3\u5e03\u5c06\u6bcf\u5e74\u76845\u670822\u65e5\u4e3a\u56fd\u9645\u751f\u7269\u591a\u6837\u6027\u65e5\u3002\u73b0\u5b9e\u793e\u4f1a\u4e2d\uff0c\u7684\u786e\u6709\u4eba\u5728\u4e3a\u201c\u4e16\u754c\u672b\u65e5\u201d\u672a\u96e8\u7ef8\u7f2a\u30022008\u5e74\uff0c\u632a\u5a01\u5728\u8ddd\u79bb\u5317\u6781\u5708\u6781\u70b91000\u591a\u516c\u91cc\u7684\u5c71\u4f53\u4e2d\u5efa\u6210\u4e86\u4e00\u5ea7\u603b\u957f120\u7c73\u7684\u201c\u5168\u7403\u79cd\u5b50\u5e93\u201d\u3002\u632a\u5a01\u4eba\u7ed9\u5b83\u53d6\u4e86\u4e2a\u54cd\u4eae\u7684\u540d\u5b57\u2014\u2014\u201c\u672b\u65e5\u7a79\u9876\u201d\u3002\u6d1e\u5185\u9762\u79ef\u7ea61\u5343\u5e73\u7c73\uff0c\u6bcf\u4e2a\u6837\u672c\u4fdd\u5b58\u7ea65\u767e\u7c92\u79cd\u5b50\u30022018\u5e74\uff0c\u201c\u5168\u7403\u79cd\u5b50\u5e93\u201d\u50a8\u5b58\u7684\u79cd\u5b50\u6837\u672c\u5df2\u8d85\u8fc7100\u4e07\u4efd\u3002\u5168\u7403\u79cd\u5b50\u5e93 | Wikimedia Commons\u79cd\u5b50\u5e93\u4f4d\u4e8e\u6781\u5bd2\u4e4b\u5730\uff0c\u53ef\u4ee5\u62b5\u5fa1\u70ed\u6d6a\u51b2\u51fb\u3002\u6d1e\u7a74\u9ad8\u4e8e\u6d77\u5e73\u9762130\u7c73\uff0c\u53ef\u4ee5\u62b5\u5fa1\u51b0\u5ddd\u878d\u5316\u5bfc\u81f4\u7684\u6d77\u9762\u5347\u9ad8\u3002\u5982\u679c\u4eba\u7c7b\u5728\u9047\u5230\u6838\u6218\u4e89\u3001\u5c0f\u884c\u661f\u649e\u51fb\u3001\u6c14\u5019\u5267\u53d8\u3001\u6d77\u5e73\u9762\u4e0a\u5347\u7b49\u672b\u65e5\u5371\u673a\uff0c\u5728\u6ca1\u6709\u5176\u4ed6\u79cd\u5b50\u53ef\u7528\u7684\u60c5\u51b5\u4e0b\uff0c\u53ef\u4ee5\u6253\u5f00\u201c\u672b\u65e5\u7a79\u9876\u201d\uff0c\u5728\u5730\u7403\u4e0a\u91cd\u65b0\u5efa\u7acb\u8c37\u7269\u751f\u4ea7\u7cfb\u7edf\u3002\u4eca\u5929\uff0c\u5f88\u591a\u670b\u53cb\u611f\u53d7\u7684\u662f\u5c81\u6708\u9759\u597d\u3001\u8863\u98df\u65e0\u5fe7\uff0c\u4f24\u8111\u7b4b\u7684\u662f\u8425\u517b\u8fc7\u5269\u548c\u80a5\u80d6\u75c7\u3002\u5176\u5b9e\u4e2d\u56fd\u80fd\u5403\u9971\u996d\uff0c\u4e0d\u8fc7\u662f\u6700\u8fd130\u5e74\u7684\u4e8b\u60c5\u3002\u4eca\u5929\uff0c\u6211\u4eec\u9762\u4e34\u7740\u4eba\u591a\u5730\u5c11\u3001\u5316\u5b66\u519c\u4e1a\u6c61\u67d3\u3001\u8015\u5730\u8d28\u91cf\u9000\u5316\u3001\u5730\u4e0b\u6c34\u4f4d\u4e0b\u964d\u3001\u8c37\u7269\u54c1\u79cd\u591a\u6837\u6027\u4e27\u5931\u7b49\u590d\u6742\u95ee\u9898\uff0c\u5982\u4f55\u5b9e\u73b0\u7cae\u98df\u5b89\u5168\u548c\u8d44\u6e90\u3001\u73af\u5883\u548c\u53ef\u6301\u7eed\u519c\u4e1a\u4e4b\u95f4\u7684\u5e73\u8861\uff0c\u662f\u6446\u5728\u4e2d\u56fd\u9762\u524d\u7684\u4e00\u9879\u523b\u4e0d\u5bb9\u7f13\u7684\u6218\u7565\u8bae\u9898\u3002\u5e0c\u671b\u5927\u5bb6\u5bf9\u7cae\u98df\u5b89\u5168\u80fd\u6709\u66f4\u4e3a\u5168\u9762\u7684\u8ba4\u8bc6\uff0c\u7231\u62a4\u73af\u5883\uff0c\u73cd\u60dc\u7cae\u98df\uff01\uff08\u7f16\u8f91\uff1aYuki\uff09\u5d14\u51ef\u300a\u8c37\u7269\u7684\u6545\u4e8b\u300b\u7cfb\u5217:\u8c37\u7269\u7684\u8d77\u6e90\uff1a\u6211\u4eec\u6bcf\u5929\u5403\u7684\u7c73\u996d\uff0c\u6700\u65e9\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u8c37\u7269\u7684\u4e00\u751f\uff1a\u6ca1\u7a7a\u604b\u7231\uff0c\u53ea\u80fd\u548c\u65b9\u5706\u767e\u7c73\u7684\u5f02\u6027\u95ea\u5a5a\uff0c\u5b83\u4eec\u5b9e\u5728\u82e6\u547d\uff01\u53c2\u8003\u6587\u732e\uff1a[1].Kai Cui etc\uff0cA look at food security in China\uff0cnpj Science of Food (2018) 2:4-5\u9875;[2].\u4e54\u91d1\u4eae\uff0c\u9ed1\u571f\u5730\u53d8\u8584\u4ee4\u4eba\u62c5\u5fe7 \u300a\u7ecf\u6d4e\u65e5\u62a5\u300b2015\u5e747\u670828\u65e5[3].\u5218\u6d77\u82e5\u7b49\uff0c\u534e\u5317\u4e95\u704c\u533a\u5730\u4e0b\u6c34\u6c34\u4f4d\u53d8\u5316\u73b0\u72b6\u53ca\u5e94\u5bf9\u63aa\u65bd\u5efa\u8bae\uff0c\u6c34\u8d44\u6e90\u7ba1\u7406\uff0c2016\u5e74\u7b2c9\u671f\uff1a25-28[4].\u5d14\u51ef\uff0c\u7528\u5927\u8c46\u53cd\u5236\u7f8e\u56fd\u8d38\u6613\u6218\uff1f\u6ca1\u90a3\u4e48\u7b80\u5355\uff01\u77e5\u8bc6\u5206\u5b50\u201d\u516c\u4f17\u53f7\uff0c2018\u5e743\u670827\u65e5[5].\u5d14\u51ef\uff0c\u300a\u5168\u56fd\u8f6c\u57fa\u56e0\u79d1\u666e\u5de1\u8bb2\u300b\u8bb2\u4e49\uff0c\u4e2d\u56fd\u519c\u5b66\u4f1a\uff0c2019\u5e749\u670826\u65e5[6].\u4faf\u840c\u7476\u7b49\uff0c\u4e2d\u56fd\u4e3b\u8981\u519c\u4f5c\u7269\u5316\u80a5\u7528\u91cf\u4f30\u7b97\uff0c\u519c\u4e1a\u8d44\u6e90\u4e0e\u73af\u5883\u5b66\u62a5\uff0c2017 \u5e74\u7b2c34 \u5377\uff1a360-367[7].\u738b\u7433\u7433\uff0c\u751f\u7269\u591a\u6837\u6027\u662f\u7cae\u98df\u5b89\u5168\u548c\u4eba\u7c7b\u5065\u5eb7\u7684\u57fa\u7840\uff0c\u4e2d\u56fd\u73af\u5883\u62a5\uff0c2019\u5e745\u670822\u65e5[8].\u6768\u8212\uff0c\u5b88\u62a4\u597d\u6211\u4eec\u7684\u79cd\u8d28\u8d44\u6e90\uff0c\u5149\u660e\u65e5\u62a5 2019\u5e745\u670822\u65e5", "title": "2019\u5e74\u4e86\uff0c\u4e2d\u56fd\u771f\u7684\u6446\u8131\u7cae\u98df\u5371\u673a\u4e86\u5417\uff1f", "ukey_author": "bl1jig", "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "\u8d3e\u9e64\u9e4f", "title": "\u79d1\u5b66\u8bc4\u8bba\u5b66\u8005", "ukey": "nq0zan"}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "\u8d3e\u9e64\u9e4f", "title": "\u79d1\u5b66\u8bc4\u8bba\u5b66\u8005", "ukey": "nq0zan"}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<p>\u5728\u4e4b\u524d\u7684\u4e13\u680f\u6587\u7ae0&nbsp;<a href=\"https://www.guokr.com/article/454804/\" target=\"_blank\">\u300a\u7535\u5f71\u4e2d\u79d1\u5b66\u5bb6\u662f\u82f1\u96c4\u8fd8\u662f\u6076\u68cd\uff1f\u90a3\u8981\u770b\u662f\u5565\u65f6\u5019\u7684\u7535\u5f71\u4e86</a><a href=\"https://mp.weixin.qq.com/s?__biz=MzI0MzA3OTI1MA==&amp;mid=2651611835&amp;idx=1&amp;sn=0c331aea5495902620508b893ebb551f&amp;chksm=f28a4386c5fdca90eaed580a32528913f398a8ab9c0e1da8b0c1af434379cc08861b3555ae80&amp;token=1960790046&amp;lang=zh_CN&amp;scene=21#wechat_redirect\" target=\"_blank\"><strong>\u300b</strong></a>\u4e2d\uff0c\u6211\u501f\u7528\u7f51\u53cb\u7684\u8bc4\u8bba&ldquo;\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5f00\u521b\u4e86\u4e2d\u56fd\u79d1\u5e7b\u7535\u5f71\u7684\u5143\u5e74\u3001\u300a\u4e0a\u6d77\u5821\u5792\u300b\u53c8\u7ec8\u7ed3\u4e86\u8fd9\u4e2a\u5143\u5e74&rdquo;\u4f5c\u4e3a\u6587\u7ae0\u7684\u5f15\u5b50\u3002\u9650\u4e8e\u7bc7\u5e45\uff0c\u90a3\u7bc7\u6587\u7ae0\u6ca1\u6709\u4e13\u95e8\u63a2\u8ba8\u6240\u8c13&ldquo;\u4e2d\u56fd\u79d1\u5e7b\u7535\u5f71\u5143\u5e74&rdquo;\u7684\u95ee\u9898\uff0c\u672c\u7bc7\u6211\u5c06\u4ece\u4e00\u4f4d\u79d1\u5b66\u4f20\u64ad\u5de5\u4f5c\u8005\u7684\u89d2\u5ea6\uff0c\u53cd\u601d\u4e00\u4e0b\u8fd9\u4e2a\u8bf4\u6cd5\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/Tro-BEBNI7ppybSprQt4La-mDbQhAtPvjUdOuM5B0F44BAAA9wIAAEpQ.jpg\" style=\"height:450px; width:640px\" />\u4e24\u90e8\u7535\u5f71\u4e2d\u90fd\u6709\u4e0a\u6d77\u5143\u7d20</p>\r\n\r\n<p>\u9996\u5148\u8981\u58f0\u660e\uff0c\u6211\u4e0d\u662f\u5f71\u8bc4\u5bb6\uff0c\u6211\u7684\u79d1\u5b66\u4f20\u64ad\u7814\u7a76\u7684\u4e3b\u9898\u4e5f\u8c08\u4e0d\u4e0a\u4e13\u6ce8\u4e8e\u79d1\u5b66\u5f71\u89c6\u751a\u81f3\u79d1\u5e7b\u6587\u5b66\u3002\u56e0\u6b64\u8fd9\u91cc\u4e3b\u8981\u662f\u4ece\u79d1\u5b66\u6587\u5316\u7684\u89d2\u5ea6\u6765\u53cd\u601d\u4e0b\u79d1\u5e7b\u7535\u5f71\u8fd9\u4e00\u4e3b\u9898\u3002</p>\r\n\r\n<h2><strong>\u79d1\u5e7b\u7535\u5f71\u7684\u5386\u7a0b</strong></h2>\r\n\r\n<p><strong>\u7535\u5f71\u4ece\u840c\u82bd\u65f6\u4ee3\u5f00\u59cb\uff0c\u5c31\u4e0e\u79d1\u5e7b\u7ed3\u4e0b\u4e86\u4e0d\u89e3\u4e4b\u7f18</strong>\u3002\u8ffd\u6eaf\u7535\u5f71\u65e9\u671f\u53d1\u5c55\u5386\u7a0b\uff0c\u5f88\u591a\u4eba\u90fd\u4f1a\u8c08\u8d771902\u5e74\u62cd\u6444\u7684\u300a\u6708\u7403\u4e4b\u65c5\u300b\uff08<em>Le Voyage dans la Lune</em>\uff09\uff0c\u90a3\u90e8\u7535\u5f71\u6d77\u62a5\u4e0a\u753b\u7740\u6708\u4eae\u7684\u4e00\u53ea\u773c\u775b\u88ab\u63d2\u4e0a\u4e86\u706b\u7bad\u3002\u7535\u5f71\u7684\u60c5\u8282\u5982\u4eca\u60f3\u6765\u5df2\u7ecf\u5e73\u6de1\u65e0\u5947\uff0c\u4f46\u5267\u4f5c\u8005\u57fa\u4e8e\u5f53\u65f6\u79d1\u5b66\u6c34\u5e73\u7684\u60f3\u8c61\u529b\u8fd8\u662f\u503c\u5f97\u94a6\u4f69\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/vnk-W2R7a6uov32CvAKOe9SwTazvAHlz9UCcJeE1Fug4BAAANQMAAEpQ.jpg\" style=\"height:487px; width:640px\" />\u7535\u5f71\u300a\u6708\u7403\u4e4b\u65c5\u300b</p>\r\n\r\n<p>\u79d1\u5e7b\u7535\u5f71\u53d1\u5c55\u53f2\u968f\u540e\u503c\u5f97\u4e00\u4e66\u7684\u5c31\u662f\u7231\u8fea\u751f\u5de5\u4f5c\u5ba4\u51fa\u54c1\u7684\u300a\u5f17\u5170\u80af\u65af\u5766\u300b\uff08<em>Frankenstein</em>\uff09\u3002\u867d\u7136\u5982\u4eca\u4eba\u4eec\u63d0\u8d77\u5f17\u5170\u80af\u65af\u5766\u5c06\u5176\u5f53\u4f5c\u4e00\u4e2a\u79d1\u5b66\u602a\u4eba\uff0c\u800c\u5f17\u5170\u80af\u65af\u5766\u98df\u54c1\uff08Frenkenfood\uff09\u5219\u6210\u4e3a\u5996\u9b54\u5316\u8f6c\u57fa\u56e0\u7684\u5e38\u89c1\u8bcd\u6c47\uff0c\u4f46\u5728\u7231\u8fea\u751f\u7684\u4f5c\u54c1\u4e2d\uff0c\u5f17\u5170\u80af\u65af\u5766\u53ef\u662f\u4e00\u4f4d\u6b63\u6d3e\u7684\u53d1\u660e\u5bb6\uff0c\u4e0d\u8fc7\u603b\u662f\u6267\u8ff7\u4e8e\u5947\u601d\u602a\u60f3\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/ysoffu6yycgSJY7jrim7zOU9MsYQaHqciP-WWb1BXuKAAgAAbAEAAEpQ.jpg\" style=\"height:364px; width:640px\" />\u7535\u5f71\u300a\u5f17\u5170\u80af\u65af\u5766\u300b\uff0c\u5bfc\u6f14\u662f\u8a79\u59c6\u65af&middot;\u60e0\u5c14</p>\r\n\r\n<p>\u53e6\u5916\u4e00\u90e8\u503c\u5f97\u8bf4\u4e24\u53e5\u7684\u65e9\u671f\u79d1\u5e7b\u7535\u5f71\u5219\u662f\u6839\u636e\u5112\u52d2&middot;\u51e1\u5c14\u7eb3\u540c\u540d\u5c0f\u8bf4\u6539\u7f16\u7684\u300a\u6d77\u5e95\u4e24\u4e07\u91cc\u300b\u3002\u5b83\u57281916\u5e74\u30011954\u5e74\u548c1997\u5e74\u88ab\u4e09\u6b21\u642c\u4e0a\u94f6\u5e55\uff0c\u8db3\u4ee5\u8bf4\u660e\u4eba\u4eec\u5bf9\u8fd9\u7c7b\u63a2\u9669\u79d1\u5e7b\u9898\u6750\u7684\u6301\u4e45\u5174\u8da3\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/PeO7TwNyvToHO1cqG7BcF5OR4Vg4p6xPS_FjquUfJ-M4BAAA3QEAAEpQ.jpg\" style=\"height:283px; width:640px\" />1916\u5e74\u30011954\u5e74\u548c1997\u5e74\u7684\u7535\u5f71\u300a\u6d77\u5e95\u4e24\u4e07\u91cc\u300b</p>\r\n\r\n<p>\u8fd9\u4e9b\u65e9\u671f\u79d1\u5e7b\u7535\u5f71\u7684\u5171\u6027\u662f\u4ec0\u4e48\u5462\uff1f\u90a3\u5c31\u662f<strong>\u901a\u8fc7\u5c55\u793a\u79d1\u5b66\u5185\u5bb9\uff0c\u8ba9\u6211\u4eec\u770b\u5230\u4e86\u7535\u5f71\u53ef\u4ee5\u5e26\u7ed9\u4eba\u4eec\u7684\u7f8e\u4e3d\u65b0\u4e16\u754c\u3002</strong>\u800c\u53cd\u8fc7\u6765\uff0c\u5305\u62ec\u7231\u8fea\u751f\u5728\u5185\u7684\u65e9\u671f\u79d1\u666e\u4eba\u58eb\uff08\u300a\u79d1\u5b66\u300b\u6742\u5fd7\u6b63\u662f\u5728\u7231\u8fea\u751f\u8d44\u52a9\u4e0b\u5f97\u4ee5\u51fa\u7248\uff09\u4e5f\u8ba4\u8bc6\u5230\u901a\u8fc7\u79d1\u5e7b\u7535\u5f71\u8fd9\u79cd\u5f62\u5f0f\u53ef\u4ee5\u6781\u5927\u5730\u63d0\u5347\u4eba\u4eec\u5bf9\u79d1\u5b66\u7684\u5174\u8da3\u4e0e\u5411\u5f80\u3002\u6b63\u56e0\u4e3a\u8fd9\u4e2a\u539f\u56e0\uff0c\u5728\u597d\u83b1\u575e\u7684\u7535\u5f71\u5de5\u4e1a\u53d1\u5c55\u8d77\u6765\u540e\uff0c\u79d1\u5e7b\u7535\u5f71\u4e00\u76f4\u53d7\u5230\u5236\u7247\u4eba\u4eec\u7684\u5782\u9752\uff0c\u800c\u90e8\u5206\u79d1\u5b66\u5bb6\u4eec\u5219\u5728\u5f88\u65e9\u5c31\u613f\u610f\u901a\u8fc7\u7535\u5f71\u5c55\u793a\u81ea\u5df1\u7684\u79d1\u7814\u6210\u679c\uff0c\u540e\u6765\u5219\u53c8\u901a\u8fc7\u597d\u83b1\u575e\u79d1\u5e7b\u7535\u5f71\u7684\u79d1\u5b66\u987e\u95ee\u5236\u5ea6\u4ecb\u5165\u5230\u7535\u5f71\u7684\u79d1\u5b66\u53d9\u4e8b\u4e2d\u3002</p>\r\n\r\n<h2><strong>\u79d1\u5e7b\u7535\u5f71\u4e0e\u79d1\u5b66\u7684\u7ea0\u7ed3</strong></h2>\r\n\r\n<p>\u4e0d\u4ec5\u79d1\u5b66\u754c\u91cd\u89c6\u79d1\u5e7b\u7535\u5f71\u7684\u79d1\u666e\u4f5c\u7528\uff0c\u751a\u81f3\u6709\u7814\u7a76\u8868\u660e\uff0c<strong>\u4e00\u4e9b\u8457\u540d\u7684\u79d1\u5e7b\u7535\u5f71\u8fd8\u5bf9\u79d1\u7814\u8fdb\u7a0b\u672c\u8eab\u6709\u5f71\u54cd</strong>\uff0c\u6bd4\u5982\u7535\u5f71\u300a\u4f8f\u7f57\u7eaa\u516c\u56ed\u300b\u4e2d\u590d\u6d3b\u6050\u9f99\u7684\u5c1d\u8bd5\uff0c\u6700\u521d\u5e76\u4e0d\u662f\u6765\u81ea\u6210\u4e3a\u53e4\u751f\u7269\u5b66\u5171\u8bc6\u7684\u60f3\u6cd5\u3002\u4f46\u901a\u8fc7\u5927\u83b7\u6210\u529f\u7684\u7535\u5f71\uff0c\u8fd9\u4e2a\u60f3\u6cd5\u5f00\u59cb\u5e7f\u6cdb\u5730\u5f71\u54cd\u4e86\u76f8\u5173\u7684\u79d1\u5b66\u8bae\u7a0b\uff0c\u5e76\u6210\u4e3a\u4e86\u5f88\u591a\u4e25\u8083\u79d1\u5b66\u5bb6\u8fdb\u884c\u5c1d\u8bd5\u7684\u79d1\u7814\u9879\u76ee\uff08\u5e94\u8be5\u4e5f\u5f71\u54cd\u4e86\u7f8e\u56fd\u79d1\u5b66\u57fa\u91d1\u7684\u53d1\u653e\uff0c\u5c3d\u7ba1\u6211\u770b\u5230\u7684\u6587\u732e\u4e2d\u6ca1\u6709\u63d0\u5230\u8fd9\u4e00\u70b9\uff09\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/Lx5k1ow5aW2v8cxv9f4woBW7IM-CwKJWJKnSYro7qe44BAAAxgIAAEpQ.jpg\" style=\"height:421px; width:640px\" />1993\u5e74\u7684\u7535\u5f71\u300a\u4f8f\u7f57\u7eaa\u516c\u56ed\u300b\uff0c\u5bfc\u6f14\u662f\u53f2\u8482\u6587&middot;\u65af\u76ae\u5c14\u4f2f\u683c</p>\r\n\r\n<p>\u5176\u4ed6\u901a\u8fc7\u79d1\u5e7b\u7535\u5f71\u8ba9\u79d1\u6280\u754c\u5185\u90e8\u4e89\u8bba\u4e2d\u7684\u4e00\u65b9\u83b7\u5f97\u66f4\u5e7f\u6cdb\u5f71\u54cd\u7684\u8fd8\u6709\u4e0d\u5c11\uff0c\u6bd4\u5982\u300a\u661f\u9645\u7a7f\u8d8a\u300b(<em>Interstellar</em>)\u4e2d\u7684&ldquo;\u866b\u6d1e&rdquo;\u3002\u5b9e\u9645\u4e0a\uff0c\u79d1\u5e7b\u5c0f\u8bf4\u8fd9\u65b9\u9762\u7684\u60c5\u8282\u66f4\u591a\u3002\u8bfb\u300a\u4e09\u4f53\u300b\u65f6\uff0c\u8bfb\u8005\u53ef\u80fd\u4f1a\u6ce8\u610f\u5230\u4e09\u4f53\u4eba\u9501\u6b7b\u5730\u7403\u4eba\u7684\u52a0\u901f\u5668\uff0c\u5bfc\u81f4\u9ad8\u80fd\u7269\u7406\u65e0\u6cd5\u53d1\u5c55\u4ece\u800c\u79d1\u5b66\u65e0\u6cd5\u5b9e\u73b0\u672c\u8d28\u4e0a\u7684\u98de\u8dc3\u3002\u8fd9\u63cf\u8ff0\u542c\u7740\u50cf\u4e0d\u50cf\u9ad8\u80fd\u7269\u7406\u5b66\u754c\u5bf9\u81ea\u5df1\u4e0d\u80fd\u5982\u613f\u5f97\u5230\u5927\u79d1\u5b66\u9879\u76ee\u62e8\u6b3e\u7684\u62b1\u6028\u5462\uff1f</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/F6G0gcOjpWbfZPbnyzRvlR7XUnJHLe6PL1yETTrgP6zoAwAAMwIAAEpQ.jpg\" style=\"height:360px; width:640px\" />\u300a\u661f\u9645\u7a7f\u8d8a\u300b\uff0c\u5bfc\u6f14\u662f\u514b\u91cc\u65af\u6258\u5f17&middot;\u8bfa\u5170</p>\r\n\r\n<p>\u76f8\u6bd4\u5177\u4f53\u7684\u79d1\u5b66\u5185\u5bb9\u6e17\u5165\u5230\u79d1\u5e7b\u5f71\u89c6\u4e2d\uff0c\u501f\u52a9\u79d1\u5e7b\u5f71\u89c6\u7684\u5e7f\u6cdb\u5f71\u54cd\u6765\u53cd\u6620\u79d1\u6280\u7684\u4eba\u6587\u5173\u6000\u548c\u5177\u6709\u65f6\u4ee3\u7279\u70b9\u7684\u8bc9\u6c42\u4e5f\u5f88\u5e38\u89c1\u3002\u5728<a href=\"https://mp.weixin.qq.com/s?__biz=MzI0MzA3OTI1MA==&amp;mid=2651611835&amp;idx=1&amp;sn=0c331aea5495902620508b893ebb551f&amp;chksm=f28a4386c5fdca90eaed580a32528913f398a8ab9c0e1da8b0c1af434379cc08861b3555ae80&amp;token=1960790046&amp;lang=zh_CN&amp;scene=21#wechat_redirect\" target=\"_blank\"><strong>\u300a</strong></a>&nbsp;<a href=\"https://www.guokr.com/article/454804/\" target=\"_blank\">\u7535\u5f71\u4e2d\u79d1\u5b66\u5bb6\u662f\u82f1\u96c4\u8fd8\u662f\u6076\u68cd\uff1f\u90a3\u8981\u770b\u662f\u5565\u65f6\u5019\u7684\u7535\u5f71\u4e86</a><a href=\"https://mp.weixin.qq.com/s?__biz=MzI0MzA3OTI1MA==&amp;mid=2651611835&amp;idx=1&amp;sn=0c331aea5495902620508b893ebb551f&amp;chksm=f28a4386c5fdca90eaed580a32528913f398a8ab9c0e1da8b0c1af434379cc08861b3555ae80&amp;token=1960790046&amp;lang=zh_CN&amp;scene=21#wechat_redirect\" target=\"_blank\"><strong>\u300b</strong></a>\u4e2d\uff0c\u6211\u4ecb\u7ecd\u4e86\u4e0d\u540c\u65f6\u671f\u597d\u83b1\u575e\u5c4f\u5e55\u4e0a\u7684\u79d1\u5b66\u5bb6\u5f62\u8c61\u662f\u4e0d\u540c\u7684\uff0c1980\u5e74\u4ee3\u4e4b\u524d\u7684\u79d1\u5b66\u5bb6\u4ee5\u8d1f\u9762\u5f62\u8c61\u5c45\u591a\uff0c\u800c\u8fdb\u51651990\u5e74\u4ee3\u540e\u5219\u6709\u8d8a\u6765\u8d8a\u591a\u7684\u9ad8\u5927\u4e0a\u7684\u79d1\u5b66\u5bb6\u82f1\u96c4\u5f62\u8c61\u51fa\u73b0\u3002\u9020\u6210\u8fd9\u79cd\u5dee\u5f02\u7684\u539f\u56e0\uff0c\u6050\u6015\u6b63\u597d\u662f\u5728\u4e0d\u540c\u65f6\u4ee3\uff0c\u4eba\u4eec\u5bf9\u5f85\u79d1\u5b66\u7684\u6001\u5ea6\u4e0d\u540c\u30021960\u5e74\u4ee3\u5f00\u59cb\u8513\u5ef6\u3001\u5176\u5f71\u54cd\u5ef6\u4f38\u52301980\u5e74\u4ee3\u7684\u6fc0\u8fdb\u73af\u4fdd\u8fd0\u52a8\u8d28\u7591\u73b0\u4ee3\u79d1\u5b66\uff0c\u4e5f\u8d28\u7591\u79d1\u5b66\u5bb6\u662f\u5de5\u4e1a\u6c61\u67d3\u7684\u5e2e\u51f6\u3002\u5176\u7ed3\u679c\u5c31\u53cd\u6620\u5230\u5176\u65f6\u7684\u7535\u5f71\u548c\u7535\u89c6\u5c4f\u5e55\u7684\u79d1\u5b66\u5bb6\u5f62\u8c61\u4e0a\u3002</p>\r\n\r\n<p>\u6b64\u5916\uff0c<strong>\u79d1\u5e7b\u7535\u5f71\u4e2d\u53cd\u6620\u51fa\u7684\u4f26\u7406\u8d28\u7591\uff0c\u5219\u4eff\u4f5b\u5982\u5f71\u968f\u5f62\u5730\u7d27\u8ddf\u6700\u65b0\u79d1\u6280</strong>\u3002\u6bd4\u5982\u4e0a\u8ff0\u7684\u300a\u4f8f\u7f57\u7eaa\u516c\u56ed\u300b\u4e2d\u5bf9\u590d\u6d3b\u6050\u9f99\u7684\u63a2\u8ba8\uff0c\u76f4\u6307\u9057\u4f20\u6280\u672f\u7684\u6f5c\u5728\u98ce\u9669\uff0c\u800c\u300a\u4f8f\u7f57\u7eaa\u4e16\u754c\u300b\u4e2d\u7684\u57fa\u56e0\u6539\u9020\u6050\u9f99\uff0c\u5219\u53cd\u6620\u51fa\u5f71\u89c6\u754c\u5bf9\u57fa\u56e0\u7f16\u8f91\u6280\u672f\u7684\u62c5\u5fe7\u3002</p>\r\n\r\n<p>\u6211\u5728\u6b64\u524d\u7cfb\u5217\u7684\u679c\u58f3\u7f51\u6587\u7ae0\u4e2d\uff0c\u53cd\u590d\u5f3a\u8c03\u8fc7\uff0c\u79d1\u5e7b\u7535\u5f71\u4e2d\u7684\u90aa\u6076\u535a\u58eb\uff0c\u4e0d\u4f1a\u6210\u4e3a\u79d1\u5b66\u754c\u7684\u73b0\u5b9e\u3002\u4f46\u6b64\u5904\u5219\u8981\u6307\u51fa\uff0c\u5bf9\u5404\u79cd\u90aa\u6076\u535a\u58eb\u7684\u62c5\u5fe7\uff0c\u6216\u8005\u5bf9\u79d1\u6280\u53ea\u6709\u8b66\u793a\u800c\u65e0\u89e3\u51b3\u80fd\u529b\u7684\u63cf\u8ff0\uff08\u5982\u300a\u5929\u5730\u5927\u78b0\u649e\u300b\uff0c\u300a\u540e\u5929\u300b\uff09\uff0c\u6b63\u50cf1902\u5e74\u7684\u300a\u6708\u7403\u4e4b\u65c5\u300b\u8981\u8868\u73b0\u79d1\u5b66\u5947\u5e7b\u4e00\u6837\uff0c\u662f\u79d1\u5e7b\u5f71\u89c6\u5bf9\u4eba\u4eec\u7684\u4e3b\u8981\u5438\u5f15\u529b\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/R0IPA88tgJPHW-6aN4s-jqGQz-CWwqXptV0UBJ0F3zg4BAAASgIAAEpQ.jpg\" style=\"height:347px; width:640px\" />\u7535\u5f71\u300a\u540e\u5929\u300b\uff0c\u5bfc\u6f14\u662f\u7f57\u5170&middot;\u827e\u9ed8\u91cc\u5947</p>\r\n\r\n<p>\u53ef\u4ee5\u8bf4\uff0c<strong>\u5f88\u591a</strong><strong>\u4f18\u79c0\u7684\u79d1\u5e7b\u7535\u5f71\uff0c\u5df2\u7ecf\u4e0e\u79d1\u5b66\u548c\u79d1\u5b66\u754c\u5b9e\u73b0\u4e86\u9ad8\u5ea6\u878d\u5408</strong>\u3002\u79d1\u5b66\u5bb6\u3001\u79d1\u5b66\u793e\u4f1a\u5b66\u8005\u3001\u79d1\u7814\u4f26\u7406\u5b66\u8005\u548c\u79d1\u666e\u5de5\u4f5c\u8005\u90fd\u8bd5\u56fe\u5728\u79d1\u5e7b\u7247\u7684\u751f\u4ea7\u8fc7\u7a0b\u4e2d\u627e\u5230\u81ea\u5df1\u7684\u4f4d\u7f6e\u3002</p>\r\n\r\n<h2><strong>\u4ece\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5230\u300a\u4e0a\u6d77\u5821\u5792\u300b</strong></h2>\r\n\r\n<p>\u56de\u5230\u706b\u7206\u7684\u300a\u6d41\u6d6a\u5730\u7403\u300b\u548c\u5e7f\u53d7\u6307\u8d23\u7684\u300a\u4e0a\u6d77\u5821\u5792\u300b\uff0c\u6211\u4eec\u4f1a\u76f4\u89c2\u5730\u53d1\u73b0\uff0c\u4e24\u90e8\u79d1\u5e7b\u7535\u5f71\u90fd\u6ca1\u6709\u4ec0\u4e48\u79d1\u5b66\u5bb6\u7684\u5f62\u8c61\u51fa\u73b0\uff0c\u867d\u7136\u5728\u73b0\u5b9e\u4e2d\uff0c\u6b27\u7f8e\u5f88\u591a\u5b87\u822a\u5458\u8eab\u517c\u79d1\u5b66\u5bb6\u8eab\u4efd\uff0c\u4f46\u300a\u6d41\u6d6a\u5730\u7403\u300b\u4e2d\u7684\u5b87\u822a\u5458\uff0c\u5374\u7f3a\u4e4f\u8fd9\u6837\u7684\u7b26\u53f7\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/_q3Olv2moT3OL7THgFuMZYL99CLlBnYNrdonAgZnd5U4BAAAzAEAAEpQ.jpg\" style=\"height:273px; width:640px\" />\u300a\u4e0a\u6d77\u5821\u5792\u300b\u548c\u300a\u6d41\u6d6a\u5730\u7403\u300b</p>\r\n\r\n<p>\u79d1\u5e7b\u7535\u5f71\u5f53\u7136\u6ca1\u6709\u4e49\u52a1\u4e00\u5b9a\u8981\u523b\u753b\u79d1\u5b66\u5bb6\uff0c\u4f46\u628a\u5730\u7403\u5b58\u4ea1\u8fd9\u6837\u7684\u4e25\u5cfb\u79d1\u5b66\u95ee\u9898\uff0c\u4ec5\u4ec5\u4f5c\u4e3a\u4f53\u73b0\u4e00\u5e72\u4e3b\u914d\u89d2\u4eec\u7684\u80cc\u666f\uff0c\u5e76\u628a\u8bf8\u5982\u53ea\u670930\u4ebf\u4eba\u80fd\u83b7\u51c6\u8fdb\u5165\u5730\u4e0b\u57ce\u8fd9\u79cd\u672c\u8be5\u5728\u79d1\u5e7b\u7247\u4e2d\u5927\u4e66\u7279\u4e66\u7684\u4f26\u7406\u60c5\u8282\u5ffd\u7565\uff0c\u8fd9\u4e0d\u80fd\u4e0d\u8bf4\uff0c\u5373\u4fbf\u4ee5\u5e7f\u53d7\u597d\u8bc4\u3001\u573a\u9762\u5b8f\u5927\u548c\u60c5\u8282\u7d27\u51d1\u7684\u300a\u6d41\u6d6a\u5730\u7403\u300b\uff0c\u4e5f\u51e0\u4e4e\u6ca1\u6709\u592a\u591a\u4e0a\u8ff0\u7684\u4f53\u73b0\u79d1\u5b66\u4e0e\u79d1\u5e7b\u7535\u5f71\u7ea0\u7ed3\u5728\u4e00\u8d77\u7684\u597d\u83b1\u575e\u5143\u7d20\u3002</p>\r\n\r\n<p>\u867d\u7136\u300a\u6d41\u6d6a\u5730\u7403\u300b\u4e00\u7247\u8058\u8bf7\u4e86\u79d1\u5b66\u987e\u95ee\uff0c\u4e5f\u5728\u5ba3\u4f20\u65f6\u628a\u6b64\u4e8b\u5f53\u6210\u5356\u70b9\uff0c\u540e\u6765\u6709\u5173\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5230\u5e95\u6709\u591a\u79d1\u5b66\u8fd8\u7206\u53d1\u4e86\u5f88\u591a\u4e89\u8bba\uff08\u8fd9\u79cd\u4e13\u4e1a\u548c\u5584\u610f\u7684\u4e89\u8bba\u5f80\u5f80\u80fd\u4fc3\u8fdb\u800c\u4e0d\u662f\u538b\u6291\u7968\u623f\uff09\uff1b\u867d\u7136\u4f5c\u4e3a\u79d1\u5e7b\u4f5c\u5bb6\u7684\u5218\u6148\u6b23\u7684\u77e5\u540d\u5ea6\u4e5f\u6210\u4e3a\u5f71\u7247\u4eae\u70b9\uff0c\u4f46\u8bf4\u5230\u5e95\uff0c\u79d1\u5b66\u7ec8\u5f52\u662f\u5f71\u7247\u7684\u4e00\u4e2a\u914d\u89d2\uff0c\u66f4\u8c08\u4e0d\u4e0a\u79d1\u5b66\u5bb6\u548c\u79d1\u5b66\u754c\u8981\u501f\u7740\u5f71\u7247\u6765\u8fbe\u6210\u79d1\u5b66\u5171\u540c\u4f53\u7684\u76ee\u6807\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/dQAw42oGk5ztvtze0mvZCEc9muoDA7NK7ls0aFgEM444BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" />\u7535\u5f71\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5de5\u4f5c\u7167</p>\r\n\r\n<p>\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5f53\u7136\u6bd4\u88ab\u5e7f\u6cdb\u5410\u69fd\u7684\u300a\u4e0a\u6d77\u5821\u5792\u300b\u8981\u597d\u5f97\u591a\uff0c\u5b83\u89e6\u53ca\u5230\u672a\u6765\u5730\u7403\u4f55\u4ee5\u4e3a\u5bb6\u7684\u5b8f\u5927\u4e3b\u9898\uff0c\u8868\u73b0\u51fa\u5bf9\u91cd\u5927\u95ee\u9898\u7684\u8bda\u6073\u4e0e\u8ba4\u771f\uff0c\u5728\u5f71\u7247\u5c06\u8fd1\u7ed3\u675f\u65f6\u7528\u5218\u57f9\u5f3a\u548c\u7a7a\u95f4\u7ad9\u667a\u80fd\u63a7\u5236\u7cfb\u7edf\u7684\u51b2\u7a81\u8868\u73b0\u4e86\u4eba\u6027\u4e0e\u79d1\u5b66\u7406\u6027\u7684\u51b2\u7a81\uff08\u867d\u7136\u5e76\u4e0d\u5145\u5206\uff09\u3002\u800c\u300a\u4e0a\u6d77\u5821\u5792\u300b\u7684\u4ed9\u85e4&mdash;&mdash;\u5916\u661f\u6bcd\u8230&mdash;&mdash;\u59d0\u5f1f\u604b\u8fd9\u4e00\u60c5\u8282\u94fe\u6761\uff0c\u5219\u8fde\u4f5c\u4e3a\u80cc\u666f\u7684\u79d1\u5b66\u90fd\u61d2\u5f97\uff08\u6216\u8005\u6ca1\u6709\u80fd\u529b\uff09\u89e6\u53ca\u3002\u79d1\u5e7b\u5728\u8fd9\u91cc\u4e0d\u8fc7\u63d0\u4f9b\u4e86\u4e00\u4e2a\u7528\u7535\u8111\u7279\u6548\u6765\u6279\u91cf\u751f\u4ea7\u673a\u5668\u4eba\u9020\u578b\u7684\u501f\u53e3\uff0c\u9057\u61be\u7684\u662f\u8fd9\u91cc\u7684\u7279\u6548\u4e5f\u4e4f\u5584\u53ef\u9648\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/MXuomqb9v20oajQ9kkBF_kWBxNo-h0VVgCwy3C1Vhps4BAAAxAEAAEpQ.jpg\" style=\"height:268px; width:640px\" />\u7535\u5f71\u300a\u4e0a\u6d77\u5821\u5792\u300b\u4e2d\u7684\u5916\u661f\u4eba\u6bcd\u8230</p>\r\n\r\n<p>\u672c\u6587\u4e0d\u662f\u5f71\u8bc4\u3002\u8fd9\u91cc\u7684\u5206\u6790\u662f\u60f3\u8bf4\u660e\uff0c<strong>\u5f53\u4e00\u90e8\u79d1\u5e7b\u7535\u5f71\u4ec5\u662f\u628a\u79d1\u5b66\u62ff\u6765\u505a\u505a\u7279\u6548\u501f\u53e3\uff0c\u6ca1\u6709\u79d1\u5b66\u5143\u7d20\u800c\u8d28\u91cf\u53c8\u4f4e\u52a3\u65f6\uff0c\u522b\u6307\u671b\u6709\u79d1\u5e7b\u4e8c\u5b57\u5c31\u80fd\u6210\u4e3a\u7535\u5f71\u7968\u623f\u7684\u4fdd\u8bc1</strong>\u3002</p>\r\n\r\n<h2><strong>\u4e2d\u56fd\u8fdb\u5165\uff08\u6216\u7ec8\u7ed3\u4e86\uff09\u79d1\u5e7b\u7535\u5f71\u5143\u5e74\u4e48\uff1f</strong></h2>\r\n\r\n<p>\u7136\u800c\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5728\u53e6\u5916\u4e00\u4e2a\u5c42\u9762\u4e0a\u5927\u653e\u5f02\u5f69\uff0c\u90a3\u5c31\u662f\u501f\u7740\u5730\u7403\u707e\u96be\u7684\u80cc\u666f\u6765\u5f18\u626c\u4e2d\u56fd\u5728\u672a\u6765\u56fd\u9645\u9ad8\u79d1\u6280\u53d1\u5c55\u4e2d\u7684\u9886\u5bfc\u5730\u4f4d\u3002\u8fd9\u4e0d\u4ec5\u4ec5\u6709\u591a\u6b21\u51fa\u73b0\u56fd\u5fbd\u90a3\u6837\u7684\u660e\u793a\uff0c\u4e5f\u6709\u6709\u5173\u5218\u57f9\u5f3a\u4e2d\u6821\u5728\u672a\u6765\u7a7a\u95f4\u7ad9\u4e0a\u7684\u91cd\u8981\u5730\u4f4d\u90a3\u6837\u7684\u6697\u793a\u3002\u5e94\u8be5\u8bf4\u300a\u4e0a\u6d77\u5821\u5792\u300b\u628a\u4eba\u7c7b\u6700\u540e\u7684\u5e0c\u671b\u653e\u5728\u4e0a\u6d77\uff0c\u4e5f\u662f\u8bd5\u56fe\u6293\u4f4f\u8fd9\u65b9\u9762\u7684\u5e02\u573a\u3002\u53ef\u60dc\uff0c\u4f4e\u52a3\u7684\u7279\u6548\u548c\u60c5\u8282\u8bbe\u8ba1\u628a\u8fd9\u4e00\u70b9\u7ed9\u6d6a\u8d39\u4e86\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/HSf9vBcE4opNDcy4vH9mHFdLkpBOkErllCR54tEo8h44BAAAKgMAAEpQ.jpg\" style=\"height:480px; width:640px\" />\u7535\u5f71\u300a\u6d41\u6d6a\u5730\u7403\u300b</p>\r\n\r\n<p>\u79d1\u5e7b\u7535\u5f71\u5f18\u626c\u7231\u56fd\u7cbe\u795e\u5f53\u7136\u65e0\u53ef\u539a\u975e\u3002\u597d\u83b1\u575e\u5f71\u7247\u4e2d\u7684\u661f\u6761\u65d7\u968f\u5904\u53ef\u89c1\uff0c\u5f53\u5e74\u7f8e\u82cf\u4e89\u9738\u65f6\uff0c\u50cf\u300a\u661f\u7403\u5927\u6218\u300b\u90a3\u6837\u7684\u5f71\u7247\uff0c\u5728\u6fc0\u53d1\u7f8e\u56fd\u4eba\u63a2\u7d22\u592a\u7a7a\u7684\u5174\u8da3\u65f6\uff0c\u4e5f\u634e\u5e26\u7740\u5e2e\u52a9\u628a\u82cf\u8054\u62d6\u5165&ldquo;\u661f\u7403\u5927\u6218\u8ba1\u5212&rdquo;\uff0c&nbsp;\u6700\u7ec8\u62d6\u57ae\u4e86\u82cf\u8054\u3002\u4f46\u662f\uff0c\u4f5c\u4e3a\u79d1\u5e7b\u7535\u5f71\uff0c\u4e0d\u628a\u7231\u56fd\u4e3b\u4e49\u8fd9\u79cd\u5b8f\u5927\u653f\u6cbb\u53d9\u4e8b\u878d\u5165\u5bf9\u79d1\u5b66\u3001\u793e\u4f1a\u4e0e\u4eba\u7c7b\u53d1\u5c55\u7684\u63a2\u8ba8\u4e2d\uff0c\u5219\u5f88\u96be\u79f0\u5f97\u4e0a\u6210\u529f\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/-ifFJyI0oYKorJN0157MT9CySe87oCSUVp-scDDUiYA4BAAAOwIAAEpQ.jpg\" style=\"height:338px; width:640px\" />1996\u5e74\u7684\u7f8e\u56fd\u79d1\u5e7b\u7535\u5f71\u300a\u72ec\u7acb\u65e5\u300b\uff0c\u4e5f\u662f\u7f8e\u56fd\u7a7a\u519b\u7edd\u4f73\u7684\u5f81\u5175\u5e7f\u544a</p>\r\n\r\n<p>\u5728\u4e00\u5b9a\u610f\u4e49\u4e0a\uff0c\u6211\u4eec\u751a\u81f3\u53ef\u4ee5\u8bf4\uff0c\u5982\u679c\u7f3a\u4e4f\u5bf9\u79d1\u5b66\u4e0e\u793e\u4f1a\u5f20\u529b\u7684\u4e25\u8083\u6df1\u5165\u63a2\u8ba8\uff0c\u79d1\u5e7b\u7535\u5f71\u5373\u4fbf\u83b7\u5f97\u7968\u623f\u6210\u529f\uff0c\u5176\u4ef7\u503c\u4e5f\u66f4\u50cf\u4e2d\u56fd\u6c11\u65cf\u7535\u5f71\u4e8b\u4e1a\u8d77\u6b65\u65f6\u7684\u6b66\u4fa0\u7247\uff0c\u4ece1920\u5e74\u4ee3\u672b\u5f00\u59cb\uff0c\u300a\u6c5f\u5357\u5973\u4fa0\u300b\u3001\u300a\u706b\u70e7\u4e03\u661f\u697c\u300b\u3001\u300a\u5173\u4e1c\u5927\u4fa0\u300b\u7b49\u4e00\u6279\u810d\u7099\u4eba\u53e3\u7684\u6b66\u4fa0\u7535\u5f71\uff0c\u8ba9\u4e2d\u56fd\u5927\u57ce\u5e02\u7684\u89c2\u4f17\u5f00\u59cb\u611f\u53d7\u5230\u7535\u5f71\u827a\u672f\u7684\u795e\u5947\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/Ae83MBy05gWDeJAQJzN2T4wkvzRKq8H6o9wL4Hvx47osAgAAbgEAAEpQ.jpg\" style=\"height:366px; width:556px\" />1929\u5e74\u7684\u7535\u5f71\u300a\u706b\u70e7\u4e03\u661f\u697c\u300b</p>\r\n\r\n<p>\u503c\u5f97\u6b23\u6170\u7684\u662f\uff0c\u5728\u300a\u6d41\u6d6a\u5730\u7403\u300b\u83b7\u5f97\u7968\u623f\u6210\u529f\u540e\uff0c\u6709\u4e2d\u592e\u9886\u5bfc\u6279\u793a\u8981\u5927\u529b\u63a8\u52a8\u79d1\u5e7b\u827a\u672f\u7684\u53d1\u5c55\uff0c\u4e2d\u56fd\u5f88\u591a\u79d1\u666e\u793e\u56e2\u90fd\u52a0\u5927\u4e86\u8fd9\u65b9\u9762\u7684\u53c2\u4e0e\u529b\u5ea6\u3002\u5e02\u573a\u4e0a\u7684\u79d1\u5e7b\u4f5c\u54c1\u4e5f\u70ed\u70ed\u95f9\u95f9\uff0c\u5168\u56fd\u79d1\u5e7b\u5927\u4f1a\u4e5f\u5f00\u5f97\u98ce\u98ce\u706b\u706b\u3002\u4f46\u662f\uff0c\u5982\u679c\u4e2d\u56fd\u7684\u79d1\u5e7b\u5f71\u89c6\u548c\u79d1\u5e7b\u6587\u5b66\u4e0d\u5bf9\u79d1\u5b66\u4e0e\u793e\u4f1a\u7684\u5173\u7cfb\u8fdb\u884c\u6df1\u5165\u63a2\u8ba8\uff0c\u4e5f\u4e0d\u5bf9\u79d1\u5b66\u5982\u4f55\u5f71\u54cd\u672a\u6765\u7684\u4e16\u754c\u8fdb\u884c\u524d\u77bb\u6027\u5c55\u671b\uff0c\u800c\u4e3b\u8981\u6ee1\u8db3\u4e8e\u901a\u8fc7\u4e00\u4e2a\u79d1\u5e7b\u80cc\u666f\u6765\u5851\u9020\u5c4f\u5e55\u7279\u6548\uff0c\u90a3\u4e2d\u56fd\u7684\u79d1\u5e7b\u5f71\u89c6\uff0c\u5176\u5b9e\u8d28\u4e0e\u300a\u6708\u7403\u4e4b\u65c5\u300b\u6216\u300a\u6c5f\u5357\u5973\u4fa0\u300b\u4e5f\u5c31\u6ca1\u6709\u8db3\u591f\u7684\u533a\u522b\u3002</p>\r\n\r\n<p>\u5982\u679c\u4e2d\u56fd\u7684\u79d1\u5b66\u754c\u5bf9\u79d1\u5e7b\u7535\u5f71\u7684\u53c2\u4e0e\uff0c\u4ec5\u4ec5\u505c\u7559\u5728\u770b\u770b\u4e00\u4e9b\u7ec6\u8282\u662f\u5426\u51c6\u786e\uff1b\u5982\u679c\u4e2d\u56fd\u7684\u7535\u5f71\u89c2\u4f17\u5bf9\u79d1\u5e7b\u7535\u5f71\u7684\u8bc9\u6c42\uff0c\u4e3b\u8981\u5c31\u662f\u91cd\u78c5\u7279\u6548\u4e2d\u7684\u4e00\u70b9\u5bb6\u56fd\u60c5\u6000\uff0c\u90a3\u50cf\u300a\u6d41\u6d6a\u5730\u7403\u300b\u53d6\u5f97\u7684\u79d1\u5e7b\u7535\u5f71\u7684\u6210\u529f\uff0c\u5c31\u5f88\u96be\u6301\u7eed\u3002\u6240\u8c13\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5f00\u521b\u4e86\u4e2d\u56fd\u79d1\u5e7b\u7535\u5f71\u5143\u5e74\u7684\u8bf4\u6cd5\uff0c\u5c31\u53ef\u80fd\u662f\u4e00\u4e2a\u5047\u547d\u9898\u3002\uff08\u7f16\u8f91\uff1aYuki\uff09</p>", "date_created": "2019-10-30T11:24:26.345896+08:00", "date_modified": "2019-10-30T11:47:47.396106+08:00", "date_published": "2019-10-30T11:45:11+08:00", "id": 454946, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "microsf", "replies_count": 6, "small_image": "https://2-im.guokr.com/K8CiuYx0R18NdQEe3c_HHPWThsCDizOWqXbWh4-ixqE4BAAAzAEAAEpQ.jpg", "subject": {"articles_count": 517, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "sci-fiction", "minisite_key": "microsf", "name": "\u79d1\u5e7b", "sort_score": 12, "subject_type": "normal"}, "subject_key": "sci-fiction", "summary": "\u79d1\u5e7b\u7535\u5f71\u5143\u5e74\u7684\u7406\u60f3\u4e0e\u9aa8\u5e72\u73b0\u5b9e\u3002", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u5728\u4e4b\u524d\u7684\u4e13\u680f\u6587\u7ae0\u00a0\u300a\u7535\u5f71\u4e2d\u79d1\u5b66\u5bb6\u662f\u82f1\u96c4\u8fd8\u662f\u6076\u68cd\uff1f\u90a3\u8981\u770b\u662f\u5565\u65f6\u5019\u7684\u7535\u5f71\u4e86\u300b\u4e2d\uff0c\u6211\u501f\u7528\u7f51\u53cb\u7684\u8bc4\u8bba\u201c\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5f00\u521b\u4e86\u4e2d\u56fd\u79d1\u5e7b\u7535\u5f71\u7684\u5143\u5e74\u3001\u300a\u4e0a\u6d77\u5821\u5792\u300b\u53c8\u7ec8\u7ed3\u4e86\u8fd9\u4e2a\u5143\u5e74\u201d\u4f5c\u4e3a\u6587\u7ae0\u7684\u5f15\u5b50\u3002\u9650\u4e8e\u7bc7\u5e45\uff0c\u90a3\u7bc7\u6587\u7ae0\u6ca1\u6709\u4e13\u95e8\u63a2\u8ba8\u6240\u8c13\u201c\u4e2d\u56fd\u79d1\u5e7b\u7535\u5f71\u5143\u5e74\u201d\u7684\u95ee\u9898\uff0c\u672c\u7bc7\u6211\u5c06\u4ece\u4e00\u4f4d\u79d1\u5b66\u4f20\u64ad\u5de5\u4f5c\u8005\u7684\u89d2\u5ea6\uff0c\u53cd\u601d\u4e00\u4e0b\u8fd9\u4e2a\u8bf4\u6cd5\u3002\u4e24\u90e8\u7535\u5f71\u4e2d\u90fd\u6709\u4e0a\u6d77\u5143\u7d20\u9996\u5148\u8981\u58f0\u660e\uff0c\u6211\u4e0d\u662f\u5f71\u8bc4\u5bb6\uff0c\u6211\u7684\u79d1\u5b66\u4f20\u64ad\u7814\u7a76\u7684\u4e3b\u9898\u4e5f\u8c08\u4e0d\u4e0a\u4e13\u6ce8\u4e8e\u79d1\u5b66\u5f71\u89c6\u751a\u81f3\u79d1\u5e7b\u6587\u5b66\u3002\u56e0\u6b64\u8fd9\u91cc\u4e3b\u8981\u662f\u4ece\u79d1\u5b66\u6587\u5316\u7684\u89d2\u5ea6\u6765\u53cd\u601d\u4e0b\u79d1\u5e7b\u7535\u5f71\u8fd9\u4e00\u4e3b\u9898\u3002\u79d1\u5e7b\u7535\u5f71\u7684\u5386\u7a0b\u7535\u5f71\u4ece\u840c\u82bd\u65f6\u4ee3\u5f00\u59cb\uff0c\u5c31\u4e0e\u79d1\u5e7b\u7ed3\u4e0b\u4e86\u4e0d\u89e3\u4e4b\u7f18\u3002\u8ffd\u6eaf\u7535\u5f71\u65e9\u671f\u53d1\u5c55\u5386\u7a0b\uff0c\u5f88\u591a\u4eba\u90fd\u4f1a\u8c08\u8d771902\u5e74\u62cd\u6444\u7684\u300a\u6708\u7403\u4e4b\u65c5\u300b\uff08Le Voyage dans la Lune\uff09\uff0c\u90a3\u90e8\u7535\u5f71\u6d77\u62a5\u4e0a\u753b\u7740\u6708\u4eae\u7684\u4e00\u53ea\u773c\u775b\u88ab\u63d2\u4e0a\u4e86\u706b\u7bad\u3002\u7535\u5f71\u7684\u60c5\u8282\u5982\u4eca\u60f3\u6765\u5df2\u7ecf\u5e73\u6de1\u65e0\u5947\uff0c\u4f46\u5267\u4f5c\u8005\u57fa\u4e8e\u5f53\u65f6\u79d1\u5b66\u6c34\u5e73\u7684\u60f3\u8c61\u529b\u8fd8\u662f\u503c\u5f97\u94a6\u4f69\u3002\u7535\u5f71\u300a\u6708\u7403\u4e4b\u65c5\u300b\u79d1\u5e7b\u7535\u5f71\u53d1\u5c55\u53f2\u968f\u540e\u503c\u5f97\u4e00\u4e66\u7684\u5c31\u662f\u7231\u8fea\u751f\u5de5\u4f5c\u5ba4\u51fa\u54c1\u7684\u300a\u5f17\u5170\u80af\u65af\u5766\u300b\uff08Frankenstein\uff09\u3002\u867d\u7136\u5982\u4eca\u4eba\u4eec\u63d0\u8d77\u5f17\u5170\u80af\u65af\u5766\u5c06\u5176\u5f53\u4f5c\u4e00\u4e2a\u79d1\u5b66\u602a\u4eba\uff0c\u800c\u5f17\u5170\u80af\u65af\u5766\u98df\u54c1\uff08Frenkenfood\uff09\u5219\u6210\u4e3a\u5996\u9b54\u5316\u8f6c\u57fa\u56e0\u7684\u5e38\u89c1\u8bcd\u6c47\uff0c\u4f46\u5728\u7231\u8fea\u751f\u7684\u4f5c\u54c1\u4e2d\uff0c\u5f17\u5170\u80af\u65af\u5766\u53ef\u662f\u4e00\u4f4d\u6b63\u6d3e\u7684\u53d1\u660e\u5bb6\uff0c\u4e0d\u8fc7\u603b\u662f\u6267\u8ff7\u4e8e\u5947\u601d\u602a\u60f3\u3002\u7535\u5f71\u300a\u5f17\u5170\u80af\u65af\u5766\u300b\uff0c\u5bfc\u6f14\u662f\u8a79\u59c6\u65af\u00b7\u60e0\u5c14\u53e6\u5916\u4e00\u90e8\u503c\u5f97\u8bf4\u4e24\u53e5\u7684\u65e9\u671f\u79d1\u5e7b\u7535\u5f71\u5219\u662f\u6839\u636e\u5112\u52d2\u00b7\u51e1\u5c14\u7eb3\u540c\u540d\u5c0f\u8bf4\u6539\u7f16\u7684\u300a\u6d77\u5e95\u4e24\u4e07\u91cc\u300b\u3002\u5b83\u57281916\u5e74\u30011954\u5e74\u548c1997\u5e74\u88ab\u4e09\u6b21\u642c\u4e0a\u94f6\u5e55\uff0c\u8db3\u4ee5\u8bf4\u660e\u4eba\u4eec\u5bf9\u8fd9\u7c7b\u63a2\u9669\u79d1\u5e7b\u9898\u6750\u7684\u6301\u4e45\u5174\u8da3\u30021916\u5e74\u30011954\u5e74\u548c1997\u5e74\u7684\u7535\u5f71\u300a\u6d77\u5e95\u4e24\u4e07\u91cc\u300b\u8fd9\u4e9b\u65e9\u671f\u79d1\u5e7b\u7535\u5f71\u7684\u5171\u6027\u662f\u4ec0\u4e48\u5462\uff1f\u90a3\u5c31\u662f\u901a\u8fc7\u5c55\u793a\u79d1\u5b66\u5185\u5bb9\uff0c\u8ba9\u6211\u4eec\u770b\u5230\u4e86\u7535\u5f71\u53ef\u4ee5\u5e26\u7ed9\u4eba\u4eec\u7684\u7f8e\u4e3d\u65b0\u4e16\u754c\u3002\u800c\u53cd\u8fc7\u6765\uff0c\u5305\u62ec\u7231\u8fea\u751f\u5728\u5185\u7684\u65e9\u671f\u79d1\u666e\u4eba\u58eb\uff08\u300a\u79d1\u5b66\u300b\u6742\u5fd7\u6b63\u662f\u5728\u7231\u8fea\u751f\u8d44\u52a9\u4e0b\u5f97\u4ee5\u51fa\u7248\uff09\u4e5f\u8ba4\u8bc6\u5230\u901a\u8fc7\u79d1\u5e7b\u7535\u5f71\u8fd9\u79cd\u5f62\u5f0f\u53ef\u4ee5\u6781\u5927\u5730\u63d0\u5347\u4eba\u4eec\u5bf9\u79d1\u5b66\u7684\u5174\u8da3\u4e0e\u5411\u5f80\u3002\u6b63\u56e0\u4e3a\u8fd9\u4e2a\u539f\u56e0\uff0c\u5728\u597d\u83b1\u575e\u7684\u7535\u5f71\u5de5\u4e1a\u53d1\u5c55\u8d77\u6765\u540e\uff0c\u79d1\u5e7b\u7535\u5f71\u4e00\u76f4\u53d7\u5230\u5236\u7247\u4eba\u4eec\u7684\u5782\u9752\uff0c\u800c\u90e8\u5206\u79d1\u5b66\u5bb6\u4eec\u5219\u5728\u5f88\u65e9\u5c31\u613f\u610f\u901a\u8fc7\u7535\u5f71\u5c55\u793a\u81ea\u5df1\u7684\u79d1\u7814\u6210\u679c\uff0c\u540e\u6765\u5219\u53c8\u901a\u8fc7\u597d\u83b1\u575e\u79d1\u5e7b\u7535\u5f71\u7684\u79d1\u5b66\u987e\u95ee\u5236\u5ea6\u4ecb\u5165\u5230\u7535\u5f71\u7684\u79d1\u5b66\u53d9\u4e8b\u4e2d\u3002\u79d1\u5e7b\u7535\u5f71\u4e0e\u79d1\u5b66\u7684\u7ea0\u7ed3\u4e0d\u4ec5\u79d1\u5b66\u754c\u91cd\u89c6\u79d1\u5e7b\u7535\u5f71\u7684\u79d1\u666e\u4f5c\u7528\uff0c\u751a\u81f3\u6709\u7814\u7a76\u8868\u660e\uff0c\u4e00\u4e9b\u8457\u540d\u7684\u79d1\u5e7b\u7535\u5f71\u8fd8\u5bf9\u79d1\u7814\u8fdb\u7a0b\u672c\u8eab\u6709\u5f71\u54cd\uff0c\u6bd4\u5982\u7535\u5f71\u300a\u4f8f\u7f57\u7eaa\u516c\u56ed\u300b\u4e2d\u590d\u6d3b\u6050\u9f99\u7684\u5c1d\u8bd5\uff0c\u6700\u521d\u5e76\u4e0d\u662f\u6765\u81ea\u6210\u4e3a\u53e4\u751f\u7269\u5b66\u5171\u8bc6\u7684\u60f3\u6cd5\u3002\u4f46\u901a\u8fc7\u5927\u83b7\u6210\u529f\u7684\u7535\u5f71\uff0c\u8fd9\u4e2a\u60f3\u6cd5\u5f00\u59cb\u5e7f\u6cdb\u5730\u5f71\u54cd\u4e86\u76f8\u5173\u7684\u79d1\u5b66\u8bae\u7a0b\uff0c\u5e76\u6210\u4e3a\u4e86\u5f88\u591a\u4e25\u8083\u79d1\u5b66\u5bb6\u8fdb\u884c\u5c1d\u8bd5\u7684\u79d1\u7814\u9879\u76ee\uff08\u5e94\u8be5\u4e5f\u5f71\u54cd\u4e86\u7f8e\u56fd\u79d1\u5b66\u57fa\u91d1\u7684\u53d1\u653e\uff0c\u5c3d\u7ba1\u6211\u770b\u5230\u7684\u6587\u732e\u4e2d\u6ca1\u6709\u63d0\u5230\u8fd9\u4e00\u70b9\uff09\u30021993\u5e74\u7684\u7535\u5f71\u300a\u4f8f\u7f57\u7eaa\u516c\u56ed\u300b\uff0c\u5bfc\u6f14\u662f\u53f2\u8482\u6587\u00b7\u65af\u76ae\u5c14\u4f2f\u683c\u5176\u4ed6\u901a\u8fc7\u79d1\u5e7b\u7535\u5f71\u8ba9\u79d1\u6280\u754c\u5185\u90e8\u4e89\u8bba\u4e2d\u7684\u4e00\u65b9\u83b7\u5f97\u66f4\u5e7f\u6cdb\u5f71\u54cd\u7684\u8fd8\u6709\u4e0d\u5c11\uff0c\u6bd4\u5982\u300a\u661f\u9645\u7a7f\u8d8a\u300b(Interstellar)\u4e2d\u7684\u201c\u866b\u6d1e\u201d\u3002\u5b9e\u9645\u4e0a\uff0c\u79d1\u5e7b\u5c0f\u8bf4\u8fd9\u65b9\u9762\u7684\u60c5\u8282\u66f4\u591a\u3002\u8bfb\u300a\u4e09\u4f53\u300b\u65f6\uff0c\u8bfb\u8005\u53ef\u80fd\u4f1a\u6ce8\u610f\u5230\u4e09\u4f53\u4eba\u9501\u6b7b\u5730\u7403\u4eba\u7684\u52a0\u901f\u5668\uff0c\u5bfc\u81f4\u9ad8\u80fd\u7269\u7406\u65e0\u6cd5\u53d1\u5c55\u4ece\u800c\u79d1\u5b66\u65e0\u6cd5\u5b9e\u73b0\u672c\u8d28\u4e0a\u7684\u98de\u8dc3\u3002\u8fd9\u63cf\u8ff0\u542c\u7740\u50cf\u4e0d\u50cf\u9ad8\u80fd\u7269\u7406\u5b66\u754c\u5bf9\u81ea\u5df1\u4e0d\u80fd\u5982\u613f\u5f97\u5230\u5927\u79d1\u5b66\u9879\u76ee\u62e8\u6b3e\u7684\u62b1\u6028\u5462\uff1f\u300a\u661f\u9645\u7a7f\u8d8a\u300b\uff0c\u5bfc\u6f14\u662f\u514b\u91cc\u65af\u6258\u5f17\u00b7\u8bfa\u5170\u76f8\u6bd4\u5177\u4f53\u7684\u79d1\u5b66\u5185\u5bb9\u6e17\u5165\u5230\u79d1\u5e7b\u5f71\u89c6\u4e2d\uff0c\u501f\u52a9\u79d1\u5e7b\u5f71\u89c6\u7684\u5e7f\u6cdb\u5f71\u54cd\u6765\u53cd\u6620\u79d1\u6280\u7684\u4eba\u6587\u5173\u6000\u548c\u5177\u6709\u65f6\u4ee3\u7279\u70b9\u7684\u8bc9\u6c42\u4e5f\u5f88\u5e38\u89c1\u3002\u5728\u300a\u00a0\u7535\u5f71\u4e2d\u79d1\u5b66\u5bb6\u662f\u82f1\u96c4\u8fd8\u662f\u6076\u68cd\uff1f\u90a3\u8981\u770b\u662f\u5565\u65f6\u5019\u7684\u7535\u5f71\u4e86\u300b\u4e2d\uff0c\u6211\u4ecb\u7ecd\u4e86\u4e0d\u540c\u65f6\u671f\u597d\u83b1\u575e\u5c4f\u5e55\u4e0a\u7684\u79d1\u5b66\u5bb6\u5f62\u8c61\u662f\u4e0d\u540c\u7684\uff0c1980\u5e74\u4ee3\u4e4b\u524d\u7684\u79d1\u5b66\u5bb6\u4ee5\u8d1f\u9762\u5f62\u8c61\u5c45\u591a\uff0c\u800c\u8fdb\u51651990\u5e74\u4ee3\u540e\u5219\u6709\u8d8a\u6765\u8d8a\u591a\u7684\u9ad8\u5927\u4e0a\u7684\u79d1\u5b66\u5bb6\u82f1\u96c4\u5f62\u8c61\u51fa\u73b0\u3002\u9020\u6210\u8fd9\u79cd\u5dee\u5f02\u7684\u539f\u56e0\uff0c\u6050\u6015\u6b63\u597d\u662f\u5728\u4e0d\u540c\u65f6\u4ee3\uff0c\u4eba\u4eec\u5bf9\u5f85\u79d1\u5b66\u7684\u6001\u5ea6\u4e0d\u540c\u30021960\u5e74\u4ee3\u5f00\u59cb\u8513\u5ef6\u3001\u5176\u5f71\u54cd\u5ef6\u4f38\u52301980\u5e74\u4ee3\u7684\u6fc0\u8fdb\u73af\u4fdd\u8fd0\u52a8\u8d28\u7591\u73b0\u4ee3\u79d1\u5b66\uff0c\u4e5f\u8d28\u7591\u79d1\u5b66\u5bb6\u662f\u5de5\u4e1a\u6c61\u67d3\u7684\u5e2e\u51f6\u3002\u5176\u7ed3\u679c\u5c31\u53cd\u6620\u5230\u5176\u65f6\u7684\u7535\u5f71\u548c\u7535\u89c6\u5c4f\u5e55\u7684\u79d1\u5b66\u5bb6\u5f62\u8c61\u4e0a\u3002\u6b64\u5916\uff0c\u79d1\u5e7b\u7535\u5f71\u4e2d\u53cd\u6620\u51fa\u7684\u4f26\u7406\u8d28\u7591\uff0c\u5219\u4eff\u4f5b\u5982\u5f71\u968f\u5f62\u5730\u7d27\u8ddf\u6700\u65b0\u79d1\u6280\u3002\u6bd4\u5982\u4e0a\u8ff0\u7684\u300a\u4f8f\u7f57\u7eaa\u516c\u56ed\u300b\u4e2d\u5bf9\u590d\u6d3b\u6050\u9f99\u7684\u63a2\u8ba8\uff0c\u76f4\u6307\u9057\u4f20\u6280\u672f\u7684\u6f5c\u5728\u98ce\u9669\uff0c\u800c\u300a\u4f8f\u7f57\u7eaa\u4e16\u754c\u300b\u4e2d\u7684\u57fa\u56e0\u6539\u9020\u6050\u9f99\uff0c\u5219\u53cd\u6620\u51fa\u5f71\u89c6\u754c\u5bf9\u57fa\u56e0\u7f16\u8f91\u6280\u672f\u7684\u62c5\u5fe7\u3002\u6211\u5728\u6b64\u524d\u7cfb\u5217\u7684\u679c\u58f3\u7f51\u6587\u7ae0\u4e2d\uff0c\u53cd\u590d\u5f3a\u8c03\u8fc7\uff0c\u79d1\u5e7b\u7535\u5f71\u4e2d\u7684\u90aa\u6076\u535a\u58eb\uff0c\u4e0d\u4f1a\u6210\u4e3a\u79d1\u5b66\u754c\u7684\u73b0\u5b9e\u3002\u4f46\u6b64\u5904\u5219\u8981\u6307\u51fa\uff0c\u5bf9\u5404\u79cd\u90aa\u6076\u535a\u58eb\u7684\u62c5\u5fe7\uff0c\u6216\u8005\u5bf9\u79d1\u6280\u53ea\u6709\u8b66\u793a\u800c\u65e0\u89e3\u51b3\u80fd\u529b\u7684\u63cf\u8ff0\uff08\u5982\u300a\u5929\u5730\u5927\u78b0\u649e\u300b\uff0c\u300a\u540e\u5929\u300b\uff09\uff0c\u6b63\u50cf1902\u5e74\u7684\u300a\u6708\u7403\u4e4b\u65c5\u300b\u8981\u8868\u73b0\u79d1\u5b66\u5947\u5e7b\u4e00\u6837\uff0c\u662f\u79d1\u5e7b\u5f71\u89c6\u5bf9\u4eba\u4eec\u7684\u4e3b\u8981\u5438\u5f15\u529b\u3002\u7535\u5f71\u300a\u540e\u5929\u300b\uff0c\u5bfc\u6f14\u662f\u7f57\u5170\u00b7\u827e\u9ed8\u91cc\u5947\u53ef\u4ee5\u8bf4\uff0c\u5f88\u591a\u4f18\u79c0\u7684\u79d1\u5e7b\u7535\u5f71\uff0c\u5df2\u7ecf\u4e0e\u79d1\u5b66\u548c\u79d1\u5b66\u754c\u5b9e\u73b0\u4e86\u9ad8\u5ea6\u878d\u5408\u3002\u79d1\u5b66\u5bb6\u3001\u79d1\u5b66\u793e\u4f1a\u5b66\u8005\u3001\u79d1\u7814\u4f26\u7406\u5b66\u8005\u548c\u79d1\u666e\u5de5\u4f5c\u8005\u90fd\u8bd5\u56fe\u5728\u79d1\u5e7b\u7247\u7684\u751f\u4ea7\u8fc7\u7a0b\u4e2d\u627e\u5230\u81ea\u5df1\u7684\u4f4d\u7f6e\u3002\u4ece\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5230\u300a\u4e0a\u6d77\u5821\u5792\u300b\u56de\u5230\u706b\u7206\u7684\u300a\u6d41\u6d6a\u5730\u7403\u300b\u548c\u5e7f\u53d7\u6307\u8d23\u7684\u300a\u4e0a\u6d77\u5821\u5792\u300b\uff0c\u6211\u4eec\u4f1a\u76f4\u89c2\u5730\u53d1\u73b0\uff0c\u4e24\u90e8\u79d1\u5e7b\u7535\u5f71\u90fd\u6ca1\u6709\u4ec0\u4e48\u79d1\u5b66\u5bb6\u7684\u5f62\u8c61\u51fa\u73b0\uff0c\u867d\u7136\u5728\u73b0\u5b9e\u4e2d\uff0c\u6b27\u7f8e\u5f88\u591a\u5b87\u822a\u5458\u8eab\u517c\u79d1\u5b66\u5bb6\u8eab\u4efd\uff0c\u4f46\u300a\u6d41\u6d6a\u5730\u7403\u300b\u4e2d\u7684\u5b87\u822a\u5458\uff0c\u5374\u7f3a\u4e4f\u8fd9\u6837\u7684\u7b26\u53f7\u3002\u300a\u4e0a\u6d77\u5821\u5792\u300b\u548c\u300a\u6d41\u6d6a\u5730\u7403\u300b\u79d1\u5e7b\u7535\u5f71\u5f53\u7136\u6ca1\u6709\u4e49\u52a1\u4e00\u5b9a\u8981\u523b\u753b\u79d1\u5b66\u5bb6\uff0c\u4f46\u628a\u5730\u7403\u5b58\u4ea1\u8fd9\u6837\u7684\u4e25\u5cfb\u79d1\u5b66\u95ee\u9898\uff0c\u4ec5\u4ec5\u4f5c\u4e3a\u4f53\u73b0\u4e00\u5e72\u4e3b\u914d\u89d2\u4eec\u7684\u80cc\u666f\uff0c\u5e76\u628a\u8bf8\u5982\u53ea\u670930\u4ebf\u4eba\u80fd\u83b7\u51c6\u8fdb\u5165\u5730\u4e0b\u57ce\u8fd9\u79cd\u672c\u8be5\u5728\u79d1\u5e7b\u7247\u4e2d\u5927\u4e66\u7279\u4e66\u7684\u4f26\u7406\u60c5\u8282\u5ffd\u7565\uff0c\u8fd9\u4e0d\u80fd\u4e0d\u8bf4\uff0c\u5373\u4fbf\u4ee5\u5e7f\u53d7\u597d\u8bc4\u3001\u573a\u9762\u5b8f\u5927\u548c\u60c5\u8282\u7d27\u51d1\u7684\u300a\u6d41\u6d6a\u5730\u7403\u300b\uff0c\u4e5f\u51e0\u4e4e\u6ca1\u6709\u592a\u591a\u4e0a\u8ff0\u7684\u4f53\u73b0\u79d1\u5b66\u4e0e\u79d1\u5e7b\u7535\u5f71\u7ea0\u7ed3\u5728\u4e00\u8d77\u7684\u597d\u83b1\u575e\u5143\u7d20\u3002\u867d\u7136\u300a\u6d41\u6d6a\u5730\u7403\u300b\u4e00\u7247\u8058\u8bf7\u4e86\u79d1\u5b66\u987e\u95ee\uff0c\u4e5f\u5728\u5ba3\u4f20\u65f6\u628a\u6b64\u4e8b\u5f53\u6210\u5356\u70b9\uff0c\u540e\u6765\u6709\u5173\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5230\u5e95\u6709\u591a\u79d1\u5b66\u8fd8\u7206\u53d1\u4e86\u5f88\u591a\u4e89\u8bba\uff08\u8fd9\u79cd\u4e13\u4e1a\u548c\u5584\u610f\u7684\u4e89\u8bba\u5f80\u5f80\u80fd\u4fc3\u8fdb\u800c\u4e0d\u662f\u538b\u6291\u7968\u623f\uff09\uff1b\u867d\u7136\u4f5c\u4e3a\u79d1\u5e7b\u4f5c\u5bb6\u7684\u5218\u6148\u6b23\u7684\u77e5\u540d\u5ea6\u4e5f\u6210\u4e3a\u5f71\u7247\u4eae\u70b9\uff0c\u4f46\u8bf4\u5230\u5e95\uff0c\u79d1\u5b66\u7ec8\u5f52\u662f\u5f71\u7247\u7684\u4e00\u4e2a\u914d\u89d2\uff0c\u66f4\u8c08\u4e0d\u4e0a\u79d1\u5b66\u5bb6\u548c\u79d1\u5b66\u754c\u8981\u501f\u7740\u5f71\u7247\u6765\u8fbe\u6210\u79d1\u5b66\u5171\u540c\u4f53\u7684\u76ee\u6807\u3002\u7535\u5f71\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5de5\u4f5c\u7167\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5f53\u7136\u6bd4\u88ab\u5e7f\u6cdb\u5410\u69fd\u7684\u300a\u4e0a\u6d77\u5821\u5792\u300b\u8981\u597d\u5f97\u591a\uff0c\u5b83\u89e6\u53ca\u5230\u672a\u6765\u5730\u7403\u4f55\u4ee5\u4e3a\u5bb6\u7684\u5b8f\u5927\u4e3b\u9898\uff0c\u8868\u73b0\u51fa\u5bf9\u91cd\u5927\u95ee\u9898\u7684\u8bda\u6073\u4e0e\u8ba4\u771f\uff0c\u5728\u5f71\u7247\u5c06\u8fd1\u7ed3\u675f\u65f6\u7528\u5218\u57f9\u5f3a\u548c\u7a7a\u95f4\u7ad9\u667a\u80fd\u63a7\u5236\u7cfb\u7edf\u7684\u51b2\u7a81\u8868\u73b0\u4e86\u4eba\u6027\u4e0e\u79d1\u5b66\u7406\u6027\u7684\u51b2\u7a81\uff08\u867d\u7136\u5e76\u4e0d\u5145\u5206\uff09\u3002\u800c\u300a\u4e0a\u6d77\u5821\u5792\u300b\u7684\u4ed9\u85e4\u2014\u2014\u5916\u661f\u6bcd\u8230\u2014\u2014\u59d0\u5f1f\u604b\u8fd9\u4e00\u60c5\u8282\u94fe\u6761\uff0c\u5219\u8fde\u4f5c\u4e3a\u80cc\u666f\u7684\u79d1\u5b66\u90fd\u61d2\u5f97\uff08\u6216\u8005\u6ca1\u6709\u80fd\u529b\uff09\u89e6\u53ca\u3002\u79d1\u5e7b\u5728\u8fd9\u91cc\u4e0d\u8fc7\u63d0\u4f9b\u4e86\u4e00\u4e2a\u7528\u7535\u8111\u7279\u6548\u6765\u6279\u91cf\u751f\u4ea7\u673a\u5668\u4eba\u9020\u578b\u7684\u501f\u53e3\uff0c\u9057\u61be\u7684\u662f\u8fd9\u91cc\u7684\u7279\u6548\u4e5f\u4e4f\u5584\u53ef\u9648\u3002\u7535\u5f71\u300a\u4e0a\u6d77\u5821\u5792\u300b\u4e2d\u7684\u5916\u661f\u4eba\u6bcd\u8230\u672c\u6587\u4e0d\u662f\u5f71\u8bc4\u3002\u8fd9\u91cc\u7684\u5206\u6790\u662f\u60f3\u8bf4\u660e\uff0c\u5f53\u4e00\u90e8\u79d1\u5e7b\u7535\u5f71\u4ec5\u662f\u628a\u79d1\u5b66\u62ff\u6765\u505a\u505a\u7279\u6548\u501f\u53e3\uff0c\u6ca1\u6709\u79d1\u5b66\u5143\u7d20\u800c\u8d28\u91cf\u53c8\u4f4e\u52a3\u65f6\uff0c\u522b\u6307\u671b\u6709\u79d1\u5e7b\u4e8c\u5b57\u5c31\u80fd\u6210\u4e3a\u7535\u5f71\u7968\u623f\u7684\u4fdd\u8bc1\u3002\u4e2d\u56fd\u8fdb\u5165\uff08\u6216\u7ec8\u7ed3\u4e86\uff09\u79d1\u5e7b\u7535\u5f71\u5143\u5e74\u4e48\uff1f\u7136\u800c\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5728\u53e6\u5916\u4e00\u4e2a\u5c42\u9762\u4e0a\u5927\u653e\u5f02\u5f69\uff0c\u90a3\u5c31\u662f\u501f\u7740\u5730\u7403\u707e\u96be\u7684\u80cc\u666f\u6765\u5f18\u626c\u4e2d\u56fd\u5728\u672a\u6765\u56fd\u9645\u9ad8\u79d1\u6280\u53d1\u5c55\u4e2d\u7684\u9886\u5bfc\u5730\u4f4d\u3002\u8fd9\u4e0d\u4ec5\u4ec5\u6709\u591a\u6b21\u51fa\u73b0\u56fd\u5fbd\u90a3\u6837\u7684\u660e\u793a\uff0c\u4e5f\u6709\u6709\u5173\u5218\u57f9\u5f3a\u4e2d\u6821\u5728\u672a\u6765\u7a7a\u95f4\u7ad9\u4e0a\u7684\u91cd\u8981\u5730\u4f4d\u90a3\u6837\u7684\u6697\u793a\u3002\u5e94\u8be5\u8bf4\u300a\u4e0a\u6d77\u5821\u5792\u300b\u628a\u4eba\u7c7b\u6700\u540e\u7684\u5e0c\u671b\u653e\u5728\u4e0a\u6d77\uff0c\u4e5f\u662f\u8bd5\u56fe\u6293\u4f4f\u8fd9\u65b9\u9762\u7684\u5e02\u573a\u3002\u53ef\u60dc\uff0c\u4f4e\u52a3\u7684\u7279\u6548\u548c\u60c5\u8282\u8bbe\u8ba1\u628a\u8fd9\u4e00\u70b9\u7ed9\u6d6a\u8d39\u4e86\u3002\u7535\u5f71\u300a\u6d41\u6d6a\u5730\u7403\u300b\u79d1\u5e7b\u7535\u5f71\u5f18\u626c\u7231\u56fd\u7cbe\u795e\u5f53\u7136\u65e0\u53ef\u539a\u975e\u3002\u597d\u83b1\u575e\u5f71\u7247\u4e2d\u7684\u661f\u6761\u65d7\u968f\u5904\u53ef\u89c1\uff0c\u5f53\u5e74\u7f8e\u82cf\u4e89\u9738\u65f6\uff0c\u50cf\u300a\u661f\u7403\u5927\u6218\u300b\u90a3\u6837\u7684\u5f71\u7247\uff0c\u5728\u6fc0\u53d1\u7f8e\u56fd\u4eba\u63a2\u7d22\u592a\u7a7a\u7684\u5174\u8da3\u65f6\uff0c\u4e5f\u634e\u5e26\u7740\u5e2e\u52a9\u628a\u82cf\u8054\u62d6\u5165\u201c\u661f\u7403\u5927\u6218\u8ba1\u5212\u201d\uff0c\u00a0\u6700\u7ec8\u62d6\u57ae\u4e86\u82cf\u8054\u3002\u4f46\u662f\uff0c\u4f5c\u4e3a\u79d1\u5e7b\u7535\u5f71\uff0c\u4e0d\u628a\u7231\u56fd\u4e3b\u4e49\u8fd9\u79cd\u5b8f\u5927\u653f\u6cbb\u53d9\u4e8b\u878d\u5165\u5bf9\u79d1\u5b66\u3001\u793e\u4f1a\u4e0e\u4eba\u7c7b\u53d1\u5c55\u7684\u63a2\u8ba8\u4e2d\uff0c\u5219\u5f88\u96be\u79f0\u5f97\u4e0a\u6210\u529f\u30021996\u5e74\u7684\u7f8e\u56fd\u79d1\u5e7b\u7535\u5f71\u300a\u72ec\u7acb\u65e5\u300b\uff0c\u4e5f\u662f\u7f8e\u56fd\u7a7a\u519b\u7edd\u4f73\u7684\u5f81\u5175\u5e7f\u544a\u5728\u4e00\u5b9a\u610f\u4e49\u4e0a\uff0c\u6211\u4eec\u751a\u81f3\u53ef\u4ee5\u8bf4\uff0c\u5982\u679c\u7f3a\u4e4f\u5bf9\u79d1\u5b66\u4e0e\u793e\u4f1a\u5f20\u529b\u7684\u4e25\u8083\u6df1\u5165\u63a2\u8ba8\uff0c\u79d1\u5e7b\u7535\u5f71\u5373\u4fbf\u83b7\u5f97\u7968\u623f\u6210\u529f\uff0c\u5176\u4ef7\u503c\u4e5f\u66f4\u50cf\u4e2d\u56fd\u6c11\u65cf\u7535\u5f71\u4e8b\u4e1a\u8d77\u6b65\u65f6\u7684\u6b66\u4fa0\u7247\uff0c\u4ece1920\u5e74\u4ee3\u672b\u5f00\u59cb\uff0c\u300a\u6c5f\u5357\u5973\u4fa0\u300b\u3001\u300a\u706b\u70e7\u4e03\u661f\u697c\u300b\u3001\u300a\u5173\u4e1c\u5927\u4fa0\u300b\u7b49\u4e00\u6279\u810d\u7099\u4eba\u53e3\u7684\u6b66\u4fa0\u7535\u5f71\uff0c\u8ba9\u4e2d\u56fd\u5927\u57ce\u5e02\u7684\u89c2\u4f17\u5f00\u59cb\u611f\u53d7\u5230\u7535\u5f71\u827a\u672f\u7684\u795e\u5947\u30021929\u5e74\u7684\u7535\u5f71\u300a\u706b\u70e7\u4e03\u661f\u697c\u300b\u503c\u5f97\u6b23\u6170\u7684\u662f\uff0c\u5728\u300a\u6d41\u6d6a\u5730\u7403\u300b\u83b7\u5f97\u7968\u623f\u6210\u529f\u540e\uff0c\u6709\u4e2d\u592e\u9886\u5bfc\u6279\u793a\u8981\u5927\u529b\u63a8\u52a8\u79d1\u5e7b\u827a\u672f\u7684\u53d1\u5c55\uff0c\u4e2d\u56fd\u5f88\u591a\u79d1\u666e\u793e\u56e2\u90fd\u52a0\u5927\u4e86\u8fd9\u65b9\u9762\u7684\u53c2\u4e0e\u529b\u5ea6\u3002\u5e02\u573a\u4e0a\u7684\u79d1\u5e7b\u4f5c\u54c1\u4e5f\u70ed\u70ed\u95f9\u95f9\uff0c\u5168\u56fd\u79d1\u5e7b\u5927\u4f1a\u4e5f\u5f00\u5f97\u98ce\u98ce\u706b\u706b\u3002\u4f46\u662f\uff0c\u5982\u679c\u4e2d\u56fd\u7684\u79d1\u5e7b\u5f71\u89c6\u548c\u79d1\u5e7b\u6587\u5b66\u4e0d\u5bf9\u79d1\u5b66\u4e0e\u793e\u4f1a\u7684\u5173\u7cfb\u8fdb\u884c\u6df1\u5165\u63a2\u8ba8\uff0c\u4e5f\u4e0d\u5bf9\u79d1\u5b66\u5982\u4f55\u5f71\u54cd\u672a\u6765\u7684\u4e16\u754c\u8fdb\u884c\u524d\u77bb\u6027\u5c55\u671b\uff0c\u800c\u4e3b\u8981\u6ee1\u8db3\u4e8e\u901a\u8fc7\u4e00\u4e2a\u79d1\u5e7b\u80cc\u666f\u6765\u5851\u9020\u5c4f\u5e55\u7279\u6548\uff0c\u90a3\u4e2d\u56fd\u7684\u79d1\u5e7b\u5f71\u89c6\uff0c\u5176\u5b9e\u8d28\u4e0e\u300a\u6708\u7403\u4e4b\u65c5\u300b\u6216\u300a\u6c5f\u5357\u5973\u4fa0\u300b\u4e5f\u5c31\u6ca1\u6709\u8db3\u591f\u7684\u533a\u522b\u3002\u5982\u679c\u4e2d\u56fd\u7684\u79d1\u5b66\u754c\u5bf9\u79d1\u5e7b\u7535\u5f71\u7684\u53c2\u4e0e\uff0c\u4ec5\u4ec5\u505c\u7559\u5728\u770b\u770b\u4e00\u4e9b\u7ec6\u8282\u662f\u5426\u51c6\u786e\uff1b\u5982\u679c\u4e2d\u56fd\u7684\u7535\u5f71\u89c2\u4f17\u5bf9\u79d1\u5e7b\u7535\u5f71\u7684\u8bc9\u6c42\uff0c\u4e3b\u8981\u5c31\u662f\u91cd\u78c5\u7279\u6548\u4e2d\u7684\u4e00\u70b9\u5bb6\u56fd\u60c5\u6000\uff0c\u90a3\u50cf\u300a\u6d41\u6d6a\u5730\u7403\u300b\u53d6\u5f97\u7684\u79d1\u5e7b\u7535\u5f71\u7684\u6210\u529f\uff0c\u5c31\u5f88\u96be\u6301\u7eed\u3002\u6240\u8c13\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5f00\u521b\u4e86\u4e2d\u56fd\u79d1\u5e7b\u7535\u5f71\u5143\u5e74\u7684\u8bf4\u6cd5\uff0c\u5c31\u53ef\u80fd\u662f\u4e00\u4e2a\u5047\u547d\u9898\u3002\uff08\u7f16\u8f91\uff1aYuki\uff09", "title": "\u4ece\u300a\u6d41\u6d6a\u5730\u7403\u300b\u5230\u300a\u4e0a\u6d77\u5821\u5792\u300b\uff0c\u4e2d\u56fd\u79d1\u5e7b\u7535\u5f71\u5143\u5e74\u771f\u7684\u6765\u4e86\u5417\uff1f", "ukey_author": "nq0zan", "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\">\u672c\u6587\u4e3a2019\u5e749\u670814\u65e5&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u4e03\u671f&mdash;&mdash;\u5584\u826f\u7684\u529b\u91cf&nbsp;| \u5218\u51ef\u594e&nbsp;\u6f14\u8bb2\u5b9e\u5f55\uff1a</span></span></p>\r\n\r\n<p class=\"editors-note\">\u4ee5\u5f80\u7c98\u8d34\u5bfb\u4eba\u542f\u4e8b\u7684\u65b9\u5f0f\uff0c\u4fe1\u606f\u4f20\u9012\u7684\u6548\u7387\u975e\u5e38\u4f4e\uff0c\u800c\u79fb\u52a8\u4e92\u8054\u7f51\u7684\u51fa\u73b0\u8ba9\u7cbe\u51c6\u5ea6\u548c\u65f6\u6548\u6027\u90fd\u5f97\u5230\u4e86\u5f88\u5927\u7684\u63d0\u5347\u3002&ldquo;\u56e2\u5706\u7cfb\u7edf&rdquo;\u7a76\u7adf\u600e\u4e48\u8ba9\u6c11\u4f17\u3001\u8b66\u5bdf\u548c\u4e92\u8054\u7f51\u516c\u53f8\u9ad8\u6548\u534f\u540c\uff0c\u9001\u5931\u8e2a\u513f\u7ae5\u56de\u5bb6\uff1f\u963f\u91cc\u5df4\u5df4\u56e2\u5706\u516c\u76ca\u9879\u76ee\u6280\u672f\u5fd7\u613f\u8005\u3001\u9ad8\u5fb7\u4e8b\u4e1a\u7fa4\u8d44\u6df1\u7b97\u6cd5\u4e13\u5bb6\u5218\u51ef\u594e\u5e26\u6765\u300a\u7528\u4e92\u8054\u7f51\u6280\u672f\uff0c\u5e2e\u5931\u8e2a\u7684\u5b69\u5b50\u4e0e\u5bb6\u4eba\u56e2\u5706\u300b\u3002</p>\r\n\r\n<p>\u5218\u51ef\u594e\u6f14\u8bb2\u89c6\u9891\uff1a</p>\r\n\r\n<p class=\"img-caption\"><a href=\"https://v.qq.com/x/page/w3002qysijh.html\"><img alt=\"\" src=\"https://2-im.guokr.com/dT5iswFQD_VwAY7fSYupgNMyKQsfRzo3jhR3_5VwtqE4BAAA0AIAAEpQ.jpg\" style=\"height:427px; width:640px\" /></a><strong>\u25b2\u70b9\u51fb\u56fe\u7247\u53ef\u89c2\u770b\u5218\u51ef\u594e\u6f14\u8bb2\u89c6\u9891\u25b2</strong></p>\r\n\r\n<p><strong>\u4ee5\u4e0b\u4e3a\u5218\u51ef\u594e\u6f14\u8bb2\u5b9e\u5f55\uff1a</strong></p>\r\n\r\n<p>\u6211\u662f\u5218\u51ef\u594e\uff0c\u6765\u81ea\u963f\u91cc\u5df4\u5df4\u9ad8\u5fb7\u5730\u56fe\u3002</p>\r\n\r\n<p>\u6211\u7684\u4e3b\u8981\u5de5\u4f5c\u662f\u7528\u5927\u6570\u636e\u4eba\u5de5\u667a\u80fd\u7684\u65b9\u6cd5\u6765\u4e3a\u4ea4\u901a\u5bfc\u822a\u8d4b\u80fd\uff0c\u8ba9\u51fa\u884c\u66f4\u7f8e\u597d\u3002\u7b80\u5355\u4e3e\u4e2a\u4f8b\u5b50\uff0c\u540c\u6837\u662f\u4eceA\u70b9\u5230B\u70b9\uff0c\u4e0d\u540c\u7528\u6237\u5bf9\u8def\u7ebf\u7684\u9700\u6c42\u662f\u4e0d\u4e00\u6837\u7684\uff0c\u6709\u4e9b\u60f3\u8d70\u9ad8\u901f\u60f3\u7701\u65f6\u95f4\uff0c\u6709\u4e9b\u4e0d\u60f3\u8d70\u9ad8\u901f\u60f3\u7701\u94b1\u3002\u6211\u4eec\u7684\u5de5\u4f5c\u5c31\u662f\u627e\u5230\u6bcf\u4e2a\u7528\u6237\u7684\u9700\u6c42\uff0c\u7ed9\u4ed6\u89c4\u5212\u51fa\u4ed6\u559c\u6b22\u7684\u8def\u7ebf\u3002</p>\r\n\r\n<p>\u4eca\u5929\u6211\u4e0d\u60f3\u4ecb\u7ecd\u6211\u7684\u4e3b\u8981\u5de5\u4f5c\uff0c\u800c\u662f\u60f3\u804a\u4e00\u804a\u6211\u5728\u963f\u91cc\u53c2\u4e0e\u7684&ldquo;\u56e2\u5706&rdquo;\u516c\u76ca\u9879\u76ee\u3002\u6211\u4eec\u505a\u8fd9\u4e2a\u9879\u76ee\uff0c\u662f\u60f3<strong>\u5e2e\u52a9\u5bfb\u627e\u66f4\u591a\u7684\u5931\u8e2a\u513f\u7ae5\uff0c\u80fd\u591f\u8ba9\u4ed6\u4eec\u8ddf\u5bb6\u4eba\u56e2\u5706</strong>\u3002</p>\r\n\r\n<p>\u9a6c\u4e91\u8001\u5e08\u7ecf\u5e38\u8ddf\u6211\u4eec\u8bf4\uff0c\u5927\u516c\u53f8\u7684&ldquo;\u5927&rdquo;\u5e76\u4e0d\u662f\u6307\u6536\u5165\u5927\u3001\u5229\u6da6\u5927\u3001\u5e02\u573a\u4efd\u989d\u5927\uff0c\u800c\u662f\u5728\u4e8e\u5b83\u7684\u8d23\u4efb\u5927\u3001\u62c5\u5f53\u5927\u3002\u4eba\u4eec\u5e0c\u671b\u4f60\u80fd\u62c5\u5f53\u66f4\u5927\u7684\u8d23\u4efb\uff0c\u4e3a\u793e\u4f1a\u505a\u51fa\u66f4\u5927\u7684\u8d21\u732e\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/NTv_xLAWsRpCofDVI5B98pIHcuomrbQWBpQRr9sOtkE4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>2015\u5e749\u670810\u65e5\uff0c\u9a6c\u4e91\u8001\u5e08\u63d0\u51fa\u4e86\u4e00\u4e2a\u60f3\u6cd5\uff1a\u5e0c\u671b\u963f\u91cc\u6bcf\u4eba\u6bcf\u5e74\u81f3\u5c11\u505a\u4e09\u5c0f\u65f6\u516c\u76ca\u3002\u4e8e\u662f\u5f88\u591a\u540c\u4e8b\u5c31\u5728\u5927\u8857\u4e0a\u6216\u8005\u516c\u56ed\u91cc\u6361\u5783\u573e\uff0c\u4e5f\u6709\u4e00\u4e9b\u53bb\u656c\u8001\u9662\u505a\u516c\u76ca\u6d3b\u52a8\u3002\u800c\u6211\u4eec\u60f3\uff0c\u662f\u4e0d\u662f\u80fd<strong>\u901a\u8fc7\u6280\u672f\u7684\u624b\u6bb5\uff0c\u6765\u4e3a\u793e\u4f1a\u505a\u4e00\u4e9b\u516c\u76ca\u7684\u4e8b\u60c5</strong>\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/BeJ3NRGKKFhNmZ62nT8pDfvmU16zB0oSlEmGQCfmm-84BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4e4b\u524d\u770b\u8fc7\u4e00\u90e8\u7535\u5f71\u53eb\u300a\u7206\u88c2\u65e0\u58f0\u300b\uff0c\u8bb2\u8ff0\u7684\u662f\u4e00\u4e2a\u65f7\u5de5\u7236\u4eb2\u5f20\u4fdd\u6c11\u5728\u627e\u5bfb\u81ea\u5df1\u4e22\u5931\u5b69\u5b50\u7684\u8fc7\u7a0b\u4e2d\u53d1\u751f\u7684\u4e00\u4e9b\u6545\u4e8b\u3002\u4ed6\u5728\u5927\u8857\u5c0f\u5df7\u91cc\u5f20\u8d34\u5bfb\u4eba\u542f\u4e8b\uff0c\u5e0c\u671b\u6709\u66f4\u591a\u4eba\u770b\u5230\u3002</p>\r\n\r\n<p>\u6211\u5f53\u65f6\u5c31\u60f3\uff0c\u8fd9\u6837\u4f20\u9012\u4fe1\u606f\u7684\u6548\u7387\u592a\u4f4e\u4e86\uff0c\u8986\u76d6\u9762\u4e5f\u975e\u5e38\u5c0f\uff0c\u53ea\u6709\u770b\u5230\u5bfb\u4eba\u542f\u4e8b\u7684\u4eba\u624d\u77e5\u9053&mdash;&mdash;\u5982\u679c\u6ca1\u6709\u770b\u5230\u5bfb\u4eba\u542f\u4e8b\uff0c\u54ea\u6015\u9644\u8fd1\u7684\u4eba\u770b\u5230\u4e86\u8fd9\u4e2a\u5c0f\u5b69\uff0c\u4e5f\u4e0d\u4f1a\u77e5\u9053\u4ed6\u662f\u8d70\u5931\u7684\u3002</p>\r\n\r\n<p>\u800c\u79fb\u52a8\u4e92\u8054\u7f51\u6280\u672f\u7684\u51fa\u73b0\uff0c\u53ef\u4ee5\u8ba9\u4fe1\u606f\u4f20\u9012\u7684\u6548\u7387\u63d0\u9ad8\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/AERR_DCGriHIiQloqea5gi2cPq41U5GAJ2qaXyINeps4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6b63\u597d\uff0c\u516c\u5b89\u90e8\u4e5f\u6709\u5229\u7528\u4e92\u8054\u7f51\u6280\u672f\u63d0\u5347\u6253\u62d0\u6548\u7387\u7684\u9700\u6c42\u3002\u56e0\u6b64\uff0c\u5728\u674e\u514b\u5f3a\u603b\u7406\u63d0\u51fa&ldquo;\u4e92\u8054\u7f51+&rdquo;\u7684\u6307\u5bfc\u65b9\u9488\u4e0b\uff0c\u963f\u91cc\u8ddf\u516c\u5b89\u90e8\u8054\u5408\u542f\u52a8\u4e86&ldquo;\u56e2\u5706&rdquo;\u9879\u76ee\uff0c\u521d\u8877\u662f&ldquo;\u56e2\u805a\u8b66\u6c11\u5fc3\uff0c\u5706\u5b50\u56de\u5bb6\u68a6&rdquo;\uff0c\u76ee\u6807\u662f&ldquo;\u5168\u6c11\u53c2\u4e0e\uff0c\u5929\u4e0b\u65e0\u62d0&rdquo;\u3002</p>\r\n\r\n<p><a href=\"https://v.qq.com/x/page/p3007emqhbd.html\"><img alt=\"\" src=\"https://2-im.guokr.com/bCyikG49t0g600rYZunRXJm-daNUdlHWBhCqpxuu9DB0AwAAYQEAAFBO.png\" style=\"height:256px; width:640px\" /></a></p>\r\n\r\n<p>\u56e2\u5706\u7cfb\u7edf\uff0c\u600e\u4e48\u505a\uff1f\u80cc\u540e\u53c8\u6709\u54ea\u4e9b\u6280\u672f\u652f\u6491\uff1f</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/snmp-Ng0AGh_f8b2VbzWxceFznqOBxPJ6DgzWpMvvtU4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4ece\u5927\u65b9\u5411\u6765\u8bf4\uff0c\u8981\u4f9d\u8d56\u4e8e\u79fb\u52a8\u4e92\u8054\u7f51\u3001\u5927\u6570\u636e\u548c\u4eba\u5de5\u667a\u80fd\u6280\u672f\uff1b</p>\r\n\r\n<p>\u4ece\u6838\u5fc3\u5b9e\u73b0\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u8981\u5305\u62ec\u6743\u5a01\u53d1\u5e03\u3001\u7cbe\u51c6\u5b9a\u4f4d\u548c\u9ad8\u6548\u534f\u540c\u8fd9\u4e09\u4e2a\u8fc7\u7a0b\u3002</p>\r\n\r\n<h2><strong>No.1 \u6743\u5a01\u53d1\u5e03</strong></h2>\r\n\r\n<p>\u513f\u7ae5\u5931\u8e2a\u7684\u4fe1\u606f\uff0c\u8c01\u6765\u53d1\u5e03\uff1f\u53c8\u662f\u600e\u4e48\u53d1\u5e03\u7684\uff1f</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/YKSeF0BePvXO8-LRkW5WVmeDWtIBXP-S2jdH2wKw3Qc4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4eec\u5e0c\u671b\u7528\u6237\u5bf9\u56e2\u5706\u7cfb\u7edf\u4ea7\u751f\u4fe1\u4efb\uff0c\u4ece\u800c\u5b9e\u73b0\u5168\u6c11\u6301\u7eed\u53c2\u4e0e\u3002\u56e0\u6b64\uff0c<strong>\u7cfb\u7edf\u53d1\u5e03\u7684\u4fe1\u606f\u8981\u767e\u5206\u4e4b\u767e\u51c6\u786e\uff0c\u53ea\u5141\u8bb8\u6253\u62d0\u8b66\u5bdf\u6709\u53d1\u5e03\u8d70\u5931\u6d88\u606f\u7684\u6743\u9650</strong>\u3002\u5168\u56fd\u67096000\u591a\u540d\u6253\u62d0\u8b66\u5bdf\uff0c\u4ed6\u4eec\u6536\u5230\u62a5\u8b66\u4fe1\u606f\u540e\uff0c\u4f1a\u4ece\u81ea\u5df1\u4e13\u4e1a\u7684\u89d2\u5ea6\u5224\u65ad\u4fe1\u606f\u662f\u5426\u662f\u51c6\u786e\u3001\u5168\u9762\u3002\u7ecf\u8fc7\u5ba1\u6838\u4e4b\u540e\uff0c\u8b66\u5bdf\u4f1a\u5728\u9489\u9489\u5f00\u53d1\u7684\u56e2\u5706\u7cfb\u7edf\u4e0a\u53d1\u5e03\u51c6\u786e\u7684\u6d88\u606f\uff08\u5305\u62ec\u513f\u7ae5\u7684\u5e74\u9f84\u3001\u6027\u522b\u3001\u7167\u7247\uff0c\u8863\u7740\u5916\u8c8c\u7279\u5f81\uff0c\u4ee5\u53ca\u4e22\u5931\u65f6\u95f4\u548c\u4f4d\u7f6e\uff09\u3002</p>\r\n\r\n<h2><strong>No.2 \u7cbe\u51c6\u5b9a\u4f4d</strong></h2>\r\n\r\n<p>\u8b66\u5bdf\u53d1\u5e03\u4e86\u4e22\u5931\u513f\u7ae5\u4fe1\u606f\u540e\uff0c\u63a8\u9001\u7ed9\u8c01\uff1f\u5f80\u54ea\u91cc\u63a8\u9001\uff1f</p>\r\n\r\n<p>\u7406\u60f3\u6765\u8bf4\uff0c\u6211\u4eec\u5e94\u8be5\u628a\u4fe1\u606f\u63a8\u9001\u5230\u5c0f\u5b69\u4e22\u5931\u540e\u6240\u6709\u89c1\u8fc7\u4ed6\u7684\u4eba\u3002\u5728\u7535\u5f71\u300a\u7206\u88c2\u65e0\u58f0\u300b\u91cc\uff0c\u4e22\u5931\u5b69\u5b50\u7684\u5bb6\u957f\u5f20\u4fdd\u6c11\u5230\u5904\u5f20\u8d34\u5bfb\u4eba\u542f\u4e8b\uff0c\u5c31\u662f\u5e0c\u671b\u66f4\u591a\u7684\u4eba\u80fd\u591f\u770b\u5230\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/yAKecBCODoXln9wf1Z3beo31pgip3jG9fgYMPhowxes4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8981\u5b9e\u73b0\u4e0a\u8ff0\u7684\u7406\u60f3\u72b6\u6001\uff0c\u5c31\u8981\u4f9d\u8d56\u7cbe\u51c6\u5b9a\u4f4d\u7684\u6280\u672f\u3002</p>\r\n\r\n<p>\u6211\u4eec\u6839\u636e\u8b66\u5bdf\u5728\u56e2\u5706\u5e73\u53f0\u4e0a\u9762\u53d1\u5e03\u7684\u4fe1\u606f\uff0c\u4ee5\u513f\u7ae5\u4e22\u5931\u7684\u4f4d\u7f6e\u4e3a\u4e2d\u5fc3\u753b\u4e00\u4e2a\u534a\u5f84\u4e00\u767e\u516c\u91cc\u7684\u5706&mdash;&mdash;\u53ef\u4ee5\u7406\u89e3\u4e3a\u4e00\u4e2a\u865a\u62df\u7684\u7535\u5b50\u56f4\u680f\uff0c\u6211\u4eec\u4f1a\u7ed9\u56f4\u680f\u91cc\u6240\u6709\u7684\u4eba\u63a8\u9001\u4fe1\u606f\u3002</p>\r\n\r\n<p>\u73b0\u5728\u79fb\u52a8\u4e92\u8054\u7f51\u975e\u5e38\u666e\u53ca\uff0c\u5927\u5bb6\u90fd\u5728\u4f7f\u7528\u624b\u673a\uff0c\u6240\u4ee5\u5229\u7528\u624b\u673a\u63a8\u9001\u89e6\u8fbe\u76f8\u5e94\u4eba\u7fa4\u662f\u6700\u9ad8\u6548\u7684\u4e00\u79cd\u65b9\u5f0f\u3002\u6211\u4eec\u6700\u5148\u60f3\u5230\u7684\u662f\u5f00\u53d1\u4e00\u4e2aAPP\uff0c\u4f46\u5b83\u53ea\u80fd\u5728\u6253\u62d0\u548c\u5bfb\u4eba\u65b9\u9762\u5e94\u7528\uff0c\u8fd0\u8425\u548c\u63a8\u5e7f\u975e\u5e38\u56f0\u96be\u3002\u4e8e\u662f\uff0c\u6211\u4eec\u5c31\u60f3\u5230\u8ddf\u70ed\u95e8APP\u5408\u4f5c\u3002</p>\r\n\r\n<p>\u56e2\u5706\u7cfb\u7edf\u7b2c\u4e00\u6279\u5408\u4f5c\u7684APP\u5305\u62ec\u9ad8\u5fb7\u3001\u9489\u9489\u548c\u65b0\u6d6a\u5fae\u535a\u3002</p>\r\n\r\n<p>\u4ee5\u9ad8\u5fb7\u4e3a\u4f8b\uff1a<strong>\u8b66\u5bdf\u53d1\u5e03\u4e86\u513f\u7ae5\u4e22\u5931\u7684\u4fe1\u606f\u540e\uff0c\u9ad8\u5fb7\u4f1a\u4ece\u56e2\u5706\u7cfb\u7edf\u4e0a\u5b9e\u65f6\u83b7\u53d6\uff0c\u7136\u540e\u4ee5\u4e22\u5931\u5730\u4e3a\u539f\u70b9\u8fdb\u884c\u6d88\u606f\u63a8\u9001</strong>\u3002\u4e3a\u4e86\u66f4\u7cbe\u51c6\u5730\u63a8\u9001\u6d88\u606f\uff0c\u9ad8\u5fb7\u4f9d\u636e\u72af\u7f6a\u5206\u5b50\u53ef\u80fd\u7684\u9003\u8dd1\u901f\u5ea6\uff0c\u6309\u71671\u5c0f\u65f6100\u516c\u91cc\uff0c2\u5c0f\u65f6200\u516c\u91cc\uff0c3\u5c0f\u65f6300\u516c\u91cc\uff0c\u5927\u4e8e3\u5c0f\u65f6500\u516c\u91cc\u7684\u6807\u51c6\u6846\u5b9a\u5730\u7406\u8303\u56f4\u3002</p>\r\n\r\n<p>\u5176\u4e2d\u6838\u5fc3\u4f9d\u8d56\u7684\u662f\u9ad8\u5fb7\u7684<strong>\u7cbe\u786e\u5b9a\u4f4d\u6280\u672f</strong>\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/vkbUpwcL1H1W9DcpIdOeQhDShR9tsmq0i4enchn-NFc4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u9ad8\u5fb7\u7684\u5b9a\u4f4d\u6280\u672f\u6db5\u76d6GPS\u3001wifi\u3001\u57fa\u7ad9\u3001\u84dd\u7259\u8bbe\u5907\u7b49\u7b49\uff0c\u56e0\u6b64\u5373\u4fbf\u5728\u6ca1\u6709GPS\u4fe1\u53f7\u7684\u5730\u65b9\uff08\u6bd4\u5982\u5ba4\u5185\u3001\u9ad8\u94c1\u4e0a\u3001\u96a7\u9053\u5185\uff09\uff0c\u4e5f\u80fd\u591f\u5b9e\u73b0\u51c6\u786e\u5b9e\u65f6\u5b9a\u4f4d\u3002</p>\r\n\r\n<p class=\"editors-note\">\u5927\u81f4\u539f\u7406\uff1awifi\u3001\u57fa\u7ad9\u548c\u84dd\u7259\u8bbe\u5907\u90fd\u6709\u552f\u4e00\u7684\u6807\u8bc6\u7801\uff08\u88ab\u79f0\u4e3a\u6307\u7eb9\u6570\u636e\uff09\u3002\u501f\u52a9\u9ad8\u5fb7\u6d77\u91cf\u7684\u5b9a\u4f4d\u6570\u636e\u548c\u963f\u91cc\u4e91\u5f3a\u5927\u7684\u8ba1\u7b97\u5e73\u53f0\uff0c\u6211\u4eec\u518d\u7528\u4eba\u5de5\u667a\u80fd\u7684\u65b9\u6cd5\u5bf9\u5404\u79cd\u6307\u7eb9\u6570\u636e\u8fdb\u884c\u6df1\u5165\u6316\u6398\uff0c\u5efa\u7acb\u4e86\u8986\u76d6\u5168\u7403\u7684\u6307\u7eb9\u6570\u636e\u5e93\u3002\u8fd9\u6837\uff0c\u5c31\u53ef\u4ee5\u5bf9\u7528\u6237\u8fdb\u884c\u5b9e\u65f6\u5b9a\u4f4d\u3002</p>\r\n\r\n<h2><strong>No.3</strong><em><strong>&nbsp;</strong></em><strong>\u9ad8\u6548\u534f\u540c</strong></h2>\r\n\r\n<p>\u8b66\u5bdf\u548c\u6c11\u4f17\u7684\u534f\u540c\u3002\u7528\u6237\u63a5\u6536\u5230\u4e22\u5931\u513f\u7ae5\u7684\u4fe1\u606f\u540e\uff0c\u5982\u679c\u6070\u597d\u770b\u89c1\u4eba\u8d29\u5b50\u5e26\u7740\u8fd9\u4e2a\u5c0f\u5b69\u7ecf\u8fc7\uff0c\u5c31\u4f1a\u62a5\u8b66\uff0c\u8b66\u5bdf\u5c31\u53ef\u4ee5\u7ee7\u7eed\u8ffd\u8e2a\uff0c\u4ece\u800c\u89e3\u6551\u513f\u7ae5\u3002</p>\r\n\r\n<p>\u8b66\u5bdf\u548c\u8b66\u5bdf\u7684\u534f\u540c\u3002\u5982\u679c\u6709\u4e00\u4e2a\u5c0f\u5b69\u5728\u5317\u4eac\u62a5\u6848\u8d70\u5931\uff0c\u540e\u6765\u5929\u6d25\u6709\u4eba\u63d0\u4f9b\u4e86\u7ebf\u7d22\uff0c\u5317\u4eac\u548c\u5929\u6d25\u7684\u8b66\u5bdf\u5c31\u53ef\u4ee5\u901a\u8fc7\u56e2\u5706\u7cfb\u7edf\u9ad8\u6548\u6c9f\u901a\u548c\u540c\u6b65\u4fe1\u606f\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/ER6wu4SCeUWHKEzc_b4oE3yHRZWFHcOY0PeL9onlLQ04BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4e92\u8054\u7f51\u516c\u53f8\u4e4b\u95f4\u7684\u534f\u540c\u30022016\u5e74\u4e0a\u7ebf\u521d\u671f\uff0c\u53ea\u6709\u4e09\u5bb6\u516c\u53f8\u53c2\u4e0e\u8fdb\u6765\uff1b\u800c\u76ee\u524d\uff0c\u56fd\u5185\u5df2\u7ecf\u6709\u6570\u5341\u5bb6\u516c\u53f8\u768425\u6b3e\u8d85\u7ea7APP\u63a5\u5165\uff0c\u5305\u62ec\u9ad8\u5fb7\u3001\u652f\u4ed8\u5b9d\u3001\u6dd8\u5b9d\u3001\u817e\u8baf\u3001\u767e\u5ea6\u3001\u4eca\u65e5\u5934\u6761\u3001UC\u5934\u6761\u7b49\u7b49\u3002\u8fd9\u662f<strong>\u9996\u6b21\u5728\u516c\u76ca\u9886\u57df\u7a81\u7834\u4e86\u5546\u4e1a\u7ade\u4e89\u7684\u5c40\u9762\uff0c\u5927\u5bb6\u4e3a\u4e86\u5171\u540c\u7684\u76ee\u6807\u53bb\u8d21\u732e\u81ea\u5df1\u7684\u4e00\u4efd\u529b\u91cf</strong>\u3002</p>\r\n\r\n<p>\u6211\u4eec\u8bbe\u8ba1\u4e86\u56e2\u5706\u7cfb\u7edf\uff0c\u4f46\u662f\u5b83\u80fd\u4e0d\u80fd\u7b26\u5408\u6211\u4eec\u7684\u9884\u671f\u8d77\u5230\u4f5c\u7528\u5462\uff1f</p>\r\n\r\n<p>\u5148\u542c\u4e00\u4e2a\u5c0f\u6545\u4e8b\uff0c\u53d1\u751f\u5728\u6cb3\u5317\u8861\u6c34&mdash;&mdash;</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/Ui7gA-fpauTh359IkYXhsyn64YN-4zA6vpuOGUHfrWk4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u56e2\u5706\u7cfb\u7edf2016\u5e745\u670815\u65e5\u4e0a\u7ebf\u524d\u5915\uff0c\u56e2\u5706\u7cfb\u7edf\u7ba1\u7406\u5458\u8ba9\u5927\u5bb6\u628a\u4e4b\u524d\u7684\u6d4b\u8bd5\u6570\u636e\u90fd\u5220\u6389\u3002\u4f46\u662f\u540e\u6765\u4e0a\u7ebf\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u8fd8\u662f\u6709\u4e00\u6761\u63a8\u9001\u51fa\u53bb\u4e86&mdash;&mdash;\u4e0d\u8fc7\u53d1\u5e03\u7684\u8b66\u5bdf\u8bf4\u5176\u5b9e\u4e0d\u662f\u6f0f\u5220\uff0c\u800c\u662f\u521a\u521a\u62a5\u6848\u7684\u6848\u4ef6\u3002\u521a\u4e0a\u7ebf\uff0c\u5927\u5bb6\u5fc3\u91cc\u90fd\u5f88\u6ca1\u6709\u5e95\uff0c\u4f46\u662f\u4e0d\u4e45\u5c31\u6709\u4e00\u4e2a\u51fa\u79df\u8f66\u53f8\u673a\u5728\u8861\u6c34\u706b\u8f66\u7ad9\uff08\u53ef\u80fd\u662f\u62c9\u5c0f\u5b69\u5230\u706b\u8f66\u7ad9\u7684\u53f8\u673a\uff09\u63d0\u4f9b\u4e86\u4e00\u6761\u91cd\u8981\u7684\u7ebf\u7d22\u3002\u8b66\u65b9\u5c31\u636e\u6b64\u5728\u516c\u5b89\u90e8\u6253\u62d0\u529e\u7684\u534f\u8c03\u6307\u6325\u4e0b\uff0c\u5728\u6cb3\u5357\u90d1\u5dde\u627e\u5230\u4e86\u72af\u7f6a\u5acc\u7591\u4eba\uff0c\u5b89\u5168\u89e3\u6551\u51fa\u4e86\u8fd9\u4e2a\u5c0f\u5973\u5b69\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/A8PcMlX2HN5ilPtJIHAHgsehTgDCJCebhPxi_kxAX2k4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u56e2\u5706\u7cfb\u7edf\u5728\u521a\u4e0a\u7ebf\u4e00\u4e2a\u6708\u7684\u65f6\u5019\uff0c\u513f\u7ae5\u7684\u627e\u56de\u7387\u5c31\u5df2\u7ecf\u8fbe\u5230\u4e8689%\uff1b\u4e0a\u7ebf\u534a\u5e74\u7684\u65f6\u5019\uff0c\u627e\u56de\u7387\u8fbe\u523090.91%\u3002\u6839\u636e\u6700\u65b0\u7684\u6570\u636e\uff0c\u622a\u6b62\u4eca\u5e749\u67085\u53f7\uff0c\u6211\u4eec\u4e00\u5171\u53d1\u5e03\u4e864191\u6761\u4e22\u5931\u513f\u7ae5\u7684\u4fe1\u606f\uff0c\u627e\u56de\u4e864102\u4eba\uff0c<strong>\u627e\u56de\u7387\u9ad8\u8fbe98%</strong>\u3002</p>\r\n\r\n<p>\u56e2\u5706\u7cfb\u7edf\u7684\u6210\u529f\uff0c\u79bb\u4e0d\u5f00\u963f\u91cc\u6280\u672f\u5fd7\u613f\u8005\u6301\u7eed\u4e0d\u65ad\u7684\u6295\u5165\u3002</p>\r\n\r\n<p>\u5176\u5b9e2016\u5e745\u670815\u65e5\u56e2\u5706\u7cfb\u7edf\u4e0a\u7ebf\u540e\uff0c\u6211\u4eec\u7684\u5de5\u4f5c\u5c31\u5df2\u7ecf\u5b8c\u6210\u4e86\uff0c\u540e\u9762\u662f\u516c\u5b89\u90e8\u7684\u5de5\u4f5c\uff0c\u4ed6\u4eec\u6765\u6301\u7eed\u8fd0\u8425\u8fd9\u5957\u7cfb\u7edf\u3002\u4f46\u4e8b\u5b9e\u5e76\u975e\u5982\u6b64\uff0c\u4ece2016\u5e74\u81f3\u4eca\uff0c\u56e2\u5706\u7cfb\u7edf\u7ecf\u5386\u4e865\u4e2a\u7248\u672c\u7684\u8fed\u4ee3\uff0c\u8fd9\u4e9b\u90fd\u662f\u5fd7\u613f\u8005\u5229\u7528\u4e1a\u4f59\u65f6\u95f4\u5b8c\u6210\u7684\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/EjAeKzU8PL1lcWCMG2Qj_iX9NO1IBpDc0_QeXLG1akA4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>1.0\u7248\u672c\uff0c\u53ea\u6709\u53d1\u5e03\u548c\u63a8\u9001\u7684\u529f\u80fd\u3002</p>\r\n\r\n<p>2.0\u7248\u672c\uff0c\u589e\u52a0\u4e86\u627e\u56de\u513f\u7ae5\u7684\u53cd\u9988\u4fe1\u606f\uff0c\u80fd\u8ba9\u63d0\u4f9b\u7ebf\u7d22\u7684\u7fa4\u4f17\u77e5\u9053\u4ed6\u63d0\u4f9b\u7684\u7ebf\u7d22\u786e\u5b9e\u6709\u4ef7\u503c\u3002</p>\r\n\r\n<p>3.0\u7248\u672c\uff0c\u52a0\u5165\u4e86\u5927\u6570\u636e\u81ea\u52a8\u7edf\u8ba1\u62a5\u8868\u7684\u529f\u80fd\u3002</p>\r\n\r\n<p>4.0\u7248\u672c\uff08\u56fd\u9645\u677f\uff09\uff0c\u5728\u8054\u5408\u56fd\u63a8\u5e7f\u56e2\u5706\u79d1\u6280\u6253\u62d0\u7684\u4e2d\u56fd\u7ecf\u9a8c\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/V-GP83JPQuJneSziTaI4feyzcV69eH7rqyF69e0nLSA4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>5.0\u7248\u672c\u6b63\u5728\u7070\u5ea6\u8fed\u4ee3\u7684\u8fc7\u7a0b\u4e2d\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/ogD8n0HNQDMvisqigdE7sfqmKrTQrNap27OHm7-88eo4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u5927\u5bb6\u53ef\u80fd\u4f1a\u89c9\u5f97\uff0c\u5931\u8e2a\u513f\u7ae5\u7684\u6700\u5927\u539f\u56e0\u662f\u88ab\u62d0\u5356\u3002\u5b9e\u9645\u4e0a\uff0c\u4ece\u6211\u4eec\u8fd9\u4e9b\u5e74\u7edf\u8ba1\u7684\u6570\u636e\u6765\u770b\uff0c<strong>\u6700\u5927\u7684\u539f\u56e0\uff0860.2%\uff09\u662f\u79bb\u5bb6\u51fa\u8d70</strong>\u3002\u6240\u4ee5\u6211\u4eec\u4e5f\u5e0c\u671b\u7ed9\u5bb6\u957f\u63d0\u9192\u4e00\u4e0b\uff0c<strong>\u8981\u5b66\u4f1a\u66f4\u597d\u5730\u8ddf\u513f\u7ae5\u6c9f\u901a</strong>\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/rkWno2SoRV2mAVb4Fv6XkSz1Tngr6mcNjWyU-00aYKM4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>2017\u5e749\u6708\uff0c\u9ad8\u5fb7\u5730\u56fe\u8ddf\u516c\u5b89\u90e8\u6253\u62d0DNA\u5e93\u5408\u4f5c\uff0c\u4e0a\u7ebf\u4e86\u4e00\u4e2a\u5bfb\u4eb2\u5bfc\u822a\u7684\u529f\u80fd\uff0c\u5e2e\u52a9\u5931\u6563\u591a\u5e74\u7684\u7236\u6bcd\u548c\u5b69\u5b50\u56e2\u5706\u3002\u5982\u679c\u4f60\u6000\u7591\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u53ef\u4ee5\u5728\u9ad8\u5fb7\u5730\u56fe\u4e0a\u641c\u7d22&ldquo;\u5bfb\u4eb2&rdquo;\uff0c\u9ad8\u5fb7\u53ef\u4ee5\u5e2e\u4f60\u627e\u5230\u9644\u8fd1\u7684\u516c\u5b89\u673a\u5173\u514d\u8d39\u91c7\u8840\uff0c\u7136\u540e\u4e0a\u4f20\u5230\u516c\u5b89\u90e8\u6253\u62d0DNA\u5e93\u6bd4\u5bf9\uff0c<strong>\u8fd9\u4e2aDNA\u5e93\u73b0\u5728\u5df2\u7ecf\u5e2e\u52a96000\u591a\u4e2a\u5bb6\u5ead\u5b9e\u73b0\u4e86\u56e2\u5706\u68a6</strong>\u3002</p>\r\n\r\n<p>\u9664\u4e86\u56e2\u5706\u7cfb\u7edf\uff0c\u9ad8\u5fb7\u5176\u5b9e\u8fd8\u505a\u4e86&ldquo;\u7eff\u8272\u901a\u9053&rdquo;\u7684\u516c\u76ca\u9879\u76ee\u3002\u53bb\u5e74\u6709\u4e00\u4e2a\u4e2d\u5b66\u751f\uff08\u5c0f\u5b87\u6cfd\uff09\u56e0\u4e3a\u610f\u5916\u8111\u90e8\u53d7\u4f24\uff0c\u4ed6\u539f\u672c\u5728\u5185\u8499\u53e4\u5c31\u533b\uff0c\u4f46\u540e\u6765\u75c5\u60c5\u7a81\u7136\u6025\u5267\u6076\u5316\uff0c\u9700\u8981\u8f6c\u9662\u5230\u5317\u4eac\u3002\u9ad8\u5fb7\u77e5\u9053\u540e\uff0c\u7b2c\u4e00\u65f6\u95f4\u5c31\u542f\u52a8\u4e86\u7eff\u8272\u901a\u9053\u529f\u80fd\uff0c\u5728\u4ed6\u8f6c\u9662\u7684\u8fc7\u7a0b\u4e2d\u4e00\u8def\u7ed9\u6cbf\u9014\u4f7f\u7528\u9ad8\u5fb7\u5bfc\u822a\u7684\u53f8\u673a\u63a8\u9001\u907f\u8ba9\u6d88\u606f\uff0c\u4f7f\u5f97\u539f\u672c7\u4e2a\u591a\u5c0f\u65f6\u3001520\u516c\u91cc\u7684\u8def\u7a0b\uff0c\u6700\u540e\u53ea\u7528\u4e865\u4e2a\u5c0f\u65f620\u5206\u949f\u5c31\u8d70\u5b8c\u4e86\uff0c\u4e3a\u75c5\u60c5\u7684\u6551\u6cbb\u5927\u5927\u8282\u7701\u4e86\u65f6\u95f4\u3002</p>\r\n\r\n<p>\u524d\u4e0d\u4e45\uff0c\u9a6c\u4e91\u8001\u5e08\u5728\u963f\u91cc20\u5468\u5e74\u751f\u65e5\u7684\u5e74\u4f1a\u4e0a\u8bf4\uff0c\u8fc7\u53bb\u768420\u5e74\u963f\u91cc\u62e5\u6709\u4e86\u6700\u597d\u7684\u4eba\u624d\u3001\u6700\u597d\u7684\u6280\u672f\u548c\u6700\u591a\u7684\u8d44\u6e90\uff0c\u8fd9\u5176\u5b9e\u4ee3\u8868\u4e86\u793e\u4f1a\u5bf9\u963f\u91cc\u7684\u4fe1\u4efb\u3002<strong>\u6211\u4eec\u5e94\u8be5\u7528\u4e00\u4e9b\u5b9e\u9645\u7684\u884c\u52a8\u4e3a\u793e\u4f1a\u505a\u4e00\u4e9b\u6709\u4ef7\u503c\u3001\u6709\u610f\u4e49\u7684\u4e8b\u60c5</strong>\uff0c\u8c22\u8c22\u5927\u5bb6\u3002\uff08\u7f16\u8f91\uff1a\u9ea6\u82bd\u6768\u3001\u51dd\u97f3\uff09</p>", "date_created": "2019-10-21T10:10:11.297711+08:00", "date_modified": "2019-10-21T10:28:36.744755+08:00", "date_published": "2019-10-21T10:28:38+08:00", "id": 454891, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": null, "replies_count": 5, "small_image": "https://2-im.guokr.com/YqezynxCji_8ng6E0-iYTIctSMF0634sOVIiOIqW8qg4BAAA0AIAAEpQ.jpg", "subject": {"articles_count": 423, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "society", "minisite_key": "None", "name": "\u793e\u4f1a", "sort_score": 21, "subject_type": "normal"}, "subject_key": "society", "summary": "\u7528\u4e92\u8054\u7f51\u6280\u672f\uff0c\u5e2e\u5931\u8e2a\u7684\u5b69\u5b50\u4e0e\u5bb6\u4eba\u56e2\u5706\u3002", "text": "\u672c\u6587\u4e3a2019\u5e749\u670814\u65e5\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u4e03\u671f\u2014\u2014\u5584\u826f\u7684\u529b\u91cf\u00a0| \u5218\u51ef\u594e\u00a0\u6f14\u8bb2\u5b9e\u5f55\uff1a\u4ee5\u5f80\u7c98\u8d34\u5bfb\u4eba\u542f\u4e8b\u7684\u65b9\u5f0f\uff0c\u4fe1\u606f\u4f20\u9012\u7684\u6548\u7387\u975e\u5e38\u4f4e\uff0c\u800c\u79fb\u52a8\u4e92\u8054\u7f51\u7684\u51fa\u73b0\u8ba9\u7cbe\u51c6\u5ea6\u548c\u65f6\u6548\u6027\u90fd\u5f97\u5230\u4e86\u5f88\u5927\u7684\u63d0\u5347\u3002\u201c\u56e2\u5706\u7cfb\u7edf\u201d\u7a76\u7adf\u600e\u4e48\u8ba9\u6c11\u4f17\u3001\u8b66\u5bdf\u548c\u4e92\u8054\u7f51\u516c\u53f8\u9ad8\u6548\u534f\u540c\uff0c\u9001\u5931\u8e2a\u513f\u7ae5\u56de\u5bb6\uff1f\u963f\u91cc\u5df4\u5df4\u56e2\u5706\u516c\u76ca\u9879\u76ee\u6280\u672f\u5fd7\u613f\u8005\u3001\u9ad8\u5fb7\u4e8b\u4e1a\u7fa4\u8d44\u6df1\u7b97\u6cd5\u4e13\u5bb6\u5218\u51ef\u594e\u5e26\u6765\u300a\u7528\u4e92\u8054\u7f51\u6280\u672f\uff0c\u5e2e\u5931\u8e2a\u7684\u5b69\u5b50\u4e0e\u5bb6\u4eba\u56e2\u5706\u300b\u3002\u5218\u51ef\u594e\u6f14\u8bb2\u89c6\u9891\uff1a\u25b2\u70b9\u51fb\u56fe\u7247\u53ef\u89c2\u770b\u5218\u51ef\u594e\u6f14\u8bb2\u89c6\u9891\u25b2\u4ee5\u4e0b\u4e3a\u5218\u51ef\u594e\u6f14\u8bb2\u5b9e\u5f55\uff1a\u6211\u662f\u5218\u51ef\u594e\uff0c\u6765\u81ea\u963f\u91cc\u5df4\u5df4\u9ad8\u5fb7\u5730\u56fe\u3002\u6211\u7684\u4e3b\u8981\u5de5\u4f5c\u662f\u7528\u5927\u6570\u636e\u4eba\u5de5\u667a\u80fd\u7684\u65b9\u6cd5\u6765\u4e3a\u4ea4\u901a\u5bfc\u822a\u8d4b\u80fd\uff0c\u8ba9\u51fa\u884c\u66f4\u7f8e\u597d\u3002\u7b80\u5355\u4e3e\u4e2a\u4f8b\u5b50\uff0c\u540c\u6837\u662f\u4eceA\u70b9\u5230B\u70b9\uff0c\u4e0d\u540c\u7528\u6237\u5bf9\u8def\u7ebf\u7684\u9700\u6c42\u662f\u4e0d\u4e00\u6837\u7684\uff0c\u6709\u4e9b\u60f3\u8d70\u9ad8\u901f\u60f3\u7701\u65f6\u95f4\uff0c\u6709\u4e9b\u4e0d\u60f3\u8d70\u9ad8\u901f\u60f3\u7701\u94b1\u3002\u6211\u4eec\u7684\u5de5\u4f5c\u5c31\u662f\u627e\u5230\u6bcf\u4e2a\u7528\u6237\u7684\u9700\u6c42\uff0c\u7ed9\u4ed6\u89c4\u5212\u51fa\u4ed6\u559c\u6b22\u7684\u8def\u7ebf\u3002\u4eca\u5929\u6211\u4e0d\u60f3\u4ecb\u7ecd\u6211\u7684\u4e3b\u8981\u5de5\u4f5c\uff0c\u800c\u662f\u60f3\u804a\u4e00\u804a\u6211\u5728\u963f\u91cc\u53c2\u4e0e\u7684\u201c\u56e2\u5706\u201d\u516c\u76ca\u9879\u76ee\u3002\u6211\u4eec\u505a\u8fd9\u4e2a\u9879\u76ee\uff0c\u662f\u60f3\u5e2e\u52a9\u5bfb\u627e\u66f4\u591a\u7684\u5931\u8e2a\u513f\u7ae5\uff0c\u80fd\u591f\u8ba9\u4ed6\u4eec\u8ddf\u5bb6\u4eba\u56e2\u5706\u3002\u9a6c\u4e91\u8001\u5e08\u7ecf\u5e38\u8ddf\u6211\u4eec\u8bf4\uff0c\u5927\u516c\u53f8\u7684\u201c\u5927\u201d\u5e76\u4e0d\u662f\u6307\u6536\u5165\u5927\u3001\u5229\u6da6\u5927\u3001\u5e02\u573a\u4efd\u989d\u5927\uff0c\u800c\u662f\u5728\u4e8e\u5b83\u7684\u8d23\u4efb\u5927\u3001\u62c5\u5f53\u5927\u3002\u4eba\u4eec\u5e0c\u671b\u4f60\u80fd\u62c5\u5f53\u66f4\u5927\u7684\u8d23\u4efb\uff0c\u4e3a\u793e\u4f1a\u505a\u51fa\u66f4\u5927\u7684\u8d21\u732e\u30022015\u5e749\u670810\u65e5\uff0c\u9a6c\u4e91\u8001\u5e08\u63d0\u51fa\u4e86\u4e00\u4e2a\u60f3\u6cd5\uff1a\u5e0c\u671b\u963f\u91cc\u6bcf\u4eba\u6bcf\u5e74\u81f3\u5c11\u505a\u4e09\u5c0f\u65f6\u516c\u76ca\u3002\u4e8e\u662f\u5f88\u591a\u540c\u4e8b\u5c31\u5728\u5927\u8857\u4e0a\u6216\u8005\u516c\u56ed\u91cc\u6361\u5783\u573e\uff0c\u4e5f\u6709\u4e00\u4e9b\u53bb\u656c\u8001\u9662\u505a\u516c\u76ca\u6d3b\u52a8\u3002\u800c\u6211\u4eec\u60f3\uff0c\u662f\u4e0d\u662f\u80fd\u901a\u8fc7\u6280\u672f\u7684\u624b\u6bb5\uff0c\u6765\u4e3a\u793e\u4f1a\u505a\u4e00\u4e9b\u516c\u76ca\u7684\u4e8b\u60c5\u3002\u6211\u4e4b\u524d\u770b\u8fc7\u4e00\u90e8\u7535\u5f71\u53eb\u300a\u7206\u88c2\u65e0\u58f0\u300b\uff0c\u8bb2\u8ff0\u7684\u662f\u4e00\u4e2a\u65f7\u5de5\u7236\u4eb2\u5f20\u4fdd\u6c11\u5728\u627e\u5bfb\u81ea\u5df1\u4e22\u5931\u5b69\u5b50\u7684\u8fc7\u7a0b\u4e2d\u53d1\u751f\u7684\u4e00\u4e9b\u6545\u4e8b\u3002\u4ed6\u5728\u5927\u8857\u5c0f\u5df7\u91cc\u5f20\u8d34\u5bfb\u4eba\u542f\u4e8b\uff0c\u5e0c\u671b\u6709\u66f4\u591a\u4eba\u770b\u5230\u3002\u6211\u5f53\u65f6\u5c31\u60f3\uff0c\u8fd9\u6837\u4f20\u9012\u4fe1\u606f\u7684\u6548\u7387\u592a\u4f4e\u4e86\uff0c\u8986\u76d6\u9762\u4e5f\u975e\u5e38\u5c0f\uff0c\u53ea\u6709\u770b\u5230\u5bfb\u4eba\u542f\u4e8b\u7684\u4eba\u624d\u77e5\u9053\u2014\u2014\u5982\u679c\u6ca1\u6709\u770b\u5230\u5bfb\u4eba\u542f\u4e8b\uff0c\u54ea\u6015\u9644\u8fd1\u7684\u4eba\u770b\u5230\u4e86\u8fd9\u4e2a\u5c0f\u5b69\uff0c\u4e5f\u4e0d\u4f1a\u77e5\u9053\u4ed6\u662f\u8d70\u5931\u7684\u3002\u800c\u79fb\u52a8\u4e92\u8054\u7f51\u6280\u672f\u7684\u51fa\u73b0\uff0c\u53ef\u4ee5\u8ba9\u4fe1\u606f\u4f20\u9012\u7684\u6548\u7387\u63d0\u9ad8\u3002\u6b63\u597d\uff0c\u516c\u5b89\u90e8\u4e5f\u6709\u5229\u7528\u4e92\u8054\u7f51\u6280\u672f\u63d0\u5347\u6253\u62d0\u6548\u7387\u7684\u9700\u6c42\u3002\u56e0\u6b64\uff0c\u5728\u674e\u514b\u5f3a\u603b\u7406\u63d0\u51fa\u201c\u4e92\u8054\u7f51+\u201d\u7684\u6307\u5bfc\u65b9\u9488\u4e0b\uff0c\u963f\u91cc\u8ddf\u516c\u5b89\u90e8\u8054\u5408\u542f\u52a8\u4e86\u201c\u56e2\u5706\u201d\u9879\u76ee\uff0c\u521d\u8877\u662f\u201c\u56e2\u805a\u8b66\u6c11\u5fc3\uff0c\u5706\u5b50\u56de\u5bb6\u68a6\u201d\uff0c\u76ee\u6807\u662f\u201c\u5168\u6c11\u53c2\u4e0e\uff0c\u5929\u4e0b\u65e0\u62d0\u201d\u3002\u56e2\u5706\u7cfb\u7edf\uff0c\u600e\u4e48\u505a\uff1f\u80cc\u540e\u53c8\u6709\u54ea\u4e9b\u6280\u672f\u652f\u6491\uff1f\u4ece\u5927\u65b9\u5411\u6765\u8bf4\uff0c\u8981\u4f9d\u8d56\u4e8e\u79fb\u52a8\u4e92\u8054\u7f51\u3001\u5927\u6570\u636e\u548c\u4eba\u5de5\u667a\u80fd\u6280\u672f\uff1b\u4ece\u6838\u5fc3\u5b9e\u73b0\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u8981\u5305\u62ec\u6743\u5a01\u53d1\u5e03\u3001\u7cbe\u51c6\u5b9a\u4f4d\u548c\u9ad8\u6548\u534f\u540c\u8fd9\u4e09\u4e2a\u8fc7\u7a0b\u3002No.1 \u6743\u5a01\u53d1\u5e03\u513f\u7ae5\u5931\u8e2a\u7684\u4fe1\u606f\uff0c\u8c01\u6765\u53d1\u5e03\uff1f\u53c8\u662f\u600e\u4e48\u53d1\u5e03\u7684\uff1f\u6211\u4eec\u5e0c\u671b\u7528\u6237\u5bf9\u56e2\u5706\u7cfb\u7edf\u4ea7\u751f\u4fe1\u4efb\uff0c\u4ece\u800c\u5b9e\u73b0\u5168\u6c11\u6301\u7eed\u53c2\u4e0e\u3002\u56e0\u6b64\uff0c\u7cfb\u7edf\u53d1\u5e03\u7684\u4fe1\u606f\u8981\u767e\u5206\u4e4b\u767e\u51c6\u786e\uff0c\u53ea\u5141\u8bb8\u6253\u62d0\u8b66\u5bdf\u6709\u53d1\u5e03\u8d70\u5931\u6d88\u606f\u7684\u6743\u9650\u3002\u5168\u56fd\u67096000\u591a\u540d\u6253\u62d0\u8b66\u5bdf\uff0c\u4ed6\u4eec\u6536\u5230\u62a5\u8b66\u4fe1\u606f\u540e\uff0c\u4f1a\u4ece\u81ea\u5df1\u4e13\u4e1a\u7684\u89d2\u5ea6\u5224\u65ad\u4fe1\u606f\u662f\u5426\u662f\u51c6\u786e\u3001\u5168\u9762\u3002\u7ecf\u8fc7\u5ba1\u6838\u4e4b\u540e\uff0c\u8b66\u5bdf\u4f1a\u5728\u9489\u9489\u5f00\u53d1\u7684\u56e2\u5706\u7cfb\u7edf\u4e0a\u53d1\u5e03\u51c6\u786e\u7684\u6d88\u606f\uff08\u5305\u62ec\u513f\u7ae5\u7684\u5e74\u9f84\u3001\u6027\u522b\u3001\u7167\u7247\uff0c\u8863\u7740\u5916\u8c8c\u7279\u5f81\uff0c\u4ee5\u53ca\u4e22\u5931\u65f6\u95f4\u548c\u4f4d\u7f6e\uff09\u3002No.2 \u7cbe\u51c6\u5b9a\u4f4d\u8b66\u5bdf\u53d1\u5e03\u4e86\u4e22\u5931\u513f\u7ae5\u4fe1\u606f\u540e\uff0c\u63a8\u9001\u7ed9\u8c01\uff1f\u5f80\u54ea\u91cc\u63a8\u9001\uff1f\u7406\u60f3\u6765\u8bf4\uff0c\u6211\u4eec\u5e94\u8be5\u628a\u4fe1\u606f\u63a8\u9001\u5230\u5c0f\u5b69\u4e22\u5931\u540e\u6240\u6709\u89c1\u8fc7\u4ed6\u7684\u4eba\u3002\u5728\u7535\u5f71\u300a\u7206\u88c2\u65e0\u58f0\u300b\u91cc\uff0c\u4e22\u5931\u5b69\u5b50\u7684\u5bb6\u957f\u5f20\u4fdd\u6c11\u5230\u5904\u5f20\u8d34\u5bfb\u4eba\u542f\u4e8b\uff0c\u5c31\u662f\u5e0c\u671b\u66f4\u591a\u7684\u4eba\u80fd\u591f\u770b\u5230\u3002\u8981\u5b9e\u73b0\u4e0a\u8ff0\u7684\u7406\u60f3\u72b6\u6001\uff0c\u5c31\u8981\u4f9d\u8d56\u7cbe\u51c6\u5b9a\u4f4d\u7684\u6280\u672f\u3002\u6211\u4eec\u6839\u636e\u8b66\u5bdf\u5728\u56e2\u5706\u5e73\u53f0\u4e0a\u9762\u53d1\u5e03\u7684\u4fe1\u606f\uff0c\u4ee5\u513f\u7ae5\u4e22\u5931\u7684\u4f4d\u7f6e\u4e3a\u4e2d\u5fc3\u753b\u4e00\u4e2a\u534a\u5f84\u4e00\u767e\u516c\u91cc\u7684\u5706\u2014\u2014\u53ef\u4ee5\u7406\u89e3\u4e3a\u4e00\u4e2a\u865a\u62df\u7684\u7535\u5b50\u56f4\u680f\uff0c\u6211\u4eec\u4f1a\u7ed9\u56f4\u680f\u91cc\u6240\u6709\u7684\u4eba\u63a8\u9001\u4fe1\u606f\u3002\u73b0\u5728\u79fb\u52a8\u4e92\u8054\u7f51\u975e\u5e38\u666e\u53ca\uff0c\u5927\u5bb6\u90fd\u5728\u4f7f\u7528\u624b\u673a\uff0c\u6240\u4ee5\u5229\u7528\u624b\u673a\u63a8\u9001\u89e6\u8fbe\u76f8\u5e94\u4eba\u7fa4\u662f\u6700\u9ad8\u6548\u7684\u4e00\u79cd\u65b9\u5f0f\u3002\u6211\u4eec\u6700\u5148\u60f3\u5230\u7684\u662f\u5f00\u53d1\u4e00\u4e2aAPP\uff0c\u4f46\u5b83\u53ea\u80fd\u5728\u6253\u62d0\u548c\u5bfb\u4eba\u65b9\u9762\u5e94\u7528\uff0c\u8fd0\u8425\u548c\u63a8\u5e7f\u975e\u5e38\u56f0\u96be\u3002\u4e8e\u662f\uff0c\u6211\u4eec\u5c31\u60f3\u5230\u8ddf\u70ed\u95e8APP\u5408\u4f5c\u3002\u56e2\u5706\u7cfb\u7edf\u7b2c\u4e00\u6279\u5408\u4f5c\u7684APP\u5305\u62ec\u9ad8\u5fb7\u3001\u9489\u9489\u548c\u65b0\u6d6a\u5fae\u535a\u3002\u4ee5\u9ad8\u5fb7\u4e3a\u4f8b\uff1a\u8b66\u5bdf\u53d1\u5e03\u4e86\u513f\u7ae5\u4e22\u5931\u7684\u4fe1\u606f\u540e\uff0c\u9ad8\u5fb7\u4f1a\u4ece\u56e2\u5706\u7cfb\u7edf\u4e0a\u5b9e\u65f6\u83b7\u53d6\uff0c\u7136\u540e\u4ee5\u4e22\u5931\u5730\u4e3a\u539f\u70b9\u8fdb\u884c\u6d88\u606f\u63a8\u9001\u3002\u4e3a\u4e86\u66f4\u7cbe\u51c6\u5730\u63a8\u9001\u6d88\u606f\uff0c\u9ad8\u5fb7\u4f9d\u636e\u72af\u7f6a\u5206\u5b50\u53ef\u80fd\u7684\u9003\u8dd1\u901f\u5ea6\uff0c\u6309\u71671\u5c0f\u65f6100\u516c\u91cc\uff0c2\u5c0f\u65f6200\u516c\u91cc\uff0c3\u5c0f\u65f6300\u516c\u91cc\uff0c\u5927\u4e8e3\u5c0f\u65f6500\u516c\u91cc\u7684\u6807\u51c6\u6846\u5b9a\u5730\u7406\u8303\u56f4\u3002\u5176\u4e2d\u6838\u5fc3\u4f9d\u8d56\u7684\u662f\u9ad8\u5fb7\u7684\u7cbe\u786e\u5b9a\u4f4d\u6280\u672f\u3002\u9ad8\u5fb7\u7684\u5b9a\u4f4d\u6280\u672f\u6db5\u76d6GPS\u3001wifi\u3001\u57fa\u7ad9\u3001\u84dd\u7259\u8bbe\u5907\u7b49\u7b49\uff0c\u56e0\u6b64\u5373\u4fbf\u5728\u6ca1\u6709GPS\u4fe1\u53f7\u7684\u5730\u65b9\uff08\u6bd4\u5982\u5ba4\u5185\u3001\u9ad8\u94c1\u4e0a\u3001\u96a7\u9053\u5185\uff09\uff0c\u4e5f\u80fd\u591f\u5b9e\u73b0\u51c6\u786e\u5b9e\u65f6\u5b9a\u4f4d\u3002\u5927\u81f4\u539f\u7406\uff1awifi\u3001\u57fa\u7ad9\u548c\u84dd\u7259\u8bbe\u5907\u90fd\u6709\u552f\u4e00\u7684\u6807\u8bc6\u7801\uff08\u88ab\u79f0\u4e3a\u6307\u7eb9\u6570\u636e\uff09\u3002\u501f\u52a9\u9ad8\u5fb7\u6d77\u91cf\u7684\u5b9a\u4f4d\u6570\u636e\u548c\u963f\u91cc\u4e91\u5f3a\u5927\u7684\u8ba1\u7b97\u5e73\u53f0\uff0c\u6211\u4eec\u518d\u7528\u4eba\u5de5\u667a\u80fd\u7684\u65b9\u6cd5\u5bf9\u5404\u79cd\u6307\u7eb9\u6570\u636e\u8fdb\u884c\u6df1\u5165\u6316\u6398\uff0c\u5efa\u7acb\u4e86\u8986\u76d6\u5168\u7403\u7684\u6307\u7eb9\u6570\u636e\u5e93\u3002\u8fd9\u6837\uff0c\u5c31\u53ef\u4ee5\u5bf9\u7528\u6237\u8fdb\u884c\u5b9e\u65f6\u5b9a\u4f4d\u3002No.3\u00a0\u9ad8\u6548\u534f\u540c\u8b66\u5bdf\u548c\u6c11\u4f17\u7684\u534f\u540c\u3002\u7528\u6237\u63a5\u6536\u5230\u4e22\u5931\u513f\u7ae5\u7684\u4fe1\u606f\u540e\uff0c\u5982\u679c\u6070\u597d\u770b\u89c1\u4eba\u8d29\u5b50\u5e26\u7740\u8fd9\u4e2a\u5c0f\u5b69\u7ecf\u8fc7\uff0c\u5c31\u4f1a\u62a5\u8b66\uff0c\u8b66\u5bdf\u5c31\u53ef\u4ee5\u7ee7\u7eed\u8ffd\u8e2a\uff0c\u4ece\u800c\u89e3\u6551\u513f\u7ae5\u3002\u8b66\u5bdf\u548c\u8b66\u5bdf\u7684\u534f\u540c\u3002\u5982\u679c\u6709\u4e00\u4e2a\u5c0f\u5b69\u5728\u5317\u4eac\u62a5\u6848\u8d70\u5931\uff0c\u540e\u6765\u5929\u6d25\u6709\u4eba\u63d0\u4f9b\u4e86\u7ebf\u7d22\uff0c\u5317\u4eac\u548c\u5929\u6d25\u7684\u8b66\u5bdf\u5c31\u53ef\u4ee5\u901a\u8fc7\u56e2\u5706\u7cfb\u7edf\u9ad8\u6548\u6c9f\u901a\u548c\u540c\u6b65\u4fe1\u606f\u3002\u4e92\u8054\u7f51\u516c\u53f8\u4e4b\u95f4\u7684\u534f\u540c\u30022016\u5e74\u4e0a\u7ebf\u521d\u671f\uff0c\u53ea\u6709\u4e09\u5bb6\u516c\u53f8\u53c2\u4e0e\u8fdb\u6765\uff1b\u800c\u76ee\u524d\uff0c\u56fd\u5185\u5df2\u7ecf\u6709\u6570\u5341\u5bb6\u516c\u53f8\u768425\u6b3e\u8d85\u7ea7APP\u63a5\u5165\uff0c\u5305\u62ec\u9ad8\u5fb7\u3001\u652f\u4ed8\u5b9d\u3001\u6dd8\u5b9d\u3001\u817e\u8baf\u3001\u767e\u5ea6\u3001\u4eca\u65e5\u5934\u6761\u3001UC\u5934\u6761\u7b49\u7b49\u3002\u8fd9\u662f\u9996\u6b21\u5728\u516c\u76ca\u9886\u57df\u7a81\u7834\u4e86\u5546\u4e1a\u7ade\u4e89\u7684\u5c40\u9762\uff0c\u5927\u5bb6\u4e3a\u4e86\u5171\u540c\u7684\u76ee\u6807\u53bb\u8d21\u732e\u81ea\u5df1\u7684\u4e00\u4efd\u529b\u91cf\u3002\u6211\u4eec\u8bbe\u8ba1\u4e86\u56e2\u5706\u7cfb\u7edf\uff0c\u4f46\u662f\u5b83\u80fd\u4e0d\u80fd\u7b26\u5408\u6211\u4eec\u7684\u9884\u671f\u8d77\u5230\u4f5c\u7528\u5462\uff1f\u5148\u542c\u4e00\u4e2a\u5c0f\u6545\u4e8b\uff0c\u53d1\u751f\u5728\u6cb3\u5317\u8861\u6c34\u2014\u2014\u56e2\u5706\u7cfb\u7edf2016\u5e745\u670815\u65e5\u4e0a\u7ebf\u524d\u5915\uff0c\u56e2\u5706\u7cfb\u7edf\u7ba1\u7406\u5458\u8ba9\u5927\u5bb6\u628a\u4e4b\u524d\u7684\u6d4b\u8bd5\u6570\u636e\u90fd\u5220\u6389\u3002\u4f46\u662f\u540e\u6765\u4e0a\u7ebf\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u8fd8\u662f\u6709\u4e00\u6761\u63a8\u9001\u51fa\u53bb\u4e86\u2014\u2014\u4e0d\u8fc7\u53d1\u5e03\u7684\u8b66\u5bdf\u8bf4\u5176\u5b9e\u4e0d\u662f\u6f0f\u5220\uff0c\u800c\u662f\u521a\u521a\u62a5\u6848\u7684\u6848\u4ef6\u3002\u521a\u4e0a\u7ebf\uff0c\u5927\u5bb6\u5fc3\u91cc\u90fd\u5f88\u6ca1\u6709\u5e95\uff0c\u4f46\u662f\u4e0d\u4e45\u5c31\u6709\u4e00\u4e2a\u51fa\u79df\u8f66\u53f8\u673a\u5728\u8861\u6c34\u706b\u8f66\u7ad9\uff08\u53ef\u80fd\u662f\u62c9\u5c0f\u5b69\u5230\u706b\u8f66\u7ad9\u7684\u53f8\u673a\uff09\u63d0\u4f9b\u4e86\u4e00\u6761\u91cd\u8981\u7684\u7ebf\u7d22\u3002\u8b66\u65b9\u5c31\u636e\u6b64\u5728\u516c\u5b89\u90e8\u6253\u62d0\u529e\u7684\u534f\u8c03\u6307\u6325\u4e0b\uff0c\u5728\u6cb3\u5357\u90d1\u5dde\u627e\u5230\u4e86\u72af\u7f6a\u5acc\u7591\u4eba\uff0c\u5b89\u5168\u89e3\u6551\u51fa\u4e86\u8fd9\u4e2a\u5c0f\u5973\u5b69\u3002\u56e2\u5706\u7cfb\u7edf\u5728\u521a\u4e0a\u7ebf\u4e00\u4e2a\u6708\u7684\u65f6\u5019\uff0c\u513f\u7ae5\u7684\u627e\u56de\u7387\u5c31\u5df2\u7ecf\u8fbe\u5230\u4e8689%\uff1b\u4e0a\u7ebf\u534a\u5e74\u7684\u65f6\u5019\uff0c\u627e\u56de\u7387\u8fbe\u523090.91%\u3002\u6839\u636e\u6700\u65b0\u7684\u6570\u636e\uff0c\u622a\u6b62\u4eca\u5e749\u67085\u53f7\uff0c\u6211\u4eec\u4e00\u5171\u53d1\u5e03\u4e864191\u6761\u4e22\u5931\u513f\u7ae5\u7684\u4fe1\u606f\uff0c\u627e\u56de\u4e864102\u4eba\uff0c\u627e\u56de\u7387\u9ad8\u8fbe98%\u3002\u56e2\u5706\u7cfb\u7edf\u7684\u6210\u529f\uff0c\u79bb\u4e0d\u5f00\u963f\u91cc\u6280\u672f\u5fd7\u613f\u8005\u6301\u7eed\u4e0d\u65ad\u7684\u6295\u5165\u3002\u5176\u5b9e2016\u5e745\u670815\u65e5\u56e2\u5706\u7cfb\u7edf\u4e0a\u7ebf\u540e\uff0c\u6211\u4eec\u7684\u5de5\u4f5c\u5c31\u5df2\u7ecf\u5b8c\u6210\u4e86\uff0c\u540e\u9762\u662f\u516c\u5b89\u90e8\u7684\u5de5\u4f5c\uff0c\u4ed6\u4eec\u6765\u6301\u7eed\u8fd0\u8425\u8fd9\u5957\u7cfb\u7edf\u3002\u4f46\u4e8b\u5b9e\u5e76\u975e\u5982\u6b64\uff0c\u4ece2016\u5e74\u81f3\u4eca\uff0c\u56e2\u5706\u7cfb\u7edf\u7ecf\u5386\u4e865\u4e2a\u7248\u672c\u7684\u8fed\u4ee3\uff0c\u8fd9\u4e9b\u90fd\u662f\u5fd7\u613f\u8005\u5229\u7528\u4e1a\u4f59\u65f6\u95f4\u5b8c\u6210\u7684\u30021.0\u7248\u672c\uff0c\u53ea\u6709\u53d1\u5e03\u548c\u63a8\u9001\u7684\u529f\u80fd\u30022.0\u7248\u672c\uff0c\u589e\u52a0\u4e86\u627e\u56de\u513f\u7ae5\u7684\u53cd\u9988\u4fe1\u606f\uff0c\u80fd\u8ba9\u63d0\u4f9b\u7ebf\u7d22\u7684\u7fa4\u4f17\u77e5\u9053\u4ed6\u63d0\u4f9b\u7684\u7ebf\u7d22\u786e\u5b9e\u6709\u4ef7\u503c\u30023.0\u7248\u672c\uff0c\u52a0\u5165\u4e86\u5927\u6570\u636e\u81ea\u52a8\u7edf\u8ba1\u62a5\u8868\u7684\u529f\u80fd\u30024.0\u7248\u672c\uff08\u56fd\u9645\u677f\uff09\uff0c\u5728\u8054\u5408\u56fd\u63a8\u5e7f\u56e2\u5706\u79d1\u6280\u6253\u62d0\u7684\u4e2d\u56fd\u7ecf\u9a8c\u30025.0\u7248\u672c\u6b63\u5728\u7070\u5ea6\u8fed\u4ee3\u7684\u8fc7\u7a0b\u4e2d\u3002\u5927\u5bb6\u53ef\u80fd\u4f1a\u89c9\u5f97\uff0c\u5931\u8e2a\u513f\u7ae5\u7684\u6700\u5927\u539f\u56e0\u662f\u88ab\u62d0\u5356\u3002\u5b9e\u9645\u4e0a\uff0c\u4ece\u6211\u4eec\u8fd9\u4e9b\u5e74\u7edf\u8ba1\u7684\u6570\u636e\u6765\u770b\uff0c\u6700\u5927\u7684\u539f\u56e0\uff0860.2%\uff09\u662f\u79bb\u5bb6\u51fa\u8d70\u3002\u6240\u4ee5\u6211\u4eec\u4e5f\u5e0c\u671b\u7ed9\u5bb6\u957f\u63d0\u9192\u4e00\u4e0b\uff0c\u8981\u5b66\u4f1a\u66f4\u597d\u5730\u8ddf\u513f\u7ae5\u6c9f\u901a\u30022017\u5e749\u6708\uff0c\u9ad8\u5fb7\u5730\u56fe\u8ddf\u516c\u5b89\u90e8\u6253\u62d0DNA\u5e93\u5408\u4f5c\uff0c\u4e0a\u7ebf\u4e86\u4e00\u4e2a\u5bfb\u4eb2\u5bfc\u822a\u7684\u529f\u80fd\uff0c\u5e2e\u52a9\u5931\u6563\u591a\u5e74\u7684\u7236\u6bcd\u548c\u5b69\u5b50\u56e2\u5706\u3002\u5982\u679c\u4f60\u6000\u7591\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u53ef\u4ee5\u5728\u9ad8\u5fb7\u5730\u56fe\u4e0a\u641c\u7d22\u201c\u5bfb\u4eb2\u201d\uff0c\u9ad8\u5fb7\u53ef\u4ee5\u5e2e\u4f60\u627e\u5230\u9644\u8fd1\u7684\u516c\u5b89\u673a\u5173\u514d\u8d39\u91c7\u8840\uff0c\u7136\u540e\u4e0a\u4f20\u5230\u516c\u5b89\u90e8\u6253\u62d0DNA\u5e93\u6bd4\u5bf9\uff0c\u8fd9\u4e2aDNA\u5e93\u73b0\u5728\u5df2\u7ecf\u5e2e\u52a96000\u591a\u4e2a\u5bb6\u5ead\u5b9e\u73b0\u4e86\u56e2\u5706\u68a6\u3002\u9664\u4e86\u56e2\u5706\u7cfb\u7edf\uff0c\u9ad8\u5fb7\u5176\u5b9e\u8fd8\u505a\u4e86\u201c\u7eff\u8272\u901a\u9053\u201d\u7684\u516c\u76ca\u9879\u76ee\u3002\u53bb\u5e74\u6709\u4e00\u4e2a\u4e2d\u5b66\u751f\uff08\u5c0f\u5b87\u6cfd\uff09\u56e0\u4e3a\u610f\u5916\u8111\u90e8\u53d7\u4f24\uff0c\u4ed6\u539f\u672c\u5728\u5185\u8499\u53e4\u5c31\u533b\uff0c\u4f46\u540e\u6765\u75c5\u60c5\u7a81\u7136\u6025\u5267\u6076\u5316\uff0c\u9700\u8981\u8f6c\u9662\u5230\u5317\u4eac\u3002\u9ad8\u5fb7\u77e5\u9053\u540e\uff0c\u7b2c\u4e00\u65f6\u95f4\u5c31\u542f\u52a8\u4e86\u7eff\u8272\u901a\u9053\u529f\u80fd\uff0c\u5728\u4ed6\u8f6c\u9662\u7684\u8fc7\u7a0b\u4e2d\u4e00\u8def\u7ed9\u6cbf\u9014\u4f7f\u7528\u9ad8\u5fb7\u5bfc\u822a\u7684\u53f8\u673a\u63a8\u9001\u907f\u8ba9\u6d88\u606f\uff0c\u4f7f\u5f97\u539f\u672c7\u4e2a\u591a\u5c0f\u65f6\u3001520\u516c\u91cc\u7684\u8def\u7a0b\uff0c\u6700\u540e\u53ea\u7528\u4e865\u4e2a\u5c0f\u65f620\u5206\u949f\u5c31\u8d70\u5b8c\u4e86\uff0c\u4e3a\u75c5\u60c5\u7684\u6551\u6cbb\u5927\u5927\u8282\u7701\u4e86\u65f6\u95f4\u3002\u524d\u4e0d\u4e45\uff0c\u9a6c\u4e91\u8001\u5e08\u5728\u963f\u91cc20\u5468\u5e74\u751f\u65e5\u7684\u5e74\u4f1a\u4e0a\u8bf4\uff0c\u8fc7\u53bb\u768420\u5e74\u963f\u91cc\u62e5\u6709\u4e86\u6700\u597d\u7684\u4eba\u624d\u3001\u6700\u597d\u7684\u6280\u672f\u548c\u6700\u591a\u7684\u8d44\u6e90\uff0c\u8fd9\u5176\u5b9e\u4ee3\u8868\u4e86\u793e\u4f1a\u5bf9\u963f\u91cc\u7684\u4fe1\u4efb\u3002\u6211\u4eec\u5e94\u8be5\u7528\u4e00\u4e9b\u5b9e\u9645\u7684\u884c\u52a8\u4e3a\u793e\u4f1a\u505a\u4e00\u4e9b\u6709\u4ef7\u503c\u3001\u6709\u610f\u4e49\u7684\u4e8b\u60c5\uff0c\u8c22\u8c22\u5927\u5bb6\u3002\uff08\u7f16\u8f91\uff1a\u9ea6\u82bd\u6768\u3001\u51dd\u97f3\uff09", "title": "\u201c\u56e2\u5706\u201d\u53d1\u5e03\u4e864191\u6761\u4fe1\u606f\uff0c\u627e\u56de\u4e864102\u4e2a\u4e22\u5931\u7684\u5b69\u5b50\uff0c\u4f46\u53d1\u73b0\u5b69\u5b50\u4e22\u5931\u7684\u6700\u5927\u539f\u56e0\u4e0d\u662f\u62d0\u5356\uff5c\u5218\u51ef\u594e", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160/format/PNG", "normal": "https://1-im.guokr.com/X1xkPOsjddD8FotCpDX_OajYPhmVgAO5CzGxxwP6LqKgAAAAeAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://1-im.guokr.com/X1xkPOsjddD8FotCpDX_OajYPhmVgAO5CzGxxwP6LqKgAAAAeAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "\u5d14\u51ef", "title": "", "ukey": "bl1jig"}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160/format/PNG", "normal": "https://1-im.guokr.com/X1xkPOsjddD8FotCpDX_OajYPhmVgAO5CzGxxwP6LqKgAAAAeAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://1-im.guokr.com/X1xkPOsjddD8FotCpDX_OajYPhmVgAO5CzGxxwP6LqKgAAAAeAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "\u5d14\u51ef", "title": "", "ukey": "bl1jig"}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<p class=\"editors-note\">\u4e00\u65e5\u4e09\u9910\uff0c\u6211\u4eec\u6bcf\u5929\u90fd\u4f1a\u4e0e\u8c37\u7269\u89c1\u9762\uff1b\u79bb\u5f00\u9910\u684c\uff0c\u8c37\u7269\u4f3c\u4e4e\u79bb\u6211\u4eec\u5f88\u9065\u8fdc\u3002\u8bb8\u591a\u4e45\u5c45\u57ce\u5e02\u7684\u670b\u53cb\u4eec\uff0c\u751a\u81f3\u6e10\u6e10\u53d8\u5f97&ldquo;\u4e94\u8c37\u4e0d\u5206&rdquo;\u3002\u8c01\u77e5\u76d8\u4e2d\u9910\uff0c\u7c92\u7c92\u7686\u8f9b\u82e6\u3002\u8c37\u7269\u662f\u5982\u4f55\u4ece\u91ce\u8349\u8fdb\u5316\u6210\u4eca\u5929\u7684\u6837\u5b50\uff1f\u4ece\u4e00\u7c92\u79cd\u5b50\u6210\u957f\u5230\u4e00\u682a\u8c37\u7269\u7ecf\u8fc7\u4e86\u54ea\u4e9b\u5386\u7a0b\uff1f\u8c37\u7530\u91cc\u4f1a\u6709\u600e\u6837\u7684\u52a8\u7269\u4e16\u754c\uff1f\u4e2d\u56fd\u7684\u8015\u5730\u80fd\u517b\u6d3b14\u4ebf\u4eba\u5417\uff1f\u6c5f\u5357\u5927\u5b66\u98df\u54c1\u5de5\u7a0b\u535a\u58eb\uff0c\u300a\u4e2d\u56fd\u98df\u54c1\u4ea7\u4e1a\u5730\u56fe\u300b\u4e3b\u7f16\u5d14\u51ef\u8001\u5e08\uff0c\u5c06\u7ed3\u5408\u81ea\u7136\u3001\u79d1\u6280\u3001\u5386\u53f2\u3001\u8bd7\u6b4c\u548c\u91ce\u8da3\uff0c\u5c06\u7528\u751f\u52a8\u5e7d\u9ed8\u7684\u8bed\u8a00\u8c08\u8c08&ldquo;\u76d8\u4e2d\u9910&rdquo;\u548c4000\u5e74\u7684\u534e\u590f\u519c\u8015\u6587\u660e\uff0c\u8bb2\u8ff0\u4e00\u6bb5\u522b\u5f00\u751f\u9762\u7684\u300a\u8c37\u7269\u7684\u6545\u4e8b\u300b\u3002</p>\r\n\r\n<h2><strong>\u8c37\u7269\u7684\u4e00\u751f</strong></h2>\r\n\r\n<p>&ldquo;\u4ece\u660e\u5929\u8d77\uff0c\u505a\u4e00\u4e2a\u5e78\u798f\u7684\u4eba\u3002\u5582\u9a6c\uff0c\u5288\u67f4\uff0c\u5468\u6e38\u4e16\u754c\u3002\u4ece\u660e\u5929\u8d77\uff0c\u5173\u5fc3\u7cae\u98df\u548c\u852c\u83dc\uff0c\u6211\u6709\u4e00\u6240\u623f\u5b50\uff0c\u9762\u671d\u5927\u6d77\uff0c\u6625\u6696\u82b1\u5f00\u3002&rdquo;</p>\r\n\r\n<p>\u8fd9\u662f\u6d77\u5b50\u7b14\u4e0b\u7684\u4e00\u6bb5\u8bd7\u53e5\uff0c\u900f\u7740\u6d53\u6d53\u7684\u4e61\u571f\u7530\u56ed\u60c5\u6000\u3002\u8c37\u7269\u7684\u4e00\u751f\u662f\u600e\u4e48\u5ea6\u8fc7\u7684\uff1f\u4e00\u7c92\u79cd\u5b50\u5982\u4f55\u53d8\u6210\u6c89\u7538\u7538\u7684\u8c37\u7a57\uff1f\u4eca\u5929\uff0c\u6211\u6765\u548c\u5927\u5bb6\u804a\u804a\u8fd9\u4e2a\u8bdd\u9898\uff1a</p>\r\n\r\n<p><strong>\u4e00\u5e74\u4e4b\u8ba1\u5728\u4e8e\u6625</strong></p>\r\n\r\n<p>\u6211\u4eec\u5148\u6765\u91cd\u6e29\u8fd9\u9996\u59cb\u4e8e\u79e6\u4ee3\u7684<strong>\u300a\u4e8c\u5341\u56db\u8282\u6c14\u6b4c\u300b</strong>\uff1a</p>\r\n\r\n<p>&ldquo;\u6625\u96e8\u60ca\u6625\u6e05\u8c37\u5929\uff0c\u590f\u6ee1\u8292\u590f\u6691\u76f8\u8fde\u3002\u79cb\u5904\u9732\u79cb\u5bd2\u971c\u964d\uff0c\u51ac\u96ea\u96ea\u51ac\u5c0f\u5927\u5bd2\u3002&rdquo;</p>\r\n\r\n<p>\u56db\u5b63\u8f6e\u56de\uff0c\u5468\u800c\u590d\u59cb\u3002\u519c\u4eba\u6839\u636e\u8282\u6c14\u53d8\u5316\uff0c\u5b89\u6392\u519c\u4e8b\u3002\u6bd4\u5982\uff1a\u8c37\u96e8\u5f00\u59cb\u64ad\u8c37\u964d\u96e8\uff0c\u5c0f\u6ee1\u4ee3\u8868\u7c7d\u7c92\u5f00\u59cb\u704c\u6d46\uff0c\u4f46\u5c1a\u672a\u6210\u719f\uff0c\u53ea\u662f&ldquo;\u5c0f\u6ee1&rdquo;\u3002\u8292\u79cd\u5219\u662f&ldquo;\u6709\u8292\u7684\u9ea6\u5b50\u5feb\u6536\uff0c\u6709\u8292\u7684\u7a3b\u5b50\u53ef\u79cd&rdquo;\u3002\u5927\u5bb6\u770b\u770b\uff0c\u4e8c\u5341\u56db\u8282\u6c14\u672c\u8eab\u5c31\u662f\u8c37\u7269\u7684\u6545\u4e8b\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/FXr-jK7uNgzglRclSZ5feh7xYIoqENg6ObTPMhBlrlLAAwAAGwIAAEpQ.jpg\" style=\"height:359px; width:640px\" />\u62d6\u62c9\u673a\u6625\u8015 | pixabay</p>\r\n\r\n<p>\u4fd7\u8bdd\u8bf4\uff1a&ldquo;\u6625\u8015\u4e0d\u80af\u5fd9\uff0c\u79cb\u540e\u8138\u997f\u9ec4\u3002&rdquo;\u5927\u5bb6\u60f3\u60f3\u519c\u7530\u4e3a\u4ec0\u4e48\u9700\u8981\u6625\u8015\uff1f\u4eba\u5750\u4e45\u4e86\uff0c\u8981\u7ad9\u8d77\u6765\u8212\u7f13\u4e00\u4e0b\u7b4b\u9aa8\uff0c\u6240\u4ee5\u5b66\u6821\u91cc\u4f1a\u5b89\u6392\u5b66\u751f\u5728\u8bfe\u95f4\u505a\u5e7f\u64ad\u4f53\u64cd\u3002\u540c\u6837\u7684\u9053\u7406\uff0c\u571f\u58e4\u7ecf\u8fc7\u4e00\u5e74\u56db\u5b63\u7684\u6c89\u79ef\uff0c\u4e5f\u4f1a\u53d8\u5f97\u677f\u7ed3\u3002<strong>\u5728\u64ad\u79cd\u4e4b\u524d\uff0c\u9700\u8981\u8fdb\u884c\u758f\u677e\uff0c\u8fd9\u5c06\u5229\u4e8e\u5438\u7eb3\u6c34\u5206\uff0c\u4fbf\u4e8e\u8c37\u7269\u6839\u7cfb\u4f38\u5c55\uff0c\u8fd8\u80fd\u591f\u6291\u5236\u75c5\u3001\u866b\u3001\u6742\u8349\u7684\u751f\u957f\u7e41\u80b2\uff0c\u6700\u7ec8\u63d0\u9ad8\u8c37\u7269\u7684\u4ea7\u91cf\u3002</strong>\u5f53\u7136\uff0c\u7ffb\u8015\u4e5f\u5e76\u975e\u5168\u662f\u597d\u5904\uff1a\u571f\u58e4\u91cc\u6c34\u5206\u662f\u975e\u5e38\u5b9d\u8d35\u7684\uff0c\u5c24\u5176\u5728\u5317\u65b9\u5e72\u65f1\u7684\u5730\u533a\u3002\u7ffb\u8015\u76f8\u5f53\u4e8e\u628a\u571f\u58e4\u50cf\u8863\u670d\u4e00\u6837\u667e\u6652\uff0c\u5bfc\u81f4\u6c34\u5206\u84b8\u53d1\u3002\u4eca\u5929\uff0c\u6709\u4e9b\u5730\u65b9\u4e5f\u5728\u63a8\u5e7f\u514d\u8015\u79cd\u690d\u6280\u672f\u3002</p>\r\n\r\n<p>\u6625\u8015\u4e4b\u540e\uff0c\u6211\u4eec\u4f1a\u5728\u7530\u91ce\u91cc\u64ad\u6492\u5e0c\u671b\u7684\u79cd\u5b50&mdash;&mdash;\u6563\u6587\u4e2d\u7ecf\u5e38\u4f7f\u7528\u8fd9\u6837\u7684\u8bed\u53e5\u3002\u901a\u5e38\uff0c\u5c0f\u9ea6\u3001\u7389\u7c73\u3001\u5927\u8c46\u7b49\u65f1\u7530\u4f5c\u7269\u5927\u90fd\u76f4\u63a5\u5728\u7530\u95f4\u64ad\u79cd\uff0c\u7136\u540e\u671f\u5f85\u4e00\u573a\u5927\u96e8\uff0c\u8ba9\u79cd\u5b50\u751f\u6839\u53d1\u82bd&mdash;&mdash;<strong>\u6625\u96e8\u8d35\u5982\u6cb9</strong>\u3002\u7136\u800c\u6c34\u7a3b\u5219\u4eab\u53d7\u4e00\u79cd&ldquo;\u8d85\u56fd\u6c11\u5f85\u9047&rdquo;\uff0c\u53ef\u4ee5\u5148\u5728\u6e29\u5ba4\u5927\u68da\u91cc\u80b2\u82d7\u4e00\u4e2a\u6708\uff0c\u518d\u79fb\u683d\u5230\u5927\u7530\u91cc\u3002\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f</p>\r\n\r\n<p><strong>\u5411\u5929\u95ee\u51b7\u6696</strong></p>\r\n\r\n<p>\u4e2d\u56fd\u5e45\u5458\u8fbd\u9614\uff0c\u5357\u5317\u6e29\u5dee\u5f88\u5927\u3002\u9686\u51ac\u65f6\u8282\uff0c\u5317\u56fd\u98ce\u5149\uff0c\u5343\u91cc\u51b0\u5c01\uff0c\u4e07\u91cc\u96ea\u98d8\u3002\u800c\u6d77\u5357\u5c9b\u4e0a\u4f9d\u7136\u8273\u9633\u9ad8\u7167\uff0c\u6e29\u6696\u5982\u590f\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/3A-fsayZgZKQrO-ZbwwmPDcY5Z0IudyipCAIBpaP9-vEAwAAZgIAAEpQ.jpg\" style=\"height:408px; width:640px\" />\u4e2d\u56fd\u6e29\u5ea6\u5e26\u5212\u5206\u5316\u519c\u4f5c\u7269\u719f\u5236 | \u4e2d\u5b66\u300a\u5730\u7406\u300b</p>\r\n\r\n<p>\u8bf7\u5927\u5bb6\u5148\u6765\u770b\u770b\u8fd9\u5f20\u4e2d\u56fd\u6e29\u5ea6\u5e26\u5206\u5e03\u56fe\u3002\u5728\u5317\u65b9\u6e29\u5e26\uff0c\u5f88\u591a\u5730\u65b9\u4e00\u5e74\u53ea\u80fd\u79cd\u690d1\u5b63\u8c37\u7269\uff0c\u800c\u5728\u5357\u65b9\u4e9a\u70ed\u5e26\uff0c\u5219\u53ef\u4ee5\u79cd\u690d2-3\u5b63\u8c37\u7269\u3002<strong>\u65e0\u8bba\u5357\u5317\uff0c\u6bcf\u5b63\u4f5c\u7269\u90fd\u53ea\u6709\u7ea6100\u5929\u7684\u751f\u957f\u671f\u3002</strong>\u519c\u65f6\u4e0d\u7b49\u4eba\uff0c\u8ba9\u6c34\u7a3b\u63d0\u524d\u5728\u5927\u68da\u4e2d\u80b2\u79e7\uff0c\u76f8\u5f53\u4e8e\u589e\u52a0\u4e861\u4e2a\u6708\u7684\u751f\u957f\u671f\u3002\u53e6\u5916\uff0c\u6c34\u7a3b\u7684\u82d7\u671f\u76f8\u5f53\u4e8e\u4eba\u7684\u5a74\u5e7c\u513f\u9636\u6bb5\uff0c\u82d7\u5e8a\u76f8\u5f53\u4e8e\u80b2\u5a74\u623f\uff0c\u8fd9\u91cc\u53ef\u4ee5\u63a7\u5236\u5149\u7167\u3001\u6e29\u5ea6\u548c\u6c34\u5206\uff0c\u51cf\u5c11\u75c5\u866b\u5bb3\u3002\u7cbe\u5fc3\u914d\u5236\u7684\u82d7\u5e8a\u571f\u91cc\u5305\u542b\u4e86\u5404\u79cd\u8425\u517b\u8981\u7d20\uff0c\u8ba9\u6839\u7cfb\u53d1\u80b2\u5f3a\u58ee\uff0c\u8fd9\u76f8\u5f53\u4e8e\u7ed9\u5c0f\u670b\u53cb\u5403\u4e0a\u914d\u65b9\u5976\u7c89\uff0c\u4e3a\u4ee5\u540e\u7684\u5065\u5eb7\u6210\u957f\u6253\u4e0b\u575a\u5b9e\u7684\u57fa\u7840\u3002\u79e7\u82d7\u5728\u6e29\u5ba4\u91cc\u957f\u5230\u4e09\u7247\u534a\u53f6\u65f6\uff0c\u5f00\u59cb\u5927\u7530\u79fb\u683d\u3002</p>\r\n\r\n<p>\u66fe\u6709\u670b\u53cb\u95ee\u6211\uff1a\u65e2\u7136\u6709\u8fd9\u6837\u7684\u597d\u5904\uff0c\u4e3a\u4f55\u4e0d\u8ba9\u5c0f\u9ea6\u3001\u7389\u7c73\u3001\u5927\u8c46\u8fd9\u4e9b\u65f1\u7530\u4f5c\u7269\u4e5f\u5728\u6e29\u5ba4\u80b2\u82d7\u540e\u518d\u79fb\u683d\uff1f\u4ece\u6280\u672f\u4e0a\uff0c\u79fb\u683d\u7684\u79e7\u82d7\u9700\u8981\u5c3d\u5feb\u6d47\u6c34\uff0c\u7136\u800c\u65f1\u7530\u5f80\u5f80\u4e0d\u5177\u5907\u8fd9\u6837\u7684\u704c\u6e89\u6761\u4ef6\u3002\u66f4\u4e3a\u91cd\u8981\u7684\u662f\uff0c<strong>\u79fb\u683d\u8981\u8017\u8d39\u5927\u91cf\u7684\u4eba\u529b\u548c\u6210\u672c\uff0c\u519c\u6c11\u8981\u8003\u8651</strong><strong>\u6295\u5165\u548c\u4ea7\u51fa\u4e4b\u95f4\u7684\u7ecf\u6d4e\u6548\u76ca</strong>\u3002\u7389\u7c73\u4ea7\u91cf\u9ad8\u4f46\u4ef7\u683c\u4f4e\uff0c\u5927\u8c46\u4ef7\u683c\u9ad8\u4f46\u4ea7\u91cf\u4f4e\uff0c\u53ea\u6709\u9ad8\u4ea7\u3001\u9ad8\u4ef7\u7684\u6c34\u7a3b\u80b2\u79e7\u624d\u662f\u5212\u7b97\u7684\u3002</p>\r\n\r\n<p>\u5343\u767e\u5e74\u6765\uff0c\u519c\u4eba\u7cbe\u6253\u7ec6\u7b97\uff0c\u60f3\u65b9\u8bbe\u6cd5\u8ba9\u8015\u5730\u6253\u51fa\u66f4\u591a\u7684\u7cae\u98df\u3002\u5728\u4e2d\u56fd\u5357\u65b9\uff0c\u5f88\u591a\u5730\u65b9\u79cd\u690d\u4e00\u5e74\u4e09\u719f\u7684\u4f5c\u7269\uff1a\u9ea6\u5b50\u3001\u65e9\u7a3b\u548c\u665a\u7a3b\uff0c\u73af\u73af\u7d27\u6263\u3002\u519c\u65f6\u4e0d\u7b49\u4eba\uff0c\u4e00\u65e6\u9519\u8fc7\u65f6\u8282\uff0c\u4ea7\u91cf\u5c31\u4f1a\u5927\u51cf\uff0c\u751a\u81f3\u7edd\u6536\u3002\u8fd9\u4e2a\u8fc7\u7a0b\u5f88\u50cf\u57ce\u5e02\u4e0a\u73ed\u9ad8\u5cf0\u671f\u7684\u5730\u94c1\u6362\u4e58\uff0c\u4e0b\u8f66\u7684\u4eba\u521a\u521a\u51b2\u51fa\u8f66\u95e8\uff0c\u4e0a\u8f66\u7684\u4eba\u9a6c\u4e0a\u5c31\u4f1a\u6d8c\u8fdb\u6765\u3002\u4ece\u4e03\u6708\u4e2d\u5230\u516b\u6708\u521d\uff0c<strong>\u4e00\u4e2a\u6708\u5185\u8981\u5728\u708e\u708e\u590f\u65e5\u4e0b\u62a2\u6536\u65e9\u7a3b\u548c\u62a2\u79cd\u665a\u7a3b\uff0c\u4fd7\u79f0&ldquo;\u53cc\u62a2&rdquo;</strong>&mdash;&mdash;\u8fd9\u662f\u4f20\u7edf\u519c\u6c11\u7684\u68a6\u9b47\u65f6\u5206\u3002</p>\r\n\r\n<p>\u4eca\u5929\uff0c\u6709\u4e9b\u670b\u53cb\u538c\u5026\u4e86\u57ce\u5e02\u91cc\u7684\u96fe\u973e\u548c\u55a7\u56a3\uff0c\u8bf4\u5230\u7530\u56ed\u751f\u6d3b\uff0c\u5c31\u60f3\u8d77\u5929\u7136\u6c27\u5427\u3001\u9752\u5c71\u7eff\u6c34\u548c\u9ad8\u5929\u6d41\u4e91\uff0c\u9752\u7fe0\u6b32\u6ef4\uff0c\u8d4f\u5fc3\u60a6\u76ee\u3002\u7136\u800c\uff0c\u8fd9\u53ea\u662f\u65c5\u884c\u8005\u7684\u5fc3\u6001\uff0c\u519c\u4e8b\u52b3\u4f5c\u8fdc\u975e\u90a3\u4e48\u6d6a\u6f2b\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/_LCywvGbKRWJfkdxbtSR7VIyR8UdW540NHuSENS5l-QYBAAATAIAAEpQ.jpg\" style=\"height:359px; width:640px\" />\u6c34\u7a3b\u80b2\u82d7\u548c\u63d2\u79e7</p>\r\n\r\n<p>2000\u5e74\u4ee5\u524d\uff0c\u5f88\u591a\u5730\u65b9\u90fd\u662f\u4eba\u5de5\u63d2\u79e7\u30021992\u5e74\uff0c\u6211\u66fe\u7ecf\u5728\u5185\u8499\u53e4\u624e\u8d49\u7279\u65d7\u62c5\u4efb\u519c\u4e1a\u6280\u672f\u5458\uff0c\u6307\u5bfc5000\u4ea9\u6c34\u7a3b\u7684\u80b2\u82d7\u5de5\u4f5c\u3002\u81f3\u4eca\u56de\u60f3\uff0c\u5f2f\u8170\u63d2\u79e7\u662f\u6700\u4e3a\u8270\u82e6\u7684\u519c\u4e8b\u52b3\u4f5c\u3002\u6c34\u4e2d\u6709\u6c34\u86ed\uff0c\u4fd7\u79f0\u8682\u87e5\uff0c\u53ee\u54ac\u5728\u8eab\u4e0a\u540e\uff0c\u4f1a\u94bb\u5230\u8089\u91cc\u5438\u8840\u3002\u4eca\u5929\u8d8a\u6765\u8d8a\u591a\u7684\u7a3b\u7530\u90fd\u662f\u673a\u68b0\u63d2\u79e7\uff0c\u751a\u81f3\u6709\u4e86GPS\u5b9a\u4f4d\u7684\u65e0\u4eba\u9a7e\u9a76\u63d2\u79e7\u673a\u3002\u4e00\u53f0\u63d2\u79e7\u673a\u7684\u529f\u6548\u76f8\u5f53\u4e8e20\u4e2a\u4eba\u3002</p>\r\n\r\n<p>\u5f53\u7136\uff0c\u673a\u68b0\u5316\u4e5f\u4e0d\u662f\u5b8c\u7f8e\u65e0\u7f3a\u3002\u4e3e\u4e2a\u4f8b\u5b50\uff1a\u79e7\u82d7\u5982\u540c\u5a74\u513f\uff0c\u4eba\u624b\u4f1a\u52a0\u4ee5\u5475\u62a4\u3002\u4f46\u673a\u5668\u5374\u4e0d\u61c2\u8fd9\u4e9b\uff0c\u5bf9\u6c34\u7a3b\u79e7\u82d7\u6293\u53d6\u7b80\u5355\u7c97\u66b4\uff0c\u5bb9\u6613\u4f24\u6839\u4f24\u82d7\u3002\u9047\u5230\u4e0d\u5e73\u6574\u7684\u571f\u5730\uff0c\u4e5f\u4e0d\u638c\u63e1\u6df1\u6d45\uff0c\u63d2\u79e7\u8d28\u91cf\u4e0d\u597d\uff0c\u751a\u81f3\u4f1a\u6f02\u82d7\u3002\u7b80\u8a00\u4e4b\uff0c\u673a\u68b0\u63d2\u79e7\u7684\u6c34\u7a3b\u4ea7\u91cf\u6bd4\u4eba\u5de5\u63d2\u79e7\u8981\u5c115-10%\u3002\u6240\u4ee5\u4ecd\u6709\u4e9b\u7cbe\u6253\u7ec6\u7b97\u7684\u519c\u6c11\u575a\u6301\u4eba\u5de5\u63d2\u79e7\u3002</p>\r\n\r\n<p><strong>\u7530\u95f4\u7ba1\u7406\uff1a\u9504\u79be\u65e5\u5f53\u5348</strong></p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/qp793_MvlzOLh0xXTkB8WAhwnh-CUoE7ZWbvSL6dIMo4BAAAYQIAAEpQ.jpg\" style=\"height:361px; width:640px\" />\u7530\u95f4\u7ba1\u7406</p>\r\n\r\n<p>\u4e3a\u4e86\u5b69\u5b50\u957f\u5927\u6210\u4eba\uff0c\u592a\u591a\u7684\u7236\u6bcd\u8d77\u65e9\u8d2a\u9ed1\u3001\u65e9\u63a5\u665a\u9001\uff0c\u503e\u6ce8\u65f6\u95f4\u7cbe\u529b&mdash;&mdash;\u4e0d\u517b\u513f\u4e0d\u77e5\u7236\u6bcd\u6069\u3002\u8c37\u7269\u548c\u4eba\u4e00\u6837\uff0c\u4e5f\u9700\u8981\u519c\u4eba\u7cbe\u8015\u7ec6\u4f5c\u3002\u4e0a\u9762\u8fd9\u79cd\u56fe\u7247\u5c31\u662f\u65bd\u80a5\u3001\u55b7\u836f\u3001\u9664\u8349\u548c\u704c\u6e89\u7b49\u7530\u95f4\u7ba1\u7406\u3002\u5927\u5bb6\u60f3\u60f3\uff1a\u5982\u679c\u8ba9\u4f60\u9876\u7740\u708e\u708e\u70c8\u65e5\uff0c\u7a7f\u7740\u539a\u5916\u8863\u5728\u7530\u91ce\u4e2d\u52b3\u4f5c\uff0c\u4f60\u80fd\u575a\u6301\u591a\u4e45\uff1f\u519c\u6c11\u662f\u7528\u4e0d\u8d77\u9632\u6652\u971c\u7684\uff0c\u8138\u988a\u88ab\u6652\u5f97\u9ed1\u7ea2\u3002\u8bf7\u5927\u5bb6\u7279\u522b\u6ce8\u610f\u55b7\u6d12\u519c\u836f\u8fd9\u5f20\u56fe\u7247\uff1a\u519c\u6c11\u4e3a\u4ec0\u4e48\u90fd\u6234\u7740\u53e3\u7f69\uff1f\u4e0d\u5c11\u519c\u836f\u5bf9\u4eba\u4f53\u662f\u6709\u6bd2\u5bb3\u7684\uff0c\u6709\u4e9b\u751a\u81f3\u662f\u5267\u6bd2\u3002\u4e2d\u56fd\u67091/3\u7684\u519c\u6c11\u5728\u55b7\u6d12\u519c\u836f\u8fc7\u7a0b\u4e2d\u66fe\u7ecf\u4e0d\u540c\u7a0b\u5ea6\u5730\u906d\u53d7\u8fc7\u519c\u836f\u4e2d\u6bd2\u3002</p>\r\n\r\n<p><strong>\u7cae\u98df\u771f\u7684\u6765\u4e4b\u4e0d\u6613\uff0c\u5e0c\u671b\u5927\u5bb6\u80fd\u591f\u73cd\u60dc\u3002</strong></p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/8EcYU2-2ck99iL4eCxKmfcoAXdnKdcsAgJ11Hcy6jyqEAgAAvwEAAEpQ.jpg\" style=\"height:444px; width:640px\" />\u5927\u8c46\u6839\u7624\u83cc | Wikimedia Commons</p>\r\n\r\n<p>\u5c0f\u9ea6\u3001\u7389\u7c73\u3001\u6c34\u7a3b\u3001\u9ad8\u7cb1\u7b49\u8c37\u7269\u7b49\u591a\u6570\u8c37\u7269\u90fd\u662f\u79be\u672c\u79d1\u690d\u7269\uff0c\u800c\u5927\u8c46\u3001\u8695\u8c46\u3001\u8c4c\u8c46\u3001\u7eff\u8c46\u3001\u82b1\u751f\u7b49\u5219\u5c5e\u4e8e\u8c46\u79d1\u690d\u7269\u3002&ldquo;\u5e84\u7a3c\u4e00\u679d\u82b1\uff0c\u5168\u51ed\u80a5\u5f53\u5bb6&rdquo;\uff0c\u8c37\u7269\u5728\u751f\u957f\u8fc7\u7a0b\u4e2d\u79bb\u4e0d\u5f00\u6c2e\u3001\u78f7\u3001\u94be\u7b49\u80a5\u6599\u3002\u5982\u679c\u7f3a\u5c11\u4e86\u8fd9\u4e9b\u80a5\u6599\uff0c\u5c24\u5176\u662f\u7f3a\u5c11\u4e86\u6c2e\u80a5\uff0c\u8c37\u7269\u5c31\u4f1a\u957f\u5f97\u53c8\u77ee\u53c8\u5c0f\u3002\u7a7a\u6c14\u4e2d\u542b\u6709\u5927\u91cf\u7684\u6c2e\u7d20\uff0c\u79be\u672c\u79d1\u8c37\u7269\u5374\u4e0d\u80fd\u76f4\u63a5\u5229\u7528\uff0c\u800c\u8c46\u79d1\u690d\u7269\u5374\u6709\u4e00\u4e2a\u7279\u6b8a\u7684\u672c\u9886\uff0c\u5b83\u4eec\u7684\u6839\u90e8\u90fd\u6709\u5c0f\u5c0f\u7684&ldquo;<strong>\u5730\u4e0b\u6c2e\u80a5\u5382</strong>&rdquo;&mdash;&mdash;\u6839\u7624\u3002</p>\r\n\r\n<p>\u56fa\u6c2e\u7684\u8fc7\u7a0b\u5f88\u50cf\u662f\u4e00\u573a\u604b\u7231\uff1a\u8c46\u79d1\u690d\u7269\u5728\u5e7c\u82d7\u671f\uff0c\u5176\u6839\u6bdb\u5c31\u4f1a\u5206\u6ccc\u6709\u673a\u7269\uff0c\u571f\u58e4\u4e2d\u7684\u6839\u7624\u83cc\u4fbf\u88ab\u5438\u5f15\u8fc7\u6765\uff0c\u9010\u6e10\u805a\u96c6\u5728\u6839\u6bdb\u7684\u5468\u56f4\u3002\u5f88\u5feb\uff0c\u6839\u7624\u83cc\u901a\u8fc7\u5206\u6ccc\u7269\u4fb5\u5165\u5230\u690d\u7269\u6839\u90e8\u7684\u76ae\u5c42\u7ec6\u80de\u4e2d\uff0c\u8fc5\u901f\u5b89\u8425\u624e\u5be8\u3001\u5206\u88c2\u7e41\u6b96\u3002\u773c\u770b\u7740\u6839\u7624\u83cc\u4e3b\u52a8\u6295\u6000\u9001\u62b1\uff0c\u6839\u90e8\u76ae\u5c42\u7ec6\u80de\u4e5f\u5fc3\u9886\u795e\u4f1a\uff0c\u5feb\u901f\u6269\u5efa\u4f4f\u5b85\uff0c\u8fdb\u884c\u7ec6\u80de\u5206\u88c2\uff0c\u4f53\u79ef\u81a8\u5927\u5f62\u6210\u7624\u72b6\u51f8\u8d77\uff0c\u5c31\u5f62\u6210\u4e86\u6839\u7624\u3002\u6b64\u540e\uff0c\u6839\u7624\u83cc\u5c06\u7a7a\u6c14\u4e2d\u7684\u6c2e\u8f6c\u5316\u4e3a\u690d\u7269\u80fd\u5438\u6536\u7684\u542b\u6c2e\u7269\u8d28\uff0c\u800c\u690d\u7269\u4e3a\u6839\u7624\u83cc\u63d0\u4f9b\u78b3\u6e90\u548c\u80fd\u6e90\uff0c\u4e8c\u4eba\u76f8\u4eb2\u76f8\u7231\uff0c\u65e5\u5b50\u8d8a\u8fc7\u8d8a\u7ea2\u706b&mdash;&mdash;\u8fd9\u79cd\u5173\u7cfb\u5728\u751f\u7269\u5b66\u4e0a\u88ab\u79f0\u4e4b\u4e3a<strong>\u5171\u751f\u73b0\u8c61</strong>\u3002\u6709\u4e9b\u571f\u58e4\u4e2d\u7f3a\u5c11\u6839\u7624\u83cc\uff0c\u5728\u64ad\u79cd\u8c46\u79d1\u690d\u7269\u65f6\uff0c\u540c\u65f6\u65bd\u7528\u4eba\u5de5\u57f9\u517b\u7684\u6839\u7624\u83cc\u5236\u5242\uff0c\u4e3a\u5f62\u6210\u6839\u7624\u521b\u9020\u6761\u4ef6\uff0c\u53ef\u4f7f\u5927\u8c46\u3001\u82b1\u751f\u589e\u4ea710%\u4ee5\u4e0a\u3002\u79cd\u690d\u8c46\u79d1\u4f5c\u7269\u540e\u7684\u571f\u5730\uff0c\u56e0\u4e3a\u6b8b\u7559\u4e86\u5927\u91cf\u7684\u56fa\u6c2e\u6839\u7624\uff0c\u571f\u58e4\u80a5\u529b\u5927\u5927\u63d0\u9ad8\u3002\u7b2c\u4e8c\u5e74\u8f6e\u4f5c\u7389\u7c73\u6216\u5c0f\u9ea6\u65f6\uff0c\u4e5f\u4f1a\u6709\u660e\u663e\u7684\u589e\u4ea7\u6548\u679c\u3002</p>\r\n\r\n<p><strong>\u8c37\u7269\u7684\u82b1\u6837\u5e74\u534e</strong></p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/7BG4Vfs-13Xk18yAOEOGGZFWaVu5hoFOTK59NXV5hcKAAwAA9wEAAEpQ.jpg\" style=\"height:359px; width:640px\" />\u8c37\u7269\u7684\u82b1\u6735</p>\r\n\r\n<p>\u4eba\u5230\u4e86\u9752\u6625\u671f\u4f1a\u8c08\u604b\u7231\uff0c\u79cd\u5b50\u690d\u7269\u5219\u4f1a\u626c\u82b1\u6388\u7c89\u3002\u4eba\u751f\u6709\u767e\u5e74\uff0c\u4f46\u8c37\u7269\u7684\u4e00\u751f\u53ea\u6709100\u5929\u3002\u4e3a\u4e86\u5c3d\u5feb\u751f\u5a03\uff0c\u5c31\u5f97\u7f29\u77ed\u8c08\u604b\u7231\u7684\u65f6\u95f4\uff0c\u626c\u82b1\u671f\u53ea\u6709\u4e09\u4e94\u5929\u65f6\u95f4\uff0c\u662f\u5178\u578b\u7684&ldquo;\u95ea\u5a5a&rdquo;\u3002\u82b1\u7c89\u7684\u5b58\u6d3b\u671f\u4e5f\u5f88\u77ed\uff0c\u6c34\u7a3b\u82b1\u7c89\u53ef\u5b58\u6d3b10\uff5e15\u5206\u949f\uff0c\u5c0f\u9ea6\u82b1\u7c89\u53ef\u5b58\u6d3b30\u5206\u949f\uff0c\u7389\u7c73\u82b1\u7c89\u53ef\u5b58\u6d3b5\uff5e6\u5c0f\u65f6\u3002\u82e5\u5728\u626c\u82b1\u671f\u9047\u5230\u66b4\u96e8\u3001\u72c2\u98ce\u3001\u4f4e\u6e29\u7b49\u5f02\u5e38\u5929\u6c14\uff0c\u5f71\u54cd\u6388\u7c89\uff0c\u8c37\u7269\u4ea7\u91cf\u5c06\u4f1a\u5927\u5e45\u5ea6\u964d\u4f4e\u3002</p>\r\n\r\n<p>\u690d\u7269\u8981\u7ed3\u51fa\u679c\u5b9e\u5fc5\u987b\u7ecf\u8fc7\u6388\u7c89\uff0c\u5373\u628a\u96c4\u854a\u7684\u82b1\u7c89\u4f20\u7ed9\u96cc\u854a\uff0c\u8ba9\u96cc\u854a\u53d7\u7cbe\u3002\u6c34\u7a3b\u3001\u5c0f\u9ea6\u3001\u5927\u8c46\u90fd\u5c5e\u4e8e<strong>\u81ea\u82b1\u6388\u7c89</strong>\uff0c\u800c\u7389\u7c73\u5c5e\u4e8e\u5f02\u82b1\u6388\u7c89\u3002\u8c37\u7269\u7684\u82b1\u7c89\u53ea\u80fd\u98d8\u51e0\u767e\u7c73\u8fdc\uff0c\u751a\u81f3\u53ea\u6709\u51e0\u5341\u7c73\u8fdc\u3002\u5927\u5bb6\u60f3\u60f3\u770b\uff1a\u5982\u679c\u8fd9\u8f88\u5b50\u53ea\u80fd\u548c\u51fa\u751f\u5730\u65b9\u57061\u516c\u91cc\u8303\u56f4\u5185\u7684\u4eba\u7ed3\u5a5a\uff0c\u4f60\u4f1a\u505a\u4f55\u611f\u60f3\uff1f\u6240\u4ee5\uff0c\u4eba\u6bd4\u8c37\u7269\u8981\u5e78\u798f\u5f97\u591a\uff01</p>\r\n\r\n<p>\u6309\u7167\u6388\u7c89\u65b9\u5f0f\uff0c\u690d\u7269\u4e3b\u8981\u5206\u4e3a<strong>\u866b\u5a92\u82b1\u548c\u98ce\u5a92\u82b1</strong>\u3002\u679c\u56ed\u91cc\u7ecf\u5e38\u770b\u5230\u871c\u8702\u98de\u821e\uff0c\u4f46\u5728\u8c37\u7530\u91cc\u5374\u4e0d\u4e00\u5b9a\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u7389\u7c73\u3001\u5c0f\u9ea6\u3001\u6c34\u7a3b\u3001\u9ad8\u7cb1\u7b49\u591a\u6570\u8c37\u7269\u90fd\u662f\u98ce\u5a92\u82b1\uff0c\u5411\u65e5\u8475\u3001\u6cb9\u83dc\u3001\u835e\u9ea6\u548c\u74dc\u679c\u7c7b\u5219\u662f\u866b\u5a92\u82b1\u3002\u866b\u5a92\u82b1\u662f\u559c\u6b22\u6253\u626e\u7684\u6f02\u4eae\u5973\u5b69\uff0c\u82b1\u5f62\u5927\u3001\u989c\u8272\u9c9c\u8273\uff0c\u6709\u82b3\u9999\u7684\u6c14\u5473\u548c\u751c\u7f8e\u7684\u82b1\u871c\uff0c\u4ece\u800c\u62db\u8702\u5f15\u8776\u5c06\u81ea\u5df1\u7684\u82b1\u7c89\u5e26\u5230\u53e6\u4e00\u6735\u82b1\u4e0a\u53bb\u3002\u800c\u98ce\u5a92\u82b1\u5728\u6606\u866b\u773c\u4e2d\u5219\u662f\u4f4e\u8c03\u5185\u655b\u7684\u7070\u59d1\u5a18\uff0c\u4e0d\u5c5e\u4e8e\u767d\u5bcc\u7f8e\uff1a\u82b1\u5f62\u5c0f\u4e14\u4e0d\u9c9c\u8273\uff0c\u6c14\u5473\u6de1\uff0c\u7f3a\u5c11\u82b1\u871c\u3002\u4f46\u5b83\u4eec\u82b1\u7c89\u7c92\u5c0f\u5e72\u71e5\uff0c\u5bb9\u6613\u968f\u98ce\u5439\u6563&mdash;&mdash;\u65e2\u7136\u4e0d\u80fd\u5438\u5f15\u6606\u866b\uff0c\u53ea\u80fd\u8bf7\u98ce\u6765\u505a&ldquo;\u5a92\u4eba&rdquo;\u4e86\u3002\u8fd9\u91cc\u6709\u4ef6\u4e8b\u60f3\u544a\u8bc9\u5927\u5bb6\uff1a\u5927\u8c46\u6709\u4e00\u79cd\u5f88\u7279\u522b\u7684\u6388\u7c89\u65b9\u5f0f\uff0c\u65e2\u4e0d\u7b97\u98ce\u5a92\u82b1\uff0c\u4e5f\u4e0d\u7b97\u866b\u5a92\u82b1\uff1a\u82b1\u672a\u5f00\u65f6\uff0c\u91cc\u9762\u7684\u96c4\u854a\u548c\u96cc\u854a\u5c31\u8fdb\u884c\u53d7\u7c89\uff0c\u6388\u7c89\u5b8c\u6210\u540e\u624d\u5f00\u82b1\uff0c\u751f\u7269\u5b66\u4e0a\u79f0\u4e4b\u4e3a&ldquo;<strong>\u95ed\u82b1\u6388\u7c89</strong>&rdquo;\u3002\u76f8\u5f53\u4e8e\u4e24\u4eba\u4ece\u5c0f\u9752\u6885\u7af9\u9a6c\uff0c\u7236\u6bcd\u8001\u65e9\u5c31\u7ed9\u5b69\u5b50\u5b9a\u4e86\u5a03\u5a03\u4eb2\uff0c\u6839\u672c\u5c31\u4e0d\u7528\u98ce\u548c\u866b\u6765\u505a\u5a92\uff0c\u5916\u4eba\uff08\u5f02\u82b1\uff09\u4e5f\u752d\u60e6\u8bb0\uff0c\u4eba\u5bb6\u662f\u5148\u5165\u6d1e\u623f\uff08\u6388\u7c89\uff09\uff0c\u540e\u6446\u5a5a\u5bb4\uff08\u5f00\u82b1\uff09\u3002</p>\r\n\r\n<p>\u6211\u4eec\u5728\u8fdb\u884c\u623f\u5c4b\u88c5\u4fee\u7684\u65f6\u5019\uff0c\u4e0d\u4ec5\u8981\u8003\u8651\u684c\u6905\u677f\u51f3\u7684\u6446\u653e\uff0c\u4e5f\u8981\u8bbe\u8ba1\u597d\u8863\u67dc\u3001\u58c1\u6a71\u548c\u540a\u67dc\uff0c\u4f18\u5316\u5229\u7528\u7a7a\u95f4\u8d44\u6e90\u3002\u7530\u91ce\u4e5f\u4e00\u6837\uff0c\u79cd\u690d\u5e03\u5c40\u65f6\u9700\u8981\u8003\u8651\u901a\u98ce\u548c\u900f\u5149\u4e8b\u5b9c\u3002\u5728\u5343\u767e\u5e74\u7684\u519c\u8015\u5b9e\u8df5\u4e2d\uff0c\u6211\u4eec\u7684\u7956\u5148\u6478\u7d22\u51fa\u4e00\u5957\u5408\u7406\u7684\u8015\u4f5c\u5236\uff1a\u8ba9\u7389\u7c73\u4e0e\u5927\u8c46\u6216\u5c0f\u9ea6\u7a7f\u63d2\u79cd\u690d\uff08<strong>\u95f4\u4f5c</strong>\uff09\uff0c\u5373\u6709\u5229\u4e8e\u571f\u58e4\u517b\u5206\u7684\u5e73\u8861\uff0c\u4e5f\u901a\u8fc7&ldquo;\u9ad8\u77ee\u642d\u914d&rdquo;\uff0c\u63d0\u9ad8\u901a\u98ce\u548c\u5149\u5408\u6548\u7387\uff0c\u540c\u65f6\u7269\u79cd\u591a\u6837\u53ef\u4ee5\u51cf\u5c11\u75c5\u5bb3\u3002</p>\r\n\r\n<p>\u571f\u58e4\u548c\u4eba\u4e00\u6837\uff0c\u4e5f\u9700\u8981\u4fee\u751f\u517b\u606f\u3002\u5982\u679c\u5728\u4e00\u7247\u8015\u5730\u4e0a\u8fde\u5e74\u91cd\u590d\u79cd\u690d\u540c\u4e00\u79cd\u8c37\u7269\uff08<strong>\u8fde\u4f5c</strong>\uff09\uff0c\u4f5c\u7269\u79cd\u7c7b\u548c\u8425\u517b\u7ed3\u6784\u5355\u4e00\uff0c\u5bb9\u6613\u7206\u53d1\u75c5\u5bb3\uff0c\u571f\u58e4\u4e5f\u4f1a\u79ef\u52b3\u6210\u75be\u3002\u4e3a\u4e86\u53ef\u6301\u7eed\u79cd\u690d\uff0c\u7f8e\u56fd\u3001\u6b27\u76df\u548c\u65e5\u672c\u7b49\u56fd\u5bb6\u5efa\u7acb\u4e86\u4f11\u8015\u5236\u5ea6\uff0c\u9009\u62e9\u5408\u9002\u7684\u5e74\u4efd\uff0c\u8ba9\u5730\u50cf\u4eba\u4e00\u6837\u4e5f\u6b47\u606f\u4e00\u4e0b\uff0c\u8eab\u5fc3\u653e\u677e\u5730\u8fc7\u4e2a\u5047\u671f\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/9Osinz92LRtKrZebtv8RComHQlRIcRglvdQsmad08fo4BAAAXgEAAEpQ.jpg\" style=\"height:207px; width:640px\" />\u7389\u7c73/\u5927\u8c46\u95f4\u4f5c\u548c\u7389\u7c73/\u5c0f\u9ea6\u95f4\u4f5c</p>\r\n\r\n<p>\u521a\u624d\u8bf4\u5230&ldquo;\u9ad8\u77ee\u642d\u914d&rdquo;\uff0c\u8bf7\u5927\u5bb6\u770b\u770b\u4e0a\u9762\u7684\u56fe\u7247\uff1a\u5c0f\u9ea6\u548c\u5927\u8c46\u7684\u8eab\u9ad8\u53ea\u67091\u7c73\uff0c\u800c\u7389\u7c73\u7684\u8eab\u9ad8\u5219\u662f2\u7c73\u591a\uff0c\u662f\u8c37\u7269\u4e2d\u7684&ldquo;\u59da\u660e&rdquo;\u3002\u800c\u4e14\u5728\u4ea7\u91cf\u4e0a\uff0c\u7389\u7c73\u4e5f\u662f\u8c37\u7269\u4e2d\u7684\u51a0\u519b\u3002\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\uff1f\u7389\u7c73\u548c\u9ad8\u7cb1\u3001\u7518\u8517\u8d77\u6e90\u4e8e\u70ed\u5e26\uff0c\u662f\u5f88\u7279\u522b\u7684<strong>C4\u690d\u7269</strong>\uff0c\u6709\u72ec\u7279\u7684\u5149\u5408\u4f5c\u7528\u673a\u7406\uff0c\u5149\u5408\u6548\u7387\u660e\u663e\u9ad8\u4e8e\u5c0f\u9ea6\u3001\u6c34\u7a3b\u3001\u5927\u8c46\u7b49<strong>C3\u690d\u7269</strong>\u3002\u7136\u800c\u8fd9\u4e9b\u8c37\u7269\u5c5e\u4e8e\u4e0d\u540c\u7684\u7269\u79cd\uff0c\u5b58\u5728\u751f\u6b96\u9694\u79bb\uff0c\u4e0d\u80fd\u5b9e\u73b0\u6742\u4ea4\u80b2\u79cd\u3002\u8881\u9686\u5e73\u9662\u58eb\u6b63\u5728\u901a\u8fc7\u8f6c\u57fa\u56e0\u6280\u672f\uff0c\u5c1d\u8bd5\u628a\u7389\u7c73\u7684\u9ad8\u5149\u5408\u57fa\u56e0\u8f6c\u5230\u6c34\u7a3b\u4e2d\uff0c\u4ece\u800c\u8fdb\u4e00\u6b65\u589e\u52a0\u6c34\u7a3b\u4ea7\u91cf\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/eWmIjjBfGrP6d5Ged2cnVUqIC4XQBWjDCe5J8Uy4nY5gAgAAwAIAAEpQ.jpg\" style=\"height:704px; width:608px\" />\u5272\u8349\u7528\u7684\u7af9\u7b50 | \u6444\u4e8e\u6d59\u6c5f\u6d77\u5b81\u6bb3\u5bb6\u5830\u6751\u519c\u5bb6</p>\r\n\r\n<p>\u519c\u7530\u91cc\u4e0d\u4ec5\u6709\u8c37\u7269\uff0c\u8fd8\u6709\u8302\u76db\u7684\u91ce\u8349\u3002\u56de\u60f3\u5c11\u5e74\u65f6\u4ee3\uff0c\u653e\u5b66\u540e\u540c\u5b66\u4eec\u5b09\u7b11\u6253\u95f9\uff0c\u6cbf\u7740\u4e61\u95f4\u5c0f\u8def\u8d70\u56de\u6751\u91cc\u3002\u5bb6\u91cc\u5f53\u65f6\u517b\u4e86\u51e0\u5341\u53ea\u9e21\u3001\u9e2d\u3001\u9e45\u548c\u5154\u5b50\uff0c\u6211\u8981\u5148\u62ff\u8d77\u6241\u62c5\u3001\u9570\u5200\u548c\u7bee\u7b50\u53bb\u7530\u91cc\u5272\u8349\u3002\u8c37\u7269\u56e0\u4e3a\u6709\u4eba\u7c7b\u7684\u7cbe\u5fc3\u5475\u62a4\uff0c\u624d\u5f97\u4ee5\u8301\u58ee\u6210\u957f\u3002<strong>\u91ce\u8349\u662f\u6ca1\u4eba\u7231\u7684\u91ce\u5b69\u5b50\uff0c\u5374\u6709\u7740\u987d\u5f3a\u7684\u751f\u547d\u529b</strong>&mdash;&mdash;\u91ce\u706b\u70e7\u4e0d\u5c3d\uff0c\u6625\u98ce\u5439\u53c8\u751f\u3002\u6700\u4e0d\u559c\u6b22\u4e00\u79cd\u540d\u4e3a&ldquo;\u524c\u524c\u79e7&rdquo;\u7684\u91ce\u8349\uff0c\u5b83\u5728\u7530\u91ce\u91cc\u8fde\u7247\u530d\u5310\u7f20\u7ed5\u3002\u4e00\u6839\u85e4\u80fd\u6709\u51e0\u7c73\u957f\uff0c\u85e4\u4e0a\u5bc6\u5e03\u7740\u7ec6\u523a\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u522e\u5728\u76ae\u80a4\u4e0a\uff0c\u5c31\u662f\u4e00\u9053\u8840\u75d5\u3002\u60f3\u77e5\u9053\u5b83\u957f\u4ec0\u4e48\u6837\u5417\uff1f\u770b\u770b\u4e0b\u9762\u7684\u89c6\u9891\uff1a</p>\r\n\r\n<p class=\"img-caption\"><a href=\"https://v.qq.com/x/page/k3008xv4m8l.html\"><img alt=\"\" src=\"https://3-im.guokr.com/HuBMB3hoKcsVQyX9D2jaLu5b3rb2tybyKo201axgkm6nAQAA5wIAAFBO.png\" style=\"height:743px; width:423px\" /></a>\u524c\u524c\u79e7\u89c6\u9891(\u70b9\u51fb\u56fe\u7247\u53ef\u4ee5\u89c2\u770b\u89c6\u9891\uff09</p>\r\n\r\n<p>\u4eba\u6709\u65e6\u5915\u7978\u798f\uff0c\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u707e\u5bb3\u4f1a\u4e0d\u671f\u800c\u81f3\u3002\u8c37\u7269\u4e0d\u80fd\u50cf\u4eba\u4e00\u6837\u56db\u6563\u5954\u9003\uff0c\u53ea\u80fd\u542c\u5929\u7531\u547d\u3002\u4e00\u573a\u53f0\u98ce\uff0c\u8c37\u7269\u4f1a\u5927\u9762\u79ef\u5012\u4f0f\uff1b\u4e00\u573a\u51b0\u96f9\uff0c\u4f1a\u628a\u690d\u682a\u7838\u5f97\u652f\u79bb\u7834\u788e\uff1b\u7acb\u67af\u75c5\u53ef\u4ee5\u8ba9\u6c34\u7a3b\u5728\u82d7\u671f\u90fd\u6210\u7247\u67af\u840e\uff1b\u866b\u5bb3\u53ef\u4ee5\u9020\u6210\u7389\u7c7310-20%\u7684\u635f\u5931\uff1b\u4e00\u573a\u7a81\u5982\u5176\u6765\u7684\u51bb\u5bb3\uff0c\u751a\u81f3\u53ef\u4ee5\u8ba9\u8c37\u7269\u7edd\u6536\u3002</p>\r\n\r\n<p class=\"img-caption\"><a href=\"https://v.qq.com/x/page/x30086v6j1c.html\"><img alt=\"\" src=\"https://3-im.guokr.com/PKgUw3EYEpY45JpQ7LML1mtcaMM1KvWmo5hIitl8CGXbAgAAUAEAAFBO.png\" style=\"height:294px; width:640px\" /></a>\u53f0\u98ce\u8fc7\u540e\uff0c\u7a3b\u7530\u6210\u7247\u5012\u4f0f | \u6444\u4e8e\u5409\u6797\u5e02\u5b64\u5e97\u5b50\u9547(\u70b9\u51fb\u56fe\u7247\u53ef\u4ee5\u89c2\u770b\u89c6\u9891\uff09</p>\r\n\r\n<p>\u66fe\u6709\u670b\u53cb\u95ee\u6211\uff1a\u4e3a\u4ec0\u4e48\u51ac\u5c0f\u9ea6\u51ac\u5929\u51bb\u4e0d\u6b7b\uff1f<strong>\u8fdb\u5165\u8d8a\u51ac\u671f\uff0c\u5c0f\u9ea6\u7ec6\u80de\u6db2\u4e2d\u7684\u542b\u7cd6\u91cf\u589e\u52a0\uff0c\u4f7f\u7ed3\u51b0\u70b9\u964d\u4f4e\uff0c\u63d0\u9ad8\u4e86\u6297\u5bd2\u80fd\u529b\u3002</strong>\u51ac\u5929\u91cc\uff0c\u73af\u536b\u5de5\u4eba\u5411\u51b0\u96ea\u8986\u76d6\u7684\u9053\u8def\u4e0a\u6492\u76d0\u878d\u96ea\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\u3002\u53e6\u5916\uff0c\u4e00\u573a\u5927\u96ea\u540e\uff0c\u79ef\u96ea\u8986\u76d6\u5728\u5c0f\u9ea6\u4e0a\uff0c\u9694\u7edd\u4e86\u5bd2\u51b7\u7684\u7a7a\u6c14, \u76f8\u5f53\u4e8e\u7ed9\u5c0f\u9ea6\u76d6\u4e86\u4e00\u5e8a\u5fa1\u5bd2\u7684\u68c9\u88ab\uff0c\u8d77\u5230\u4e86\u4fdd\u6e29\u7684\u4f5c\u7528\u3002\u6240\u4ee5\u6709\u8fd9\u6837\u4e00\u53e5\u519c\u8c1a\uff1a\u4eca\u51ac\u9ea6\u76d6\u4e09\u5c42\u88ab\uff0c\u6765\u5e74\u6795\u7740\u9992\u5934\u7761\u3002</p>\r\n\r\n<p>\u6625\u5b63\u64ad\u79cd\uff0c\u8c37\u7269\u6839\u690d\u6c83\u571f\uff0c\u6e90\u6e90\u4e0d\u65ad\u5730\u4ece\u6ce5\u571f\u4e2d\u6c72\u53d6\u8425\u517b\uff0c\u830e\u53f6\u633a\u51fa\u5730\u9762\u8fdb\u884c\u5149\u5408\u4f5c\u7528\uff0c\u5408\u6210\u6709\u673a\u7269\uff0c\u6700\u540e\u5c06\u5927\u91cf\u517b\u5206\u8f93\u9001\u5230\u79cb\u5929\u7684\u8c37\u7c92\u4e4b\u4e2d\uff0c\u5b8c\u6210\u4e86\u4e00\u4e2a\u751f\u957f\u5b63\u7684\u4f7f\u547d\u3002\u6625\u64ad\u4e00\u7c92\u79cd\uff0c\u79cb\u6536\u4e07\u62c5\u7cae\uff0c\u8fd9\u662f\u8c37\u7269\u4e16\u754c\u7684\u751f\u547d\u8f6e\u56de\u3002\u4e00\u7c92\u79cd\u5b50\u5230\u4e0b\u4e00\u4ee3\u80fd\u591f\u7e41\u80b2\u51fa\u591a\u5c11\u7c92\u79cd\u5b50\uff1f\u5927\u8c46\u5927\u7ea6\u662f50\u7c92\uff0c\u800c\u5c0f\u9ea6\u3001\u6c34\u7a3b\u548c\u7389\u7c73\u5219\u53ef\u4ee5\u8fbe\u5230150\u7c92\u3001250\u7c92\u548c500\u7c92\u3002\u600e\u4e48\u6837\uff0c\u8c37\u7269\u7684\u7e41\u6b96\u80fd\u529b\u6bd4\u4eba\u7c7b\u5f3a\u5927\u5f97\u591a\u5427\uff01\u8fd9\u79cd\u5dee\u5f02\u4e5f\u5bfc\u81f4\u4e86\u8c37\u7269\u7684\u5355\u4f4d\u9762\u79ef\u4ea7\u91cf\u6392\u5e8f\uff1a\u5927\u8c46&lt;\u5c0f\u9ea6&lt;\u6c34\u7a3b&lt;\u7389\u7c73\u3002</p>\r\n\r\n<p class=\"img-caption\"><a href=\"https://v.qq.com/x/page/y30081a385p.html\"><img alt=\"\" src=\"https://2-im.guokr.com/yijlStmxymSDxyN9bnA1AIYfb7RqXNs-qDYxNmvderFPAwAAzgEAAFBO.png\" style=\"height:349px; width:640px\" /></a>\u6c34\u7a3b\u6536\u5272 | \u6444\u4e8e\u5409\u6797\u5e02\u6625\u65b0\u751f\u6001\u5bb6\u5ead\u519c\u573a(\u70b9\u51fb\u56fe\u7247\u53ef\u4ee5\u89c2\u770b\u89c6\u9891\uff09</p>\r\n\r\n<p><strong>\u8c37\u7269\u6536\u83b7\u540e\uff0c\u8fd8\u8981\u7ecf\u8fc7\u8131\u58f3\u3001\u53bb\u7ce0\u7b49\u78be\u7c73\u5de5\u5e8f\uff0c\u624d\u80fd\u6700\u7ec8\u53d8\u6210\u53a8\u623f\u91cc\u7684\u7c73\u9762\u3002</strong>\u8fd9\u4e2a\u8fc7\u7a0b\u6709\u4e9b\u50cf\u73a9\u4fc4\u7f57\u65af\u5957\u5a03\uff0c\u4e00\u5c42\u5c42\u8715\u76ae\u3002\u4e00\u65a4\u7a3b\u8c37\u4e03\u4e24\u7c73\uff0c\u4e00\u65a4\u5c0f\u9ea6\u4e03\u4e24\u9762\uff0c&ldquo;\u51fa\u7387&rdquo;\u5927\u81f4\u662f70%\u3002\u5176\u5b9e\uff0c\u8c37\u7c92\u88ab\u78e8\u6389\u90e8\u5206\u662f\u76ae\u5c42\u548c\u80da\uff0c\u8425\u517b\u4ef7\u503c\u975e\u5e38\u9ad8\uff0c\u5bcc\u542b\u86cb\u767d\u8d28\u548c\u591a\u79cd\u7ef4\u751f\u7d20\u3002\u5927\u5bb6\u5403\u8fc7\u989c\u8272\u5fae\u8910\u7684\u5168\u9ea6\u9762\u5305\u5427\uff1f\u5176\u5b9e\u5c31\u662f\u7528\u4fdd\u7559\u5916\u9762\u9eb8\u76ae\u548c\u9ea6\u80da\u7684\u5168\u9ea6\u9762\u7c89\u5236\u4f5c\u7684\u9762\u5305\u3002</p>\r\n\r\n<p><strong>\u8bd7\u548c\u8fdc\u65b9</strong></p>\r\n\r\n<p>\u5728\u519c\u8015\u65f6\u4ee3\uff0c\u4eba\u4eec\u7ecf\u5386\u5bd2\u6691\u6613\u8282\uff0c\u4ed8\u51fa\u8270\u8f9b\u52b3\u4f5c\uff0c<strong>\u4e00\u5206\u8015\u8018\u65b9\u6709\u4e00\u5206\u6536\u83b7</strong>\u3002\u7b80\u5355\u800c\u6162\u8282\u594f\u7684\u751f\u6d3b\u4e2d\uff0c\u6709\u9676\u6e0a\u660e\u3001\u738b\u7ef4\u548c\u5b5f\u6d69\u7136\u7684\u7530\u56ed\u8bd7\u53e5\uff0c\u6cb3\u6c34\u662f\u6e05\u6f88\u7684\uff0c\u8c37\u7269\u662f\u6709\u5473\u9053\u7684\u3002\u4eba\u4eec\u5fc3\u6001\u5e73\u548c\u3001\u4ece\u5bb9\u3001\u6709\u8010\u5fc3\uff0c\u52e4\u4fed\u6301\u5bb6\uff0c\u548c\u7766\u76f8\u5904\uff0c\u5c0a\u8001\u7231\u5e7c\uff0c\u7b11\u5bb9\u662f\u771f\u5b9e\u7684\uff0c\u4eba\u4eec\u662f\u5feb\u4e50\u7684\u3002</p>\r\n\r\n<p>\u8fdb\u5165\u4e92\u8054\u7f51\u65f6\u4ee3\uff0c\u7f51\u7ea2\u6613\u901d\uff0c\u65b0\u5ba0\u592a\u591a\uff0c\u5fc3\u6001\u8ff7\u60d1\uff0c\u7126\u8651\u5145\u65a5\uff0c\u5a31\u4e50\u81f3\u6b7b\uff0c\u552f\u6211\u72ec\u5c0a\u3002\u5927\u5bb6\u90fd\u5728\u8ffd\u6c42\u5feb\u8282\u594f\uff0c\u82db\u6c42\u5feb\u9012\u5c0f\u54e5\u4e89\u5206\u593a\u79d2\u3001\u9a6c\u4e0a\u5c31\u5230\uff0c\u770b\u4e00\u7bc7\u6587\u7ae0\u7684\u8010\u5fc3\u751a\u81f3\u6ca1\u670910\u5206\u949f\uff0c\u5fc3\u6d6e\u6c14\u8e81\u3001\u97ad\u6253\u5feb\u725b\u3001\u62d4\u82d7\u52a9\u957f\uff0c\u5e7b\u60f3\u80fd\u627e\u5230\u4e00\u672c\u4e07\u5229\u3001\u4e00\u591c\u66b4\u5bcc\u7684\u6377\u5f84\u3002</p>\r\n\r\n<p>\u7136\u800c\u7f8e\u597d\u7684\u4e8b\u7269\u5c31\u662f\u9700\u8981\u7ecf\u53d7\u5bd2\u6691\u98ce\u971c\u7684\u6d17\u793c\uff0c\u4e00\u8e74\u800c\u5c31\u7684\u4e1c\u897f\u5f80\u5f80\u7f3a\u5c11\u5e95\u8574\u3002\u8fd9\u4e2a\u793e\u4f1a\uff0c\u771f\u7684\u9700\u8981\u6162\u4e0b\u6765\u4e00\u4e9b\uff0c\u8ba9\u5fc3\u7075\u8ddf\u4e0a\u811a\u6b65\uff01</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/nQGoJMxiYjkWzGfVrH2AgCA--i5F7u0odw1e7ycDQ_mkAwAAKgIAAEpQ.jpg\" style=\"height:380px; width:640px\" />\u79cb\u5929\u7684\u7a3b\u7530 | \u6444\u4e8e\u5409\u6797\u5e02\u6625\u65b0\u751f\u6001\u5bb6\u5ead\u519c\u573a</p>\r\n\r\n<p>\u300a\u6211\u548c\u6211\u7684\u7956\u56fd\u300b\u5531\u5230\uff1a&ldquo;\u6211\u6b4c\u5531\u6bcf\u4e00\u5ea7\u9ad8\u5c71\uff0c\u6211\u6b4c\u5531\u6bcf\u4e00\u6761\u6cb3\u3002\u8885\u8885\u708a\u70df\u5c0f\u5c0f\u6751\u843d\uff0c\u8def\u4e0a\u4e00\u9053\u8f99\u3002&rdquo;\u66fe\u7ecf\uff0c\u6211\u4eec\u7684\u7956\u5148\u65e5\u51fa\u800c\u4f5c\uff0c\u65e5\u843d\u800c\u606f\uff0c\u65e5\u590d\u4e00\u65e5\uff0c\u5e74\u590d\u4e00\u5e74\u3002\u5343\u767e\u5e74\u540e\uff0c\u4eba\u7c7b\u811a\u4e0b\u4f9d\u7136\u662f\u539a\u5fb7\u8f7d\u7269\u7684\u571f\u5730\uff0c\u4eba\u7c7b\u8d56\u4ee5\u7ef4\u6301\u751f\u547d\u7684\u4f9d\u7136\u662f\u7c73\u3001\u9762\u3001\u7cae\u3001\u6cb9\u3002\u76f8\u4fe1\u79d1\u6280\u8fdb\u6b65\u80fd\u591f\u8ba9\u4f20\u7edf\u519c\u4e1a\u8131\u80ce\u6362\u9aa8\uff0c\u66f4\u5e0c\u671b<strong>4000\u5e74\u534e\u590f\u519c\u8015\u6587\u660e\u79ef\u6dc0\u4e0b\u6765\u7684\u6c11\u65cf\u7cbe\u795e\uff0c\u80fd\u591f\u54fa\u80b2\u6211\u4eec\u8d70\u5411\u81ea\u5f3a\u3001\u751f\u751f\u4e0d\u606f\uff01</strong>\uff08\u7f16\u8f91\uff1aYuki\uff09</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#808080\"><strong>\u81f4\u8c22\uff1a</strong>\u5728\u5199\u4f5c\u8fc7\u7a0b\u4e2d\uff0c\u5f97\u5230\u4e86\u738b\u8574\u6ce2\u6559\u6388\u3001\u5f20\u541b\u6559\u6388\u3001\u6768\u6c5f\u4e49\u6559\u6388\u548c\u738b\u8bed\u6674\u3001\u5f20\u5434\u7aeb\u3001\u5434\u60a0\u60a0\u3001\u5b89\u82ae\u5609\u3001\u8363\u6bd3\u8431\u3001\u5d14\u7b71\u91ce\u7b49\u540c\u5b66\u7684\u542f\u53d1\u548c\u5efa\u8bae\uff0c\u5728\u6b64\u4e00\u5e76\u81f4\u8c22\uff01</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#808080\">*\u672c\u6587\u6839\u636e\u4f5c\u8005\u5728&ldquo;\u676d\u5dde\u79d1\u5b66\u5927\u8bb2\u5802&rdquo;\u7684\u79d1\u666e\u8bb2\u5ea7\u6587\u7a3f\u6574\u7406\u4fee\u6539\u800c\u6210\u3002</span></span></p>", "date_created": "2019-10-16T13:02:59.547664+08:00", "date_modified": "2019-10-30T11:50:17.964982+08:00", "date_published": "2019-10-16T17:19:10.546228+08:00", "id": 454871, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": null, "replies_count": 8, "small_image": "https://3-im.guokr.com/pjMQiF7e4JfAoChPdNzalkWt-mBavR4A0kubDrjPnCnAAwAAgAIAAEpQ.jpg", "subject": {"articles_count": 87, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "agronomy", "minisite_key": "None", "name": "\u519c\u5b66", "sort_score": 15, "subject_type": "normal"}, "subject_key": "agronomy", "summary": "\u300a\u8c37\u7269\u7684\u6545\u4e8b\u300b2\uff1a\u8c37\u7269\u7684\u4e00\u751f\u3002", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u4e00\u65e5\u4e09\u9910\uff0c\u6211\u4eec\u6bcf\u5929\u90fd\u4f1a\u4e0e\u8c37\u7269\u89c1\u9762\uff1b\u79bb\u5f00\u9910\u684c\uff0c\u8c37\u7269\u4f3c\u4e4e\u79bb\u6211\u4eec\u5f88\u9065\u8fdc\u3002\u8bb8\u591a\u4e45\u5c45\u57ce\u5e02\u7684\u670b\u53cb\u4eec\uff0c\u751a\u81f3\u6e10\u6e10\u53d8\u5f97\u201c\u4e94\u8c37\u4e0d\u5206\u201d\u3002\u8c01\u77e5\u76d8\u4e2d\u9910\uff0c\u7c92\u7c92\u7686\u8f9b\u82e6\u3002\u8c37\u7269\u662f\u5982\u4f55\u4ece\u91ce\u8349\u8fdb\u5316\u6210\u4eca\u5929\u7684\u6837\u5b50\uff1f\u4ece\u4e00\u7c92\u79cd\u5b50\u6210\u957f\u5230\u4e00\u682a\u8c37\u7269\u7ecf\u8fc7\u4e86\u54ea\u4e9b\u5386\u7a0b\uff1f\u8c37\u7530\u91cc\u4f1a\u6709\u600e\u6837\u7684\u52a8\u7269\u4e16\u754c\uff1f\u4e2d\u56fd\u7684\u8015\u5730\u80fd\u517b\u6d3b14\u4ebf\u4eba\u5417\uff1f\u6c5f\u5357\u5927\u5b66\u98df\u54c1\u5de5\u7a0b\u535a\u58eb\uff0c\u300a\u4e2d\u56fd\u98df\u54c1\u4ea7\u4e1a\u5730\u56fe\u300b\u4e3b\u7f16\u5d14\u51ef\u8001\u5e08\uff0c\u5c06\u7ed3\u5408\u81ea\u7136\u3001\u79d1\u6280\u3001\u5386\u53f2\u3001\u8bd7\u6b4c\u548c\u91ce\u8da3\uff0c\u5c06\u7528\u751f\u52a8\u5e7d\u9ed8\u7684\u8bed\u8a00\u8c08\u8c08\u201c\u76d8\u4e2d\u9910\u201d\u548c4000\u5e74\u7684\u534e\u590f\u519c\u8015\u6587\u660e\uff0c\u8bb2\u8ff0\u4e00\u6bb5\u522b\u5f00\u751f\u9762\u7684\u300a\u8c37\u7269\u7684\u6545\u4e8b\u300b\u3002\u8c37\u7269\u7684\u4e00\u751f\u201c\u4ece\u660e\u5929\u8d77\uff0c\u505a\u4e00\u4e2a\u5e78\u798f\u7684\u4eba\u3002\u5582\u9a6c\uff0c\u5288\u67f4\uff0c\u5468\u6e38\u4e16\u754c\u3002\u4ece\u660e\u5929\u8d77\uff0c\u5173\u5fc3\u7cae\u98df\u548c\u852c\u83dc\uff0c\u6211\u6709\u4e00\u6240\u623f\u5b50\uff0c\u9762\u671d\u5927\u6d77\uff0c\u6625\u6696\u82b1\u5f00\u3002\u201d\u8fd9\u662f\u6d77\u5b50\u7b14\u4e0b\u7684\u4e00\u6bb5\u8bd7\u53e5\uff0c\u900f\u7740\u6d53\u6d53\u7684\u4e61\u571f\u7530\u56ed\u60c5\u6000\u3002\u8c37\u7269\u7684\u4e00\u751f\u662f\u600e\u4e48\u5ea6\u8fc7\u7684\uff1f\u4e00\u7c92\u79cd\u5b50\u5982\u4f55\u53d8\u6210\u6c89\u7538\u7538\u7684\u8c37\u7a57\uff1f\u4eca\u5929\uff0c\u6211\u6765\u548c\u5927\u5bb6\u804a\u804a\u8fd9\u4e2a\u8bdd\u9898\uff1a\u4e00\u5e74\u4e4b\u8ba1\u5728\u4e8e\u6625\u6211\u4eec\u5148\u6765\u91cd\u6e29\u8fd9\u9996\u59cb\u4e8e\u79e6\u4ee3\u7684\u300a\u4e8c\u5341\u56db\u8282\u6c14\u6b4c\u300b\uff1a\u201c\u6625\u96e8\u60ca\u6625\u6e05\u8c37\u5929\uff0c\u590f\u6ee1\u8292\u590f\u6691\u76f8\u8fde\u3002\u79cb\u5904\u9732\u79cb\u5bd2\u971c\u964d\uff0c\u51ac\u96ea\u96ea\u51ac\u5c0f\u5927\u5bd2\u3002\u201d\u56db\u5b63\u8f6e\u56de\uff0c\u5468\u800c\u590d\u59cb\u3002\u519c\u4eba\u6839\u636e\u8282\u6c14\u53d8\u5316\uff0c\u5b89\u6392\u519c\u4e8b\u3002\u6bd4\u5982\uff1a\u8c37\u96e8\u5f00\u59cb\u64ad\u8c37\u964d\u96e8\uff0c\u5c0f\u6ee1\u4ee3\u8868\u7c7d\u7c92\u5f00\u59cb\u704c\u6d46\uff0c\u4f46\u5c1a\u672a\u6210\u719f\uff0c\u53ea\u662f\u201c\u5c0f\u6ee1\u201d\u3002\u8292\u79cd\u5219\u662f\u201c\u6709\u8292\u7684\u9ea6\u5b50\u5feb\u6536\uff0c\u6709\u8292\u7684\u7a3b\u5b50\u53ef\u79cd\u201d\u3002\u5927\u5bb6\u770b\u770b\uff0c\u4e8c\u5341\u56db\u8282\u6c14\u672c\u8eab\u5c31\u662f\u8c37\u7269\u7684\u6545\u4e8b\u3002\u62d6\u62c9\u673a\u6625\u8015 | pixabay\u4fd7\u8bdd\u8bf4\uff1a\u201c\u6625\u8015\u4e0d\u80af\u5fd9\uff0c\u79cb\u540e\u8138\u997f\u9ec4\u3002\u201d\u5927\u5bb6\u60f3\u60f3\u519c\u7530\u4e3a\u4ec0\u4e48\u9700\u8981\u6625\u8015\uff1f\u4eba\u5750\u4e45\u4e86\uff0c\u8981\u7ad9\u8d77\u6765\u8212\u7f13\u4e00\u4e0b\u7b4b\u9aa8\uff0c\u6240\u4ee5\u5b66\u6821\u91cc\u4f1a\u5b89\u6392\u5b66\u751f\u5728\u8bfe\u95f4\u505a\u5e7f\u64ad\u4f53\u64cd\u3002\u540c\u6837\u7684\u9053\u7406\uff0c\u571f\u58e4\u7ecf\u8fc7\u4e00\u5e74\u56db\u5b63\u7684\u6c89\u79ef\uff0c\u4e5f\u4f1a\u53d8\u5f97\u677f\u7ed3\u3002\u5728\u64ad\u79cd\u4e4b\u524d\uff0c\u9700\u8981\u8fdb\u884c\u758f\u677e\uff0c\u8fd9\u5c06\u5229\u4e8e\u5438\u7eb3\u6c34\u5206\uff0c\u4fbf\u4e8e\u8c37\u7269\u6839\u7cfb\u4f38\u5c55\uff0c\u8fd8\u80fd\u591f\u6291\u5236\u75c5\u3001\u866b\u3001\u6742\u8349\u7684\u751f\u957f\u7e41\u80b2\uff0c\u6700\u7ec8\u63d0\u9ad8\u8c37\u7269\u7684\u4ea7\u91cf\u3002\u5f53\u7136\uff0c\u7ffb\u8015\u4e5f\u5e76\u975e\u5168\u662f\u597d\u5904\uff1a\u571f\u58e4\u91cc\u6c34\u5206\u662f\u975e\u5e38\u5b9d\u8d35\u7684\uff0c\u5c24\u5176\u5728\u5317\u65b9\u5e72\u65f1\u7684\u5730\u533a\u3002\u7ffb\u8015\u76f8\u5f53\u4e8e\u628a\u571f\u58e4\u50cf\u8863\u670d\u4e00\u6837\u667e\u6652\uff0c\u5bfc\u81f4\u6c34\u5206\u84b8\u53d1\u3002\u4eca\u5929\uff0c\u6709\u4e9b\u5730\u65b9\u4e5f\u5728\u63a8\u5e7f\u514d\u8015\u79cd\u690d\u6280\u672f\u3002\u6625\u8015\u4e4b\u540e\uff0c\u6211\u4eec\u4f1a\u5728\u7530\u91ce\u91cc\u64ad\u6492\u5e0c\u671b\u7684\u79cd\u5b50\u2014\u2014\u6563\u6587\u4e2d\u7ecf\u5e38\u4f7f\u7528\u8fd9\u6837\u7684\u8bed\u53e5\u3002\u901a\u5e38\uff0c\u5c0f\u9ea6\u3001\u7389\u7c73\u3001\u5927\u8c46\u7b49\u65f1\u7530\u4f5c\u7269\u5927\u90fd\u76f4\u63a5\u5728\u7530\u95f4\u64ad\u79cd\uff0c\u7136\u540e\u671f\u5f85\u4e00\u573a\u5927\u96e8\uff0c\u8ba9\u79cd\u5b50\u751f\u6839\u53d1\u82bd\u2014\u2014\u6625\u96e8\u8d35\u5982\u6cb9\u3002\u7136\u800c\u6c34\u7a3b\u5219\u4eab\u53d7\u4e00\u79cd\u201c\u8d85\u56fd\u6c11\u5f85\u9047\u201d\uff0c\u53ef\u4ee5\u5148\u5728\u6e29\u5ba4\u5927\u68da\u91cc\u80b2\u82d7\u4e00\u4e2a\u6708\uff0c\u518d\u79fb\u683d\u5230\u5927\u7530\u91cc\u3002\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u5411\u5929\u95ee\u51b7\u6696\u4e2d\u56fd\u5e45\u5458\u8fbd\u9614\uff0c\u5357\u5317\u6e29\u5dee\u5f88\u5927\u3002\u9686\u51ac\u65f6\u8282\uff0c\u5317\u56fd\u98ce\u5149\uff0c\u5343\u91cc\u51b0\u5c01\uff0c\u4e07\u91cc\u96ea\u98d8\u3002\u800c\u6d77\u5357\u5c9b\u4e0a\u4f9d\u7136\u8273\u9633\u9ad8\u7167\uff0c\u6e29\u6696\u5982\u590f\u3002\u4e2d\u56fd\u6e29\u5ea6\u5e26\u5212\u5206\u5316\u519c\u4f5c\u7269\u719f\u5236 | \u4e2d\u5b66\u300a\u5730\u7406\u300b\u8bf7\u5927\u5bb6\u5148\u6765\u770b\u770b\u8fd9\u5f20\u4e2d\u56fd\u6e29\u5ea6\u5e26\u5206\u5e03\u56fe\u3002\u5728\u5317\u65b9\u6e29\u5e26\uff0c\u5f88\u591a\u5730\u65b9\u4e00\u5e74\u53ea\u80fd\u79cd\u690d1\u5b63\u8c37\u7269\uff0c\u800c\u5728\u5357\u65b9\u4e9a\u70ed\u5e26\uff0c\u5219\u53ef\u4ee5\u79cd\u690d2-3\u5b63\u8c37\u7269\u3002\u65e0\u8bba\u5357\u5317\uff0c\u6bcf\u5b63\u4f5c\u7269\u90fd\u53ea\u6709\u7ea6100\u5929\u7684\u751f\u957f\u671f\u3002\u519c\u65f6\u4e0d\u7b49\u4eba\uff0c\u8ba9\u6c34\u7a3b\u63d0\u524d\u5728\u5927\u68da\u4e2d\u80b2\u79e7\uff0c\u76f8\u5f53\u4e8e\u589e\u52a0\u4e861\u4e2a\u6708\u7684\u751f\u957f\u671f\u3002\u53e6\u5916\uff0c\u6c34\u7a3b\u7684\u82d7\u671f\u76f8\u5f53\u4e8e\u4eba\u7684\u5a74\u5e7c\u513f\u9636\u6bb5\uff0c\u82d7\u5e8a\u76f8\u5f53\u4e8e\u80b2\u5a74\u623f\uff0c\u8fd9\u91cc\u53ef\u4ee5\u63a7\u5236\u5149\u7167\u3001\u6e29\u5ea6\u548c\u6c34\u5206\uff0c\u51cf\u5c11\u75c5\u866b\u5bb3\u3002\u7cbe\u5fc3\u914d\u5236\u7684\u82d7\u5e8a\u571f\u91cc\u5305\u542b\u4e86\u5404\u79cd\u8425\u517b\u8981\u7d20\uff0c\u8ba9\u6839\u7cfb\u53d1\u80b2\u5f3a\u58ee\uff0c\u8fd9\u76f8\u5f53\u4e8e\u7ed9\u5c0f\u670b\u53cb\u5403\u4e0a\u914d\u65b9\u5976\u7c89\uff0c\u4e3a\u4ee5\u540e\u7684\u5065\u5eb7\u6210\u957f\u6253\u4e0b\u575a\u5b9e\u7684\u57fa\u7840\u3002\u79e7\u82d7\u5728\u6e29\u5ba4\u91cc\u957f\u5230\u4e09\u7247\u534a\u53f6\u65f6\uff0c\u5f00\u59cb\u5927\u7530\u79fb\u683d\u3002\u66fe\u6709\u670b\u53cb\u95ee\u6211\uff1a\u65e2\u7136\u6709\u8fd9\u6837\u7684\u597d\u5904\uff0c\u4e3a\u4f55\u4e0d\u8ba9\u5c0f\u9ea6\u3001\u7389\u7c73\u3001\u5927\u8c46\u8fd9\u4e9b\u65f1\u7530\u4f5c\u7269\u4e5f\u5728\u6e29\u5ba4\u80b2\u82d7\u540e\u518d\u79fb\u683d\uff1f\u4ece\u6280\u672f\u4e0a\uff0c\u79fb\u683d\u7684\u79e7\u82d7\u9700\u8981\u5c3d\u5feb\u6d47\u6c34\uff0c\u7136\u800c\u65f1\u7530\u5f80\u5f80\u4e0d\u5177\u5907\u8fd9\u6837\u7684\u704c\u6e89\u6761\u4ef6\u3002\u66f4\u4e3a\u91cd\u8981\u7684\u662f\uff0c\u79fb\u683d\u8981\u8017\u8d39\u5927\u91cf\u7684\u4eba\u529b\u548c\u6210\u672c\uff0c\u519c\u6c11\u8981\u8003\u8651\u6295\u5165\u548c\u4ea7\u51fa\u4e4b\u95f4\u7684\u7ecf\u6d4e\u6548\u76ca\u3002\u7389\u7c73\u4ea7\u91cf\u9ad8\u4f46\u4ef7\u683c\u4f4e\uff0c\u5927\u8c46\u4ef7\u683c\u9ad8\u4f46\u4ea7\u91cf\u4f4e\uff0c\u53ea\u6709\u9ad8\u4ea7\u3001\u9ad8\u4ef7\u7684\u6c34\u7a3b\u80b2\u79e7\u624d\u662f\u5212\u7b97\u7684\u3002\u5343\u767e\u5e74\u6765\uff0c\u519c\u4eba\u7cbe\u6253\u7ec6\u7b97\uff0c\u60f3\u65b9\u8bbe\u6cd5\u8ba9\u8015\u5730\u6253\u51fa\u66f4\u591a\u7684\u7cae\u98df\u3002\u5728\u4e2d\u56fd\u5357\u65b9\uff0c\u5f88\u591a\u5730\u65b9\u79cd\u690d\u4e00\u5e74\u4e09\u719f\u7684\u4f5c\u7269\uff1a\u9ea6\u5b50\u3001\u65e9\u7a3b\u548c\u665a\u7a3b\uff0c\u73af\u73af\u7d27\u6263\u3002\u519c\u65f6\u4e0d\u7b49\u4eba\uff0c\u4e00\u65e6\u9519\u8fc7\u65f6\u8282\uff0c\u4ea7\u91cf\u5c31\u4f1a\u5927\u51cf\uff0c\u751a\u81f3\u7edd\u6536\u3002\u8fd9\u4e2a\u8fc7\u7a0b\u5f88\u50cf\u57ce\u5e02\u4e0a\u73ed\u9ad8\u5cf0\u671f\u7684\u5730\u94c1\u6362\u4e58\uff0c\u4e0b\u8f66\u7684\u4eba\u521a\u521a\u51b2\u51fa\u8f66\u95e8\uff0c\u4e0a\u8f66\u7684\u4eba\u9a6c\u4e0a\u5c31\u4f1a\u6d8c\u8fdb\u6765\u3002\u4ece\u4e03\u6708\u4e2d\u5230\u516b\u6708\u521d\uff0c\u4e00\u4e2a\u6708\u5185\u8981\u5728\u708e\u708e\u590f\u65e5\u4e0b\u62a2\u6536\u65e9\u7a3b\u548c\u62a2\u79cd\u665a\u7a3b\uff0c\u4fd7\u79f0\u201c\u53cc\u62a2\u201d\u2014\u2014\u8fd9\u662f\u4f20\u7edf\u519c\u6c11\u7684\u68a6\u9b47\u65f6\u5206\u3002\u4eca\u5929\uff0c\u6709\u4e9b\u670b\u53cb\u538c\u5026\u4e86\u57ce\u5e02\u91cc\u7684\u96fe\u973e\u548c\u55a7\u56a3\uff0c\u8bf4\u5230\u7530\u56ed\u751f\u6d3b\uff0c\u5c31\u60f3\u8d77\u5929\u7136\u6c27\u5427\u3001\u9752\u5c71\u7eff\u6c34\u548c\u9ad8\u5929\u6d41\u4e91\uff0c\u9752\u7fe0\u6b32\u6ef4\uff0c\u8d4f\u5fc3\u60a6\u76ee\u3002\u7136\u800c\uff0c\u8fd9\u53ea\u662f\u65c5\u884c\u8005\u7684\u5fc3\u6001\uff0c\u519c\u4e8b\u52b3\u4f5c\u8fdc\u975e\u90a3\u4e48\u6d6a\u6f2b\u3002\u6c34\u7a3b\u80b2\u82d7\u548c\u63d2\u79e72000\u5e74\u4ee5\u524d\uff0c\u5f88\u591a\u5730\u65b9\u90fd\u662f\u4eba\u5de5\u63d2\u79e7\u30021992\u5e74\uff0c\u6211\u66fe\u7ecf\u5728\u5185\u8499\u53e4\u624e\u8d49\u7279\u65d7\u62c5\u4efb\u519c\u4e1a\u6280\u672f\u5458\uff0c\u6307\u5bfc5000\u4ea9\u6c34\u7a3b\u7684\u80b2\u82d7\u5de5\u4f5c\u3002\u81f3\u4eca\u56de\u60f3\uff0c\u5f2f\u8170\u63d2\u79e7\u662f\u6700\u4e3a\u8270\u82e6\u7684\u519c\u4e8b\u52b3\u4f5c\u3002\u6c34\u4e2d\u6709\u6c34\u86ed\uff0c\u4fd7\u79f0\u8682\u87e5\uff0c\u53ee\u54ac\u5728\u8eab\u4e0a\u540e\uff0c\u4f1a\u94bb\u5230\u8089\u91cc\u5438\u8840\u3002\u4eca\u5929\u8d8a\u6765\u8d8a\u591a\u7684\u7a3b\u7530\u90fd\u662f\u673a\u68b0\u63d2\u79e7\uff0c\u751a\u81f3\u6709\u4e86GPS\u5b9a\u4f4d\u7684\u65e0\u4eba\u9a7e\u9a76\u63d2\u79e7\u673a\u3002\u4e00\u53f0\u63d2\u79e7\u673a\u7684\u529f\u6548\u76f8\u5f53\u4e8e20\u4e2a\u4eba\u3002\u5f53\u7136\uff0c\u673a\u68b0\u5316\u4e5f\u4e0d\u662f\u5b8c\u7f8e\u65e0\u7f3a\u3002\u4e3e\u4e2a\u4f8b\u5b50\uff1a\u79e7\u82d7\u5982\u540c\u5a74\u513f\uff0c\u4eba\u624b\u4f1a\u52a0\u4ee5\u5475\u62a4\u3002\u4f46\u673a\u5668\u5374\u4e0d\u61c2\u8fd9\u4e9b\uff0c\u5bf9\u6c34\u7a3b\u79e7\u82d7\u6293\u53d6\u7b80\u5355\u7c97\u66b4\uff0c\u5bb9\u6613\u4f24\u6839\u4f24\u82d7\u3002\u9047\u5230\u4e0d\u5e73\u6574\u7684\u571f\u5730\uff0c\u4e5f\u4e0d\u638c\u63e1\u6df1\u6d45\uff0c\u63d2\u79e7\u8d28\u91cf\u4e0d\u597d\uff0c\u751a\u81f3\u4f1a\u6f02\u82d7\u3002\u7b80\u8a00\u4e4b\uff0c\u673a\u68b0\u63d2\u79e7\u7684\u6c34\u7a3b\u4ea7\u91cf\u6bd4\u4eba\u5de5\u63d2\u79e7\u8981\u5c115-10%\u3002\u6240\u4ee5\u4ecd\u6709\u4e9b\u7cbe\u6253\u7ec6\u7b97\u7684\u519c\u6c11\u575a\u6301\u4eba\u5de5\u63d2\u79e7\u3002\u7530\u95f4\u7ba1\u7406\uff1a\u9504\u79be\u65e5\u5f53\u5348\u7530\u95f4\u7ba1\u7406\u4e3a\u4e86\u5b69\u5b50\u957f\u5927\u6210\u4eba\uff0c\u592a\u591a\u7684\u7236\u6bcd\u8d77\u65e9\u8d2a\u9ed1\u3001\u65e9\u63a5\u665a\u9001\uff0c\u503e\u6ce8\u65f6\u95f4\u7cbe\u529b\u2014\u2014\u4e0d\u517b\u513f\u4e0d\u77e5\u7236\u6bcd\u6069\u3002\u8c37\u7269\u548c\u4eba\u4e00\u6837\uff0c\u4e5f\u9700\u8981\u519c\u4eba\u7cbe\u8015\u7ec6\u4f5c\u3002\u4e0a\u9762\u8fd9\u79cd\u56fe\u7247\u5c31\u662f\u65bd\u80a5\u3001\u55b7\u836f\u3001\u9664\u8349\u548c\u704c\u6e89\u7b49\u7530\u95f4\u7ba1\u7406\u3002\u5927\u5bb6\u60f3\u60f3\uff1a\u5982\u679c\u8ba9\u4f60\u9876\u7740\u708e\u708e\u70c8\u65e5\uff0c\u7a7f\u7740\u539a\u5916\u8863\u5728\u7530\u91ce\u4e2d\u52b3\u4f5c\uff0c\u4f60\u80fd\u575a\u6301\u591a\u4e45\uff1f\u519c\u6c11\u662f\u7528\u4e0d\u8d77\u9632\u6652\u971c\u7684\uff0c\u8138\u988a\u88ab\u6652\u5f97\u9ed1\u7ea2\u3002\u8bf7\u5927\u5bb6\u7279\u522b\u6ce8\u610f\u55b7\u6d12\u519c\u836f\u8fd9\u5f20\u56fe\u7247\uff1a\u519c\u6c11\u4e3a\u4ec0\u4e48\u90fd\u6234\u7740\u53e3\u7f69\uff1f\u4e0d\u5c11\u519c\u836f\u5bf9\u4eba\u4f53\u662f\u6709\u6bd2\u5bb3\u7684\uff0c\u6709\u4e9b\u751a\u81f3\u662f\u5267\u6bd2\u3002\u4e2d\u56fd\u67091/3\u7684\u519c\u6c11\u5728\u55b7\u6d12\u519c\u836f\u8fc7\u7a0b\u4e2d\u66fe\u7ecf\u4e0d\u540c\u7a0b\u5ea6\u5730\u906d\u53d7\u8fc7\u519c\u836f\u4e2d\u6bd2\u3002\u7cae\u98df\u771f\u7684\u6765\u4e4b\u4e0d\u6613\uff0c\u5e0c\u671b\u5927\u5bb6\u80fd\u591f\u73cd\u60dc\u3002\u5927\u8c46\u6839\u7624\u83cc | Wikimedia Commons\u5c0f\u9ea6\u3001\u7389\u7c73\u3001\u6c34\u7a3b\u3001\u9ad8\u7cb1\u7b49\u8c37\u7269\u7b49\u591a\u6570\u8c37\u7269\u90fd\u662f\u79be\u672c\u79d1\u690d\u7269\uff0c\u800c\u5927\u8c46\u3001\u8695\u8c46\u3001\u8c4c\u8c46\u3001\u7eff\u8c46\u3001\u82b1\u751f\u7b49\u5219\u5c5e\u4e8e\u8c46\u79d1\u690d\u7269\u3002\u201c\u5e84\u7a3c\u4e00\u679d\u82b1\uff0c\u5168\u51ed\u80a5\u5f53\u5bb6\u201d\uff0c\u8c37\u7269\u5728\u751f\u957f\u8fc7\u7a0b\u4e2d\u79bb\u4e0d\u5f00\u6c2e\u3001\u78f7\u3001\u94be\u7b49\u80a5\u6599\u3002\u5982\u679c\u7f3a\u5c11\u4e86\u8fd9\u4e9b\u80a5\u6599\uff0c\u5c24\u5176\u662f\u7f3a\u5c11\u4e86\u6c2e\u80a5\uff0c\u8c37\u7269\u5c31\u4f1a\u957f\u5f97\u53c8\u77ee\u53c8\u5c0f\u3002\u7a7a\u6c14\u4e2d\u542b\u6709\u5927\u91cf\u7684\u6c2e\u7d20\uff0c\u79be\u672c\u79d1\u8c37\u7269\u5374\u4e0d\u80fd\u76f4\u63a5\u5229\u7528\uff0c\u800c\u8c46\u79d1\u690d\u7269\u5374\u6709\u4e00\u4e2a\u7279\u6b8a\u7684\u672c\u9886\uff0c\u5b83\u4eec\u7684\u6839\u90e8\u90fd\u6709\u5c0f\u5c0f\u7684\u201c\u5730\u4e0b\u6c2e\u80a5\u5382\u201d\u2014\u2014\u6839\u7624\u3002\u56fa\u6c2e\u7684\u8fc7\u7a0b\u5f88\u50cf\u662f\u4e00\u573a\u604b\u7231\uff1a\u8c46\u79d1\u690d\u7269\u5728\u5e7c\u82d7\u671f\uff0c\u5176\u6839\u6bdb\u5c31\u4f1a\u5206\u6ccc\u6709\u673a\u7269\uff0c\u571f\u58e4\u4e2d\u7684\u6839\u7624\u83cc\u4fbf\u88ab\u5438\u5f15\u8fc7\u6765\uff0c\u9010\u6e10\u805a\u96c6\u5728\u6839\u6bdb\u7684\u5468\u56f4\u3002\u5f88\u5feb\uff0c\u6839\u7624\u83cc\u901a\u8fc7\u5206\u6ccc\u7269\u4fb5\u5165\u5230\u690d\u7269\u6839\u90e8\u7684\u76ae\u5c42\u7ec6\u80de\u4e2d\uff0c\u8fc5\u901f\u5b89\u8425\u624e\u5be8\u3001\u5206\u88c2\u7e41\u6b96\u3002\u773c\u770b\u7740\u6839\u7624\u83cc\u4e3b\u52a8\u6295\u6000\u9001\u62b1\uff0c\u6839\u90e8\u76ae\u5c42\u7ec6\u80de\u4e5f\u5fc3\u9886\u795e\u4f1a\uff0c\u5feb\u901f\u6269\u5efa\u4f4f\u5b85\uff0c\u8fdb\u884c\u7ec6\u80de\u5206\u88c2\uff0c\u4f53\u79ef\u81a8\u5927\u5f62\u6210\u7624\u72b6\u51f8\u8d77\uff0c\u5c31\u5f62\u6210\u4e86\u6839\u7624\u3002\u6b64\u540e\uff0c\u6839\u7624\u83cc\u5c06\u7a7a\u6c14\u4e2d\u7684\u6c2e\u8f6c\u5316\u4e3a\u690d\u7269\u80fd\u5438\u6536\u7684\u542b\u6c2e\u7269\u8d28\uff0c\u800c\u690d\u7269\u4e3a\u6839\u7624\u83cc\u63d0\u4f9b\u78b3\u6e90\u548c\u80fd\u6e90\uff0c\u4e8c\u4eba\u76f8\u4eb2\u76f8\u7231\uff0c\u65e5\u5b50\u8d8a\u8fc7\u8d8a\u7ea2\u706b\u2014\u2014\u8fd9\u79cd\u5173\u7cfb\u5728\u751f\u7269\u5b66\u4e0a\u88ab\u79f0\u4e4b\u4e3a\u5171\u751f\u73b0\u8c61\u3002\u6709\u4e9b\u571f\u58e4\u4e2d\u7f3a\u5c11\u6839\u7624\u83cc\uff0c\u5728\u64ad\u79cd\u8c46\u79d1\u690d\u7269\u65f6\uff0c\u540c\u65f6\u65bd\u7528\u4eba\u5de5\u57f9\u517b\u7684\u6839\u7624\u83cc\u5236\u5242\uff0c\u4e3a\u5f62\u6210\u6839\u7624\u521b\u9020\u6761\u4ef6\uff0c\u53ef\u4f7f\u5927\u8c46\u3001\u82b1\u751f\u589e\u4ea710%\u4ee5\u4e0a\u3002\u79cd\u690d\u8c46\u79d1\u4f5c\u7269\u540e\u7684\u571f\u5730\uff0c\u56e0\u4e3a\u6b8b\u7559\u4e86\u5927\u91cf\u7684\u56fa\u6c2e\u6839\u7624\uff0c\u571f\u58e4\u80a5\u529b\u5927\u5927\u63d0\u9ad8\u3002\u7b2c\u4e8c\u5e74\u8f6e\u4f5c\u7389\u7c73\u6216\u5c0f\u9ea6\u65f6\uff0c\u4e5f\u4f1a\u6709\u660e\u663e\u7684\u589e\u4ea7\u6548\u679c\u3002\u8c37\u7269\u7684\u82b1\u6837\u5e74\u534e\u8c37\u7269\u7684\u82b1\u6735\u4eba\u5230\u4e86\u9752\u6625\u671f\u4f1a\u8c08\u604b\u7231\uff0c\u79cd\u5b50\u690d\u7269\u5219\u4f1a\u626c\u82b1\u6388\u7c89\u3002\u4eba\u751f\u6709\u767e\u5e74\uff0c\u4f46\u8c37\u7269\u7684\u4e00\u751f\u53ea\u6709100\u5929\u3002\u4e3a\u4e86\u5c3d\u5feb\u751f\u5a03\uff0c\u5c31\u5f97\u7f29\u77ed\u8c08\u604b\u7231\u7684\u65f6\u95f4\uff0c\u626c\u82b1\u671f\u53ea\u6709\u4e09\u4e94\u5929\u65f6\u95f4\uff0c\u662f\u5178\u578b\u7684\u201c\u95ea\u5a5a\u201d\u3002\u82b1\u7c89\u7684\u5b58\u6d3b\u671f\u4e5f\u5f88\u77ed\uff0c\u6c34\u7a3b\u82b1\u7c89\u53ef\u5b58\u6d3b10\uff5e15\u5206\u949f\uff0c\u5c0f\u9ea6\u82b1\u7c89\u53ef\u5b58\u6d3b30\u5206\u949f\uff0c\u7389\u7c73\u82b1\u7c89\u53ef\u5b58\u6d3b5\uff5e6\u5c0f\u65f6\u3002\u82e5\u5728\u626c\u82b1\u671f\u9047\u5230\u66b4\u96e8\u3001\u72c2\u98ce\u3001\u4f4e\u6e29\u7b49\u5f02\u5e38\u5929\u6c14\uff0c\u5f71\u54cd\u6388\u7c89\uff0c\u8c37\u7269\u4ea7\u91cf\u5c06\u4f1a\u5927\u5e45\u5ea6\u964d\u4f4e\u3002\u690d\u7269\u8981\u7ed3\u51fa\u679c\u5b9e\u5fc5\u987b\u7ecf\u8fc7\u6388\u7c89\uff0c\u5373\u628a\u96c4\u854a\u7684\u82b1\u7c89\u4f20\u7ed9\u96cc\u854a\uff0c\u8ba9\u96cc\u854a\u53d7\u7cbe\u3002\u6c34\u7a3b\u3001\u5c0f\u9ea6\u3001\u5927\u8c46\u90fd\u5c5e\u4e8e\u81ea\u82b1\u6388\u7c89\uff0c\u800c\u7389\u7c73\u5c5e\u4e8e\u5f02\u82b1\u6388\u7c89\u3002\u8c37\u7269\u7684\u82b1\u7c89\u53ea\u80fd\u98d8\u51e0\u767e\u7c73\u8fdc\uff0c\u751a\u81f3\u53ea\u6709\u51e0\u5341\u7c73\u8fdc\u3002\u5927\u5bb6\u60f3\u60f3\u770b\uff1a\u5982\u679c\u8fd9\u8f88\u5b50\u53ea\u80fd\u548c\u51fa\u751f\u5730\u65b9\u57061\u516c\u91cc\u8303\u56f4\u5185\u7684\u4eba\u7ed3\u5a5a\uff0c\u4f60\u4f1a\u505a\u4f55\u611f\u60f3\uff1f\u6240\u4ee5\uff0c\u4eba\u6bd4\u8c37\u7269\u8981\u5e78\u798f\u5f97\u591a\uff01\u6309\u7167\u6388\u7c89\u65b9\u5f0f\uff0c\u690d\u7269\u4e3b\u8981\u5206\u4e3a\u866b\u5a92\u82b1\u548c\u98ce\u5a92\u82b1\u3002\u679c\u56ed\u91cc\u7ecf\u5e38\u770b\u5230\u871c\u8702\u98de\u821e\uff0c\u4f46\u5728\u8c37\u7530\u91cc\u5374\u4e0d\u4e00\u5b9a\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u7389\u7c73\u3001\u5c0f\u9ea6\u3001\u6c34\u7a3b\u3001\u9ad8\u7cb1\u7b49\u591a\u6570\u8c37\u7269\u90fd\u662f\u98ce\u5a92\u82b1\uff0c\u5411\u65e5\u8475\u3001\u6cb9\u83dc\u3001\u835e\u9ea6\u548c\u74dc\u679c\u7c7b\u5219\u662f\u866b\u5a92\u82b1\u3002\u866b\u5a92\u82b1\u662f\u559c\u6b22\u6253\u626e\u7684\u6f02\u4eae\u5973\u5b69\uff0c\u82b1\u5f62\u5927\u3001\u989c\u8272\u9c9c\u8273\uff0c\u6709\u82b3\u9999\u7684\u6c14\u5473\u548c\u751c\u7f8e\u7684\u82b1\u871c\uff0c\u4ece\u800c\u62db\u8702\u5f15\u8776\u5c06\u81ea\u5df1\u7684\u82b1\u7c89\u5e26\u5230\u53e6\u4e00\u6735\u82b1\u4e0a\u53bb\u3002\u800c\u98ce\u5a92\u82b1\u5728\u6606\u866b\u773c\u4e2d\u5219\u662f\u4f4e\u8c03\u5185\u655b\u7684\u7070\u59d1\u5a18\uff0c\u4e0d\u5c5e\u4e8e\u767d\u5bcc\u7f8e\uff1a\u82b1\u5f62\u5c0f\u4e14\u4e0d\u9c9c\u8273\uff0c\u6c14\u5473\u6de1\uff0c\u7f3a\u5c11\u82b1\u871c\u3002\u4f46\u5b83\u4eec\u82b1\u7c89\u7c92\u5c0f\u5e72\u71e5\uff0c\u5bb9\u6613\u968f\u98ce\u5439\u6563\u2014\u2014\u65e2\u7136\u4e0d\u80fd\u5438\u5f15\u6606\u866b\uff0c\u53ea\u80fd\u8bf7\u98ce\u6765\u505a\u201c\u5a92\u4eba\u201d\u4e86\u3002\u8fd9\u91cc\u6709\u4ef6\u4e8b\u60f3\u544a\u8bc9\u5927\u5bb6\uff1a\u5927\u8c46\u6709\u4e00\u79cd\u5f88\u7279\u522b\u7684\u6388\u7c89\u65b9\u5f0f\uff0c\u65e2\u4e0d\u7b97\u98ce\u5a92\u82b1\uff0c\u4e5f\u4e0d\u7b97\u866b\u5a92\u82b1\uff1a\u82b1\u672a\u5f00\u65f6\uff0c\u91cc\u9762\u7684\u96c4\u854a\u548c\u96cc\u854a\u5c31\u8fdb\u884c\u53d7\u7c89\uff0c\u6388\u7c89\u5b8c\u6210\u540e\u624d\u5f00\u82b1\uff0c\u751f\u7269\u5b66\u4e0a\u79f0\u4e4b\u4e3a\u201c\u95ed\u82b1\u6388\u7c89\u201d\u3002\u76f8\u5f53\u4e8e\u4e24\u4eba\u4ece\u5c0f\u9752\u6885\u7af9\u9a6c\uff0c\u7236\u6bcd\u8001\u65e9\u5c31\u7ed9\u5b69\u5b50\u5b9a\u4e86\u5a03\u5a03\u4eb2\uff0c\u6839\u672c\u5c31\u4e0d\u7528\u98ce\u548c\u866b\u6765\u505a\u5a92\uff0c\u5916\u4eba\uff08\u5f02\u82b1\uff09\u4e5f\u752d\u60e6\u8bb0\uff0c\u4eba\u5bb6\u662f\u5148\u5165\u6d1e\u623f\uff08\u6388\u7c89\uff09\uff0c\u540e\u6446\u5a5a\u5bb4\uff08\u5f00\u82b1\uff09\u3002\u6211\u4eec\u5728\u8fdb\u884c\u623f\u5c4b\u88c5\u4fee\u7684\u65f6\u5019\uff0c\u4e0d\u4ec5\u8981\u8003\u8651\u684c\u6905\u677f\u51f3\u7684\u6446\u653e\uff0c\u4e5f\u8981\u8bbe\u8ba1\u597d\u8863\u67dc\u3001\u58c1\u6a71\u548c\u540a\u67dc\uff0c\u4f18\u5316\u5229\u7528\u7a7a\u95f4\u8d44\u6e90\u3002\u7530\u91ce\u4e5f\u4e00\u6837\uff0c\u79cd\u690d\u5e03\u5c40\u65f6\u9700\u8981\u8003\u8651\u901a\u98ce\u548c\u900f\u5149\u4e8b\u5b9c\u3002\u5728\u5343\u767e\u5e74\u7684\u519c\u8015\u5b9e\u8df5\u4e2d\uff0c\u6211\u4eec\u7684\u7956\u5148\u6478\u7d22\u51fa\u4e00\u5957\u5408\u7406\u7684\u8015\u4f5c\u5236\uff1a\u8ba9\u7389\u7c73\u4e0e\u5927\u8c46\u6216\u5c0f\u9ea6\u7a7f\u63d2\u79cd\u690d\uff08\u95f4\u4f5c\uff09\uff0c\u5373\u6709\u5229\u4e8e\u571f\u58e4\u517b\u5206\u7684\u5e73\u8861\uff0c\u4e5f\u901a\u8fc7\u201c\u9ad8\u77ee\u642d\u914d\u201d\uff0c\u63d0\u9ad8\u901a\u98ce\u548c\u5149\u5408\u6548\u7387\uff0c\u540c\u65f6\u7269\u79cd\u591a\u6837\u53ef\u4ee5\u51cf\u5c11\u75c5\u5bb3\u3002\u571f\u58e4\u548c\u4eba\u4e00\u6837\uff0c\u4e5f\u9700\u8981\u4fee\u751f\u517b\u606f\u3002\u5982\u679c\u5728\u4e00\u7247\u8015\u5730\u4e0a\u8fde\u5e74\u91cd\u590d\u79cd\u690d\u540c\u4e00\u79cd\u8c37\u7269\uff08\u8fde\u4f5c\uff09\uff0c\u4f5c\u7269\u79cd\u7c7b\u548c\u8425\u517b\u7ed3\u6784\u5355\u4e00\uff0c\u5bb9\u6613\u7206\u53d1\u75c5\u5bb3\uff0c\u571f\u58e4\u4e5f\u4f1a\u79ef\u52b3\u6210\u75be\u3002\u4e3a\u4e86\u53ef\u6301\u7eed\u79cd\u690d\uff0c\u7f8e\u56fd\u3001\u6b27\u76df\u548c\u65e5\u672c\u7b49\u56fd\u5bb6\u5efa\u7acb\u4e86\u4f11\u8015\u5236\u5ea6\uff0c\u9009\u62e9\u5408\u9002\u7684\u5e74\u4efd\uff0c\u8ba9\u5730\u50cf\u4eba\u4e00\u6837\u4e5f\u6b47\u606f\u4e00\u4e0b\uff0c\u8eab\u5fc3\u653e\u677e\u5730\u8fc7\u4e2a\u5047\u671f\u3002\u7389\u7c73/\u5927\u8c46\u95f4\u4f5c\u548c\u7389\u7c73/\u5c0f\u9ea6\u95f4\u4f5c\u521a\u624d\u8bf4\u5230\u201c\u9ad8\u77ee\u642d\u914d\u201d\uff0c\u8bf7\u5927\u5bb6\u770b\u770b\u4e0a\u9762\u7684\u56fe\u7247\uff1a\u5c0f\u9ea6\u548c\u5927\u8c46\u7684\u8eab\u9ad8\u53ea\u67091\u7c73\uff0c\u800c\u7389\u7c73\u7684\u8eab\u9ad8\u5219\u662f2\u7c73\u591a\uff0c\u662f\u8c37\u7269\u4e2d\u7684\u201c\u59da\u660e\u201d\u3002\u800c\u4e14\u5728\u4ea7\u91cf\u4e0a\uff0c\u7389\u7c73\u4e5f\u662f\u8c37\u7269\u4e2d\u7684\u51a0\u519b\u3002\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\uff1f\u7389\u7c73\u548c\u9ad8\u7cb1\u3001\u7518\u8517\u8d77\u6e90\u4e8e\u70ed\u5e26\uff0c\u662f\u5f88\u7279\u522b\u7684C4\u690d\u7269\uff0c\u6709\u72ec\u7279\u7684\u5149\u5408\u4f5c\u7528\u673a\u7406\uff0c\u5149\u5408\u6548\u7387\u660e\u663e\u9ad8\u4e8e\u5c0f\u9ea6\u3001\u6c34\u7a3b\u3001\u5927\u8c46\u7b49C3\u690d\u7269\u3002\u7136\u800c\u8fd9\u4e9b\u8c37\u7269\u5c5e\u4e8e\u4e0d\u540c\u7684\u7269\u79cd\uff0c\u5b58\u5728\u751f\u6b96\u9694\u79bb\uff0c\u4e0d\u80fd\u5b9e\u73b0\u6742\u4ea4\u80b2\u79cd\u3002\u8881\u9686\u5e73\u9662\u58eb\u6b63\u5728\u901a\u8fc7\u8f6c\u57fa\u56e0\u6280\u672f\uff0c\u5c1d\u8bd5\u628a\u7389\u7c73\u7684\u9ad8\u5149\u5408\u57fa\u56e0\u8f6c\u5230\u6c34\u7a3b\u4e2d\uff0c\u4ece\u800c\u8fdb\u4e00\u6b65\u589e\u52a0\u6c34\u7a3b\u4ea7\u91cf\u3002\u5272\u8349\u7528\u7684\u7af9\u7b50 | \u6444\u4e8e\u6d59\u6c5f\u6d77\u5b81\u6bb3\u5bb6\u5830\u6751\u519c\u5bb6\u519c\u7530\u91cc\u4e0d\u4ec5\u6709\u8c37\u7269\uff0c\u8fd8\u6709\u8302\u76db\u7684\u91ce\u8349\u3002\u56de\u60f3\u5c11\u5e74\u65f6\u4ee3\uff0c\u653e\u5b66\u540e\u540c\u5b66\u4eec\u5b09\u7b11\u6253\u95f9\uff0c\u6cbf\u7740\u4e61\u95f4\u5c0f\u8def\u8d70\u56de\u6751\u91cc\u3002\u5bb6\u91cc\u5f53\u65f6\u517b\u4e86\u51e0\u5341\u53ea\u9e21\u3001\u9e2d\u3001\u9e45\u548c\u5154\u5b50\uff0c\u6211\u8981\u5148\u62ff\u8d77\u6241\u62c5\u3001\u9570\u5200\u548c\u7bee\u7b50\u53bb\u7530\u91cc\u5272\u8349\u3002\u8c37\u7269\u56e0\u4e3a\u6709\u4eba\u7c7b\u7684\u7cbe\u5fc3\u5475\u62a4\uff0c\u624d\u5f97\u4ee5\u8301\u58ee\u6210\u957f\u3002\u91ce\u8349\u662f\u6ca1\u4eba\u7231\u7684\u91ce\u5b69\u5b50\uff0c\u5374\u6709\u7740\u987d\u5f3a\u7684\u751f\u547d\u529b\u2014\u2014\u91ce\u706b\u70e7\u4e0d\u5c3d\uff0c\u6625\u98ce\u5439\u53c8\u751f\u3002\u6700\u4e0d\u559c\u6b22\u4e00\u79cd\u540d\u4e3a\u201c\u524c\u524c\u79e7\u201d\u7684\u91ce\u8349\uff0c\u5b83\u5728\u7530\u91ce\u91cc\u8fde\u7247\u530d\u5310\u7f20\u7ed5\u3002\u4e00\u6839\u85e4\u80fd\u6709\u51e0\u7c73\u957f\uff0c\u85e4\u4e0a\u5bc6\u5e03\u7740\u7ec6\u523a\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u522e\u5728\u76ae\u80a4\u4e0a\uff0c\u5c31\u662f\u4e00\u9053\u8840\u75d5\u3002\u60f3\u77e5\u9053\u5b83\u957f\u4ec0\u4e48\u6837\u5417\uff1f\u770b\u770b\u4e0b\u9762\u7684\u89c6\u9891\uff1a\u524c\u524c\u79e7\u89c6\u9891(\u70b9\u51fb\u56fe\u7247\u53ef\u4ee5\u89c2\u770b\u89c6\u9891\uff09\u4eba\u6709\u65e6\u5915\u7978\u798f\uff0c\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u707e\u5bb3\u4f1a\u4e0d\u671f\u800c\u81f3\u3002\u8c37\u7269\u4e0d\u80fd\u50cf\u4eba\u4e00\u6837\u56db\u6563\u5954\u9003\uff0c\u53ea\u80fd\u542c\u5929\u7531\u547d\u3002\u4e00\u573a\u53f0\u98ce\uff0c\u8c37\u7269\u4f1a\u5927\u9762\u79ef\u5012\u4f0f\uff1b\u4e00\u573a\u51b0\u96f9\uff0c\u4f1a\u628a\u690d\u682a\u7838\u5f97\u652f\u79bb\u7834\u788e\uff1b\u7acb\u67af\u75c5\u53ef\u4ee5\u8ba9\u6c34\u7a3b\u5728\u82d7\u671f\u90fd\u6210\u7247\u67af\u840e\uff1b\u866b\u5bb3\u53ef\u4ee5\u9020\u6210\u7389\u7c7310-20%\u7684\u635f\u5931\uff1b\u4e00\u573a\u7a81\u5982\u5176\u6765\u7684\u51bb\u5bb3\uff0c\u751a\u81f3\u53ef\u4ee5\u8ba9\u8c37\u7269\u7edd\u6536\u3002\u53f0\u98ce\u8fc7\u540e\uff0c\u7a3b\u7530\u6210\u7247\u5012\u4f0f | \u6444\u4e8e\u5409\u6797\u5e02\u5b64\u5e97\u5b50\u9547(\u70b9\u51fb\u56fe\u7247\u53ef\u4ee5\u89c2\u770b\u89c6\u9891\uff09\u66fe\u6709\u670b\u53cb\u95ee\u6211\uff1a\u4e3a\u4ec0\u4e48\u51ac\u5c0f\u9ea6\u51ac\u5929\u51bb\u4e0d\u6b7b\uff1f\u8fdb\u5165\u8d8a\u51ac\u671f\uff0c\u5c0f\u9ea6\u7ec6\u80de\u6db2\u4e2d\u7684\u542b\u7cd6\u91cf\u589e\u52a0\uff0c\u4f7f\u7ed3\u51b0\u70b9\u964d\u4f4e\uff0c\u63d0\u9ad8\u4e86\u6297\u5bd2\u80fd\u529b\u3002\u51ac\u5929\u91cc\uff0c\u73af\u536b\u5de5\u4eba\u5411\u51b0\u96ea\u8986\u76d6\u7684\u9053\u8def\u4e0a\u6492\u76d0\u878d\u96ea\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\u3002\u53e6\u5916\uff0c\u4e00\u573a\u5927\u96ea\u540e\uff0c\u79ef\u96ea\u8986\u76d6\u5728\u5c0f\u9ea6\u4e0a\uff0c\u9694\u7edd\u4e86\u5bd2\u51b7\u7684\u7a7a\u6c14, \u76f8\u5f53\u4e8e\u7ed9\u5c0f\u9ea6\u76d6\u4e86\u4e00\u5e8a\u5fa1\u5bd2\u7684\u68c9\u88ab\uff0c\u8d77\u5230\u4e86\u4fdd\u6e29\u7684\u4f5c\u7528\u3002\u6240\u4ee5\u6709\u8fd9\u6837\u4e00\u53e5\u519c\u8c1a\uff1a\u4eca\u51ac\u9ea6\u76d6\u4e09\u5c42\u88ab\uff0c\u6765\u5e74\u6795\u7740\u9992\u5934\u7761\u3002\u6625\u5b63\u64ad\u79cd\uff0c\u8c37\u7269\u6839\u690d\u6c83\u571f\uff0c\u6e90\u6e90\u4e0d\u65ad\u5730\u4ece\u6ce5\u571f\u4e2d\u6c72\u53d6\u8425\u517b\uff0c\u830e\u53f6\u633a\u51fa\u5730\u9762\u8fdb\u884c\u5149\u5408\u4f5c\u7528\uff0c\u5408\u6210\u6709\u673a\u7269\uff0c\u6700\u540e\u5c06\u5927\u91cf\u517b\u5206\u8f93\u9001\u5230\u79cb\u5929\u7684\u8c37\u7c92\u4e4b\u4e2d\uff0c\u5b8c\u6210\u4e86\u4e00\u4e2a\u751f\u957f\u5b63\u7684\u4f7f\u547d\u3002\u6625\u64ad\u4e00\u7c92\u79cd\uff0c\u79cb\u6536\u4e07\u62c5\u7cae\uff0c\u8fd9\u662f\u8c37\u7269\u4e16\u754c\u7684\u751f\u547d\u8f6e\u56de\u3002\u4e00\u7c92\u79cd\u5b50\u5230\u4e0b\u4e00\u4ee3\u80fd\u591f\u7e41\u80b2\u51fa\u591a\u5c11\u7c92\u79cd\u5b50\uff1f\u5927\u8c46\u5927\u7ea6\u662f50\u7c92\uff0c\u800c\u5c0f\u9ea6\u3001\u6c34\u7a3b\u548c\u7389\u7c73\u5219\u53ef\u4ee5\u8fbe\u5230150\u7c92\u3001250\u7c92\u548c500\u7c92\u3002\u600e\u4e48\u6837\uff0c\u8c37\u7269\u7684\u7e41\u6b96\u80fd\u529b\u6bd4\u4eba\u7c7b\u5f3a\u5927\u5f97\u591a\u5427\uff01\u8fd9\u79cd\u5dee\u5f02\u4e5f\u5bfc\u81f4\u4e86\u8c37\u7269\u7684\u5355\u4f4d\u9762\u79ef\u4ea7\u91cf\u6392\u5e8f\uff1a\u5927\u8c46<\u5c0f\u9ea6<\u6c34\u7a3b<\u7389\u7c73\u3002\u6c34\u7a3b\u6536\u5272 | \u6444\u4e8e\u5409\u6797\u5e02\u6625\u65b0\u751f\u6001\u5bb6\u5ead\u519c\u573a(\u70b9\u51fb\u56fe\u7247\u53ef\u4ee5\u89c2\u770b\u89c6\u9891\uff09\u8c37\u7269\u6536\u83b7\u540e\uff0c\u8fd8\u8981\u7ecf\u8fc7\u8131\u58f3\u3001\u53bb\u7ce0\u7b49\u78be\u7c73\u5de5\u5e8f\uff0c\u624d\u80fd\u6700\u7ec8\u53d8\u6210\u53a8\u623f\u91cc\u7684\u7c73\u9762\u3002\u8fd9\u4e2a\u8fc7\u7a0b\u6709\u4e9b\u50cf\u73a9\u4fc4\u7f57\u65af\u5957\u5a03\uff0c\u4e00\u5c42\u5c42\u8715\u76ae\u3002\u4e00\u65a4\u7a3b\u8c37\u4e03\u4e24\u7c73\uff0c\u4e00\u65a4\u5c0f\u9ea6\u4e03\u4e24\u9762\uff0c\u201c\u51fa\u7387\u201d\u5927\u81f4\u662f70%\u3002\u5176\u5b9e\uff0c\u8c37\u7c92\u88ab\u78e8\u6389\u90e8\u5206\u662f\u76ae\u5c42\u548c\u80da\uff0c\u8425\u517b\u4ef7\u503c\u975e\u5e38\u9ad8\uff0c\u5bcc\u542b\u86cb\u767d\u8d28\u548c\u591a\u79cd\u7ef4\u751f\u7d20\u3002\u5927\u5bb6\u5403\u8fc7\u989c\u8272\u5fae\u8910\u7684\u5168\u9ea6\u9762\u5305\u5427\uff1f\u5176\u5b9e\u5c31\u662f\u7528\u4fdd\u7559\u5916\u9762\u9eb8\u76ae\u548c\u9ea6\u80da\u7684\u5168\u9ea6\u9762\u7c89\u5236\u4f5c\u7684\u9762\u5305\u3002\u8bd7\u548c\u8fdc\u65b9\u5728\u519c\u8015\u65f6\u4ee3\uff0c\u4eba\u4eec\u7ecf\u5386\u5bd2\u6691\u6613\u8282\uff0c\u4ed8\u51fa\u8270\u8f9b\u52b3\u4f5c\uff0c\u4e00\u5206\u8015\u8018\u65b9\u6709\u4e00\u5206\u6536\u83b7\u3002\u7b80\u5355\u800c\u6162\u8282\u594f\u7684\u751f\u6d3b\u4e2d\uff0c\u6709\u9676\u6e0a\u660e\u3001\u738b\u7ef4\u548c\u5b5f\u6d69\u7136\u7684\u7530\u56ed\u8bd7\u53e5\uff0c\u6cb3\u6c34\u662f\u6e05\u6f88\u7684\uff0c\u8c37\u7269\u662f\u6709\u5473\u9053\u7684\u3002\u4eba\u4eec\u5fc3\u6001\u5e73\u548c\u3001\u4ece\u5bb9\u3001\u6709\u8010\u5fc3\uff0c\u52e4\u4fed\u6301\u5bb6\uff0c\u548c\u7766\u76f8\u5904\uff0c\u5c0a\u8001\u7231\u5e7c\uff0c\u7b11\u5bb9\u662f\u771f\u5b9e\u7684\uff0c\u4eba\u4eec\u662f\u5feb\u4e50\u7684\u3002\u8fdb\u5165\u4e92\u8054\u7f51\u65f6\u4ee3\uff0c\u7f51\u7ea2\u6613\u901d\uff0c\u65b0\u5ba0\u592a\u591a\uff0c\u5fc3\u6001\u8ff7\u60d1\uff0c\u7126\u8651\u5145\u65a5\uff0c\u5a31\u4e50\u81f3\u6b7b\uff0c\u552f\u6211\u72ec\u5c0a\u3002\u5927\u5bb6\u90fd\u5728\u8ffd\u6c42\u5feb\u8282\u594f\uff0c\u82db\u6c42\u5feb\u9012\u5c0f\u54e5\u4e89\u5206\u593a\u79d2\u3001\u9a6c\u4e0a\u5c31\u5230\uff0c\u770b\u4e00\u7bc7\u6587\u7ae0\u7684\u8010\u5fc3\u751a\u81f3\u6ca1\u670910\u5206\u949f\uff0c\u5fc3\u6d6e\u6c14\u8e81\u3001\u97ad\u6253\u5feb\u725b\u3001\u62d4\u82d7\u52a9\u957f\uff0c\u5e7b\u60f3\u80fd\u627e\u5230\u4e00\u672c\u4e07\u5229\u3001\u4e00\u591c\u66b4\u5bcc\u7684\u6377\u5f84\u3002\u7136\u800c\u7f8e\u597d\u7684\u4e8b\u7269\u5c31\u662f\u9700\u8981\u7ecf\u53d7\u5bd2\u6691\u98ce\u971c\u7684\u6d17\u793c\uff0c\u4e00\u8e74\u800c\u5c31\u7684\u4e1c\u897f\u5f80\u5f80\u7f3a\u5c11\u5e95\u8574\u3002\u8fd9\u4e2a\u793e\u4f1a\uff0c\u771f\u7684\u9700\u8981\u6162\u4e0b\u6765\u4e00\u4e9b\uff0c\u8ba9\u5fc3\u7075\u8ddf\u4e0a\u811a\u6b65\uff01\u79cb\u5929\u7684\u7a3b\u7530 | \u6444\u4e8e\u5409\u6797\u5e02\u6625\u65b0\u751f\u6001\u5bb6\u5ead\u519c\u573a\u300a\u6211\u548c\u6211\u7684\u7956\u56fd\u300b\u5531\u5230\uff1a\u201c\u6211\u6b4c\u5531\u6bcf\u4e00\u5ea7\u9ad8\u5c71\uff0c\u6211\u6b4c\u5531\u6bcf\u4e00\u6761\u6cb3\u3002\u8885\u8885\u708a\u70df\u5c0f\u5c0f\u6751\u843d\uff0c\u8def\u4e0a\u4e00\u9053\u8f99\u3002\u201d\u66fe\u7ecf\uff0c\u6211\u4eec\u7684\u7956\u5148\u65e5\u51fa\u800c\u4f5c\uff0c\u65e5\u843d\u800c\u606f\uff0c\u65e5\u590d\u4e00\u65e5\uff0c\u5e74\u590d\u4e00\u5e74\u3002\u5343\u767e\u5e74\u540e\uff0c\u4eba\u7c7b\u811a\u4e0b\u4f9d\u7136\u662f\u539a\u5fb7\u8f7d\u7269\u7684\u571f\u5730\uff0c\u4eba\u7c7b\u8d56\u4ee5\u7ef4\u6301\u751f\u547d\u7684\u4f9d\u7136\u662f\u7c73\u3001\u9762\u3001\u7cae\u3001\u6cb9\u3002\u76f8\u4fe1\u79d1\u6280\u8fdb\u6b65\u80fd\u591f\u8ba9\u4f20\u7edf\u519c\u4e1a\u8131\u80ce\u6362\u9aa8\uff0c\u66f4\u5e0c\u671b4000\u5e74\u534e\u590f\u519c\u8015\u6587\u660e\u79ef\u6dc0\u4e0b\u6765\u7684\u6c11\u65cf\u7cbe\u795e\uff0c\u80fd\u591f\u54fa\u80b2\u6211\u4eec\u8d70\u5411\u81ea\u5f3a\u3001\u751f\u751f\u4e0d\u606f\uff01\uff08\u7f16\u8f91\uff1aYuki\uff09\u81f4\u8c22\uff1a\u5728\u5199\u4f5c\u8fc7\u7a0b\u4e2d\uff0c\u5f97\u5230\u4e86\u738b\u8574\u6ce2\u6559\u6388\u3001\u5f20\u541b\u6559\u6388\u3001\u6768\u6c5f\u4e49\u6559\u6388\u548c\u738b\u8bed\u6674\u3001\u5f20\u5434\u7aeb\u3001\u5434\u60a0\u60a0\u3001\u5b89\u82ae\u5609\u3001\u8363\u6bd3\u8431\u3001\u5d14\u7b71\u91ce\u7b49\u540c\u5b66\u7684\u542f\u53d1\u548c\u5efa\u8bae\uff0c\u5728\u6b64\u4e00\u5e76\u81f4\u8c22\uff01*\u672c\u6587\u6839\u636e\u4f5c\u8005\u5728\u201c\u676d\u5dde\u79d1\u5b66\u5927\u8bb2\u5802\u201d\u7684\u79d1\u666e\u8bb2\u5ea7\u6587\u7a3f\u6574\u7406\u4fee\u6539\u800c\u6210\u3002", "title": "\u6ca1\u7a7a\u604b\u7231\uff0c\u53ea\u80fd\u548c\u65b9\u5706\u767e\u7c73\u7684\u5f02\u6027\u95ea\u5a5a\uff0c\u5b83\u4eec\u5b9e\u5728\u82e6\u547d\uff01", "ukey_author": "bl1jig", "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/5II_6ce-_EI9SYM6SLGtJTdUPgX-nW2BmY-w67oJqyW_AAAAvQAAAFBO.png", "normal": "https://3-im.guokr.com/5II_6ce-_EI9SYM6SLGtJTdUPgX-nW2BmY-w67oJqyW_AAAAvQAAAFBO.png", "small": "https://3-im.guokr.com/5II_6ce-_EI9SYM6SLGtJTdUPgX-nW2BmY-w67oJqyW_AAAAvQAAAFBO.png"}, "introduction": "\u4e2d\u79d1\u9662\u53e4\u810a\u690e\u6240\u7814\u7a76\u751f\uff0c\u4ece\u4e8b\u5c0f\u4f17\u4eba\u7c7b\u5b66\u7814\u7a76\u591a\u5e74\uff0c\u7ecf\u5e38\u4e0e\u5316\u77f3\u6587\u7269\u6253\u4ea4\u9053\u3002\u53ef\u80fd\u662f\u4e2a\u7cbe\u795e\u5e7f\u4e1c\u4eba\uff0c\u5982\u679c\u660e\u5929\u662f\u4e16\u754c\u672b\u65e5\uff0c\u4ed6\u4f1a\u62bd\u534a\u5929\u65f6\u95f4\u5403\u65e9\u8336\u3002", "nickname": "\u60e0\u5bb6\u660e", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/5II_6ce-_EI9SYM6SLGtJTdUPgX-nW2BmY-w67oJqyW_AAAAvQAAAFBO.png?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/5II_6ce-_EI9SYM6SLGtJTdUPgX-nW2BmY-w67oJqyW_AAAAvQAAAFBO.png?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/5II_6ce-_EI9SYM6SLGtJTdUPgX-nW2BmY-w67oJqyW_AAAAvQAAAFBO.png?imageView2/1/w/24/h/24"}, "introduction": "\u4e2d\u79d1\u9662\u53e4\u810a\u690e\u6240\u7814\u7a76\u751f\uff0c\u4ece\u4e8b\u5c0f\u4f17\u4eba\u7c7b\u5b66\u7814\u7a76\u591a\u5e74\uff0c\u7ecf\u5e38\u4e0e\u5316\u77f3\u6587\u7269\u6253\u4ea4\u9053\u3002\u53ef\u80fd\u662f\u4e2a\u7cbe\u795e\u5e7f\u4e1c\u4eba\uff0c\u5982\u679c\u660e\u5929\u662f\u4e16\u754c\u672b\u65e5\uff0c\u4ed6\u4f1a\u62bd\u534a\u5929\u65f6\u95f4\u5403\u65e9\u8336\u3002", "nickname": "\u60e0\u5bb6\u660e", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [{"articles_count": 615, "channel_type": "normal", "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "fact", "name": "\u8c23\u8a00\u7c89\u788e\u673a", "sort_score": 6}], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<p>&ldquo;\u5c71\u4e0d\u5728\u9ad8\uff0c\u6709\u4ed9\u5219\u540d\u3002\u6c34\u4e0d\u5728\u6df1\uff0c\u6709\u9f99\u5219\u7075\u3002&rdquo;\u8fd9\u53e5\u4e2d\u56fd\u53e4\u8bdd\u653e\u5728\u82f1\u56fd\u82cf\u683c\u5170\u7684\u5c3c\u65af\u6e56\u4e5f\u540c\u6837\u9002\u7528\u3002\u867d\u7136\u5b83\u4e0d\u662f\u8fd9\u91cc\u6700\u5927\u6700\u6df1\u7684\u6e56\u6cca\uff0c\u4f46\u7531\u4e8e\u5f53\u5730\u6d41\u4f20\u5df2\u4e45\u7684&ldquo;\u6c34\u602a&rdquo;\u4f20\u8bf4\uff0c\u5c3c\u65af\u6e56\u7684\u540d\u5b57\u65e9\u5c31\u54cd\u5f7b\u5168\u7403\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/SUnzIiCOwF8Z_85eZ56uHCP1BWAiGe1-bG9ktld8DFEgAwAAEgIAAEpQ.jpg\" style=\"height:424px; width:640px\" />\u5c3c\u65af\u6e56\u4f4d\u4e8e\u82cf\u683c\u5170\u5317\u90e8\uff0c\u5b83\u4e0d\u4ec5\u6709\u6c34\u602a\u4f20\u8bf4\uff0c\u8fd8\u6709\u6e56\u7554\u7684\u5386\u53f2\u9057\u8ff9\uff5cWikimedia Commons - Sam Fentress</p>\r\n\r\n<p>\u4e3a\u4e86\u627e\u5230\u5c3c\u65af\u6e56\u6c34\u602a\u7684\u771f\u8eab\uff0c\u8fd1\u767e\u5e74\u6765\uff0c\u65e0\u6570\u5b66\u8005\u548c\u6e38\u5ba2\u7eb7\u7eb7\u524d\u53bb\u5bfb\u627e\u8e2a\u8ff9\u3002\u5404\u5f0f\u5404\u6837\u7684\u6c34\u602a\u7167\u7247\u4e5f\u5927\u884c\u4e8e\u4e16\uff0c\u4ee4\u4e0d\u5c11\u4eba\u575a\u4fe1\u6c34\u602a\u786e\u5b9e\u5b58\u5728\u3002\u7136\u800c\uff0c\u65b0\u897f\u5170\u5965\u5854\u54e5\u5927\u5b66\u505a\u51fa\u7684\u6700\u65b0\u7814\u7a76\u6210\u679c\uff0c\u53ef\u80fd\u8981\u8ba9&ldquo;\u6c34\u602a\u8ff7&rdquo;\u4eec\u5931\u671b\u4e86\u3002\u4ed6\u4eec\u68c0\u6d4b\u4e86\u6e56\u6c34\u4e2d\u7684DNA\u540e\u53d1\u73b0\uff1a<strong>\u5c3c\u65af\u6e56\u91cc\u5f88\u53ef\u80fd\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u6c34\u602a\uff0c\u6240\u8c13\u7684\u6c34\u602a\u6216\u8bb8\u53ea\u662f\u4f53\u578b\u5f88\u5927\u7684&ldquo;\u9cd7\u9c7c&rdquo;</strong>[1]\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/BhWcsdainzR7IO9DQS-7cT0s27lOQPGQTwwogDkfwRg4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" />\u56fe\uff5c\u300a\u54c6\u5566A\u68a6\u300b</p>\r\n\r\n<h2><strong>\u6c34\u602a\u7b80\u53f2</strong></h2>\r\n\r\n<p>\u5728\u5404\u7c7b\u79d1\u666e\u8282\u76ee\u548c\u4e66\u7c4d\u4e2d\uff0c&ldquo;\u5c3c\u65af\u6e56\u6c34\u602a&rdquo;\u7684\u6545\u4e8b\u8fd1\u5e74\u6765\u4e00\u76f4\u6709\u5f88\u9ad8\u7684\u51fa\u955c\u7387\u3002\u4e0d\u8fc7\uff0c\u5b83\u53ef\u4e0d\u662f\u5728\u73b0\u4ee3\u624d\u53d8\u6210\u7f51\u7ea2\u7684\u3002\u65e9\u5728\u5c06\u8fd11500\u5e74\u524d\uff0c&ldquo;\u6c34\u602a&rdquo;\u5c31\u5df2\u7ecf\u6210\u4e3a\u4e86\u5c3c\u65af\u6e56\u7684\u4f20\u5947\u3002</p>\r\n\r\n<p><strong>\u5c3c\u65af\u6e56\u6c34\u602a\u7684\u6545\u4e8b\u6700\u65e9\u89c1\u4e8e\u516c\u51437\u4e16\u7eaa\u9762\u4e16\u7684\u4f20\u8bb0\u300a\u5723\u5e93\u4ed1\u751f\u5e73\u300b\uff08<em>Life of St. Columba</em>\uff09</strong>\u3002\u4e66\u4e2d\u5199\u5230\uff1a\u8457\u540d\u4fee\u9053\u58eb\u5723\u5e93\u4ed1\u542c\u95fb\u5c3c\u65af\u6cb3\uff08\u6e90\u81ea\u5c3c\u65af\u6e56\u7684\u4e00\u6761\u5c0f\u6cb3\uff09\u4e2d\u6709\u51f6\u731b\u91ce\u517d\u88ad\u51fb\u4eba\u7c7b\uff0c\u4ed6\u5c31\u60f3\u8981\u8fd8\u5c45\u6c11\u4e00\u65b9\u5b89\u5b81\u3002\u4e8e\u662f\uff0c\u4ed6\u6d3e\u81ea\u5df1\u7684\u624b\u4e0b\u5728\u5c3c\u65af\u6cb3\u4e2d\u6e38\u6cf3\u3002\u679c\u4e0d\u5176\u7136\uff0c\u6c34\u602a\u7a81\u7136\u6d6e\u51fa\u6c34\u9762\u51c6\u5907\u884c\u51f6\u3002\u8fd9\u65f6\uff0c\u5723\u5e93\u4ed1\u7528\u624b\u6307\u5212\u51fa\u5341\u5b57\uff0c\u5475\u65a5\u6c34\u602a\u8bf4&ldquo;\u83ab\u9760\u8fd1\uff0c\u83ab\u4f24\u4eba\uff0c\u901f\u5f52\u53bb\uff01&rdquo;\u3002\u6ca1\u60f3\u5230\uff0c\u6c34\u602a\u4e00\u542c\u8fd9\u8bdd\uff0c\u9a6c\u4e0a\u88ab\u4e00\u79cd\u65e0\u5f62\u7684\u529b\u91cf\u7ed9\u62fd\u8dd1\u4e86[2]\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/rdIu7fgENmoEfWPjMIkA_Yn-upI6Ya7be54VXVQtiLIkBAAANQYAAEpQ.jpg\" style=\"height:959px; width:640px\" />\u5723\u5e93\u4ed1\u662f\u82cf\u683c\u5170\u5730\u533a\u4f20\u5947\u4eba\u7269\uff0c\u4ed6\u66fe\u662f\u8457\u540d\u7684\u5929\u4e3b\u6559\u50e7\u4fa3\uff5cWikimedia Commons - J. R. Skelton</p>\r\n\r\n<p>\u5723\u5e93\u4ed1\u7684\u6545\u4e8b\u5f53\u7136\u4e0d\u53ef\u4fe1\u3002\u5728\u90a3\u4e2a\u65f6\u4ee3\uff0c\u5199\u8fd9\u79cd\u6c34\u602a\u7684\u6545\u4e8b\u53ef\u80fd\u53ea\u662f\u4e3a\u4e86\u5ba3\u626c\u795e\u529b\u548c\u4e2a\u4eba\u9b45\u529b\u3002\u5e76\u4e14\uff0c\u5b83\u4e5f\u6ca1\u63cf\u8ff0\u5c3c\u65af\u6e56\u6c34\u602a\u7a76\u7adf\u957f\u5f97\u662f\u4e2a\u4ec0\u4e48\u6a21\u6837\u3002\u4f46\u662f\u7b49\u523019\u4e16\u7eaa\u4ee5\u540e\uff0c\u5404\u7c7b\u76ee\u51fb\u62a5\u544a\u4e43\u81f3\u6444\u5f71\u7167\u7247\u9010\u6e10\u4e30\u5bcc\u4e86\u8d77\u6765\uff0c\u8fd8\u628a\u6c34\u602a\u7684\u6837\u5b50\u523b\u753b\u5f97\u6829\u6829\u5982\u751f\uff0c\u8fd9\u5c31\u4e0d\u5f97\u4e0d\u8ba9\u4eba\u601d\u8003\uff1a\u5c3c\u65af\u6e56\u91cc\u662f\u4e0d\u662f\u771f\u7684\u6709\u4e0d\u4e3a\u4eba\u77e5\u7684\u795e\u79d8\u602a\u7269\u5462\uff1f</p>\r\n\r\n<p>\u8fd9\u4e9b\u76ee\u51fb\u4e8b\u4ef6\u53d1\u751f\u7684\u4f4d\u7f6e\u51e0\u4e4e\u904d\u5e03\u5c3c\u65af\u6e56\u5404\u4e2a\u89d2\u843d\uff0c\u6240\u63cf\u8ff0\u7684\u6c34\u602a\u5f62\u6001\u4e5f\u5404\u6709\u4e0d\u540c\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u53e3\u4e2d\u7684\u6c34\u602a\u5f62\u8c61\u90fd\u6709\u4e00\u4e9b\u5171\u6027\u3002\u5c31\u4ee5\u6700\u5177\u4ee3\u8868\u6027\u7684\u4e00\u5f20\u7167\u7247\u4e3a\u4f8b\uff0c\u56fe\u4e2d\u7684\u6c34\u602a\u6709\u7ec6\u5c0f\u7684\u8111\u888b\u3001\u4fee\u957f\u7684\u8116\u5b50\u3001\u5e9e\u5927\u7684\u8eab\u8eaf&hellip;&hellip;\u6211\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4f60\u7684\u8111\u6d77\u4e2d\u662f\u4e0d\u662f\u6d6e\u73b0\u51fa\u4e86\u4e00\u79cd\u8fdc\u53e4\u751f\u7269\u7684\u5f62\u8c61\uff1f</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/eJwW_3ErbjX6ouyydc979ifgY2kMppbYL2pubTJP3fnoAAAA1wAAAEpQ.jpg\" style=\"height:215px; width:232px\" />\u5c3c\u65af\u6e56\u6c34\u602a\u6700\u7ecf\u5178\u7684\u4e00\u5f20\u7167\u7247\uff0c\u6444\u4e8e1934\u5e74\uff5cWikimedia Commons -&nbsp; Robert Kenneth Wilson</p>\r\n\r\n<p>\u6ca1\u9519\uff01\u4eba\u4eec\u63cf\u7ed8\u7684\u6c34\u602a\u50cf\u6781\u4e86\u86c7\u9888\u9f99\u3002\u90a3\u662f\u4e00\u79cd\u751f\u6d3b\u5728\u4f8f\u7f57\u7eaa\u65f6\u4ee3\u7684\u5de8\u578b\u6d77\u6d0b\u722c\u884c\u52a8\u7269\u3002\u4e8e\u662f\u4e4e\uff0c<strong>\u5f88\u591a\u4eba\u5f00\u59cb\u6000\u7591\u5c3c\u65af\u6e56\u6c34\u602a\u5176\u5b9e\u662f\u6050\u9f99\u65f6\u4ee3\u9057\u7559\u81f3\u4eca\u7684 &ldquo;\u6d3b\u5316\u77f3&rdquo;</strong>[3]\u3002\u5728\u76f8\u5173\u5f71\u89c6\u4f5c\u54c1\u4e2d\uff0c\u5c3c\u65af\u6e56\u6c34\u602a\u4e5f\u666e\u904d\u662f\u6050\u9f99\u6216\u86c7\u9888\u9f99\u7684\u5f62\u8c61\u3002\u6b64\u5916\uff0c\u4e5f\u6709\u4e2a\u522b\u4eba\u6839\u636e\u5176\u4ed6\u76ee\u51fb\u62a5\u544a\u63a8\u6d4b\u6c34\u602a\u662f\u67d0\u79cd\u672a\u77e5\u7684\u65e0\u810a\u690e\u52a8\u7269\u6216\u4e24\u6816\u52a8\u7269[4]\u3002\u65e0\u8bba\u5982\u4f55\uff0c\u516c\u4f17\u5df2\u7ecf\u4e0d\u518d\u50cf\u5723\u5e93\u4ed1\u90a3\u4e2a\u65f6\u4ee3\u4e00\u6837\u89c9\u5f97\u6c34\u602a\u662f\u8d85\u81ea\u7136\u7684\u3001\u4e0e\u5b97\u6559\u6709\u5173\u7684\u795e\u517d\uff0c\u800c\u662f\u8ba4\u4e3a\u5b83\u662f\u67d0\u79cd\u5c1a\u672a\u88ab\u53d1\u73b0\u7684\u7269\u79cd\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/iA7zgYsLjnhrwTwtRhi-jLMpz3XJt3PPWiGzkvYlZ_4cAwAAWAIAAEpQ.jpg\" style=\"height:482px; width:640px\" />\u5f53\u5730\u4eba\u6839\u636e\u76ee\u51fb\u8005\u63cf\u8ff0\u5236\u4f5c\u51fa\u7684\u5c3c\u65af\u6e56\u6c34\u602a\u6a21\u578b\uff0c\u548c\u86c7\u9888\u9f99\u9ad8\u5ea6\u76f8\u4f3c\uff5cWikimedia Commons - A Obeidat</p>\r\n\r\n<p><strong>\u662f\u9c7c\u662f\u602a\uff1f\u7528DNA\u8bc1\u636e\u8bf4\u8bdd</strong></p>\r\n\r\n<p>\u65e2\u7136&ldquo;\u6c34\u602a&rdquo;\u88ab\u8ba4\u4e3a\u662f\u79cd\u751f\u7269\uff0c\u90a3\u4e48\u5b83\u5fc5\u5b9a\u4e5f\u6709\u8840\u6709\u8089\uff0c\u8bf4\u4e0d\u5b9a\u751f\u6d3b\u4e60\u6027\u4e5f\u4f1a\u50cf\u957f\u9888\u9f99\u4e00\u6837\uff0c\u9700\u8981\u7ecf\u5e38\u6d6e\u51fa\u6c34\u9762\u6362\u6c14\u3001\u89c5\u98df\u3001\u770b\u98ce\u666f\uff08\u8bef\uff09\u3002\u603b\u4e4b\u4eba\u4eec\u76f8\u4fe1\uff0c\u5982\u679c\u6c34\u602a\u771f\u7684\u5b58\u5728\uff0c\u90a3\u4e48\u4ed6\u4e00\u5b9a\u662f\u80fd\u88ab\u4eba\u7c7b\u89c2\u5bdf\u5230\u7684\u3002\u4e8e\u662f\u4e4e\uff0c\u6210\u767e\u4e0a\u5343\u7684\u6e38\u5ba2\u4e0e\u6c34\u602a\u7231\u597d\u8005\u90fd\u62ff\u7740\u957f\u67aa\u77ed\u70ae\u5b88\u5728\u6e56\u8fb9\uff0c\u4e0d\u5c11\u7814\u7a76\u673a\u6784\u751a\u81f3\u8fd8\u52a8\u7528\u4e86\u58f0\u5450\u6280\u672f\u641c\u7d22\u6c34\u602a\u8e2a\u8ff9\u3002<strong>\u53ef\u662f\u8fd1\u4e00\u4e2a\u4e16\u7eaa\u4e0b\u6765\uff0c\u6240\u6709\u7684\u641c\u5bfb\u6d3b\u52a8\u90fd\u6ca1\u83b7\u5f97\u5b9e\u8d28\u6027\u8fdb\u5c55</strong>\u3002\u9664\u4e86\u5c11\u91cf\u771f\u4f2a\u96be\u8fa8\u7684\u7167\u7247\u4e4b\u5916\uff0c\u6211\u4eec\u8fde\u6c34\u602a\u7684\u6bdb\u4e5f\u6ca1\u635e\u5230\u3002</p>\r\n\r\n<p>\u76f8\u6bd4\u4e4b\u4e0b\uff0c\u65b0\u897f\u5170\u5965\u5854\u54e5\u5927\u5b66\u6559\u6388\u683c\u6885\u5c14\uff08Neil Gemmell\uff09\u7684\u56e2\u961f\u6709\u70b9&ldquo;\u5251\u8d70\u504f\u950b&rdquo;\u3002\u4ed6\u4eec\u6ca1\u6709\u50cf\u5176\u4ed6\u4eba\u4e00\u6837\u4e13\u95e8\u53bb\u641c\u5bfb\u6c34\u602a\u7684\u771f\u8eab\uff0c\u800c\u662f\u5f00\u7740\u4e2a\u8239\u5728\u6e56\u91cc\u5230\u5904\u635e\u6e56\u6c34\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u8fd9\u4e48\u505a\u5230\u5e95\u662f\u5728\u5e72\u4ec0\u4e48\u5462\uff1f</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/1yvlWk4eZsZJTFdjrqc5t8qnpBklistXWkzPxuPIsLiMAgAAwgEAAEpQ.jpg\" style=\"height:442px; width:640px\" />\u683c\u6885\u5c14\u6559\u6388\u7ad9\u5728\u5c3c\u65af\u6e56\u7554\uff5cwww.otago.ac.nz</p>\r\n\r\n<p>\u539f\u6765\uff0c\u5965\u5854\u54e5\u5927\u5b66\u56e2\u961f\u662f\u5728\u6536\u96c6&ldquo;\u73af\u5883DNA&rdquo;\u3002\u5176\u4e2d\u539f\u7406\u5728\u4e8e\uff0c\u6c34\u602a\u5982\u679c\u771f\u7684\u5b58\u5728\uff0c\u90a3\u80af\u5b9a\u4e5f\u4f1a\u5403\u559d\u62c9\u6492\u3001\u751f\u8001\u75c5\u6b7b\u3002\u56e0\u6b64\uff0c\u6c34\u602a\u7684\u5c38\u4f53\u3001\u5206\u6ccc\u7269\u3001\u6392\u6cc4\u7269\u4ee5\u53ca\u4f53\u8868\u7684\u76ae\u5c51\u6216\u9cde\u7247\u90fd\u4f1a\u9057\u843d\u5728\u8fd9\u4e00\u6c6a\u6e56\u6c34\u4e2d\uff0c\u5e76\u5728\u73af\u5883\u4e2d\u91ca\u653e\u51faDNA\u3002\u5982\u679c\u6c34\u602a\u662f\u50cf\u86c7\u9888\u9f99\u8fd9\u4e48\u5927\u4f53\u578b\u7684\u5de8\u517d\uff0c\u7559\u4e0b\u7684\u751f\u7269\u6837\u672c\u4f53\u91cf\u4f1a\u975e\u5e38\u5927\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u8981\u662f\u6c34\u602a\u771f\u7684\u5b58\u5728\uff0c\u6574\u4e2a\u5c3c\u65af\u6e56\u5c31\u50cf\u662f\u98d8\u8361\u7740\u6c34\u602a\u751f\u7269\u6837\u672c\u7684\u5de8\u578bDNA\u5e93\u3002</p>\r\n\r\n<p>\u4ece\u8fd9\u4e2a\u601d\u8def\u6765\u770b\uff0c\u5373\u4f7f\u6211\u4eec\u8fd9\u4e9b\u5e74\u6765\u6ca1\u80fd\u627e\u5230\u6c34\u602a\u7684\u771f\u8eab\uff0c<strong>\u5982\u679c\u80fd\u5728\u6e56\u6c34\u4e2d\u68c0\u6d4b\u4e2d\u5b83\u7684DNA\uff0c\u90a3\u4e0d\u4e5f\u80fd\u591f\u8bc1\u660e\u6c34\u602a\u5b58\u5728\u4e8e\u4e16\u5417\uff1f</strong></p>\r\n\r\n<p>\u4e3a\u6b64\uff0c\u7814\u7a76\u8005\u4eec\u5728\u5c3c\u65af\u6e56\u5404\u4e2a\u533a\u57df\u63d0\u53d6\u4e86250\u4efd\u6c34\u6837\uff0c\u5e76\u5bf9\u5176\u4e2d\u7684DNA\u6837\u672c\u8fdb\u884c\u6d4b\u5e8f\u5206\u6790\u3002\u7edf\u8ba1\u7ed3\u679c\u663e\u793a\uff0c\u6e56\u6c34\u4e2d\u5b58\u5728\u81f3\u5c113000\u4e2a\u7269\u79cd\u7684\u57fa\u56e0\u7247\u6bb5\u3002\u5176\u4e2d\u4e0d\u4ec5\u4ec5\u6709\u5404\u7c7b\u9c7c\u866b\u3001\u690d\u7269\uff0c\u8fd8\u6709\u4e9bDNA\u6765\u81ea\u6e56\u8fb9\u6816\u606f\u7684\u725b\u7f8a\u3001\u5ba0\u7269\u72d7\u3001\u9e1f\u7c7b&hellip;.\u8fd9\u8bf4\u660e\u7814\u7a76\u8005\u7684\u53d6\u6837\u5de5\u4f5c\u505a\u5f97\u5f88\u5b8c\u5584\uff0c\u6837\u672c\u4ee3\u8868\u6027\u5f88\u5f3a\uff0c\u51e1\u662f\u548c\u5c3c\u65af\u6e56\u6cbe\u70b9\u8fb9\u513f\u7684\u751f\u7269DNA\u51e0\u4e4e\u90fd\u88ab\u68c0\u6d4b\u51fa\u6765\u4e86\u3002\u7167\u7406\u8bf4\uff0c\u6c34\u602a\u8981\u662f\u4e5f\u6d3b\u5728\u5c3c\u65af\u6e56\u91cc\uff0c\u5b83\u7684\u57fa\u56e0\u7247\u6bb5\u4e5f\u5e94\u8be5\u51fa\u73b0\u5728\u6c34\u6837\u4e2d\u3002</p>\r\n\r\n<p>\u53ef\u662f\uff0c<strong>\u6837\u672c\u4e2d\u504f\u504f\u6ca1\u6709\u4efb\u4f55\u6765\u81ea\u672a\u77e5\u795e\u79d8\u751f\u7269\u7684DNA</strong><strong>\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55DNA\u6837\u672c\u50cf\u662f\u86c7\u9888\u9f99\u4e00\u7c7b\u7684\u6d77\u6d0b\u722c\u884c\u52a8\u7269\u7559\u4e0b\u7684\u3002</strong>\u81f3\u6b64\u6211\u4eec\u53ea\u80fd\u5f88\u9057\u61be\u5730\u8bf4\uff0c\u6240\u8c13\u7684\u5c3c\u65af\u6e56\u6c34\u602a\u5e94\u5f53\u662f\u4e0d\u5b58\u5728\u7684\u3002</p>\r\n\r\n<p><strong>\u8349\u6728\u7686\u5175\u7684\u5c3c\u65af\u6e56\uff0c\u770b\u5565\u90fd\u50cf\u6c34\u602a</strong></p>\r\n\r\n<p>\u770b\u5230\u8fd9\u6837\u7684\u7814\u7a76\u7ed3\u679c\uff0c\u5f88\u591a\u4eba\u5fc5\u7136\u4e0d\u670d\u6c14\u3002\u8981\u662f\u771f\u6ca1\u6709\u4ec0\u4e48\u5c3c\u65af\u6e56\u6c34\u602a\u7684\u8bdd\uff0c\u90a3\u4e48\u4ee5\u524d\u90a3\u4e9b\u76ee\u51fb\u62a5\u544a\u548c\u7167\u7247\u53c8\u662f\u600e\u4e48\u6765\u7684\u5462\uff1f\u96be\u9053\u662f\u80e1\u7f16\u4e71\u9020\uff1f</p>\r\n\r\n<p>\u6ca1\u9519\uff0c\u4ee5\u5f80\u7684\u8bc1\u636e\u4e2d\u6709\u4e0d\u5c11\u90fd\u662f\u4f2a\u9020\u7684\u3002\u5c31\u4ee5\u6700\u7ecf\u5178\u7684\u5c3c\u65af\u6e56\u6c34\u602a\u7167\u7247\u4e3a\u4f8b\uff0c\u90a3\u5e45\u6444\u4e8e1934\u5e74\u7684\u9ed1\u767d\u7167\u7247\u5176\u5b9e\u662f\u6446\u62cd\u3002<strong>\u6444\u5f71\u8005\u4e34\u7ec8\u524d\u627f\u8ba4\u6240\u8c13\u7684\u6c34\u602a\u53ea\u662f\u4eba\u9020\u6a21\u578b</strong>\uff0c\u6ca1\u60f3\u5230\u5ffd\u60a0\u4e86\u5168\u4e16\u754c\u4eba\u6c11\u51e0\u5341\u5e74\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/0opH4PpifiS0_mMaU2l7JXcuBi7sI-T3IDMibRHq1fqSAQAAWQIAAFBO.png\" style=\"height:601px; width:402px\" />\u76ee\u51fb\u62a5\u544a\u4e2d\uff0c\u5c3c\u65af\u6e56\u6c34\u602a\u9732\u51fa\u6c34\u9762\u65f6\u6700\u5e38\u89c1\u7684\u51e0\u79cd\u5f62\u6001\uff5cWikimedia Commons</p>\r\n\r\n<p>\u9664\u6b64\u4e4b\u5916\uff0c\u6709\u4e0d\u5c11\u7167\u7247\u4e0e\u76ee\u51fb\u62a5\u544a\u53ea\u662f\u5f53\u4e8b\u4eba\u8ba4\u9519\u4e86\u3002\u5728&ldquo;\u8349\u6728\u7686\u5175&rdquo;\u7684\u5c3c\u65af\u6e56\uff0c\u4e00\u4e9b\u6d6e\u6728\u3001\u77f3\u5934\u3001\u4eba\u5de5\u5236\u54c1\u90fd\u6709\u53ef\u80fd\u5728\u6643\u52a8\u7684\u6ce2\u6d9b\u4e2d\u88ab\u7d27\u76ef\u7740\u6e56\u6c34\u7684\u8def\u4eba\u5f3a\u884c\u9519\u8ba4\u6210\u6c34\u602a\u3002\u7136\u800c\uff0c\u6b64\u7c7b\u9759\u7269\u5e76\u4e0d\u80fd\u89e3\u91ca\u6240\u6709\u76ee\u51fb\u62a5\u544a\u7684\u7531\u6765\u3002\u8fd8\u6709\u4e00\u4e9b\u62a5\u544a\u4e2d\u7684&ldquo;\u6c34\u602a&rdquo;\u662f\u50cf\u86c7\u4e00\u6837\u5feb\u901f\u6e38\u52a8\u7684\uff0c\u5b83\u4eec\u53c8\u662f\u4ec0\u4e48\u52a8\u7269\u5462\uff1f</p>\r\n\r\n<p>\u5965\u5854\u683c\u5927\u5b66\u7684\u7814\u7a76\u56e2\u961f\u79f0\uff0c<strong>\u8fd9\u4e9b\u6240\u8c13\u7684\u6c34\u602a\u516b\u6210\u5c31\u662f\u9cd7\u9c7c</strong>\u3002\u5c3c\u65af\u6e56\u4e2d\u76db\u4ea7\u9cd7\u9c7c\uff0c\u5176DNA\u5728\u672c\u6b21\u68c0\u9a8c\u7684\u6c34\u6837\u4e2d\u6781\u5176\u4e30\u5bcc\u3002\u9cd7\u9c7c\u4f53\u578b\u53ef\u4ee5\u751f\u957f\u5f97\u975e\u5e38\u5de8\u5927\uff0c\u6700\u5927\u7684\u5355\u988c\u9cd7\uff08<em>Monognathus</em>&nbsp;\uff09\u4f53\u957f\u8d85\u8fc74\u7c73\uff0c\u57fa\u672c\u80fd\u8fbe\u5230\u5c3c\u65af\u6e56\u6c34\u602a\u7684\u4f53\u578b\u6807\u51c6\u3002\u66f4\u91cd\u8981\u7684\u662f\uff0c\u9cd7\u9c7c\u8eab\u4f53\u4fee\u957f\uff0c\u8fd9\u548c\u76ee\u51fb\u62a5\u544a\u4e2d&ldquo;\u4fee\u957f\u7684\u8116\u9888&rdquo;\u3001&ldquo;\u7ec6\u5c0f\u7684\u8111\u888b&rdquo;\u90fd\u80fd\u5bf9\u4e0a\u53f7\u3002</p>\r\n\r\n<p>\u5f53\u7136\uff0c\u9cd7\u9c7c\u4e5f\u4e0d\u662f\u552f\u4e00\u7684\u7b54\u6848\u3002\u6709\u4eba\u8bf4\u6c34\u602a\u5c31\u662f\u5927\u8c61\u7684\u9f3b\u5b50\uff0c\u56e0\u4e3a\u6e56\u7554\u4ee5\u524d\u66fe\u6709\u9a6c\u620f\u56e2\u517b\u8fc7\u5927\u8c61\uff0c\u8fd8\u8ba9\u5b83\u4eec\u5728\u6e56\u91cc\u6e38\u6cf3\u3002\u53e6\u6709\u4eba\u8ba4\u4e3a\u6c34\u602a\u53ea\u662f\u6c34\u736d\uff0c\u800c\u6211\u4eec\u770b\u89c1\u7684\u957f\u8116\u5b50\u4e0e\u80cc\u810a\u5176\u5b9e\u662f\u6c34\u736d\u7684\u5c41\u80a1\u4e0e\u5c3e\u5df4\u3002</p>\r\n\r\n<p>\u603b\u800c\u8a00\u4e4b\uff0c\u5c3c\u65af\u6e56\u91cc\u5e76\u6ca1\u6709\u4ec0\u4e48\u6c34\u602a\u3002\u4eba\u4eec\u5728\u5c3c\u65af\u6e56\u91cc\u770b\u89c1\u7684\u79cd\u79cd&ldquo;\u795e\u79d8\u751f\u7269&rdquo;\uff0c\u4e0d\u8fc7\u662f\u4e00\u4e9b\u4eba\u60f3\u8c61\u529b\u52a0\u5de5\u653e\u5927\u7684\u5e7b\u5f71\uff0c\u4ee5\u53ca\u53e6\u4e00\u4e9b\u4eba\u5e0c\u671b\u901a\u8fc7\u9020\u5047\u6765\u51fa\u540d\u7684\u865a\u8363\u5fc3\u3002(\u7f16\u8f91\uff1aYuki)</p>\r\n\r\n<h2 class=\"references\">\u53c2\u8003\u6587\u732e\uff1a</h2>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[1] https://www.otago.ac.nz/news/news/otago717609.html</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[2] J. A Carruth&nbsp;Loch Ness and its Monster, (1950) Abbey Press, Fort Augustus, cited by Tim Dinsdale (1961)&nbsp;Loch Ness Monsterpp. 33&ndash;35</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[3] R. J. Binns (1983)&nbsp;The Loch Ness Mystery Solved, page 22</span></span></div>\r\n\r\n<div><span style=\"color:#808080\"><span style=\"font-size:14px\">[4] R. P. Mackal (1976)&nbsp;The Monsters of Loch Ness&nbsp;pages 141&ndash;142, chapter XIV</span></span></div>", "date_created": "2019-10-16T10:04:31.483590+08:00", "date_modified": "2019-10-30T11:50:35.161969+08:00", "date_published": "2019-10-16T11:22:07.816680+08:00", "id": 454868, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "sciblog", "replies_count": 9, "small_image": "https://2-im.guokr.com/MFqaCFymApqPPuUwyrfDhsLggmyrfxoba8HC7JyYU20cAwAAWAIAAEpQ.jpg", "subject": {"articles_count": 2752, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "biology", "minisite_key": "sciblog", "name": "\u751f\u7269", "sort_score": 2, "subject_type": "normal"}, "subject_key": "biology", "summary": "\u4ec0\u4e48\uff1f\u5c3c\u65af\u6e56\u6c34\u602a\u539f\u6765\u662f\u6761...", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u201c\u5c71\u4e0d\u5728\u9ad8\uff0c\u6709\u4ed9\u5219\u540d\u3002\u6c34\u4e0d\u5728\u6df1\uff0c\u6709\u9f99\u5219\u7075\u3002\u201d\u8fd9\u53e5\u4e2d\u56fd\u53e4\u8bdd\u653e\u5728\u82f1\u56fd\u82cf\u683c\u5170\u7684\u5c3c\u65af\u6e56\u4e5f\u540c\u6837\u9002\u7528\u3002\u867d\u7136\u5b83\u4e0d\u662f\u8fd9\u91cc\u6700\u5927\u6700\u6df1\u7684\u6e56\u6cca\uff0c\u4f46\u7531\u4e8e\u5f53\u5730\u6d41\u4f20\u5df2\u4e45\u7684\u201c\u6c34\u602a\u201d\u4f20\u8bf4\uff0c\u5c3c\u65af\u6e56\u7684\u540d\u5b57\u65e9\u5c31\u54cd\u5f7b\u5168\u7403\u3002\u5c3c\u65af\u6e56\u4f4d\u4e8e\u82cf\u683c\u5170\u5317\u90e8\uff0c\u5b83\u4e0d\u4ec5\u6709\u6c34\u602a\u4f20\u8bf4\uff0c\u8fd8\u6709\u6e56\u7554\u7684\u5386\u53f2\u9057\u8ff9\uff5cWikimedia Commons - Sam Fentress\u4e3a\u4e86\u627e\u5230\u5c3c\u65af\u6e56\u6c34\u602a\u7684\u771f\u8eab\uff0c\u8fd1\u767e\u5e74\u6765\uff0c\u65e0\u6570\u5b66\u8005\u548c\u6e38\u5ba2\u7eb7\u7eb7\u524d\u53bb\u5bfb\u627e\u8e2a\u8ff9\u3002\u5404\u5f0f\u5404\u6837\u7684\u6c34\u602a\u7167\u7247\u4e5f\u5927\u884c\u4e8e\u4e16\uff0c\u4ee4\u4e0d\u5c11\u4eba\u575a\u4fe1\u6c34\u602a\u786e\u5b9e\u5b58\u5728\u3002\u7136\u800c\uff0c\u65b0\u897f\u5170\u5965\u5854\u54e5\u5927\u5b66\u505a\u51fa\u7684\u6700\u65b0\u7814\u7a76\u6210\u679c\uff0c\u53ef\u80fd\u8981\u8ba9\u201c\u6c34\u602a\u8ff7\u201d\u4eec\u5931\u671b\u4e86\u3002\u4ed6\u4eec\u68c0\u6d4b\u4e86\u6e56\u6c34\u4e2d\u7684DNA\u540e\u53d1\u73b0\uff1a\u5c3c\u65af\u6e56\u91cc\u5f88\u53ef\u80fd\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u6c34\u602a\uff0c\u6240\u8c13\u7684\u6c34\u602a\u6216\u8bb8\u53ea\u662f\u4f53\u578b\u5f88\u5927\u7684\u201c\u9cd7\u9c7c\u201d[1]\u3002\u56fe\uff5c\u300a\u54c6\u5566A\u68a6\u300b\u6c34\u602a\u7b80\u53f2\u5728\u5404\u7c7b\u79d1\u666e\u8282\u76ee\u548c\u4e66\u7c4d\u4e2d\uff0c\u201c\u5c3c\u65af\u6e56\u6c34\u602a\u201d\u7684\u6545\u4e8b\u8fd1\u5e74\u6765\u4e00\u76f4\u6709\u5f88\u9ad8\u7684\u51fa\u955c\u7387\u3002\u4e0d\u8fc7\uff0c\u5b83\u53ef\u4e0d\u662f\u5728\u73b0\u4ee3\u624d\u53d8\u6210\u7f51\u7ea2\u7684\u3002\u65e9\u5728\u5c06\u8fd11500\u5e74\u524d\uff0c\u201c\u6c34\u602a\u201d\u5c31\u5df2\u7ecf\u6210\u4e3a\u4e86\u5c3c\u65af\u6e56\u7684\u4f20\u5947\u3002\u5c3c\u65af\u6e56\u6c34\u602a\u7684\u6545\u4e8b\u6700\u65e9\u89c1\u4e8e\u516c\u51437\u4e16\u7eaa\u9762\u4e16\u7684\u4f20\u8bb0\u300a\u5723\u5e93\u4ed1\u751f\u5e73\u300b\uff08Life of St. Columba\uff09\u3002\u4e66\u4e2d\u5199\u5230\uff1a\u8457\u540d\u4fee\u9053\u58eb\u5723\u5e93\u4ed1\u542c\u95fb\u5c3c\u65af\u6cb3\uff08\u6e90\u81ea\u5c3c\u65af\u6e56\u7684\u4e00\u6761\u5c0f\u6cb3\uff09\u4e2d\u6709\u51f6\u731b\u91ce\u517d\u88ad\u51fb\u4eba\u7c7b\uff0c\u4ed6\u5c31\u60f3\u8981\u8fd8\u5c45\u6c11\u4e00\u65b9\u5b89\u5b81\u3002\u4e8e\u662f\uff0c\u4ed6\u6d3e\u81ea\u5df1\u7684\u624b\u4e0b\u5728\u5c3c\u65af\u6cb3\u4e2d\u6e38\u6cf3\u3002\u679c\u4e0d\u5176\u7136\uff0c\u6c34\u602a\u7a81\u7136\u6d6e\u51fa\u6c34\u9762\u51c6\u5907\u884c\u51f6\u3002\u8fd9\u65f6\uff0c\u5723\u5e93\u4ed1\u7528\u624b\u6307\u5212\u51fa\u5341\u5b57\uff0c\u5475\u65a5\u6c34\u602a\u8bf4\u201c\u83ab\u9760\u8fd1\uff0c\u83ab\u4f24\u4eba\uff0c\u901f\u5f52\u53bb\uff01\u201d\u3002\u6ca1\u60f3\u5230\uff0c\u6c34\u602a\u4e00\u542c\u8fd9\u8bdd\uff0c\u9a6c\u4e0a\u88ab\u4e00\u79cd\u65e0\u5f62\u7684\u529b\u91cf\u7ed9\u62fd\u8dd1\u4e86[2]\u3002\u5723\u5e93\u4ed1\u662f\u82cf\u683c\u5170\u5730\u533a\u4f20\u5947\u4eba\u7269\uff0c\u4ed6\u66fe\u662f\u8457\u540d\u7684\u5929\u4e3b\u6559\u50e7\u4fa3\uff5cWikimedia Commons - J. R. Skelton\u5723\u5e93\u4ed1\u7684\u6545\u4e8b\u5f53\u7136\u4e0d\u53ef\u4fe1\u3002\u5728\u90a3\u4e2a\u65f6\u4ee3\uff0c\u5199\u8fd9\u79cd\u6c34\u602a\u7684\u6545\u4e8b\u53ef\u80fd\u53ea\u662f\u4e3a\u4e86\u5ba3\u626c\u795e\u529b\u548c\u4e2a\u4eba\u9b45\u529b\u3002\u5e76\u4e14\uff0c\u5b83\u4e5f\u6ca1\u63cf\u8ff0\u5c3c\u65af\u6e56\u6c34\u602a\u7a76\u7adf\u957f\u5f97\u662f\u4e2a\u4ec0\u4e48\u6a21\u6837\u3002\u4f46\u662f\u7b49\u523019\u4e16\u7eaa\u4ee5\u540e\uff0c\u5404\u7c7b\u76ee\u51fb\u62a5\u544a\u4e43\u81f3\u6444\u5f71\u7167\u7247\u9010\u6e10\u4e30\u5bcc\u4e86\u8d77\u6765\uff0c\u8fd8\u628a\u6c34\u602a\u7684\u6837\u5b50\u523b\u753b\u5f97\u6829\u6829\u5982\u751f\uff0c\u8fd9\u5c31\u4e0d\u5f97\u4e0d\u8ba9\u4eba\u601d\u8003\uff1a\u5c3c\u65af\u6e56\u91cc\u662f\u4e0d\u662f\u771f\u7684\u6709\u4e0d\u4e3a\u4eba\u77e5\u7684\u795e\u79d8\u602a\u7269\u5462\uff1f\u8fd9\u4e9b\u76ee\u51fb\u4e8b\u4ef6\u53d1\u751f\u7684\u4f4d\u7f6e\u51e0\u4e4e\u904d\u5e03\u5c3c\u65af\u6e56\u5404\u4e2a\u89d2\u843d\uff0c\u6240\u63cf\u8ff0\u7684\u6c34\u602a\u5f62\u6001\u4e5f\u5404\u6709\u4e0d\u540c\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u53e3\u4e2d\u7684\u6c34\u602a\u5f62\u8c61\u90fd\u6709\u4e00\u4e9b\u5171\u6027\u3002\u5c31\u4ee5\u6700\u5177\u4ee3\u8868\u6027\u7684\u4e00\u5f20\u7167\u7247\u4e3a\u4f8b\uff0c\u56fe\u4e2d\u7684\u6c34\u602a\u6709\u7ec6\u5c0f\u7684\u8111\u888b\u3001\u4fee\u957f\u7684\u8116\u5b50\u3001\u5e9e\u5927\u7684\u8eab\u8eaf\u2026\u2026\u6211\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4f60\u7684\u8111\u6d77\u4e2d\u662f\u4e0d\u662f\u6d6e\u73b0\u51fa\u4e86\u4e00\u79cd\u8fdc\u53e4\u751f\u7269\u7684\u5f62\u8c61\uff1f\u5c3c\u65af\u6e56\u6c34\u602a\u6700\u7ecf\u5178\u7684\u4e00\u5f20\u7167\u7247\uff0c\u6444\u4e8e1934\u5e74\uff5cWikimedia Commons -\u00a0 Robert Kenneth Wilson\u6ca1\u9519\uff01\u4eba\u4eec\u63cf\u7ed8\u7684\u6c34\u602a\u50cf\u6781\u4e86\u86c7\u9888\u9f99\u3002\u90a3\u662f\u4e00\u79cd\u751f\u6d3b\u5728\u4f8f\u7f57\u7eaa\u65f6\u4ee3\u7684\u5de8\u578b\u6d77\u6d0b\u722c\u884c\u52a8\u7269\u3002\u4e8e\u662f\u4e4e\uff0c\u5f88\u591a\u4eba\u5f00\u59cb\u6000\u7591\u5c3c\u65af\u6e56\u6c34\u602a\u5176\u5b9e\u662f\u6050\u9f99\u65f6\u4ee3\u9057\u7559\u81f3\u4eca\u7684 \u201c\u6d3b\u5316\u77f3\u201d[3]\u3002\u5728\u76f8\u5173\u5f71\u89c6\u4f5c\u54c1\u4e2d\uff0c\u5c3c\u65af\u6e56\u6c34\u602a\u4e5f\u666e\u904d\u662f\u6050\u9f99\u6216\u86c7\u9888\u9f99\u7684\u5f62\u8c61\u3002\u6b64\u5916\uff0c\u4e5f\u6709\u4e2a\u522b\u4eba\u6839\u636e\u5176\u4ed6\u76ee\u51fb\u62a5\u544a\u63a8\u6d4b\u6c34\u602a\u662f\u67d0\u79cd\u672a\u77e5\u7684\u65e0\u810a\u690e\u52a8\u7269\u6216\u4e24\u6816\u52a8\u7269[4]\u3002\u65e0\u8bba\u5982\u4f55\uff0c\u516c\u4f17\u5df2\u7ecf\u4e0d\u518d\u50cf\u5723\u5e93\u4ed1\u90a3\u4e2a\u65f6\u4ee3\u4e00\u6837\u89c9\u5f97\u6c34\u602a\u662f\u8d85\u81ea\u7136\u7684\u3001\u4e0e\u5b97\u6559\u6709\u5173\u7684\u795e\u517d\uff0c\u800c\u662f\u8ba4\u4e3a\u5b83\u662f\u67d0\u79cd\u5c1a\u672a\u88ab\u53d1\u73b0\u7684\u7269\u79cd\u3002\u5f53\u5730\u4eba\u6839\u636e\u76ee\u51fb\u8005\u63cf\u8ff0\u5236\u4f5c\u51fa\u7684\u5c3c\u65af\u6e56\u6c34\u602a\u6a21\u578b\uff0c\u548c\u86c7\u9888\u9f99\u9ad8\u5ea6\u76f8\u4f3c\uff5cWikimedia Commons - A Obeidat\u662f\u9c7c\u662f\u602a\uff1f\u7528DNA\u8bc1\u636e\u8bf4\u8bdd\u65e2\u7136\u201c\u6c34\u602a\u201d\u88ab\u8ba4\u4e3a\u662f\u79cd\u751f\u7269\uff0c\u90a3\u4e48\u5b83\u5fc5\u5b9a\u4e5f\u6709\u8840\u6709\u8089\uff0c\u8bf4\u4e0d\u5b9a\u751f\u6d3b\u4e60\u6027\u4e5f\u4f1a\u50cf\u957f\u9888\u9f99\u4e00\u6837\uff0c\u9700\u8981\u7ecf\u5e38\u6d6e\u51fa\u6c34\u9762\u6362\u6c14\u3001\u89c5\u98df\u3001\u770b\u98ce\u666f\uff08\u8bef\uff09\u3002\u603b\u4e4b\u4eba\u4eec\u76f8\u4fe1\uff0c\u5982\u679c\u6c34\u602a\u771f\u7684\u5b58\u5728\uff0c\u90a3\u4e48\u4ed6\u4e00\u5b9a\u662f\u80fd\u88ab\u4eba\u7c7b\u89c2\u5bdf\u5230\u7684\u3002\u4e8e\u662f\u4e4e\uff0c\u6210\u767e\u4e0a\u5343\u7684\u6e38\u5ba2\u4e0e\u6c34\u602a\u7231\u597d\u8005\u90fd\u62ff\u7740\u957f\u67aa\u77ed\u70ae\u5b88\u5728\u6e56\u8fb9\uff0c\u4e0d\u5c11\u7814\u7a76\u673a\u6784\u751a\u81f3\u8fd8\u52a8\u7528\u4e86\u58f0\u5450\u6280\u672f\u641c\u7d22\u6c34\u602a\u8e2a\u8ff9\u3002\u53ef\u662f\u8fd1\u4e00\u4e2a\u4e16\u7eaa\u4e0b\u6765\uff0c\u6240\u6709\u7684\u641c\u5bfb\u6d3b\u52a8\u90fd\u6ca1\u83b7\u5f97\u5b9e\u8d28\u6027\u8fdb\u5c55\u3002\u9664\u4e86\u5c11\u91cf\u771f\u4f2a\u96be\u8fa8\u7684\u7167\u7247\u4e4b\u5916\uff0c\u6211\u4eec\u8fde\u6c34\u602a\u7684\u6bdb\u4e5f\u6ca1\u635e\u5230\u3002\u76f8\u6bd4\u4e4b\u4e0b\uff0c\u65b0\u897f\u5170\u5965\u5854\u54e5\u5927\u5b66\u6559\u6388\u683c\u6885\u5c14\uff08Neil Gemmell\uff09\u7684\u56e2\u961f\u6709\u70b9\u201c\u5251\u8d70\u504f\u950b\u201d\u3002\u4ed6\u4eec\u6ca1\u6709\u50cf\u5176\u4ed6\u4eba\u4e00\u6837\u4e13\u95e8\u53bb\u641c\u5bfb\u6c34\u602a\u7684\u771f\u8eab\uff0c\u800c\u662f\u5f00\u7740\u4e2a\u8239\u5728\u6e56\u91cc\u5230\u5904\u635e\u6e56\u6c34\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u8fd9\u4e48\u505a\u5230\u5e95\u662f\u5728\u5e72\u4ec0\u4e48\u5462\uff1f\u683c\u6885\u5c14\u6559\u6388\u7ad9\u5728\u5c3c\u65af\u6e56\u7554\uff5cwww.otago.ac.nz\u539f\u6765\uff0c\u5965\u5854\u54e5\u5927\u5b66\u56e2\u961f\u662f\u5728\u6536\u96c6\u201c\u73af\u5883DNA\u201d\u3002\u5176\u4e2d\u539f\u7406\u5728\u4e8e\uff0c\u6c34\u602a\u5982\u679c\u771f\u7684\u5b58\u5728\uff0c\u90a3\u80af\u5b9a\u4e5f\u4f1a\u5403\u559d\u62c9\u6492\u3001\u751f\u8001\u75c5\u6b7b\u3002\u56e0\u6b64\uff0c\u6c34\u602a\u7684\u5c38\u4f53\u3001\u5206\u6ccc\u7269\u3001\u6392\u6cc4\u7269\u4ee5\u53ca\u4f53\u8868\u7684\u76ae\u5c51\u6216\u9cde\u7247\u90fd\u4f1a\u9057\u843d\u5728\u8fd9\u4e00\u6c6a\u6e56\u6c34\u4e2d\uff0c\u5e76\u5728\u73af\u5883\u4e2d\u91ca\u653e\u51faDNA\u3002\u5982\u679c\u6c34\u602a\u662f\u50cf\u86c7\u9888\u9f99\u8fd9\u4e48\u5927\u4f53\u578b\u7684\u5de8\u517d\uff0c\u7559\u4e0b\u7684\u751f\u7269\u6837\u672c\u4f53\u91cf\u4f1a\u975e\u5e38\u5927\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u8981\u662f\u6c34\u602a\u771f\u7684\u5b58\u5728\uff0c\u6574\u4e2a\u5c3c\u65af\u6e56\u5c31\u50cf\u662f\u98d8\u8361\u7740\u6c34\u602a\u751f\u7269\u6837\u672c\u7684\u5de8\u578bDNA\u5e93\u3002\u4ece\u8fd9\u4e2a\u601d\u8def\u6765\u770b\uff0c\u5373\u4f7f\u6211\u4eec\u8fd9\u4e9b\u5e74\u6765\u6ca1\u80fd\u627e\u5230\u6c34\u602a\u7684\u771f\u8eab\uff0c\u5982\u679c\u80fd\u5728\u6e56\u6c34\u4e2d\u68c0\u6d4b\u4e2d\u5b83\u7684DNA\uff0c\u90a3\u4e0d\u4e5f\u80fd\u591f\u8bc1\u660e\u6c34\u602a\u5b58\u5728\u4e8e\u4e16\u5417\uff1f\u4e3a\u6b64\uff0c\u7814\u7a76\u8005\u4eec\u5728\u5c3c\u65af\u6e56\u5404\u4e2a\u533a\u57df\u63d0\u53d6\u4e86250\u4efd\u6c34\u6837\uff0c\u5e76\u5bf9\u5176\u4e2d\u7684DNA\u6837\u672c\u8fdb\u884c\u6d4b\u5e8f\u5206\u6790\u3002\u7edf\u8ba1\u7ed3\u679c\u663e\u793a\uff0c\u6e56\u6c34\u4e2d\u5b58\u5728\u81f3\u5c113000\u4e2a\u7269\u79cd\u7684\u57fa\u56e0\u7247\u6bb5\u3002\u5176\u4e2d\u4e0d\u4ec5\u4ec5\u6709\u5404\u7c7b\u9c7c\u866b\u3001\u690d\u7269\uff0c\u8fd8\u6709\u4e9bDNA\u6765\u81ea\u6e56\u8fb9\u6816\u606f\u7684\u725b\u7f8a\u3001\u5ba0\u7269\u72d7\u3001\u9e1f\u7c7b\u2026.\u8fd9\u8bf4\u660e\u7814\u7a76\u8005\u7684\u53d6\u6837\u5de5\u4f5c\u505a\u5f97\u5f88\u5b8c\u5584\uff0c\u6837\u672c\u4ee3\u8868\u6027\u5f88\u5f3a\uff0c\u51e1\u662f\u548c\u5c3c\u65af\u6e56\u6cbe\u70b9\u8fb9\u513f\u7684\u751f\u7269DNA\u51e0\u4e4e\u90fd\u88ab\u68c0\u6d4b\u51fa\u6765\u4e86\u3002\u7167\u7406\u8bf4\uff0c\u6c34\u602a\u8981\u662f\u4e5f\u6d3b\u5728\u5c3c\u65af\u6e56\u91cc\uff0c\u5b83\u7684\u57fa\u56e0\u7247\u6bb5\u4e5f\u5e94\u8be5\u51fa\u73b0\u5728\u6c34\u6837\u4e2d\u3002\u53ef\u662f\uff0c\u6837\u672c\u4e2d\u504f\u504f\u6ca1\u6709\u4efb\u4f55\u6765\u81ea\u672a\u77e5\u795e\u79d8\u751f\u7269\u7684DNA\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55DNA\u6837\u672c\u50cf\u662f\u86c7\u9888\u9f99\u4e00\u7c7b\u7684\u6d77\u6d0b\u722c\u884c\u52a8\u7269\u7559\u4e0b\u7684\u3002\u81f3\u6b64\u6211\u4eec\u53ea\u80fd\u5f88\u9057\u61be\u5730\u8bf4\uff0c\u6240\u8c13\u7684\u5c3c\u65af\u6e56\u6c34\u602a\u5e94\u5f53\u662f\u4e0d\u5b58\u5728\u7684\u3002\u8349\u6728\u7686\u5175\u7684\u5c3c\u65af\u6e56\uff0c\u770b\u5565\u90fd\u50cf\u6c34\u602a\u770b\u5230\u8fd9\u6837\u7684\u7814\u7a76\u7ed3\u679c\uff0c\u5f88\u591a\u4eba\u5fc5\u7136\u4e0d\u670d\u6c14\u3002\u8981\u662f\u771f\u6ca1\u6709\u4ec0\u4e48\u5c3c\u65af\u6e56\u6c34\u602a\u7684\u8bdd\uff0c\u90a3\u4e48\u4ee5\u524d\u90a3\u4e9b\u76ee\u51fb\u62a5\u544a\u548c\u7167\u7247\u53c8\u662f\u600e\u4e48\u6765\u7684\u5462\uff1f\u96be\u9053\u662f\u80e1\u7f16\u4e71\u9020\uff1f\u6ca1\u9519\uff0c\u4ee5\u5f80\u7684\u8bc1\u636e\u4e2d\u6709\u4e0d\u5c11\u90fd\u662f\u4f2a\u9020\u7684\u3002\u5c31\u4ee5\u6700\u7ecf\u5178\u7684\u5c3c\u65af\u6e56\u6c34\u602a\u7167\u7247\u4e3a\u4f8b\uff0c\u90a3\u5e45\u6444\u4e8e1934\u5e74\u7684\u9ed1\u767d\u7167\u7247\u5176\u5b9e\u662f\u6446\u62cd\u3002\u6444\u5f71\u8005\u4e34\u7ec8\u524d\u627f\u8ba4\u6240\u8c13\u7684\u6c34\u602a\u53ea\u662f\u4eba\u9020\u6a21\u578b\uff0c\u6ca1\u60f3\u5230\u5ffd\u60a0\u4e86\u5168\u4e16\u754c\u4eba\u6c11\u51e0\u5341\u5e74\u3002\u76ee\u51fb\u62a5\u544a\u4e2d\uff0c\u5c3c\u65af\u6e56\u6c34\u602a\u9732\u51fa\u6c34\u9762\u65f6\u6700\u5e38\u89c1\u7684\u51e0\u79cd\u5f62\u6001\uff5cWikimedia Commons\u9664\u6b64\u4e4b\u5916\uff0c\u6709\u4e0d\u5c11\u7167\u7247\u4e0e\u76ee\u51fb\u62a5\u544a\u53ea\u662f\u5f53\u4e8b\u4eba\u8ba4\u9519\u4e86\u3002\u5728\u201c\u8349\u6728\u7686\u5175\u201d\u7684\u5c3c\u65af\u6e56\uff0c\u4e00\u4e9b\u6d6e\u6728\u3001\u77f3\u5934\u3001\u4eba\u5de5\u5236\u54c1\u90fd\u6709\u53ef\u80fd\u5728\u6643\u52a8\u7684\u6ce2\u6d9b\u4e2d\u88ab\u7d27\u76ef\u7740\u6e56\u6c34\u7684\u8def\u4eba\u5f3a\u884c\u9519\u8ba4\u6210\u6c34\u602a\u3002\u7136\u800c\uff0c\u6b64\u7c7b\u9759\u7269\u5e76\u4e0d\u80fd\u89e3\u91ca\u6240\u6709\u76ee\u51fb\u62a5\u544a\u7684\u7531\u6765\u3002\u8fd8\u6709\u4e00\u4e9b\u62a5\u544a\u4e2d\u7684\u201c\u6c34\u602a\u201d\u662f\u50cf\u86c7\u4e00\u6837\u5feb\u901f\u6e38\u52a8\u7684\uff0c\u5b83\u4eec\u53c8\u662f\u4ec0\u4e48\u52a8\u7269\u5462\uff1f\u5965\u5854\u683c\u5927\u5b66\u7684\u7814\u7a76\u56e2\u961f\u79f0\uff0c\u8fd9\u4e9b\u6240\u8c13\u7684\u6c34\u602a\u516b\u6210\u5c31\u662f\u9cd7\u9c7c\u3002\u5c3c\u65af\u6e56\u4e2d\u76db\u4ea7\u9cd7\u9c7c\uff0c\u5176DNA\u5728\u672c\u6b21\u68c0\u9a8c\u7684\u6c34\u6837\u4e2d\u6781\u5176\u4e30\u5bcc\u3002\u9cd7\u9c7c\u4f53\u578b\u53ef\u4ee5\u751f\u957f\u5f97\u975e\u5e38\u5de8\u5927\uff0c\u6700\u5927\u7684\u5355\u988c\u9cd7\uff08Monognathus\u00a0\uff09\u4f53\u957f\u8d85\u8fc74\u7c73\uff0c\u57fa\u672c\u80fd\u8fbe\u5230\u5c3c\u65af\u6e56\u6c34\u602a\u7684\u4f53\u578b\u6807\u51c6\u3002\u66f4\u91cd\u8981\u7684\u662f\uff0c\u9cd7\u9c7c\u8eab\u4f53\u4fee\u957f\uff0c\u8fd9\u548c\u76ee\u51fb\u62a5\u544a\u4e2d\u201c\u4fee\u957f\u7684\u8116\u9888\u201d\u3001\u201c\u7ec6\u5c0f\u7684\u8111\u888b\u201d\u90fd\u80fd\u5bf9\u4e0a\u53f7\u3002\u5f53\u7136\uff0c\u9cd7\u9c7c\u4e5f\u4e0d\u662f\u552f\u4e00\u7684\u7b54\u6848\u3002\u6709\u4eba\u8bf4\u6c34\u602a\u5c31\u662f\u5927\u8c61\u7684\u9f3b\u5b50\uff0c\u56e0\u4e3a\u6e56\u7554\u4ee5\u524d\u66fe\u6709\u9a6c\u620f\u56e2\u517b\u8fc7\u5927\u8c61\uff0c\u8fd8\u8ba9\u5b83\u4eec\u5728\u6e56\u91cc\u6e38\u6cf3\u3002\u53e6\u6709\u4eba\u8ba4\u4e3a\u6c34\u602a\u53ea\u662f\u6c34\u736d\uff0c\u800c\u6211\u4eec\u770b\u89c1\u7684\u957f\u8116\u5b50\u4e0e\u80cc\u810a\u5176\u5b9e\u662f\u6c34\u736d\u7684\u5c41\u80a1\u4e0e\u5c3e\u5df4\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u5c3c\u65af\u6e56\u91cc\u5e76\u6ca1\u6709\u4ec0\u4e48\u6c34\u602a\u3002\u4eba\u4eec\u5728\u5c3c\u65af\u6e56\u91cc\u770b\u89c1\u7684\u79cd\u79cd\u201c\u795e\u79d8\u751f\u7269\u201d\uff0c\u4e0d\u8fc7\u662f\u4e00\u4e9b\u4eba\u60f3\u8c61\u529b\u52a0\u5de5\u653e\u5927\u7684\u5e7b\u5f71\uff0c\u4ee5\u53ca\u53e6\u4e00\u4e9b\u4eba\u5e0c\u671b\u901a\u8fc7\u9020\u5047\u6765\u51fa\u540d\u7684\u865a\u8363\u5fc3\u3002(\u7f16\u8f91\uff1aYuki)\u53c2\u8003\u6587\u732e\uff1a[1] https://www.otago.ac.nz/news/news/otago717609.html[2] J. A Carruth\u00a0Loch Ness and its Monster, (1950) Abbey Press, Fort Augustus, cited by Tim Dinsdale (1961)\u00a0Loch Ness Monsterpp. 33\u201335[3] R. J. Binns (1983)\u00a0The Loch Ness Mystery Solved, page 22[4] R. P. Mackal (1976)\u00a0The Monsters of Loch Ness\u00a0pages 141\u2013142, chapter XIV", "title": "\u5c3c\u65af\u6e56\u6c34\u602a\u4e4b\u8c1c\uff0c\u7ec8\u4e8e\u771f\u76f8\u4e86\uff01", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "\u8d3e\u9e64\u9e4f", "title": "\u79d1\u5b66\u8bc4\u8bba\u5b66\u8005", "ukey": "nq0zan"}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/9bhrQqNRGwas7ewrAaNwGnEPeqv4yz_QyykcJLk_s_ugAAAAoAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "\u8d3e\u9e64\u9e4f", "title": "\u79d1\u5b66\u8bc4\u8bba\u5b66\u8005", "ukey": "nq0zan"}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [{"articles_count": 3848, "channel_type": "normal", "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "viewpoint", "name": "\u89c2\u70b9", "sort_score": 2}], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<p>\u5f53\u56fd\u4eba\u8fd8\u6c89\u6d78\u5728\u56fd\u5e86\u5927\u9605\u5175\u5c55\u793a\u7684\u5c16\u7aef\u519b\u4e8b\u79d1\u6280\u65f6\uff0c\u592e\u89c6\u8001\u724c\u79d1\u5b66\u7535\u89c6\u8282\u76ee\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u4ece10\u6708\u8d77\u5c06\u505c\u64ad\u7684\u6d88\u606f\uff0c\u5373\u4fbf\u5728\u79d1\u5b66\u4f20\u64ad\u5708\u5b50\u91cc\uff0c\u4e5f\u663e\u5f97\u6ce2\u6f9c\u4e0d\u60ca\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/wn4DQfUS5YHDIKlxREv5w8P5nIAEK8F3Fdq9--PmUB84BAAAowIAAEpQ.jpg\" style=\"height:400px; width:640px\" /></p>\r\n\r\n<p>\u8bf4\u5b9e\u8bdd\uff0c\u51fa\u56fd\u540e\u6211\u5df2\u591a\u5e74\u6ca1\u770b\u8fd9\u6863\u8282\u76ee\u4e86\uff0c\u4e0d\u8fc7\u4f5c\u4e3a\u5f53\u5e74\u592e\u89c6\u79d1\u5b66\u7c7b\u8282\u76ee\u6392\u884c\u699c\u540d\u5217\u524d\u8305\u7684\u8282\u76ee\uff0c\u5176\u505c\u64ad\u8fd8\u662f\u5177\u6709\u67d0\u79cd\u7b26\u53f7\u610f\u4e49\u3002\u4e8e\u662f\uff0c\u5728\u770b\u4e86\u300a\u4f20\u5a92\u89c1\u95fb\u300b\u516c\u4f17\u53f7\u520a\u767b\u7684\u76f8\u5173\u6587\u7ae0\u540e\uff0c\u6211\u4e0eiScientist\u7684Yuki\u7f16\u8f91\u804a\u8d77\u6b64\u4e8b\uff0cYuki\u8bf4\u4e86\u4e00\u53e5\u975e\u5e38\u7ecf\u5178\u7684\u8bdd\uff1a&ldquo;<strong>\u8d70\u8fd1\u79d1\u5b66\u8d70\u4e86\uff0c\u4f46\u79d1\u5b66\u4ece\u672a\u8d70\u8fdc!</strong>&rdquo;</p>\r\n\r\n<p>\u8fd9\u53e5\u7ecf\u5178\u8bc4\u4ef7\u63d0\u9192\u6211\uff0c\u5e94\u8be5\u5728\u6211\u7684&ldquo;<strong>\u79d1\u5b66\u4f20\u64ad\u7684\u79d1\u666e</strong>&rdquo;\u4e13\u680f\u4e2d\u63a2\u8ba8\u4e0b\u7535\u89c6\u4e2d\u7684\u79d1\u5b66\u8fd9\u4e00\u79d1\u666e\u7684\u91cd\u8981\u5f62\u5f0f\u3002</p>\r\n\r\n<h2><strong>&ldquo;\u8d70\u8fd1\u79d1\u5b66&rdquo;\u7684\u7ea0\u7ed3</strong></h2>\r\n\r\n<p>\u5728\u63a2\u8ba8\u7535\u89c6\u4e2d\u7684\u79d1\u5b66\u8fd9\u4e2a\u8bdd\u9898\u524d\uff0c\u8fd8\u662f\u6709\u5fc5\u8981\u5148\u770b\u770b\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u7684\u5386\u7a0b\u3002\u8fd9\u91cc\u5e76\u4e0d\u6253\u7b97\u8be6\u7ec6\u63cf\u8ff0\u8fd9\u6863\u8282\u76ee\u7684\u5386\u53f2\uff0c\u6709\u5174\u8da3\u7684\u8bfb\u8005\u53ef\u4ee5\u4ece\u5404\u79cd\u5728\u7ebf\u767e\u79d1\u4e2d\u4e86\u89e3\u3002</p>\r\n\r\n<p>\u6211\u53ea\u8bb0\u5f97\uff0c\u8fd9\u6863\u8282\u76ee\u6700\u521d\u662f\u5728\u592e\u89c6&ldquo;\u65b0\u95fb\u8054\u64ad&rdquo;\u6216&ldquo;\u7126\u70b9\u8bbf\u8c08&rdquo;\u540e\u76f8\u5f53\u597d\u7684\u65f6\u6bb5\u64ad\u51fa\u3002\u6700\u65e9\u7684\u51e0\u5e74\uff0c\u8282\u76ee\u62a5\u9053\u7684\uff0c\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\u8bb4\u6b4c\u7684\uff0c\u90fd\u662f\u56fd\u5185\u6709\u91cd\u5927\u610f\u4e49\u7684\u79d1\u6280\u8fdb\u6b65\uff0c\u800c\u5224\u65ad\u91cd\u8981\u610f\u4e49\u7684\u6807\u51c6\u4e3b\u8981\u662f\u6240\u62a5\u9053\u7684\u79d1\u6280\u5982\u4f55\u4fc3\u8fdb\u56fd\u5bb6\u7684\u79d1\u6280\u8fdb\u6b65\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/lKVkFeErJ0kycJKKhf7AKykORmsAfTXdhUxvfOeedRUYBAAABwMAAEpQ.jpg\" style=\"height:473px; width:640px\" />1998\u5e74\u300a\u8d70\u8fdb\u79d1\u5b66\u300b\u7684\u7247\u5934 | \u4f18\u9177</p>\r\n\r\n<p>\u7531\u4e8e\u65b0\u95fb\u8054\u64ad\u4e2d\u5f88\u5c11\u62a5\u9053\u79d1\u5b66\uff0c\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u5f53\u65f6\u5728\u8282\u76ee\u8bbe\u7f6e\u4e0a\u66f4\u50cf\u662f\u957f\u65b0\u95fb\uff0c\u4ece\u79d1\u666e\u4f9b\u7ed9\u7684\u89d2\u5ea6\u6765\u770b\uff0c\u8fd9\u65e0\u7591\u662f\u4e00\u5927\u8fdb\u6b65\u3002\u8fd8\u8bb0\u5f97\u5f53\u65f6<strong>\u6807\u51c6\u7684\u53d9\u4e8b\u65b9\u5f0f\u662f\u56fd\u6c11\u7ecf\u6d4e\u4e2d\u9047\u5230\u4e86\u4ec0\u4e48\u95ee\u9898\uff0c\u7136\u540e\u79d1\u5b66\u5bb6\u6216\u5de5\u7a0b\u5e08\u4eec\u51fa\u73b0\u4e86\uff0c\u7528\u4ed6\u4eec\u7684\u667a\u6167\u6218\u80dc\u4e86\u6311\u6218</strong>\u3002</p>\r\n\r\n<p>\u9057\u61be\u7684\u662f\uff0c\u5b83\u7684\u6536\u89c6\u7387\u4f3c\u4e4e\u4e00\u76f4\u4e0d\u9ad8\u3002\u6211\u6000\u7591\u666e\u901a\u89c2\u4f17\u53ef\u80fd\u4e3b\u8981\u5229\u7528\u8fd9\u4e2a\u4ecb\u4e8e\u65b0\u95fb\u8054\u64ad\u4e0e\u9ec4\u91d1\u6863\u7535\u89c6\u5267\u4e4b\u95f4\u7684\u65f6\u6bb5\u4e0a\u5395\u6240\u6216\u8005\u5403\u665a\u996d\u3002\u4e8e\u662f\uff0c\u8282\u76ee\u540e\u6765\u88ab\u79fb\u51fa\u4e86\u9ec4\u91d1\u65f6\u6863\uff0c\u53c8\u4ece\u592e\u89c6\u4e00\u5957\u8fc1\u5230\u79d1\u6559\u9891\u9053\u64ad\u51fa\u3002</p>\r\n\r\n<p>\u6b63\u662f\u8fd9\u6b21\u642c\u5bb6\uff0c\u8ba9\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u6447\u8eab\u4e00\u53d8\uff0c\u6536\u89c6\u7387\u8fc5\u901f\u6500\u5347\u3002\u539f\u56e0\u4e48\uff1f\u7528\u6279\u8bc4\u8005\u7684\u8bdd\u8bb2\uff0c\u4f3c\u4e4e\u662f&ldquo;\u8d70\u8fd1\u79d1\u5b66&rdquo;\u53d8\u6210\u4e86&ldquo;\u8d70\u8fd1\u4e0d\u79d1\u5b66&rdquo;\u3002<strong>\u8fd9\u4e2a\u9636\u6bb5\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\uff0c\u5927\u591a\u6570\u62a5\u9053\u7684\u662f\u5404\u79cd\u7075\u5f02\u4e8b\u4ef6\uff0c\u7ecf\u8fc7\u66f2\u6298\u8ff7\u79bb\u751a\u81f3\u662f\u60ca\u5fc3\u52a8\u9b44\u7684\u53d1\u5c55\uff0c\u6700\u7ec8\u7684&ldquo;\u79d1\u5b66&rdquo;\u7ed3\u8bba\u662f\uff0c\u539f\u6765\u5f53\u4e8b\u4eba\u641e\u9519\u4e86</strong>\u3002</p>\r\n\r\n<p>\u501f\u7528\u300a\u4f20\u5a92\u89c1\u95fb\u300b\u516c\u53f7\u6587\u7ae0\u4e3e\u7684\u51e0\u4e2a\u5e7f\u6cdb\u6d41\u4f20\u7684\u4f8b\u5b50\u3002\u4e00\u4f4d\u6cb3\u5317\u519c\u6c11\u8fde\u7eed\u4e09\u6b21\u795e\u79d8\u5931\u8e2a\uff0c\u4e00\u591c\u4e4b\u95f4\u817e\u98de\u81f3\u4e00\u5343\u591a\u516c\u91cc\u5916\u7684\u5357\u4eac\u548c\u4e0a\u6d77\uff0c\u591a\u5e74\u6765\u8fd9\u6bb5\u7ecf\u5386\u4e00\u76f4\u4e0eUFO\u6302\u94a9\u3002\u4f46\u5728\u5c42\u5c42\u63a8\u7406\u540e\uff0c\u8282\u76ee\u6700\u540e\u53d1\u73b0\uff0c\u4e00\u5207\u90fd\u662f\u5f53\u4e8b\u4eba\u7684\u5e7b\u89c9\uff0c\u4ece\u5bb6\u91cc\u5931\u8e2a\u5219\u662f\u56e0\u4e3a\u68a6\u6e38\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/2dfoTVrmGRra3dCeuYWsKowe6IFHbRutCbhRMCQRRGS4AQAAIgEAAEpQ.jpg\" style=\"height:290px; width:440px\" /></p>\r\n\r\n<p>\u53e6\u4e00\u671f\u8282\u76ee\u4e2d\uff0c\u4e00\u4f4d\u5410\u8840\u5947\u4eba\uff0c\u8c08\u7b11\u98ce\u751f\u95f4\u4fbf\u53ef\u4ee5\u53e3\u5410\u9c9c\u8840\uff0c\u8bf7\u6765\u533b\u751f\u5bf9\u5f53\u4e8b\u4eba\u8fdb\u884c\u4e86\u5185\u810f\u3001\u9f3b\u606f\u7b49\u5168\u65b9\u4f4d\u7684\u68c0\u67e5\uff0c\u90fd\u672a\u53d1\u73b0\u5f02\u72b6\u3002\u4f46\u6700\u540e\uff0c\u533b\u751f\u5076\u7136\u53d1\u73b0\u5f53\u4e8b\u4eba\u53ea\u662f\u7259\u9f88\u53d1\u708e\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/-ft7F23y0W8XDXrrw57anXofb-GMTYR3a8CUWOIfZuCQAQAALAEAAEpQ.jpg\" style=\"height:300px; width:400px\" /></p>\r\n\r\n<p>\u300a\u4f20\u5a92\u89c1\u95fb\u300b\u7684\u516c\u53f7\u6587\u7ae0\u603b\u4f53\u4e0a\u5bf9\u6b64\u6301\u80af\u5b9a\u6001\u5ea6\uff0c&ldquo;\u867d\u7136\u8282\u76ee\u7684\u5f15\u5b50\u591a\u51fa\u81ea\u7075\u5f02\u4e8b\u4ef6\uff0c\u4f46\u6700\u540e\u7684\u79d1\u5b66\u8be0\u91ca\uff0c\u6700\u7ec8\u8fd8\u662f\u8ba9\u4eba\u5bf9\u79bb\u5947\u53e4\u602a\u7684\u672a\u77e5\u7406\u89e3\u843d\u811a\u5728&lsquo;\u79d1\u5b66&rsquo;\u800c\u975e\u8ff7\u4fe1\u7684\u7406\u89e3\u4e0a\u3002\u4ece\u4e00\u6b21\u6b21\u60ac\u800c\u672a\u51b3\u5230\u771f\u76f8\u5927\u767d\uff0c\u53cd\u53cd\u590d\u590d\u7684\u5f15\u5bfc\u4e0b\uff0c\u5b9e\u9645\u4e0a\u4eba\u4eec\u7684\u601d\u7ef4\u5728\u4e0d\u7ecf\u610f\u95f4\u90fd\u70d9\u4e0a\u4e86\u76f8\u4fe1\u79d1\u5b66\u8be0\u91ca\u7684\u5f71\u5b50&rdquo;\u3002</p>\r\n\r\n<p>\u5bf9\u6b64\u6211\u8868\u793a\u8d5e\u540c\u3002\u5b9e\u9645\u4e0a\uff0c\u5373\u4fbf\u5728\u5f53\u5e74\u4e0d\u5c11\u5708\u91cc\u4eba\u58eb\u6279\u8bc4\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u65f6\uff0c\u6211\u4e5f\u5728\u4e3a\u5176\u8fa9\u62a4\u3002\u56e0\u4e3a\u76f8\u8f83\u51e0\u5341\u5e74\u6765\u79d1\u666e\u88ab\u7b49\u540c\u4e8e\u666e\u53ca\u56fd\u5bb6\u7684\u79d1\u6280\u6210\u5c31\u7684\u505a\u6cd5\uff0c\u4ee5\u53ca\u8282\u76ee\u4e2d\u79d1\u5b66\u5bb6\u603b\u662f\u6b63\u895f\u5371\u5750\u7684\u98ce\u683c\uff0c<strong>\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u51e0\u4e4e\u53ef\u4ee5\u7b97\u4f5c\u4e00\u573a\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u7684\u5174\u8da3\u9769\u547d</strong>\u3002\u591a\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u8ba4\u4e3a\uff0c\u4e3b\u8981\u76ee\u7684\u662f\u8bba\u8bc1\u56fd\u5bb6\u79d1\u6280\u6210\u5c31\u548c\u79d1\u6280\u653f\u7b56\u82f1\u660e\u7684\u79d1\u666e\uff0c\u5b9e\u9645\u4e0a\u4f1a\u8ba9\u53d7\u4f17\u8fdc\u79bb\u79d1\u5b66\u672c\u8eab\u3002</p>\r\n\r\n<p>\u9057\u61be\u7684\u662f\uff0c\u592e\u89c6\u7684\u9886\u5bfc\u80af\u5b9a\u4e0d\u8fd9\u4e48\u770b\u3002\u6240\u4ee5\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u540e\u6765\u518d\u6b21\u6539\u7248\u6210\u9ad8\u5927\u4e0a\u548c\u4e00\u672c\u6b63\u7ecf\u7684\u5ba3\u4f20\u79d1\u5b66\u7684\u5f62\u5f0f\uff0c\u6536\u89c6\u7387\u76f4\u7ebf\u4e0b\u964d\uff0c\u6700\u7ec8\u8d70\u5230\u4e86\u505c\u64ad\u8fd9\u4e00\u6b65\u3002\u5f53\u7136\uff0c\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u7684\u6536\u89c6\u7387\u4e0b\u964d\uff0c\u5e94\u8be5\u4e5f\u4e0e\u79fb\u52a8\u4e92\u8054\u7f51\u7279\u522b\u662f\u5728\u7ebf\u89c6\u9891\u680f\u76ee\u7684\u5174\u8d77\u6709\u5173\uff0c\u8fd9\u662f\u6211\u4eec\u4e0b\u9762\u8981\u63a2\u8ba8\u7684\u8bdd\u9898\u3002</p>\r\n\r\n<h2><strong>\u4f18\u79c0\u7535\u89c6\u8282\u76ee\u4e2d\u7684\u79d1\u5b66\u662f\u5565\u6837</strong></h2>\r\n\r\n<p>\u65e2\u7136\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u7684\u5c1d\u8bd5\u4e24\u5934\u4e0d\u8ba8\u597d\uff0c\u90a3\u4e48\u4ec0\u4e48\u6837\u7684\u7535\u89c6\u79d1\u5b66\u680f\u76ee\u624d\u80fd\u514b\u670d\u8fd9\u4e00\u70b9\u5462\uff1f\u5e94\u8be5\u8bf4\uff0c\u4ece1950\u5e74\u4ee3\u7535\u89c6\u5174\u8d77\uff0c\u897f\u65b9\u7684\u79d1\u5b66\u754c\u548c\u79d1\u666e\u754c\u5c31\u5bf9\u6b64\u5c55\u5f00\u63a2\u8ba8\u3002\u8fd9\u7bc7\u6587\u7ae0\u9650\u4e8e\u7bc7\u5e45\uff0c\u96be\u4ee5\u7cfb\u7edf\u68b3\u7406\u8fd9\u4e00\u8fc7\u7a0b\uff0c\u4e0d\u8fc7\u6211\u4eec\u4e0d\u59a8\u770b\u770b\u4f18\u79c0\u7684\u975e\u5a31\u4e50\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u90fd\u662f\u600e\u4e48\u73a9\u7684\u3002</p>\r\n\r\n<p>NOVA\u7684ScienceNOW \u7cfb\u5217\u8282\u76ee\uff0cDiscovery\u7684\u5404\u79cd\u79d1\u5b66\u63a2\u7d22\uff0c\u56fd\u5bb6\u5730\u7406\u7535\u89c6\u9891\u9053\u7684\u661f\u9645\u65c5\u884c\uff0cBBC\u7684Planet Earth\u548c\u5404\u79cd\u79d1\u5b66\u5b9e\u9a8c\u63a2\u79d8\uff0c\u5b83\u4eec\u90fd\u6709\u4ec0\u4e48\u5171\u540c\u7279\u70b9\u5462\uff1f</p>\r\n\r\n<p>\u65e0\u7591\uff0c\u5b83\u4eec\u90fd\u6709\u4f18\u7f8e\u6216\u8005\u5f3a\u70c8\u7684\u89c6\u89c9\u6548\u679c\uff0c\u8fd9\u4e5f\u662f\u7535\u89c6\u8d70\u5411\u5927\u4f17\u540e\u4e0d\u4e45\u5c31\u5438\u5f15\u4e86\u5f88\u591a\u79d1\u666e\u4eba\u58eb\u6295\u8eab\u5176\u4e2d\u7684\u539f\u56e0\u3002\u5b83\u4eec\u4e5f\u90fd<strong>\u6709\u6bd4\u8f83\u7cbe\u5f69\u66f2\u6298\u7684\u60c5\u8282\uff0c\u6709\u7740\u7535\u89c6\u955c\u5934\u5207\u6362\u6240\u8d4b\u4e88\u7684\u7d27\u51d1\u6d41\u7545\u7684\u53d9\u4e8b\u7ed3\u6784</strong>\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/FWXuz63WtPaDxOcm4P23d3GGhvubnEO72_j8DmY0Grg4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" />NOVA\u7684ScienceNOW\u7cfb\u5217\u8282\u76ee\u7684\u7247\u5934&nbsp;|&nbsp;NOVA</p>\r\n\r\n<p>\u5982\u679c\u4ec5\u4ec5\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u6211\u4eec\u6709\u5145\u5206\u7684\u7406\u7531\u4e3a\u7075\u5f02\u65f6\u4ee3\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u558a\u51a4\u3002\u4e0d\u8bba\u662f\u98de\u789f\u5931\u8e2a\u6848\u8fd8\u662f\u53e3\u5410\u9c9c\u8840\uff0c\u54ea\u4e00\u4e2a\u6ca1\u6709\u89c6\u89c9\u6548\u679c\uff1f\u54ea\u4e00\u4e2a\u6ca1\u6709\u4fa6\u63a2\u5c0f\u8bf4\u4e00\u822c\u7684\u66f2\u6298\u60c5\u8282\uff1f</p>\r\n\r\n<p>\u90a3\u540c\u6837\u5f3a\u8c03\u60c5\u8282\u66f2\u6298\u548c\u89c6\u89c9\u5438\u5f15\u529b\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\uff0c\u4e0e\u8fd9\u4e9b\u4f18\u79c0\u7684\u79d1\u5b66\u7535\u89c6\u8282\u76ee\u6bd4\uff0c\u7f3a\u4e86\u4ec0\u4e48\u5462\uff1f</p>\r\n\r\n<p>\u4e24\u4e2a\u5b57\uff0c&ldquo;<strong>\u79d1\u5b66</strong>&rdquo;\u3002</p>\r\n\r\n<p>\u4e0a\u8ff0\u77e5\u540d\u7684\u56fd\u9645\u7535\u89c6\u79d1\u5b66\u680f\u76ee\u4e2d\u7684\u5f88\u591a\u8282\u76ee\uff08\u5f53\u7136\u4e0d\u662f\u5168\u90e8\uff09\uff0c\u4e0d\u8bba\u662f\u7545\u60f3\u592a\u7a7a\u65c5\u884c\u8fd8\u662f\u63a2\u79d8\u739b\u96c5\u53e4\u57ce\uff0c\u6291\u6216\u662f\u8fa9\u8bba\u7ea2\u8089\u7684\u5065\u5eb7\u98ce\u9669\uff0c\u5176<strong>\u6700\u7ec8\u7684\u843d\u811a\u70b9\uff0c\u662f\u79d1\u5b66\u754c\u5728\u76f8\u5173\u8bae\u9898\u4e0a\u7684\u4e3b\u6d41\u7ed3\u8bba</strong>\uff0c\u4ee5\u53ca\u8fd9\u79cd\u7ed3\u8bba\u4e0e\u6211\u4eec\u7406\u89e3\u7684\u5dee\u5f02\u3001\u4e0e\u793e\u4f1a\u7684\u5f20\u529b\u3001\u6216\u8005\u8fd9\u4e9b\u4e3b\u6d41\u7ed3\u8bba\u81ea\u8eab\u7684\u4e0d\u786e\u5b9a\u6027\u3002\u79d1\u7814\u8fc7\u7a0b\u672c\u8eab\u6240\u5177\u6709\u7684\u66f2\u6298\u4e0e\u63a2\u7d22\u672a\u77e5\u7684\u60c5\u8da3\uff0c\u6784\u6210\u4e86\u4e0d\u5c11\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u4e2d\u7684\u4e3b\u8981\u60c5\u8282\u548c\u4e3b\u5e72\u7ebf\u7d22\u3002</p>\r\n\r\n<p>\u800c\u7075\u5f02\u65f6\u4ee3\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u867d\u7136\u4e5f\u8981\u501f\u52a9\u79d1\u5b66\uff08\u66f4\u51c6\u786e\u5730\u8bb2\u662f\u5305\u62ec\u666e\u901a\u533b\u5b66\u77e5\u8bc6\u7684\u79d1\u6280\uff09\u6765\u63ed\u79d8\uff0c\u4f46\u6b64\u5904\u7684&ldquo;\u79d1\u5b66&rdquo;\u5176\u5b9e\u4e0e\u79d1\u5b66\u5bb6\u4eec\u6240\u8fdb\u884c\u7684\u5bf9\u672a\u77e5\u4e16\u754c\u7684\u63a2\u7d22\u6ca1\u6709\u592a\u5927\u5173\u7cfb\u3002\u6211\u4eec\u53ef\u4ee5\u80af\u5b9a\u5b83\u6709\u5f15\u5bfc\u516c\u4f17\u5728\u9762\u5bf9\u5947\u95fb\u5f02\u4e8b\u65f6\u5e94\u8be5\u76f8\u4fe1\u79d1\u5b66\u8be0\u91ca\u8fd9\u79cd\u4f5c\u7528\uff0c\u4f46\u8fd9\u53ea\u662f\u5f88\u57fa\u672c\u7684\u79d1\u5b66\u4fe1\u5ff5\uff0c\u5dee\u4e0d\u591a\u7b49\u540c\u4e8e\u8ba9\u6211\u4eec\u76f8\u4fe1\u5df2\u7ecf\u88ab\u79d1\u5b66\u6240\u5305\u88c5\u8d77\u6765\u7684\u73b0\u4ee3\u793e\u4f1a\u7684\u5e38\u8bc6\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/4rpnyD5Jq7K51E_RIP9rHQ5N3klCn53Gi8M0Bo0ilVEkAgAAUAEAAEpQ.jpg\" style=\"height:336px; width:548px\" /></p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/LJoxEacXltNPuF-92pcqLtIqZcs03tHJ6FJz4MPjQv0oAgAAMQEAAEpQ.jpg\" style=\"height:305px; width:552px\" />\u300a\u8d70\u8fdb\u79d1\u5b66\u300b\u7684\u7075\u5f02\u9009\u9898</p>\r\n\r\n<p>\u8fd9\u5f53\u7136\u4e0d\u662f\u6ca1\u6709\u610f\u4e49\u3002\u5b9e\u9645\u4e0a\u76f8\u4fe1\u57fa\u4e8e\u79d1\u5b66\u7684\u5e38\u8bc6\uff0c\u5230\u4eca\u5929\u8fd8\u662f\u5e2e\u52a9\u6211\u4eec\u6218\u80dc\u5404\u79cd\u7f51\u7edc\u8c23\u8a00\uff0c\u5c24\u5176\u662f\u4e0e\u5065\u5eb7\u53ca\u98df\u54c1\u76f8\u5173\u8c23\u8a00\u7684\u4e3b\u8981\u5229\u5668\u4e4b\u4e00\uff08\u53e6\u4e00\u4e2a\u5229\u5668\u662f\u6211\u4eec\u5bf9\u7a0b\u5e8f\u548c\u903b\u8f91\u7684\u8ba4\u53ef\u4e0e\u9075\u5faa\uff09\u3002\u4f46\u51fa\u4e8e\u6536\u89c6\u7387\u7684\u9700\u6c42\uff0c<strong>\u7075\u5f02\u65f6\u4ee3\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u5927\u90e8\u5206\u65f6\u95f4\u90fd\u5728\u6829\u6829\u5982\u751f\u5730\u523b\u753b\u5404\u79cd\u53cd\u5e38\u8bc6\u7684\u8ba4\u8bc6</strong>\u3002\u5bf9\u4e8e\u89c2\u4f17\u800c\u8a00\uff0c\u662f\u5426\u6700\u540e\u90a3\u4e00\u5239\u90a3\u7684&ldquo;\u79d1\u5b66\u63ed\u79d8&rdquo;\u80fd\u8ba9\u4eba\u66f4\u76f8\u4fe1\u57fa\u4e8e\u79d1\u5b66\u7684\u5e38\u8bc6\uff0c\u8fd9\u5176\u5b9e\u5f88\u4e0d\u786e\u5b9a\u3002</p>\r\n\r\n<p>\u73b0\u4ee3\u5a92\u4ecb\u5fc3\u7406\u5b66\uff08Media psychology\uff09\u7684\u7814\u7a76\u8868\u660e\uff0c\u4eba\u4eec\u770b\u7535\u89c6\u4e0a\u7684\u79d1\u5b66\u8282\u76ee\uff0c\u5f88\u53ef\u80fd\u4e0d\u662f\u88ab\u5176\u4e2d\u6240\u5305\u542b\u7684\u4fe1\u606f\u6240\u5f71\u54cd\uff0c\u800c\u662f\u53d7\u5236\u4e8e\u4e00\u79cd\u79fb\u60c5\u4f5c\u7528\uff08transportation\uff09\uff0c\u5c06\u81ea\u5df1\u7b49\u540c\u4e8e\u8282\u76ee\u4e2d\u7684\u89d2\u8272\u3002\u5f53\u4eba\u4eec\u878d\u5165\u8fd9\u79cd\u89d2\u8272\u6216\u60c5\u8282\u4e2d\uff0c\u5f88\u591a\u65f6\u5019\u89c2\u770b\u652f\u6301\u79d1\u5b66\u7684\u7535\u5f71\u548c\u7535\u89c6\u5267\u540e\uff0c\u4ed6\u4eec\u7684\u6001\u5ea6\u53cd\u800c\u66f4\u63a5\u8fd1\u4e8e\u8282\u76ee\u4e2d\u90a3\u4e9b\u53cd\u79d1\u5b66\u7684\u4eba\u7269\u3002</p>\r\n\r\n<p>\u6b64\u5904\u63d0\u5230&ldquo;\u79fb\u60c5\u4f5c\u7528&rdquo;\u4ecd\u7136\u6709\u5f88\u591a\u4e0d\u786e\u5b9a\u6027\uff0c\u5e76\u4e0d\u662f\u4e3a\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u653e\u5f03\u7075\u5f02\u4e8b\u4ef6\u91cd\u5f52\u9ad8\u5927\u4e0a\u7684\u79d1\u5b66\u800c\u8fa9\u62a4\u3002\u5b9e\u9645\u4e0a\uff0c\u5ba3\u4f20\u56fd\u5bb6\u79d1\u6280\u6210\u5c31\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u4e00\u6837\u6ca1\u6709\u771f\u6b63\u8868\u73b0\u79d1\u5b66\u3002\u5728\u5927\u591a\u6570\u8fd9\u7c7b\u8282\u76ee\u4e2d\uff0c\u79d1\u5b66\u4e3b\u8981\u662f\u4e00\u4e2a\u4f53\u73b0\u56fd\u5bb6\u8fdb\u6b65\u7684\u7b26\u53f7\uff0c\u81f3\u4e8e\u8fd9\u4e2a\u7b26\u53f7\u4e3a\u6211\u4eec\u5e26\u6765\u4e86\u54ea\u4e9b\u5173\u4e8e\u672a\u77e5\u4e16\u754c\u7684\u77e5\u8bc6\uff0c\u6216\u8005\u6539\u53d8\u4e86\u54ea\u4e9b\u5e38\u89c4\u77e5\u8bc6\uff0c\u8fd9\u5e76\u4e0d\u662f\u7f16\u5bfc\u4eec\u5173\u5fc3\u7684\u5185\u5bb9\u3002\u81ea\u7136\uff0c\u79d1\u7814\u8fc7\u7a0b\u672c\u8eab\u6240\u5177\u6709\u7684\u66f2\u6298\u4e0e\u63a2\u7d22\u672a\u77e5\u7684\u60c5\u8da3\uff0c\u4e5f\u5c31\u5728\u6211\u4eec\u7684\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u4e2d\u88ab\u7701\u7565\u4e86\u3002</p>\r\n\r\n<h2><strong>\u600e\u6837\u8ba9\u79d1\u5b66\u4e0d\u8d70\u8fdc</strong></h2>\r\n\r\n<p>\u90a3\u4e48\uff0c\u600e\u6837\u8ba9\u79d1\u5b66\u4e0d\u8fdc\u79bb\u7535\u89c6\uff0c\u4ee5\u53ca\u66f4\u5e7f\u6cdb\u610f\u4e49\u4e0a\u7684\u5404\u79cd\u5a92\u4ecb\u5462\uff1f<strong>\u5229\u7528\u79d1\u7814\u8fc7\u7a0b\u672c\u8eab\u6240\u5177\u6709\u7684\u66f2\u6298\uff0c\u63a2\u7a76\u79d1\u5b66\u4e0e\u793e\u4f1a\u7684\u5f20\u529b\uff0c\u4ee5\u53ca\u5bfb\u6c42\u4e0d\u540c\u79d1\u5b66\u89e3\u91ca\u7684\u78b0\u649e</strong>\uff0c\u8fd9\u4e9b\u53ef\u4ee5\u4e3a\u76f8\u5f53\u591a\u7684\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u5e26\u6765\u751f\u673a\u3002</p>\r\n\r\n<p>\u5728\u524d\u8ff0\u7684ScienceNOW \u6216Space travel\u7cfb\u5217\u8282\u76ee\u4e2d\uff0c\u79d1\u7814\u6216\u63a2\u7d22\u8fc7\u7a0b\u7684\u5c42\u5c42\u9012\u8fdb\uff0c\u79d1\u5b66\u5bb6\u4eec\u53cd\u590d\u906d\u9047\u7684\u632b\u6298\uff0c\u90e8\u5206\u7814\u7a76\u906d\u5230\u6253\u538b\uff0c\u79d1\u7814\u6761\u4ef6\u8ba9\u82f1\u96c4\u65e0\u7528\u6b66\u4e4b\u5730\uff0c\u4ee5\u53ca\u5b9e\u9a8c\u8fc7\u7a0b\u4e2d\u7075\u5149\u4e00\u73b0\u7684\u706b\u82b1\uff0c\u90fd\u53ef\u4ee5\u6784\u6210\u66f2\u6298\u60c5\u8282\u3002\u5f53\u7136\uff0c\u8fd9\u91cc\u8bf4\u7684\u751f\u52a8\u66f2\u6298\u7ecf\u8fc7\u4e86\u827a\u672f\u5316\u5904\u7406\uff0c\u7565\u53bb\u4e86\u7edd\u5927\u591a\u6570\u5197\u957f\u6c89\u95f7\u7684\u5b9e\u9645\u79d1\u7814\u7ec6\u8282\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/P1n7tu-VZXoKkEih3_Hg0iThjC9ogf3N4_pPD0EKQ-_4AgAANwIAAEpQ.jpg\" style=\"height:477px; width:640px\" />\u5929\u7a7a\u547c\u5524\u6211\u4eec | \u300a\u56fd\u5bb6\u5730\u7406\u300b\u7684MARS\u7cfb\u5217\u8282\u76ee</p>\r\n\r\n<p>\u5b9e\u9645\u4e0a\uff0c\u628a\u79d1\u7814\u8fc7\u7a0b\u6240\u5177\u6709\u7684\u751f\u52a8\u66f2\u6298\u4f5c\u4e3a\u5356\u70b9\uff0c\u5df2\u7ecf\u662f\u5305\u62ec\u679c\u58f3\u7f51\u5728\u5185\u7684\u5f88\u591a\u516c\u4f17\u53f7\u6216\u7f51\u7edc\u65b0\u5a92\u4f53\u79d1\u666e\u6587\u7ae0\u7684\u5e38\u89c4\u5957\u8def\uff0c\u5c3d\u7ba1\u7565\u663e\u9057\u61be\u7684\u662f\uff0c\u8fd9\u4e9b\u6587\u7ae0\u4e2d\u7684\u7d20\u6750\uff0c\u5927\u90e8\u5206\u6765\u6e90\u4e8e\u56fd\u5916\u79d1\u666e\u6587\u7ae0\u6216\u79d1\u7814\u6210\u679c\u3002</p>\r\n\r\n<p>\u5728\u793e\u4ea4\u5a92\u4f53\u4e0a\uff0c\u540c\u6837\u4e5f\u6709\u4f18\u79c0\u7684\u79d1\u5b66\u89c6\u9891\u65e2\u53eb\u597d\u53c8\u5356\u5ea7\u3002\u6bd4\u5982\u5728\u79d1\u666e\u5708\u91cc\u88ab\u79f0\u4e3a&ldquo;\u6625\u54e5&rdquo;\u7684\u56fd\u5bb6\u5929\u6587\u53f0\u90d1\u6c38\u6625\u7814\u7a76\u5458\uff0c\u4e0e\u817e\u8baf\u89c6\u9891\u5408\u4f5c\u7684\u300a\u706b\u661f\u79fb\u6c11\u300b\uff0c\u5f88\u65e9\u5c31\u83b7\u5f973000\u4e07\u6b21\u8bbf\u95ee\u91cf\u3002\u636e\u8bf4\uff0c\u4e2d\u79d1\u9662\u52a8\u7269\u6240\u5468\u742a\u9662\u58eb\u7684\u4e00\u6bb5\u5b9e\u9a8c\u89c6\u9891\uff0c\u4e5f\u5728\u77ed\u77ed\u51e0\u5c0f\u65f6\u5438\u5f15\u4e863\u4e07\u6b21\u70b9\u51fb\u3002\u6a21\u4effTED\u7684\u79d1\u5b66\u5bb6\u77ed\u6f14\u8bb2\uff0c\u56fd\u5185\u4e5f\u6709\u4e86\u5305\u62ec\u4e2d\u56fd\u79d1\u534f\u4e3b\u529e\u3001\u679c\u58f3\u627f\u529e\u7684&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u6f14\u8bb2\uff0c\u4e2d\u79d1\u9662SELF\u8bb2\u575b\u7b49\u7b49\u5728\u5185\u7684\u591a\u4e2a\u7248\u672c\u3002\u8fd9\u4e9b\u89c6\u9891\u5171\u540c\u7684\u7279\u70b9\uff0c\u662f\u79d1\u5b66\u4e0d\u518d\u4ec5\u4ec5\u662f\u5ba3\u626c\u79d1\u6280\u6210\u5c31\u7684\u4e00\u4e2a\u7b26\u53f7\uff0c\u800c\u662f\u6709\u4e86\u72ec\u7acb\u7684\u751f\u547d\u3002\u4e00\u7ebf\u79d1\u5b66\u5bb6\u7684\u76f4\u63a5\u6295\u5165\uff0c\u4e5f\u8ba9\u79d1\u5b66\u672c\u8eab\u7684\u5947\u5999\uff0c\u6210\u4e3a\u79d1\u5b66\u89c6\u9891\u8282\u76ee\u7684\u8981\u7d20\u4e4b\u4e00\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/q0qu_LtRew9k0jVcaAUm8NR8YCY3i6aJkR8f50OpeWY4BAAAYQIAAEpQ.jpg\" style=\"height:361px; width:640px\" />\u56fd\u5bb6\u5929\u6587\u53f0\u90d1\u6c38\u6625\u300a\u706b\u661f\u79fb\u6c11\u300b\u79d1\u666e\u6f14\u8bb2 | \u817e\u8baf\u89c6\u9891</p>\r\n\r\n<p>\u4e3a\u4f55\u8fd9\u4e9b\u4e0d\u65ad\u6d8c\u73b0\u7684\u8d44\u6e90\u548c\u5e9e\u5927\u7684\u89c2\u4f17\u5374\u65e0\u6cd5&ldquo;\u633d\u6551&rdquo;\u592e\u89c6\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u5462\uff1f\u524d\u9762\u8bb2\u7684\u628a\u79d1\u666e\u7b49\u540c\u4e8e\u5ba3\u793a\u56fd\u5bb6\u79d1\u6280\u6210\u5c31\uff0c\u65e0\u7591\u662f\u539f\u56e0\u4e4b\u4e00\u3002\u89c2\u4f17\u4eec\u5f53\u7136\u6709\u56e0\u4e3a\u8d5e\u53f9\u7956\u56fd\u79d1\u6280\u5f3a\u76db\u800c\u89c2\u770b\u79d1\u5b66\u8282\u76ee\u7684\u52a8\u673a\uff0c\u4f46\u90a3\u4e3b\u8981\u5c40\u9650\u5728\u56fd\u5e86\u5468\u5e74\u3001\u5ae6\u5a25\u767b\u6708\u8fd9\u6837\u7279\u5b9a\u7684\u91cd\u5927\u4e8b\u4ef6\u53d1\u751f\u65f6\uff0c\u8fd9\u79cd\u52a8\u673a\u4e0d\u8db3\u4ee5\u6210\u4e3a\u6bcf\u5929\u652f\u6491\u6211\u4eec\u89c2\u770b\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u6216\u5176\u4ed6\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u7684\u7406\u7531\u3002</p>\r\n\r\n<p>\u4ec5\u4ec5\u63ed\u793a\u51fa\u8fd9\u4e00\u70b9\u8fd8\u4e0d\u591f\u3002\u6211\u4eec\u4e5f\u8981\u770b\u5230\uff0c\u57fa\u4e8e\u6d41\u91cf\u6280\u672f\u7684\u793e\u4ea4\u5a92\u4f53\uff0c\u5728\u52a8\u5458\u79d1\u5b66\u5bb6\u6295\u8eab\u79d1\u666e\u3001\u9501\u5b9a\u9002\u5b9c\u7684\u79d1\u5b66\u89c2\u4f17\u3001\u4e3a\u53d7\u4f17\u6309\u9700\u64ad\u653e\u4ee5\u53ca\u4e3a\u79d1\u5b66\u5bb6\u6216\u79d1\u666e\u4eba\u58eb\u7684\u52b3\u52a8\u4ea7\u751f\u56de\u62a5\u65b9\u9762\uff0c\u90fd\u6bd4\u4f20\u7edf\u7535\u89c6\u5a92\u4f53\u66f4\u5177\u4f18\u52bf\u3002</p>\r\n\r\n<p>\u4f46\u4e0e\u6b64\u540c\u65f6\uff0c\u5fc5\u987b\u627f\u8ba4\uff0c\u7f51\u7edc\u6d41\u5a92\u4f53\u6216\u516c\u4f17\u53f7\u662f\u65e0\u6cd5\u5b8c\u5168\u53d6\u4ee3\u4f20\u7edf\u7684\u4e3b\u6d41\u7535\u89c6\u5a92\u4f53\u7684\uff0c\u5c24\u5176\u662f\u592e\u89c6\u8fd9\u79cd\u6709\u91cd\u5927\u653f\u6cbb\u610f\u4e49\u7684\u6743\u5a01\u5a92\u4f53\u3002\u6240\u4ee5\uff0c\u5728\u611f\u53f9\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u7684\u505c\u64ad\u6807\u5fd7\u7740\u4e2d\u56fd\u4f20\u7edf\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u5f0f\u5fae\u7684\u540c\u65f6\uff0c\u6211\u4eec\u4e5f<strong>\u671f\u5f85\u7740\u4f20\u7edf\u7684\u6743\u5a01\u7535\u89c6\u5a92\u4f53\u80fd\u5229\u7528\u793e\u4ea4\u5a92\u4f53\u6240\u521b\u9020\u7684\u5404\u79cd\u673a\u9047\uff0c\u62cd\u6444\u51fa\u7b26\u5408\u79d1\u5b66\u4f20\u64ad\u81ea\u8eab\u7279\u70b9\u7684\u4f18\u79c0\u7535\u89c6\u8282\u76ee</strong>\u3002</p>\r\n\r\n<p>\u79d1\u5b66\u6ca1\u6709\u8d70\u8fdc\uff0c\u56e0\u4e3a\u5b83\u672c\u6765\u5c31\u5728\u6211\u4eec\u4e4b\u4e2d\u3002\u73b0\u5728\u662f\u65f6\u5019\u8ba9\u6211\u4eec\u4e0e\u4f20\u7edf\u7684\u7535\u89c6\u4eba\u4e00\u8d77\u52aa\u529b\uff0c\u8ba9\u79d1\u5b66\u8d70\u8fd1\u7535\u89c6\u4e86\u3002\uff08\u7f16\u8f91\uff1aYuki\uff09</p>", "date_created": "2019-10-11T13:01:10.557591+08:00", "date_modified": "2019-10-11T13:11:25.360845+08:00", "date_published": "2019-10-11T13:10:26+08:00", "id": 454848, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": null, "replies_count": 14, "small_image": "https://1-im.guokr.com/3ztjE6tYoDtnbunV-C1CdQhccgI8SZ4ASO-6KZ3z3p04BAAAowIAAEpQ.jpg", "subject": {"articles_count": 423, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "society", "minisite_key": "None", "name": "\u793e\u4f1a", "sort_score": 21, "subject_type": "normal"}, "subject_key": "society", "summary": "\u8d70\u8fd1\u79d1\u5b66\u8d70\u4e86\uff0c\u4f46\u79d1\u5b66\u4ece\u672a\u8d70\u8fdc\uff01", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u5f53\u56fd\u4eba\u8fd8\u6c89\u6d78\u5728\u56fd\u5e86\u5927\u9605\u5175\u5c55\u793a\u7684\u5c16\u7aef\u519b\u4e8b\u79d1\u6280\u65f6\uff0c\u592e\u89c6\u8001\u724c\u79d1\u5b66\u7535\u89c6\u8282\u76ee\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u4ece10\u6708\u8d77\u5c06\u505c\u64ad\u7684\u6d88\u606f\uff0c\u5373\u4fbf\u5728\u79d1\u5b66\u4f20\u64ad\u5708\u5b50\u91cc\uff0c\u4e5f\u663e\u5f97\u6ce2\u6f9c\u4e0d\u60ca\u3002\u8bf4\u5b9e\u8bdd\uff0c\u51fa\u56fd\u540e\u6211\u5df2\u591a\u5e74\u6ca1\u770b\u8fd9\u6863\u8282\u76ee\u4e86\uff0c\u4e0d\u8fc7\u4f5c\u4e3a\u5f53\u5e74\u592e\u89c6\u79d1\u5b66\u7c7b\u8282\u76ee\u6392\u884c\u699c\u540d\u5217\u524d\u8305\u7684\u8282\u76ee\uff0c\u5176\u505c\u64ad\u8fd8\u662f\u5177\u6709\u67d0\u79cd\u7b26\u53f7\u610f\u4e49\u3002\u4e8e\u662f\uff0c\u5728\u770b\u4e86\u300a\u4f20\u5a92\u89c1\u95fb\u300b\u516c\u4f17\u53f7\u520a\u767b\u7684\u76f8\u5173\u6587\u7ae0\u540e\uff0c\u6211\u4e0eiScientist\u7684Yuki\u7f16\u8f91\u804a\u8d77\u6b64\u4e8b\uff0cYuki\u8bf4\u4e86\u4e00\u53e5\u975e\u5e38\u7ecf\u5178\u7684\u8bdd\uff1a\u201c\u8d70\u8fd1\u79d1\u5b66\u8d70\u4e86\uff0c\u4f46\u79d1\u5b66\u4ece\u672a\u8d70\u8fdc!\u201d\u8fd9\u53e5\u7ecf\u5178\u8bc4\u4ef7\u63d0\u9192\u6211\uff0c\u5e94\u8be5\u5728\u6211\u7684\u201c\u79d1\u5b66\u4f20\u64ad\u7684\u79d1\u666e\u201d\u4e13\u680f\u4e2d\u63a2\u8ba8\u4e0b\u7535\u89c6\u4e2d\u7684\u79d1\u5b66\u8fd9\u4e00\u79d1\u666e\u7684\u91cd\u8981\u5f62\u5f0f\u3002\u201c\u8d70\u8fd1\u79d1\u5b66\u201d\u7684\u7ea0\u7ed3\u5728\u63a2\u8ba8\u7535\u89c6\u4e2d\u7684\u79d1\u5b66\u8fd9\u4e2a\u8bdd\u9898\u524d\uff0c\u8fd8\u662f\u6709\u5fc5\u8981\u5148\u770b\u770b\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u7684\u5386\u7a0b\u3002\u8fd9\u91cc\u5e76\u4e0d\u6253\u7b97\u8be6\u7ec6\u63cf\u8ff0\u8fd9\u6863\u8282\u76ee\u7684\u5386\u53f2\uff0c\u6709\u5174\u8da3\u7684\u8bfb\u8005\u53ef\u4ee5\u4ece\u5404\u79cd\u5728\u7ebf\u767e\u79d1\u4e2d\u4e86\u89e3\u3002\u6211\u53ea\u8bb0\u5f97\uff0c\u8fd9\u6863\u8282\u76ee\u6700\u521d\u662f\u5728\u592e\u89c6\u201c\u65b0\u95fb\u8054\u64ad\u201d\u6216\u201c\u7126\u70b9\u8bbf\u8c08\u201d\u540e\u76f8\u5f53\u597d\u7684\u65f6\u6bb5\u64ad\u51fa\u3002\u6700\u65e9\u7684\u51e0\u5e74\uff0c\u8282\u76ee\u62a5\u9053\u7684\uff0c\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\u8bb4\u6b4c\u7684\uff0c\u90fd\u662f\u56fd\u5185\u6709\u91cd\u5927\u610f\u4e49\u7684\u79d1\u6280\u8fdb\u6b65\uff0c\u800c\u5224\u65ad\u91cd\u8981\u610f\u4e49\u7684\u6807\u51c6\u4e3b\u8981\u662f\u6240\u62a5\u9053\u7684\u79d1\u6280\u5982\u4f55\u4fc3\u8fdb\u56fd\u5bb6\u7684\u79d1\u6280\u8fdb\u6b65\u30021998\u5e74\u300a\u8d70\u8fdb\u79d1\u5b66\u300b\u7684\u7247\u5934 | \u4f18\u9177\u7531\u4e8e\u65b0\u95fb\u8054\u64ad\u4e2d\u5f88\u5c11\u62a5\u9053\u79d1\u5b66\uff0c\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u5f53\u65f6\u5728\u8282\u76ee\u8bbe\u7f6e\u4e0a\u66f4\u50cf\u662f\u957f\u65b0\u95fb\uff0c\u4ece\u79d1\u666e\u4f9b\u7ed9\u7684\u89d2\u5ea6\u6765\u770b\uff0c\u8fd9\u65e0\u7591\u662f\u4e00\u5927\u8fdb\u6b65\u3002\u8fd8\u8bb0\u5f97\u5f53\u65f6\u6807\u51c6\u7684\u53d9\u4e8b\u65b9\u5f0f\u662f\u56fd\u6c11\u7ecf\u6d4e\u4e2d\u9047\u5230\u4e86\u4ec0\u4e48\u95ee\u9898\uff0c\u7136\u540e\u79d1\u5b66\u5bb6\u6216\u5de5\u7a0b\u5e08\u4eec\u51fa\u73b0\u4e86\uff0c\u7528\u4ed6\u4eec\u7684\u667a\u6167\u6218\u80dc\u4e86\u6311\u6218\u3002\u9057\u61be\u7684\u662f\uff0c\u5b83\u7684\u6536\u89c6\u7387\u4f3c\u4e4e\u4e00\u76f4\u4e0d\u9ad8\u3002\u6211\u6000\u7591\u666e\u901a\u89c2\u4f17\u53ef\u80fd\u4e3b\u8981\u5229\u7528\u8fd9\u4e2a\u4ecb\u4e8e\u65b0\u95fb\u8054\u64ad\u4e0e\u9ec4\u91d1\u6863\u7535\u89c6\u5267\u4e4b\u95f4\u7684\u65f6\u6bb5\u4e0a\u5395\u6240\u6216\u8005\u5403\u665a\u996d\u3002\u4e8e\u662f\uff0c\u8282\u76ee\u540e\u6765\u88ab\u79fb\u51fa\u4e86\u9ec4\u91d1\u65f6\u6863\uff0c\u53c8\u4ece\u592e\u89c6\u4e00\u5957\u8fc1\u5230\u79d1\u6559\u9891\u9053\u64ad\u51fa\u3002\u6b63\u662f\u8fd9\u6b21\u642c\u5bb6\uff0c\u8ba9\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u6447\u8eab\u4e00\u53d8\uff0c\u6536\u89c6\u7387\u8fc5\u901f\u6500\u5347\u3002\u539f\u56e0\u4e48\uff1f\u7528\u6279\u8bc4\u8005\u7684\u8bdd\u8bb2\uff0c\u4f3c\u4e4e\u662f\u201c\u8d70\u8fd1\u79d1\u5b66\u201d\u53d8\u6210\u4e86\u201c\u8d70\u8fd1\u4e0d\u79d1\u5b66\u201d\u3002\u8fd9\u4e2a\u9636\u6bb5\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\uff0c\u5927\u591a\u6570\u62a5\u9053\u7684\u662f\u5404\u79cd\u7075\u5f02\u4e8b\u4ef6\uff0c\u7ecf\u8fc7\u66f2\u6298\u8ff7\u79bb\u751a\u81f3\u662f\u60ca\u5fc3\u52a8\u9b44\u7684\u53d1\u5c55\uff0c\u6700\u7ec8\u7684\u201c\u79d1\u5b66\u201d\u7ed3\u8bba\u662f\uff0c\u539f\u6765\u5f53\u4e8b\u4eba\u641e\u9519\u4e86\u3002\u501f\u7528\u300a\u4f20\u5a92\u89c1\u95fb\u300b\u516c\u53f7\u6587\u7ae0\u4e3e\u7684\u51e0\u4e2a\u5e7f\u6cdb\u6d41\u4f20\u7684\u4f8b\u5b50\u3002\u4e00\u4f4d\u6cb3\u5317\u519c\u6c11\u8fde\u7eed\u4e09\u6b21\u795e\u79d8\u5931\u8e2a\uff0c\u4e00\u591c\u4e4b\u95f4\u817e\u98de\u81f3\u4e00\u5343\u591a\u516c\u91cc\u5916\u7684\u5357\u4eac\u548c\u4e0a\u6d77\uff0c\u591a\u5e74\u6765\u8fd9\u6bb5\u7ecf\u5386\u4e00\u76f4\u4e0eUFO\u6302\u94a9\u3002\u4f46\u5728\u5c42\u5c42\u63a8\u7406\u540e\uff0c\u8282\u76ee\u6700\u540e\u53d1\u73b0\uff0c\u4e00\u5207\u90fd\u662f\u5f53\u4e8b\u4eba\u7684\u5e7b\u89c9\uff0c\u4ece\u5bb6\u91cc\u5931\u8e2a\u5219\u662f\u56e0\u4e3a\u68a6\u6e38\u3002\u53e6\u4e00\u671f\u8282\u76ee\u4e2d\uff0c\u4e00\u4f4d\u5410\u8840\u5947\u4eba\uff0c\u8c08\u7b11\u98ce\u751f\u95f4\u4fbf\u53ef\u4ee5\u53e3\u5410\u9c9c\u8840\uff0c\u8bf7\u6765\u533b\u751f\u5bf9\u5f53\u4e8b\u4eba\u8fdb\u884c\u4e86\u5185\u810f\u3001\u9f3b\u606f\u7b49\u5168\u65b9\u4f4d\u7684\u68c0\u67e5\uff0c\u90fd\u672a\u53d1\u73b0\u5f02\u72b6\u3002\u4f46\u6700\u540e\uff0c\u533b\u751f\u5076\u7136\u53d1\u73b0\u5f53\u4e8b\u4eba\u53ea\u662f\u7259\u9f88\u53d1\u708e\u3002\u300a\u4f20\u5a92\u89c1\u95fb\u300b\u7684\u516c\u53f7\u6587\u7ae0\u603b\u4f53\u4e0a\u5bf9\u6b64\u6301\u80af\u5b9a\u6001\u5ea6\uff0c\u201c\u867d\u7136\u8282\u76ee\u7684\u5f15\u5b50\u591a\u51fa\u81ea\u7075\u5f02\u4e8b\u4ef6\uff0c\u4f46\u6700\u540e\u7684\u79d1\u5b66\u8be0\u91ca\uff0c\u6700\u7ec8\u8fd8\u662f\u8ba9\u4eba\u5bf9\u79bb\u5947\u53e4\u602a\u7684\u672a\u77e5\u7406\u89e3\u843d\u811a\u5728\u2018\u79d1\u5b66\u2019\u800c\u975e\u8ff7\u4fe1\u7684\u7406\u89e3\u4e0a\u3002\u4ece\u4e00\u6b21\u6b21\u60ac\u800c\u672a\u51b3\u5230\u771f\u76f8\u5927\u767d\uff0c\u53cd\u53cd\u590d\u590d\u7684\u5f15\u5bfc\u4e0b\uff0c\u5b9e\u9645\u4e0a\u4eba\u4eec\u7684\u601d\u7ef4\u5728\u4e0d\u7ecf\u610f\u95f4\u90fd\u70d9\u4e0a\u4e86\u76f8\u4fe1\u79d1\u5b66\u8be0\u91ca\u7684\u5f71\u5b50\u201d\u3002\u5bf9\u6b64\u6211\u8868\u793a\u8d5e\u540c\u3002\u5b9e\u9645\u4e0a\uff0c\u5373\u4fbf\u5728\u5f53\u5e74\u4e0d\u5c11\u5708\u91cc\u4eba\u58eb\u6279\u8bc4\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u65f6\uff0c\u6211\u4e5f\u5728\u4e3a\u5176\u8fa9\u62a4\u3002\u56e0\u4e3a\u76f8\u8f83\u51e0\u5341\u5e74\u6765\u79d1\u666e\u88ab\u7b49\u540c\u4e8e\u666e\u53ca\u56fd\u5bb6\u7684\u79d1\u6280\u6210\u5c31\u7684\u505a\u6cd5\uff0c\u4ee5\u53ca\u8282\u76ee\u4e2d\u79d1\u5b66\u5bb6\u603b\u662f\u6b63\u895f\u5371\u5750\u7684\u98ce\u683c\uff0c\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u51e0\u4e4e\u53ef\u4ee5\u7b97\u4f5c\u4e00\u573a\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u7684\u5174\u8da3\u9769\u547d\u3002\u591a\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u8ba4\u4e3a\uff0c\u4e3b\u8981\u76ee\u7684\u662f\u8bba\u8bc1\u56fd\u5bb6\u79d1\u6280\u6210\u5c31\u548c\u79d1\u6280\u653f\u7b56\u82f1\u660e\u7684\u79d1\u666e\uff0c\u5b9e\u9645\u4e0a\u4f1a\u8ba9\u53d7\u4f17\u8fdc\u79bb\u79d1\u5b66\u672c\u8eab\u3002\u9057\u61be\u7684\u662f\uff0c\u592e\u89c6\u7684\u9886\u5bfc\u80af\u5b9a\u4e0d\u8fd9\u4e48\u770b\u3002\u6240\u4ee5\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u540e\u6765\u518d\u6b21\u6539\u7248\u6210\u9ad8\u5927\u4e0a\u548c\u4e00\u672c\u6b63\u7ecf\u7684\u5ba3\u4f20\u79d1\u5b66\u7684\u5f62\u5f0f\uff0c\u6536\u89c6\u7387\u76f4\u7ebf\u4e0b\u964d\uff0c\u6700\u7ec8\u8d70\u5230\u4e86\u505c\u64ad\u8fd9\u4e00\u6b65\u3002\u5f53\u7136\uff0c\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u7684\u6536\u89c6\u7387\u4e0b\u964d\uff0c\u5e94\u8be5\u4e5f\u4e0e\u79fb\u52a8\u4e92\u8054\u7f51\u7279\u522b\u662f\u5728\u7ebf\u89c6\u9891\u680f\u76ee\u7684\u5174\u8d77\u6709\u5173\uff0c\u8fd9\u662f\u6211\u4eec\u4e0b\u9762\u8981\u63a2\u8ba8\u7684\u8bdd\u9898\u3002\u4f18\u79c0\u7535\u89c6\u8282\u76ee\u4e2d\u7684\u79d1\u5b66\u662f\u5565\u6837\u65e2\u7136\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u7684\u5c1d\u8bd5\u4e24\u5934\u4e0d\u8ba8\u597d\uff0c\u90a3\u4e48\u4ec0\u4e48\u6837\u7684\u7535\u89c6\u79d1\u5b66\u680f\u76ee\u624d\u80fd\u514b\u670d\u8fd9\u4e00\u70b9\u5462\uff1f\u5e94\u8be5\u8bf4\uff0c\u4ece1950\u5e74\u4ee3\u7535\u89c6\u5174\u8d77\uff0c\u897f\u65b9\u7684\u79d1\u5b66\u754c\u548c\u79d1\u666e\u754c\u5c31\u5bf9\u6b64\u5c55\u5f00\u63a2\u8ba8\u3002\u8fd9\u7bc7\u6587\u7ae0\u9650\u4e8e\u7bc7\u5e45\uff0c\u96be\u4ee5\u7cfb\u7edf\u68b3\u7406\u8fd9\u4e00\u8fc7\u7a0b\uff0c\u4e0d\u8fc7\u6211\u4eec\u4e0d\u59a8\u770b\u770b\u4f18\u79c0\u7684\u975e\u5a31\u4e50\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u90fd\u662f\u600e\u4e48\u73a9\u7684\u3002NOVA\u7684ScienceNOW \u7cfb\u5217\u8282\u76ee\uff0cDiscovery\u7684\u5404\u79cd\u79d1\u5b66\u63a2\u7d22\uff0c\u56fd\u5bb6\u5730\u7406\u7535\u89c6\u9891\u9053\u7684\u661f\u9645\u65c5\u884c\uff0cBBC\u7684Planet Earth\u548c\u5404\u79cd\u79d1\u5b66\u5b9e\u9a8c\u63a2\u79d8\uff0c\u5b83\u4eec\u90fd\u6709\u4ec0\u4e48\u5171\u540c\u7279\u70b9\u5462\uff1f\u65e0\u7591\uff0c\u5b83\u4eec\u90fd\u6709\u4f18\u7f8e\u6216\u8005\u5f3a\u70c8\u7684\u89c6\u89c9\u6548\u679c\uff0c\u8fd9\u4e5f\u662f\u7535\u89c6\u8d70\u5411\u5927\u4f17\u540e\u4e0d\u4e45\u5c31\u5438\u5f15\u4e86\u5f88\u591a\u79d1\u666e\u4eba\u58eb\u6295\u8eab\u5176\u4e2d\u7684\u539f\u56e0\u3002\u5b83\u4eec\u4e5f\u90fd\u6709\u6bd4\u8f83\u7cbe\u5f69\u66f2\u6298\u7684\u60c5\u8282\uff0c\u6709\u7740\u7535\u89c6\u955c\u5934\u5207\u6362\u6240\u8d4b\u4e88\u7684\u7d27\u51d1\u6d41\u7545\u7684\u53d9\u4e8b\u7ed3\u6784\u3002NOVA\u7684ScienceNOW\u7cfb\u5217\u8282\u76ee\u7684\u7247\u5934\u00a0|\u00a0NOVA\u5982\u679c\u4ec5\u4ec5\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u6211\u4eec\u6709\u5145\u5206\u7684\u7406\u7531\u4e3a\u7075\u5f02\u65f6\u4ee3\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u558a\u51a4\u3002\u4e0d\u8bba\u662f\u98de\u789f\u5931\u8e2a\u6848\u8fd8\u662f\u53e3\u5410\u9c9c\u8840\uff0c\u54ea\u4e00\u4e2a\u6ca1\u6709\u89c6\u89c9\u6548\u679c\uff1f\u54ea\u4e00\u4e2a\u6ca1\u6709\u4fa6\u63a2\u5c0f\u8bf4\u4e00\u822c\u7684\u66f2\u6298\u60c5\u8282\uff1f\u90a3\u540c\u6837\u5f3a\u8c03\u60c5\u8282\u66f2\u6298\u548c\u89c6\u89c9\u5438\u5f15\u529b\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\uff0c\u4e0e\u8fd9\u4e9b\u4f18\u79c0\u7684\u79d1\u5b66\u7535\u89c6\u8282\u76ee\u6bd4\uff0c\u7f3a\u4e86\u4ec0\u4e48\u5462\uff1f\u4e24\u4e2a\u5b57\uff0c\u201c\u79d1\u5b66\u201d\u3002\u4e0a\u8ff0\u77e5\u540d\u7684\u56fd\u9645\u7535\u89c6\u79d1\u5b66\u680f\u76ee\u4e2d\u7684\u5f88\u591a\u8282\u76ee\uff08\u5f53\u7136\u4e0d\u662f\u5168\u90e8\uff09\uff0c\u4e0d\u8bba\u662f\u7545\u60f3\u592a\u7a7a\u65c5\u884c\u8fd8\u662f\u63a2\u79d8\u739b\u96c5\u53e4\u57ce\uff0c\u6291\u6216\u662f\u8fa9\u8bba\u7ea2\u8089\u7684\u5065\u5eb7\u98ce\u9669\uff0c\u5176\u6700\u7ec8\u7684\u843d\u811a\u70b9\uff0c\u662f\u79d1\u5b66\u754c\u5728\u76f8\u5173\u8bae\u9898\u4e0a\u7684\u4e3b\u6d41\u7ed3\u8bba\uff0c\u4ee5\u53ca\u8fd9\u79cd\u7ed3\u8bba\u4e0e\u6211\u4eec\u7406\u89e3\u7684\u5dee\u5f02\u3001\u4e0e\u793e\u4f1a\u7684\u5f20\u529b\u3001\u6216\u8005\u8fd9\u4e9b\u4e3b\u6d41\u7ed3\u8bba\u81ea\u8eab\u7684\u4e0d\u786e\u5b9a\u6027\u3002\u79d1\u7814\u8fc7\u7a0b\u672c\u8eab\u6240\u5177\u6709\u7684\u66f2\u6298\u4e0e\u63a2\u7d22\u672a\u77e5\u7684\u60c5\u8da3\uff0c\u6784\u6210\u4e86\u4e0d\u5c11\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u4e2d\u7684\u4e3b\u8981\u60c5\u8282\u548c\u4e3b\u5e72\u7ebf\u7d22\u3002\u800c\u7075\u5f02\u65f6\u4ee3\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u867d\u7136\u4e5f\u8981\u501f\u52a9\u79d1\u5b66\uff08\u66f4\u51c6\u786e\u5730\u8bb2\u662f\u5305\u62ec\u666e\u901a\u533b\u5b66\u77e5\u8bc6\u7684\u79d1\u6280\uff09\u6765\u63ed\u79d8\uff0c\u4f46\u6b64\u5904\u7684\u201c\u79d1\u5b66\u201d\u5176\u5b9e\u4e0e\u79d1\u5b66\u5bb6\u4eec\u6240\u8fdb\u884c\u7684\u5bf9\u672a\u77e5\u4e16\u754c\u7684\u63a2\u7d22\u6ca1\u6709\u592a\u5927\u5173\u7cfb\u3002\u6211\u4eec\u53ef\u4ee5\u80af\u5b9a\u5b83\u6709\u5f15\u5bfc\u516c\u4f17\u5728\u9762\u5bf9\u5947\u95fb\u5f02\u4e8b\u65f6\u5e94\u8be5\u76f8\u4fe1\u79d1\u5b66\u8be0\u91ca\u8fd9\u79cd\u4f5c\u7528\uff0c\u4f46\u8fd9\u53ea\u662f\u5f88\u57fa\u672c\u7684\u79d1\u5b66\u4fe1\u5ff5\uff0c\u5dee\u4e0d\u591a\u7b49\u540c\u4e8e\u8ba9\u6211\u4eec\u76f8\u4fe1\u5df2\u7ecf\u88ab\u79d1\u5b66\u6240\u5305\u88c5\u8d77\u6765\u7684\u73b0\u4ee3\u793e\u4f1a\u7684\u5e38\u8bc6\u3002\u300a\u8d70\u8fdb\u79d1\u5b66\u300b\u7684\u7075\u5f02\u9009\u9898\u8fd9\u5f53\u7136\u4e0d\u662f\u6ca1\u6709\u610f\u4e49\u3002\u5b9e\u9645\u4e0a\u76f8\u4fe1\u57fa\u4e8e\u79d1\u5b66\u7684\u5e38\u8bc6\uff0c\u5230\u4eca\u5929\u8fd8\u662f\u5e2e\u52a9\u6211\u4eec\u6218\u80dc\u5404\u79cd\u7f51\u7edc\u8c23\u8a00\uff0c\u5c24\u5176\u662f\u4e0e\u5065\u5eb7\u53ca\u98df\u54c1\u76f8\u5173\u8c23\u8a00\u7684\u4e3b\u8981\u5229\u5668\u4e4b\u4e00\uff08\u53e6\u4e00\u4e2a\u5229\u5668\u662f\u6211\u4eec\u5bf9\u7a0b\u5e8f\u548c\u903b\u8f91\u7684\u8ba4\u53ef\u4e0e\u9075\u5faa\uff09\u3002\u4f46\u51fa\u4e8e\u6536\u89c6\u7387\u7684\u9700\u6c42\uff0c\u7075\u5f02\u65f6\u4ee3\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u5927\u90e8\u5206\u65f6\u95f4\u90fd\u5728\u6829\u6829\u5982\u751f\u5730\u523b\u753b\u5404\u79cd\u53cd\u5e38\u8bc6\u7684\u8ba4\u8bc6\u3002\u5bf9\u4e8e\u89c2\u4f17\u800c\u8a00\uff0c\u662f\u5426\u6700\u540e\u90a3\u4e00\u5239\u90a3\u7684\u201c\u79d1\u5b66\u63ed\u79d8\u201d\u80fd\u8ba9\u4eba\u66f4\u76f8\u4fe1\u57fa\u4e8e\u79d1\u5b66\u7684\u5e38\u8bc6\uff0c\u8fd9\u5176\u5b9e\u5f88\u4e0d\u786e\u5b9a\u3002\u73b0\u4ee3\u5a92\u4ecb\u5fc3\u7406\u5b66\uff08Media psychology\uff09\u7684\u7814\u7a76\u8868\u660e\uff0c\u4eba\u4eec\u770b\u7535\u89c6\u4e0a\u7684\u79d1\u5b66\u8282\u76ee\uff0c\u5f88\u53ef\u80fd\u4e0d\u662f\u88ab\u5176\u4e2d\u6240\u5305\u542b\u7684\u4fe1\u606f\u6240\u5f71\u54cd\uff0c\u800c\u662f\u53d7\u5236\u4e8e\u4e00\u79cd\u79fb\u60c5\u4f5c\u7528\uff08transportation\uff09\uff0c\u5c06\u81ea\u5df1\u7b49\u540c\u4e8e\u8282\u76ee\u4e2d\u7684\u89d2\u8272\u3002\u5f53\u4eba\u4eec\u878d\u5165\u8fd9\u79cd\u89d2\u8272\u6216\u60c5\u8282\u4e2d\uff0c\u5f88\u591a\u65f6\u5019\u89c2\u770b\u652f\u6301\u79d1\u5b66\u7684\u7535\u5f71\u548c\u7535\u89c6\u5267\u540e\uff0c\u4ed6\u4eec\u7684\u6001\u5ea6\u53cd\u800c\u66f4\u63a5\u8fd1\u4e8e\u8282\u76ee\u4e2d\u90a3\u4e9b\u53cd\u79d1\u5b66\u7684\u4eba\u7269\u3002\u6b64\u5904\u63d0\u5230\u201c\u79fb\u60c5\u4f5c\u7528\u201d\u4ecd\u7136\u6709\u5f88\u591a\u4e0d\u786e\u5b9a\u6027\uff0c\u5e76\u4e0d\u662f\u4e3a\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u653e\u5f03\u7075\u5f02\u4e8b\u4ef6\u91cd\u5f52\u9ad8\u5927\u4e0a\u7684\u79d1\u5b66\u800c\u8fa9\u62a4\u3002\u5b9e\u9645\u4e0a\uff0c\u5ba3\u4f20\u56fd\u5bb6\u79d1\u6280\u6210\u5c31\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u4e00\u6837\u6ca1\u6709\u771f\u6b63\u8868\u73b0\u79d1\u5b66\u3002\u5728\u5927\u591a\u6570\u8fd9\u7c7b\u8282\u76ee\u4e2d\uff0c\u79d1\u5b66\u4e3b\u8981\u662f\u4e00\u4e2a\u4f53\u73b0\u56fd\u5bb6\u8fdb\u6b65\u7684\u7b26\u53f7\uff0c\u81f3\u4e8e\u8fd9\u4e2a\u7b26\u53f7\u4e3a\u6211\u4eec\u5e26\u6765\u4e86\u54ea\u4e9b\u5173\u4e8e\u672a\u77e5\u4e16\u754c\u7684\u77e5\u8bc6\uff0c\u6216\u8005\u6539\u53d8\u4e86\u54ea\u4e9b\u5e38\u89c4\u77e5\u8bc6\uff0c\u8fd9\u5e76\u4e0d\u662f\u7f16\u5bfc\u4eec\u5173\u5fc3\u7684\u5185\u5bb9\u3002\u81ea\u7136\uff0c\u79d1\u7814\u8fc7\u7a0b\u672c\u8eab\u6240\u5177\u6709\u7684\u66f2\u6298\u4e0e\u63a2\u7d22\u672a\u77e5\u7684\u60c5\u8da3\uff0c\u4e5f\u5c31\u5728\u6211\u4eec\u7684\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u4e2d\u88ab\u7701\u7565\u4e86\u3002\u600e\u6837\u8ba9\u79d1\u5b66\u4e0d\u8d70\u8fdc\u90a3\u4e48\uff0c\u600e\u6837\u8ba9\u79d1\u5b66\u4e0d\u8fdc\u79bb\u7535\u89c6\uff0c\u4ee5\u53ca\u66f4\u5e7f\u6cdb\u610f\u4e49\u4e0a\u7684\u5404\u79cd\u5a92\u4ecb\u5462\uff1f\u5229\u7528\u79d1\u7814\u8fc7\u7a0b\u672c\u8eab\u6240\u5177\u6709\u7684\u66f2\u6298\uff0c\u63a2\u7a76\u79d1\u5b66\u4e0e\u793e\u4f1a\u7684\u5f20\u529b\uff0c\u4ee5\u53ca\u5bfb\u6c42\u4e0d\u540c\u79d1\u5b66\u89e3\u91ca\u7684\u78b0\u649e\uff0c\u8fd9\u4e9b\u53ef\u4ee5\u4e3a\u76f8\u5f53\u591a\u7684\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u5e26\u6765\u751f\u673a\u3002\u5728\u524d\u8ff0\u7684ScienceNOW \u6216Space travel\u7cfb\u5217\u8282\u76ee\u4e2d\uff0c\u79d1\u7814\u6216\u63a2\u7d22\u8fc7\u7a0b\u7684\u5c42\u5c42\u9012\u8fdb\uff0c\u79d1\u5b66\u5bb6\u4eec\u53cd\u590d\u906d\u9047\u7684\u632b\u6298\uff0c\u90e8\u5206\u7814\u7a76\u906d\u5230\u6253\u538b\uff0c\u79d1\u7814\u6761\u4ef6\u8ba9\u82f1\u96c4\u65e0\u7528\u6b66\u4e4b\u5730\uff0c\u4ee5\u53ca\u5b9e\u9a8c\u8fc7\u7a0b\u4e2d\u7075\u5149\u4e00\u73b0\u7684\u706b\u82b1\uff0c\u90fd\u53ef\u4ee5\u6784\u6210\u66f2\u6298\u60c5\u8282\u3002\u5f53\u7136\uff0c\u8fd9\u91cc\u8bf4\u7684\u751f\u52a8\u66f2\u6298\u7ecf\u8fc7\u4e86\u827a\u672f\u5316\u5904\u7406\uff0c\u7565\u53bb\u4e86\u7edd\u5927\u591a\u6570\u5197\u957f\u6c89\u95f7\u7684\u5b9e\u9645\u79d1\u7814\u7ec6\u8282\u3002\u5929\u7a7a\u547c\u5524\u6211\u4eec | \u300a\u56fd\u5bb6\u5730\u7406\u300b\u7684MARS\u7cfb\u5217\u8282\u76ee\u5b9e\u9645\u4e0a\uff0c\u628a\u79d1\u7814\u8fc7\u7a0b\u6240\u5177\u6709\u7684\u751f\u52a8\u66f2\u6298\u4f5c\u4e3a\u5356\u70b9\uff0c\u5df2\u7ecf\u662f\u5305\u62ec\u679c\u58f3\u7f51\u5728\u5185\u7684\u5f88\u591a\u516c\u4f17\u53f7\u6216\u7f51\u7edc\u65b0\u5a92\u4f53\u79d1\u666e\u6587\u7ae0\u7684\u5e38\u89c4\u5957\u8def\uff0c\u5c3d\u7ba1\u7565\u663e\u9057\u61be\u7684\u662f\uff0c\u8fd9\u4e9b\u6587\u7ae0\u4e2d\u7684\u7d20\u6750\uff0c\u5927\u90e8\u5206\u6765\u6e90\u4e8e\u56fd\u5916\u79d1\u666e\u6587\u7ae0\u6216\u79d1\u7814\u6210\u679c\u3002\u5728\u793e\u4ea4\u5a92\u4f53\u4e0a\uff0c\u540c\u6837\u4e5f\u6709\u4f18\u79c0\u7684\u79d1\u5b66\u89c6\u9891\u65e2\u53eb\u597d\u53c8\u5356\u5ea7\u3002\u6bd4\u5982\u5728\u79d1\u666e\u5708\u91cc\u88ab\u79f0\u4e3a\u201c\u6625\u54e5\u201d\u7684\u56fd\u5bb6\u5929\u6587\u53f0\u90d1\u6c38\u6625\u7814\u7a76\u5458\uff0c\u4e0e\u817e\u8baf\u89c6\u9891\u5408\u4f5c\u7684\u300a\u706b\u661f\u79fb\u6c11\u300b\uff0c\u5f88\u65e9\u5c31\u83b7\u5f973000\u4e07\u6b21\u8bbf\u95ee\u91cf\u3002\u636e\u8bf4\uff0c\u4e2d\u79d1\u9662\u52a8\u7269\u6240\u5468\u742a\u9662\u58eb\u7684\u4e00\u6bb5\u5b9e\u9a8c\u89c6\u9891\uff0c\u4e5f\u5728\u77ed\u77ed\u51e0\u5c0f\u65f6\u5438\u5f15\u4e863\u4e07\u6b21\u70b9\u51fb\u3002\u6a21\u4effTED\u7684\u79d1\u5b66\u5bb6\u77ed\u6f14\u8bb2\uff0c\u56fd\u5185\u4e5f\u6709\u4e86\u5305\u62ec\u4e2d\u56fd\u79d1\u534f\u4e3b\u529e\u3001\u679c\u58f3\u627f\u529e\u7684\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u6f14\u8bb2\uff0c\u4e2d\u79d1\u9662SELF\u8bb2\u575b\u7b49\u7b49\u5728\u5185\u7684\u591a\u4e2a\u7248\u672c\u3002\u8fd9\u4e9b\u89c6\u9891\u5171\u540c\u7684\u7279\u70b9\uff0c\u662f\u79d1\u5b66\u4e0d\u518d\u4ec5\u4ec5\u662f\u5ba3\u626c\u79d1\u6280\u6210\u5c31\u7684\u4e00\u4e2a\u7b26\u53f7\uff0c\u800c\u662f\u6709\u4e86\u72ec\u7acb\u7684\u751f\u547d\u3002\u4e00\u7ebf\u79d1\u5b66\u5bb6\u7684\u76f4\u63a5\u6295\u5165\uff0c\u4e5f\u8ba9\u79d1\u5b66\u672c\u8eab\u7684\u5947\u5999\uff0c\u6210\u4e3a\u79d1\u5b66\u89c6\u9891\u8282\u76ee\u7684\u8981\u7d20\u4e4b\u4e00\u3002\u56fd\u5bb6\u5929\u6587\u53f0\u90d1\u6c38\u6625\u300a\u706b\u661f\u79fb\u6c11\u300b\u79d1\u666e\u6f14\u8bb2 | \u817e\u8baf\u89c6\u9891\u4e3a\u4f55\u8fd9\u4e9b\u4e0d\u65ad\u6d8c\u73b0\u7684\u8d44\u6e90\u548c\u5e9e\u5927\u7684\u89c2\u4f17\u5374\u65e0\u6cd5\u201c\u633d\u6551\u201d\u592e\u89c6\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u5462\uff1f\u524d\u9762\u8bb2\u7684\u628a\u79d1\u666e\u7b49\u540c\u4e8e\u5ba3\u793a\u56fd\u5bb6\u79d1\u6280\u6210\u5c31\uff0c\u65e0\u7591\u662f\u539f\u56e0\u4e4b\u4e00\u3002\u89c2\u4f17\u4eec\u5f53\u7136\u6709\u56e0\u4e3a\u8d5e\u53f9\u7956\u56fd\u79d1\u6280\u5f3a\u76db\u800c\u89c2\u770b\u79d1\u5b66\u8282\u76ee\u7684\u52a8\u673a\uff0c\u4f46\u90a3\u4e3b\u8981\u5c40\u9650\u5728\u56fd\u5e86\u5468\u5e74\u3001\u5ae6\u5a25\u767b\u6708\u8fd9\u6837\u7279\u5b9a\u7684\u91cd\u5927\u4e8b\u4ef6\u53d1\u751f\u65f6\uff0c\u8fd9\u79cd\u52a8\u673a\u4e0d\u8db3\u4ee5\u6210\u4e3a\u6bcf\u5929\u652f\u6491\u6211\u4eec\u89c2\u770b\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u6216\u5176\u4ed6\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u7684\u7406\u7531\u3002\u4ec5\u4ec5\u63ed\u793a\u51fa\u8fd9\u4e00\u70b9\u8fd8\u4e0d\u591f\u3002\u6211\u4eec\u4e5f\u8981\u770b\u5230\uff0c\u57fa\u4e8e\u6d41\u91cf\u6280\u672f\u7684\u793e\u4ea4\u5a92\u4f53\uff0c\u5728\u52a8\u5458\u79d1\u5b66\u5bb6\u6295\u8eab\u79d1\u666e\u3001\u9501\u5b9a\u9002\u5b9c\u7684\u79d1\u5b66\u89c2\u4f17\u3001\u4e3a\u53d7\u4f17\u6309\u9700\u64ad\u653e\u4ee5\u53ca\u4e3a\u79d1\u5b66\u5bb6\u6216\u79d1\u666e\u4eba\u58eb\u7684\u52b3\u52a8\u4ea7\u751f\u56de\u62a5\u65b9\u9762\uff0c\u90fd\u6bd4\u4f20\u7edf\u7535\u89c6\u5a92\u4f53\u66f4\u5177\u4f18\u52bf\u3002\u4f46\u4e0e\u6b64\u540c\u65f6\uff0c\u5fc5\u987b\u627f\u8ba4\uff0c\u7f51\u7edc\u6d41\u5a92\u4f53\u6216\u516c\u4f17\u53f7\u662f\u65e0\u6cd5\u5b8c\u5168\u53d6\u4ee3\u4f20\u7edf\u7684\u4e3b\u6d41\u7535\u89c6\u5a92\u4f53\u7684\uff0c\u5c24\u5176\u662f\u592e\u89c6\u8fd9\u79cd\u6709\u91cd\u5927\u653f\u6cbb\u610f\u4e49\u7684\u6743\u5a01\u5a92\u4f53\u3002\u6240\u4ee5\uff0c\u5728\u611f\u53f9\u300a\u8d70\u8fd1\u79d1\u5b66\u300b\u7684\u505c\u64ad\u6807\u5fd7\u7740\u4e2d\u56fd\u4f20\u7edf\u7535\u89c6\u79d1\u5b66\u8282\u76ee\u5f0f\u5fae\u7684\u540c\u65f6\uff0c\u6211\u4eec\u4e5f\u671f\u5f85\u7740\u4f20\u7edf\u7684\u6743\u5a01\u7535\u89c6\u5a92\u4f53\u80fd\u5229\u7528\u793e\u4ea4\u5a92\u4f53\u6240\u521b\u9020\u7684\u5404\u79cd\u673a\u9047\uff0c\u62cd\u6444\u51fa\u7b26\u5408\u79d1\u5b66\u4f20\u64ad\u81ea\u8eab\u7279\u70b9\u7684\u4f18\u79c0\u7535\u89c6\u8282\u76ee\u3002\u79d1\u5b66\u6ca1\u6709\u8d70\u8fdc\uff0c\u56e0\u4e3a\u5b83\u672c\u6765\u5c31\u5728\u6211\u4eec\u4e4b\u4e2d\u3002\u73b0\u5728\u662f\u65f6\u5019\u8ba9\u6211\u4eec\u4e0e\u4f20\u7edf\u7684\u7535\u89c6\u4eba\u4e00\u8d77\u52aa\u529b\uff0c\u8ba9\u79d1\u5b66\u8d70\u8fd1\u7535\u89c6\u4e86\u3002\uff08\u7f16\u8f91\uff1aYuki\uff09", "title": "\u62ff\u4ec0\u4e48\u62ef\u6551\u4f60\uff0c\u6211\u7684\u300a\u8d70\u8fd1\u79d1\u5b66\u300b", "ukey_author": "nq0zan", "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 173, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 3, "key": "life", "name": "\u751f\u6d3b", "sort_score": 2}, "channels": [{"articles_count": 4299, "channel_type": "normal", "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "frontier", "name": "\u524d\u6cbf", "sort_score": 1}], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\">\u672c\u6587\u4e3a2019\u5e749\u670814\u65e5&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u4e03\u671f&mdash;&mdash;\u5584\u826f\u7684\u529b\u91cf&nbsp;| \u6731\u5ef7\u52ad&nbsp;\u6f14\u8bb2\u5b9e\u5f55\uff1a</span></span></p>\r\n\r\n<p class=\"editors-note\">\u5f53\u6211\u4eec\u4eab\u53d7\u7740\u73b0\u4ee3\u751f\u6d3b\u4fbf\u5229\u4e0e\u7f8e\u597d\u7684\u540c\u65f6\uff0c\u8fd8\u6709\u4e00\u4e9b\u4eba\u7fa4\uff0c\u6b63\u5728\u9ed8\u9ed8\u627f\u53d7\u751f\u6d3b\u7684\u832b\u7136\u3001\u65e0\u52a9\u751a\u81f3\u7edd\u671b\u3002\u4e2d\u56fd\u79d1\u5b66\u9662\u5fc3\u7406\u7814\u7a76\u6240\u7814\u7a76\u5458\u6731\u5ef7\u52ad\u8001\u5e08\u6240\u505a\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u9ed8\u9ed8\u8e72\u5b88\u7740\u4e00\u4e2a\u6811\u6d1e\uff0c\u8bc6\u522b\u51fa\u6709\u81ea\u6740\u503e\u5411\u7684\u4eba\uff0c\u7136\u540e\u5bf9\u4ed6\u4eec\u8bf4\uff1a\u4e14\u7559\u4e00\u6b65\u3002\u6731\u5ef7\u52ad\u8001\u5e08\u5e26\u6765\u5206\u4eab\u300a\u5982\u4f55\u7528\u4eba\u5de5\u667a\u80fd\u6280\u672f\uff0c\u8ba9\u7ad9\u5728\u60ac\u5d16\u8fb9\u7f18\u7684\u4eba&ldquo;\u4e14\u7559\u4e00\u6b65&rdquo;\uff1f\u300b\u3002</p>\r\n\r\n<p>\u6731\u5ef7\u52ad\u6f14\u8bb2\u89c6\u9891\uff1a</p>\r\n\r\n<p class=\"img-caption\"><a href=\"https://v.qq.com/x/page/g3000nkwdpu.html\"><img alt=\"\" src=\"https://1-im.guokr.com/ne7IswpioFuVM24teRlmOjdBHgzT-8LiGq5hhMXraCM4BAAA0AIAAEpQ.jpg\" style=\"height:427px; width:640px\" /></a><strong>\u25b2\u70b9\u51fb\u56fe\u7247\u53ef\u89c2\u770b\u6731\u5ef7\u52ad\u6f14\u8bb2\u89c6\u9891\u25b2</strong></p>\r\n\r\n<p><strong><span style=\"background-color:#ADD8E6\">\u4ee5\u4e0b\u4e3a\u6731\u5ef7\u52ad\u6f14\u8bb2\u5b9e\u5f55\uff1a</span></strong></p>\r\n\r\n<p>\u6211\u662f\u6731\u5ef7\u52ad\uff0c\u6765\u81ea\u4e2d\u56fd\u79d1\u5b66\u9662\u5fc3\u7406\u7814\u7a76\u6240\u3002\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u901a\u8fc7\u6280\u672f\u624b\u6bb5\uff0c\u5411\u5904\u4e8e\u81ea\u6740\u56f0\u5883\u4e2d\u7684\u4eba\u63d0\u4f9b\u5e2e\u52a9\u3002</p>\r\n\r\n<p>\u6211\u867d\u7136\u6765\u81ea\u4e2d\u79d1\u9662\u5fc3\u7406\u6240\uff0c\u4f46\u5176\u5b9e\u662f\u7eaf\u8ba1\u7b97\u673a\u80cc\u666f\uff0c\u6211\u7684\u672c\u3001\u7855\u3001\u535a\u90fd\u662f\u8ba1\u7b97\u673a\u4e13\u4e1a\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/G1TAX7kY9M72M2A7N-bZswmir3DZqCMOka_aocUi1p04BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u535a\u58eb\u6bd5\u4e1a\u4e8e\u4e2d\u56fd\u79d1\u5b66\u9662\u8ba1\u7b97\u6280\u672f\u7814\u7a76\u6240\uff0c\u5f53\u65f6\u662f\u505a\u8bed\u97f3\u5408\u6210\u3002\u5927\u5bb6\u7ecf\u5e38\u542c\u5230\u7684&ldquo;\u5012\u8f66\u8bf7\u6ce8\u610f&rdquo;\u5c31\u662f\u5408\u6210\u7684\uff0c\u53ea\u4e0d\u8fc7\u5408\u6210\u6548\u679c\u6bd4\u8f83\u5dee\u3002\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u901a\u8fc7\u4eba\u5de5\u667a\u80fd\u5b66\u4e60\u5230\u4eba\u4eec\u8bf4\u8bdd\u7684\u97f5\u5f8b\u89c4\u5219\uff0c\u4ece\u800c\u63d0\u9ad8\u5408\u6210\u8bed\u97f3\u7684\u81ea\u7136\u5ea6\u3002</p>\r\n\r\n<p>\u4ece\u56fd\u5185\u6bd5\u4e1a\u4e4b\u540e\uff0c\u6211\u53bb\u4e86\u963f\u5c14\u4f2f\u5854\u5927\u5b66\uff08University of Alberta\uff09\uff0c\u5c5e\u4e8e\u52a0\u62ff\u5927\u4eba\u5de5\u667a\u80fd\u9886\u57df\u6392\u540d\u524d\u4e09\u7684\u5b66\u6821\uff0c\u963f\u5c14\u6cd5\u72d7\uff08AlphaGo\uff09\u3001\u963f\u5c14\u6cd5\u96f6\uff08AlphaZero\uff09\u7684\u4e3b\u521b\u4eba\u5458\u5c31\u662f\u8fd9\u4e2a\u5b66\u6821\u7684AI\u7ec4\u6bd5\u4e1a\u7684\u3002\u6211\u5728\u52a0\u62ff\u5927\u4e3b\u8981\u7814\u7a76\u63a8\u8350\u7cfb\u7edf\uff0c\u5e0c\u671b\u901a\u8fc7\u5bf9\u4eba\u6d4f\u89c8\u7f51\u9875\u65f6\u7684\u884c\u4e3a\u5206\u6790\u4e86\u89e3\u4ed6\u7684\u4fe1\u606f\u9700\u6c42\uff0c\u7136\u540e\u4ece\u5168\u7f51\u627e\u5230\u80fd\u591f\u6ee1\u8db3\u4ed6\u9700\u6c42\u7684\u5185\u5bb9\u3002\u8fd9\u91cc\u9762\u5c31\u6d89\u53ca\u5230\u5bf9\u4eba\u7684\u884c\u4e3a\u5206\u6790\uff0c\u6240\u4ee5\u540e\u6765\u6211\u5c31\u9010\u6e10\u63a5\u89e6\u5fc3\u7406\u5b66\uff0c\u5e0c\u671b\u80fd\u591f\u66f4\u591a\u5730\u4e86\u89e3\u5b83\uff0c\u7136\u540e\u628a\u5b83\u8ddf\u8ba1\u7b97\u673a\u7ed3\u5408\u5728\u4e00\u8d77\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/XJuz4arTmg9iNi1xvxO5eW0z2soPXlMrdS2Tgva6h-I4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>2012\u5e74\uff0c\u6211\u5230\u4e86\u4e2d\u79d1\u9662\u5fc3\u7406\u6240\u3002\u7ed3\u679c\u53d1\u73b0\uff0c\u5fc3\u7406\u5b66\u548c\u8ba1\u7b97\u673a\u662f\u4e24\u4e2a\u5b8c\u5168\u4e0d\u540c\u7684\u4e16\u754c\uff0c\u5176\u4e2d\u7684\u5dee\u522b\u53ef\u80fd\u8d85\u8fc7\u4e2d\u56fd\u5357\u65b9\u548c\u5317\u65b9\u3002\u7ecf\u8fc7\u51e0\u5e74\u7684\u78e8\u5408\uff0c\u6211\u89c9\u5f97\u73b0\u5728\u8fd8\u7b97\u9002\u5e94\u3002</p>\r\n\r\n<p>\u6211\u5728\u5fc3\u7406\u6240\u7684\u4e3b\u8981\u5de5\u4f5c\uff0c\u5c31\u662f\u628a\u65b0\u7684\u8ba1\u7b97\u673a\u6280\u672f\u8fd0\u7528\u5230\u5fc3\u7406\u5b66\u9886\u57df\uff0c\u53bb\u7ed3\u5408\u5fc3\u7406\u5b66\u7684\u7814\u7a76\u6216\u8005\u5b9e\u8df5\u3002\u6bd4\u5982<strong>\u901a\u8fc7\u5bf9\u4eba\u7684\u4e00\u4e9b\u884c\u4e3a\u5206\u6790\uff08\u5305\u62ec\u6b65\u6001\u3001\u9762\u90e8\u3001\u8bed\u97f3\u5206\u6790\uff09\uff0c\u53bb\u4e86\u89e3\u4ed6\u7684\u5fc3\u7406\u72b6\u6001</strong>\uff0c\u5c31\u50cf\u6211\u4eec\u7ecf\u5e38\u8bf4\u7684&ldquo;\u8bfb\u5fc3\u672f&rdquo;\u3002\u5f53\u7136\uff0c\u6211\u4eec\u8fd9\u4e2a\u662f\u79d1\u5b66\u7814\u7a76\uff0c \u4e0d\u662f\u8ff7\u4fe1\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/u9Hc6jK7yMWoWFnGVRcTtdoTDgv6L8aP5JaOu6NKwoM4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6bd4\u5982\uff0c\u6211\u4eec\u60f3\u901a\u8fc7\u5206\u6790\u4e2d\u56fd\u51e0\u5343\u5e74\u7684\u6587\u8a00\u6587\uff0c\u53bb\u4e86\u89e3\u53e4\u4eba\u4e2a\u4f53\u4e3b\u4e49\u548c\u96c6\u4f53\u4e3b\u4e49\u7684\u5fc3\u7406\u53d8\u5316\u7279\u5f81\u3002\u4ece\u7ed3\u679c\u6765\u770b\uff0c\u5148\u79e6\u65f6\u671f\u7684\u4e2a\u4eba\u4e3b\u4e49\u6bd4\u8f83\u5f3a\u5927\uff0c\u4f46\u968f\u7740\u671d\u4ee3\u7684\u53d8\u8fc1\u53d7\u5230\u4e86\u4e0d\u65ad\u589e\u5f3a\u7684\u538b\u5236\u3002</p>\r\n\r\n<p>\u7531\u6b64\uff0c\u6211\u4eec\u4e5f\u8bd5\u56fe\u89e3\u91ca&ldquo;\u674e\u7ea6\u745f\u4e4b\u95ee&rdquo;\uff1a\u4e3a\u4ec0\u4e48\u4e2d\u56fd\u5728\u660e\u671d\u540e\u671f\u51fa\u73b0\u4e86\u8d44\u672c\u4e3b\u4e49\u840c\u82bd\uff0c\u5374\u6ca1\u6709\u51fa\u73b0\u8d44\u672c\u4e3b\u4e49\u53d1\u5c55\uff1f\u6211\u4eec\u89c9\u5f97\u53ef\u80fd\u4e0e\u4e2a\u4f53\u53d7\u5230\u957f\u671f\u7684\u538b\u5236\u6709\u5173\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/nVLMKaxGuvjmPykxCnDuHrBv5AakwMrX-_0afXTu6Pg4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u4eec\u4e5f\u505a\u4e00\u4e9b\u7ebf\u4e0b\u7cbe\u795e\u75be\u75c5\u7684\u8bca\u65ad\u3002\u7531\u4e8e\u4ee5\u533b\u751f\u7684\u4e3b\u89c2\u8bca\u65ad\u4e3a\u4e3b\uff0c\u6211\u4eec\u6709\u65f6\u4e5f\u80fd\u770b\u5230\u62a5\u9053\u4e0a\u8bf4&ldquo;\u88ab\u7cbe\u795e\u75be\u75c5&rdquo;\u3002\u90a3\u4e48\uff0c<strong>\u6709\u6ca1\u6709\u53ef\u80fd\u5229\u7528\u6280\u672f\u7684\u624b\u6bb5\u8f85\u52a9\u533b\u751f\u505a\u51fa\u66f4\u79d1\u5b66\u7684\u8bca\u65ad\uff1f</strong></p>\r\n\r\n<p>\u5982\u679c\u6211\u4eec\u73b0\u5728\u95ee\u5927\u5bb6\uff1a\u4f60\u6709\u6ca1\u6709\u7cbe\u795e\u75c5\uff1f</p>\r\n\r\n<p>\u4f60\u80af\u5b9a\u56de\u7b54\uff1a\u6211\u6ca1\u6709\u7cbe\u795e\u75c5\uff0c\u6211\u5f88\u6b63\u5e38\u3002</p>\r\n\r\n<p>\u5fc3\u7406\u5b66\u91cc\u9762\u6709\u4e00\u4e2a\u6982\u5ff5\uff0c&ldquo;\u793e\u4f1a\u8d5e\u8bb8\u6027&rdquo;&mdash;&mdash;\u6211\u4eec\u6bcf\u4e2a\u4eba\u90fd\u60f3\u628a\u81ea\u5df1\u597d\u7684\u4e00\u9762\u8868\u73b0\u51fa\u6765\u3002\u56e0\u6b64\uff0c\u56de\u7b54\u4e5f\u53ef\u80fd\u662f\u5047\u7684\uff0c\u4e0d\u8fc7\u4e00\u4e9b\u8bed\u97f3\u3001\u9762\u90e8\u548c\u6b65\u6001\u7684\u884c\u4e3a\u6307\u6807\uff08\u8fd8\u5305\u62ec\u5404\u79cd\u5404\u6837\u7684\u884c\u4e3a\uff09\u5f88\u96be\u63a9\u9970\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u60f3\u5229\u7528\u4eba\u5de5\u667a\u80fd\u7684\u529e\u6cd5\u5bf9\u4eba\u7684\u884c\u4e3a\u8fdb\u884c\u5206\u6790\uff0c\u4ee5\u6b64\u4e86\u89e3\u4ed6\u7684\u7cbe\u795e\u548c\u5fc3\u7406\u72b6\u6001\uff0c\u4ece\u800c\u4e3a\u7cbe\u795e\u75be\u75c5\u7684\u8bca\u65ad\u63d0\u4f9b\u8f85\u52a9\u3002</p>\r\n\r\n<p>\u4eba\u5de5\u667a\u80fd\u786e\u5b9e\u53ef\u4ee5\u5e2e\u6211\u4eec\u505a\u5f88\u591a\u4e8b\u60c5\uff0c\u6211\u4eec\u56fd\u5bb6\u4e5f\u5728\u5927\u529b\u63a8\u8fdb\u4eba\u5de5\u667a\u80fd\u53d1\u5c55\uff0c\u4f46\u5b83\u4e0d\u662f\u4e07\u80fd\u7684\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/XNMi8sFhsUWqs0yyAkqRTkgxFE5BeN8GjC5-wJPUePg4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6bd4\u5982\uff0c\u4e0a\u9762\u4e24\u53e5\u8bdd\u770b\u4f3c\u6ca1\u95ee\u9898\uff0c\u4f46\u5176\u5b9e\u5305\u542b\u4e86\u81ea\u6740\u610f\u5ff5\u7684\u8868\u8fbe\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/LSiLhoBkhiBUCj-pCwi2oyRqw7w8NJbFaRRZpYq8LDI4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>&ldquo;\u8d70\u996d&rdquo;\u662f\u57282012\u5e743\u6708\u81ea\u6740\u7684\u4e00\u4e2a\u5357\u4eac\u5973\u5927\u5b66\u751f\uff0c&ldquo;\u996d\u996d&rdquo;\u662f\u5bf9\u5979\u7684\u6635\u79f0\u3002\u5728\u5979\u8d70\u540e\uff0c\u5979\u7684\u6700\u540e\u4e00\u6761\u5fae\u535a\u6210\u4e86\u6811\u6d1e\uff0c\u6709\u5f88\u591a\u4eba\u4e0b\u9762\u7559\u8a00\u3002\u6839\u636e2017\u5e742\u6708\u300a\u51b0\u70b9\u5468\u520a\u300b\u7684\u7edf\u8ba1\uff0c\u5f53\u65f6\u670958\u4e07\u6761\u7559\u8a00\uff1b\u73b0\u5728\u5df2\u7ecf\u8d85\u8fc7100\u4e07\u6761\u4e86\uff0c\u5e76\u4e14\u6bcf\u6708\u589e\u52a0\u7684\u901f\u5ea6\u975e\u5e38\u5feb\uff0c\u5927\u6982\u67096000\u591a\u6761\u3002</p>\r\n\r\n<p>2012\u5e74\u52302013\u5e74\u671f\u95f4\uff0c\u7f51\u4e0a\u6709\u7279\u522b\u591a\u7684\u81ea\u6740\u6848\u4f8b\u62a5\u9053\u3002\u7531\u6b64\u6211\u4eec\u5c31\u60f3\uff0c\u6709\u6ca1\u6709\u53ef\u80fd\u5bf9\u4eba\u7684\u81ea\u6740\u610f\u5ff5\u6216\u8005\u53ef\u80fd\u6027\u505a\u51fa\u8bc6\u522b\uff1f\u8fd9\u6837\u6216\u8bb8\u53ef\u4ee5\u4e3a\u81ea\u6740\u5e72\u9884\u548c\u9884\u9632\u63d0\u4f9b\u5e2e\u52a9\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/oFMW8Ws5q8CbCaAuJjcTtxaNKgixTLe43Z5Bqa40M3M4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4eec\u4e00\u822c\u4f1a\u8ba4\u4e3a\uff0c\u81ea\u6740\u662f\u4e2a\u4eba\u9009\u62e9\uff0c\u5e72\u9884\u6ca1\u6709\u5fc5\u8981\u3002\u4f46\u662f\u4ece\u7814\u7a76\u4e2d\u6765\u770b\uff0c\u5176\u5b9e\u5c06\u8fd180%\u7684\u4eba\u5e76\u975e\u89c9\u5f97\u6ca1\u6709\u5fc5\u8981\uff0c\u800c\u662f\u4e0d\u77e5\u9053\u5411\u54ea\u6c42\u52a9\u6216\u8005\u5982\u4f55\u6c42\u52a9&mdash;&mdash;\u4ed6\u9047\u5230\u95ee\u9898\u4f46\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u89e3\u51b3\uff0c\u53ef\u80fd\u81ea\u6740\u662f\u552f\u4e00\u7684\u9009\u62e9\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/9jqsklTdZwga6FqQo9Hr84ILdIEPn53sv1FJF5O1Wvg4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u867d\u7136\u76ee\u524d\u793e\u4f1a\u4e0a\u6709\u5f88\u591a\u81ea\u6740\u5e72\u9884\u548c\u6551\u52a9\u70ed\u7ebf\uff0c\u4f46\u5728\u67d0\u4e9b\u60c5\u51b5\u4e0b\uff0c\u6c42\u52a9\u4eba\u662f\u4e00\u8fb9\u5272\u8155\u4e00\u8fb9\u6253\u7535\u8bdd\uff0c\u4e3a\u65f6\u5df2\u665a\u3002\u90a3\u4e48\uff0c\u6709\u6ca1\u6709\u53ef\u80fd<strong>\u628a\u5e72\u9884\u5c3d\u91cf\u524d\u79fb\uff0c\u5728\u4ed6\u521a\u5f00\u59cb\u6709\u81ea\u6740\u610f\u5ff5\u6216\u60f3\u6cd5\u7684\u65f6\u5019\u5c31\u63d0\u4f9b\u53ca\u65f6\u4e13\u4e1a\u7684\u6307\u5bfc\uff0c\u5e2e\u4ed6\u89e3\u51b3\u95ee\u9898\uff0c\u4e0d\u8981\u7b49\u4e8b\u60c5\u4e0d\u53ef\u633d\u56de\u7684\u65f6\u5019\u518d\u53bb\u60f3\u529e\u6cd5\u8865\u6551</strong>\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/DRB_5vTNLB_pE06qNJBiQTz1hegLOf4u6F5mEzgVlEU4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u56e0\u6b64\uff0c\u5c31\u9700\u8981\u6211\u4eec\u53ca\u65e9\u4e3b\u52a8\u5730\u53bb\u53d1\u73b0\u90a3\u4e9b\u81ea\u6740\u610f\u5ff5\u8005\uff0c\u800c\u4e0d\u662f\u88ab\u52a8\u7b49\u5f85\u4ed6\u4eec\u6253\u6765\u7535\u8bdd\u3002</p>\r\n\r\n<p>\u6211\u4eec\u4ee5\u524d\u5c1d\u8bd5\u8fc7\u4eba\u5de5\u6807\u6ce8\uff0c\u7ed3\u679c\u53d1\u73b0\u5de5\u4f5c\u91cf\u7279\u522b\u5927\uff0c\u592a\u591a\u7684\u8d1f\u5411\u4fe1\u606f\u5bf9\u5de5\u4f5c\u4eba\u5458\u5f71\u54cd\u4e5f\u975e\u5e38\u5927\u3002\u6211\u4eec\u7edf\u8ba1\u4e86\u4e00\u4e0b\uff0c\u4e24\u4e09\u5343\u6761\u6570\u636e\u4eba\u5de5\u6807\u6ce8\u5927\u7ea6\u9700\u8981\u4e00\u5468\u751a\u81f3\u4e24\u5468\uff0c\u53ef\u80fd\u5c31\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\u3002</p>\r\n\r\n<p>\u6211\u60f3\u5229\u7528\u4eba\u5de5\u667a\u80fd\u6280\u672f\u5bf9\u7f51\u7edc\u4e0a\u7684\u4fe1\u606f\u505a\u53ca\u65f6\u81ea\u52a8\u7684\u8bc6\u522b\uff0c\u8ba9\u6211\u4eec\u77e5\u9053\u67d0\u4e2a\u4eba\u662f\u5426\u5177\u6709\u81ea\u6740\u610f\u5ff5\u3002\u4e0d\u8fc7\u5176\u4e2d\u4e5f\u6709\u4e2a\u7591\u95ee\uff1a\u4eba\u5de5\u667a\u80fd\u5230\u5e95\u80fd\u4e0d\u80fd\u505a\u5230\uff1f</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/lYm9ssGjF0OYUjLn_yHaxlw47lDAmtTKVPUDI989XEY4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4e3e\u4e2a\u4f8b\u5b50\uff0c\u8fd9\u662f\u4e24\u4e2a\u4eba\u901b\u5546\u573a\u7684\u884c\u4e3a\u8f68\u8ff9\uff0c\u5927\u5bb6\u89c9\u5f97\u54ea\u4e2a\u662f\u5973\u6027\uff1f</p>\r\n\r\n<p>\u663e\u7136\u662f\u53f3\u8fb9\u3002</p>\r\n\r\n<p>\u5927\u5bb6\u867d\u7136\u6ca1\u6709\u770b\u5230\u7167\u7247\uff0c\u4e5f\u6ca1\u6709\u770b\u5230\u8fd9\u4e24\u4e2a\u4eba\u7684\u8eab\u4efd\u8bc1\u53f7\uff0c\u4f46\u5374\u80fd\u591f\u6839\u636e\u884c\u4e3a\u6307\u6807\u5224\u65ad\u51fa\u6027\u522b\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5982\u679c\u628a\u8fd9\u4e9b\u5546\u94fa\u7684\u540d\u5b57\u5217\u51fa\u6765\uff0c\u8fd8\u80fd\u5927\u81f4\u77e5\u9053\u4ed6\u4eec\u7684\u6536\u5165\u60c5\u51b5\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/43lQ_9h7SNNWTVHwOkH03_khjit02xt6qN3_HDCwgnQ4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6240\u4ee5\uff0c<strong>\u6211\u4eec\u6839\u636e\u4e00\u4e2a\u4eba\u7684\u884c\u4e3a\u6307\u6807\uff0c\u5b8c\u5168\u53ef\u4ee5\u5bf9\u4ed6\u7684\u67d0\u4e9b\u4e2a\u6027\u7279\u70b9\u505a\u51fa\u81ea\u52a8\u5224\u65ad</strong>\uff0c\u5728\u4eba\u5de5\u667a\u80fd\u91cc\u8fb9\u7684\u65b9\u6cd5\u5c31\u662f\u673a\u5668\u5b66\u4e60\uff08Machine Learning\uff09\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/nwd0noLTQYktfmV94MQ23H5JU-QRln_SBGhrM1a_bIc4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4e3a\u4e86\u5bf9\u81ea\u6740\u610f\u5ff5\u505a\u51fa\u8bc6\u522b\uff0c\u6211\u4eec\u6839\u636e\u4e0d\u540c\u7d27\u6025\u7a0b\u5ea6\u505a\u4e86\u4e00\u4e9b\u6807\u6ce8\uff0c\u5206\u4e3a<strong>\u6709\u610f\u5ff5\u3001\u6709\u8ba1\u5212\u3001\u6709\u5b9e\u65bd</strong>\u4e09\u7c7b\u3002\u6211\u4eec\u5e0c\u671b\u901a\u8fc7\u4eba\u5de5\u667a\u80fd\u7684\u529e\u6cd5\uff0c\u901a\u8fc7\u5bf9\u8fd9\u4e9b\u77ed\u6587\u672c\u7684\u5206\u6790\uff08\u4ee5\u524d\u7684\u81ea\u6740\u8bc6\u522b\u57fa\u672c\u4e0a\u662f\u57fa\u4e8e\u7bc7\u5e45\u6bd4\u8f83\u957f\u7684\u9057\u4e66\u548c\u9057\u8a00\uff09\uff0c\u53bb\u4e86\u89e3\u80cc\u540e\u6709\u6ca1\u6709\u81ea\u6740\u7684\u610f\u5ff5\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/O9ByTj6RT5l3gM45cEogHZR25Z3wdiYdJ80678k19nE4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4eec\u901a\u8fc7\u6a21\u578b\u7684\u4f18\u5316\u4ee5\u53ca\u81ea\u7136\u8bed\u8a00\u5904\u7406\u6280\u672f\u548c\u4eba\u5de5\u667a\u80fd\u6280\u672f\u7684\u5b66\u4e60\uff0c\u8bc6\u522b\u51c6\u786e\u7387\u6700\u540e\u80fd\u8fbe\u523084%\u3002\u73b0\u5728\u6211\u4eec\u6b63\u5728\u8ddf\u767e\u5ea6\u5408\u4f5c\uff0c\u5e0c\u671b\u80fd\u591f\u7528\u66f4\u590d\u6742\u7684\u4eba\u5de5\u667a\u80fd\u6280\u672f\u63d0\u9ad8\u9884\u6d4b\u7cbe\u5ea6\uff0c\u8282\u7ea6\u4eba\u5de5\u7684\u65f6\u95f4\u3002</p>\r\n\r\n<p>\u5230\u6b64\u4e3a\u6b62\uff0c\u5176\u5b9e\u4ece\u505a\u7814\u7a76\u7684\u89d2\u5ea6\u6765\u8bb2\u5c31\u5dee\u4e0d\u591a\u4e86\uff0c\u5199\u6587\u7ae0\u4e5f\u8db3\u591f\u4e86\u3002\u4f46\u662f\u6211\u4eec\u60f3\u518d\u5f80\u524d\u63a8\u8fdb\u4e00\u6b65\uff0c\u8ddf\u81ea\u6740\u610f\u5ff5\u8005\u8054\u7cfb\u4ea4\u6d41\u3002</p>\r\n\r\n<p>\u6211\u4eec\u5f53\u65f6\u5c31\u9047\u5230\u4e00\u4e2a\u95ee\u9898\uff1a\u600e\u4e48\u8ddf\u4ed6\u4eec\u6c9f\u901a\uff1f</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/83A3tC7MwB5txatzlwjqbChYP30JPh9VyYaisto50FM4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4e00\u79cd\u529e\u6cd5\u5c31\u662f\u5e7f\u800c\u544a\u4e4b\uff0c\u5411\u4ed6\u7684\u4eb2\u670b\u597d\u53cb\u53d1\u4fe1\u606f\uff0c\u8ba9\u5927\u5bb6\u8d76\u7d27\u5173\u6ce8\u4ed6\u3002\u82f1\u56fd\u5b66\u8005\u5728\u65e9\u671f\u505a\u8fc7\u8fd9\u6837\u7684\u5de5\u4f5c\uff0c\u7ed3\u679c\u56e0\u4e3a\u4fb5\u72af\u9690\u79c1\u5f88\u5feb\u88ab\u505c\u6389\u3002\u6211\u4eec\u5f53\u65f6\u89c9\u5f97\uff0c\u8fd9\u79cd\u65b9\u5f0f\u53ef\u80fd\u4f1a\u5f15\u53d1\u5176\u4ed6\u4eba\u5bf9\u4ed6\u7684\u504f\u89c1\uff0c\u4f1a\u5b8c\u5168\u78e8\u706d\u4ed6\u4ee5\u540e\u4e3b\u52a8\u6c42\u52a9\u7684\u53ef\u80fd\u6027\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u4eec\u4e5f\u89c9\u5f97\u8981\u4fdd\u62a4\u4ed6\u7684\u4e2a\u4eba\u5c0a\u4e25\u3002</p>\r\n\r\n<p>\u6211\u4eec\u4e4b\u524d\u63a5\u89e6\u8fc7\u4e00\u4e2a\u6848\u4f8b\uff0c\u4e00\u4e2a\u5c0f\u5973\u5b69\u8df3\u697c\u81ea\u6740\uff0c\u867d\u7136\u6700\u540e\u88ab\u6551\u4e0b\u6765\u4e86\uff0c\u4f46\u540e\u6765\u5979\u8bf4\uff0c\u5982\u679c\u4ee5\u540e\u518d\u8df3\u697c\uff0c\u4f1a\u627e\u4e00\u4e2a\u5176\u4ed6\u4eba\u7edd\u5bf9\u627e\u4e0d\u5230\u7684\u5730\u65b9\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5979\u518d\u6b21\u5b9e\u65bd\u81ea\u6740\u7684\u65b9\u5f0f\u53ef\u80fd\u7279\u522b\u51b3\u7edd\u3002\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u7528\u66f4\u597d\u7684\u529e\u6cd5\uff0c<strong>\u5728\u4fdd\u62a4\u4ed6\u4eec\u7684\u9690\u79c1\u548c\u5c0a\u4e25\u7684\u60c5\u51b5\u4e0b\u5f00\u5c55\u4ea4\u6d41</strong>\u3002</p>\r\n\r\n<p>\u6700\u540e\uff0c\u6211\u4eec\u9009\u62e9\u4e86\u5fae\u535a\u79c1\u4fe1\u7684\u65b9\u5f0f\u3002\u79c1\u4fe1\u662f\u4e00\u5bf9\u4e00\u7684\u90ae\u4ef6\u7cfb\u7edf\uff0c\u5728\u7cfb\u7edf\u524d\u53f0\u6839\u672c\u770b\u4e0d\u5230\u4ea4\u4e92\uff0c\u6211\u4eec\u7684\u7cfb\u7edf\u66fe\u7ecf\u8fd8\u5dee\u70b9\u56e0\u4e3a\u65e5\u6d3b\u91cf\u592a\u5c11\u88ab\u5173\u95ed&mdash;&mdash;\u5176\u5b9e\uff0c\u8fd9\u4e24\u5e74\u591a\u6211\u4eec\u540e\u53f0\u670916\u4e07\u7684\u4ea4\u4e92\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/ObRXdmRRghZFYkG22HpPmis51doI9k1FY_Xz0rYyO5c4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4eec\u7ecf\u5e38\u5728\u7f51\u4e0a\u63a5\u6536\u5230\u5783\u573e\u6d88\u606f\uff0c\u4e00\u822c\u770b\u90fd\u4e0d\u770b\u5c31\u5220\u6389\u4e86\u3002\u90a3\u4e48\uff0c\u5982\u4f55\u80fd\u4fdd\u8bc1\u8fd9\u4e9b\u4eba\u4f1a\u70b9\u5f00\u770b\uff1f</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/rvGND545Oi2op_SudbeE_uTlYiG3U8lcJe7-iyJ91SY4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>2016\u5e74\u76843\u6708\u52309\u6708\uff0c\u6211\u4eec\u7ed9\u770b\u5230\u53d1\u8868\u8fc7\u6709\u81ea\u6740\u610f\u56fe\u5fae\u535a\u7684\u7528\u6237\u53d1\u4e86\u7528\u6237\u5b9e\u9a8c\u7684\u9080\u8bf7\u4fe1\uff0c\u60f3\u4e86\u89e3\u6709\u81ea\u6740\u610f\u5ff5\u7684\u4eba\u9700\u8981\u770b\u5230\u4ec0\u4e48\u6837\u7684\u4fe1\u606f\u3002\u5176\u5b9e\uff0c\u9080\u8bf7\u4fe1\u7684\u5185\u5bb9\u4e5f\u662f\u5e72\u9884\u5185\u5bb9\uff0c\u8fd9\u5f20\u622a\u56fe\u5c31\u662f\u6211\u4eec\u7b2c\u4e00\u6b21\u53d1\u51fa\u6765\u7684\u4fe1\u606f\u3002</p>\r\n\r\n<p>\u53d1\u51fa\u53bb\u540e\uff0c\u6211\u4eec\u5fc3\u91cc\u975e\u5e38\u5fd0\u5fd1\uff0c\u56e0\u4e3a\u73b0\u5728\u7f51\u4e0a\u6709\u5f88\u591a\u952e\u76d8\u4fa0\uff0c\u4e0d\u7ba1\u4f60\u505a\u4ec0\u4e48\u603b\u6709\u4eba\u9a82\uff1b\u800c\u8fd9\u4e2a\u53c8\u6d89\u53ca\u9690\u79c1\uff0c\u7279\u522b\u654f\u611f\u3002\u6211\u4eec\u975e\u5e38\u62c5\u5fc3\u4f1a\u88ab\u63a8\u4e0a\u70ed\u641c\uff0c\u5f04\u6210\u4e00\u4e2a\u8206\u60c5\u4e8b\u4ef6\uff0c\u79d1\u5b66\u9662\u518d\u95ee\u8d23\uff0c\u6240\u91cc\u8fb9\u627e\u6211\u9ebb\u70e6\uff0c\u6700\u540e\u5c31\u505a\u4e0d\u4e86\u4e86\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/VdC9LG5QEunaGLqPElUWFI6fQqT0ridFXdSmBkZy-lU4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4eec\u7ed94222\u4e2a\u7528\u6237\u53d1\u51fa\u4fe1\u606f\uff0c\u5e76\u4e14\u6536\u5230\u4e86\u4e00\u4e9b\u56de\u590d\u3002\u770b\u5230\u56de\u590d\u5185\u5bb9\u540e\uff0c\u6211\u4eec\u677e\u4e86\u4e00\u53e3\u6c14\uff1a\u5f88\u591a\u4eba\u7ed9\u6211\u4eec\u53d1\u6765\u611f\u8c22\uff0c\u53ea\u6709\u4e2a\u4f4d\u6570\u7684\u56de\u590d\u7279\u522b\u8d1f\u9762\uff0c\u4e0d\u8fc7\u8fd9\u5f88\u6b63\u5e38\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/4RU22ruj0_57rRwSr3RYZnRdFMLosV909pBr9vA5dnY4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4f46\u662f\u6709\u4e00\u4f4d\u5fae\u535a\u7528\u6237\uff0c\u4ed6\u7684\u4eb2\u4eba\u7ed9\u6211\u56de\u590d\u8bf4\u4ed6\u5df2\u7ecf\u81ea\u6740\u6b7b\u4ea1\u4e86\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u534a\u5e74\u4e4b\u5185\u8fd9\u4e2a\u4eba\u6709\u8fc7\u81ea\u6740\u8868\u8fbe\uff0c\u6700\u540e\u771f\u7684\u81ea\u6740\u6b7b\u4ea1\u4e86\uff0c\u5f53\u65f6\u7ed9\u6211\u4eec\u7684\u9707\u64bc\u5f88\u5927&mdash;&mdash;<strong>\u867d\u7136\u6211\u4eec\u53d1\u4e86\u79c1\u4fe1\u548c\u8f6c\u4ecb\u4fe1\u606f\uff0c\u5c3d\u5230\u4e86\u8d23\u4efb\uff0c\u4f46\u662f\u5e76\u6ca1\u6709\u8fbe\u5230\u4e00\u4e2a\u597d\u7684\u6548\u679c</strong>\u3002\u6211\u4eec\u5c31\u60f3\uff0c\u6709\u6ca1\u6709\u53ef\u80fd\u66f4\u8fdb\u4e00\u6b65\u5730\u76f4\u63a5\u63d0\u4f9b\u5e72\u9884\u4fe1\u606f\uff0c\u771f\u6b63\u5730\u5e2e\u52a9\u4ed6\u4eec\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/7Ry4VKoZ7iyS09XM_PJiG1FupgFwXsfq1VNoIu_5RVc4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u9664\u4e86\u70ed\u7ebf\u8f6c\u63a5\u4e4b\u5916\uff0c\u6211\u4eec\u8fd8\u589e\u52a0\u4e86\u5728\u7ebf\u7684\u5fc3\u7406\u6d4b\u91cf\u548c\u81ea\u52a9\u5e72\u9884\u9879\u76ee\u3002\u5fc3\u7406\u6d4b\u91cf\u4e0d\u540c\u4e8e\u7f51\u4e0a\u7684\u5a31\u4e50\u6d4b\u8bd5\uff0c\u662f\u901a\u8fc7\u4e13\u4e1a\u7684\u624b\u6bb5\u8ba9\u4ed6\u4eec\u77e5\u9053\u81ea\u5df1\u5f53\u524d\u7684\u60c5\u51b5\u7a76\u7adf\u5982\u4f55\u3002\u800c\u81ea\u4e3b\u5e72\u9884\u662f\u56e0\u4e3a\u6211\u4eec\u53d1\u73b0\u5f88\u591a\u4eba\u4e0d\u613f\u610f\u8ddf\u4eba\u8bb2\u8bdd\uff0c\u800c\u662f\u613f\u610f\u8ddf\u673a\u5668\u4ea4\u6d41\u3002</p>\r\n\r\n<p>\u540c\u65f6\uff0c\u6211\u4eec\u4e5f\u62db\u52df\u5fd7\u613f\u8005\uff08\u6211\u4eec\u5e0c\u671b\u4ed6\u662f\u4e09\u7ea7\u4ee5\u4e0a\u54a8\u8be2\u5e08\uff0c\u56e0\u4e3a\u662f\u975e\u5e38\u4e13\u4e1a\u7684\u4ea4\u6d41\uff0c\u6709\u65f6\u5019\u53ef\u80fd\u4f1a\u5f71\u54cd\u5230\u4e2a\u4eba\uff09\uff0c\u7279\u522b\u8981\u6c42\u4ed6\u4eec\u7528\u7edf\u4e00\u7684\u8d26\u53f7\u8fdb\u884c\u4ea4\u6d41\uff0c\u5728\u4fdd\u62a4\u5bf9\u65b9\u540c\u65f6\u4e5f\u4fdd\u62a4\u81ea\u5df1\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/dqW8wnyuC2TM23ZBr5YXfV2QJwvQXvbZYO2A5RGs7Tw4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6240\u4ee5\u6574\u4e2a\u6d41\u7a0b\u5c31\u662f\uff1a\u7528\u8ba1\u7b97\u673a\u7684\u529e\u6cd5\u53bb\u6293\u53d6\u5230\u6570\u636e\uff0c\u7136\u540e\u7528\u8bad\u7ec3\u597d\u7684\u6a21\u578b\u8bc6\u522b\u51fa\u6709\u81ea\u6740\u610f\u5ff5\u7684\u7528\u6237\uff0c\u5e76\u4e14\u4e5f\u8fdb\u884c\u4eba\u5de5\u7684\u6838\u67e5\u3002</p>\r\n\r\n<p style=\"margin-left:40px\">\u5f53\u65f6\u505a\u7528\u6237\u5b9e\u9a8c\u65f6\uff0c\u6709\u4eba\u7acb\u9a6c\u56de\u590d\u79c1\u4fe1\uff0c\u4ed6\u8bf4\u89c9\u5f97\u81ea\u5df1\u5f88\u6b63\u5e38\uff0c\u4ece\u6765\u6ca1\u6709\u8bf4\u8fc7\u6709\u81ea\u6740\u610f\u5ff5\u7684\u8bdd\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4f1a\u6536\u5230\u8fd9\u6837\u7684\u4fe1\u606f\uff1f\u540e\u6765\u6211\u4eec\u627e\u4e86\u4e00\u4e0b\uff0c\u53d1\u73b0\u8fd9\u4e2a\u4eba\u662f\u5728\u67d0\u4e2a\u5730\u65b9\u8bf4\u8fc7\uff0c\u4f46\u662f\u4ed6\u81ea\u5df1\u7684\u5fae\u535a\u4e0a\u6ca1\u6709\uff0c\u4e8e\u662f\u6211\u4eec\u5c31\u628a\u90a3\u53e5\u8bdd\u7c98\u8d34\u8fc7\u53bb\u56de\u590d\u4ed6\u3002</p>\r\n\r\n<p>\u8fd9\u4ef6\u4e8b\u60c5\u8ba9\u6211\u4eec\u5f88\u8b66\u89c9\uff0c\u56e0\u4e3a\u5982\u679c\u5168\u7528\u8ba1\u7b97\u673a\u4f1a\u6709\u8bef\u5dee\uff0c\u800c\u8bef\u5dee\u4e00\u65e6\u51fa\u6765\uff0c\u4e92\u4fe1\u5c31\u5f7b\u5e95\u88ab\u6253\u7834\u4e86\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u7684\u6bcf\u4e00\u6761\u79c1\u4fe1\u5fc5\u987b\u4fdd\u8bc1\u662f\u53d1\u7ed9\u4e86\u786e\u786e\u5b9e\u5b9e\u6709\u81ea\u6740\u610f\u5ff5\u7684\u4eba\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/sIXA_dKE2LRxOSkiJt5FyU4fCpDRqxphfNgaKdVRlIs4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u901a\u8fc7\u79c1\u4fe1\u7684\u53d1\u9001\u548c\u4ea4\u4e92\uff0c\u6211\u4eec\u80fd\u591f\u63d0\u4f9b\u53ca\u65f6\u7684\u5e72\u9884\u4fe1\u606f\u3002<strong>\u5fd7\u613f\u8005\u4f1a\u505a\u4e00\u4e9b\u7b80\u5355\u7684\u8bc4\u4f30\uff0c\u6700\u540e\u80fd\u770b\u5230\u4ed6\u5fc3\u60c5\u7684\u53d8\u5316\uff0c\u964d\u4f4e\u81ea\u6740\u7684\u53ef\u80fd\u6027</strong>\u3002</p>\r\n\r\n<p>\u79c1\u4fe1\u7a76\u7adf\u8be5\u53d1\u51e0\u6b21\uff1f\u4e0d\u80fd\u6bcf\u6b21\u90fd\u53d1\uff0c\u5426\u5219\u5c31\u662f\u9a9a\u6270\uff1b\u4ee5\u524d\u4e5f\u4ece\u6765\u6ca1\u6709\u53ef\u4f9b\u501f\u9274\u7684\u7814\u7a76\u6210\u679c\u3002\u6211\u4eec\u901a\u5e38\u8bb2\u4e8b\u4e0d\u8fc7\u4e09\uff0c\u4f46\u662f\u53c8\u89c9\u5f97\u53ef\u80fd\u5f88\u591a\u4eba\u4e0d\u770b\uff0c\u6240\u4ee5\u540e\u6765\u5b9a\u4e86\u4e94\u6b21\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/A83Z7rhb79Khdm-v0253HHrRY_2-Mo8ssjmsLBTCkAc4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4ece\u7ed3\u679c\u6765\u770b\uff0c\u867d\u7136\u6709\u4eba\u4ece\u6765\u4e0d\u56de\u590d\u79c1\u4fe1\uff0c\u4e5f\u6ca1\u6709\u8ddf\u5fd7\u613f\u8005\u4ea4\u6d41\u8fc7\uff0c\u4f46\u662f\u8fd9\u51e0\u6b21\u7684\u53d1\u9001\u4f7f\u5f97\u4ed6\u89c9\u5f97\u6709\u4eba\u5728\u5173\u6ce8\u4ed6\uff0c\u80fd\u591f\u611f\u53d7\u5230\u6765\u81ea\u793e\u4f1a\u7684\u652f\u6301&mdash;&mdash;\u8fd9\u6709\u53ef\u80fd\u662f\u4ed6\u514b\u670d\u56f0\u96be\u7684\u6700\u5927\u52a8\u529b\u3002</p>\r\n\r\n<p>\u4e24\u5e74\u5185\uff0c\u6211\u4eec\u4e00\u76f4\u5728\u63a8\u8fdb\u8fd9\u9879\u5de5\u4f5c\u3002\u6211\u4eec\u7279\u522b\u7edf\u8ba1\u4e86\u8ddf\u5fd7\u613f\u8005\u6709\u8fc7\u4ea4\u6d41\u7684\u4eba\uff0c\u4e00\u5171\u67093733\u540d\uff0c\u60f3\u4e86\u89e3\u4ed6\u4eec\u7684\u53cd\u9988\u600e\u4e48\u6837\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/PcyiFhh0gEBbuU9e7m4b8icRss1RMPtcuWHYUxI_2144BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u9996\u5148\uff0c\u4ed6\u4eec\u7684\u6001\u5ea6\u5927\u591a\u6bd4\u8f83\u79ef\u6781\uff0877%\uff09\uff0c\u6d88\u6781\u6001\u5ea6\u7684\u6bd4\u4f8b\u4e5f\u5728\u5408\u7406\u533a\u95f4\u5185\uff0810%\uff09\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u4eec\u8ddf\u5fd7\u613f\u8005\u4ea4\u6d41\u7684\u5185\u5bb9\uff0c\u4e3b\u8981\u662f\u60f3\u4e86\u89e3\u4e00\u4e0b\u6551\u52a9\u65b9\u6cd5\uff0c\u6216\u8005\u8bf4\u660e\u81ea\u8eab\u60c5\u51b5\u3002\u6211\u4eec\u89c9\u5f97\u8fd9\u79cd\u529e\u6cd5\u80fd\u591f\u6fc0\u6d3b\u4ed6\u4eec\u5411\u5916\u754c\u6c42\u52a9\u7684\u52a8\u673a\uff0c\u662f\u975e\u5e38\u91cd\u8981\u7684\u4e00\u6b65\u3002</p>\r\n\r\n<p>\u6709\u4e00\u70b9\uff0c\u6211\u4eec\u4e00\u76f4\u5f88\u5173\u6ce8\uff1a\u5230\u5e95\u662f\u54ea\u4e9b\u56e0\u7d20\u5bfc\u81f4\u4e86\u81ea\u6740\u7684\u53d1\u751f\uff1f\u56fd\u5916\u7684\u7814\u7a76\u8bf4\u81ea\u674090%\u662f\u957f\u671f\u7cbe\u795e\u75be\u75c5\u5bfc\u81f4\u7684\uff0c\u800c\u56fd\u5185\u4e00\u76f4\u90fd\u6709\u8d28\u7591\u548c\u4e89\u8bba\uff0c\u8ba4\u4e3a\u6ca1\u6709\u5230\u8fd9\u4e2a\u6570\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/7JcYpIGMhH9STX3w48gOK4X6LIYbua-Lt6E871069N84BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4ece\u6211\u4eec\u7684\u7ed3\u679c\u6765\u770b\uff0c\u5728\u6240\u6709\u6709\u81ea\u6740\u610f\u5ff5\u751a\u81f3\u4ed8\u8bf8\u5b9e\u65bd\u7684\u4eba\u7fa4\u91cc\uff0c\u63a5\u8fd1\u4e00\u534a\u7684\u4eba\u662f\u7cbe\u795e\u5065\u5eb7\u7684\uff0c\u4ed6\u4eec\u9762\u4e34\u7684\u6700\u4e3b\u8981\u7684\u95ee\u9898\u662f\u5bb6\u5ead\u95ee\u9898\uff0c\u8fd9\u662f\u975e\u5e38\u4ee4\u4eba\u60ca\u8bb6\u7684\u7ed3\u679c\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e9b\u56e0\u7d20\u4e5f\u4f1a\u5bfc\u81f4\u81ea\u6740\u7684\u53ef\u80fd\u6027\uff0c\u6bd4\u5982\u5de5\u4f5c\u3001\u5b66\u4e60\u3001\u5a5a\u604b\uff0c\u5305\u62ec\u53d7\u6b3a\u51cc\u3001\u751a\u81f3\u662f\u88ab\u6027\u4fb5\u7684\u7ecf\u5386\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/wtN0DcZwfgL91aoWTemjUShZN_km4SpSfbjiuvJdEPE4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4eec\u4e5f\u7814\u7a76\u4e86\u81ea\u6740\u610f\u5ff5\u8005\u5411\u8c01\u6c42\u52a9\u3002\u7ed3\u679c\u53d1\u73b0\uff0c\u6709\u4e00\u534a\u7684\u4eba\u4ece\u672a\u5bfb\u6c42\u8fc7\u5e2e\u52a9\u3002\u800c\u4e14\u5728\u6c42\u52a9\u4eba\u7fa4\u91cc\u8fb9\uff0c\u5bb6\u4eba\u4e0d\u4ec5\u4e0d\u662f\u7b2c\u4e00\u4f4d\uff0c\u800c\u4e14\u6bd4\u4f8b\u751a\u81f3\u4f4e\u4e8e\u964c\u751f\u7f51\u53cb\u3002\u5f88\u591a\u65f6\u5019\u6211\u4eec\u8ba4\u4e3a\u5bb6\u5ead\u662f\u4e00\u4e2a\u4eba\u6700\u4e3b\u8981\u4e5f\u6700\u91cd\u8981\u7684\u8d44\u6e90\uff0c\u4f46\u662f\u5728\u6700\u5371\u673a\u7684\u65f6\u5019\uff0c\u5bb6\u4eba\u4e0d\u662f\u7b2c\u4e00\u4f4d\u7684\uff0c\u8fd9\u662f\u9700\u8981\u6211\u4eec\u53cd\u601d\u7684\u4e00\u4e2a\u95ee\u9898\u3002</p>\r\n\r\n<p>\u6211\u4eec\u9700\u8981\u6709\u66f4\u597d\u7684\u5bb6\u5ead\u6559\u80b2\uff0c\u5305\u62ec\u751f\u6b7b\u6559\u80b2\u3002\u524d\u6bb5\u65f6\u95f4\u4e0a\u6d77\u7684\u521d\u4e2d\u751f\u8df3\u6865\u4e8b\u4ef6\uff0c\u53ef\u80fd\u5e76\u4e0d\u4ee3\u8868\u4ed6\u771f\u7684\u5c31\u60f3\u81ea\u6740\uff0c\u6709\u53ef\u80fd\u4ed6\u662f\u901a\u8fc7\u8fd9\u79cd\u884c\u4e3a\u53bb\u8868\u8fbe\u5bf9\u5bb6\u4eba\u7684\u4e0d\u6ee1\uff0c\u4f46\u4ed6\u6ca1\u6709\u60f3\u5230\u8df3\u6865\u90a3\u523b\u4e4b\u540e\u5c31\u65e0\u6cd5\u633d\u56de\u4e86\uff0c\u6211\u89c9\u5f97\u4ed6\u6ca1\u6709\u63a5\u53d7\u5f88\u597d\u7684\u751f\u6b7b\u6559\u80b2\u3002</p>\r\n\r\n<p>\u9664\u6b64\u4e4b\u5916\uff0c\u9752\u5c11\u5e74\u548c\u8001\u5e74\u662f\u81ea\u6740\u7684\u91cd\u707e\u533a\u3002\u4e2d\u56fd\u519c\u675150\u5c81\u4ee5\u4e0a\u7684\u8001\u5e74\u4eba\u81ea\u6740\u6bd4\u4f8b\u975e\u5e38\u9ad8\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6ca1\u6709\u624b\u673a\uff0c\u4e5f\u4e0d\u4e0a\u7f51\uff0c\u4e27\u5931\u52b3\u52a8\u80fd\u529b\u540e\u5c31\u8ba4\u4e3a\u81ea\u5df1\u6210\u4e3a\u4e86\u5bb6\u5ead\u7684\u7d2f\u8d58\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/7Padmnuc6e9nsc23PGlsyteZCIFF0KRV4TpSaMPHsGI4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u4eec\u4e5f\u770b\u5230\u4e2d\u56fd\u7684\u81ea\u6740\u60c5\u51b5\u8ddf\u56fd\u5916\u4e0d\u592a\u4e00\u6837\uff0c\u6709\u4e2d\u56fd\u7684\u7279\u70b9\uff0c\u6240\u4ee5\u4e5f\u9700\u8981\u7814\u7a76\u9002\u5408\u4e2d\u56fd\u4eba\u7fa4\u7684\u81ea\u6740\u5e72\u9884\u65b9\u6cd5\u3002</p>\r\n\r\n<p>\u53e6\u5916\uff0c\u6211\u4eec\u89c9\u5f97\u81ea\u6740\u5e76\u4e0d\u4ec5\u4ec5\u662f\u5fc3\u7406\u95ee\u9898\u5bfc\u81f4\u7684\uff0c\u5176\u5b9e\u5305\u542b\u5f88\u591a\u7684\u73b0\u5b9e\u95ee\u9898\uff0c\u6240\u4ee5\u5b83\u4e5f\u662f\u4e00\u4e2a\u793e\u4f1a\u5de5\u7a0b\uff0c\u9700\u8981\u6574\u4e2a\u793e\u4f1a\u4e00\u8d77\u52aa\u529b\u53bb\u89e3\u51b3\u81ea\u6740\u95ee\u9898\u3002</p>\r\n\r\n<p>\u867d\u7136\u6211\u662f\u5b66\u8ba1\u7b97\u673a\u7684\uff0c\u4f46\u6211\u4e5f\u8ba4\u4e3a\u4eba\u5de5\u667a\u80fd\u53ea\u662f\u4e2a\u6280\u672f\u3002\u5b83\u53ef\u4ee5\u5e2e\u52a9\u6211\u4eec\uff0c\u4f46\u5176\u672c\u8eab\u65e0\u6cd5\u89e3\u51b3\u81ea\u6740\u95ee\u9898\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u7f51\u53cb\u5bf9\u6211\u4eec\u5de5\u4f5c\u7684\u8bc4\u4ef7\uff0c\u6211\u89c9\u5f97\u7279\u522b\u8d34\u5207&mdash;&mdash;</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/vxDqtJkELpZHDrn1OLlao-t2S01X9hSufweOwRALX2o4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u7528\u57fa\u7840\u7684\u529e\u6cd5\uff0c\u53bb\u628a\u4e13\u4e1a\u7684\u65b9\u6cd5\u548c\u624b\u6bb5\u6709\u6548\u5730\u4f20\u8fbe\u5230\u9700\u8981\u7684\u4eba\u7fa4\u4e2d\uff0c\u8ba9\u4ed6\u4eec\u7528\u66f4\u4e13\u4e1a\u7684\u65b9\u6cd5\u53bb\u5e94\u5bf9\u81ea\u5df1\u7684\u95ee\u9898\u3002<strong>\u4e0d\u7ba1\u6700\u540e\u7ed3\u679c\u600e\u4e48\u6837\uff0c\u6211\u4eec\u90fd\u5e0c\u671b\u4ed6\u4eec\u80fd\u591f\u5728\u81ea\u5df1\u7684\u4e16\u754c\u91cc\u8fb9\u5e78\u798f\u5b89\u5eb7</strong>\u3002</p>\r\n\r\n<p>\u8c22\u8c22\u5927\u5bb6\u3002\uff08\u7f16\u8f91\uff1a\u9ea6\u82bd\u6768\u3001\u51dd\u97f3\uff09</p>", "date_created": "2019-10-11T11:43:39.012307+08:00", "date_modified": "2019-10-11T12:06:14.258007+08:00", "date_published": "2019-10-11T12:03:14+08:00", "id": 454847, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "psybst", "replies_count": 7, "small_image": "https://1-im.guokr.com/n-75y-esZVzEleNlJphKJwV1_kQWJNpV0rtZKJml2EA4BAAA0AIAAEpQ.jpg", "subject": {"articles_count": 1370, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "psychology", "minisite_key": "psybst", "name": "\u5fc3\u7406", "sort_score": 10, "subject_type": "normal"}, "subject_key": "psychology", "summary": "\u5982\u4f55\u7528\u4eba\u5de5\u667a\u80fd\u6280\u672f\uff0c\u8ba9\u7ad9\u5728\u60ac\u5d16\u8fb9\u7684\u4eba\u201c\u4e14\u7559\u4e00\u6b65\u201d\uff1f", "text": "\u672c\u6587\u4e3a2019\u5e749\u670814\u65e5\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u4e03\u671f\u2014\u2014\u5584\u826f\u7684\u529b\u91cf\u00a0| \u6731\u5ef7\u52ad\u00a0\u6f14\u8bb2\u5b9e\u5f55\uff1a\u5f53\u6211\u4eec\u4eab\u53d7\u7740\u73b0\u4ee3\u751f\u6d3b\u4fbf\u5229\u4e0e\u7f8e\u597d\u7684\u540c\u65f6\uff0c\u8fd8\u6709\u4e00\u4e9b\u4eba\u7fa4\uff0c\u6b63\u5728\u9ed8\u9ed8\u627f\u53d7\u751f\u6d3b\u7684\u832b\u7136\u3001\u65e0\u52a9\u751a\u81f3\u7edd\u671b\u3002\u4e2d\u56fd\u79d1\u5b66\u9662\u5fc3\u7406\u7814\u7a76\u6240\u7814\u7a76\u5458\u6731\u5ef7\u52ad\u8001\u5e08\u6240\u505a\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u9ed8\u9ed8\u8e72\u5b88\u7740\u4e00\u4e2a\u6811\u6d1e\uff0c\u8bc6\u522b\u51fa\u6709\u81ea\u6740\u503e\u5411\u7684\u4eba\uff0c\u7136\u540e\u5bf9\u4ed6\u4eec\u8bf4\uff1a\u4e14\u7559\u4e00\u6b65\u3002\u6731\u5ef7\u52ad\u8001\u5e08\u5e26\u6765\u5206\u4eab\u300a\u5982\u4f55\u7528\u4eba\u5de5\u667a\u80fd\u6280\u672f\uff0c\u8ba9\u7ad9\u5728\u60ac\u5d16\u8fb9\u7f18\u7684\u4eba\u201c\u4e14\u7559\u4e00\u6b65\u201d\uff1f\u300b\u3002\u6731\u5ef7\u52ad\u6f14\u8bb2\u89c6\u9891\uff1a\u25b2\u70b9\u51fb\u56fe\u7247\u53ef\u89c2\u770b\u6731\u5ef7\u52ad\u6f14\u8bb2\u89c6\u9891\u25b2\u4ee5\u4e0b\u4e3a\u6731\u5ef7\u52ad\u6f14\u8bb2\u5b9e\u5f55\uff1a\u6211\u662f\u6731\u5ef7\u52ad\uff0c\u6765\u81ea\u4e2d\u56fd\u79d1\u5b66\u9662\u5fc3\u7406\u7814\u7a76\u6240\u3002\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u901a\u8fc7\u6280\u672f\u624b\u6bb5\uff0c\u5411\u5904\u4e8e\u81ea\u6740\u56f0\u5883\u4e2d\u7684\u4eba\u63d0\u4f9b\u5e2e\u52a9\u3002\u6211\u867d\u7136\u6765\u81ea\u4e2d\u79d1\u9662\u5fc3\u7406\u6240\uff0c\u4f46\u5176\u5b9e\u662f\u7eaf\u8ba1\u7b97\u673a\u80cc\u666f\uff0c\u6211\u7684\u672c\u3001\u7855\u3001\u535a\u90fd\u662f\u8ba1\u7b97\u673a\u4e13\u4e1a\u3002\u6211\u535a\u58eb\u6bd5\u4e1a\u4e8e\u4e2d\u56fd\u79d1\u5b66\u9662\u8ba1\u7b97\u6280\u672f\u7814\u7a76\u6240\uff0c\u5f53\u65f6\u662f\u505a\u8bed\u97f3\u5408\u6210\u3002\u5927\u5bb6\u7ecf\u5e38\u542c\u5230\u7684\u201c\u5012\u8f66\u8bf7\u6ce8\u610f\u201d\u5c31\u662f\u5408\u6210\u7684\uff0c\u53ea\u4e0d\u8fc7\u5408\u6210\u6548\u679c\u6bd4\u8f83\u5dee\u3002\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u901a\u8fc7\u4eba\u5de5\u667a\u80fd\u5b66\u4e60\u5230\u4eba\u4eec\u8bf4\u8bdd\u7684\u97f5\u5f8b\u89c4\u5219\uff0c\u4ece\u800c\u63d0\u9ad8\u5408\u6210\u8bed\u97f3\u7684\u81ea\u7136\u5ea6\u3002\u4ece\u56fd\u5185\u6bd5\u4e1a\u4e4b\u540e\uff0c\u6211\u53bb\u4e86\u963f\u5c14\u4f2f\u5854\u5927\u5b66\uff08University of Alberta\uff09\uff0c\u5c5e\u4e8e\u52a0\u62ff\u5927\u4eba\u5de5\u667a\u80fd\u9886\u57df\u6392\u540d\u524d\u4e09\u7684\u5b66\u6821\uff0c\u963f\u5c14\u6cd5\u72d7\uff08AlphaGo\uff09\u3001\u963f\u5c14\u6cd5\u96f6\uff08AlphaZero\uff09\u7684\u4e3b\u521b\u4eba\u5458\u5c31\u662f\u8fd9\u4e2a\u5b66\u6821\u7684AI\u7ec4\u6bd5\u4e1a\u7684\u3002\u6211\u5728\u52a0\u62ff\u5927\u4e3b\u8981\u7814\u7a76\u63a8\u8350\u7cfb\u7edf\uff0c\u5e0c\u671b\u901a\u8fc7\u5bf9\u4eba\u6d4f\u89c8\u7f51\u9875\u65f6\u7684\u884c\u4e3a\u5206\u6790\u4e86\u89e3\u4ed6\u7684\u4fe1\u606f\u9700\u6c42\uff0c\u7136\u540e\u4ece\u5168\u7f51\u627e\u5230\u80fd\u591f\u6ee1\u8db3\u4ed6\u9700\u6c42\u7684\u5185\u5bb9\u3002\u8fd9\u91cc\u9762\u5c31\u6d89\u53ca\u5230\u5bf9\u4eba\u7684\u884c\u4e3a\u5206\u6790\uff0c\u6240\u4ee5\u540e\u6765\u6211\u5c31\u9010\u6e10\u63a5\u89e6\u5fc3\u7406\u5b66\uff0c\u5e0c\u671b\u80fd\u591f\u66f4\u591a\u5730\u4e86\u89e3\u5b83\uff0c\u7136\u540e\u628a\u5b83\u8ddf\u8ba1\u7b97\u673a\u7ed3\u5408\u5728\u4e00\u8d77\u30022012\u5e74\uff0c\u6211\u5230\u4e86\u4e2d\u79d1\u9662\u5fc3\u7406\u6240\u3002\u7ed3\u679c\u53d1\u73b0\uff0c\u5fc3\u7406\u5b66\u548c\u8ba1\u7b97\u673a\u662f\u4e24\u4e2a\u5b8c\u5168\u4e0d\u540c\u7684\u4e16\u754c\uff0c\u5176\u4e2d\u7684\u5dee\u522b\u53ef\u80fd\u8d85\u8fc7\u4e2d\u56fd\u5357\u65b9\u548c\u5317\u65b9\u3002\u7ecf\u8fc7\u51e0\u5e74\u7684\u78e8\u5408\uff0c\u6211\u89c9\u5f97\u73b0\u5728\u8fd8\u7b97\u9002\u5e94\u3002\u6211\u5728\u5fc3\u7406\u6240\u7684\u4e3b\u8981\u5de5\u4f5c\uff0c\u5c31\u662f\u628a\u65b0\u7684\u8ba1\u7b97\u673a\u6280\u672f\u8fd0\u7528\u5230\u5fc3\u7406\u5b66\u9886\u57df\uff0c\u53bb\u7ed3\u5408\u5fc3\u7406\u5b66\u7684\u7814\u7a76\u6216\u8005\u5b9e\u8df5\u3002\u6bd4\u5982\u901a\u8fc7\u5bf9\u4eba\u7684\u4e00\u4e9b\u884c\u4e3a\u5206\u6790\uff08\u5305\u62ec\u6b65\u6001\u3001\u9762\u90e8\u3001\u8bed\u97f3\u5206\u6790\uff09\uff0c\u53bb\u4e86\u89e3\u4ed6\u7684\u5fc3\u7406\u72b6\u6001\uff0c\u5c31\u50cf\u6211\u4eec\u7ecf\u5e38\u8bf4\u7684\u201c\u8bfb\u5fc3\u672f\u201d\u3002\u5f53\u7136\uff0c\u6211\u4eec\u8fd9\u4e2a\u662f\u79d1\u5b66\u7814\u7a76\uff0c \u4e0d\u662f\u8ff7\u4fe1\u3002\u6bd4\u5982\uff0c\u6211\u4eec\u60f3\u901a\u8fc7\u5206\u6790\u4e2d\u56fd\u51e0\u5343\u5e74\u7684\u6587\u8a00\u6587\uff0c\u53bb\u4e86\u89e3\u53e4\u4eba\u4e2a\u4f53\u4e3b\u4e49\u548c\u96c6\u4f53\u4e3b\u4e49\u7684\u5fc3\u7406\u53d8\u5316\u7279\u5f81\u3002\u4ece\u7ed3\u679c\u6765\u770b\uff0c\u5148\u79e6\u65f6\u671f\u7684\u4e2a\u4eba\u4e3b\u4e49\u6bd4\u8f83\u5f3a\u5927\uff0c\u4f46\u968f\u7740\u671d\u4ee3\u7684\u53d8\u8fc1\u53d7\u5230\u4e86\u4e0d\u65ad\u589e\u5f3a\u7684\u538b\u5236\u3002\u7531\u6b64\uff0c\u6211\u4eec\u4e5f\u8bd5\u56fe\u89e3\u91ca\u201c\u674e\u7ea6\u745f\u4e4b\u95ee\u201d\uff1a\u4e3a\u4ec0\u4e48\u4e2d\u56fd\u5728\u660e\u671d\u540e\u671f\u51fa\u73b0\u4e86\u8d44\u672c\u4e3b\u4e49\u840c\u82bd\uff0c\u5374\u6ca1\u6709\u51fa\u73b0\u8d44\u672c\u4e3b\u4e49\u53d1\u5c55\uff1f\u6211\u4eec\u89c9\u5f97\u53ef\u80fd\u4e0e\u4e2a\u4f53\u53d7\u5230\u957f\u671f\u7684\u538b\u5236\u6709\u5173\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u4eec\u4e5f\u505a\u4e00\u4e9b\u7ebf\u4e0b\u7cbe\u795e\u75be\u75c5\u7684\u8bca\u65ad\u3002\u7531\u4e8e\u4ee5\u533b\u751f\u7684\u4e3b\u89c2\u8bca\u65ad\u4e3a\u4e3b\uff0c\u6211\u4eec\u6709\u65f6\u4e5f\u80fd\u770b\u5230\u62a5\u9053\u4e0a\u8bf4\u201c\u88ab\u7cbe\u795e\u75be\u75c5\u201d\u3002\u90a3\u4e48\uff0c\u6709\u6ca1\u6709\u53ef\u80fd\u5229\u7528\u6280\u672f\u7684\u624b\u6bb5\u8f85\u52a9\u533b\u751f\u505a\u51fa\u66f4\u79d1\u5b66\u7684\u8bca\u65ad\uff1f\u5982\u679c\u6211\u4eec\u73b0\u5728\u95ee\u5927\u5bb6\uff1a\u4f60\u6709\u6ca1\u6709\u7cbe\u795e\u75c5\uff1f\u4f60\u80af\u5b9a\u56de\u7b54\uff1a\u6211\u6ca1\u6709\u7cbe\u795e\u75c5\uff0c\u6211\u5f88\u6b63\u5e38\u3002\u5fc3\u7406\u5b66\u91cc\u9762\u6709\u4e00\u4e2a\u6982\u5ff5\uff0c\u201c\u793e\u4f1a\u8d5e\u8bb8\u6027\u201d\u2014\u2014\u6211\u4eec\u6bcf\u4e2a\u4eba\u90fd\u60f3\u628a\u81ea\u5df1\u597d\u7684\u4e00\u9762\u8868\u73b0\u51fa\u6765\u3002\u56e0\u6b64\uff0c\u56de\u7b54\u4e5f\u53ef\u80fd\u662f\u5047\u7684\uff0c\u4e0d\u8fc7\u4e00\u4e9b\u8bed\u97f3\u3001\u9762\u90e8\u548c\u6b65\u6001\u7684\u884c\u4e3a\u6307\u6807\uff08\u8fd8\u5305\u62ec\u5404\u79cd\u5404\u6837\u7684\u884c\u4e3a\uff09\u5f88\u96be\u63a9\u9970\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u60f3\u5229\u7528\u4eba\u5de5\u667a\u80fd\u7684\u529e\u6cd5\u5bf9\u4eba\u7684\u884c\u4e3a\u8fdb\u884c\u5206\u6790\uff0c\u4ee5\u6b64\u4e86\u89e3\u4ed6\u7684\u7cbe\u795e\u548c\u5fc3\u7406\u72b6\u6001\uff0c\u4ece\u800c\u4e3a\u7cbe\u795e\u75be\u75c5\u7684\u8bca\u65ad\u63d0\u4f9b\u8f85\u52a9\u3002\u4eba\u5de5\u667a\u80fd\u786e\u5b9e\u53ef\u4ee5\u5e2e\u6211\u4eec\u505a\u5f88\u591a\u4e8b\u60c5\uff0c\u6211\u4eec\u56fd\u5bb6\u4e5f\u5728\u5927\u529b\u63a8\u8fdb\u4eba\u5de5\u667a\u80fd\u53d1\u5c55\uff0c\u4f46\u5b83\u4e0d\u662f\u4e07\u80fd\u7684\u3002\u6bd4\u5982\uff0c\u4e0a\u9762\u4e24\u53e5\u8bdd\u770b\u4f3c\u6ca1\u95ee\u9898\uff0c\u4f46\u5176\u5b9e\u5305\u542b\u4e86\u81ea\u6740\u610f\u5ff5\u7684\u8868\u8fbe\u3002\u201c\u8d70\u996d\u201d\u662f\u57282012\u5e743\u6708\u81ea\u6740\u7684\u4e00\u4e2a\u5357\u4eac\u5973\u5927\u5b66\u751f\uff0c\u201c\u996d\u996d\u201d\u662f\u5bf9\u5979\u7684\u6635\u79f0\u3002\u5728\u5979\u8d70\u540e\uff0c\u5979\u7684\u6700\u540e\u4e00\u6761\u5fae\u535a\u6210\u4e86\u6811\u6d1e\uff0c\u6709\u5f88\u591a\u4eba\u4e0b\u9762\u7559\u8a00\u3002\u6839\u636e2017\u5e742\u6708\u300a\u51b0\u70b9\u5468\u520a\u300b\u7684\u7edf\u8ba1\uff0c\u5f53\u65f6\u670958\u4e07\u6761\u7559\u8a00\uff1b\u73b0\u5728\u5df2\u7ecf\u8d85\u8fc7100\u4e07\u6761\u4e86\uff0c\u5e76\u4e14\u6bcf\u6708\u589e\u52a0\u7684\u901f\u5ea6\u975e\u5e38\u5feb\uff0c\u5927\u6982\u67096000\u591a\u6761\u30022012\u5e74\u52302013\u5e74\u671f\u95f4\uff0c\u7f51\u4e0a\u6709\u7279\u522b\u591a\u7684\u81ea\u6740\u6848\u4f8b\u62a5\u9053\u3002\u7531\u6b64\u6211\u4eec\u5c31\u60f3\uff0c\u6709\u6ca1\u6709\u53ef\u80fd\u5bf9\u4eba\u7684\u81ea\u6740\u610f\u5ff5\u6216\u8005\u53ef\u80fd\u6027\u505a\u51fa\u8bc6\u522b\uff1f\u8fd9\u6837\u6216\u8bb8\u53ef\u4ee5\u4e3a\u81ea\u6740\u5e72\u9884\u548c\u9884\u9632\u63d0\u4f9b\u5e2e\u52a9\u3002\u6211\u4eec\u4e00\u822c\u4f1a\u8ba4\u4e3a\uff0c\u81ea\u6740\u662f\u4e2a\u4eba\u9009\u62e9\uff0c\u5e72\u9884\u6ca1\u6709\u5fc5\u8981\u3002\u4f46\u662f\u4ece\u7814\u7a76\u4e2d\u6765\u770b\uff0c\u5176\u5b9e\u5c06\u8fd180%\u7684\u4eba\u5e76\u975e\u89c9\u5f97\u6ca1\u6709\u5fc5\u8981\uff0c\u800c\u662f\u4e0d\u77e5\u9053\u5411\u54ea\u6c42\u52a9\u6216\u8005\u5982\u4f55\u6c42\u52a9\u2014\u2014\u4ed6\u9047\u5230\u95ee\u9898\u4f46\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u89e3\u51b3\uff0c\u53ef\u80fd\u81ea\u6740\u662f\u552f\u4e00\u7684\u9009\u62e9\u3002\u867d\u7136\u76ee\u524d\u793e\u4f1a\u4e0a\u6709\u5f88\u591a\u81ea\u6740\u5e72\u9884\u548c\u6551\u52a9\u70ed\u7ebf\uff0c\u4f46\u5728\u67d0\u4e9b\u60c5\u51b5\u4e0b\uff0c\u6c42\u52a9\u4eba\u662f\u4e00\u8fb9\u5272\u8155\u4e00\u8fb9\u6253\u7535\u8bdd\uff0c\u4e3a\u65f6\u5df2\u665a\u3002\u90a3\u4e48\uff0c\u6709\u6ca1\u6709\u53ef\u80fd\u628a\u5e72\u9884\u5c3d\u91cf\u524d\u79fb\uff0c\u5728\u4ed6\u521a\u5f00\u59cb\u6709\u81ea\u6740\u610f\u5ff5\u6216\u60f3\u6cd5\u7684\u65f6\u5019\u5c31\u63d0\u4f9b\u53ca\u65f6\u4e13\u4e1a\u7684\u6307\u5bfc\uff0c\u5e2e\u4ed6\u89e3\u51b3\u95ee\u9898\uff0c\u4e0d\u8981\u7b49\u4e8b\u60c5\u4e0d\u53ef\u633d\u56de\u7684\u65f6\u5019\u518d\u53bb\u60f3\u529e\u6cd5\u8865\u6551\u3002\u56e0\u6b64\uff0c\u5c31\u9700\u8981\u6211\u4eec\u53ca\u65e9\u4e3b\u52a8\u5730\u53bb\u53d1\u73b0\u90a3\u4e9b\u81ea\u6740\u610f\u5ff5\u8005\uff0c\u800c\u4e0d\u662f\u88ab\u52a8\u7b49\u5f85\u4ed6\u4eec\u6253\u6765\u7535\u8bdd\u3002\u6211\u4eec\u4ee5\u524d\u5c1d\u8bd5\u8fc7\u4eba\u5de5\u6807\u6ce8\uff0c\u7ed3\u679c\u53d1\u73b0\u5de5\u4f5c\u91cf\u7279\u522b\u5927\uff0c\u592a\u591a\u7684\u8d1f\u5411\u4fe1\u606f\u5bf9\u5de5\u4f5c\u4eba\u5458\u5f71\u54cd\u4e5f\u975e\u5e38\u5927\u3002\u6211\u4eec\u7edf\u8ba1\u4e86\u4e00\u4e0b\uff0c\u4e24\u4e09\u5343\u6761\u6570\u636e\u4eba\u5de5\u6807\u6ce8\u5927\u7ea6\u9700\u8981\u4e00\u5468\u751a\u81f3\u4e24\u5468\uff0c\u53ef\u80fd\u5c31\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\u3002\u6211\u60f3\u5229\u7528\u4eba\u5de5\u667a\u80fd\u6280\u672f\u5bf9\u7f51\u7edc\u4e0a\u7684\u4fe1\u606f\u505a\u53ca\u65f6\u81ea\u52a8\u7684\u8bc6\u522b\uff0c\u8ba9\u6211\u4eec\u77e5\u9053\u67d0\u4e2a\u4eba\u662f\u5426\u5177\u6709\u81ea\u6740\u610f\u5ff5\u3002\u4e0d\u8fc7\u5176\u4e2d\u4e5f\u6709\u4e2a\u7591\u95ee\uff1a\u4eba\u5de5\u667a\u80fd\u5230\u5e95\u80fd\u4e0d\u80fd\u505a\u5230\uff1f\u4e3e\u4e2a\u4f8b\u5b50\uff0c\u8fd9\u662f\u4e24\u4e2a\u4eba\u901b\u5546\u573a\u7684\u884c\u4e3a\u8f68\u8ff9\uff0c\u5927\u5bb6\u89c9\u5f97\u54ea\u4e2a\u662f\u5973\u6027\uff1f\u663e\u7136\u662f\u53f3\u8fb9\u3002\u5927\u5bb6\u867d\u7136\u6ca1\u6709\u770b\u5230\u7167\u7247\uff0c\u4e5f\u6ca1\u6709\u770b\u5230\u8fd9\u4e24\u4e2a\u4eba\u7684\u8eab\u4efd\u8bc1\u53f7\uff0c\u4f46\u5374\u80fd\u591f\u6839\u636e\u884c\u4e3a\u6307\u6807\u5224\u65ad\u51fa\u6027\u522b\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5982\u679c\u628a\u8fd9\u4e9b\u5546\u94fa\u7684\u540d\u5b57\u5217\u51fa\u6765\uff0c\u8fd8\u80fd\u5927\u81f4\u77e5\u9053\u4ed6\u4eec\u7684\u6536\u5165\u60c5\u51b5\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u6839\u636e\u4e00\u4e2a\u4eba\u7684\u884c\u4e3a\u6307\u6807\uff0c\u5b8c\u5168\u53ef\u4ee5\u5bf9\u4ed6\u7684\u67d0\u4e9b\u4e2a\u6027\u7279\u70b9\u505a\u51fa\u81ea\u52a8\u5224\u65ad\uff0c\u5728\u4eba\u5de5\u667a\u80fd\u91cc\u8fb9\u7684\u65b9\u6cd5\u5c31\u662f\u673a\u5668\u5b66\u4e60\uff08Machine Learning\uff09\u3002\u4e3a\u4e86\u5bf9\u81ea\u6740\u610f\u5ff5\u505a\u51fa\u8bc6\u522b\uff0c\u6211\u4eec\u6839\u636e\u4e0d\u540c\u7d27\u6025\u7a0b\u5ea6\u505a\u4e86\u4e00\u4e9b\u6807\u6ce8\uff0c\u5206\u4e3a\u6709\u610f\u5ff5\u3001\u6709\u8ba1\u5212\u3001\u6709\u5b9e\u65bd\u4e09\u7c7b\u3002\u6211\u4eec\u5e0c\u671b\u901a\u8fc7\u4eba\u5de5\u667a\u80fd\u7684\u529e\u6cd5\uff0c\u901a\u8fc7\u5bf9\u8fd9\u4e9b\u77ed\u6587\u672c\u7684\u5206\u6790\uff08\u4ee5\u524d\u7684\u81ea\u6740\u8bc6\u522b\u57fa\u672c\u4e0a\u662f\u57fa\u4e8e\u7bc7\u5e45\u6bd4\u8f83\u957f\u7684\u9057\u4e66\u548c\u9057\u8a00\uff09\uff0c\u53bb\u4e86\u89e3\u80cc\u540e\u6709\u6ca1\u6709\u81ea\u6740\u7684\u610f\u5ff5\u3002\u6211\u4eec\u901a\u8fc7\u6a21\u578b\u7684\u4f18\u5316\u4ee5\u53ca\u81ea\u7136\u8bed\u8a00\u5904\u7406\u6280\u672f\u548c\u4eba\u5de5\u667a\u80fd\u6280\u672f\u7684\u5b66\u4e60\uff0c\u8bc6\u522b\u51c6\u786e\u7387\u6700\u540e\u80fd\u8fbe\u523084%\u3002\u73b0\u5728\u6211\u4eec\u6b63\u5728\u8ddf\u767e\u5ea6\u5408\u4f5c\uff0c\u5e0c\u671b\u80fd\u591f\u7528\u66f4\u590d\u6742\u7684\u4eba\u5de5\u667a\u80fd\u6280\u672f\u63d0\u9ad8\u9884\u6d4b\u7cbe\u5ea6\uff0c\u8282\u7ea6\u4eba\u5de5\u7684\u65f6\u95f4\u3002\u5230\u6b64\u4e3a\u6b62\uff0c\u5176\u5b9e\u4ece\u505a\u7814\u7a76\u7684\u89d2\u5ea6\u6765\u8bb2\u5c31\u5dee\u4e0d\u591a\u4e86\uff0c\u5199\u6587\u7ae0\u4e5f\u8db3\u591f\u4e86\u3002\u4f46\u662f\u6211\u4eec\u60f3\u518d\u5f80\u524d\u63a8\u8fdb\u4e00\u6b65\uff0c\u8ddf\u81ea\u6740\u610f\u5ff5\u8005\u8054\u7cfb\u4ea4\u6d41\u3002\u6211\u4eec\u5f53\u65f6\u5c31\u9047\u5230\u4e00\u4e2a\u95ee\u9898\uff1a\u600e\u4e48\u8ddf\u4ed6\u4eec\u6c9f\u901a\uff1f\u4e00\u79cd\u529e\u6cd5\u5c31\u662f\u5e7f\u800c\u544a\u4e4b\uff0c\u5411\u4ed6\u7684\u4eb2\u670b\u597d\u53cb\u53d1\u4fe1\u606f\uff0c\u8ba9\u5927\u5bb6\u8d76\u7d27\u5173\u6ce8\u4ed6\u3002\u82f1\u56fd\u5b66\u8005\u5728\u65e9\u671f\u505a\u8fc7\u8fd9\u6837\u7684\u5de5\u4f5c\uff0c\u7ed3\u679c\u56e0\u4e3a\u4fb5\u72af\u9690\u79c1\u5f88\u5feb\u88ab\u505c\u6389\u3002\u6211\u4eec\u5f53\u65f6\u89c9\u5f97\uff0c\u8fd9\u79cd\u65b9\u5f0f\u53ef\u80fd\u4f1a\u5f15\u53d1\u5176\u4ed6\u4eba\u5bf9\u4ed6\u7684\u504f\u89c1\uff0c\u4f1a\u5b8c\u5168\u78e8\u706d\u4ed6\u4ee5\u540e\u4e3b\u52a8\u6c42\u52a9\u7684\u53ef\u80fd\u6027\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u4eec\u4e5f\u89c9\u5f97\u8981\u4fdd\u62a4\u4ed6\u7684\u4e2a\u4eba\u5c0a\u4e25\u3002\u6211\u4eec\u4e4b\u524d\u63a5\u89e6\u8fc7\u4e00\u4e2a\u6848\u4f8b\uff0c\u4e00\u4e2a\u5c0f\u5973\u5b69\u8df3\u697c\u81ea\u6740\uff0c\u867d\u7136\u6700\u540e\u88ab\u6551\u4e0b\u6765\u4e86\uff0c\u4f46\u540e\u6765\u5979\u8bf4\uff0c\u5982\u679c\u4ee5\u540e\u518d\u8df3\u697c\uff0c\u4f1a\u627e\u4e00\u4e2a\u5176\u4ed6\u4eba\u7edd\u5bf9\u627e\u4e0d\u5230\u7684\u5730\u65b9\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5979\u518d\u6b21\u5b9e\u65bd\u81ea\u6740\u7684\u65b9\u5f0f\u53ef\u80fd\u7279\u522b\u51b3\u7edd\u3002\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u7528\u66f4\u597d\u7684\u529e\u6cd5\uff0c\u5728\u4fdd\u62a4\u4ed6\u4eec\u7684\u9690\u79c1\u548c\u5c0a\u4e25\u7684\u60c5\u51b5\u4e0b\u5f00\u5c55\u4ea4\u6d41\u3002\u6700\u540e\uff0c\u6211\u4eec\u9009\u62e9\u4e86\u5fae\u535a\u79c1\u4fe1\u7684\u65b9\u5f0f\u3002\u79c1\u4fe1\u662f\u4e00\u5bf9\u4e00\u7684\u90ae\u4ef6\u7cfb\u7edf\uff0c\u5728\u7cfb\u7edf\u524d\u53f0\u6839\u672c\u770b\u4e0d\u5230\u4ea4\u4e92\uff0c\u6211\u4eec\u7684\u7cfb\u7edf\u66fe\u7ecf\u8fd8\u5dee\u70b9\u56e0\u4e3a\u65e5\u6d3b\u91cf\u592a\u5c11\u88ab\u5173\u95ed\u2014\u2014\u5176\u5b9e\uff0c\u8fd9\u4e24\u5e74\u591a\u6211\u4eec\u540e\u53f0\u670916\u4e07\u7684\u4ea4\u4e92\u3002\u6211\u4eec\u7ecf\u5e38\u5728\u7f51\u4e0a\u63a5\u6536\u5230\u5783\u573e\u6d88\u606f\uff0c\u4e00\u822c\u770b\u90fd\u4e0d\u770b\u5c31\u5220\u6389\u4e86\u3002\u90a3\u4e48\uff0c\u5982\u4f55\u80fd\u4fdd\u8bc1\u8fd9\u4e9b\u4eba\u4f1a\u70b9\u5f00\u770b\uff1f2016\u5e74\u76843\u6708\u52309\u6708\uff0c\u6211\u4eec\u7ed9\u770b\u5230\u53d1\u8868\u8fc7\u6709\u81ea\u6740\u610f\u56fe\u5fae\u535a\u7684\u7528\u6237\u53d1\u4e86\u7528\u6237\u5b9e\u9a8c\u7684\u9080\u8bf7\u4fe1\uff0c\u60f3\u4e86\u89e3\u6709\u81ea\u6740\u610f\u5ff5\u7684\u4eba\u9700\u8981\u770b\u5230\u4ec0\u4e48\u6837\u7684\u4fe1\u606f\u3002\u5176\u5b9e\uff0c\u9080\u8bf7\u4fe1\u7684\u5185\u5bb9\u4e5f\u662f\u5e72\u9884\u5185\u5bb9\uff0c\u8fd9\u5f20\u622a\u56fe\u5c31\u662f\u6211\u4eec\u7b2c\u4e00\u6b21\u53d1\u51fa\u6765\u7684\u4fe1\u606f\u3002\u53d1\u51fa\u53bb\u540e\uff0c\u6211\u4eec\u5fc3\u91cc\u975e\u5e38\u5fd0\u5fd1\uff0c\u56e0\u4e3a\u73b0\u5728\u7f51\u4e0a\u6709\u5f88\u591a\u952e\u76d8\u4fa0\uff0c\u4e0d\u7ba1\u4f60\u505a\u4ec0\u4e48\u603b\u6709\u4eba\u9a82\uff1b\u800c\u8fd9\u4e2a\u53c8\u6d89\u53ca\u9690\u79c1\uff0c\u7279\u522b\u654f\u611f\u3002\u6211\u4eec\u975e\u5e38\u62c5\u5fc3\u4f1a\u88ab\u63a8\u4e0a\u70ed\u641c\uff0c\u5f04\u6210\u4e00\u4e2a\u8206\u60c5\u4e8b\u4ef6\uff0c\u79d1\u5b66\u9662\u518d\u95ee\u8d23\uff0c\u6240\u91cc\u8fb9\u627e\u6211\u9ebb\u70e6\uff0c\u6700\u540e\u5c31\u505a\u4e0d\u4e86\u4e86\u3002\u6211\u4eec\u7ed94222\u4e2a\u7528\u6237\u53d1\u51fa\u4fe1\u606f\uff0c\u5e76\u4e14\u6536\u5230\u4e86\u4e00\u4e9b\u56de\u590d\u3002\u770b\u5230\u56de\u590d\u5185\u5bb9\u540e\uff0c\u6211\u4eec\u677e\u4e86\u4e00\u53e3\u6c14\uff1a\u5f88\u591a\u4eba\u7ed9\u6211\u4eec\u53d1\u6765\u611f\u8c22\uff0c\u53ea\u6709\u4e2a\u4f4d\u6570\u7684\u56de\u590d\u7279\u522b\u8d1f\u9762\uff0c\u4e0d\u8fc7\u8fd9\u5f88\u6b63\u5e38\u3002\u4f46\u662f\u6709\u4e00\u4f4d\u5fae\u535a\u7528\u6237\uff0c\u4ed6\u7684\u4eb2\u4eba\u7ed9\u6211\u56de\u590d\u8bf4\u4ed6\u5df2\u7ecf\u81ea\u6740\u6b7b\u4ea1\u4e86\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u534a\u5e74\u4e4b\u5185\u8fd9\u4e2a\u4eba\u6709\u8fc7\u81ea\u6740\u8868\u8fbe\uff0c\u6700\u540e\u771f\u7684\u81ea\u6740\u6b7b\u4ea1\u4e86\uff0c\u5f53\u65f6\u7ed9\u6211\u4eec\u7684\u9707\u64bc\u5f88\u5927\u2014\u2014\u867d\u7136\u6211\u4eec\u53d1\u4e86\u79c1\u4fe1\u548c\u8f6c\u4ecb\u4fe1\u606f\uff0c\u5c3d\u5230\u4e86\u8d23\u4efb\uff0c\u4f46\u662f\u5e76\u6ca1\u6709\u8fbe\u5230\u4e00\u4e2a\u597d\u7684\u6548\u679c\u3002\u6211\u4eec\u5c31\u60f3\uff0c\u6709\u6ca1\u6709\u53ef\u80fd\u66f4\u8fdb\u4e00\u6b65\u5730\u76f4\u63a5\u63d0\u4f9b\u5e72\u9884\u4fe1\u606f\uff0c\u771f\u6b63\u5730\u5e2e\u52a9\u4ed6\u4eec\u3002\u9664\u4e86\u70ed\u7ebf\u8f6c\u63a5\u4e4b\u5916\uff0c\u6211\u4eec\u8fd8\u589e\u52a0\u4e86\u5728\u7ebf\u7684\u5fc3\u7406\u6d4b\u91cf\u548c\u81ea\u52a9\u5e72\u9884\u9879\u76ee\u3002\u5fc3\u7406\u6d4b\u91cf\u4e0d\u540c\u4e8e\u7f51\u4e0a\u7684\u5a31\u4e50\u6d4b\u8bd5\uff0c\u662f\u901a\u8fc7\u4e13\u4e1a\u7684\u624b\u6bb5\u8ba9\u4ed6\u4eec\u77e5\u9053\u81ea\u5df1\u5f53\u524d\u7684\u60c5\u51b5\u7a76\u7adf\u5982\u4f55\u3002\u800c\u81ea\u4e3b\u5e72\u9884\u662f\u56e0\u4e3a\u6211\u4eec\u53d1\u73b0\u5f88\u591a\u4eba\u4e0d\u613f\u610f\u8ddf\u4eba\u8bb2\u8bdd\uff0c\u800c\u662f\u613f\u610f\u8ddf\u673a\u5668\u4ea4\u6d41\u3002\u540c\u65f6\uff0c\u6211\u4eec\u4e5f\u62db\u52df\u5fd7\u613f\u8005\uff08\u6211\u4eec\u5e0c\u671b\u4ed6\u662f\u4e09\u7ea7\u4ee5\u4e0a\u54a8\u8be2\u5e08\uff0c\u56e0\u4e3a\u662f\u975e\u5e38\u4e13\u4e1a\u7684\u4ea4\u6d41\uff0c\u6709\u65f6\u5019\u53ef\u80fd\u4f1a\u5f71\u54cd\u5230\u4e2a\u4eba\uff09\uff0c\u7279\u522b\u8981\u6c42\u4ed6\u4eec\u7528\u7edf\u4e00\u7684\u8d26\u53f7\u8fdb\u884c\u4ea4\u6d41\uff0c\u5728\u4fdd\u62a4\u5bf9\u65b9\u540c\u65f6\u4e5f\u4fdd\u62a4\u81ea\u5df1\u3002\u6240\u4ee5\u6574\u4e2a\u6d41\u7a0b\u5c31\u662f\uff1a\u7528\u8ba1\u7b97\u673a\u7684\u529e\u6cd5\u53bb\u6293\u53d6\u5230\u6570\u636e\uff0c\u7136\u540e\u7528\u8bad\u7ec3\u597d\u7684\u6a21\u578b\u8bc6\u522b\u51fa\u6709\u81ea\u6740\u610f\u5ff5\u7684\u7528\u6237\uff0c\u5e76\u4e14\u4e5f\u8fdb\u884c\u4eba\u5de5\u7684\u6838\u67e5\u3002\u5f53\u65f6\u505a\u7528\u6237\u5b9e\u9a8c\u65f6\uff0c\u6709\u4eba\u7acb\u9a6c\u56de\u590d\u79c1\u4fe1\uff0c\u4ed6\u8bf4\u89c9\u5f97\u81ea\u5df1\u5f88\u6b63\u5e38\uff0c\u4ece\u6765\u6ca1\u6709\u8bf4\u8fc7\u6709\u81ea\u6740\u610f\u5ff5\u7684\u8bdd\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4f1a\u6536\u5230\u8fd9\u6837\u7684\u4fe1\u606f\uff1f\u540e\u6765\u6211\u4eec\u627e\u4e86\u4e00\u4e0b\uff0c\u53d1\u73b0\u8fd9\u4e2a\u4eba\u662f\u5728\u67d0\u4e2a\u5730\u65b9\u8bf4\u8fc7\uff0c\u4f46\u662f\u4ed6\u81ea\u5df1\u7684\u5fae\u535a\u4e0a\u6ca1\u6709\uff0c\u4e8e\u662f\u6211\u4eec\u5c31\u628a\u90a3\u53e5\u8bdd\u7c98\u8d34\u8fc7\u53bb\u56de\u590d\u4ed6\u3002\u8fd9\u4ef6\u4e8b\u60c5\u8ba9\u6211\u4eec\u5f88\u8b66\u89c9\uff0c\u56e0\u4e3a\u5982\u679c\u5168\u7528\u8ba1\u7b97\u673a\u4f1a\u6709\u8bef\u5dee\uff0c\u800c\u8bef\u5dee\u4e00\u65e6\u51fa\u6765\uff0c\u4e92\u4fe1\u5c31\u5f7b\u5e95\u88ab\u6253\u7834\u4e86\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u7684\u6bcf\u4e00\u6761\u79c1\u4fe1\u5fc5\u987b\u4fdd\u8bc1\u662f\u53d1\u7ed9\u4e86\u786e\u786e\u5b9e\u5b9e\u6709\u81ea\u6740\u610f\u5ff5\u7684\u4eba\u3002\u901a\u8fc7\u79c1\u4fe1\u7684\u53d1\u9001\u548c\u4ea4\u4e92\uff0c\u6211\u4eec\u80fd\u591f\u63d0\u4f9b\u53ca\u65f6\u7684\u5e72\u9884\u4fe1\u606f\u3002\u5fd7\u613f\u8005\u4f1a\u505a\u4e00\u4e9b\u7b80\u5355\u7684\u8bc4\u4f30\uff0c\u6700\u540e\u80fd\u770b\u5230\u4ed6\u5fc3\u60c5\u7684\u53d8\u5316\uff0c\u964d\u4f4e\u81ea\u6740\u7684\u53ef\u80fd\u6027\u3002\u79c1\u4fe1\u7a76\u7adf\u8be5\u53d1\u51e0\u6b21\uff1f\u4e0d\u80fd\u6bcf\u6b21\u90fd\u53d1\uff0c\u5426\u5219\u5c31\u662f\u9a9a\u6270\uff1b\u4ee5\u524d\u4e5f\u4ece\u6765\u6ca1\u6709\u53ef\u4f9b\u501f\u9274\u7684\u7814\u7a76\u6210\u679c\u3002\u6211\u4eec\u901a\u5e38\u8bb2\u4e8b\u4e0d\u8fc7\u4e09\uff0c\u4f46\u662f\u53c8\u89c9\u5f97\u53ef\u80fd\u5f88\u591a\u4eba\u4e0d\u770b\uff0c\u6240\u4ee5\u540e\u6765\u5b9a\u4e86\u4e94\u6b21\u3002\u4ece\u7ed3\u679c\u6765\u770b\uff0c\u867d\u7136\u6709\u4eba\u4ece\u6765\u4e0d\u56de\u590d\u79c1\u4fe1\uff0c\u4e5f\u6ca1\u6709\u8ddf\u5fd7\u613f\u8005\u4ea4\u6d41\u8fc7\uff0c\u4f46\u662f\u8fd9\u51e0\u6b21\u7684\u53d1\u9001\u4f7f\u5f97\u4ed6\u89c9\u5f97\u6709\u4eba\u5728\u5173\u6ce8\u4ed6\uff0c\u80fd\u591f\u611f\u53d7\u5230\u6765\u81ea\u793e\u4f1a\u7684\u652f\u6301\u2014\u2014\u8fd9\u6709\u53ef\u80fd\u662f\u4ed6\u514b\u670d\u56f0\u96be\u7684\u6700\u5927\u52a8\u529b\u3002\u4e24\u5e74\u5185\uff0c\u6211\u4eec\u4e00\u76f4\u5728\u63a8\u8fdb\u8fd9\u9879\u5de5\u4f5c\u3002\u6211\u4eec\u7279\u522b\u7edf\u8ba1\u4e86\u8ddf\u5fd7\u613f\u8005\u6709\u8fc7\u4ea4\u6d41\u7684\u4eba\uff0c\u4e00\u5171\u67093733\u540d\uff0c\u60f3\u4e86\u89e3\u4ed6\u4eec\u7684\u53cd\u9988\u600e\u4e48\u6837\u3002\u9996\u5148\uff0c\u4ed6\u4eec\u7684\u6001\u5ea6\u5927\u591a\u6bd4\u8f83\u79ef\u6781\uff0877%\uff09\uff0c\u6d88\u6781\u6001\u5ea6\u7684\u6bd4\u4f8b\u4e5f\u5728\u5408\u7406\u533a\u95f4\u5185\uff0810%\uff09\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u4eec\u8ddf\u5fd7\u613f\u8005\u4ea4\u6d41\u7684\u5185\u5bb9\uff0c\u4e3b\u8981\u662f\u60f3\u4e86\u89e3\u4e00\u4e0b\u6551\u52a9\u65b9\u6cd5\uff0c\u6216\u8005\u8bf4\u660e\u81ea\u8eab\u60c5\u51b5\u3002\u6211\u4eec\u89c9\u5f97\u8fd9\u79cd\u529e\u6cd5\u80fd\u591f\u6fc0\u6d3b\u4ed6\u4eec\u5411\u5916\u754c\u6c42\u52a9\u7684\u52a8\u673a\uff0c\u662f\u975e\u5e38\u91cd\u8981\u7684\u4e00\u6b65\u3002\u6709\u4e00\u70b9\uff0c\u6211\u4eec\u4e00\u76f4\u5f88\u5173\u6ce8\uff1a\u5230\u5e95\u662f\u54ea\u4e9b\u56e0\u7d20\u5bfc\u81f4\u4e86\u81ea\u6740\u7684\u53d1\u751f\uff1f\u56fd\u5916\u7684\u7814\u7a76\u8bf4\u81ea\u674090%\u662f\u957f\u671f\u7cbe\u795e\u75be\u75c5\u5bfc\u81f4\u7684\uff0c\u800c\u56fd\u5185\u4e00\u76f4\u90fd\u6709\u8d28\u7591\u548c\u4e89\u8bba\uff0c\u8ba4\u4e3a\u6ca1\u6709\u5230\u8fd9\u4e2a\u6570\u3002\u4ece\u6211\u4eec\u7684\u7ed3\u679c\u6765\u770b\uff0c\u5728\u6240\u6709\u6709\u81ea\u6740\u610f\u5ff5\u751a\u81f3\u4ed8\u8bf8\u5b9e\u65bd\u7684\u4eba\u7fa4\u91cc\uff0c\u63a5\u8fd1\u4e00\u534a\u7684\u4eba\u662f\u7cbe\u795e\u5065\u5eb7\u7684\uff0c\u4ed6\u4eec\u9762\u4e34\u7684\u6700\u4e3b\u8981\u7684\u95ee\u9898\u662f\u5bb6\u5ead\u95ee\u9898\uff0c\u8fd9\u662f\u975e\u5e38\u4ee4\u4eba\u60ca\u8bb6\u7684\u7ed3\u679c\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e9b\u56e0\u7d20\u4e5f\u4f1a\u5bfc\u81f4\u81ea\u6740\u7684\u53ef\u80fd\u6027\uff0c\u6bd4\u5982\u5de5\u4f5c\u3001\u5b66\u4e60\u3001\u5a5a\u604b\uff0c\u5305\u62ec\u53d7\u6b3a\u51cc\u3001\u751a\u81f3\u662f\u88ab\u6027\u4fb5\u7684\u7ecf\u5386\u3002\u6211\u4eec\u4e5f\u7814\u7a76\u4e86\u81ea\u6740\u610f\u5ff5\u8005\u5411\u8c01\u6c42\u52a9\u3002\u7ed3\u679c\u53d1\u73b0\uff0c\u6709\u4e00\u534a\u7684\u4eba\u4ece\u672a\u5bfb\u6c42\u8fc7\u5e2e\u52a9\u3002\u800c\u4e14\u5728\u6c42\u52a9\u4eba\u7fa4\u91cc\u8fb9\uff0c\u5bb6\u4eba\u4e0d\u4ec5\u4e0d\u662f\u7b2c\u4e00\u4f4d\uff0c\u800c\u4e14\u6bd4\u4f8b\u751a\u81f3\u4f4e\u4e8e\u964c\u751f\u7f51\u53cb\u3002\u5f88\u591a\u65f6\u5019\u6211\u4eec\u8ba4\u4e3a\u5bb6\u5ead\u662f\u4e00\u4e2a\u4eba\u6700\u4e3b\u8981\u4e5f\u6700\u91cd\u8981\u7684\u8d44\u6e90\uff0c\u4f46\u662f\u5728\u6700\u5371\u673a\u7684\u65f6\u5019\uff0c\u5bb6\u4eba\u4e0d\u662f\u7b2c\u4e00\u4f4d\u7684\uff0c\u8fd9\u662f\u9700\u8981\u6211\u4eec\u53cd\u601d\u7684\u4e00\u4e2a\u95ee\u9898\u3002\u6211\u4eec\u9700\u8981\u6709\u66f4\u597d\u7684\u5bb6\u5ead\u6559\u80b2\uff0c\u5305\u62ec\u751f\u6b7b\u6559\u80b2\u3002\u524d\u6bb5\u65f6\u95f4\u4e0a\u6d77\u7684\u521d\u4e2d\u751f\u8df3\u6865\u4e8b\u4ef6\uff0c\u53ef\u80fd\u5e76\u4e0d\u4ee3\u8868\u4ed6\u771f\u7684\u5c31\u60f3\u81ea\u6740\uff0c\u6709\u53ef\u80fd\u4ed6\u662f\u901a\u8fc7\u8fd9\u79cd\u884c\u4e3a\u53bb\u8868\u8fbe\u5bf9\u5bb6\u4eba\u7684\u4e0d\u6ee1\uff0c\u4f46\u4ed6\u6ca1\u6709\u60f3\u5230\u8df3\u6865\u90a3\u523b\u4e4b\u540e\u5c31\u65e0\u6cd5\u633d\u56de\u4e86\uff0c\u6211\u89c9\u5f97\u4ed6\u6ca1\u6709\u63a5\u53d7\u5f88\u597d\u7684\u751f\u6b7b\u6559\u80b2\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u9752\u5c11\u5e74\u548c\u8001\u5e74\u662f\u81ea\u6740\u7684\u91cd\u707e\u533a\u3002\u4e2d\u56fd\u519c\u675150\u5c81\u4ee5\u4e0a\u7684\u8001\u5e74\u4eba\u81ea\u6740\u6bd4\u4f8b\u975e\u5e38\u9ad8\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6ca1\u6709\u624b\u673a\uff0c\u4e5f\u4e0d\u4e0a\u7f51\uff0c\u4e27\u5931\u52b3\u52a8\u80fd\u529b\u540e\u5c31\u8ba4\u4e3a\u81ea\u5df1\u6210\u4e3a\u4e86\u5bb6\u5ead\u7684\u7d2f\u8d58\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u4eec\u4e5f\u770b\u5230\u4e2d\u56fd\u7684\u81ea\u6740\u60c5\u51b5\u8ddf\u56fd\u5916\u4e0d\u592a\u4e00\u6837\uff0c\u6709\u4e2d\u56fd\u7684\u7279\u70b9\uff0c\u6240\u4ee5\u4e5f\u9700\u8981\u7814\u7a76\u9002\u5408\u4e2d\u56fd\u4eba\u7fa4\u7684\u81ea\u6740\u5e72\u9884\u65b9\u6cd5\u3002\u53e6\u5916\uff0c\u6211\u4eec\u89c9\u5f97\u81ea\u6740\u5e76\u4e0d\u4ec5\u4ec5\u662f\u5fc3\u7406\u95ee\u9898\u5bfc\u81f4\u7684\uff0c\u5176\u5b9e\u5305\u542b\u5f88\u591a\u7684\u73b0\u5b9e\u95ee\u9898\uff0c\u6240\u4ee5\u5b83\u4e5f\u662f\u4e00\u4e2a\u793e\u4f1a\u5de5\u7a0b\uff0c\u9700\u8981\u6574\u4e2a\u793e\u4f1a\u4e00\u8d77\u52aa\u529b\u53bb\u89e3\u51b3\u81ea\u6740\u95ee\u9898\u3002\u867d\u7136\u6211\u662f\u5b66\u8ba1\u7b97\u673a\u7684\uff0c\u4f46\u6211\u4e5f\u8ba4\u4e3a\u4eba\u5de5\u667a\u80fd\u53ea\u662f\u4e2a\u6280\u672f\u3002\u5b83\u53ef\u4ee5\u5e2e\u52a9\u6211\u4eec\uff0c\u4f46\u5176\u672c\u8eab\u65e0\u6cd5\u89e3\u51b3\u81ea\u6740\u95ee\u9898\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u7f51\u53cb\u5bf9\u6211\u4eec\u5de5\u4f5c\u7684\u8bc4\u4ef7\uff0c\u6211\u89c9\u5f97\u7279\u522b\u8d34\u5207\u2014\u2014\u6211\u4eec\u5e0c\u671b\u80fd\u591f\u7528\u57fa\u7840\u7684\u529e\u6cd5\uff0c\u53bb\u628a\u4e13\u4e1a\u7684\u65b9\u6cd5\u548c\u624b\u6bb5\u6709\u6548\u5730\u4f20\u8fbe\u5230\u9700\u8981\u7684\u4eba\u7fa4\u4e2d\uff0c\u8ba9\u4ed6\u4eec\u7528\u66f4\u4e13\u4e1a\u7684\u65b9\u6cd5\u53bb\u5e94\u5bf9\u81ea\u5df1\u7684\u95ee\u9898\u3002\u4e0d\u7ba1\u6700\u540e\u7ed3\u679c\u600e\u4e48\u6837\uff0c\u6211\u4eec\u90fd\u5e0c\u671b\u4ed6\u4eec\u80fd\u591f\u5728\u81ea\u5df1\u7684\u4e16\u754c\u91cc\u8fb9\u5e78\u798f\u5b89\u5eb7\u3002\u8c22\u8c22\u5927\u5bb6\u3002\uff08\u7f16\u8f91\uff1a\u9ea6\u82bd\u6768\u3001\u51dd\u97f3\uff09", "title": "\u6211\u4eec\u6d4b\u8bd5\u4e864222\u6761\u79c1\u4fe1\uff0c\u6709\u4eba\u611f\u8c22\uff0c\u6709\u4eba\u56de\u590d\u201c\u5fae\u535a\u7528\u6237\u5df2\u7ecf\u81ea\u6740\u6b7b\u4ea1\u201d\uff5c\u6731\u5ef7\u52ad", "ukey_author": null, "video_content": ""}]

