let healths = [{"author": {"avatar": {"large": "https://3-im.guokr.com/ADzPM973jx-OHF1DSd4c5Xvbm-ErRYUQiMdx029hC9ygAAAAoAAAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/ADzPM973jx-OHF1DSd4c5Xvbm-ErRYUQiMdx029hC9ygAAAAoAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/ADzPM973jx-OHF1DSd4c5Xvbm-ErRYUQiMdx029hC9ygAAAAoAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "SILENTGHOST", "title": "\u679c\u58f3\u7231\u5ba0\u7ad9\u7f16\u8f91", "ukey": "s48nj7"}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/ADzPM973jx-OHF1DSd4c5Xvbm-ErRYUQiMdx029hC9ygAAAAoAAAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/ADzPM973jx-OHF1DSd4c5Xvbm-ErRYUQiMdx029hC9ygAAAAoAAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/ADzPM973jx-OHF1DSd4c5Xvbm-ErRYUQiMdx029hC9ygAAAAoAAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "nickname": "SILENTGHOST", "title": "\u679c\u58f3\u7231\u5ba0\u7ad9\u7f16\u8f91", "ukey": "s48nj7"}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [{"articles_count": 4299, "channel_type": "normal", "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "frontier", "name": "\u524d\u6cbf", "sort_score": 1}], "content": "<p><span style=\"font-size:14px\">\u51e0\u4e4e\u53ea\u8981\u7537\u4eba\u88ab\u786e\u8bca\u60a3\u6709\u7cd6\u5c3f\u75c5\uff0c\u4ed6\u4eec\u5c31\u4f1a\u542c\u5230\u53ef\u6015\u7684\u5173\u4e8eED\u7684\u8b66\u544a\u3002\u5176\u5b9e\uff0c\u6709\u65f6\u5019\u662f\u81ea\u6211\u5b9e\u73b0\u7684\u9884\u8a00\u7684\u4e00\u4e2a\u7ecf\u5178\u6848\u4f8b&mdash;&mdash;\u5bf9ED\u7684\u7126\u8651\u53ef\u80fd\u4f1a\u5bfc\u81f4ED\u3002\u5f53\u7136\uff0c\u4e8b\u5b9e\u4e0a\u7cd6\u5c3f\u75c5\u7684\u786e\u53ef\u5f71\u54cd\u52c3\u8d77\uff0c\u4f46\u8fd9\u5e76\u975e\u4e0d\u53ef\u907f\u514d\uff0c\u800c\u4e14\u5c31\u7b97\u771f\u7684\u53d1\u751f\u4e86\uff0c\u8fd8\u6709\u4e00\u4e9b\u5b89\u5168\u6709\u6548\u7684\u6cbb\u7597\u65b9\u6cd5\u53ef\u7528\u3002\u6709\u4e86\u6b63\u786e\u7684\u6cbb\u7597\uff0c\u7cd6\u5c3f\u75c5\u60a3\u8005\u4e5f\u53ef\u4ee5\u4eab\u53d7\u6ee1\u610f\u7684\u6027\u751f\u6d3b\u3002</span></p>\n\n<h2>\u7537\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u7684\u6027\u95ee\u9898</h2>\n\n<p>\u96ea\u57ce\u5927\u5b66\u7684\u7814\u7a76\u4eba\u5458\u56de\u987e\u4e86\u52c3\u8d77\u969c\u788d\u7684\u7537\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u768423\u9879\u7814\u7a76\u3002\u4ed6\u4eec\u7684\u7ed3\u8bba\u662f\uff1a\u5927\u7ea6\u670930\uff05\u7684\u7cd6\u5c3f\u75c5\u60a3\u8005\u60a3\u6709\u91cd\u5ea6ED\uff0c\u6bd4\u8d77\u6ca1\u60a3\u7cd6\u5c3f\u75c5\u7537\u6027\uff0c\u60a3\u7cd6\u5c3f\u75c5\u7684\u7537\u6027ED\u98ce\u9669\u662f\u524d\u8005\u7684\u4e24\u500d\uff0c\u4f46\u8fd9\u4e9b\u7814\u7a76\u7ed3\u679c\u540c\u65f6\u4e5f\u8868\u660e\uff0c\u60a3\u6709\u7cd6\u5c3f\u75c5\u7684\u7537\u6027\u7edd\u975e\u6ce8\u5b9a\u8981ED\u3002</p>\n\n<p>\u5f15\u8d77\u7537\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u52c3\u8d77\u969c\u788d\u7684\u539f\u56e0\u6709\u4e24\u4e2a\uff1a\u5fc3\u8840\u7ba1\u5e76\u53d1\u75c7\uff08\u52a8\u8109\u7ca5\u6837\u786c\u5316\uff09\u548c\u795e\u7ecf\u635f\u4f24\u3002\u7cd6\u5c3f\u75c5\u4f1a\u52a0\u901f\u52a8\u8109\u5835\u585e\uff0c\u4ece\u800c\u964d\u4f4e\u4e86\u9634\u830e\u7684\u8840\u6db2\u6d41\u5165\u5e76\u5f71\u54cd\u52c3\u8d77\u3002\u800c\u795e\u7ecf\u7cfb\u7edf\u635f\u5bb3\u5219\u5f71\u54cd\u4e86\u53c2\u4e0e\u7684\u52c3\u8d77\u8fc7\u7a0b\u7684\u795e\u7ecf\u3002</p>\n\n<p>\u603b\u7684\u6765\u8bf4\uff0c\u7cd6\u5c3f\u75c5\u5bfc\u81f4ED\u7684\u5371\u9669\u56e0\u7d20\u5305\u62ec\u4e0b\u8fb9\u8fd9\u4e9b\uff1a</p>\n\n<p>&bull;\u60a3\u7cd6\u5c3f\u75c5\u7684\u65f6\u95f4\u3002\u5fc3\u8840\u7ba1\u75be\u75c5\u548c\u795e\u7ecf\u75c5\u53d8\uff0c\u90fd\u4f1a\u968f\u7740\u65f6\u95f4\u7684\u63a8\u79fb\u53d1\u5c55\u3002\u60a3\u7cd6\u5c3f\u75c5\u65f6\u95f4\u8d8a\u957f\uff0c\u98ce\u9669\u8d8a\u5927\u3002\u5728\u5e74\u8f7b\u7684\u65f6\u5019\u5c31\u88ab\u8bca\u65ad\u60a3\u6709I\u578b\u7cd6\u5c3f\u75c5\u7537\u6027\uff0c\u6bd4\u5e74\u7eaa\u8f83\u5927\u7684\u88ab\u8bca\u65ad\u4e3aII\u578b\u7cd6\u5c3f\u75c5\u7684\u7537\u6027\u66f4\u5bb9\u6613\u53d1\u751fED\u3002</p>\n\n<p>&bull;\u7cd6\u5c3f\u75c5\u5e76\u53d1\u75c7\u3002\u7f79\u60a3\u7cd6\u5c3f\u75c5\u5e76\u53d1\u75c7\uff0c\u5c24\u5176\u662f\u5fc3\u8840\u7ba1\u75be\u75c5\u7684\u7537\u6027\u66f4\u5bb9\u6613\u60a3\u4e0aED\u3002</p>\n\n<p>&bull;\u5f71\u54cd\u8840\u538b\u7684\u836f\u7269\u7684\u4f7f\u7528\u3002\u8bb8\u591a\u9ad8\u8840\u538b\u836f\u7269\u7684\u4e00\u4e2a\u526f\u4f5c\u7528\u662f\u52c3\u8d77\u969c\u788d\u3002</p>\n\n<p>&bull;\u80a5\u80d6\u3002\u5b83\u4e0e\u5fc3\u8840\u7ba1\u75be\u75c5\u76f8\u5173\u3002</p>\n\n<p>&bull;\u5438\u70df\u3002\u8fd9\u4f1a\u52a0\u901f\u5fc3\u8840\u7ba1\u75be\u75c5\u7684\u53d1\u5c55\u3002</p>\n\n<p>\u5c31\u7b97\u56e0\u60a3\u7cd6\u5c3f\u75c5\u60a3\u4e0a\u4e86\u52c3\u8d77\u969c\u788d\uff0c\u4e5f\u4e0d\u7528\u7edd\u671b\u3002\u4eca\u5929\uff0c\u5df2\u7ecf\u6709\u4f17\u591a\u7684\u6cbb\u7597\u65b9\u6848\u53ef\u4ee5\u5e2e\u52a9\uff1a</p>\n\n<p>&bull;\u670d\u836f &nbsp;\u73b0\u5728\u80fd\u6539\u5584\u52c3\u8d77\u72b6\u51b5\u7684\u836f\u7269\u5df2\u7ecf\u6709\u5f88\u591a\u4e86\uff0c\u8fd9\u4e9b\u836f\u540c\u6837\u5bf9\u7cd6\u5c3f\u75c5\u60a3\u8005\u8d77\u4f5c\u7528\u3002\u4e0d\u8fc7\uff0c\u5f97\u8bb0\u5f97\u5411\u5927\u592b\u62a5\u544a\u4f60\u6b63\u5728\u5403\u7684\u836f\uff0c\u770b\u5b83\u4eec\u662f\u5426\u6709\u635f\u5bb3\u52c3\u8d77\u7684\u526f\u4f5c\u7528\uff0c\u800c\u4e14\u6709\u5fc5\u8981\u6d4b\u8bd5\u4e00\u4e0b\u6fc0\u7d20\u662f\u5426\u5931\u8861\u3002\u4f46\u662f\uff0c\u5982\u679c\u4f60\u6b63\u5728\u670d\u7528\u785d\u9178\u76d0\u7c7b\u836f\u7269\u6765\u6cbb\u7597\u5fc3\u7ede\u75db\uff0c\u90a3\u5c31\u5f97\u614e\u7528\u5e2e\u52a9\u52c3\u8d77\u7684\u836f\u7269\u3002\u800c\u4e14\uff0c\u5982\u679c\u52c3\u8d77\u969c\u788d\u662f\u7531\u4e8e\u795e\u7ecf\u635f\u4f24\uff0c\u90a3\u8fd9\u7c7b\u836f\u7269\u5c31\u65e0\u80fd\u4e3a\u529b\u4e86\u3002</p>\n\n<p>&bull;\u771f\u7a7a\u6536\u7f29\u88c5\u7f6e\u3002\u8fd9\u79cd\u5c0f\u5de5\u5177\u80fd\u5728\u9634\u830e\u5468\u56f4\u521b\u9020\u4e00\u4e2a\u5c40\u90e8\u771f\u7a7a\uff0c\u4f7f\u8840\u6db2\u6d41\u5411\u9634\u830e\u5e76\u4f7f\u9634\u830e\u4e34\u65f6\u52c3\u8d77\u3002\u8fd9\u7c7b\u88c5\u7f6e\u6709\u5f88\u591a\u578b\u53f7\uff0c\u4f46\u90fd\u5305\u62ec\u4e00\u4e2a\u5957\u5728\u9634\u830e\u4e0a\u7684\u5851\u6599\u7ba1\uff0c\u8fd8\u6709\u914d\u5957\u7684\u6324\u538b\u7403\u6cf5\u3002\u538b\u7f29\u7403\u6cf5\u4ece\u7ba1\u4e2d\u62bd\u7a7a\u7a7a\u6c14\uff0c\u4f7f\u8840\u6db2\u6d41\u5165\u9634\u830e\u3002\u4e00\u65e6\u7537\u6027\u6210\u529f\u52c3\u8d77\uff0c\u5c31\u53ef\u4ee5\u7528\u6a61\u80f6\u73af\u6ed1\u5230\u9634\u830e\u6839\u90e8\uff0c\u538b\u8feb\u9759\u8109\u6765\u7ef4\u6301\u52c3\u8d77\u3002\u6700\u8fd1\u7684\u4e00\u9879\u7814\u7a76\u8bc4\u4f30\u4e86\u771f\u7a7a\u6536\u7f29\u88c5\u7f6e\u7684\u6709\u6548\u6027\uff0c21\u4f4dED\u8005\u670917\u4eba\u90fd\u80fd\u901a\u8fc7\u8fd9\u79cd\u8bbe\u5907\u8fbe\u5230\u5b8c\u5168\u52c3\u8d77\u6216\u534a\u52c3\u8d77\u72b6\u6001\u6765\u5b8c\u6210\u6027\u4ea4\u3002\u5c3d\u7ba1\u5e02\u9762\u4e0a\u6709\u552e\uff0c\u4f46\u662f\u8fd8\u662f\u5efa\u8bae\u4f7f\u7528\u524d\u5148\u53bb\u54a8\u8be2\u5927\u592b\u4ee5\u4fbf\u9009\u62e9\u9002\u5408\u81ea\u5df1\u7684\u578b\u53f7\u3002</p>\n\n<p>&bull;\u624b\u672f\u3002\u5982\u679cED\u4e3b\u8981\u662f\u7531\u5fc3\u8840\u7ba1\u7cfb\u7edf\u7684\u5e76\u53d1\u75c7\u800c\u4e0d\u662f\u795e\u7ecf\u7cfb\u7edf\u5e76\u53d1\u75c7\u5f15\u8d77\u7684\uff0c\u9634\u830e\u8840\u7ba1\u52a8\u8109\u642d\u6865\u624b\u672f\uff0c\u53ef\u6539\u5584\u6d41\u52a8\u5230\u9634\u830e\u7684\u8840\u6d41\u72b6\u51b5\u3002\u8fd9\u7c7b\u4f3c\u4e8e\u5fc3\u810f\u642d\u6865\u624b\u672f\uff0c\u5916\u79d1\u533b\u751f\u4f1a\u4ece\u8eab\u4f53\u7684\u522b\u5904\u5207\u4e0b\u4e00\u6bb5\u52a8\u8109\uff0c\u7136\u540e\u7528\u5b83\u6765\u505a\u4e00\u4e2a\u652f\u8def\u7528\u6765\u7ed5\u8fc7\u5835\u585e\u7684\u90e8\u5206\u3002\u9634\u830e\u4e3b\u52a8\u8109\u91cd\u5efa\u548c\u80cc\u9759\u8109\u52a8\u8109\u5316\u4e5f\u589e\u52a0\u9634\u830e\u8840\u6d41\u91cf\u3002\u6709\u7814\u7a76\u8868\u660e\uff0c\u7ea6\u6709\u4e00\u534a\u7684\u7537\u6027\u4ece\u9634\u830e\u624b\u672f\u6062\u590d\u540e\uff0c\u6062\u590d\u4e86\u52c3\u8d77\u529f\u80fd\u3002</p>\n\n<h2>\u5973\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u7684\u6027\u95ee\u9898</h2>\n\n<p>\u7cd6\u5c3f\u75c5\u5bf9\u5973\u4eba\u6027\u65b9\u9762\u7684\u5f71\u54cd\u5f88\u5c11\u88ab\u63d0\u53ca\uff0c\u4e0d\u8fc7\u8fd9\u79cd\u5f71\u54cd\u5e76\u975e\u4e0d\u5b58\u5728\uff0c\u53ea\u662f\u76f8\u5bf9\u4e8e\u7537\u4eba\u6765\u8bf4\uff0c\u8fd9\u79cd\u5f71\u54cd\u66f4\u52a0\u5fae\u5999\u3002\u5b9e\u9645\u4e0a\u7cd6\u5c3f\u75c5\u5bf9\u5973\u60a3\u8005\u6027\u65b9\u9762\u7684\u5f71\u54cd\u662f\u503c\u5f97\u6ce8\u610f\u7684\u3002</p>\n\n<p>\u7cd6\u5c3f\u75c5\u5e26\u6765\u7684\u4e3b\u8981\u95ee\u9898\u662f\u9634\u9053\u5e72\u6da9\u3002\u8fd9\u662f\u56e0\u4e3a\uff0c\u9634\u9053\u751f\u4ea7\u5929\u7136\u7684\u6da6\u6ed1\u6db2\u9700\u4f9d\u8d56\u9634\u9053\u58c1\u5145\u5206\u7684\u8840\u6db2\u4f9b\u5e94\u3002\u6b63\u5982\u7cd6\u5c3f\u75c5\u76f8\u5173\u7684\u5fc3\u8840\u7ba1\u75be\u75c5\u4f1a\u51cf\u5c11\u6d41\u5165\u9634\u830e\u7684\u8840\u6db2\uff0c\u8fd9\u540c\u6837\u4e5f\u4f1a\u5f71\u54cd\u9634\u9053\u58c1\u7684\u8840\u6d41\u91cf\u3002</p>\n\n<p>\u5973\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u7684\u795e\u7ecf\u75c5\u53d8\uff0c\u4f1a\u4f7f\u9634\u8482\u53d7\u5230\u523a\u6fc0\u65f6\u53d8\u5f97\u8fdf\u949d\uff0c\u540c\u65f6\u53d7\u5230\u5f71\u54cd\u7684\u8fd8\u6709\u5bf9\u7231\u629a\u53d8\u5f97\u4e0d\u654f\u611f\u548c\u96be\u4ee5\u8fbe\u5230\u9ad8\u6f6e\u3002\u6da6\u6ed1\u6cb9\u80fd\u591f\u589e\u52a0\u5bf9\u89e6\u6478\u7684\u654f\u611f\uff0c\u4f1a\u6709\u70b9\u5e2e\u52a9\u3002\u800c\u5176\u4ed6\u8bf8\u5982\u632f\u52a8\u68d2\u4e4b\u7c7b\u60c5\u8da3\u7528\u54c1\uff0c\u80fd\u591f\u66f4\u597d\u5730\u523a\u6fc0\u9634\u8482\u4ee5\u6539\u5584\u6027\u751f\u6d3b\u8d28\u91cf\u3002</p>\n\n<p class=\"editors-note\">PS\uff1a\u4e0d\u80fd\u5403\u7cd6\u5df2\u7ecf\u591f\u4e0d\u5e78\u798f\u7684\u4e86\uff0c\u96ea\u4e0a\u52a0\u971c\u600e\u4e48\u5f97\u4e86\u554a~~~</p>\n\n<p><strong>\u672c\u6587\u7f16\u8bd1\u81ea \uff1a</strong><br />\nThe Real Truth About Sex And Diabetes | Psychology Today</p>\n\n<p><strong><span style=\"font-size:14px\">\u76f8\u5173\u5c0f\u7ec4\uff1a</span></strong></p>\n\n<p><a href=\"http://www.guokr.com/group/30/\"><span style=\"font-size:14px\">\u6027\u60c5</span></a></p>\n\n<p><span style=\"font-size:14px\"><a href=\"http://www.guokr.com/group/28/\">\u5065\u5eb7\u671d\u4e5d\u665a\u4e94</a></span></p>\n\n<p><strong>\u76f8\u5173\u53c2\u8003\u6587\u732e\uff1a</strong></p>\n\n<p>El-Bahrawy, M. et al. &ldquo;Noninvasive Vacuum Constriction in theManagement of Erectile Dysfunction,&rdquo; <em>International Journal of Urology and Nephrology</em> (1995) 27:331.</p>\n\n<p>Weinhardt, LS and MP Carey. &ldquo;Prevalence of Erectile Disorder Among Men with Diabetes Melllitus: Comprehensive Review,&rdquo;<em>Journal of Sex Research</em> (1996) 33:205.</p>\n\n<p>DePalma, R.G. et al. &ldquo;Vascular Interventions for Impotence: Lessons Learned,&rdquo; <em>Journal of Vascular Surgery</em> (1995) 21:576.</p>\n\n<div>\n<p>\u4e0b\u8f7d\u77e5\u6027APP \u4f60\u53ef\u4ee5\u505a\u5f97\u66f4\u597d</p>\n<a href=\"http://sex.guokr.com/app/\" target=\"_blank\" title=\"\u4e0b\u8f7d\u77e5\u6027APP\"><img src=\"https://2-im.guokr.com/c-jHMEuICRW2LOpmV22-9IlD3lklx_ztI7r8Id5cJq8EAQAABAEAAEdJ.gif\" style=\"height:130px; width:130px\" /></a>\n\n<p>\u66f4\u591a\u76f8\u5173\u8ba8\u8bba\uff0c\u70b9\u6211\u8bbf\u95ee<a href=\"http://sex.guokr.com/post/1050348/\" style=\"color:#46b6b6;text-decoration:none\" target=\"_blank\" title=\"\u77e5\u6027\u793e\u533a\">\u77e5\u6027\u793e\u533a</a>\uff08\u539f\u679c\u58f3\u6027\u60c5)</p>\n</div>", "date_created": "2013-05-03T16:42:36.080508+08:00", "date_modified": "2019-10-09T13:41:18.616590+08:00", "date_published": "2019-10-09T13:42:18+08:00", "id": 436989, "image": "https://2-im.guokr.com/9jvrJtwRv-bG7hRJCV39uzyVXfM6S_v-XoNm0C-eC3BXAQAACwEAAEpQ.jpg", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "sex", "replies_count": 48, "small_image": "https://2-im.guokr.com/9jvrJtwRv-bG7hRJCV39uzyVXfM6S_v-XoNm0C-eC3BXAQAACwEAAEpQ.jpg", "subject": {"articles_count": 448, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "sex", "minisite_key": "sex", "name": "\u6027\u60c5", "sort_score": 8, "subject_type": "normal"}, "subject_key": "sex", "summary": "\u6709\u4e24\u4e2a\u5173\u4e8e\u7cd6\u5c3f\u75c5\u548c\u6027\u7684\u4f20\u8bf4\uff1a\u4e00\u662f\u60a3\u7cd6\u5c3f\u75c5\u7684\u7537\u4eba\uff0c\u6ce8\u5b9a\u8981\u52c3\u8d77\u529f\u80fd\u969c\u788d\uff08ED\uff09\u3002\u800c\u662f\u60a3\u6709\u7cd6\u5c3f\u75c5\u7684\u5973\u6027\u5728\u6027\u65b9\u9762\u4e0d\u4f1a\u53d7\u592a\u5927\u5f71\u54cd\u3002\u4e0d\u8fc7\u6027\u4e13\u680f\u4f5c\u8005\u5361\u745f\u66fc\u7684\u8fd9\u7bc7\u535a\u6587\u5c06\u544a\u8bc9\u6211\u4eec\uff0c\u8fd9\u4e24\u79cd\u8ba4\u8bc6\u90fd\u662f\u9519\u8bef\u7684\u3002", "text": "\u51e0\u4e4e\u53ea\u8981\u7537\u4eba\u88ab\u786e\u8bca\u60a3\u6709\u7cd6\u5c3f\u75c5\uff0c\u4ed6\u4eec\u5c31\u4f1a\u542c\u5230\u53ef\u6015\u7684\u5173\u4e8eED\u7684\u8b66\u544a\u3002\u5176\u5b9e\uff0c\u6709\u65f6\u5019\u662f\u81ea\u6211\u5b9e\u73b0\u7684\u9884\u8a00\u7684\u4e00\u4e2a\u7ecf\u5178\u6848\u4f8b\u2014\u2014\u5bf9ED\u7684\u7126\u8651\u53ef\u80fd\u4f1a\u5bfc\u81f4ED\u3002\u5f53\u7136\uff0c\u4e8b\u5b9e\u4e0a\u7cd6\u5c3f\u75c5\u7684\u786e\u53ef\u5f71\u54cd\u52c3\u8d77\uff0c\u4f46\u8fd9\u5e76\u975e\u4e0d\u53ef\u907f\u514d\uff0c\u800c\u4e14\u5c31\u7b97\u771f\u7684\u53d1\u751f\u4e86\uff0c\u8fd8\u6709\u4e00\u4e9b\u5b89\u5168\u6709\u6548\u7684\u6cbb\u7597\u65b9\u6cd5\u53ef\u7528\u3002\u6709\u4e86\u6b63\u786e\u7684\u6cbb\u7597\uff0c\u7cd6\u5c3f\u75c5\u60a3\u8005\u4e5f\u53ef\u4ee5\u4eab\u53d7\u6ee1\u610f\u7684\u6027\u751f\u6d3b\u3002\u7537\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u7684\u6027\u95ee\u9898\u96ea\u57ce\u5927\u5b66\u7684\u7814\u7a76\u4eba\u5458\u56de\u987e\u4e86\u52c3\u8d77\u969c\u788d\u7684\u7537\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u768423\u9879\u7814\u7a76\u3002\u4ed6\u4eec\u7684\u7ed3\u8bba\u662f\uff1a\u5927\u7ea6\u670930\uff05\u7684\u7cd6\u5c3f\u75c5\u60a3\u8005\u60a3\u6709\u91cd\u5ea6ED\uff0c\u6bd4\u8d77\u6ca1\u60a3\u7cd6\u5c3f\u75c5\u7537\u6027\uff0c\u60a3\u7cd6\u5c3f\u75c5\u7684\u7537\u6027ED\u98ce\u9669\u662f\u524d\u8005\u7684\u4e24\u500d\uff0c\u4f46\u8fd9\u4e9b\u7814\u7a76\u7ed3\u679c\u540c\u65f6\u4e5f\u8868\u660e\uff0c\u60a3\u6709\u7cd6\u5c3f\u75c5\u7684\u7537\u6027\u7edd\u975e\u6ce8\u5b9a\u8981ED\u3002\u5f15\u8d77\u7537\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u52c3\u8d77\u969c\u788d\u7684\u539f\u56e0\u6709\u4e24\u4e2a\uff1a\u5fc3\u8840\u7ba1\u5e76\u53d1\u75c7\uff08\u52a8\u8109\u7ca5\u6837\u786c\u5316\uff09\u548c\u795e\u7ecf\u635f\u4f24\u3002\u7cd6\u5c3f\u75c5\u4f1a\u52a0\u901f\u52a8\u8109\u5835\u585e\uff0c\u4ece\u800c\u964d\u4f4e\u4e86\u9634\u830e\u7684\u8840\u6db2\u6d41\u5165\u5e76\u5f71\u54cd\u52c3\u8d77\u3002\u800c\u795e\u7ecf\u7cfb\u7edf\u635f\u5bb3\u5219\u5f71\u54cd\u4e86\u53c2\u4e0e\u7684\u52c3\u8d77\u8fc7\u7a0b\u7684\u795e\u7ecf\u3002\u603b\u7684\u6765\u8bf4\uff0c\u7cd6\u5c3f\u75c5\u5bfc\u81f4ED\u7684\u5371\u9669\u56e0\u7d20\u5305\u62ec\u4e0b\u8fb9\u8fd9\u4e9b\uff1a\u2022\u60a3\u7cd6\u5c3f\u75c5\u7684\u65f6\u95f4\u3002\u5fc3\u8840\u7ba1\u75be\u75c5\u548c\u795e\u7ecf\u75c5\u53d8\uff0c\u90fd\u4f1a\u968f\u7740\u65f6\u95f4\u7684\u63a8\u79fb\u53d1\u5c55\u3002\u60a3\u7cd6\u5c3f\u75c5\u65f6\u95f4\u8d8a\u957f\uff0c\u98ce\u9669\u8d8a\u5927\u3002\u5728\u5e74\u8f7b\u7684\u65f6\u5019\u5c31\u88ab\u8bca\u65ad\u60a3\u6709I\u578b\u7cd6\u5c3f\u75c5\u7537\u6027\uff0c\u6bd4\u5e74\u7eaa\u8f83\u5927\u7684\u88ab\u8bca\u65ad\u4e3aII\u578b\u7cd6\u5c3f\u75c5\u7684\u7537\u6027\u66f4\u5bb9\u6613\u53d1\u751fED\u3002\u2022\u7cd6\u5c3f\u75c5\u5e76\u53d1\u75c7\u3002\u7f79\u60a3\u7cd6\u5c3f\u75c5\u5e76\u53d1\u75c7\uff0c\u5c24\u5176\u662f\u5fc3\u8840\u7ba1\u75be\u75c5\u7684\u7537\u6027\u66f4\u5bb9\u6613\u60a3\u4e0aED\u3002\u2022\u5f71\u54cd\u8840\u538b\u7684\u836f\u7269\u7684\u4f7f\u7528\u3002\u8bb8\u591a\u9ad8\u8840\u538b\u836f\u7269\u7684\u4e00\u4e2a\u526f\u4f5c\u7528\u662f\u52c3\u8d77\u969c\u788d\u3002\u2022\u80a5\u80d6\u3002\u5b83\u4e0e\u5fc3\u8840\u7ba1\u75be\u75c5\u76f8\u5173\u3002\u2022\u5438\u70df\u3002\u8fd9\u4f1a\u52a0\u901f\u5fc3\u8840\u7ba1\u75be\u75c5\u7684\u53d1\u5c55\u3002\u5c31\u7b97\u56e0\u60a3\u7cd6\u5c3f\u75c5\u60a3\u4e0a\u4e86\u52c3\u8d77\u969c\u788d\uff0c\u4e5f\u4e0d\u7528\u7edd\u671b\u3002\u4eca\u5929\uff0c\u5df2\u7ecf\u6709\u4f17\u591a\u7684\u6cbb\u7597\u65b9\u6848\u53ef\u4ee5\u5e2e\u52a9\uff1a\u2022\u670d\u836f \u00a0\u73b0\u5728\u80fd\u6539\u5584\u52c3\u8d77\u72b6\u51b5\u7684\u836f\u7269\u5df2\u7ecf\u6709\u5f88\u591a\u4e86\uff0c\u8fd9\u4e9b\u836f\u540c\u6837\u5bf9\u7cd6\u5c3f\u75c5\u60a3\u8005\u8d77\u4f5c\u7528\u3002\u4e0d\u8fc7\uff0c\u5f97\u8bb0\u5f97\u5411\u5927\u592b\u62a5\u544a\u4f60\u6b63\u5728\u5403\u7684\u836f\uff0c\u770b\u5b83\u4eec\u662f\u5426\u6709\u635f\u5bb3\u52c3\u8d77\u7684\u526f\u4f5c\u7528\uff0c\u800c\u4e14\u6709\u5fc5\u8981\u6d4b\u8bd5\u4e00\u4e0b\u6fc0\u7d20\u662f\u5426\u5931\u8861\u3002\u4f46\u662f\uff0c\u5982\u679c\u4f60\u6b63\u5728\u670d\u7528\u785d\u9178\u76d0\u7c7b\u836f\u7269\u6765\u6cbb\u7597\u5fc3\u7ede\u75db\uff0c\u90a3\u5c31\u5f97\u614e\u7528\u5e2e\u52a9\u52c3\u8d77\u7684\u836f\u7269\u3002\u800c\u4e14\uff0c\u5982\u679c\u52c3\u8d77\u969c\u788d\u662f\u7531\u4e8e\u795e\u7ecf\u635f\u4f24\uff0c\u90a3\u8fd9\u7c7b\u836f\u7269\u5c31\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u2022\u771f\u7a7a\u6536\u7f29\u88c5\u7f6e\u3002\u8fd9\u79cd\u5c0f\u5de5\u5177\u80fd\u5728\u9634\u830e\u5468\u56f4\u521b\u9020\u4e00\u4e2a\u5c40\u90e8\u771f\u7a7a\uff0c\u4f7f\u8840\u6db2\u6d41\u5411\u9634\u830e\u5e76\u4f7f\u9634\u830e\u4e34\u65f6\u52c3\u8d77\u3002\u8fd9\u7c7b\u88c5\u7f6e\u6709\u5f88\u591a\u578b\u53f7\uff0c\u4f46\u90fd\u5305\u62ec\u4e00\u4e2a\u5957\u5728\u9634\u830e\u4e0a\u7684\u5851\u6599\u7ba1\uff0c\u8fd8\u6709\u914d\u5957\u7684\u6324\u538b\u7403\u6cf5\u3002\u538b\u7f29\u7403\u6cf5\u4ece\u7ba1\u4e2d\u62bd\u7a7a\u7a7a\u6c14\uff0c\u4f7f\u8840\u6db2\u6d41\u5165\u9634\u830e\u3002\u4e00\u65e6\u7537\u6027\u6210\u529f\u52c3\u8d77\uff0c\u5c31\u53ef\u4ee5\u7528\u6a61\u80f6\u73af\u6ed1\u5230\u9634\u830e\u6839\u90e8\uff0c\u538b\u8feb\u9759\u8109\u6765\u7ef4\u6301\u52c3\u8d77\u3002\u6700\u8fd1\u7684\u4e00\u9879\u7814\u7a76\u8bc4\u4f30\u4e86\u771f\u7a7a\u6536\u7f29\u88c5\u7f6e\u7684\u6709\u6548\u6027\uff0c21\u4f4dED\u8005\u670917\u4eba\u90fd\u80fd\u901a\u8fc7\u8fd9\u79cd\u8bbe\u5907\u8fbe\u5230\u5b8c\u5168\u52c3\u8d77\u6216\u534a\u52c3\u8d77\u72b6\u6001\u6765\u5b8c\u6210\u6027\u4ea4\u3002\u5c3d\u7ba1\u5e02\u9762\u4e0a\u6709\u552e\uff0c\u4f46\u662f\u8fd8\u662f\u5efa\u8bae\u4f7f\u7528\u524d\u5148\u53bb\u54a8\u8be2\u5927\u592b\u4ee5\u4fbf\u9009\u62e9\u9002\u5408\u81ea\u5df1\u7684\u578b\u53f7\u3002\u2022\u624b\u672f\u3002\u5982\u679cED\u4e3b\u8981\u662f\u7531\u5fc3\u8840\u7ba1\u7cfb\u7edf\u7684\u5e76\u53d1\u75c7\u800c\u4e0d\u662f\u795e\u7ecf\u7cfb\u7edf\u5e76\u53d1\u75c7\u5f15\u8d77\u7684\uff0c\u9634\u830e\u8840\u7ba1\u52a8\u8109\u642d\u6865\u624b\u672f\uff0c\u53ef\u6539\u5584\u6d41\u52a8\u5230\u9634\u830e\u7684\u8840\u6d41\u72b6\u51b5\u3002\u8fd9\u7c7b\u4f3c\u4e8e\u5fc3\u810f\u642d\u6865\u624b\u672f\uff0c\u5916\u79d1\u533b\u751f\u4f1a\u4ece\u8eab\u4f53\u7684\u522b\u5904\u5207\u4e0b\u4e00\u6bb5\u52a8\u8109\uff0c\u7136\u540e\u7528\u5b83\u6765\u505a\u4e00\u4e2a\u652f\u8def\u7528\u6765\u7ed5\u8fc7\u5835\u585e\u7684\u90e8\u5206\u3002\u9634\u830e\u4e3b\u52a8\u8109\u91cd\u5efa\u548c\u80cc\u9759\u8109\u52a8\u8109\u5316\u4e5f\u589e\u52a0\u9634\u830e\u8840\u6d41\u91cf\u3002\u6709\u7814\u7a76\u8868\u660e\uff0c\u7ea6\u6709\u4e00\u534a\u7684\u7537\u6027\u4ece\u9634\u830e\u624b\u672f\u6062\u590d\u540e\uff0c\u6062\u590d\u4e86\u52c3\u8d77\u529f\u80fd\u3002\u5973\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u7684\u6027\u95ee\u9898\u7cd6\u5c3f\u75c5\u5bf9\u5973\u4eba\u6027\u65b9\u9762\u7684\u5f71\u54cd\u5f88\u5c11\u88ab\u63d0\u53ca\uff0c\u4e0d\u8fc7\u8fd9\u79cd\u5f71\u54cd\u5e76\u975e\u4e0d\u5b58\u5728\uff0c\u53ea\u662f\u76f8\u5bf9\u4e8e\u7537\u4eba\u6765\u8bf4\uff0c\u8fd9\u79cd\u5f71\u54cd\u66f4\u52a0\u5fae\u5999\u3002\u5b9e\u9645\u4e0a\u7cd6\u5c3f\u75c5\u5bf9\u5973\u60a3\u8005\u6027\u65b9\u9762\u7684\u5f71\u54cd\u662f\u503c\u5f97\u6ce8\u610f\u7684\u3002\u7cd6\u5c3f\u75c5\u5e26\u6765\u7684\u4e3b\u8981\u95ee\u9898\u662f\u9634\u9053\u5e72\u6da9\u3002\u8fd9\u662f\u56e0\u4e3a\uff0c\u9634\u9053\u751f\u4ea7\u5929\u7136\u7684\u6da6\u6ed1\u6db2\u9700\u4f9d\u8d56\u9634\u9053\u58c1\u5145\u5206\u7684\u8840\u6db2\u4f9b\u5e94\u3002\u6b63\u5982\u7cd6\u5c3f\u75c5\u76f8\u5173\u7684\u5fc3\u8840\u7ba1\u75be\u75c5\u4f1a\u51cf\u5c11\u6d41\u5165\u9634\u830e\u7684\u8840\u6db2\uff0c\u8fd9\u540c\u6837\u4e5f\u4f1a\u5f71\u54cd\u9634\u9053\u58c1\u7684\u8840\u6d41\u91cf\u3002\u5973\u6027\u7cd6\u5c3f\u75c5\u60a3\u8005\u7684\u795e\u7ecf\u75c5\u53d8\uff0c\u4f1a\u4f7f\u9634\u8482\u53d7\u5230\u523a\u6fc0\u65f6\u53d8\u5f97\u8fdf\u949d\uff0c\u540c\u65f6\u53d7\u5230\u5f71\u54cd\u7684\u8fd8\u6709\u5bf9\u7231\u629a\u53d8\u5f97\u4e0d\u654f\u611f\u548c\u96be\u4ee5\u8fbe\u5230\u9ad8\u6f6e\u3002\u6da6\u6ed1\u6cb9\u80fd\u591f\u589e\u52a0\u5bf9\u89e6\u6478\u7684\u654f\u611f\uff0c\u4f1a\u6709\u70b9\u5e2e\u52a9\u3002\u800c\u5176\u4ed6\u8bf8\u5982\u632f\u52a8\u68d2\u4e4b\u7c7b\u60c5\u8da3\u7528\u54c1\uff0c\u80fd\u591f\u66f4\u597d\u5730\u523a\u6fc0\u9634\u8482\u4ee5\u6539\u5584\u6027\u751f\u6d3b\u8d28\u91cf\u3002PS\uff1a\u4e0d\u80fd\u5403\u7cd6\u5df2\u7ecf\u591f\u4e0d\u5e78\u798f\u7684\u4e86\uff0c\u96ea\u4e0a\u52a0\u971c\u600e\u4e48\u5f97\u4e86\u554a~~~\u672c\u6587\u7f16\u8bd1\u81ea \uff1aThe Real Truth About Sex And Diabetes | Psychology Today\u76f8\u5173\u5c0f\u7ec4\uff1a\u6027\u60c5\u5065\u5eb7\u671d\u4e5d\u665a\u4e94\u76f8\u5173\u53c2\u8003\u6587\u732e\uff1aEl-Bahrawy, M. et al. \u201cNoninvasive Vacuum Constriction in theManagement of Erectile Dysfunction,\u201d International Journal of Urology and Nephrology (1995) 27:331.Weinhardt, LS and MP Carey. \u201cPrevalence of Erectile Disorder Among Men with Diabetes Melllitus: Comprehensive Review,\u201dJournal of Sex Research (1996) 33:205.DePalma, R.G. et al. \u201cVascular Interventions for Impotence: Lessons Learned,\u201d Journal of Vascular Surgery (1995) 21:576.\u4e0b\u8f7d\u77e5\u6027APP \u4f60\u53ef\u4ee5\u505a\u5f97\u66f4\u597d\u66f4\u591a\u76f8\u5173\u8ba8\u8bba\uff0c\u70b9\u6211\u8bbf\u95ee\u77e5\u6027\u793e\u533a\uff08\u539f\u679c\u58f3\u6027\u60c5)", "title": "\u4ece\u7cd6\u5c3f\u75c5\u624b\u4e2d\u593a\u56de\u6027\u798f", "ukey_author": "s48nj7", "video_content": ""}, {"author": {"avatar": {"large": "http://3-im.guokr.com/N1Tre9hCSOb4JOEtJTNwv0uEyfmaF5udjLAmDZDe8mrdAAAA4gAAAFBO.png", "normal": "http://3-im.guokr.com/N1Tre9hCSOb4JOEtJTNwv0uEyfmaF5udjLAmDZDe8mrdAAAA4gAAAFBO.png", "small": "http://3-im.guokr.com/N1Tre9hCSOb4JOEtJTNwv0uEyfmaF5udjLAmDZDe8mrdAAAA4gAAAFBO.png"}, "introduction": "\u533b\u5b66\u535a\u58eb\uff0c301\u533b\u9662\u526f\u7814\u7a76\u5458\u3002\u9664\u4e86\u672c\u884c\u751f\u547d\u79d1\u5b66\uff0c\u5bf9\u5386\u53f2\u4e5f\u6709\u5f88\u5927\u5174\u8da3\u3002", "nickname": "\u84b9\u846d\u4ece\u98ce", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/N1Tre9hCSOb4JOEtJTNwv0uEyfmaF5udjLAmDZDe8mrdAAAA4gAAAFBO.png?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/N1Tre9hCSOb4JOEtJTNwv0uEyfmaF5udjLAmDZDe8mrdAAAA4gAAAFBO.png?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/N1Tre9hCSOb4JOEtJTNwv0uEyfmaF5udjLAmDZDe8mrdAAAA4gAAAFBO.png?imageView2/1/w/24/h/24"}, "introduction": "\u533b\u5b66\u535a\u58eb\uff0c301\u533b\u9662\u526f\u7814\u7a76\u5458\u3002\u9664\u4e86\u672c\u884c\u751f\u547d\u79d1\u5b66\uff0c\u5bf9\u5386\u53f2\u4e5f\u6709\u5f88\u5927\u5174\u8da3\u3002", "nickname": "\u84b9\u846d\u4ece\u98ce", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [], "content": "<p><span style=\"color:#808080\"><span style=\"font-size:14px\"><strong>|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</strong></span></span></p>\r\n\r\n<p>1894\u5e7411\u670816\u65e5\uff0c\u5a01\u6d77\u5218\u516c\u5c9b\uff0c\u6d45\u6ee9\u4e0a\u4f0f\u7740\u5317\u6d0b\u6d77\u519b\u7684\u94c1\u7532\u8230\u9547\u8fdc\u53f7\uff0c\u8eab\u4e0a\u6ee1\u662f\u5f39\u5b54\u3001\u51f9\u69fd\u548c\u706b\u75d5\uff0c\u50cf\u5934\u9971\u7ecf\u6ca7\u6851\u7684\u5de8\u9cb8\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/RFrZzJgVjdbbblCZp6U8-m34h4VDJ-aAsG-hEiQVUNQgAwAAywEAAEpQ.jpg\" style=\"height:367px; width:640px\" />\u7532\u5348\u6218\u540e\u7684\u9547\u8fdc\u8230\uff0c\u767d\u5708\u6807\u51fa\u7740\u5f39\u70b9 | \u5386\u53f2\u8d44\u6599</p>\r\n\r\n<p>\u591c\u8272\u6de1\u53bb\uff0c\u592a\u9633\u7167\u5e38\u5347\u8d77\uff0c\u5f88\u5feb\u6709\u4eba\u53d1\u73b0\u4e86\u5f02\u6837\u3002\u6768\u526f\u7ba1\u9a7e\u5e26\u4eba\u51b2\u8fdb\u4f4f\u8231\u649e\u5f00\u8230\u957f\u5ba4\uff0c\u88ab\u773c\u524d\u7684\u4e00\u5207\u60ca\u5446\u4e86\uff1a</p>\r\n\r\n<p>&ldquo;\u77b3\u5b54\u6781\u5ea6\u7f29\u5c0f\uff0c\u76ae\u80a4\u6e7f\u51b7\uff0c\u547c\u5438\u6291\u5236&hellip;&hellip;\u5178\u578b\u7684\u5417\u5561\u4e2d\u6bd2\uff0c\u53ef\u662f\u592a\u665a\u4e86\uff0c\u62b1\u6b49\uff0c\u6211\u5df2\u7ecf\u65e0\u80fd\u4e3a\u529b\u3002&rdquo;\u5218\u516c\u5c9b\u75c5\u9662\u7684\u82f1\u56fd\u533b\u751f\u683c\u536b\u9f84\u68c0\u67e5\u5b8c\u53f9\u4e86\u53e3\u6c14\u3002\u684c\u4e0a\u6709\u4e00\u53ea\u7a7a\u7897\uff0c\u5e95\u90e8\u6709\u4e9b\u8bb8\u9ed1\u8272\u5757\u72b6\u7269\uff0c\u90a3\u662f\u8fd8\u6ca1\u6709\u6eb6\u89e3\u7684\u9e26\u7247\u3002</p>\r\n\r\n<p>\u5317\u6d0b\u6d77\u519b\u5de6\u7ffc\u603b\u5175\u3001\u9547\u8fdc\u8230\u7ba1\u5e26\uff08\u8230\u957f\uff09\u6797\u6cf0\u66fe\uff0c\u5728125\u5e74\u524d\u90a3\u4e2a\u6df1\u79cb\u6e05\u6668\u670d\u9e26\u7247\u81ea\u6740\uff0c\u65f6\u5e7442\u5c81\u3002</p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:640px\">\r\n\t<tbody>\r\n\t\t<tr>\r\n\t\t\t<td><img alt=\"\" src=\"https://3-im.guokr.com/15H_UiGZ4CrOb0oh5JS5CDRT5Lq2DEMBzUT05REMl_dwAQAAIAMAAEpQ.jpg\" style=\"height:652px; width:300px\" /></td>\r\n\t\t\t<td><img alt=\"\" src=\"https://2-im.guokr.com/w4vnI_zUa7HaK6xk9Ge5XE1tkKKCZg1EWUx_mWiwQRjsAQAAIAMAAEpQ.jpg\" style=\"height:488px; width:300px\" /></td>\r\n\t\t</tr>\r\n\t</tbody>\r\n</table>\r\n\r\n<p class=\"img-caption\">\u6797\u6cf0\u66fe\u7167\u7247\uff1b\u5de6\u56fe\u6444\u4e8e1877-1879\u5e74\uff0c\u6cd5\u56fd\uff0c\u6797\u4f5c\u4e3a\u82f1\u56fd\u7687\u5bb6\u6d77\u519b\u7559\u5b66\u751f\u65f6\u671f | \u539f\u56fe\u7531\u6cd5\u56fd\u8239\u653f\u6587\u5316\u7814\u7a76\u8005\u9b4f\u5ef6\u5e74\u5148\u751f\u63d0\u4f9b</p>\r\n\r\n<p>\u5317\u6d0b\u6d77\u519b\u7684\u5de6\u7ffc\u603b\u5175\u519b\u8854\u76f8\u5f53\u4e8e\u4eca\u5929\u7684\u5c11\u5c06\uff0c\u804c\u6743\u4f4d\u5217\u6d77\u519b\u7b2c\u4e8c\uff1b\u9547\u8fdc\u662f\u6e05\u653f\u5e9c\u4ec5\u6709\u7684\u4e24\u6761\u94c1\u7532\u8230\u4e4b\u4e00\uff0c\u4e3a\u56fd\u4e4b\u91cd\u5668\uff0c\u7c7b\u4f3c\u4eca\u5929\u7684\u822a\u6bcd\u3002\u6797\u603b\u5175\u81ea\u6740\u524d\u4e0d\u4e45\uff0c\u4e2d\u65e5\u7532\u5348\u6d77\u6218\u521a\u521a\u6682\u544a\u6bb5\u843d\u3002\u56fd\u4e8b\u7ef4\u8270\uff0c\u6d77\u519b\u9ad8\u5b98\u7a81\u7136\u81ea\u5c3d\uff0c\u65e0\u7591\u8ba9\u4f4e\u9761\u7684\u6218\u52bf\u3001\u75b2\u655d\u7684\u56fd\u547d\u96ea\u4e0a\u52a0\u971c\u3002\u4e00\u65f6\u95f4\u519b\u5185\u54d7\u7136\uff0c\u671d\u91ce\u9707\u60ca\u3002</p>\r\n\r\n<p>\u6797\u603b\u5175\u81ea\u6740\u7684\u6d88\u606f\u6210\u4e3a\u70ed\u8bae\u8bdd\u9898\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u56e0\u4e24\u5929\u524d\u9547\u8fdc\u89e6\u7901\u8fc7\u4e8e\u5185\u759a\uff0c\u4e5f\u6709\u4eba\u8bf4\u4ed6\u662f\u81ea\u8bc1\u6e05\u767d\u9a73\u65a5\u901a\u654c\u8c23\u8a00\uff0c\u8fd8\u6709\u8bf4\u4ed6\u662f\u5bf9\u6218\u52bf\u5931\u53bb\u4fe1\u5fc3\uff0c\u66f4\u6709\u751a\u8005\u7ffb\u51fa\u4e4b\u524d\u79cd\u79cd\u516b\u5366\uff0c\u8bf4\u4ed6\u4e3a\u4eba\u80c6\u5c0f\u6015\u4e8b[1]&hellip;&hellip;\u4e0d\u77e5\u662f\u5426\u53d7\u8206\u8bba\u5f71\u54cd\uff0c\u6700\u7ec8\u5b98\u65b9\u8c03\u67e5\u7ed3\u679c\u662f\uff1a&ldquo;<strong>\u6797\u6cf0\u66fe\u5411\u6765\u80c6\u5c0f\uff0c\u60f3\u56e0\u758f\u5ffd\uff0c\u5185\u759a\u8f7b\u751f</strong>&rdquo;&nbsp;[2]\u3002</p>\r\n\r\n<p>\u80c6\u5c0f\u8f7b\u751f\uff0c\u6210\u4e3a\u6797\u6cf0\u66fe\u7684\u76d6\u68fa\u5b9a\u8bba\uff0c\u4e00\u76f4\u5f71\u54cd\u81f3\u4eca\u3002</p>\r\n\r\n<p>\u4f46\u6797\u6cf0\u66fe\u662f\u4e2d\u56fd\u7b2c\u4e00\u4ee3\u6d77\u519b\u7740\u91cd\u57f9\u517b\u7684\u4e09\u4f4d\u82f1\u624d\u4e4b\u4e00\uff0c \u505a\u8239\u653f\u5b66\u751f\u65f6\u5c31&ldquo;<strong>\u5386\u8003\u4f18\u7b49</strong>&rdquo;\uff0c\u88ab\u8a89\u4e3a&ldquo;<strong>\u95fd\u7701\u5b66\u751f\u51fa\u8272\u4e4b\u4eba</strong>&rdquo;\uff0c\u800c\u540e\u9009\u6d3e\u7559\u5b66\u3001\u63a5\u8230\u56de\u56fd\u3001\u63a5\u624b\u8425\u52a1\u3001\u521d\u521b\u6d77\u519b\u3001\u8bad\u7ec3\u5de1\u822a&hellip;&hellip;\u987a\u5229\u5b8c\u6210\u4e00\u4e2a\u4e2a\u91cd\u8981\u4efb\u52a1\u3002\u501f\u7528\u4e2d\u56fd\u5fc3\u7406\u5b66\u5bb6\u7f16\u5236\u7684\u519b\u5b98\u5927\u4e94\u91cf\u8868MOVPS[3]\u8bf7\u4e00\u4e9b\u5728\u8fd1\u4ee3\u6d77\u519b\u53f2\u65b9\u9762\u7684\u8d44\u6df1\u7814\u7a76\u8005\u5bf9\u4ed6\u8fdb\u884c\u4eba\u683c-\u5c97\u4f4d\u9002\u5e94\u6027\u7684\u8bc4\u4f30\uff0c\u53d1\u73b0\u4ed6\u64c5\u957f\u7ec4\u7ec7\u3001\u534f\u8c03\u548c\u7ba1\u7406\uff0c\u540c\u65f6\u5728\u519b\u4e8b\u6307\u6325\u624d\u80fd\u65b9\u9762\u8868\u73b0\u4f18\u79c0\u3002</p>\r\n\r\n<p>\u8bda\u5982\u674e\u9e3f\u7ae0\u6240\u8c13\u7684&ldquo;\u5411\u6765&rdquo;\u80c6\u5c0f\uff0c\u4e3a\u4f55\u8fd8\u8d39\u5c3d\u5fc3\u601d\u5c06\u4ed6\u8c03\u5165\u81ea\u5df1\u9ebe\u4e0b\uff1f\u5f53\u65f6\u81ea\u6740\u7528\u9e26\u7247\u6ce1\u9152\uff0c\u4f46\u9e26\u7247\u6eb6\u89e3\u5ea6\u5dee\uff0c\u8fbe\u5230\u81f4\u6b7b\u5242\u91cf\u4e14\u8981\u559d\u4e0a\u4e00\u58f6\uff0c\u800c\u4e14\u5473\u9053\u96be\u559d\u5f97\u8ba9\u4eba\u60f3\u6b7b\uff0c\u54e6\u4e0d\uff0c\u662f\u96be\u559d\u5f97\u8ba9\u4eba\u90fd\u4e0d\u60f3\u53bb\u6b7b\uff0c\u6bd4\u5982\u540e\u6765\u6218\u8d25\u524d\u81ea\u6740\u7684\u5218\u516c\u5c9b\u62a4\u519b\u6234\u5b97\u9a9e\u5c31\u8981\u522b\u4eba\u8865\u836f\uff0c\u5b9a\u8fdc\u8230\u7ba1\u5e26\u5218\u6b65\u87fe\u81ea\u6740\u8fc7\u7a0b\u6301\u7eed\u5230\u6b21\u65e5\u4e2d\u5348\u3002\u6797\u6cf0\u66fe\u4ece\u670d\u836f\u5230\u6b7b\u4ea1\u4ec52\u4e2a\u5c0f\u65f6\uff0c\u53ef\u89c1\u4ed6\u6c42\u6b7b\u4e4b\u5fc3\u4e4b\u51b3\u7edd\u3002\u6b7b\u90fd\u4e0d\u60e7\uff0c\u7b97\u80c6\u5c0f\u4e48\uff1f</p>\r\n\r\n<p>\u4e8b\u51fa\u8e4a\u8df7\uff0c\u8fd8\u662f\u6765\u56de\u987e\u4e0b\u4ed6\u81ea\u5c3d\u524d\u7684\u51e0\u4e2a\u5173\u952e\u8f68\u8ff9\uff1a</p>\r\n\r\n<h2><strong>\u4e8b\u4ef61\uff1a\u6218\u524d\uff0c\u731d\u4e0d\u53ca\u9632\u7684\u4e00\u679a\u6807\u7b7e</strong></h2>\r\n\r\n<p>1894\u5e746\u670820\u65e5\uff0c\u6797\u6cf0\u66fe\u4f5c\u4e3a\u5317\u6d0b\u8230\u961f\u5206\u9063\u961f\u53f8\u4ee4\uff0c\u5949\u547d\u7387\u9547\u8fdc\u3001\u8d85\u52c7\u3001\u5e7f\u4e19\u4e09\u8230\u524d\u5f80\u671d\u9c9c\u4ec1\u5ddd\u7b49\u5730\u5de1\u822a[4]\u3002\u5f88\u5feb\uff0c\u4ed6\u53d1\u73b0\u6218\u4e89\u7684\u6b65\u4f10\u65e0\u6cd5\u963b\u6321\uff0c\u4e8e\u662f\u7acb\u5373\u5411\u4e0a\u53f8\u674e\u9e3f\u7ae0\u53d1\u7535\u62a5\u7533\u8bf7\u652f\u63f4\u3002</p>\r\n\r\n<p>\u7136\u800c\u674e\u9e3f\u7ae0\u5fc3\u91cc\u4e00\u4e07\u4e2a\u4e0d\u60f3\u6253\u4ed7\u3002\u4f5c\u4e3a\u98ce\u5934\u6b63\u52b2\u7684\u6d0b\u52a1\u6743\u81e3\uff0c\u4e00\u65e6\u5f00\u6218\u5bb6\u5e95\u90fd\u5f97\u8d54\u5149\uff0c\u56e0\u6b64\u4e00\u76f4\u79ef\u6781\u5bfb\u6c42\u5217\u5f3a\u5bf9\u65e5\u672c\u7684\u5916\u4ea4\u7275\u5236\u3002\u65e5\u672c\u5916\u76f8\u9646\u5965\u5b97\u5149\u6b63\u597d\u5229\u7528\u8fd9\u79cd\u5fc3\u6001\uff0c\u4e00\u9762\u589e\u5175\u4e00\u9762\u91ca\u51fa\u548c\u5e73\u4fe1\u606f\u3002\u674e\u9e3f\u7ae0\u8d8a\u662f\u6015\u8f7b\u4e3e\u5984\u52a8\u6210\u4e3a\u65e5\u672c\u51fa\u5175\u7684\u501f\u53e3\uff0c\u624b\u4e0b\u90a3\u7fa4\u5c06\u9886\u8d8a\u4e0d\u5b89\u751f\uff1a\u8fd9\u8fb9\u6d77\u519b\u7684\u6797\u6cf0\u66fe\u8bf7\u6d3e\u9c7c\u96f7\u8247\uff0c\u9646\u519b\u7684\u53f6\u5fd7\u8d85\u56b7\u7740\u5927\u4e3e\u589e\u5175\uff0c\u8fd9\u5173\u5934\u4e0d\u7701\u5fc3\u7684\u671d\u5ef7\u8fd8\u9001\u6765\u4e00\u4efd\u56db\u767e\u91cc\u5bc6\u8c15\uff0c&ldquo;\u7b79\u6218&rdquo;&ldquo;\u94a6\u6b64&rdquo;\u7b49\u5b57\u773c\u8ba9\u674e\u9e3f\u7ae0\u5934\u5927\u4e86\u597d\u51e0\u5708\u3002</p>\r\n\r\n<p>\u4e0d\u660e\u771f\u76f8\u7684\u6797\u6cf0\u66fe\u4e00\u95e8\u5fc3\u601d\u4ece\u6218\u4e8b\u51fa\u53d1\uff0c\u51e0\u6b21\u4e09\u756a\u5730\u8bf7\u6c42\u652f\u63f4\u5907\u6218\u3002\u7ed3\u679c&hellip;&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/u6QDZaQMjoONtWtuGWfRF3wyVhePrMnbvuW2xoi9beccAgAA_AUAAEpQ.jpg\" style=\"height:1532px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/LBV7LNtDTDVAdjfwavoqkTjuRDjGCDbX-L8SCIuy0jwYAgAA_AUAAEpQ.jpg\" style=\"height:1532px; width:536px\" /></p>\r\n\r\n<p>\u6797\u6cf0\u66fe\u6ca1\u60f3\u5230\uff0c\u5162\u5162\u4e1a\u4e1a\u6362\u6765\u515c\u5934\u4e00\u76c6\u51b7\u6c34\u3002\u53ea\u662f\u51b7\u6c34\u5012\u4e5f\u7b97\u4e86\uff0c\u81ea\u5df1\u6b63\u5728\u4ee5\u8eab\u72af\u9669\u600e\u4e48\u5c31\u88ab\u4e0a\u7ea7\u76d6\u7ae0&ldquo;\u80c6\u602f\u5f20\u7687&rdquo;\uff1f\u4e0d\u5149\u6797\u6cf0\u66fe\u9707\u60ca\uff0c\u8230\u961f\u603b\u53f8\u4ee4\u4e01\u6c5d\u660c\u53ca\u6574\u4e2a\u6d77\u519b\u5728\u63a5\u5230\u674e\u9e3f\u7ae0\u7535\u8bad\u540e\u4e5f\u96c6\u4f53\u9677\u5165\u4e86\u56f0\u60d1\u548c\u8ff7\u832b\u3002\u4e01\u519b\u95e8\u53ea\u597d\u552f\u552f\u9053\uff1a&ldquo;\u6b64\u7cfb\u5927\u5e05\u6743\u8861\uff0c\u4f55\u6562\u53c2\u8bae\u3002&rdquo;</p>\r\n\r\n<p>\u53d7\u4e86\u59d4\u5c48\u7684\u6797\u603b\u5175\u5012\u6ca1\u6709\u6d88\u6781\u6020\u5de5\uff0c\u800c\u662f\u6839\u636e\u5f62\u52bf\u7387\u8230\u961f\u8f6c\u79fb\u7259\u5c71\uff0c\u4ee5\u6b64\u4e3a\u636e\u70b9\u5de1\u822a\u4fa6\u67e5\uff0c\u76f4\u5230\u4ed6\u83b7\u5f97\u4e86\u65e5\u519b\u5927\u4e3e\u589e\u5175\u4ec1\u5ddd\u7684\u6d88\u606f\u3002\u800c\u8fd9\u65f6\uff0c\u7259\u5c71\u901a\u5f80\u674e\u9e3f\u7ae0\u6240\u5728\u5730\u5929\u6d25\u7684\u7535\u62a5\u5df2\u7ecf\u963b\u65ad\uff0c\u800c\u81ea\u5df1\u4f5c\u4e3a\u5206\u8230\u961f\u53f8\u4ee4\uff0c\u5f97\u4e3a\u624b\u4e0b\u7684\u4e00\u4f17\u8230\u957f\u548c\u519b\u8230\u8d1f\u8d23\u3002</p>\r\n\r\n<p>\u7b49\u4e0d\u5230\u547d\u4ee4\uff0c\u6218\u4e8b\u5374\u62d6\u4e0d\u5f97\uff0c1894\u5e747\u67081\u65e5\uff0c\u6797\u6cf0\u66fe\u7387\u5404\u8230\u5f52\u822a\uff0c\u8ba1\u5212\u968f\u540e&ldquo;\u5408\u5927\u961f\u6765\u653b&rdquo;\u3002\u4ed6\u5927\u6982\u8ba4\u4e3a\uff0c\u674e\u9e3f\u7ae0\u4e4b\u524d\u7535\u62a5\u6307\u793a\u8fc7\u8ba9\u4ed6&ldquo;\u76f8\u673a\u8fdb\u9000&rdquo;\u6240\u4ee5\u6b64\u4e3e\u4e0d\u7b97\u8fdd\u4ee4\uff0c\u4f46\u6ca1\u60f3\u5230\u674e\u9e3f\u7ae0\u521a\u901a\u8fc7\u4fc4\u56fd\u4eba\u5f97\u5230\u4e86\u9646\u5965\u5b97\u5149\u4e0d\u5f00\u6218\u7684\u627f\u8bfa\uff0c\u5408\u961f\u6765\u653b\u81ea\u7136\u5316\u4e3a\u6ce1\u5f71\uff0c\u6797\u6cf0\u66fe\u4ec1\u5ddd\u7387\u8230\u8fd4\u56de\u53cd\u800c\u6210\u4e86\u91cd\u5927\u8fc7\u9519\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/EH1kYqkeiD3Y2eI8lgOY7PiRE_gA4MJLp6nbQz_d79MsAgAA2wUAAEpQ.jpg\" style=\"height:1499px; width:556px\" /></p>\r\n\r\n<p>\u65e5\u672c\u6218\u4e89\u6587\u5b66\u5bb6\u5c0f\u7b20\u539f\u957f\u751f\u7684\u5c0f\u8bf4\u4e2d\u5f15\u8ff0\u6218\u540e\u7f34\u83b7\u7684\u6797\u6cf0\u66fe\u65e5\u8bb0\uff0c\u63d0\u5230\u4e86\u6797\u6cf0\u66fe\u4e8b\u540e\u7684\u53cd\u5e94\uff1a&ldquo;<strong>\u545c\u547c\u3002\u6cf0\u66fe\u4e00\u4ecb\u6b66\u592b\u8033\uff0c\u624d\u62d9\u8bc6\u6d45\uff0c\u4f46\u5fe7\u56fd\u5bb6\u77e5\u65e0\u4e0d\u8a00\u3002\u65e2\u5085\u76f8\u5df2\u6709\u6210\u7b97\uff0c\u4f55\u6562\u518d\u8a00\u6218\uff1f\u552f\u6709\u8c28\u5f85\u5085\u76f8\u4e4b\u547d\u800c\u5df2\u3002</strong>&rdquo;[5]</p>\r\n\r\n<p>\u6b66\u5c06\u5728\u6e05\u671d\u5730\u4f4d\u4f4e\u4e0b\uff0c\u4f46\u665a\u6e05\u5efa\u7acb\u7684\u7b2c\u4e00\u652f\u9ad8\u5b66\u5386\u3001\u804c\u4e1a\u5316\u6d77\u519b\uff0c\u5df2\u7ecf\u6446\u8131\u4e86\u4f20\u7edf\u7684\u9119\u964b\u5370\u8ff9\uff0c\u90a3\u4e9b\u7559\u82f1\u7559\u7f8e\u7684\u6d77\u519b\u519b\u5b98\u66f4\u662f\u4ee5\u7cbe\u82f1\u81ea\u5c45\u3002\u6797\u6cf0\u66fe\u4f5c\u4e3a\u6b64\u5217\u4e2d\u4eba\uff0c\u6b64\u65f6\u5374\u81ea\u8d2c\u81ea\u5632&ldquo;<strong>\u4e00\u4ecb\u6b66\u592b</strong>&rdquo;\uff0c\u53ef\u89c1\u5907\u6218\u70ed\u60c5\u5f7b\u5e95\u51c9\u4e86\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u90a3\u679a&ldquo;\u80c6\u5c0f\u5f20\u7687&rdquo;\u7684\u6807\u7b7e\u5374\u8d8a\u53d1\u663e\u773c\uff0c\u674e\u9e3f\u7ae0\u540e\u6765\u6279\u8bc4\u4e01\u6c5d\u660c\u90fd\u4f1a\u62c9\u4ed6\u6765\u6e9c\uff1a&ldquo;\u6797\u6cf0\u66fe\u524d\u5728\u4ec1\u5ddd\u754f\u65e5\u9041\u8d70&hellip;&hellip;\u4e2d\u897f\u4eba\u4f20\u4e3a\u7b11\u8c08\u3002&rdquo;</p>\r\n\r\n<p>\u5f53\u65f6&ldquo;\u4e2d\u897f\u4eba&rdquo;\u7684\u516b\u5366\u70ed\u60c5\u4e0d\u4e9a\u4e8e\u4eca\u5929\u7684\u5403\u74dc\u7fa4\u4f17\uff0c\u6210\u5c71\u5934\u7535\u62a5\u59d4\u5458\u62a5\u544a\u7684&ldquo;\u6797\u6cf0\u66fe\u6d77\u4e0a\u9047\u65e5\u8230\u9003\u7a9c\u4e8b\u4ef6&rdquo;\u8bf4\u5f97\u6709\u9f3b\u5b50\u6709\u773c\uff0c\u4eff\u4f5b\u4ed6\u5f53\u65f6\u4eb2\u4e34\u73b0\u573a\u3002\u8fd9\u4e2a\u6545\u4e8b\u4ece\u4e00\u7fa4\u6d0b\u4eba\u90a3\u4f20\u5230\u4e86\u6797\u6cf0\u66fe\u8001\u540c\u5b66\u3001\u65f6\u4e3a\u76db\u5ba3\u6000\u79d8\u4e66\u7684\u7f57\u4e30\u7984\u8033\u4e2d\uff0c\u9042\u5c55\u5f00\u8c03\u67e5\uff0c\u7ed3\u679c\u67e5\u65e0\u6b64\u4e8b\u3002\u4f46\u9020\u8c23\u4e00\u5f20\u5634\uff0c\u8f9f\u8c23\u8dd1\u65ad\u817f\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6797\u6cf0\u66fe\u8df3\u8fdb\u9ec4\u6d77\u4e5f\u6d17\u4e0d\u6e05\u4e86\u3002</p>\r\n\r\n<h2><strong>\u4e8b\u4ef62\uff1a\u6d77\u6218\u6b63\u9163\uff0c\u8230\u957f\u5374\u7cbe\u795e\u5931\u5e38</strong></h2>\r\n\r\n<p>1894\u5e747\u670825\u65e5\uff0c\u4e2d\u65e5\u4e30\u5c9b\u6d77\u6218\u7206\u53d1\uff0c\u674e\u9e3f\u7ae0\u8c03\u505c\u907f\u6218\u7684\u5e0c\u671b\u5f7b\u5e95\u7834\u706d\u3002\u5728\u6b64\u4e4b\u9645\u6797\u6cf0\u66fe\u7533\u8bf7\u8f9e\u804c\uff0c\u6c14\u5934\u4e0a\u7684\u674e\u9e3f\u7ae0\u65ad\u7136\u62d2\u7edd\u5e76\u653e\u72e0\u8bdd\uff1a\u518d\u63d0\u4e0d\u5e72\u5c31\u780d\u4e86\u4f60\u3002\u6797\u6cf0\u66fe\u53ea\u5f97\u7ee7\u7eed\u7559\u5c97\uff0c\u76f4\u5230\u90a3\u573a\u51b3\u5b9a\u4e86\u4e2d\u65e5\u4e24\u56fd\u56fd\u547d\u7684\u5927\u4e1c\u6c9f\u6d77\u6218\u7206\u53d1\u3002</p>\r\n\r\n<p>1894\u5e749\u670817\u65e5\u4e0b\u5348\uff0c\u9e2d\u7eff\u6c5f\u53e3\u5916\u7684\u9ec4\u6d77\u6d77\u9762\u6d53\u70df\u6eda\u6eda\uff0c\u70ae\u706b\u5982\u96e8\uff0c\u51b2\u5929\u6c34\u67f1\u6b64\u8d77\u5f7c\u4f0f\u3002\u5f53\u4e2d\u56fd\u8230\u961f\u5148\u671f\u5931\u5229\uff0c\u63a5\u8fde\u635f\u5931\u4e86\u81f4\u8fdc\u3001\u7ecf\u8fdc\u7b49\u5de1\u6d0b\u8230\u540e\uff0c\u5c40\u52bf\u5168\u90fd\u7cfb\u5728\u4e24\u6761\u94c1\u7532\u8230\u4e0a\u3002\u65d7\u8230\u5b9a\u8fdc\u5728\u5317\u6d0b\u6d77\u519b\u53f3\u7ffc\u603b\u5175\u3001\u7ba1\u5e26\u5218\u6b65\u87fe\u7684\u6307\u6325\u4e0b\u5982\u4e2d\u6d41\u7825\u67f1\u62b5\u6297\u7740\u65e5\u8230\u75af\u72c2\u7684\u56f4\u653b\uff0c\u7136\u800c\u5728\u9547\u8fdc\u8230\u7684\u6307\u6325\u5854\u4e2d\u5374\u662f\u53e6\u4e00\u756a\u666f\u8c61\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/vrfckoRS33HUK_Ro9MyhpTlXdmPEma17hKKzv0DbzYz0AQAAQwEAAEpQ.jpg\" style=\"height:323px; width:500px\" />1894\u5e74\u4e2d\u65e5\u7532\u5348\u5927\u4e1c\u6c9f\u6d77\u6218 |&nbsp;1894\u5e7411\u6708\u300a\u4f26\u6566\u65b0\u95fb\u753b\u62a5\u300b\u6839\u636e\u76ee\u51fb\u8005\u63cf\u8ff0\u7ed8\u5236</p>\r\n\r\n<p>\u9547\u8fdc\u8230\u5e2e\u5e26\u5927\u526f\u3001\u7f8e\u56fd\u6d0b\u5458\u9a6c\u5409\u82ac\uff08McGiffin\uff09\u5728\u81ea\u5df1\u7684\u56de\u5fc6\u5f55\u4e2d\u8bb0\u9053\uff1a</p>\r\n\r\n<p>&ldquo;\u6211\u4e0d\u65ad\u5730\u542c\u5230\u4e00\u4e2a\u5947\u602a\u7684\u58f0\u97f3\u4ece\u6211\u4e0b\u65b9\u7684\u6307\u6325\u5854\u91cc\u4f20\u51fa&hellip;&hellip;\u6211\u60ca\u8bb6\u5730\u53d1\u73b0\u8fd9\u7adf\u662f\u6211\u90a3\u5c0a\u656c\u7684\u8230\u957f\u53d1\u51fa\u7684\uff01\u4ed6\u6b63\u8dea\u5012\u5728\u5730\uff0c\u4ee5\u6781\u5feb\u7684\u8bed\u901f\u7528\u4e2d\u6587\u5583\u5583\u81ea\u8bed&mdash;&mdash;\u7948\u7977\u7740\uff0c\u6216\u8005\u8bf4\u4e00\u8fb9\u7948\u7977\u4e00\u8fb9\u8bc5\u5492\u7740&mdash;&mdash;\u6bcf\u4e00\u53d1\u70ae\u5f39\u51fb\u4e2d\u519b\u8230\u65f6\u4ed6\u5c31\u50cf\u72d7\u4e00\u6837\u568e\u53eb\u8d77\u6765\u3002\u53ea\u8981\u6211\u8fd8\u6d3b\u7740\uff0c\u6211\u6c38\u8fdc\u4e5f\u5fd8\u4e0d\u4e86\u90a3\u5e55\u666f\u8c61\u548c\u90a3\u79cd\u58f0\u97f3\u3002&rdquo;&nbsp;[6]</p>\r\n\r\n<p>\u4e00\u4f4d\u53c2\u6218\u7684\u4e2d\u56fd\u6d77\u519b\u519b\u5b98\u5362\u6bd3\u82f1\u4e5f\u5370\u8bc1\u4e86\u6b64\u4e8b\uff1a</p>\r\n\r\n<p>&ldquo;\u5927\u4e1c\u6c9f\u4e4b\u5f79\uff0c\u6797\u60ca\u60f6\u65e0\u63aa\uff0c\u6545\u7f6e\u6d53\u9152\u4f5c\u957f\u996e\u5927\u9189\uff0c\u800c\u535c\u4e8e\u6218\u53f0\u4e4b\u4e0b\uff0c\u6240\u6709\u8c03\u9063\u6089\u8bac\u5176\u526f\u9a7e\u5e26\u6768\u7528\u9716\u4e4b\u8eab\u4e0a\uff0c\u5907\u8bf8\u6b7b\u5177\u5982\u8ff7\u836f\u6d0b\u70df\u4e4b\u7c7b\u3002&rdquo;&nbsp;[7]</p>\r\n\r\n<p>\u597d\u5728\u9547\u8fdc\u6709\u4e00\u4f4d\u975e\u5e38\u51fa\u8272\u82f1\u52c7\u7684\u526f\u8230\u957f\u6768\u7528\u9716\uff0c\u4ee3\u66ff\u8230\u957f\u5168\u6743\u6307\u6325\uff0c\u633d\u6551\u4e86\u519b\u8230\u5e76\u6539\u5199\u4e86\u6218\u5c40\uff1a\u5173\u952e\u65f6\u523b\u9547\u8fdc\u8fde\u53d1\u4e24\u679a305mm\u70ae\u5f39\u547d\u4e2d\u65e5\u672c\u65d7\u8230\u677e\u5c9b\uff0c\u5176\u4e2d\u4e00\u679a\u5f00\u82b1\u5f39\u5f15\u8d77\u677e\u5c9b\u5f39\u836f\u5e93\u7684\u4e25\u91cd\u6b89\u7206\uff0c\u81f4\u8239\u4e0a\u56db\u5206\u4e4b\u4e00\u6210\u5458\u4f24\u4ea1\uff0c\u6b66\u5668\u7cfb\u7edf\u3001\u6db2\u538b\u8235\u8f6e\u4e25\u91cd\u53d7\u635f\uff0c\u4e0d\u591a\u65f6\u4fbf\u96c6\u7ed3\u8230\u961f\u64a4\u51fa\u6218\u573a[6]\u3002\u82e5\u975e\u5982\u6b64\uff0c\u6797\u6cf0\u66fe\u8fd9\u6b21\u6e0e\u804c\u7684\u540e\u679c\u53ef\u80fd\u96be\u4ee5\u4f30\u91cf\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/wRlLiXkUVDf08NRomu6d2e1DCzbgp5ZWzwzNYgl5-tYgAwAAAAIAAEpQ.jpg\" style=\"height:410px; width:640px\" />\u65e5\u672c\u65d7\u8230\u677e\u5c9b\u88ab\u9547\u8fdc305mm\u4e3b\u70ae\u547d\u4e2d\u540e\u5de6\u8237\u7559\u4e0b\u7684\u5de8\u5927\u4f24\u53e3</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/yg-Eg5dSBvr4UFtzcCKtQkVAz7tdbRcIHIokDvWA3w3oAwAA5AMAAEpQ.jpg\" style=\"height:637px; width:640px\" />\u9ec4\u6d77\u6d77\u6218\u540e\u53d7\u4f24\u7684\u9547\u8fdc\u8230\u548c\u5979\u7684\u70ae\u52a1\u5b98\u5fb7\u56fd\u4eba\u54c8\u535c\u95e8&nbsp;|&nbsp;1895\u5e745\u67084\u65e5\u300a\u56fe\u7247\u62a5\u300b</p>\r\n\r\n<h2><strong>\u4e8b\u4ef63\uff1a\u8001\u53f8\u673a&ldquo;\u79d1\u76ee\u4e8c&rdquo;\u5931\u8d25</strong></h2>\r\n\r\n<p>1894\u5e7411\u670814\u65e5\u9ece\u660e\uff0c\u5a01\u6d77\u6e7e\u548c\u5218\u516c\u5c9b\u5939\u5cd9\u7684\u897f\u5317\u53e3\u4f20\u6765\u4e00\u58f0\u5de8\u54cd\u3002\u9547\u8fdc\u8230\u5e9e\u5927\u7684\u8eab\u8eaf\u5267\u70c8\u98a4\u6296\uff0c\u5411\u5de6\u503e\u659c\u3002\u7ecf\u9a8c\u4e30\u5bcc\u7684\u6307\u6325\u5b98\u4eec\u7acb\u5373\u542f\u52a8\u635f\u7ba1\u63aa\u65bd\uff1a\u5835\u6f0f\u3001\u62bd\u6c34\u3001\u5173\u6c34\u5bc6\u95e8&hellip;&hellip;\u7136\u800c\u6d77\u6c34\u8fd8\u662f\u6e90\u6e90\u4e0d\u65ad\u6f0f\u8fdb\u4e86\u5f39\u836f\u8231\u3001\u5e06\u8231\u3001\u7164\u8231\u3001\u9505\u7089\u8231\u53ca\u6c34\u529b\u673a\u8231\u3002</p>\r\n\r\n<p>\u636e\u540e\u6765\u7684\u8c03\u67e5\u62a5\u544a\uff0c\u4e8b\u6545\u539f\u56e0\u662f\u94c1\u7532\u8230\u5728\u9c7c\u8d2f\u5165\u6e2f\u65f6\uff0c\u524d\u9762\u5b9a\u8fdc\u4e03\u5343\u591a\u5428\u7684\u8eab\u8eaf\u4ea7\u751f\u5de8\u5927\u7684\u5206\u6c34\u538b\u63a8\u504f\u4e86\u6d6e\u6807\uff0c\u5bfc\u81f4\u540e\u9762\u7684\u9547\u8fdc\u8230\u5bf9\u822a\u9053\u5224\u65ad\u5931\u8bef\uff0c\u64e6\u8fc7\u9760\u8fd1\u5218\u516c\u5c9b\u4e00\u4fa7\u6d45\u6ee9\u7684\u7901\u77f3\uff0c\u81f4\u5de6\u8237\u8230\u5e95\u88c2\u574f\u64e6\u4f24\u8fbe8\u5904\u4e4b\u591a\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/GFvFBRI5pgq69RxTdZka_iFGoZpznWb9j19w6KH6LOA4BAAA3wEAAEpQ.jpg\" style=\"height:284px; width:640px\" />\u5982\u4eca\u5218\u516c\u5c9b\u897f\u5317\u53e3\u666f\u8c61\u3002\u524d\u65b9\u5c31\u662f\u5f53\u5e74\u9547\u8fdc\u89e6\u7901\u4e00\u5e26\u3002\u4f4d\u4e8e\u5317\u53e3\u5904\u7684\u4e24\u6845\u4e24\u70df\u56f1\u8239\u5f71\u662f\u6b63\u8981\u4e0a\u575e\u6cb9\u5e95\u76841:1\u590d\u539f\u5b9a\u8fdc\u8230\u6a21\u578b\uff0c\u540c\u7ea7\u7684\u9547\u8fdc\u8230\u5916\u5f62\u4e0e\u4e4b\u9177\u4f3c\uff0c\u604d\u60da\u6709\u79cd\u5386\u53f2\u91cd\u73b0\u7684\u5e7b\u89c9 | \u968b\u4e1c\u5347\u5148\u751f2016\u5e74\u6444\u4e8e\u5a01\u6d77\u5317\u5c71\u5634</p>\r\n\r\n<p>\u5927\u4e1c\u6c9f\u6d77\u6218\u5df2\u635f\u5931\u4e86\u6570\u6761\u5de1\u6d0b\u8230\uff0c\u4e24\u6761\u94c1\u7532\u8230\u6210\u4e86\u6e05\u653f\u5e9c\u6700\u540e\u7684\u5a01\u6151\u529b\u91cf\u3002\u5176\u4e2d\u4e00\u6761\u7adf\u5728\u8282\u9aa8\u773c\u4e0a\u51fa\u4e86\u4e25\u91cd\u4e8b\u6545\uff0c\u66f4\u7cdf\u7684\u662f\uff0c\u65e5\u672c\u9646\u519b\u5373\u5c06\u5728\u65c5\u987a\u767b\u9646\uff0c\u9547\u8fdc\u65e0\u6cd5\u524d\u5f80\u552f\u4e00\u62e5\u6709\u5e72\u575e\u7684\u65c5\u987a\u6e2f\u4fee\u7406\u3002</p>\r\n\r\n<p>\u90a3\u5929\u7684\u5165\u53e3\u6307\u6325\u7684\u662f\u7ba1\u5e26\u6797\u6cf0\u66fe\u3002\u4ed6\u4f5c\u4e3a\u4e2d\u56fd\u7b2c\u4e00\u4ee3\u6d77\u519b\u519b\u5b98\u7684\u4f7c\u4f7c\u8005\u66fe\u6218\u80dc\u5730\u4e2d\u6d77\u7684\u98d3\u98ce\u3001\u5370\u5ea6\u6d0b\u7684\u65e0\u8865\u7ed9\u822a\u7ebf\u3001\u82cf\u4f0a\u58eb\u8fd0\u6cb3\u7684\u6d45\u7a84\u6cb3\u9053\u4ee5\u53ca\u671d\u9c9c\u6d77\u5cb8\u7684\u6697\u7901\u9669\u6ee9\uff0c\u6700\u540e\u5374\u683d\u5728\u4e86\u5f80\u8fd4\u591a\u8d9f\u7684\u5218\u516c\u5c9b\u5317\u53e3\uff0c\u800c\u4e14\u90a3\u6b21\u8fd4\u6e2f\u53ea\u5f00\u51fa\u4e868\u8282\u822a\u901f\uff081\u8282=\u65f6\u901f1\u6d77\u91cc&asymp;\u65f6\u901f1.852\u516c\u91cc\uff09\u3002\u5bb6\u95e8\u53e3\u4f4e\u901f\u89e6\u7901\uff0c\u4e0d\u548c\u8001\u53f8\u673a\u5012\u8f66\u5165\u5e93\u5931\u8d25\u4e00\u6837\u4e48\uff1f\u4ee5\u81f3\u4e8e\u4e8b\u6545\u4e0a\u62a5\u540e\u674e\u9e3f\u7ae0\u7684\u7b2c\u4e00\u53cd\u5e94\u662f\uff0c\u96be\u4e0d\u6210\u6797\u662f&hellip;&hellip;\u6545\u610f\u635f\u574f\uff1f</p>\r\n\r\n<p>\u867d\u7136\u6700\u540e\u7684\u8c03\u67e5\u8bc1\u660e\u4e86\u6797\u603b\u5175\u7684\u6e05\u767d\uff0c\u4f46\u5173\u4e8e\u4ed6\u80c6\u5c0f\u8f7b\u751f\u7684\u8bba\u8c03\u5374\u6210\u4e3a\u4ed6\u6c38\u4e45\u7684\u8eab\u540e\u4e4b\u540d\u3002</p>\r\n\r\n<h2><strong>\u6797\u603b\u5175\u75c5\u60c5\u5256\u6790</strong></h2>\r\n\r\n<p>\u90a3\u4e48\uff0c\u6797\u6cf0\u66fe\u4e3a\u4ec0\u4e48\u4f1a\u5728\u6218\u524d\u51fa\u73b0\u5982\u6b64\u4e25\u91cd\u7684\u95ee\u9898\uff1f\u4e0d\u52303\u4e2a\u6708\u65f6\u95f4\uff0c\u4ed6\u4ece\u7a33\u5065\u5fe0\u8c28\u7684\u5206\u8230\u961f\u53f8\u4ee4\uff0c\u53d8\u6210\u4e00\u4e2a\u7cbe\u795e\u8fd0\u52a8\u6027\u6fc0\u8d8a\u7684\u6218\u6597\u5e94\u6fc0\u8005\uff1b2\u4e2a\u6708\u540e\u51fa\u73b0\u6291\u90c1\u75c7\u5178\u578b\u7684&ldquo;\u4e09\u4f4e&rdquo;\uff08\u60c5\u7eea\u4f4e\u843d\uff0c\u601d\u7ef4\u8fdf\u7f13\uff0c\u610f\u5fd7\u51cf\u4f4e\u3002\uff09&ldquo;\u4e09\u65e0&rdquo;\uff08\u65e0\u7528\u3001\u65e0\u52a9\u548c\u65e0\u671b\uff09&ldquo;\u4e09\u81ea&rdquo;\uff08\u81ea\u8d23\u3001\u81ea\u7f6a\u548c\u81ea\u6740\uff09\u7279\u5f81\uff0c\u662f\u4ed6\u6709\u6027\u683c\u7f3a\u9677\uff0c\u5728\u6218\u6597\u4e2d\u53d8\u5f97\u80c6\u5c0f\u6291\u90c1\uff0c\u8fd8\u662f\u53e6\u6709\u539f\u56e0\uff1f</p>\r\n\r\n<h3>\u6218\u6597\u5e94\u6fc0</h3>\r\n\r\n<p class=\"editors-note\"><strong>\u6218\u6597\u5e94\u6fc0\uff08combat&nbsp;stress&nbsp;reaction, CSR\uff09</strong>\u662f\u5f53\u524d\u5e38\u7528\u6765\u8bc4\u4ef7\u519b\u4eba\u5728\u6218\u6597\u6216\u975e\u6218\u4e89\u519b\u4e8b\u884c\u52a8\u4e2d\u7ecf\u5386\u538b\u529b\u6027\u4e8b\u4ef6\u540e\u53cd\u5e94\u7684\u519b\u4e8b\u5fc3\u7406\u5b66\u672f\u8bed\u3002\u5173\u4e8e\u6218\u6597\u5e94\u6fc0\uff0c\u5386\u53f2\u4e0a\u56e0\u8ba4\u8bc6\u7a0b\u5ea6\u4e0d\u540c\u4ea7\u751f\u8fc7\u4e0d\u540c\u540d\u79f0\u3002\u4e00\u6218\u65f6\u88ab\u79f0\u4f5c&ldquo;\u70ae\u5f39\u4f11\u514b&rdquo;&ldquo;\u6218\u4e89\u795e\u7ecf\u75c7&rdquo;\u6216&ldquo;Da Costa\u7efc\u5408\u5f81&rdquo;\u7b49\u3002\u8fd9\u4e9b\u672f\u8bed\u5728\u4e8c\u6218\u65f6\u4ecd\u88ab\u5927\u91cf\u4f7f\u7528\uff0c\u76f4\u5230\u8d8a\u6218\u65f6\u624d\u9010\u6e10\u7edf\u4e00\u4e3a\u6218\u6597\u5e94\u6fc0\u3002\u6d77\u6e7e\u6218\u4e89\u671f\u95f4\uff0c\u5305\u62ec\u521b\u4f24\u540e\u5e94\u6fc0\u969c\u788d\uff08PTSD\uff09\u5728\u5185\u7684\u4e00\u7cfb\u5217\u75c7\u72b6\u90fd\u88ab\u56ca\u62ec\u5728\u6218\u6597\u5e94\u6fc0\u53cd\u5e94\u7684\u8303\u7574\u3002[8]&nbsp;</p>\r\n\r\n<p>\u6218\u6597\u5e94\u6fc0\u53cd\u5e94\u53ef\u5206\u4e3a\u516d\u5927\u7c7b\u578b\uff1a<strong>\u8eaf\u4f53\u3001\u8ba4\u77e5\u3001\u884c\u4e3a\u3001\u60c5\u611f\u3001\u8fc7\u5931\u6027\u53ca\u9002\u5e94\u6027\u7684\u75c7\u72b6</strong>\u3002\u5982\u679c\u5bf9\u53f7\u5165\u5ea7\u7684\u8bdd\uff0c\u6797\u6cf0\u66fe\u57fa\u672c\u5c97\u4f4d\u6280\u80fd\u4e27\u5931\uff0c\u8bed\u901f\u52a0\u5feb\uff0c\u662f<strong>\u6218\u6597\u5e94\u6fc0</strong><strong>\u7684</strong><strong>\u884c\u4e3a\u75c7\u72b6</strong>\uff1b\u7126\u8651\u3001\u6613\u6fc0\u60f9\u3001\u6b47\u65af\u5e95\u91cc\uff0c\u662f\u5178\u578b\u7684<strong>\u60c5\u611f\u75c7\u72b6</strong>\uff1b\u4e25\u91cd\u7684\u632b\u8d25\u611f\u63d0\u793a\u51fa\u73b0\u4e86<strong>\u8ba4\u77e5\u65b9\u9762\u7684\u75c7\u72b6</strong>\uff1b\u65e0\u89c6\u81ea\u5df1\u7684\u8230\u957f\u8eab\u4efd\uff0c\u653e\u5f03\u6307\u6325\u5e76\u8bc9\u8bf8\u9ebb\u9189\u54c1\u548c\u9152\u7cbe\uff0c\u5df2\u7ecf\u4e0a\u5347\u5230\u4e25\u91cd\u7684<strong>\u8fc7\u5931\u884c\u4e3a</strong>\u3002</p>\r\n\r\n<p>\u4e0d\u8fc7\uff0c\u6218\u6597\u5e94\u6fc0\u7684\u9ad8\u9891\u4eba\u7fa4\u662f\u5e74\u8f7b\u3001\u4f4e\u9636\u4e14\u672a\u89c1\u8bc6\u6218\u9635\u7684\u58eb\u5175\u3002\u6797\u6cf0\u66fe\u867d\u4e5f\u662f\u7b2c\u4e00\u6b21\u7ecf\u5386\u5927\u6d77\u6218\uff0c\u4f46\u6bd5\u7adf\u4e0d\u662f\u5bf9\u6d77\u6218\u4e00\u65e0\u6240\u77e5\u7684\u65b0\u5175\u3002\u4ed6\u4ece\u5c11\u5e74\u65f6\u5c31\u63a5\u53d7\u7684\u7cfb\u7edf\u6d77\u519b\u6559\u80b2\uff0c\u5bf9\u6d77\u6218\u7684\u60e8\u70c8\u6709\u5145\u5206\u7684\u5fc3\u7406\u9884\u671f\uff0c\u7136\u800c\u75c7\u72b6\u5374\u6bd4\u666e\u901a\u58eb\u5175\u4e25\u91cd\uff1b\u666e\u901a\u7684\u6218\u6597\u5e94\u6fc0\u60a3\u8005\u5c5e\u4e8e\u6025\u6027\u5fc3\u7406\u5e94\u6fc0\u969c\u788d\uff0c85%\u5728\u4e09\u5929\u5185\u901a\u8fc7\u4f11\u606f\u4f1a\u597d\u8f6c[9]\uff0c\u800c\u6797\u6cf0\u66fe\u7684\u75c7\u72b6\u6301\u7eed\u6027\u52a0\u91cd\uff0c\u76f4\u5230\u53c8\u4e00\u6b21\u91cd\u5927\u4e8b\u6545\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/91mmixGdWpRBfLzgfF5ogjGhnH6LhkQ7HFZLm5O2o5E4BAAAYAIAAEpQ.jpg\" style=\"height:360px; width:640px\" />\u65b0\u5175\u7b2c\u4e00\u6b21\u7ecf\u5386\u5927\u6218\u5f80\u5f80\u4f1a\u51fa\u73b0\u6025\u6027\u5fc3\u7406\u5e94\u6fc0\u969c\u788d | \u300a\u6bd4\u5229&middot;\u6797\u6069\u7684\u4e2d\u573a\u6218\u4e8b\u300b</p>\r\n\r\n<p>\u6839\u636e\u7ecf\u5178\u7684<strong>\u6218\u6597-\u9003\u8dd1(Fight-or-flight response)&nbsp;</strong>\u884c\u4e3a\u5b66\u6a21\u578b\uff1a\u52a8\u7269\u9762\u5bf9\u5e94\u6fc0\u539f\u901a\u5e38\u6709\u4e24\u79cd\u9009\u62e9\uff1a\u8981\u4e48\u9003\u907f\u8981\u4e48\u53cd\u6297\u3002\u4f46\u6709\u610f\u601d\u7684\u662f\uff0c\u7ecf\u8fc7\u6291\u90c1\u9020\u6a21\u7684\u5927\u9f20\u9762\u5bf9\u5c3e\u60ac\u3001\u7535\u51fb\u3001\u5f3a\u8feb\u6e38\u6cf3\u7b49\u5e94\u6fc0\u539f\u7b49\uff0c\u5f80\u5f80\u5448\u73b0\u51fa\u4e0d\u62b5\u6297\u4e0d\u9003\u907f\u7684\u9ebb\u6728\u884c\u4e3a[10]\uff0c\u8fd9\u65f6\u5f88\u96be\u754c\u5b9a\u5b83\u4eec\u5230\u5e95\u662f\u52c7\u6562\u8fd8\u662f\u80c6\u5c0f\u3002</p>\r\n\r\n<p>\u4eba\u7684\u60c5\u51b5\u672c\u8d28\u4e0a\u7c7b\u4f3c\u3002\u6d77\u6218\u4e2d\u8bf8\u591a\u5b98\u5175\u4e0d\u60e7\u6b7b\u4f24\u987d\u5f3a\u5e94\u6218\uff0c\u6beb\u65e0\u7591\u95ee\u53ef\u8c13\u52c7\u6562\uff1b\u4e5f\u6709\u4eba\u4e34\u9635\u8131\u9003\u6216\u5f53\u7f29\u5934\u4e4c\u9f9f\uff0c\u5982\u6d4e\u8fdc\u8230\u7ba1\u5e26\u65b9\u4f2f\u8c26\u3001\u9547\u8fdc\u8230\u9a7e\u9a76\u5927\u526f\u738b\u73cd\uff0c\u8fd9\u662f\u65e0\u4e89\u8bae\u7684\u80c6\u5c0f\u3002\u800c\u6797\u6cf0\u66fe\u7684\u884c\u4e3a\u548c\u8fd9\u4e24\u79cd\u90fd\u4e0d\u4e00\u6837\uff0c\u66f4\u50cf\u7c7b\u4f3c\u6291\u90c1\u6a21\u578b\u52a8\u7269\u90a3\u79cd\u7cbe\u795e\u969c\u788d\u3002\u90a3\u4e48\uff0c\u662f\u4ec0\u4e48\u5f15\u8d77\u4e86\u6797\u6cf0\u66fe\u7684\u7cbe\u795e\u53d8\u5316\u5462\uff1f</p>\r\n\r\n<h3>\u975e\u6218\u6597\u538b\u529b</h3>\r\n\r\n<p>\u6218\u6597\u5e94\u6fc0\u53cd\u5e94\u901a\u5e38\u5f88\u590d\u6742\uff0c\u8bb8\u591a\u6848\u4f8b\u8868\u660e\u5e76\u975e\u5355\u7eaf\u7684\u6218\u6597\u538b\u529b\u5f15\u8d77\u3002\u8206\u8bba\u538b\u529b\u662f\u4e0d\u53ef\u5ffd\u89c6\u7684\u4e00\u9879\u3002\u4e2d\u56fd\u53e4\u4ee3\u957f\u5e73\u4e4b\u6218\uff0c\u4e00\u4e2a\u53cd\u95f4\u8ba1\u51e0\u4e4e\u5960\u5b9a\u6218\u5c40\uff1b\u7532\u5348\u6218\u524d\u7684\u8d1f\u9762\u8206\u8bba\u6700\u7ec8\u4e5f\u4ea7\u751f\u4e86\u83ab\u5927\u5f71\u54cd\uff0c\u8fd9\u4ef6\u4e8b\u8ffd\u8e2a\u8d77\u6765\u8fd8\u6709\u4e9b\u8010\u4eba\u5bfb\u5473\u3002\u5982\u4eca\u53f2\u5b66\u754c\u8bc1\u5b9e\u5f53\u5e74\u65e5\u672c\u95f4\u8c0d\u5728\u6218\u524d\u6d3b\u52a8\u76f8\u5f53\u7316\u7357\uff0c\u5c31\u8fde\u674e\u9e3f\u7ae0\u7684\u5916\u7525\u5f20\u58eb\u73e9\u90fd\u4e0e\u65e5\u672c\u95f4\u8c0d\u77f3\u5ddd\u4f0d\u4e00\u7275\u8fde\u4e0d\u6e05\u3002\u8fd9\u5c42\u5173\u7cfb\u5bf9\u6797\u6cf0\u66fe\u4e8b\u4ef6\u8d77\u5230\u4ec0\u4e48\u4f5c\u7528\u5df2\u4e0d\u5f97\u800c\u77e5\uff0c\u4f46\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\uff0c\u5173\u952e\u65f6\u671f\u67d0\u4e9b\u52bf\u529b\u84c4\u610f\u6311\u8845\u6fc0\u60f9\u9ad8\u9636\u519b\u4eba\u5f15\u8bf1\u4ed6\u4eec\u51fa\u73b0\u8fc7\u5931\u884c\u4e3a\uff0c\u662f\u4e00\u9879\u519b\u4e8b\u5fc3\u7406\u5b66\u8303\u7574\u7684\u5e38\u89c4\u64cd\u4f5c\u3002</p>\r\n\r\n<p>\u519b\u4eba\u7684\u627f\u53d7\u529b\u6709\u65f6\u65e0\u6bd4\u575a\u97e7\uff0c\u4f46\u6709\u65f6\u53ef\u5d29\u4e8e\u7ea4\u6beb\u4e4b\u95f4\u30021996\u5e74\uff0c\u65f6\u4efb\u7f8e\u6d77\u519b\u4f5c\u6218\u90e8\u957f\u7684\u5e03\u5c14\u8fbe\uff08Jeremy Boorda\uff09\u56db\u661f\u4e0a\u5c06\u5728\u529e\u516c\u5ba4\u4ee5\u624b\u67aa\u81ea\u6215\u3002\u4ed6\u662f\u4e00\u4f4d\u53c2\u52a0\u8fc7\u8d8a\u6218\u548c\u6ce2\u9ed1\u6218\u4e89\u4e14\u529f\u52cb\u5353\u8457\u7684\u519b\u4eba\uff0c\u4e5f\u662f\u7f8e\u56fd\u4e8c\u767e\u591a\u5e74\u6765\u552f\u4e00\u4ece\u6c34\u5175\u6210\u957f\u4e3a\u6d77\u519b\u603b\u53f8\u4ee4\u7684\u4f20\u5947\u519b\u4eba\uff0c\u81ea\u6740\u539f\u56e0\u4ec5\u4ec5\u662f\u56e0\u4e3a\u88ab\u5a92\u4f53\u8d28\u7591\u4e0d\u5177\u5907\u4f69\u6234&ldquo;V\u5b57\u52cb\u7ae0&rdquo;\u7684\u8d44\u683c\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/7-aTb1Q8ycOMAV1M2P6WNJ7OBi5_k1Ui5YEUNt8Q1HMIBAAAAAUAAEpQ.jpg\" style=\"height:794px; width:640px\" />1996\u5e745\u670816\u65e5\uff0c\u7f8e\u56fd\u6d77\u519b\u4f5c\u6218\u90e8\u957f\u6770\u91cc\u7c73&middot;\u8fc8\u514b\u5c14&middot;\u5e03\u5c14\u8fbe\u6d77\u519b\u4e0a\u5c06\u5728\u529e\u516c\u5ba4\u91cc\u81ea\u6740 |&nbsp;Wikimedia Commons</p>\r\n\r\n<p>\u519b\u4eba\u7684\u804c\u4e1a\u7279\u5f81\u51b3\u5b9a\u4ed6\u4eec\u91cd\u89c6\u8363\u8a89\u751a\u4e8e\u751f\u547d\uff0c\u8fd9\u6070\u6070\u6210\u4e3a\u4ed6\u4eec\u7684\u8f6f\u808b\u3002\u5e03\u5c14\u8fbe\u4e0a\u5c06\u81ea\u6740\u4e8b\u4ef6\u5f15\u8d77\u7f8e\u56fd\u56fd\u9632\u90e8\u7684\u9ad8\u5ea6\u91cd\u89c6\uff0c\u542f\u52a8\u4e86\u4ee5\u519b\u4e8b\u5fc3\u7406\u5b66\u4e3a\u57fa\u7840\u7684\u5404\u519b\u79cd\u7684\u81ea\u6740\u9884\u9632\u8ba1\u5212\u548c\u76f8\u5173\u7814\u7a76\u3002\u6765\u81ea\u7f8e\u519b\u533b\u5b66\u673a\u6784\u7684\u5404\u9879\u6570\u636e\u663e\u793a\uff0c\u519b\u4eba\u81ea\u6740\u6848\u4f8b\u4e2d\u88ab\u8bca\u65ad\u4e3a\u6291\u90c1\u60c5\u7eea\u3001\u4eba\u683c\u969c\u788d\u6216\u9002\u5e94\u4e0d\u826f\u7684\u6bd4\u4f8b\u5e38\u8d85\u8fc790%\u3002[11-12]</p>\r\n\r\n<p>\u6797\u6cf0\u66fe\u53c8\u662f\u4ec0\u4e48\u60c5\u51b5\uff1f</p>\r\n\r\n<p>\u5173\u4e8e\u6291\u90c1\u6027\u4eba\u683c\u7684\u7814\u7a76\u5341\u5206\u9a73\u6742\uff0c\u65e9\u5148\u8ba4\u4e3a\u56fa\u6267\u3001\u6c89\u9ed8\u3001\u654f\u611f\u3001\u505a\u4e8b\u4e00\u4e1d\u4e0d\u82df\u3001\u7ae5\u5e74\u5931\u53bb\u7236\u6bcd\u3001\u4ece\u5c0f\u6839\u690d\u63d0\u9ad8\u5bb6\u5ead\u793e\u4f1a\u5730\u4f4d\u7684\u5fd7\u5411\u3001D\u578b\u4eba\u683c\uff08\u5177\u6709\u6d88\u6781\u60c5\u611f\u3001\u793e\u4ea4\u6291\u5236\u7279\u70b9\uff09\u7b49\u7279\u5f81\u4e0e\u6291\u90c1\u75c7\u53d1\u75c5\u7387\u6709\u4e00\u5b9a\u5173\u8054\u3002\u4f5c\u4e3a\u6797\u5219\u5f90\u4f84\u5b59\u7684\u6797\u6cf0\u66fe\u51fa\u8eab\u540d\u95e8\u3001\u81ea\u5e7c\u7236\u6bcd\u53cc\u4ea1\u3001\u5bb6\u5883\u8270\u8f9b\u5e76\u80a9\u8d1f\u4e2d\u5174\u5bb6\u65cf\u7684\u4f7f\u547d\u3001\u5e73\u65e5&ldquo;<strong>\u6027\u6c89\u9ed8\uff0c\u5be1\u8a00\u7b11\uff0c\u80fd\u5fcd\u4eba\u4e4b\u6240\u4e0d\u80fd\u5fcd</strong>&rdquo;\uff0c\u4e0a\u8ff0\u7279\u5f81\u7adf\u7136\u5168\u4e2d\uff1b\u7136\u800c\uff0c\u53e6\u4e00\u4e9b\u4e0e\u6291\u90c1\u75c7\u8d1f\u76f8\u5173\u7684\u4eba\u683c\u7279\u5f81\uff0c\u5982\u968f\u548c\u3001\u8d23\u4efb\u5fc3[13]\u7b49\uff0c\u6709<strong>&ldquo;\u5f85\u4e0b\u4ec1\u6055&rdquo;&ldquo;\u5b58\u5fc3\u6148\u539a&rdquo;&ldquo;\u6027\u884c\u5fe0\u8c28&rdquo;</strong>\u4e4b\u8a89\u7684\u6797\u6cf0\u66fe\u4e5f\u90fd\u62e5\u6709\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4eca\u5929\u5173\u4e8e\u6291\u90c1\u6027\u4eba\u683c\u7684\u8bba\u8c03\u683c\u5916\u4e25\u8c28\uff0c\u8fc7\u53bb\u8ba4\u4e3a\u7684\u8bb8\u591a\u7279\u5f81\u5982\u4eca\u90fd\u4e0d\u88ab\u8ba4\u4e3a\u662f\u6291\u90c1\u75c7\u7684\u9884\u6d4b\u56e0\u5b50\u3002\u6240\u4ee5\u4ec5\u51ed\u53f2\u6599\u4e2d\u6797\u6cf0\u66fe\u5448\u73b0\u7684\u4eba\u683c\u7279\u70b9\u8fd8\u771f\u65e0\u6cd5\u5f97\u51fa\u4ed6\u662f\u5426\u5bb9\u6613\u60a3\u6291\u90c1\u75c7\u7684\u7ed3\u8bba\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/_oufuIHboriYJM6OGJO3k_NZ_kNjiKlCk22kxKWRCJ7oAwAAHQIAAEpQ.jpg\" style=\"height:346px; width:640px\" />\u65f6\u81f3\u4eca\u65e5\uff0c\u4f9d\u7136\u6ca1\u6709\u9488\u5bf9\u6291\u90c1\u75c7\u7684\u7279\u5f02\u6027\u68c0\u67e5\u9879\u76ee\uff0c\u6291\u90c1\u75c7\u7684\u8bca\u65ad\u9664\u4e86\u9700\u8981\u8eaf\u4f53\u68c0\u67e5\u53ca\u795e\u7ecf\u7cfb\u7edf\u68c0\u67e5\u5916,\u8fd8\u9700\u8981\u8f85\u52a9\u68c0\u67e5\u53ca\u5b9e\u9a8c\u5ba4\u68c0\u67e5 | \u7535\u5f71\u300a\u4e00\u5ff5\u65e0\u660e\u300b\u4e2d\u60a3\u6709\u6291\u90c1\u75c7\u7684\u963f\u4e1c</p>\r\n\r\n<p>\u4ed6\u7684\u60c5\u51b5\uff0c\u66f4\u53ef\u80fd\u662f\u4e00\u7cfb\u5217\u5916\u754c\u56e0\u7d20\uff0c\u5305\u62ec\u9876\u7aef\u56f0\u60d1\u548c\u594b\u6597\u76ee\u6807\u574d\u584c\u9020\u6210\u7684\u5fc3\u7406\u969c\u788d\u3002\u4ece\u53f2\u6599\u4e2d\u770b\u5230\uff0c\u6797\u6cf0\u66fe\u548c\u540c\u5b66\u4eec\u521a\u4ece\u8239\u653f\u5b66\u5802\u6bd5\u4e1a\u65f6\u66fe\u6709\u8c6a\u8a00\u58ee\u8bed\uff1a&ldquo;\u4ece\u4eca\u800c\u540e\uff0c\u8981\u53bb\u5bf9\u4ed8\u98d3\u98ce\uff0c\u63a7\u5236\u72c2\u6d6a\uff0c\u7aa5\u6d4b\u65e5\u661f\u7684\u884c\u52a8\uff0c\u4e86\u89e3\u66b4\u98ce\u7684\u89c4\u5f8b&hellip;&hellip;\u8ba9\u6700\u53ef\u6015\u7684\u56f0\u96be\u53d8\u6210\u5e73\u6613\u3001\u6700\u9669\u6076\u7684\u60c5\u51b5\u6210\u4e3a\u9759\u8c27&hellip;&hellip;\u6211\u4eec\u7684\u7231\u56fd\u5fc3\u5c06\u4e0d\u51cf\u5c11&rdquo;\uff0c\u591a\u5e74\u4ee5\u540e\uff0c\u4ed6\u5374\u5728\u65e5\u8bb0\u4e2d\u81ea\u5632\u4e3a&ldquo;\u4e00\u4ecb\u6b66\u592b&rdquo;\uff0c\u632b\u8d25\u6781\u5f3a\u5730\u611f\u53f9\u62a5\u56fd\u65e0\u95e8\uff0c\u5982\u6b64\u5929\u58e4\u4e4b\u522b\u771f\u6709\u4e9b\u8bbd\u523a\u3002\u6797\u6cf0\u66fe\u5b57\u91cc\u884c\u95f4\u900f\u51fa\u7684\u662f\uff1a\u6211\u539f\u4ee5\u4e3a\u81ea\u5df1\u6210\u4e86\u56fd\u4e4b\u5e72\u57ce\uff0c\u54ea\u77e5\u5230\u5934\u6765\u8fd8\u662f\u4e00\u4ecb\u6b66\u592b\uff0c\u4e3a\u56fd\u5907\u6218\u4e0d\u9057\u4f59\u529b\uff0c\u8c01\u77e5\u4e3b\u5e05\u548c\u671d\u5ef7\u5374\u5404\u6000\u5fc3\u601d\u3002\u8881\u4e16\u51ef\u4e09\u7535\u4e5e\u5f52\uff0c\u53f6\u5fd7\u8d85\u63a8\u8bff\u8d23\u4efb\uff0c\u6211\u5162\u5162\u4e1a\u4e1a\u53cd\u6210\u4e86&ldquo;\u80c6\u602f\u5f20\u7687&rdquo;&ldquo;\u754f\u65e5\u9041\u8d70&rdquo;&hellip;&hellip;\u62a5\u56fd\u65e0\u95e8\uff0c\u7f6a\u7531\u6211\u8d77\uff0c\u6d3b\u7740\u592b\u590d\u4f55\u76ca\uff1f\u6797\u6cf0\u66fe\u7a7a\u6000\u804c\u4e1a\u519b\u4eba\u7684\u8d23\u4efb\u611f\uff0c\u4ee5\u53ca\u4e00\u8154\u62a5\u56fd\u4e4b\u5fd7\uff0c\u5374\u56e0\u6e05\u653f\u5e9c\u7684\u660f\u8069\u65e0\u80fd\u548c\u9886\u5bfc\u7684\u8f7b\u7387\u8bef\u5224\uff0c\u6210\u4e86\u4e00\u5df1\u79c1\u5229\u4e0b\u7684\u70ae\u7070\uff0c\u8c6a\u60c5\u58ee\u5fd7\u5316\u4e3a\u5fc3\u7070\u610f\u51b7\u548c\u65e0\u52a9\u7edd\u671b\u3002</p>\r\n\r\n<h3>\u9152\u7cbe\u3001\u9ebb\u9189\u54c1\u6ee5\u7528</h3>\r\n\r\n<p>\u6700\u540e\uff0c\u9020\u6210\u4ed6\u75c5\u7a0b\u7684\u52a0\u901f\u7684\u9664\u4e86\u6218\u6597\u3001\u975e\u6218\u6597\u538b\u529b\u5916\uff0c\u5f88\u53ef\u80fd\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\u3002</p>\r\n\r\n<p>1770\u5e74\u82f1\u56fd\u7687\u5bb6\u6d77\u519b\u5f00\u59cb\u7ed9\u897f\u5370\u5ea6\u8230\u961f\u7684\u6c34\u624b\u5b9a\u91cf\u914d\u7ed9\u63ba\u6c34\u7684\u6717\u59c6\u9152\u6216\u5a01\u58eb\u5fcc\uff0c\u6807\u5fd7\u7740\u7687\u5bb6\u6d77\u519b\u5f00\u59cb\u6b63\u5f0f\u51cf\u5c11\u9ebb\u9189\u54c1\u4f9d\u8d56\u7684\u52aa\u529b\u3002\u55ef\uff0c\u4f60\u6ca1\u770b\u9519\uff0c\u8fd9\u662f\u7687\u5bb6\u6d77\u519b\u65e8\u5728\u964d\u4f4e\u5b98\u5175\u9152\u7cbe\u6ee5\u7528\u7684\u52aa\u529b\uff0c\u56e0\u4e3a\u90a3\u4e9b\u5bb6\u4f19\u4e4b\u524d\u90fd\u76f4\u63a5\u559d\u70c8\u9152\u3002</p>\r\n\r\n<p>\u8fc7\u53bb\u519b\u961f\u7684\u9152\u7cbe-\u9ebb\u9189\u54c1\u6ee5\u7528\u662f\u4e2a\u666e\u904d\u95ee\u9898\u3002\u7f8e\u56fd\u5185\u6218\u4e2d\u9157\u9152\u548c\u5438\u98df\u9e26\u7247\u53f8\u7a7a\u89c1\u60ef\uff0c\u8d8a\u6218\u671f\u95f434%\u7684\u58eb\u5175\u5438\u98df\u8fc7\u5927\u9ebb\uff0c50%\u7684\u4eba\u670d\u8fc7\u6d77\u6d1b\u56e0\u3002\u519b\u4eba\u9762\u4e34\u7740\u5e73\u6c11\u4e0d\u4f1a\u9047\u5230\u7684\u5de8\u5927\u538b\u529b\uff0c\u8fd9\u79cd\u751f\u6d3b\u65b9\u5f0f\u672c\u8eab\u5c31\u662f\u9152\u7cbe\u548c\u9ebb\u9189\u54c1\u6ee5\u7528\u7684\u8bf1\u56e0\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/lpfPLf3Um_6KcqeoU9MZMMQ2cEoB5kQnUZbzcWgcW6s4BAAAYQIAAEpQ.jpg\" style=\"height:361px; width:640px\" />2013\u5e7414\u540d\u5fb7\u56fd\u9a7b\u963f\u5bcc\u6c57\u58eb\u5175\u56e0\u4e3a\u9157\u9152\u95f9\u4e8b\u88ab\u9063\u8fd4 | \u5fb7\u56fd\u300a\u56fe\u7247\u62a5\u300b</p>\r\n\r\n<p>\u5317\u6d0b\u6d77\u519b\u540c\u6837\u6ca1\u80fd\u89e3\u51b3\u8fd9\u4e00\u95ee\u9898\uff0c\u5bfc\u81f4\u6797\u6cf0\u66fe\u80fd\u591f&ldquo;\u7f6e\u6d53\u9152&rdquo;&ldquo;\u5907\u8bf8\u6b7b\u5177\u5982\u8ff7\u836f\u6d0b\u70df&rdquo;\u3002\u6d0b\u70df\u662f\u9e26\u7247\uff0c\u8ff7\u836f\u6216\u8bb8\u662f\u9ebb\u9189\u5242\uff0c\u751a\u81f3\u53ef\u80fd\u662f\u5f53\u65f6\u5df2\u7ecf\u5f00\u59cb\u4f7f\u7528\u7684\u53e4\u67ef\u78b1\u3002\u53ef\u4ee5\u8bd5\u60f3\uff0c\u6797\u5728\u60c5\u7eea\u4f4e\u843d\u65f6\u6cbe\u67d3\u4e0a\u8fd9\u4e9b\u4e1c\u897f\u4ee5\u81f3\u6c89\u8ff7\uff0c\u5728\u90a3\u4e2a\u65f6\u68d8\u4eba\u56f0\u4e4b\u9645\uff0c\u52a0\u901f\u4e86\u4ed6\u7684\u7cbe\u795e\u72b6\u51b5\u7684\u6076\u5316\u3002</p>\r\n\r\n<p>\u6797\u6cf0\u66fe\u662f\u5426\u80c6\u5c0f\u6050\u6015\u8fd8\u4f1a\u7ee7\u7eed\u4e89\u8bae\u3002\u4f46\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\u4e16\u4e0a\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u662f\u7cbe\u795e\u4e0a\u7684\u5b8c\u4eba\uff0c\u9762\u5bf9\u6218\u4e89\u7684\u4eba\u6c38\u8fdc\u90fd\u5145\u6ee1\u4e0d\u786e\u5b9a\u6027\u3002\u6797\u6cf0\u66fe\u5c11\u5c06\u7684\u81ea\u6740\u4e8b\u4ef6\u8fc7\u5f80\u5df2\u4e45\uff0c\u5c31\u7b97\u4e0d\u80fd\u50cf\u5e03\u5c14\u8fbe\u4e0a\u5c06\u90a3\u6837\u5bf9\u519b\u4e8b\u5fc3\u7406\u5b66\u4ea7\u751f\u63a8\u52a8\u529b\uff0c\u5982\u679c\u80fd\u6210\u4e3a\u4e00\u4e2a\u5c0f\u5c0f\u7684\u53f2\u9274\uff0c\u5c31\u662f\u7ffb\u8fd9\u7b14\u65e7\u8d26\u7684\u610f\u4e49\u3002\uff08\u7f16\u8f91\uff1aYuki\uff09</p>\r\n\r\n<p>\u672c\u6587\u611f\u8c22\u6d77\u519b\u53f2\u7814\u7a76\u8005\u9648\u60a6\u3001\u65b9\u79be\u3001\u5409\u8fb0\u3001\u674e\u5ae3\u3001\u674e\u7389\u751f\u3001\u5218\u70dc\u8d6b\u3001\u5218\u81f4\u3001\u5415\u671b\u8212\u3001\u968b\u4e1c\u5347\u3001\u5b59\u5efa\u519b\u3001\u738b\u9e64\u3001\u5f20\u9ece\u6e90\u7684\u652f\u6301\u3002</p>\r\n\r\n<h2 class=\"references\">\u53c2\u8003\u6587\u732e\uff1a</h2>\r\n\r\n<div><span style=\"color:#808080\">[1]\u76db\u5ba3\u6000\u6863\u6848\u8d44\u6599\u9009\u8f91\u4e4b\u4e09\uff1a\u4e2d\u65e5\u7532\u5348\u6218\u4e89\uff08\u4e0b\uff09[M]\uff0c\u4e0a\u6d77\u4eba\u6c11\u51fa\u7248\u793e, 1980\u5e74\u7248.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[2]\u674e\u9e3f\u7ae0\u5168\u96c6\uff0825\uff09[M]\uff0c\u5b89\u5fbd\u6559\u80b2\u51fa\u7248\u793e,&nbsp;2008\uff0cp172.&nbsp;</span></div>\r\n\r\n<div><span style=\"color:#808080\">[3] \u738b\u8299\u84c9, \u5f20\u4e9a\u6797, \u6768\u4e16\u660c. \u519b\u5b98\u804c\u4e1a\u4eba\u683c\u91cf\u8868\u7684\u521d\u6b65\u7f16\u5236[J].&nbsp;\u4e2d\u56fd\u4e34\u5e8a\u5fc3\u7406\u5b66\u6742\u5fd7,&nbsp;2006, (03):224-6.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[4]\u5b59\u5efa\u519b. \u9547\u8fdc\u8230\u957f\u6797\u6cf0\u66fe\u5728\u97e9\u89c2\u5bdf\u8986\u547d\u4e66\u7b3a\u6ce8[J]. \u5927\u8fde\u8fd1\u4ee3\u53f2\u7814\u7a76, 2010, 7: 44-72.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[5]\u7530\u6c49.&nbsp;\u5173\u4e8e\u4e2d\u56fd\u6d77\u519b\u7684\u51e0\u4e2a\u95ee\u9898&middot;\u6797\u6cf0\u66fe[J].&nbsp;\u6d77\u519b\u6574\u5efa\u6708\u520a,&nbsp;1941(\u6c11\u56fd\u4e09\u5341\u5e74), 1(12): 5-6.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[6]\u674e&middot;\u9a6c\u5409\u82ac \u8457\uff0c\u5f20\u9ece\u6e90 \u8bd1. \u4ed6\u9009\u62e9\u4e86\u4e2d\u56fd[M].&nbsp;\u5c71\u4e1c\u753b\u62a5\u51fa\u7248\u793e,&nbsp;2013.&nbsp;</span></div>\r\n\r\n<div><span style=\"color:#808080\">[7]\u5b59\u5efa\u519b \u6574\u7406\u6821\u6ce8.&nbsp;\u5317\u6d0b\u6d77\u519b\u5b98\u5175\u56de\u5fc6\u8f91\u5f55[M].&nbsp;\u5c71\u4e1c\u753b\u62a5\u51fa\u7248\u793e,&nbsp;2016</span></div>\r\n\r\n<div><span style=\"color:#808080\">[8]\u9648\u60a6.&nbsp;\u4e2d\u65e5\u7532\u5348\u9ec4\u6d77\u5927\u51b3\u6218[M].&nbsp;\u53f0\u6d77\u51fa\u7248\u793e.&nbsp;2019.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[9] Carrie H Kennedy, Eric Zillmer.&nbsp;Military Psychology: Clinical and operational applications [M]. Guilford Publications, 2012, 2nd New edition.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[10] Castagn&eacute; V, Moser P, Roux S, Porsolt RD. Rodent models of depression: forced swim and tail suspension behavioral despair tests in rats and mice. Curr Protoc Neurosci. 2011;Chapter 8:Unit 8.10A.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[11] Trent LK. Parasuicides in the Navy and Marine Corps: Hospital admissions, 1989-1995. (Techical Document 99-4D). 1999, San Diego, CA: Naval Health Research Center.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[12] Ritchie EC, Keppler WC, Rothberg JM. Suicidal admissions in the United States military. Military Medicine, 2003, 168, 177-181.</span></div>\r\n\r\n<div><span style=\"color:#808080\">[13] Jenkins EN, Allison P, Innes K, Violanti JM, Andrew ME. Depressive Symptoms Among Police Officers: Associations with Personality and Psychosocial Factors. J Police Crim Psychol. 2019 Mar;34(1):66-77.</span></div>", "date_created": "2019-09-16T10:35:11.683196+08:00", "date_modified": "2019-09-20T10:51:45.928932+08:00", "date_published": "2019-09-16T13:01:32.477118+08:00", "id": 454723, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "psybst", "replies_count": 4, "small_image": "https://3-im.guokr.com/nrQ6v7SguEToYpDY9JMkmwQfcVD1GzDgbWThTw4nyS8gAwAAywEAAEpQ.jpg", "subject": {"articles_count": 1370, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "psychology", "minisite_key": "psybst", "name": "\u5fc3\u7406", "sort_score": 10, "subject_type": "normal"}, "subject_key": "psychology", "summary": "\u539f\u672c\u7a33\u5065\u5fe0\u8c28\u7684\u5206\u8230\u961f\u53f8\u4ee4\u6797\u6cf0\u66fe\uff0c\u7a76\u7adf\u56e0\u4f55\u7cbe\u795e\u5931\u5e38\u754f\u6218\u81ea\u6740\uff1f", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|1894\u5e7411\u670816\u65e5\uff0c\u5a01\u6d77\u5218\u516c\u5c9b\uff0c\u6d45\u6ee9\u4e0a\u4f0f\u7740\u5317\u6d0b\u6d77\u519b\u7684\u94c1\u7532\u8230\u9547\u8fdc\u53f7\uff0c\u8eab\u4e0a\u6ee1\u662f\u5f39\u5b54\u3001\u51f9\u69fd\u548c\u706b\u75d5\uff0c\u50cf\u5934\u9971\u7ecf\u6ca7\u6851\u7684\u5de8\u9cb8\u3002\u7532\u5348\u6218\u540e\u7684\u9547\u8fdc\u8230\uff0c\u767d\u5708\u6807\u51fa\u7740\u5f39\u70b9 | \u5386\u53f2\u8d44\u6599\u591c\u8272\u6de1\u53bb\uff0c\u592a\u9633\u7167\u5e38\u5347\u8d77\uff0c\u5f88\u5feb\u6709\u4eba\u53d1\u73b0\u4e86\u5f02\u6837\u3002\u6768\u526f\u7ba1\u9a7e\u5e26\u4eba\u51b2\u8fdb\u4f4f\u8231\u649e\u5f00\u8230\u957f\u5ba4\uff0c\u88ab\u773c\u524d\u7684\u4e00\u5207\u60ca\u5446\u4e86\uff1a\u201c\u77b3\u5b54\u6781\u5ea6\u7f29\u5c0f\uff0c\u76ae\u80a4\u6e7f\u51b7\uff0c\u547c\u5438\u6291\u5236\u2026\u2026\u5178\u578b\u7684\u5417\u5561\u4e2d\u6bd2\uff0c\u53ef\u662f\u592a\u665a\u4e86\uff0c\u62b1\u6b49\uff0c\u6211\u5df2\u7ecf\u65e0\u80fd\u4e3a\u529b\u3002\u201d\u5218\u516c\u5c9b\u75c5\u9662\u7684\u82f1\u56fd\u533b\u751f\u683c\u536b\u9f84\u68c0\u67e5\u5b8c\u53f9\u4e86\u53e3\u6c14\u3002\u684c\u4e0a\u6709\u4e00\u53ea\u7a7a\u7897\uff0c\u5e95\u90e8\u6709\u4e9b\u8bb8\u9ed1\u8272\u5757\u72b6\u7269\uff0c\u90a3\u662f\u8fd8\u6ca1\u6709\u6eb6\u89e3\u7684\u9e26\u7247\u3002\u5317\u6d0b\u6d77\u519b\u5de6\u7ffc\u603b\u5175\u3001\u9547\u8fdc\u8230\u7ba1\u5e26\uff08\u8230\u957f\uff09\u6797\u6cf0\u66fe\uff0c\u5728125\u5e74\u524d\u90a3\u4e2a\u6df1\u79cb\u6e05\u6668\u670d\u9e26\u7247\u81ea\u6740\uff0c\u65f6\u5e7442\u5c81\u3002\u6797\u6cf0\u66fe\u7167\u7247\uff1b\u5de6\u56fe\u6444\u4e8e1877-1879\u5e74\uff0c\u6cd5\u56fd\uff0c\u6797\u4f5c\u4e3a\u82f1\u56fd\u7687\u5bb6\u6d77\u519b\u7559\u5b66\u751f\u65f6\u671f | \u539f\u56fe\u7531\u6cd5\u56fd\u8239\u653f\u6587\u5316\u7814\u7a76\u8005\u9b4f\u5ef6\u5e74\u5148\u751f\u63d0\u4f9b\u5317\u6d0b\u6d77\u519b\u7684\u5de6\u7ffc\u603b\u5175\u519b\u8854\u76f8\u5f53\u4e8e\u4eca\u5929\u7684\u5c11\u5c06\uff0c\u804c\u6743\u4f4d\u5217\u6d77\u519b\u7b2c\u4e8c\uff1b\u9547\u8fdc\u662f\u6e05\u653f\u5e9c\u4ec5\u6709\u7684\u4e24\u6761\u94c1\u7532\u8230\u4e4b\u4e00\uff0c\u4e3a\u56fd\u4e4b\u91cd\u5668\uff0c\u7c7b\u4f3c\u4eca\u5929\u7684\u822a\u6bcd\u3002\u6797\u603b\u5175\u81ea\u6740\u524d\u4e0d\u4e45\uff0c\u4e2d\u65e5\u7532\u5348\u6d77\u6218\u521a\u521a\u6682\u544a\u6bb5\u843d\u3002\u56fd\u4e8b\u7ef4\u8270\uff0c\u6d77\u519b\u9ad8\u5b98\u7a81\u7136\u81ea\u5c3d\uff0c\u65e0\u7591\u8ba9\u4f4e\u9761\u7684\u6218\u52bf\u3001\u75b2\u655d\u7684\u56fd\u547d\u96ea\u4e0a\u52a0\u971c\u3002\u4e00\u65f6\u95f4\u519b\u5185\u54d7\u7136\uff0c\u671d\u91ce\u9707\u60ca\u3002\u6797\u603b\u5175\u81ea\u6740\u7684\u6d88\u606f\u6210\u4e3a\u70ed\u8bae\u8bdd\u9898\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u56e0\u4e24\u5929\u524d\u9547\u8fdc\u89e6\u7901\u8fc7\u4e8e\u5185\u759a\uff0c\u4e5f\u6709\u4eba\u8bf4\u4ed6\u662f\u81ea\u8bc1\u6e05\u767d\u9a73\u65a5\u901a\u654c\u8c23\u8a00\uff0c\u8fd8\u6709\u8bf4\u4ed6\u662f\u5bf9\u6218\u52bf\u5931\u53bb\u4fe1\u5fc3\uff0c\u66f4\u6709\u751a\u8005\u7ffb\u51fa\u4e4b\u524d\u79cd\u79cd\u516b\u5366\uff0c\u8bf4\u4ed6\u4e3a\u4eba\u80c6\u5c0f\u6015\u4e8b[1]\u2026\u2026\u4e0d\u77e5\u662f\u5426\u53d7\u8206\u8bba\u5f71\u54cd\uff0c\u6700\u7ec8\u5b98\u65b9\u8c03\u67e5\u7ed3\u679c\u662f\uff1a\u201c\u6797\u6cf0\u66fe\u5411\u6765\u80c6\u5c0f\uff0c\u60f3\u56e0\u758f\u5ffd\uff0c\u5185\u759a\u8f7b\u751f\u201d\u00a0[2]\u3002\u80c6\u5c0f\u8f7b\u751f\uff0c\u6210\u4e3a\u6797\u6cf0\u66fe\u7684\u76d6\u68fa\u5b9a\u8bba\uff0c\u4e00\u76f4\u5f71\u54cd\u81f3\u4eca\u3002\u4f46\u6797\u6cf0\u66fe\u662f\u4e2d\u56fd\u7b2c\u4e00\u4ee3\u6d77\u519b\u7740\u91cd\u57f9\u517b\u7684\u4e09\u4f4d\u82f1\u624d\u4e4b\u4e00\uff0c \u505a\u8239\u653f\u5b66\u751f\u65f6\u5c31\u201c\u5386\u8003\u4f18\u7b49\u201d\uff0c\u88ab\u8a89\u4e3a\u201c\u95fd\u7701\u5b66\u751f\u51fa\u8272\u4e4b\u4eba\u201d\uff0c\u800c\u540e\u9009\u6d3e\u7559\u5b66\u3001\u63a5\u8230\u56de\u56fd\u3001\u63a5\u624b\u8425\u52a1\u3001\u521d\u521b\u6d77\u519b\u3001\u8bad\u7ec3\u5de1\u822a\u2026\u2026\u987a\u5229\u5b8c\u6210\u4e00\u4e2a\u4e2a\u91cd\u8981\u4efb\u52a1\u3002\u501f\u7528\u4e2d\u56fd\u5fc3\u7406\u5b66\u5bb6\u7f16\u5236\u7684\u519b\u5b98\u5927\u4e94\u91cf\u8868MOVPS[3]\u8bf7\u4e00\u4e9b\u5728\u8fd1\u4ee3\u6d77\u519b\u53f2\u65b9\u9762\u7684\u8d44\u6df1\u7814\u7a76\u8005\u5bf9\u4ed6\u8fdb\u884c\u4eba\u683c-\u5c97\u4f4d\u9002\u5e94\u6027\u7684\u8bc4\u4f30\uff0c\u53d1\u73b0\u4ed6\u64c5\u957f\u7ec4\u7ec7\u3001\u534f\u8c03\u548c\u7ba1\u7406\uff0c\u540c\u65f6\u5728\u519b\u4e8b\u6307\u6325\u624d\u80fd\u65b9\u9762\u8868\u73b0\u4f18\u79c0\u3002\u8bda\u5982\u674e\u9e3f\u7ae0\u6240\u8c13\u7684\u201c\u5411\u6765\u201d\u80c6\u5c0f\uff0c\u4e3a\u4f55\u8fd8\u8d39\u5c3d\u5fc3\u601d\u5c06\u4ed6\u8c03\u5165\u81ea\u5df1\u9ebe\u4e0b\uff1f\u5f53\u65f6\u81ea\u6740\u7528\u9e26\u7247\u6ce1\u9152\uff0c\u4f46\u9e26\u7247\u6eb6\u89e3\u5ea6\u5dee\uff0c\u8fbe\u5230\u81f4\u6b7b\u5242\u91cf\u4e14\u8981\u559d\u4e0a\u4e00\u58f6\uff0c\u800c\u4e14\u5473\u9053\u96be\u559d\u5f97\u8ba9\u4eba\u60f3\u6b7b\uff0c\u54e6\u4e0d\uff0c\u662f\u96be\u559d\u5f97\u8ba9\u4eba\u90fd\u4e0d\u60f3\u53bb\u6b7b\uff0c\u6bd4\u5982\u540e\u6765\u6218\u8d25\u524d\u81ea\u6740\u7684\u5218\u516c\u5c9b\u62a4\u519b\u6234\u5b97\u9a9e\u5c31\u8981\u522b\u4eba\u8865\u836f\uff0c\u5b9a\u8fdc\u8230\u7ba1\u5e26\u5218\u6b65\u87fe\u81ea\u6740\u8fc7\u7a0b\u6301\u7eed\u5230\u6b21\u65e5\u4e2d\u5348\u3002\u6797\u6cf0\u66fe\u4ece\u670d\u836f\u5230\u6b7b\u4ea1\u4ec52\u4e2a\u5c0f\u65f6\uff0c\u53ef\u89c1\u4ed6\u6c42\u6b7b\u4e4b\u5fc3\u4e4b\u51b3\u7edd\u3002\u6b7b\u90fd\u4e0d\u60e7\uff0c\u7b97\u80c6\u5c0f\u4e48\uff1f\u4e8b\u51fa\u8e4a\u8df7\uff0c\u8fd8\u662f\u6765\u56de\u987e\u4e0b\u4ed6\u81ea\u5c3d\u524d\u7684\u51e0\u4e2a\u5173\u952e\u8f68\u8ff9\uff1a\u4e8b\u4ef61\uff1a\u6218\u524d\uff0c\u731d\u4e0d\u53ca\u9632\u7684\u4e00\u679a\u6807\u7b7e1894\u5e746\u670820\u65e5\uff0c\u6797\u6cf0\u66fe\u4f5c\u4e3a\u5317\u6d0b\u8230\u961f\u5206\u9063\u961f\u53f8\u4ee4\uff0c\u5949\u547d\u7387\u9547\u8fdc\u3001\u8d85\u52c7\u3001\u5e7f\u4e19\u4e09\u8230\u524d\u5f80\u671d\u9c9c\u4ec1\u5ddd\u7b49\u5730\u5de1\u822a[4]\u3002\u5f88\u5feb\uff0c\u4ed6\u53d1\u73b0\u6218\u4e89\u7684\u6b65\u4f10\u65e0\u6cd5\u963b\u6321\uff0c\u4e8e\u662f\u7acb\u5373\u5411\u4e0a\u53f8\u674e\u9e3f\u7ae0\u53d1\u7535\u62a5\u7533\u8bf7\u652f\u63f4\u3002\u7136\u800c\u674e\u9e3f\u7ae0\u5fc3\u91cc\u4e00\u4e07\u4e2a\u4e0d\u60f3\u6253\u4ed7\u3002\u4f5c\u4e3a\u98ce\u5934\u6b63\u52b2\u7684\u6d0b\u52a1\u6743\u81e3\uff0c\u4e00\u65e6\u5f00\u6218\u5bb6\u5e95\u90fd\u5f97\u8d54\u5149\uff0c\u56e0\u6b64\u4e00\u76f4\u79ef\u6781\u5bfb\u6c42\u5217\u5f3a\u5bf9\u65e5\u672c\u7684\u5916\u4ea4\u7275\u5236\u3002\u65e5\u672c\u5916\u76f8\u9646\u5965\u5b97\u5149\u6b63\u597d\u5229\u7528\u8fd9\u79cd\u5fc3\u6001\uff0c\u4e00\u9762\u589e\u5175\u4e00\u9762\u91ca\u51fa\u548c\u5e73\u4fe1\u606f\u3002\u674e\u9e3f\u7ae0\u8d8a\u662f\u6015\u8f7b\u4e3e\u5984\u52a8\u6210\u4e3a\u65e5\u672c\u51fa\u5175\u7684\u501f\u53e3\uff0c\u624b\u4e0b\u90a3\u7fa4\u5c06\u9886\u8d8a\u4e0d\u5b89\u751f\uff1a\u8fd9\u8fb9\u6d77\u519b\u7684\u6797\u6cf0\u66fe\u8bf7\u6d3e\u9c7c\u96f7\u8247\uff0c\u9646\u519b\u7684\u53f6\u5fd7\u8d85\u56b7\u7740\u5927\u4e3e\u589e\u5175\uff0c\u8fd9\u5173\u5934\u4e0d\u7701\u5fc3\u7684\u671d\u5ef7\u8fd8\u9001\u6765\u4e00\u4efd\u56db\u767e\u91cc\u5bc6\u8c15\uff0c\u201c\u7b79\u6218\u201d\u201c\u94a6\u6b64\u201d\u7b49\u5b57\u773c\u8ba9\u674e\u9e3f\u7ae0\u5934\u5927\u4e86\u597d\u51e0\u5708\u3002\u4e0d\u660e\u771f\u76f8\u7684\u6797\u6cf0\u66fe\u4e00\u95e8\u5fc3\u601d\u4ece\u6218\u4e8b\u51fa\u53d1\uff0c\u51e0\u6b21\u4e09\u756a\u5730\u8bf7\u6c42\u652f\u63f4\u5907\u6218\u3002\u7ed3\u679c\u2026\u2026\u6797\u6cf0\u66fe\u6ca1\u60f3\u5230\uff0c\u5162\u5162\u4e1a\u4e1a\u6362\u6765\u515c\u5934\u4e00\u76c6\u51b7\u6c34\u3002\u53ea\u662f\u51b7\u6c34\u5012\u4e5f\u7b97\u4e86\uff0c\u81ea\u5df1\u6b63\u5728\u4ee5\u8eab\u72af\u9669\u600e\u4e48\u5c31\u88ab\u4e0a\u7ea7\u76d6\u7ae0\u201c\u80c6\u602f\u5f20\u7687\u201d\uff1f\u4e0d\u5149\u6797\u6cf0\u66fe\u9707\u60ca\uff0c\u8230\u961f\u603b\u53f8\u4ee4\u4e01\u6c5d\u660c\u53ca\u6574\u4e2a\u6d77\u519b\u5728\u63a5\u5230\u674e\u9e3f\u7ae0\u7535\u8bad\u540e\u4e5f\u96c6\u4f53\u9677\u5165\u4e86\u56f0\u60d1\u548c\u8ff7\u832b\u3002\u4e01\u519b\u95e8\u53ea\u597d\u552f\u552f\u9053\uff1a\u201c\u6b64\u7cfb\u5927\u5e05\u6743\u8861\uff0c\u4f55\u6562\u53c2\u8bae\u3002\u201d\u53d7\u4e86\u59d4\u5c48\u7684\u6797\u603b\u5175\u5012\u6ca1\u6709\u6d88\u6781\u6020\u5de5\uff0c\u800c\u662f\u6839\u636e\u5f62\u52bf\u7387\u8230\u961f\u8f6c\u79fb\u7259\u5c71\uff0c\u4ee5\u6b64\u4e3a\u636e\u70b9\u5de1\u822a\u4fa6\u67e5\uff0c\u76f4\u5230\u4ed6\u83b7\u5f97\u4e86\u65e5\u519b\u5927\u4e3e\u589e\u5175\u4ec1\u5ddd\u7684\u6d88\u606f\u3002\u800c\u8fd9\u65f6\uff0c\u7259\u5c71\u901a\u5f80\u674e\u9e3f\u7ae0\u6240\u5728\u5730\u5929\u6d25\u7684\u7535\u62a5\u5df2\u7ecf\u963b\u65ad\uff0c\u800c\u81ea\u5df1\u4f5c\u4e3a\u5206\u8230\u961f\u53f8\u4ee4\uff0c\u5f97\u4e3a\u624b\u4e0b\u7684\u4e00\u4f17\u8230\u957f\u548c\u519b\u8230\u8d1f\u8d23\u3002\u7b49\u4e0d\u5230\u547d\u4ee4\uff0c\u6218\u4e8b\u5374\u62d6\u4e0d\u5f97\uff0c1894\u5e747\u67081\u65e5\uff0c\u6797\u6cf0\u66fe\u7387\u5404\u8230\u5f52\u822a\uff0c\u8ba1\u5212\u968f\u540e\u201c\u5408\u5927\u961f\u6765\u653b\u201d\u3002\u4ed6\u5927\u6982\u8ba4\u4e3a\uff0c\u674e\u9e3f\u7ae0\u4e4b\u524d\u7535\u62a5\u6307\u793a\u8fc7\u8ba9\u4ed6\u201c\u76f8\u673a\u8fdb\u9000\u201d\u6240\u4ee5\u6b64\u4e3e\u4e0d\u7b97\u8fdd\u4ee4\uff0c\u4f46\u6ca1\u60f3\u5230\u674e\u9e3f\u7ae0\u521a\u901a\u8fc7\u4fc4\u56fd\u4eba\u5f97\u5230\u4e86\u9646\u5965\u5b97\u5149\u4e0d\u5f00\u6218\u7684\u627f\u8bfa\uff0c\u5408\u961f\u6765\u653b\u81ea\u7136\u5316\u4e3a\u6ce1\u5f71\uff0c\u6797\u6cf0\u66fe\u4ec1\u5ddd\u7387\u8230\u8fd4\u56de\u53cd\u800c\u6210\u4e86\u91cd\u5927\u8fc7\u9519\u3002\u65e5\u672c\u6218\u4e89\u6587\u5b66\u5bb6\u5c0f\u7b20\u539f\u957f\u751f\u7684\u5c0f\u8bf4\u4e2d\u5f15\u8ff0\u6218\u540e\u7f34\u83b7\u7684\u6797\u6cf0\u66fe\u65e5\u8bb0\uff0c\u63d0\u5230\u4e86\u6797\u6cf0\u66fe\u4e8b\u540e\u7684\u53cd\u5e94\uff1a\u201c\u545c\u547c\u3002\u6cf0\u66fe\u4e00\u4ecb\u6b66\u592b\u8033\uff0c\u624d\u62d9\u8bc6\u6d45\uff0c\u4f46\u5fe7\u56fd\u5bb6\u77e5\u65e0\u4e0d\u8a00\u3002\u65e2\u5085\u76f8\u5df2\u6709\u6210\u7b97\uff0c\u4f55\u6562\u518d\u8a00\u6218\uff1f\u552f\u6709\u8c28\u5f85\u5085\u76f8\u4e4b\u547d\u800c\u5df2\u3002\u201d[5]\u6b66\u5c06\u5728\u6e05\u671d\u5730\u4f4d\u4f4e\u4e0b\uff0c\u4f46\u665a\u6e05\u5efa\u7acb\u7684\u7b2c\u4e00\u652f\u9ad8\u5b66\u5386\u3001\u804c\u4e1a\u5316\u6d77\u519b\uff0c\u5df2\u7ecf\u6446\u8131\u4e86\u4f20\u7edf\u7684\u9119\u964b\u5370\u8ff9\uff0c\u90a3\u4e9b\u7559\u82f1\u7559\u7f8e\u7684\u6d77\u519b\u519b\u5b98\u66f4\u662f\u4ee5\u7cbe\u82f1\u81ea\u5c45\u3002\u6797\u6cf0\u66fe\u4f5c\u4e3a\u6b64\u5217\u4e2d\u4eba\uff0c\u6b64\u65f6\u5374\u81ea\u8d2c\u81ea\u5632\u201c\u4e00\u4ecb\u6b66\u592b\u201d\uff0c\u53ef\u89c1\u5907\u6218\u70ed\u60c5\u5f7b\u5e95\u51c9\u4e86\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u90a3\u679a\u201c\u80c6\u5c0f\u5f20\u7687\u201d\u7684\u6807\u7b7e\u5374\u8d8a\u53d1\u663e\u773c\uff0c\u674e\u9e3f\u7ae0\u540e\u6765\u6279\u8bc4\u4e01\u6c5d\u660c\u90fd\u4f1a\u62c9\u4ed6\u6765\u6e9c\uff1a\u201c\u6797\u6cf0\u66fe\u524d\u5728\u4ec1\u5ddd\u754f\u65e5\u9041\u8d70\u2026\u2026\u4e2d\u897f\u4eba\u4f20\u4e3a\u7b11\u8c08\u3002\u201d\u5f53\u65f6\u201c\u4e2d\u897f\u4eba\u201d\u7684\u516b\u5366\u70ed\u60c5\u4e0d\u4e9a\u4e8e\u4eca\u5929\u7684\u5403\u74dc\u7fa4\u4f17\uff0c\u6210\u5c71\u5934\u7535\u62a5\u59d4\u5458\u62a5\u544a\u7684\u201c\u6797\u6cf0\u66fe\u6d77\u4e0a\u9047\u65e5\u8230\u9003\u7a9c\u4e8b\u4ef6\u201d\u8bf4\u5f97\u6709\u9f3b\u5b50\u6709\u773c\uff0c\u4eff\u4f5b\u4ed6\u5f53\u65f6\u4eb2\u4e34\u73b0\u573a\u3002\u8fd9\u4e2a\u6545\u4e8b\u4ece\u4e00\u7fa4\u6d0b\u4eba\u90a3\u4f20\u5230\u4e86\u6797\u6cf0\u66fe\u8001\u540c\u5b66\u3001\u65f6\u4e3a\u76db\u5ba3\u6000\u79d8\u4e66\u7684\u7f57\u4e30\u7984\u8033\u4e2d\uff0c\u9042\u5c55\u5f00\u8c03\u67e5\uff0c\u7ed3\u679c\u67e5\u65e0\u6b64\u4e8b\u3002\u4f46\u9020\u8c23\u4e00\u5f20\u5634\uff0c\u8f9f\u8c23\u8dd1\u65ad\u817f\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6797\u6cf0\u66fe\u8df3\u8fdb\u9ec4\u6d77\u4e5f\u6d17\u4e0d\u6e05\u4e86\u3002\u4e8b\u4ef62\uff1a\u6d77\u6218\u6b63\u9163\uff0c\u8230\u957f\u5374\u7cbe\u795e\u5931\u5e381894\u5e747\u670825\u65e5\uff0c\u4e2d\u65e5\u4e30\u5c9b\u6d77\u6218\u7206\u53d1\uff0c\u674e\u9e3f\u7ae0\u8c03\u505c\u907f\u6218\u7684\u5e0c\u671b\u5f7b\u5e95\u7834\u706d\u3002\u5728\u6b64\u4e4b\u9645\u6797\u6cf0\u66fe\u7533\u8bf7\u8f9e\u804c\uff0c\u6c14\u5934\u4e0a\u7684\u674e\u9e3f\u7ae0\u65ad\u7136\u62d2\u7edd\u5e76\u653e\u72e0\u8bdd\uff1a\u518d\u63d0\u4e0d\u5e72\u5c31\u780d\u4e86\u4f60\u3002\u6797\u6cf0\u66fe\u53ea\u5f97\u7ee7\u7eed\u7559\u5c97\uff0c\u76f4\u5230\u90a3\u573a\u51b3\u5b9a\u4e86\u4e2d\u65e5\u4e24\u56fd\u56fd\u547d\u7684\u5927\u4e1c\u6c9f\u6d77\u6218\u7206\u53d1\u30021894\u5e749\u670817\u65e5\u4e0b\u5348\uff0c\u9e2d\u7eff\u6c5f\u53e3\u5916\u7684\u9ec4\u6d77\u6d77\u9762\u6d53\u70df\u6eda\u6eda\uff0c\u70ae\u706b\u5982\u96e8\uff0c\u51b2\u5929\u6c34\u67f1\u6b64\u8d77\u5f7c\u4f0f\u3002\u5f53\u4e2d\u56fd\u8230\u961f\u5148\u671f\u5931\u5229\uff0c\u63a5\u8fde\u635f\u5931\u4e86\u81f4\u8fdc\u3001\u7ecf\u8fdc\u7b49\u5de1\u6d0b\u8230\u540e\uff0c\u5c40\u52bf\u5168\u90fd\u7cfb\u5728\u4e24\u6761\u94c1\u7532\u8230\u4e0a\u3002\u65d7\u8230\u5b9a\u8fdc\u5728\u5317\u6d0b\u6d77\u519b\u53f3\u7ffc\u603b\u5175\u3001\u7ba1\u5e26\u5218\u6b65\u87fe\u7684\u6307\u6325\u4e0b\u5982\u4e2d\u6d41\u7825\u67f1\u62b5\u6297\u7740\u65e5\u8230\u75af\u72c2\u7684\u56f4\u653b\uff0c\u7136\u800c\u5728\u9547\u8fdc\u8230\u7684\u6307\u6325\u5854\u4e2d\u5374\u662f\u53e6\u4e00\u756a\u666f\u8c61\u30021894\u5e74\u4e2d\u65e5\u7532\u5348\u5927\u4e1c\u6c9f\u6d77\u6218 |\u00a01894\u5e7411\u6708\u300a\u4f26\u6566\u65b0\u95fb\u753b\u62a5\u300b\u6839\u636e\u76ee\u51fb\u8005\u63cf\u8ff0\u7ed8\u5236\u9547\u8fdc\u8230\u5e2e\u5e26\u5927\u526f\u3001\u7f8e\u56fd\u6d0b\u5458\u9a6c\u5409\u82ac\uff08McGiffin\uff09\u5728\u81ea\u5df1\u7684\u56de\u5fc6\u5f55\u4e2d\u8bb0\u9053\uff1a\u201c\u6211\u4e0d\u65ad\u5730\u542c\u5230\u4e00\u4e2a\u5947\u602a\u7684\u58f0\u97f3\u4ece\u6211\u4e0b\u65b9\u7684\u6307\u6325\u5854\u91cc\u4f20\u51fa\u2026\u2026\u6211\u60ca\u8bb6\u5730\u53d1\u73b0\u8fd9\u7adf\u662f\u6211\u90a3\u5c0a\u656c\u7684\u8230\u957f\u53d1\u51fa\u7684\uff01\u4ed6\u6b63\u8dea\u5012\u5728\u5730\uff0c\u4ee5\u6781\u5feb\u7684\u8bed\u901f\u7528\u4e2d\u6587\u5583\u5583\u81ea\u8bed\u2014\u2014\u7948\u7977\u7740\uff0c\u6216\u8005\u8bf4\u4e00\u8fb9\u7948\u7977\u4e00\u8fb9\u8bc5\u5492\u7740\u2014\u2014\u6bcf\u4e00\u53d1\u70ae\u5f39\u51fb\u4e2d\u519b\u8230\u65f6\u4ed6\u5c31\u50cf\u72d7\u4e00\u6837\u568e\u53eb\u8d77\u6765\u3002\u53ea\u8981\u6211\u8fd8\u6d3b\u7740\uff0c\u6211\u6c38\u8fdc\u4e5f\u5fd8\u4e0d\u4e86\u90a3\u5e55\u666f\u8c61\u548c\u90a3\u79cd\u58f0\u97f3\u3002\u201d\u00a0[6]\u4e00\u4f4d\u53c2\u6218\u7684\u4e2d\u56fd\u6d77\u519b\u519b\u5b98\u5362\u6bd3\u82f1\u4e5f\u5370\u8bc1\u4e86\u6b64\u4e8b\uff1a\u201c\u5927\u4e1c\u6c9f\u4e4b\u5f79\uff0c\u6797\u60ca\u60f6\u65e0\u63aa\uff0c\u6545\u7f6e\u6d53\u9152\u4f5c\u957f\u996e\u5927\u9189\uff0c\u800c\u535c\u4e8e\u6218\u53f0\u4e4b\u4e0b\uff0c\u6240\u6709\u8c03\u9063\u6089\u8bac\u5176\u526f\u9a7e\u5e26\u6768\u7528\u9716\u4e4b\u8eab\u4e0a\uff0c\u5907\u8bf8\u6b7b\u5177\u5982\u8ff7\u836f\u6d0b\u70df\u4e4b\u7c7b\u3002\u201d\u00a0[7]\u597d\u5728\u9547\u8fdc\u6709\u4e00\u4f4d\u975e\u5e38\u51fa\u8272\u82f1\u52c7\u7684\u526f\u8230\u957f\u6768\u7528\u9716\uff0c\u4ee3\u66ff\u8230\u957f\u5168\u6743\u6307\u6325\uff0c\u633d\u6551\u4e86\u519b\u8230\u5e76\u6539\u5199\u4e86\u6218\u5c40\uff1a\u5173\u952e\u65f6\u523b\u9547\u8fdc\u8fde\u53d1\u4e24\u679a305mm\u70ae\u5f39\u547d\u4e2d\u65e5\u672c\u65d7\u8230\u677e\u5c9b\uff0c\u5176\u4e2d\u4e00\u679a\u5f00\u82b1\u5f39\u5f15\u8d77\u677e\u5c9b\u5f39\u836f\u5e93\u7684\u4e25\u91cd\u6b89\u7206\uff0c\u81f4\u8239\u4e0a\u56db\u5206\u4e4b\u4e00\u6210\u5458\u4f24\u4ea1\uff0c\u6b66\u5668\u7cfb\u7edf\u3001\u6db2\u538b\u8235\u8f6e\u4e25\u91cd\u53d7\u635f\uff0c\u4e0d\u591a\u65f6\u4fbf\u96c6\u7ed3\u8230\u961f\u64a4\u51fa\u6218\u573a[6]\u3002\u82e5\u975e\u5982\u6b64\uff0c\u6797\u6cf0\u66fe\u8fd9\u6b21\u6e0e\u804c\u7684\u540e\u679c\u53ef\u80fd\u96be\u4ee5\u4f30\u91cf\u3002\u65e5\u672c\u65d7\u8230\u677e\u5c9b\u88ab\u9547\u8fdc305mm\u4e3b\u70ae\u547d\u4e2d\u540e\u5de6\u8237\u7559\u4e0b\u7684\u5de8\u5927\u4f24\u53e3\u9ec4\u6d77\u6d77\u6218\u540e\u53d7\u4f24\u7684\u9547\u8fdc\u8230\u548c\u5979\u7684\u70ae\u52a1\u5b98\u5fb7\u56fd\u4eba\u54c8\u535c\u95e8\u00a0|\u00a01895\u5e745\u67084\u65e5\u300a\u56fe\u7247\u62a5\u300b\u4e8b\u4ef63\uff1a\u8001\u53f8\u673a\u201c\u79d1\u76ee\u4e8c\u201d\u5931\u8d251894\u5e7411\u670814\u65e5\u9ece\u660e\uff0c\u5a01\u6d77\u6e7e\u548c\u5218\u516c\u5c9b\u5939\u5cd9\u7684\u897f\u5317\u53e3\u4f20\u6765\u4e00\u58f0\u5de8\u54cd\u3002\u9547\u8fdc\u8230\u5e9e\u5927\u7684\u8eab\u8eaf\u5267\u70c8\u98a4\u6296\uff0c\u5411\u5de6\u503e\u659c\u3002\u7ecf\u9a8c\u4e30\u5bcc\u7684\u6307\u6325\u5b98\u4eec\u7acb\u5373\u542f\u52a8\u635f\u7ba1\u63aa\u65bd\uff1a\u5835\u6f0f\u3001\u62bd\u6c34\u3001\u5173\u6c34\u5bc6\u95e8\u2026\u2026\u7136\u800c\u6d77\u6c34\u8fd8\u662f\u6e90\u6e90\u4e0d\u65ad\u6f0f\u8fdb\u4e86\u5f39\u836f\u8231\u3001\u5e06\u8231\u3001\u7164\u8231\u3001\u9505\u7089\u8231\u53ca\u6c34\u529b\u673a\u8231\u3002\u636e\u540e\u6765\u7684\u8c03\u67e5\u62a5\u544a\uff0c\u4e8b\u6545\u539f\u56e0\u662f\u94c1\u7532\u8230\u5728\u9c7c\u8d2f\u5165\u6e2f\u65f6\uff0c\u524d\u9762\u5b9a\u8fdc\u4e03\u5343\u591a\u5428\u7684\u8eab\u8eaf\u4ea7\u751f\u5de8\u5927\u7684\u5206\u6c34\u538b\u63a8\u504f\u4e86\u6d6e\u6807\uff0c\u5bfc\u81f4\u540e\u9762\u7684\u9547\u8fdc\u8230\u5bf9\u822a\u9053\u5224\u65ad\u5931\u8bef\uff0c\u64e6\u8fc7\u9760\u8fd1\u5218\u516c\u5c9b\u4e00\u4fa7\u6d45\u6ee9\u7684\u7901\u77f3\uff0c\u81f4\u5de6\u8237\u8230\u5e95\u88c2\u574f\u64e6\u4f24\u8fbe8\u5904\u4e4b\u591a\u3002\u5982\u4eca\u5218\u516c\u5c9b\u897f\u5317\u53e3\u666f\u8c61\u3002\u524d\u65b9\u5c31\u662f\u5f53\u5e74\u9547\u8fdc\u89e6\u7901\u4e00\u5e26\u3002\u4f4d\u4e8e\u5317\u53e3\u5904\u7684\u4e24\u6845\u4e24\u70df\u56f1\u8239\u5f71\u662f\u6b63\u8981\u4e0a\u575e\u6cb9\u5e95\u76841:1\u590d\u539f\u5b9a\u8fdc\u8230\u6a21\u578b\uff0c\u540c\u7ea7\u7684\u9547\u8fdc\u8230\u5916\u5f62\u4e0e\u4e4b\u9177\u4f3c\uff0c\u604d\u60da\u6709\u79cd\u5386\u53f2\u91cd\u73b0\u7684\u5e7b\u89c9 | \u968b\u4e1c\u5347\u5148\u751f2016\u5e74\u6444\u4e8e\u5a01\u6d77\u5317\u5c71\u5634\u5927\u4e1c\u6c9f\u6d77\u6218\u5df2\u635f\u5931\u4e86\u6570\u6761\u5de1\u6d0b\u8230\uff0c\u4e24\u6761\u94c1\u7532\u8230\u6210\u4e86\u6e05\u653f\u5e9c\u6700\u540e\u7684\u5a01\u6151\u529b\u91cf\u3002\u5176\u4e2d\u4e00\u6761\u7adf\u5728\u8282\u9aa8\u773c\u4e0a\u51fa\u4e86\u4e25\u91cd\u4e8b\u6545\uff0c\u66f4\u7cdf\u7684\u662f\uff0c\u65e5\u672c\u9646\u519b\u5373\u5c06\u5728\u65c5\u987a\u767b\u9646\uff0c\u9547\u8fdc\u65e0\u6cd5\u524d\u5f80\u552f\u4e00\u62e5\u6709\u5e72\u575e\u7684\u65c5\u987a\u6e2f\u4fee\u7406\u3002\u90a3\u5929\u7684\u5165\u53e3\u6307\u6325\u7684\u662f\u7ba1\u5e26\u6797\u6cf0\u66fe\u3002\u4ed6\u4f5c\u4e3a\u4e2d\u56fd\u7b2c\u4e00\u4ee3\u6d77\u519b\u519b\u5b98\u7684\u4f7c\u4f7c\u8005\u66fe\u6218\u80dc\u5730\u4e2d\u6d77\u7684\u98d3\u98ce\u3001\u5370\u5ea6\u6d0b\u7684\u65e0\u8865\u7ed9\u822a\u7ebf\u3001\u82cf\u4f0a\u58eb\u8fd0\u6cb3\u7684\u6d45\u7a84\u6cb3\u9053\u4ee5\u53ca\u671d\u9c9c\u6d77\u5cb8\u7684\u6697\u7901\u9669\u6ee9\uff0c\u6700\u540e\u5374\u683d\u5728\u4e86\u5f80\u8fd4\u591a\u8d9f\u7684\u5218\u516c\u5c9b\u5317\u53e3\uff0c\u800c\u4e14\u90a3\u6b21\u8fd4\u6e2f\u53ea\u5f00\u51fa\u4e868\u8282\u822a\u901f\uff081\u8282=\u65f6\u901f1\u6d77\u91cc\u2248\u65f6\u901f1.852\u516c\u91cc\uff09\u3002\u5bb6\u95e8\u53e3\u4f4e\u901f\u89e6\u7901\uff0c\u4e0d\u548c\u8001\u53f8\u673a\u5012\u8f66\u5165\u5e93\u5931\u8d25\u4e00\u6837\u4e48\uff1f\u4ee5\u81f3\u4e8e\u4e8b\u6545\u4e0a\u62a5\u540e\u674e\u9e3f\u7ae0\u7684\u7b2c\u4e00\u53cd\u5e94\u662f\uff0c\u96be\u4e0d\u6210\u6797\u662f\u2026\u2026\u6545\u610f\u635f\u574f\uff1f\u867d\u7136\u6700\u540e\u7684\u8c03\u67e5\u8bc1\u660e\u4e86\u6797\u603b\u5175\u7684\u6e05\u767d\uff0c\u4f46\u5173\u4e8e\u4ed6\u80c6\u5c0f\u8f7b\u751f\u7684\u8bba\u8c03\u5374\u6210\u4e3a\u4ed6\u6c38\u4e45\u7684\u8eab\u540e\u4e4b\u540d\u3002\u6797\u603b\u5175\u75c5\u60c5\u5256\u6790\u90a3\u4e48\uff0c\u6797\u6cf0\u66fe\u4e3a\u4ec0\u4e48\u4f1a\u5728\u6218\u524d\u51fa\u73b0\u5982\u6b64\u4e25\u91cd\u7684\u95ee\u9898\uff1f\u4e0d\u52303\u4e2a\u6708\u65f6\u95f4\uff0c\u4ed6\u4ece\u7a33\u5065\u5fe0\u8c28\u7684\u5206\u8230\u961f\u53f8\u4ee4\uff0c\u53d8\u6210\u4e00\u4e2a\u7cbe\u795e\u8fd0\u52a8\u6027\u6fc0\u8d8a\u7684\u6218\u6597\u5e94\u6fc0\u8005\uff1b2\u4e2a\u6708\u540e\u51fa\u73b0\u6291\u90c1\u75c7\u5178\u578b\u7684\u201c\u4e09\u4f4e\u201d\uff08\u60c5\u7eea\u4f4e\u843d\uff0c\u601d\u7ef4\u8fdf\u7f13\uff0c\u610f\u5fd7\u51cf\u4f4e\u3002\uff09\u201c\u4e09\u65e0\u201d\uff08\u65e0\u7528\u3001\u65e0\u52a9\u548c\u65e0\u671b\uff09\u201c\u4e09\u81ea\u201d\uff08\u81ea\u8d23\u3001\u81ea\u7f6a\u548c\u81ea\u6740\uff09\u7279\u5f81\uff0c\u662f\u4ed6\u6709\u6027\u683c\u7f3a\u9677\uff0c\u5728\u6218\u6597\u4e2d\u53d8\u5f97\u80c6\u5c0f\u6291\u90c1\uff0c\u8fd8\u662f\u53e6\u6709\u539f\u56e0\uff1f\u6218\u6597\u5e94\u6fc0\u6218\u6597\u5e94\u6fc0\uff08combat\u00a0stress\u00a0reaction, CSR\uff09\u662f\u5f53\u524d\u5e38\u7528\u6765\u8bc4\u4ef7\u519b\u4eba\u5728\u6218\u6597\u6216\u975e\u6218\u4e89\u519b\u4e8b\u884c\u52a8\u4e2d\u7ecf\u5386\u538b\u529b\u6027\u4e8b\u4ef6\u540e\u53cd\u5e94\u7684\u519b\u4e8b\u5fc3\u7406\u5b66\u672f\u8bed\u3002\u5173\u4e8e\u6218\u6597\u5e94\u6fc0\uff0c\u5386\u53f2\u4e0a\u56e0\u8ba4\u8bc6\u7a0b\u5ea6\u4e0d\u540c\u4ea7\u751f\u8fc7\u4e0d\u540c\u540d\u79f0\u3002\u4e00\u6218\u65f6\u88ab\u79f0\u4f5c\u201c\u70ae\u5f39\u4f11\u514b\u201d\u201c\u6218\u4e89\u795e\u7ecf\u75c7\u201d\u6216\u201cDa Costa\u7efc\u5408\u5f81\u201d\u7b49\u3002\u8fd9\u4e9b\u672f\u8bed\u5728\u4e8c\u6218\u65f6\u4ecd\u88ab\u5927\u91cf\u4f7f\u7528\uff0c\u76f4\u5230\u8d8a\u6218\u65f6\u624d\u9010\u6e10\u7edf\u4e00\u4e3a\u6218\u6597\u5e94\u6fc0\u3002\u6d77\u6e7e\u6218\u4e89\u671f\u95f4\uff0c\u5305\u62ec\u521b\u4f24\u540e\u5e94\u6fc0\u969c\u788d\uff08PTSD\uff09\u5728\u5185\u7684\u4e00\u7cfb\u5217\u75c7\u72b6\u90fd\u88ab\u56ca\u62ec\u5728\u6218\u6597\u5e94\u6fc0\u53cd\u5e94\u7684\u8303\u7574\u3002[8]\u00a0\u6218\u6597\u5e94\u6fc0\u53cd\u5e94\u53ef\u5206\u4e3a\u516d\u5927\u7c7b\u578b\uff1a\u8eaf\u4f53\u3001\u8ba4\u77e5\u3001\u884c\u4e3a\u3001\u60c5\u611f\u3001\u8fc7\u5931\u6027\u53ca\u9002\u5e94\u6027\u7684\u75c7\u72b6\u3002\u5982\u679c\u5bf9\u53f7\u5165\u5ea7\u7684\u8bdd\uff0c\u6797\u6cf0\u66fe\u57fa\u672c\u5c97\u4f4d\u6280\u80fd\u4e27\u5931\uff0c\u8bed\u901f\u52a0\u5feb\uff0c\u662f\u6218\u6597\u5e94\u6fc0\u7684\u884c\u4e3a\u75c7\u72b6\uff1b\u7126\u8651\u3001\u6613\u6fc0\u60f9\u3001\u6b47\u65af\u5e95\u91cc\uff0c\u662f\u5178\u578b\u7684\u60c5\u611f\u75c7\u72b6\uff1b\u4e25\u91cd\u7684\u632b\u8d25\u611f\u63d0\u793a\u51fa\u73b0\u4e86\u8ba4\u77e5\u65b9\u9762\u7684\u75c7\u72b6\uff1b\u65e0\u89c6\u81ea\u5df1\u7684\u8230\u957f\u8eab\u4efd\uff0c\u653e\u5f03\u6307\u6325\u5e76\u8bc9\u8bf8\u9ebb\u9189\u54c1\u548c\u9152\u7cbe\uff0c\u5df2\u7ecf\u4e0a\u5347\u5230\u4e25\u91cd\u7684\u8fc7\u5931\u884c\u4e3a\u3002\u4e0d\u8fc7\uff0c\u6218\u6597\u5e94\u6fc0\u7684\u9ad8\u9891\u4eba\u7fa4\u662f\u5e74\u8f7b\u3001\u4f4e\u9636\u4e14\u672a\u89c1\u8bc6\u6218\u9635\u7684\u58eb\u5175\u3002\u6797\u6cf0\u66fe\u867d\u4e5f\u662f\u7b2c\u4e00\u6b21\u7ecf\u5386\u5927\u6d77\u6218\uff0c\u4f46\u6bd5\u7adf\u4e0d\u662f\u5bf9\u6d77\u6218\u4e00\u65e0\u6240\u77e5\u7684\u65b0\u5175\u3002\u4ed6\u4ece\u5c11\u5e74\u65f6\u5c31\u63a5\u53d7\u7684\u7cfb\u7edf\u6d77\u519b\u6559\u80b2\uff0c\u5bf9\u6d77\u6218\u7684\u60e8\u70c8\u6709\u5145\u5206\u7684\u5fc3\u7406\u9884\u671f\uff0c\u7136\u800c\u75c7\u72b6\u5374\u6bd4\u666e\u901a\u58eb\u5175\u4e25\u91cd\uff1b\u666e\u901a\u7684\u6218\u6597\u5e94\u6fc0\u60a3\u8005\u5c5e\u4e8e\u6025\u6027\u5fc3\u7406\u5e94\u6fc0\u969c\u788d\uff0c85%\u5728\u4e09\u5929\u5185\u901a\u8fc7\u4f11\u606f\u4f1a\u597d\u8f6c[9]\uff0c\u800c\u6797\u6cf0\u66fe\u7684\u75c7\u72b6\u6301\u7eed\u6027\u52a0\u91cd\uff0c\u76f4\u5230\u53c8\u4e00\u6b21\u91cd\u5927\u4e8b\u6545\u3002\u65b0\u5175\u7b2c\u4e00\u6b21\u7ecf\u5386\u5927\u6218\u5f80\u5f80\u4f1a\u51fa\u73b0\u6025\u6027\u5fc3\u7406\u5e94\u6fc0\u969c\u788d | \u300a\u6bd4\u5229\u00b7\u6797\u6069\u7684\u4e2d\u573a\u6218\u4e8b\u300b\u6839\u636e\u7ecf\u5178\u7684\u6218\u6597-\u9003\u8dd1(Fight-or-flight response)\u00a0\u884c\u4e3a\u5b66\u6a21\u578b\uff1a\u52a8\u7269\u9762\u5bf9\u5e94\u6fc0\u539f\u901a\u5e38\u6709\u4e24\u79cd\u9009\u62e9\uff1a\u8981\u4e48\u9003\u907f\u8981\u4e48\u53cd\u6297\u3002\u4f46\u6709\u610f\u601d\u7684\u662f\uff0c\u7ecf\u8fc7\u6291\u90c1\u9020\u6a21\u7684\u5927\u9f20\u9762\u5bf9\u5c3e\u60ac\u3001\u7535\u51fb\u3001\u5f3a\u8feb\u6e38\u6cf3\u7b49\u5e94\u6fc0\u539f\u7b49\uff0c\u5f80\u5f80\u5448\u73b0\u51fa\u4e0d\u62b5\u6297\u4e0d\u9003\u907f\u7684\u9ebb\u6728\u884c\u4e3a[10]\uff0c\u8fd9\u65f6\u5f88\u96be\u754c\u5b9a\u5b83\u4eec\u5230\u5e95\u662f\u52c7\u6562\u8fd8\u662f\u80c6\u5c0f\u3002\u4eba\u7684\u60c5\u51b5\u672c\u8d28\u4e0a\u7c7b\u4f3c\u3002\u6d77\u6218\u4e2d\u8bf8\u591a\u5b98\u5175\u4e0d\u60e7\u6b7b\u4f24\u987d\u5f3a\u5e94\u6218\uff0c\u6beb\u65e0\u7591\u95ee\u53ef\u8c13\u52c7\u6562\uff1b\u4e5f\u6709\u4eba\u4e34\u9635\u8131\u9003\u6216\u5f53\u7f29\u5934\u4e4c\u9f9f\uff0c\u5982\u6d4e\u8fdc\u8230\u7ba1\u5e26\u65b9\u4f2f\u8c26\u3001\u9547\u8fdc\u8230\u9a7e\u9a76\u5927\u526f\u738b\u73cd\uff0c\u8fd9\u662f\u65e0\u4e89\u8bae\u7684\u80c6\u5c0f\u3002\u800c\u6797\u6cf0\u66fe\u7684\u884c\u4e3a\u548c\u8fd9\u4e24\u79cd\u90fd\u4e0d\u4e00\u6837\uff0c\u66f4\u50cf\u7c7b\u4f3c\u6291\u90c1\u6a21\u578b\u52a8\u7269\u90a3\u79cd\u7cbe\u795e\u969c\u788d\u3002\u90a3\u4e48\uff0c\u662f\u4ec0\u4e48\u5f15\u8d77\u4e86\u6797\u6cf0\u66fe\u7684\u7cbe\u795e\u53d8\u5316\u5462\uff1f\u975e\u6218\u6597\u538b\u529b\u6218\u6597\u5e94\u6fc0\u53cd\u5e94\u901a\u5e38\u5f88\u590d\u6742\uff0c\u8bb8\u591a\u6848\u4f8b\u8868\u660e\u5e76\u975e\u5355\u7eaf\u7684\u6218\u6597\u538b\u529b\u5f15\u8d77\u3002\u8206\u8bba\u538b\u529b\u662f\u4e0d\u53ef\u5ffd\u89c6\u7684\u4e00\u9879\u3002\u4e2d\u56fd\u53e4\u4ee3\u957f\u5e73\u4e4b\u6218\uff0c\u4e00\u4e2a\u53cd\u95f4\u8ba1\u51e0\u4e4e\u5960\u5b9a\u6218\u5c40\uff1b\u7532\u5348\u6218\u524d\u7684\u8d1f\u9762\u8206\u8bba\u6700\u7ec8\u4e5f\u4ea7\u751f\u4e86\u83ab\u5927\u5f71\u54cd\uff0c\u8fd9\u4ef6\u4e8b\u8ffd\u8e2a\u8d77\u6765\u8fd8\u6709\u4e9b\u8010\u4eba\u5bfb\u5473\u3002\u5982\u4eca\u53f2\u5b66\u754c\u8bc1\u5b9e\u5f53\u5e74\u65e5\u672c\u95f4\u8c0d\u5728\u6218\u524d\u6d3b\u52a8\u76f8\u5f53\u7316\u7357\uff0c\u5c31\u8fde\u674e\u9e3f\u7ae0\u7684\u5916\u7525\u5f20\u58eb\u73e9\u90fd\u4e0e\u65e5\u672c\u95f4\u8c0d\u77f3\u5ddd\u4f0d\u4e00\u7275\u8fde\u4e0d\u6e05\u3002\u8fd9\u5c42\u5173\u7cfb\u5bf9\u6797\u6cf0\u66fe\u4e8b\u4ef6\u8d77\u5230\u4ec0\u4e48\u4f5c\u7528\u5df2\u4e0d\u5f97\u800c\u77e5\uff0c\u4f46\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\uff0c\u5173\u952e\u65f6\u671f\u67d0\u4e9b\u52bf\u529b\u84c4\u610f\u6311\u8845\u6fc0\u60f9\u9ad8\u9636\u519b\u4eba\u5f15\u8bf1\u4ed6\u4eec\u51fa\u73b0\u8fc7\u5931\u884c\u4e3a\uff0c\u662f\u4e00\u9879\u519b\u4e8b\u5fc3\u7406\u5b66\u8303\u7574\u7684\u5e38\u89c4\u64cd\u4f5c\u3002\u519b\u4eba\u7684\u627f\u53d7\u529b\u6709\u65f6\u65e0\u6bd4\u575a\u97e7\uff0c\u4f46\u6709\u65f6\u53ef\u5d29\u4e8e\u7ea4\u6beb\u4e4b\u95f4\u30021996\u5e74\uff0c\u65f6\u4efb\u7f8e\u6d77\u519b\u4f5c\u6218\u90e8\u957f\u7684\u5e03\u5c14\u8fbe\uff08Jeremy Boorda\uff09\u56db\u661f\u4e0a\u5c06\u5728\u529e\u516c\u5ba4\u4ee5\u624b\u67aa\u81ea\u6215\u3002\u4ed6\u662f\u4e00\u4f4d\u53c2\u52a0\u8fc7\u8d8a\u6218\u548c\u6ce2\u9ed1\u6218\u4e89\u4e14\u529f\u52cb\u5353\u8457\u7684\u519b\u4eba\uff0c\u4e5f\u662f\u7f8e\u56fd\u4e8c\u767e\u591a\u5e74\u6765\u552f\u4e00\u4ece\u6c34\u5175\u6210\u957f\u4e3a\u6d77\u519b\u603b\u53f8\u4ee4\u7684\u4f20\u5947\u519b\u4eba\uff0c\u81ea\u6740\u539f\u56e0\u4ec5\u4ec5\u662f\u56e0\u4e3a\u88ab\u5a92\u4f53\u8d28\u7591\u4e0d\u5177\u5907\u4f69\u6234\u201cV\u5b57\u52cb\u7ae0\u201d\u7684\u8d44\u683c\u30021996\u5e745\u670816\u65e5\uff0c\u7f8e\u56fd\u6d77\u519b\u4f5c\u6218\u90e8\u957f\u6770\u91cc\u7c73\u00b7\u8fc8\u514b\u5c14\u00b7\u5e03\u5c14\u8fbe\u6d77\u519b\u4e0a\u5c06\u5728\u529e\u516c\u5ba4\u91cc\u81ea\u6740 |\u00a0Wikimedia Commons\u519b\u4eba\u7684\u804c\u4e1a\u7279\u5f81\u51b3\u5b9a\u4ed6\u4eec\u91cd\u89c6\u8363\u8a89\u751a\u4e8e\u751f\u547d\uff0c\u8fd9\u6070\u6070\u6210\u4e3a\u4ed6\u4eec\u7684\u8f6f\u808b\u3002\u5e03\u5c14\u8fbe\u4e0a\u5c06\u81ea\u6740\u4e8b\u4ef6\u5f15\u8d77\u7f8e\u56fd\u56fd\u9632\u90e8\u7684\u9ad8\u5ea6\u91cd\u89c6\uff0c\u542f\u52a8\u4e86\u4ee5\u519b\u4e8b\u5fc3\u7406\u5b66\u4e3a\u57fa\u7840\u7684\u5404\u519b\u79cd\u7684\u81ea\u6740\u9884\u9632\u8ba1\u5212\u548c\u76f8\u5173\u7814\u7a76\u3002\u6765\u81ea\u7f8e\u519b\u533b\u5b66\u673a\u6784\u7684\u5404\u9879\u6570\u636e\u663e\u793a\uff0c\u519b\u4eba\u81ea\u6740\u6848\u4f8b\u4e2d\u88ab\u8bca\u65ad\u4e3a\u6291\u90c1\u60c5\u7eea\u3001\u4eba\u683c\u969c\u788d\u6216\u9002\u5e94\u4e0d\u826f\u7684\u6bd4\u4f8b\u5e38\u8d85\u8fc790%\u3002[11-12]\u6797\u6cf0\u66fe\u53c8\u662f\u4ec0\u4e48\u60c5\u51b5\uff1f\u5173\u4e8e\u6291\u90c1\u6027\u4eba\u683c\u7684\u7814\u7a76\u5341\u5206\u9a73\u6742\uff0c\u65e9\u5148\u8ba4\u4e3a\u56fa\u6267\u3001\u6c89\u9ed8\u3001\u654f\u611f\u3001\u505a\u4e8b\u4e00\u4e1d\u4e0d\u82df\u3001\u7ae5\u5e74\u5931\u53bb\u7236\u6bcd\u3001\u4ece\u5c0f\u6839\u690d\u63d0\u9ad8\u5bb6\u5ead\u793e\u4f1a\u5730\u4f4d\u7684\u5fd7\u5411\u3001D\u578b\u4eba\u683c\uff08\u5177\u6709\u6d88\u6781\u60c5\u611f\u3001\u793e\u4ea4\u6291\u5236\u7279\u70b9\uff09\u7b49\u7279\u5f81\u4e0e\u6291\u90c1\u75c7\u53d1\u75c5\u7387\u6709\u4e00\u5b9a\u5173\u8054\u3002\u4f5c\u4e3a\u6797\u5219\u5f90\u4f84\u5b59\u7684\u6797\u6cf0\u66fe\u51fa\u8eab\u540d\u95e8\u3001\u81ea\u5e7c\u7236\u6bcd\u53cc\u4ea1\u3001\u5bb6\u5883\u8270\u8f9b\u5e76\u80a9\u8d1f\u4e2d\u5174\u5bb6\u65cf\u7684\u4f7f\u547d\u3001\u5e73\u65e5\u201c\u6027\u6c89\u9ed8\uff0c\u5be1\u8a00\u7b11\uff0c\u80fd\u5fcd\u4eba\u4e4b\u6240\u4e0d\u80fd\u5fcd\u201d\uff0c\u4e0a\u8ff0\u7279\u5f81\u7adf\u7136\u5168\u4e2d\uff1b\u7136\u800c\uff0c\u53e6\u4e00\u4e9b\u4e0e\u6291\u90c1\u75c7\u8d1f\u76f8\u5173\u7684\u4eba\u683c\u7279\u5f81\uff0c\u5982\u968f\u548c\u3001\u8d23\u4efb\u5fc3[13]\u7b49\uff0c\u6709\u201c\u5f85\u4e0b\u4ec1\u6055\u201d\u201c\u5b58\u5fc3\u6148\u539a\u201d\u201c\u6027\u884c\u5fe0\u8c28\u201d\u4e4b\u8a89\u7684\u6797\u6cf0\u66fe\u4e5f\u90fd\u62e5\u6709\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4eca\u5929\u5173\u4e8e\u6291\u90c1\u6027\u4eba\u683c\u7684\u8bba\u8c03\u683c\u5916\u4e25\u8c28\uff0c\u8fc7\u53bb\u8ba4\u4e3a\u7684\u8bb8\u591a\u7279\u5f81\u5982\u4eca\u90fd\u4e0d\u88ab\u8ba4\u4e3a\u662f\u6291\u90c1\u75c7\u7684\u9884\u6d4b\u56e0\u5b50\u3002\u6240\u4ee5\u4ec5\u51ed\u53f2\u6599\u4e2d\u6797\u6cf0\u66fe\u5448\u73b0\u7684\u4eba\u683c\u7279\u70b9\u8fd8\u771f\u65e0\u6cd5\u5f97\u51fa\u4ed6\u662f\u5426\u5bb9\u6613\u60a3\u6291\u90c1\u75c7\u7684\u7ed3\u8bba\u3002\u65f6\u81f3\u4eca\u65e5\uff0c\u4f9d\u7136\u6ca1\u6709\u9488\u5bf9\u6291\u90c1\u75c7\u7684\u7279\u5f02\u6027\u68c0\u67e5\u9879\u76ee\uff0c\u6291\u90c1\u75c7\u7684\u8bca\u65ad\u9664\u4e86\u9700\u8981\u8eaf\u4f53\u68c0\u67e5\u53ca\u795e\u7ecf\u7cfb\u7edf\u68c0\u67e5\u5916,\u8fd8\u9700\u8981\u8f85\u52a9\u68c0\u67e5\u53ca\u5b9e\u9a8c\u5ba4\u68c0\u67e5 | \u7535\u5f71\u300a\u4e00\u5ff5\u65e0\u660e\u300b\u4e2d\u60a3\u6709\u6291\u90c1\u75c7\u7684\u963f\u4e1c\u4ed6\u7684\u60c5\u51b5\uff0c\u66f4\u53ef\u80fd\u662f\u4e00\u7cfb\u5217\u5916\u754c\u56e0\u7d20\uff0c\u5305\u62ec\u9876\u7aef\u56f0\u60d1\u548c\u594b\u6597\u76ee\u6807\u574d\u584c\u9020\u6210\u7684\u5fc3\u7406\u969c\u788d\u3002\u4ece\u53f2\u6599\u4e2d\u770b\u5230\uff0c\u6797\u6cf0\u66fe\u548c\u540c\u5b66\u4eec\u521a\u4ece\u8239\u653f\u5b66\u5802\u6bd5\u4e1a\u65f6\u66fe\u6709\u8c6a\u8a00\u58ee\u8bed\uff1a\u201c\u4ece\u4eca\u800c\u540e\uff0c\u8981\u53bb\u5bf9\u4ed8\u98d3\u98ce\uff0c\u63a7\u5236\u72c2\u6d6a\uff0c\u7aa5\u6d4b\u65e5\u661f\u7684\u884c\u52a8\uff0c\u4e86\u89e3\u66b4\u98ce\u7684\u89c4\u5f8b\u2026\u2026\u8ba9\u6700\u53ef\u6015\u7684\u56f0\u96be\u53d8\u6210\u5e73\u6613\u3001\u6700\u9669\u6076\u7684\u60c5\u51b5\u6210\u4e3a\u9759\u8c27\u2026\u2026\u6211\u4eec\u7684\u7231\u56fd\u5fc3\u5c06\u4e0d\u51cf\u5c11\u201d\uff0c\u591a\u5e74\u4ee5\u540e\uff0c\u4ed6\u5374\u5728\u65e5\u8bb0\u4e2d\u81ea\u5632\u4e3a\u201c\u4e00\u4ecb\u6b66\u592b\u201d\uff0c\u632b\u8d25\u6781\u5f3a\u5730\u611f\u53f9\u62a5\u56fd\u65e0\u95e8\uff0c\u5982\u6b64\u5929\u58e4\u4e4b\u522b\u771f\u6709\u4e9b\u8bbd\u523a\u3002\u6797\u6cf0\u66fe\u5b57\u91cc\u884c\u95f4\u900f\u51fa\u7684\u662f\uff1a\u6211\u539f\u4ee5\u4e3a\u81ea\u5df1\u6210\u4e86\u56fd\u4e4b\u5e72\u57ce\uff0c\u54ea\u77e5\u5230\u5934\u6765\u8fd8\u662f\u4e00\u4ecb\u6b66\u592b\uff0c\u4e3a\u56fd\u5907\u6218\u4e0d\u9057\u4f59\u529b\uff0c\u8c01\u77e5\u4e3b\u5e05\u548c\u671d\u5ef7\u5374\u5404\u6000\u5fc3\u601d\u3002\u8881\u4e16\u51ef\u4e09\u7535\u4e5e\u5f52\uff0c\u53f6\u5fd7\u8d85\u63a8\u8bff\u8d23\u4efb\uff0c\u6211\u5162\u5162\u4e1a\u4e1a\u53cd\u6210\u4e86\u201c\u80c6\u602f\u5f20\u7687\u201d\u201c\u754f\u65e5\u9041\u8d70\u201d\u2026\u2026\u62a5\u56fd\u65e0\u95e8\uff0c\u7f6a\u7531\u6211\u8d77\uff0c\u6d3b\u7740\u592b\u590d\u4f55\u76ca\uff1f\u6797\u6cf0\u66fe\u7a7a\u6000\u804c\u4e1a\u519b\u4eba\u7684\u8d23\u4efb\u611f\uff0c\u4ee5\u53ca\u4e00\u8154\u62a5\u56fd\u4e4b\u5fd7\uff0c\u5374\u56e0\u6e05\u653f\u5e9c\u7684\u660f\u8069\u65e0\u80fd\u548c\u9886\u5bfc\u7684\u8f7b\u7387\u8bef\u5224\uff0c\u6210\u4e86\u4e00\u5df1\u79c1\u5229\u4e0b\u7684\u70ae\u7070\uff0c\u8c6a\u60c5\u58ee\u5fd7\u5316\u4e3a\u5fc3\u7070\u610f\u51b7\u548c\u65e0\u52a9\u7edd\u671b\u3002\u9152\u7cbe\u3001\u9ebb\u9189\u54c1\u6ee5\u7528\u6700\u540e\uff0c\u9020\u6210\u4ed6\u75c5\u7a0b\u7684\u52a0\u901f\u7684\u9664\u4e86\u6218\u6597\u3001\u975e\u6218\u6597\u538b\u529b\u5916\uff0c\u5f88\u53ef\u80fd\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\u30021770\u5e74\u82f1\u56fd\u7687\u5bb6\u6d77\u519b\u5f00\u59cb\u7ed9\u897f\u5370\u5ea6\u8230\u961f\u7684\u6c34\u624b\u5b9a\u91cf\u914d\u7ed9\u63ba\u6c34\u7684\u6717\u59c6\u9152\u6216\u5a01\u58eb\u5fcc\uff0c\u6807\u5fd7\u7740\u7687\u5bb6\u6d77\u519b\u5f00\u59cb\u6b63\u5f0f\u51cf\u5c11\u9ebb\u9189\u54c1\u4f9d\u8d56\u7684\u52aa\u529b\u3002\u55ef\uff0c\u4f60\u6ca1\u770b\u9519\uff0c\u8fd9\u662f\u7687\u5bb6\u6d77\u519b\u65e8\u5728\u964d\u4f4e\u5b98\u5175\u9152\u7cbe\u6ee5\u7528\u7684\u52aa\u529b\uff0c\u56e0\u4e3a\u90a3\u4e9b\u5bb6\u4f19\u4e4b\u524d\u90fd\u76f4\u63a5\u559d\u70c8\u9152\u3002\u8fc7\u53bb\u519b\u961f\u7684\u9152\u7cbe-\u9ebb\u9189\u54c1\u6ee5\u7528\u662f\u4e2a\u666e\u904d\u95ee\u9898\u3002\u7f8e\u56fd\u5185\u6218\u4e2d\u9157\u9152\u548c\u5438\u98df\u9e26\u7247\u53f8\u7a7a\u89c1\u60ef\uff0c\u8d8a\u6218\u671f\u95f434%\u7684\u58eb\u5175\u5438\u98df\u8fc7\u5927\u9ebb\uff0c50%\u7684\u4eba\u670d\u8fc7\u6d77\u6d1b\u56e0\u3002\u519b\u4eba\u9762\u4e34\u7740\u5e73\u6c11\u4e0d\u4f1a\u9047\u5230\u7684\u5de8\u5927\u538b\u529b\uff0c\u8fd9\u79cd\u751f\u6d3b\u65b9\u5f0f\u672c\u8eab\u5c31\u662f\u9152\u7cbe\u548c\u9ebb\u9189\u54c1\u6ee5\u7528\u7684\u8bf1\u56e0\u30022013\u5e7414\u540d\u5fb7\u56fd\u9a7b\u963f\u5bcc\u6c57\u58eb\u5175\u56e0\u4e3a\u9157\u9152\u95f9\u4e8b\u88ab\u9063\u8fd4 | \u5fb7\u56fd\u300a\u56fe\u7247\u62a5\u300b\u5317\u6d0b\u6d77\u519b\u540c\u6837\u6ca1\u80fd\u89e3\u51b3\u8fd9\u4e00\u95ee\u9898\uff0c\u5bfc\u81f4\u6797\u6cf0\u66fe\u80fd\u591f\u201c\u7f6e\u6d53\u9152\u201d\u201c\u5907\u8bf8\u6b7b\u5177\u5982\u8ff7\u836f\u6d0b\u70df\u201d\u3002\u6d0b\u70df\u662f\u9e26\u7247\uff0c\u8ff7\u836f\u6216\u8bb8\u662f\u9ebb\u9189\u5242\uff0c\u751a\u81f3\u53ef\u80fd\u662f\u5f53\u65f6\u5df2\u7ecf\u5f00\u59cb\u4f7f\u7528\u7684\u53e4\u67ef\u78b1\u3002\u53ef\u4ee5\u8bd5\u60f3\uff0c\u6797\u5728\u60c5\u7eea\u4f4e\u843d\u65f6\u6cbe\u67d3\u4e0a\u8fd9\u4e9b\u4e1c\u897f\u4ee5\u81f3\u6c89\u8ff7\uff0c\u5728\u90a3\u4e2a\u65f6\u68d8\u4eba\u56f0\u4e4b\u9645\uff0c\u52a0\u901f\u4e86\u4ed6\u7684\u7cbe\u795e\u72b6\u51b5\u7684\u6076\u5316\u3002\u6797\u6cf0\u66fe\u662f\u5426\u80c6\u5c0f\u6050\u6015\u8fd8\u4f1a\u7ee7\u7eed\u4e89\u8bae\u3002\u4f46\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\u4e16\u4e0a\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u662f\u7cbe\u795e\u4e0a\u7684\u5b8c\u4eba\uff0c\u9762\u5bf9\u6218\u4e89\u7684\u4eba\u6c38\u8fdc\u90fd\u5145\u6ee1\u4e0d\u786e\u5b9a\u6027\u3002\u6797\u6cf0\u66fe\u5c11\u5c06\u7684\u81ea\u6740\u4e8b\u4ef6\u8fc7\u5f80\u5df2\u4e45\uff0c\u5c31\u7b97\u4e0d\u80fd\u50cf\u5e03\u5c14\u8fbe\u4e0a\u5c06\u90a3\u6837\u5bf9\u519b\u4e8b\u5fc3\u7406\u5b66\u4ea7\u751f\u63a8\u52a8\u529b\uff0c\u5982\u679c\u80fd\u6210\u4e3a\u4e00\u4e2a\u5c0f\u5c0f\u7684\u53f2\u9274\uff0c\u5c31\u662f\u7ffb\u8fd9\u7b14\u65e7\u8d26\u7684\u610f\u4e49\u3002\uff08\u7f16\u8f91\uff1aYuki\uff09\u672c\u6587\u611f\u8c22\u6d77\u519b\u53f2\u7814\u7a76\u8005\u9648\u60a6\u3001\u65b9\u79be\u3001\u5409\u8fb0\u3001\u674e\u5ae3\u3001\u674e\u7389\u751f\u3001\u5218\u70dc\u8d6b\u3001\u5218\u81f4\u3001\u5415\u671b\u8212\u3001\u968b\u4e1c\u5347\u3001\u5b59\u5efa\u519b\u3001\u738b\u9e64\u3001\u5f20\u9ece\u6e90\u7684\u652f\u6301\u3002\u53c2\u8003\u6587\u732e\uff1a[1]\u76db\u5ba3\u6000\u6863\u6848\u8d44\u6599\u9009\u8f91\u4e4b\u4e09\uff1a\u4e2d\u65e5\u7532\u5348\u6218\u4e89\uff08\u4e0b\uff09[M]\uff0c\u4e0a\u6d77\u4eba\u6c11\u51fa\u7248\u793e, 1980\u5e74\u7248.[2]\u674e\u9e3f\u7ae0\u5168\u96c6\uff0825\uff09[M]\uff0c\u5b89\u5fbd\u6559\u80b2\u51fa\u7248\u793e,\u00a02008\uff0cp172.\u00a0[3] \u738b\u8299\u84c9, \u5f20\u4e9a\u6797, \u6768\u4e16\u660c. \u519b\u5b98\u804c\u4e1a\u4eba\u683c\u91cf\u8868\u7684\u521d\u6b65\u7f16\u5236[J].\u00a0\u4e2d\u56fd\u4e34\u5e8a\u5fc3\u7406\u5b66\u6742\u5fd7,\u00a02006, (03):224-6.[4]\u5b59\u5efa\u519b. \u9547\u8fdc\u8230\u957f\u6797\u6cf0\u66fe\u5728\u97e9\u89c2\u5bdf\u8986\u547d\u4e66\u7b3a\u6ce8[J]. \u5927\u8fde\u8fd1\u4ee3\u53f2\u7814\u7a76, 2010, 7: 44-72.[5]\u7530\u6c49.\u00a0\u5173\u4e8e\u4e2d\u56fd\u6d77\u519b\u7684\u51e0\u4e2a\u95ee\u9898\u00b7\u6797\u6cf0\u66fe[J].\u00a0\u6d77\u519b\u6574\u5efa\u6708\u520a,\u00a01941(\u6c11\u56fd\u4e09\u5341\u5e74), 1(12): 5-6.[6]\u674e\u00b7\u9a6c\u5409\u82ac \u8457\uff0c\u5f20\u9ece\u6e90 \u8bd1. \u4ed6\u9009\u62e9\u4e86\u4e2d\u56fd[M].\u00a0\u5c71\u4e1c\u753b\u62a5\u51fa\u7248\u793e,\u00a02013.\u00a0[7]\u5b59\u5efa\u519b \u6574\u7406\u6821\u6ce8.\u00a0\u5317\u6d0b\u6d77\u519b\u5b98\u5175\u56de\u5fc6\u8f91\u5f55[M].\u00a0\u5c71\u4e1c\u753b\u62a5\u51fa\u7248\u793e,\u00a02016[8]\u9648\u60a6.\u00a0\u4e2d\u65e5\u7532\u5348\u9ec4\u6d77\u5927\u51b3\u6218[M].\u00a0\u53f0\u6d77\u51fa\u7248\u793e.\u00a02019.[9] Carrie H Kennedy, Eric Zillmer.\u00a0Military Psychology: Clinical and operational applications [M]. Guilford Publications, 2012, 2nd New edition.[10] Castagn\u00e9 V, Moser P, Roux S, Porsolt RD. Rodent models of depression: forced swim and tail suspension behavioral despair tests in rats and mice. Curr Protoc Neurosci. 2011;Chapter 8:Unit 8.10A.[11] Trent LK. Parasuicides in the Navy and Marine Corps: Hospital admissions, 1989-1995. (Techical Document 99-4D). 1999, San Diego, CA: Naval Health Research Center.[12] Ritchie EC, Keppler WC, Rothberg JM. Suicidal admissions in the United States military. Military Medicine, 2003, 168, 177-181.[13] Jenkins EN, Allison P, Innes K, Violanti JM, Andrew ME. Depressive Symptoms Among Police Officers: Associations with Personality and Psychosocial Factors. J Police Crim Psychol. 2019 Mar;34(1):66-77.", "title": "\u8fd9\u4f4d\u624d\u534e\u6a2a\u6ea2\u7684\u6d77\u519b\u5c11\u5c06\uff0c\u56e0\u4f55\u6218\u65f6\u7a81\u7136\u670d\u6bd2\u81ea\u6740\uff1f", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "", "normal": "", "small": ""}, "introduction": "", "nickname": "\u4e0d\u542f\u8f69", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "\u4e0d\u542f\u8f69", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [], "content": "<p><!--StartFragment--><span style=\"font-size:14px\"><strong><span style=\"color:#696969\">|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</span></strong></span><!--EndFragment--></p>\n\n<p>\u751f\u5b69\u5b50\uff0c\u5582\u5b69\u5b50\uff0c\u4e00\u76f4\u88ab\u89c6\u4e3a\u4e00\u5927\u5bb6\u5b50\u7684\u4eba\u751f\u5934\u7b49\u5927\u4e8b\u3002</p>\n\n<p>\u65e2\u662f\u5927\u4e8b\uff0c\u8bb2\u7a76\u5fc5\u591a\u3002\u4e8e\u662f\u5404\u79cd&ldquo;\u7981\u5fcc&rdquo;\u3001&ldquo;\u5999\u62db&rdquo;\u5c42\u51fa\u4e0d\u7a77\uff0c\u4ec0\u4e48\u8981\u5403\u6728\u74dc\u4e0b\u5976\u554a\uff0c\u8981\u5403\u9e45\u86cb\u89e3\u80ce\u6bd2\u554a\uff0c\u542c\u7740\u90fd\u8ddf\u4fee\u70bc\u5230\u4e86\u8903\u8282\u513f\u4e0a\u4e34\u8981\u5f97\u9053\u5f97\u53bb\u9664\u5996\u6c14\u4f3c\u7684\u3002</p>\n\n<p>\u8fd9\u7c7b\u8bf4\u6cd5\u8fd8\u6709\u4e2a\u7279\u70b9\uff0c\u5c31\u662f\u7279\u522b\u80fd\u76f8\u65f6\u800c\u52a8\uff0c\u5404\u79cd\u53d8\u4f53\u6781\u5177\u521b\u65b0\u6027&mdash;&mdash;\u6bcd\u4e73\u4f1a\u5f15\u8d77\u5a74\u513f\u8fc7\u654f\uff0c\u4ea7\u540e\u6291\u90c1\u4f1a\u5f71\u54cd\u5976\u6c34\u8d28\u91cf&mdash;&mdash;\u611f\u89c9\u7279\u522b\u4e0e\u65f6\u4ff1\u8fdb\u5427\uff1f\u4f46\u5c31\u5982\u6211\u4eec\u90fd\u77e5\u9053\u7684\uff0c\u8fd9\u4e9b\u8bf4\u6cd5\u597d\u591a\u90fd\u4e0d\u9760\u8c31\u3002</p>\n\n<p>\u6211\u4eec\u68b3\u7406\u4e8612\u6761\u6709\u5173\u6bcd\u4e73\u5582\u517b\u6700\u5e7f\u4e3a\u6d41\u4f20\u7684\u8c23\u8a00\u3002\u867d\u8bf4\u8c23\u8a00\u5e74\u5e74\u6709\uff0c\u5927\u5bb6\u65e9\u5c31\u505a\u597d\u4e86\u6301\u4e45\u6218\u7684\u51c6\u5907\uff0c\u4f46\u8fd8\u662f\u5e0c\u671b\u8fd912\u6761\u8c23\u8a00\u53ef\u4ee5\u7559\u5728\u4eca\u5e74\uff0c\u80fd\u8ba9\u5927\u5bb6\u5c24\u5176\u662f\u5988\u5988\u4eec\u6536\u83b7\u6b63\u786e\u7684\u77e5\u8bc6\uff0c\u4e0d\u8981\u5728\u6765\u5e74\u7ee7\u7eed\u8ba9\u8fd9\u4e9b\u4e0d\u9760\u8c31\u7684\u8bf4\u6cd5\u56f0\u6270\u4e86\u3002</p>\n\n<p><strong>\u8c23\u8a001: </strong>\u521d\u4e73\u91cc\u9762\u6709\u80ce\u6bd2\u8981\u5148\u5403\u9e45\u86cb\u5316\u89e3\u540e\u624d\u80fd\u5582\u5976</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67901</strong><br />\n&ldquo;\u80ce\u6bd2&rdquo;\u5e76\u4e0d\u5b58\u5728\uff0c\u6bcd\u4e73\u91cc\u66f4\u4e0d\u53ef\u80fd\u542b\u6709\u6709\u6bd2\u7269\u8d28\u3002\u5a74\u513f\u5e38\u89c1\u7684\u8fc7\u654f\u3001\u6e7f\u75b9\u3001\u76ae\u708e\u7b49\u90fd\u6709\u5404\u81ea\u7684\u53d1\u75c5\u539f\u56e0\uff0c\u53ea\u662f\u53e4\u4eba\u672a\u80fd\u6709\u8db3\u591f\u7684\u533b\u5b66\u77e5\u8bc6\uff0c\u53ea\u80fd\u6982\u5f52\u5165&ldquo;\u80ce\u6bd2&rdquo;\u8fd9\u4e00\u6a21\u7cca\u7684\u6982\u5ff5\u3002</p>\n\n<p><strong>\u8c23\u8a002</strong>\uff1a\u521d\u4e73\u662f\u6700\u6709\u8425\u517b\u7684\uff01\u4e00\u5b9a\u8981\u7ed9\u5b9d\u5b9d\u5403\u521d\u4e73</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67902</strong><br />\n\u4eba\u521d\u4e73\u80af\u5b9a\u662f\u73cd\u8d35\u7684\uff0c\u4f46\u4e5f\u7edd\u975e\u975e\u5403\u4e0d\u53ef\u3002\u66f4\u91cd\u8981\u7684\u610f\u4e49\u5728\u4e8e\u5a74\u513f\u5c3d\u5feb\u542e\u5438\u6bcd\u4eb2\u4e73\u5934\uff0c\u80fd\u8ba9\u6ccc\u4e73\u53cd\u5c04\u5728\u7b2c\u4e00\u65f6\u95ee\u542f\u52a8\uff0c\u4fdd\u8bc1\u540e\u9762\u5a74\u513f\u5bf9\u5976\u91cf\u7684\u9700\u6c42\u3002</p>\n\n<p><strong>\u8c23\u8a003</strong>\uff1a\u4e73\u4e43\u8840\u6240\u5316\uff0c\u9759\u7f6e\u4e09\u5e74\u53ef\u770b\u5230\u53d8\u56de\u8840\u6db2</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67903</strong><br />\n\u4e73\u817a\u7531\u6c57\u817a\u5206\u5316\u6f14\u53d8\u800c\u6765\uff0c\u8840\u6db2\u4f1a\u4e3a\u4e73\u817a\u5e26\u53bb\u6c27\u6c14\u548c\u8425\u517b\u7269\u8d28\uff0c\u4f46\u6b63\u5e38\u60c5\u51b5\u4e0b\u8840\u7ea2\u7ec6\u80de\u65e0\u6cd5\u901a\u8fc7\u8840\u7ba1\u58c1\uff0c\u6240\u4ee5\u4e73\u6c41\u4e2d\u4e0d\u53ef\u80fd\u6709\u8840\u7ea2\u86cb\u767d\u548c\u6210\u8840\u7ec6\u80de\u3002\u6240\u8c13&ldquo;\u653e\u4e09\u5e74\u53d8\u8840&rdquo;\u66f4\u662f\u65e0\u7a3d\u4e4b\u8c08\u3002</p>\n\n<p><strong>\u8c23\u8a004</strong>\uff1a\u7c73\u9152\u548c\u7a7f\u5c71\u7532\u53ef\u4ee5\u4e0b\u5976</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67904</strong><br />\n\u9152\u7cbe\u4f1a\u901a\u8fc7\u4e73\u6c41\u5206\u6ccc\u88ab\u5a74\u513f\u542e\u5438\u5230\uff0c\u5f71\u54cd\u5176\u8eab\u4f53\u548c\u667a\u529b\u53d1\u80b2\uff1b\u7a7f\u5c71\u7532\u4e5f\u6ca1\u6709\u4efb\u4f55\u7279\u6b8a\u6210\u5206\u53ef\u4ee5&ldquo;\u4e0b\u5976&rdquo;\u3002\u7531\u4e8e\u7c73\u9152\u5448\u5976\u767d\u8272\uff0c\u7a7f\u5c71\u7532\u7684&ldquo;\u7a7f&rdquo;\u7ed9\u4eba\u901a\u4e73\u7684\u611f\u89c9\uff0c\u6545\u800c\u4ea7\u751f\u8fd9\u79cd\u9644\u4f1a\u7684\u8bf4\u6cd5\u3002</p>\n\n<p><strong>\u8c23\u8a005</strong>\uff1a\u4ea7\u540e\u6291\u90c1\u4f1a\u5f71\u54cd\u6bcd\u4e73\u8d28\u91cf</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67905</strong><br />\n\u4ea7\u540e\u51fa\u73b0\u6291\u90c1\u60c5\u7eea\u662f\u5e38\u89c1\u7684\uff0c\u75c7\u72b6\u6216\u8f7b\u6216\u91cd\u3002\u4f46\u6ca1\u6709\u4efb\u4f55\u7814\u7a76\u8bc1\u636e\u8868\u660e\uff0c\u6bcd\u4eb2\u4f53\u5185\u4f1a\u56e0\u60c5\u7eea\u6ce2\u52a8\u4ea7\u751f\u6bd2\u7d20\uff0c\u66f4\u4e0d\u4f1a\u56e0\u6bcd\u4e73\u5582\u517b\u6bd2\u6b7b\u5a74\u513f\u3002</p>\n\n<p><strong>\u8c23\u8a006</strong>\uff1a\u6bcd\u4e73\u5582\u517b\u4f1a\u4f20\u64ad\u4e59\u809d</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67906</strong><br />\n\u5b9d\u5b9d\u4ece\u5988\u5988\u4f20\u67d3\u4e0a\u4e59\u809d\u57fa\u672c\u4e0a\u662f\u5728\u5206\u5a29\u8fc7\u7a0b\u4e2d\uff0c\u6bcd\u4e73\u5582\u517b\u4e0d\u4f1a\u589e\u52a0\u4e59\u809d\u5988\u5988\u6240\u751f\u5a74\u513f\u7684\u4e59\u809d\u611f\u67d3\u98ce\u9669\u3002<br />\n\u53ea\u6709\u540c\u65f6\u7b26\u5408\u4ee5\u4e0b\u6240\u6709\u6761\u4ef6\u611f\u67d3\u624d\u53ef\u80fd\u53d1\u751f\uff1a<br />\n- \u5988\u5988\u4e0e\u5b9d\u5b9d\u7684\u4f53\u6db2\u53d1\u751f\u4ea4\u6362\uff08\u5988\u5988\u51fa\u73b0\u4e73\u5934\u7834\u635f\uff0c\u540c\u65f6\u5b9d\u5b9d\u51fa\u73b0\u80a0\u80c3\u4f24\u53e3\uff09\uff1b<br />\n- \u5988\u5988\u7684\u4e59\u809d\u75c5\u6bd2\u6d53\u5ea6\u5f88\u9ad8\uff1b<br />\n- \u5c0f\u5b69\u5bf9\u4e59\u809d\u75c5\u6bd2\u6ca1\u6709\u514d\u75ab\u529b\uff08\u5982\u63a5\u79cd\u75ab\u82d7\u540e\u6ca1\u6709\u514d\u75ab\u5e94\u7b54\uff09</p>\n\n<p><strong>\u8c23\u8a007</strong>\uff1a\u6bcd\u4eb2\u53d1\u70e7\u4e0d\u80fd\u7ed9\u5b69\u5b50\u5582\u5976</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67907</strong><br />\n\u4e0a\u611f\u7c7b\u53d1\u70e7\uff0c\u6b63\u5e38\u5582\u5976\uff0c\u4e73\u6c41\u4e0d\u662f\u75c5\u6bd2\u4f20\u64ad\u7684\u9014\u5f84\uff0c\u53cd\u800c\u80fd\u628a\u6297\u4f53\u7ed9\u5b9d\u5b9d\uff1b\u80c3\u80a0\u9053\u75be\u75c5\u5f15\u8d77\u7684\u53d1\u70e7\uff0c\u6b63\u5e38\u5582\u5976\uff0c\u6ce8\u610f\u6234\u53e3\u7f69\u591a\u6d17\u624b\uff0c\u4e0d\u4eb2\u543b\u5b9d\u5b9d\uff1b\u4e73\u817a\u708e\u5f15\u8d77\u7684\u53d1\u70e7\uff0c\u66f4\u5f97\u7ee7\u7eed\u5582\u5976\uff0c\u5426\u5219\u4f1a\u52a0\u91cd\u5835\u5976\uff1b\u8d8538.5\u2103\u53ef\u5403\u5b9d\u5b9d\u7528\u9000\u70e7\u836f\u5426\u5219\u5976\u592a\u70eb\u5b9d\u5b9d\u53ef\u80fd\u4e0d\u5403\uff0c\u53e6\u5916\u5988\u5988\u4f53\u611f\u975e\u5e38\u4e0d\u9002\u3002</p>\n\n<p><strong>\u8c23\u8a008</strong>\uff1a\u5b9d\u5b9d\u62c9\u809a\u5b50\u662f\u5bf9\u6bcd\u4e73\u8fc7\u654f</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67908</strong><br />\n\u6bcd\u4e73\u8fc7\u654f\u53d1\u751f\u7387\u6781\u4f4e\u3002\u9996\u5148\u5e94\u8be5\u6000\u7591\u7ec6\u83cc\u611f\u67d3\uff0c\u5176\u6b21\u6000\u7591\u8f85\u98df\u5bfc\u81f4\u8179\u6cfb\uff0c\u8fd8\u6709\u5b9d\u5b9d\u53e3\u670d\u836f\u7269\u5f15\u8d77\u8fc7\u654f\uff1b\u6b64\u5916\u6000\u7591\u914d\u65b9\u5976\u7c89\u4e2d\u7684\u725b\u5976\u86cb\u767d\u8fc7\u654f\uff08\u5373\u4fbf\u6539\u4e3a\u5168\u6bcd\u4e73\u5582\u517b\uff0c\u4e4b\u524d\u7684\u8fc7\u654f\u75c7\u72b6\u4ecd\u53ef\u80fd\u5b58\u5728\uff09\u3002</p>\n\n<p><strong>\u8c23\u8a009</strong>\uff1a\u5b9d\u5b9d\u603b\u662f\u54ed\u5c31\u8bf4\u660e\u5976\u4e0d\u591f</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u67909</strong><br />\n\u5982\u997f\u3001\u80a0\u7ede\u75db\u3001\u6ca1\u7761\u591f\u3001\u6ca1\u6709\u5b89\u5168\u611f\u3001\u5bfb\u6c42\u5b89\u629a\u90fd\u4f1a\u5f15\u8d77\u5a74\u513f\u54ed\uff0c\u5982\u679c\u6bcf\u6b21\u90fd\u4ee5\u4e3a\u662f\u997f\u3001\u90fd\u4ee5\u5976\u5934\u4f5c\u4e3a\u5b89\u629a\uff0c\u53cd\u800c\u4e0d\u597d\u3002</p>\n\n<p><strong>\u8c23\u8a0010</strong>\uff1a\u5b9d\u5b9d\u4e00\u5c81\u540e\u6bcd\u4e73\u5c31\u6ca1\u8425\u517b\u4e86</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u679010</strong><br />\n\u6bcd\u4e73\u8425\u517b\u4e0d\u4f1a\u56e0\u4e3a\u5582\u517b\u65f6\u95f4\u957f\u800c\u6539\u4f1a\u56e0\u4e3a\u5582\u517b\u65f6\u95f4\u957f\u800c\u6539\u53d8\uff0c\u53ea\u4f1a\u968f\u7740\u5b69\u5b50\u6dfb\u52a0\u8f85\u98df\uff0c\u5403\u6bcd\u4e73\u6e10\u5c11\u800c\u51cf\u5c11\u5206\u6ccc\u91cf\u3002WHO\u5efa\u8bae\u575a\u6301\u6bcd\u4e73\u5582\u517b\u5230\u4e24\u5c81\u3002</p>\n\n<p><strong>\u8c23\u8a0011</strong>\uff1a\u5a74\u513f\u9664\u4e86\u6bcd\u4e73\u8fd8\u9700\u8981\u8865\u5145\u6c34\u5206</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u679011</strong><br />\n\u6bcd\u4e73\u542b\u6c34\u91cf\u8fbe\u523088%\uff0c\u5b8c\u5168\u6ee1\u8db3\u516d\u4e2a\u6708\u5185\u5b9d\u5b9d\u5bf9\u6c34\u5206\u7684\u9700\u8981\uff0c\u6c34\u53cd\u800c\u5360\u636e\u80c3\u5bb9\u91cf\u3002\u4f46\u516d\u4e2a\u6708\u540e\u968f\u7740\u8f85\u98df\u7684\u6dfb\u52a0\uff0c\u9700\u8981\u9002\u91cf\u559d\u6c34\u3002</p>\n\n<p><strong>\u8c23\u8a0012</strong>\uff1a\u5256\u8179\u4ea7\u4f1a\u4f7f\u6bcd\u4e73\u4e2d\u6709\u5927\u91cf\u9ebb\u9189\u5242\u6b8b\u7559</p>\n\n<p class=\"editors-note\"><strong>\u89e3\u679012</strong><br />\n\u5256\u8179\u4ea7\u540e\uff0c\u6bcd\u4eb2\u7684\u521d\u4e73\u4e2d\u53ef\u80fd\u4f1a\u6b8b\u7559\u5c11\u91cf\u9ebb\u836f\uff0c\u4f46\u542b\u91cf\u4e0d\u8db3\u4ee5\u5371\u5bb3\u5230\u5b9d\u5b9d\u3002\u6709\u4e00\u4e9b\u4ea7\u540e\u8f93\u6db2\u7528\u836f\u6709\u53ef\u80fd\u968f\u8840\u6db2\u5faa\u73af\u8fdb\u5165\u4e73\u6c41\uff0c\u4f46\u5f88\u5feb\u4f1a\u4ee3\u8c22\u51fa\u4f53\u5916\uff0c\u5bf9\u4e73\u6c41\u5206\u6ccc\u548c\u4e73\u6c41\u6210\u5206\u7684\u5f71\u54cd\u5f88\u5c0f\u3002</p>\n\n<p class=\"img-caption\">\u539f\u6587\u94fe\u63a5\uff1ahttp://zone.guokr.com/area/gates_newborn/article/409671/</p>", "date_created": "2019-08-12T16:32:12.277089+08:00", "date_modified": "2019-08-12T16:46:34.636164+08:00", "date_published": "2019-08-12T16:53:32+08:00", "id": 454505, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "health", "replies_count": 0, "small_image": "https://3-im.guokr.com/hjf7UZ_CVpcy7oGz9sG5x0BO0oKOMGPwcCtoTdA2QyMABQAAVgMAAEpQ.jpg", "subject": {"articles_count": 1946, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "medicine", "minisite_key": "health", "name": "\u533b\u5b66", "sort_score": 5, "subject_type": "normal"}, "subject_key": "medicine", "summary": "\u6bcd\u4e73\u5582\u517b\u8c23\u8a00TOP12\uff0c\u4f60\u4e2d\u8fc7\u62db\u5417\uff1f", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u751f\u5b69\u5b50\uff0c\u5582\u5b69\u5b50\uff0c\u4e00\u76f4\u88ab\u89c6\u4e3a\u4e00\u5927\u5bb6\u5b50\u7684\u4eba\u751f\u5934\u7b49\u5927\u4e8b\u3002\u65e2\u662f\u5927\u4e8b\uff0c\u8bb2\u7a76\u5fc5\u591a\u3002\u4e8e\u662f\u5404\u79cd\u201c\u7981\u5fcc\u201d\u3001\u201c\u5999\u62db\u201d\u5c42\u51fa\u4e0d\u7a77\uff0c\u4ec0\u4e48\u8981\u5403\u6728\u74dc\u4e0b\u5976\u554a\uff0c\u8981\u5403\u9e45\u86cb\u89e3\u80ce\u6bd2\u554a\uff0c\u542c\u7740\u90fd\u8ddf\u4fee\u70bc\u5230\u4e86\u8903\u8282\u513f\u4e0a\u4e34\u8981\u5f97\u9053\u5f97\u53bb\u9664\u5996\u6c14\u4f3c\u7684\u3002\u8fd9\u7c7b\u8bf4\u6cd5\u8fd8\u6709\u4e2a\u7279\u70b9\uff0c\u5c31\u662f\u7279\u522b\u80fd\u76f8\u65f6\u800c\u52a8\uff0c\u5404\u79cd\u53d8\u4f53\u6781\u5177\u521b\u65b0\u6027\u2014\u2014\u6bcd\u4e73\u4f1a\u5f15\u8d77\u5a74\u513f\u8fc7\u654f\uff0c\u4ea7\u540e\u6291\u90c1\u4f1a\u5f71\u54cd\u5976\u6c34\u8d28\u91cf\u2014\u2014\u611f\u89c9\u7279\u522b\u4e0e\u65f6\u4ff1\u8fdb\u5427\uff1f\u4f46\u5c31\u5982\u6211\u4eec\u90fd\u77e5\u9053\u7684\uff0c\u8fd9\u4e9b\u8bf4\u6cd5\u597d\u591a\u90fd\u4e0d\u9760\u8c31\u3002\u6211\u4eec\u68b3\u7406\u4e8612\u6761\u6709\u5173\u6bcd\u4e73\u5582\u517b\u6700\u5e7f\u4e3a\u6d41\u4f20\u7684\u8c23\u8a00\u3002\u867d\u8bf4\u8c23\u8a00\u5e74\u5e74\u6709\uff0c\u5927\u5bb6\u65e9\u5c31\u505a\u597d\u4e86\u6301\u4e45\u6218\u7684\u51c6\u5907\uff0c\u4f46\u8fd8\u662f\u5e0c\u671b\u8fd912\u6761\u8c23\u8a00\u53ef\u4ee5\u7559\u5728\u4eca\u5e74\uff0c\u80fd\u8ba9\u5927\u5bb6\u5c24\u5176\u662f\u5988\u5988\u4eec\u6536\u83b7\u6b63\u786e\u7684\u77e5\u8bc6\uff0c\u4e0d\u8981\u5728\u6765\u5e74\u7ee7\u7eed\u8ba9\u8fd9\u4e9b\u4e0d\u9760\u8c31\u7684\u8bf4\u6cd5\u56f0\u6270\u4e86\u3002\u8c23\u8a001: \u521d\u4e73\u91cc\u9762\u6709\u80ce\u6bd2\u8981\u5148\u5403\u9e45\u86cb\u5316\u89e3\u540e\u624d\u80fd\u5582\u5976\u89e3\u67901\u201c\u80ce\u6bd2\u201d\u5e76\u4e0d\u5b58\u5728\uff0c\u6bcd\u4e73\u91cc\u66f4\u4e0d\u53ef\u80fd\u542b\u6709\u6709\u6bd2\u7269\u8d28\u3002\u5a74\u513f\u5e38\u89c1\u7684\u8fc7\u654f\u3001\u6e7f\u75b9\u3001\u76ae\u708e\u7b49\u90fd\u6709\u5404\u81ea\u7684\u53d1\u75c5\u539f\u56e0\uff0c\u53ea\u662f\u53e4\u4eba\u672a\u80fd\u6709\u8db3\u591f\u7684\u533b\u5b66\u77e5\u8bc6\uff0c\u53ea\u80fd\u6982\u5f52\u5165\u201c\u80ce\u6bd2\u201d\u8fd9\u4e00\u6a21\u7cca\u7684\u6982\u5ff5\u3002\u8c23\u8a002\uff1a\u521d\u4e73\u662f\u6700\u6709\u8425\u517b\u7684\uff01\u4e00\u5b9a\u8981\u7ed9\u5b9d\u5b9d\u5403\u521d\u4e73\u89e3\u67902\u4eba\u521d\u4e73\u80af\u5b9a\u662f\u73cd\u8d35\u7684\uff0c\u4f46\u4e5f\u7edd\u975e\u975e\u5403\u4e0d\u53ef\u3002\u66f4\u91cd\u8981\u7684\u610f\u4e49\u5728\u4e8e\u5a74\u513f\u5c3d\u5feb\u542e\u5438\u6bcd\u4eb2\u4e73\u5934\uff0c\u80fd\u8ba9\u6ccc\u4e73\u53cd\u5c04\u5728\u7b2c\u4e00\u65f6\u95ee\u542f\u52a8\uff0c\u4fdd\u8bc1\u540e\u9762\u5a74\u513f\u5bf9\u5976\u91cf\u7684\u9700\u6c42\u3002\u8c23\u8a003\uff1a\u4e73\u4e43\u8840\u6240\u5316\uff0c\u9759\u7f6e\u4e09\u5e74\u53ef\u770b\u5230\u53d8\u56de\u8840\u6db2\u89e3\u67903\u4e73\u817a\u7531\u6c57\u817a\u5206\u5316\u6f14\u53d8\u800c\u6765\uff0c\u8840\u6db2\u4f1a\u4e3a\u4e73\u817a\u5e26\u53bb\u6c27\u6c14\u548c\u8425\u517b\u7269\u8d28\uff0c\u4f46\u6b63\u5e38\u60c5\u51b5\u4e0b\u8840\u7ea2\u7ec6\u80de\u65e0\u6cd5\u901a\u8fc7\u8840\u7ba1\u58c1\uff0c\u6240\u4ee5\u4e73\u6c41\u4e2d\u4e0d\u53ef\u80fd\u6709\u8840\u7ea2\u86cb\u767d\u548c\u6210\u8840\u7ec6\u80de\u3002\u6240\u8c13\u201c\u653e\u4e09\u5e74\u53d8\u8840\u201d\u66f4\u662f\u65e0\u7a3d\u4e4b\u8c08\u3002\u8c23\u8a004\uff1a\u7c73\u9152\u548c\u7a7f\u5c71\u7532\u53ef\u4ee5\u4e0b\u5976\u89e3\u67904\u9152\u7cbe\u4f1a\u901a\u8fc7\u4e73\u6c41\u5206\u6ccc\u88ab\u5a74\u513f\u542e\u5438\u5230\uff0c\u5f71\u54cd\u5176\u8eab\u4f53\u548c\u667a\u529b\u53d1\u80b2\uff1b\u7a7f\u5c71\u7532\u4e5f\u6ca1\u6709\u4efb\u4f55\u7279\u6b8a\u6210\u5206\u53ef\u4ee5\u201c\u4e0b\u5976\u201d\u3002\u7531\u4e8e\u7c73\u9152\u5448\u5976\u767d\u8272\uff0c\u7a7f\u5c71\u7532\u7684\u201c\u7a7f\u201d\u7ed9\u4eba\u901a\u4e73\u7684\u611f\u89c9\uff0c\u6545\u800c\u4ea7\u751f\u8fd9\u79cd\u9644\u4f1a\u7684\u8bf4\u6cd5\u3002\u8c23\u8a005\uff1a\u4ea7\u540e\u6291\u90c1\u4f1a\u5f71\u54cd\u6bcd\u4e73\u8d28\u91cf\u89e3\u67905\u4ea7\u540e\u51fa\u73b0\u6291\u90c1\u60c5\u7eea\u662f\u5e38\u89c1\u7684\uff0c\u75c7\u72b6\u6216\u8f7b\u6216\u91cd\u3002\u4f46\u6ca1\u6709\u4efb\u4f55\u7814\u7a76\u8bc1\u636e\u8868\u660e\uff0c\u6bcd\u4eb2\u4f53\u5185\u4f1a\u56e0\u60c5\u7eea\u6ce2\u52a8\u4ea7\u751f\u6bd2\u7d20\uff0c\u66f4\u4e0d\u4f1a\u56e0\u6bcd\u4e73\u5582\u517b\u6bd2\u6b7b\u5a74\u513f\u3002\u8c23\u8a006\uff1a\u6bcd\u4e73\u5582\u517b\u4f1a\u4f20\u64ad\u4e59\u809d\u89e3\u67906\u5b9d\u5b9d\u4ece\u5988\u5988\u4f20\u67d3\u4e0a\u4e59\u809d\u57fa\u672c\u4e0a\u662f\u5728\u5206\u5a29\u8fc7\u7a0b\u4e2d\uff0c\u6bcd\u4e73\u5582\u517b\u4e0d\u4f1a\u589e\u52a0\u4e59\u809d\u5988\u5988\u6240\u751f\u5a74\u513f\u7684\u4e59\u809d\u611f\u67d3\u98ce\u9669\u3002\u53ea\u6709\u540c\u65f6\u7b26\u5408\u4ee5\u4e0b\u6240\u6709\u6761\u4ef6\u611f\u67d3\u624d\u53ef\u80fd\u53d1\u751f\uff1a- \u5988\u5988\u4e0e\u5b9d\u5b9d\u7684\u4f53\u6db2\u53d1\u751f\u4ea4\u6362\uff08\u5988\u5988\u51fa\u73b0\u4e73\u5934\u7834\u635f\uff0c\u540c\u65f6\u5b9d\u5b9d\u51fa\u73b0\u80a0\u80c3\u4f24\u53e3\uff09\uff1b- \u5988\u5988\u7684\u4e59\u809d\u75c5\u6bd2\u6d53\u5ea6\u5f88\u9ad8\uff1b- \u5c0f\u5b69\u5bf9\u4e59\u809d\u75c5\u6bd2\u6ca1\u6709\u514d\u75ab\u529b\uff08\u5982\u63a5\u79cd\u75ab\u82d7\u540e\u6ca1\u6709\u514d\u75ab\u5e94\u7b54\uff09\u8c23\u8a007\uff1a\u6bcd\u4eb2\u53d1\u70e7\u4e0d\u80fd\u7ed9\u5b69\u5b50\u5582\u5976\u89e3\u67907\u4e0a\u611f\u7c7b\u53d1\u70e7\uff0c\u6b63\u5e38\u5582\u5976\uff0c\u4e73\u6c41\u4e0d\u662f\u75c5\u6bd2\u4f20\u64ad\u7684\u9014\u5f84\uff0c\u53cd\u800c\u80fd\u628a\u6297\u4f53\u7ed9\u5b9d\u5b9d\uff1b\u80c3\u80a0\u9053\u75be\u75c5\u5f15\u8d77\u7684\u53d1\u70e7\uff0c\u6b63\u5e38\u5582\u5976\uff0c\u6ce8\u610f\u6234\u53e3\u7f69\u591a\u6d17\u624b\uff0c\u4e0d\u4eb2\u543b\u5b9d\u5b9d\uff1b\u4e73\u817a\u708e\u5f15\u8d77\u7684\u53d1\u70e7\uff0c\u66f4\u5f97\u7ee7\u7eed\u5582\u5976\uff0c\u5426\u5219\u4f1a\u52a0\u91cd\u5835\u5976\uff1b\u8d8538.5\u2103\u53ef\u5403\u5b9d\u5b9d\u7528\u9000\u70e7\u836f\u5426\u5219\u5976\u592a\u70eb\u5b9d\u5b9d\u53ef\u80fd\u4e0d\u5403\uff0c\u53e6\u5916\u5988\u5988\u4f53\u611f\u975e\u5e38\u4e0d\u9002\u3002\u8c23\u8a008\uff1a\u5b9d\u5b9d\u62c9\u809a\u5b50\u662f\u5bf9\u6bcd\u4e73\u8fc7\u654f\u89e3\u67908\u6bcd\u4e73\u8fc7\u654f\u53d1\u751f\u7387\u6781\u4f4e\u3002\u9996\u5148\u5e94\u8be5\u6000\u7591\u7ec6\u83cc\u611f\u67d3\uff0c\u5176\u6b21\u6000\u7591\u8f85\u98df\u5bfc\u81f4\u8179\u6cfb\uff0c\u8fd8\u6709\u5b9d\u5b9d\u53e3\u670d\u836f\u7269\u5f15\u8d77\u8fc7\u654f\uff1b\u6b64\u5916\u6000\u7591\u914d\u65b9\u5976\u7c89\u4e2d\u7684\u725b\u5976\u86cb\u767d\u8fc7\u654f\uff08\u5373\u4fbf\u6539\u4e3a\u5168\u6bcd\u4e73\u5582\u517b\uff0c\u4e4b\u524d\u7684\u8fc7\u654f\u75c7\u72b6\u4ecd\u53ef\u80fd\u5b58\u5728\uff09\u3002\u8c23\u8a009\uff1a\u5b9d\u5b9d\u603b\u662f\u54ed\u5c31\u8bf4\u660e\u5976\u4e0d\u591f\u89e3\u67909\u5982\u997f\u3001\u80a0\u7ede\u75db\u3001\u6ca1\u7761\u591f\u3001\u6ca1\u6709\u5b89\u5168\u611f\u3001\u5bfb\u6c42\u5b89\u629a\u90fd\u4f1a\u5f15\u8d77\u5a74\u513f\u54ed\uff0c\u5982\u679c\u6bcf\u6b21\u90fd\u4ee5\u4e3a\u662f\u997f\u3001\u90fd\u4ee5\u5976\u5934\u4f5c\u4e3a\u5b89\u629a\uff0c\u53cd\u800c\u4e0d\u597d\u3002\u8c23\u8a0010\uff1a\u5b9d\u5b9d\u4e00\u5c81\u540e\u6bcd\u4e73\u5c31\u6ca1\u8425\u517b\u4e86\u89e3\u679010\u6bcd\u4e73\u8425\u517b\u4e0d\u4f1a\u56e0\u4e3a\u5582\u517b\u65f6\u95f4\u957f\u800c\u6539\u4f1a\u56e0\u4e3a\u5582\u517b\u65f6\u95f4\u957f\u800c\u6539\u53d8\uff0c\u53ea\u4f1a\u968f\u7740\u5b69\u5b50\u6dfb\u52a0\u8f85\u98df\uff0c\u5403\u6bcd\u4e73\u6e10\u5c11\u800c\u51cf\u5c11\u5206\u6ccc\u91cf\u3002WHO\u5efa\u8bae\u575a\u6301\u6bcd\u4e73\u5582\u517b\u5230\u4e24\u5c81\u3002\u8c23\u8a0011\uff1a\u5a74\u513f\u9664\u4e86\u6bcd\u4e73\u8fd8\u9700\u8981\u8865\u5145\u6c34\u5206\u89e3\u679011\u6bcd\u4e73\u542b\u6c34\u91cf\u8fbe\u523088%\uff0c\u5b8c\u5168\u6ee1\u8db3\u516d\u4e2a\u6708\u5185\u5b9d\u5b9d\u5bf9\u6c34\u5206\u7684\u9700\u8981\uff0c\u6c34\u53cd\u800c\u5360\u636e\u80c3\u5bb9\u91cf\u3002\u4f46\u516d\u4e2a\u6708\u540e\u968f\u7740\u8f85\u98df\u7684\u6dfb\u52a0\uff0c\u9700\u8981\u9002\u91cf\u559d\u6c34\u3002\u8c23\u8a0012\uff1a\u5256\u8179\u4ea7\u4f1a\u4f7f\u6bcd\u4e73\u4e2d\u6709\u5927\u91cf\u9ebb\u9189\u5242\u6b8b\u7559\u89e3\u679012\u5256\u8179\u4ea7\u540e\uff0c\u6bcd\u4eb2\u7684\u521d\u4e73\u4e2d\u53ef\u80fd\u4f1a\u6b8b\u7559\u5c11\u91cf\u9ebb\u836f\uff0c\u4f46\u542b\u91cf\u4e0d\u8db3\u4ee5\u5371\u5bb3\u5230\u5b9d\u5b9d\u3002\u6709\u4e00\u4e9b\u4ea7\u540e\u8f93\u6db2\u7528\u836f\u6709\u53ef\u80fd\u968f\u8840\u6db2\u5faa\u73af\u8fdb\u5165\u4e73\u6c41\uff0c\u4f46\u5f88\u5feb\u4f1a\u4ee3\u8c22\u51fa\u4f53\u5916\uff0c\u5bf9\u4e73\u6c41\u5206\u6ccc\u548c\u4e73\u6c41\u6210\u5206\u7684\u5f71\u54cd\u5f88\u5c0f\u3002\u539f\u6587\u94fe\u63a5\uff1ahttp://zone.guokr.com/area/gates_newborn/article/409671/", "title": "\u9e45\u86cb\u89e3\u80ce\u6bd2\uff1f\u7a7f\u5c71\u7532\u4e0b\u5976\uff1f......\u8fd9\u662f\u54fa\u4e73\u8fd8\u662f\u4fee\u4ed9\uff1f", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "", "normal": "", "small": ""}, "introduction": "", "nickname": "Rodrigo de Oliveira Andrade", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/Jgf1yhBovQsxK_Ovir-gtiM3ZKkOB2bBIzbxUoZRjBBQAAAAUAAAAFBO.png?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "Rodrigo de Oliveira Andrade", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [], "content": "<p><!--StartFragment--><span style=\"font-size:14px\"><strong><span style=\"color:#696969\">|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</span></strong></span><!--EndFragment--></p>\r\n\r\n<p>2018\u5e746\u6708,\u751f\u6001\u73af\u5883\u90e8\u542f\u52a8\u4e862018-2019\u5e74\u5ea6\u84dd\u5929\u4fdd\u536b\u6218\uff0c\u5373\u5bf9\u91cd\u70b9\u533a\u57df\u5f3a\u5316\u76d1\u6d4b\u7a7a\u6c14\u6c61\u67d3\u72b6\u51b5\uff0c\u5e76\u76d1\u7763\u6cbb\u7406\u30022019\u5e745\u6708\uff0c\u65b0\u4e00\u8f6e&ldquo;\u84dd\u5929\u4fdd\u536b\u6218&rdquo;\u6253\u54cd\uff0c\u5404\u7701\u5e02\u79ef\u6781\u54cd\u5e94\uff0c\u52a0\u5f3a\u6cbb\u7406\u529b\u5ea6\uff1b\u4e0e\u6b64\u540c\u65f6\uff0c\u8054\u5408\u56fd\u73af\u5883\u89c4\u5212\u7f72\u4e3b\u6301\u7684\u5fae\u535a\u8bdd\u9898&ldquo;#\u84dd\u5929\u4fdd\u536b\u6218\u6211\u662f\u884c\u52a8\u8005#&rdquo;\u4e5f\u53d7\u5230\u5173\u6ce8\uff0c\u4e00\u4f17\u660e\u661f\u7eb7\u7eb7\u52a9\u529b&mdash;&mdash;</p>\r\n\r\n<p><img alt=\"\" src=\"http://3-im.guokr.com/lIfHZyAi3Ew3AkOeY5vZZvy8soWynuLEpdpdOSn6aEY4BAAAIAIAAEpQ.jpg\" style=\"height:322px; width:640px\" /></p>\r\n\r\n<p>\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\uff0c\u4e0d\u4ec5\u662f\u4e2d\u56fd\u9762\u4e34\u7684\u95ee\u9898\uff0c\u66f4\u662f\u5168\u7403\u9762\u4e34\u7684\u4e25\u5cfb\u95ee\u9898\u3002\u91cd\u89c6\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\uff0c\u4fdd\u536b\u84dd\u5929\uff0c\u5176\u5b9e\u6b63\u662f\u5bf9\u6211\u4eec\u6bcf\u4e2a\u4eba\u7684\u8eab\u4f53\u5065\u5eb7\u548c\u751f\u547d\u5b89\u5168\u7684\u91cd\u89c6\u548c\u4fdd\u62a4\u3002</p>\r\n\r\n<h2>\u8054\u5408\u58f0\u660e\uff1a\u5e94\u5bf9\u7a7a\u6c14\u6c61\u67d3\uff0c\u9700\u8981\u5168\u7403\u534f\u4f5c</h2>\r\n\r\n<p>\u5168\u7403\u6bcf\u5e74\u56e0\u7a7a\u6c14\u6c61\u67d3\u6b7b\u4ea1\u7684\u4eba\u6570\u9ad8\u8fbe\u6570\u767e\u4e07\uff0c\u4e3a\u6b64\uff0c5\u4e2a\u56fd\u5bb6\u7ea7\u7684\u79d1\u5b66\u9662\u548c\u533b\u5b66\u9662\u8054\u5408\u63d0\u8bae\uff0c\u6566\u4fc3\u5168\u4e16\u754c\u91c7\u53d6\u5177\u4f53\u63aa\u65bd\u5e94\u5bf9\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\u3002</p>\r\n\r\n<p>\u5177\u4f53\u8bf4\u6765\uff0c\u8fd9\u9879\u63d0\u8bae\u7531\u5fb7\u56fd\u3001\u5df4\u897f\u3001\u5357\u975e\u7684\u79d1\u5b66\u9662\u4ee5\u53ca\u7f8e\u56fd\u7684\u79d1\u5b66\u548c\u533b\u5b66\u9662\u63d0\u51fa\u3002\u8be5\u63d0\u8bae\u547c\u5401\u8fbe\u6210\u4e00\u9879\u5168\u7403\u516c\u7ea6\uff1a\u5728\u4e16\u754c\u8303\u56f4\u5185\u666e\u904d\u5b9e\u884c\u4e25\u63a7\u6c61\u67d3\u7684\u653f\u7b56\uff0c\u5e76\u91c7\u7528\u904f\u5236\u6c61\u67d3\u7684\u6280\u672f\u3002</p>\r\n\r\n<p>\u4e3a\u4e86\u8d77\u8349\u8fd9\u4efd\u516c\u7ea6\uff0c\u79d1\u5b66\u9662\u59d4\u4efb\u7684\u4e13\u5bb6\u4eec\u5206\u6790\u4e86\u8fd1\u51e0\u5341\u5e74\u6765\u516c\u5e03\u7684\u6709\u5173\u7a7a\u6c14\u6c61\u67d3\u5bf9\u4eba\u7c7b\u5065\u5eb7\u548c\u73af\u5883\u5f71\u54cd\u7684\u6570\u636e\uff0c\u786e\u4fdd\u58f0\u660e\u4e2d\u7684\u63d0\u8bae\u57fa\u4e8e\u79d1\u5b66\u5b9e\u8bc1\u3002</p>\r\n\r\n<p>\u8be5\u516c\u7ea6\u5c06\u4e8e9\u6708\u4efd\u5728\u7ebd\u7ea6\u4e3e\u884c\u7684\u8054\u5408\u56fd\u6c14\u5019\u5cf0\u4f1a\u4e0a\u63d0\u4ea4\uff0c\u5021\u8bae\u516c\u7ea6\u7684\u79d1\u5b66\u9662\u6566\u4fc3\u5168\u7403\u6240\u6709\u56fd\u5bb6\u90fd\u5bf9\u5de5\u4e1a\u6392\u653e\u8fdb\u884c\u63a7\u5236\uff0c\u5e76\u5efa\u7acb\u7a7a\u6c14\u76d1\u6d4b\u7cfb\u7edf\u3002\u4ed6\u4eec\u8fd8\u63d0\u8bae\uff0c\u6210\u529f\u63a7\u5236\u5de5\u4e1a\u6c61\u67d3\u7684\u57ce\u5e02\u548c\u56fd\u5bb6\u5e94\u5f53\u5c3d\u53ef\u80fd\u5730\u5206\u4eab\u7ecf\u9a8c\uff0c\u4ee5\u5e2e\u52a9\u90a3\u4e9b\u4ecd\u7136\u6323\u624e\u5728\u63d0\u5347\u7a7a\u6c14\u8d28\u91cf\u56f0\u5883\u4e2d\u7684\u5730\u533a\u3002</p>\r\n\r\n<p>\u5df4\u897f\u79d1\u5b66\u9662\u9662\u957f\u8def\u6613\u65af&middot;\u8fbe\u7ef4\u591a\u7ef4\u5947\uff08Luiz Davidovich\uff09\u5728\u63a5\u53d7\u56fd\u9645\u79d1\u5b66\u4e0e\u53d1\u5c55\u7f51\uff08SciDev.Net\uff09\u91c7\u8bbf\u65f6\u8bf4\uff1a&ldquo;\u8be5\u516c\u7ea6\u8981\u6c42\u63d0\u9ad8\u5bf9\u6d01\u51c0\u7a7a\u6c14\u7684\u91cd\u89c6\uff0c\u5e76\u671f\u5f85\u9488\u5bf9\u63a7\u5236\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\u5236\u5b9a\u66f4\u4e3a\u6709\u6548\u6e05\u6670\u7684\u53d1\u5c55\u7b56\u7565\uff0c\u4ee5\u6b64\u523a\u6fc0\u76f8\u5173\u4ea7\u4e1a\u5347\u7ea7\u7684\u6295\u8d44\uff0c\u4ece\u800c\u4fc3\u8fdb\u73af\u5883\u53cb\u597d\u578b\u7684\u53ef\u6301\u7eed\u53d1\u5c55\u653f\u7b56\u3002&rdquo;</p>\r\n\r\n<p>\u5723\u4fdd\u7f57\u5927\u5b66\u533b\u5b66\u9662\u7814\u7a76\u5458\u4fdd\u7f57&middot;\u8428\u5c14\u8fea\u74e6\uff08Paulo Saldiva\uff09\u53c2\u4e0e\u8d77\u8349\u4e86\u8fd9\u4efd\u62a5\u544a\uff0c\u4ed6\u8868\u793a\uff0c\u8fd9\u662f\u4e0d\u540c\u56fd\u5bb6\u7684\u51e0\u4e2a\u79d1\u5b66\u9662\u9996\u6b21\u8054\u5408\u8d77\u6765\uff0c\u5728\u540c\u4e00\u4e2a\u9879\u76ee\u4e2d\u5171\u540c\u53d1\u58f0\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://3-im.guokr.com/svBgRQyEw8jrYxIRHwxBAy9-CyA7Wd-W9n3YySrelnM4BAAAKgMAAEpQ.jpg\" style=\"height:480px; width:640px\" />5\u4e2a\u56fd\u5bb6\u7ea7\u7684\u79d1\u5b66\u9662\u548c\u533b\u5b66\u9662\u8054\u5408\u63d0\u8bae\uff0c\u6566\u4fc3\u5168\u4e16\u754c\u91c7\u53d6\u5177\u4f53\u63aa\u65bd\u5e94\u5bf9\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898 | pixabay</p>\r\n\r\n<h2>\u5927\u6570\u636e\u5b9e\u8bc1\uff1a\u7a7a\u6c14\u6c61\u67d3\u5371\u5bb3\u8d85\u4e4e\u4f60\u60f3\u8c61</h2>\r\n\r\n<p>\u4eba\u7c7b\u7684\u65e5\u5e38\u751f\u6d3b\u9700\u8981\u80fd\u6e90\u652f\u6301\uff1a\u53d1\u7535\uff0c\u53d1\u70ed\uff0c\u8fd0\u8f93\u4ee5\u53ca\u519c\u4e1a\u7b49\u90fd\u5728\u5229\u7528\u5316\u77f3\u71c3\u6599\u548c\u751f\u7269\u8d28\u71c3\u70e7\u83b7\u53d6\u80fd\u6e90\u3002\u6839\u636e\u8054\u5408\u58f0\u660e\u7684\u8c03\u67e5\u7ed3\u679c\uff0c\u8fd9\u4e9b\u71c3\u6599\u7684\u5e7f\u6cdb\u4f7f\u7528\uff0c\u6b63\u662f\u9020\u6210\u7a7a\u6c14\u6c61\u67d3\u7684\u7f6a\u9b41\u7978\u9996\u3002</p>\r\n\r\n<p>\u5316\u77f3\u71c3\u6599\u9020\u6210\u7684\u6c61\u67d3\u5bf9\u4eba\u7c7b\u7684\u5371\u5bb3\u5c24\u5176\u4e25\u91cd\uff0c\u56e0\u4e3a\u5b83\u542b\u6709\u5927\u91cf\u9897\u7c92\u7269\uff0c\u5176\u4e2d\u76f4\u5f84\u5c0f\u4e8e2.5\u5fae\u7c73\uff08PM2.5\uff09\u7684\u9897\u7c92\u7269\u5728\u7a7a\u6c14\u4e2d\u505c\u7559\u7684\u65f6\u95f4\u5f80\u5f80\u6bd4\u8f83\u91cd\u7684\u9897\u7c92\u7269\u957f\u3002\u5f53\u8fd9\u4e9b\u9897\u7c92\u7269\u8fdb\u5165\u4eba\u4f53\u540e\uff0c\u5b83\u4eec\u53ef\u4ee5\u7a7f\u900f\u80ba\u90e8\uff0c\u751a\u81f3\u9010\u6e10\u6e17\u900f\u8fdb\u8840\u6db2\u4e2d\uff0c\u4ece\u800c\u589e\u52a0\u60a3\u75c5\u98ce\u9669\u3002</p>\r\n\r\n<p>\u5168\u7403\u73af\u5883\u6743\u5a01\u673a\u6784&mdash;&mdash;\u8054\u5408\u56fd\u73af\u5883\u7f72\uff08UN Environment\uff09\u7684\u8c03\u67e5\u6570\u636e\u663e\u793a\uff0c\u6bcf\u5e74\u5168\u7403\u4f1a\u6709\u5927\u7ea6700\u4e07\u4eba\u53e3\u6b7b\u4e8e\u7a7a\u6c14\u6c61\u67d3\uff0c\u4e3b\u8981\u53d7\u5bb3\u7fa4\u4f53\u5305\u62ec\u5987\u5973\u3001\u8001\u4eba\u3001\u513f\u7ae5\u4ee5\u53ca\u8d2b\u56f0\u4eba\u53e3\u3002\u7a7a\u6c14\u6c61\u67d3\u4f1a\u5f71\u54cd\u513f\u7ae5\u5927\u8111\u53d1\u80b2\uff0c\u8fd8\u4f1a\u5f15\u53d1\u4e25\u91cd\u7684\u5065\u5eb7\u95ee\u9898&mdash;&mdash;\u5305\u62ec\u5fc3\u810f\u75c5\u3001\u54ee\u5598\u548c\u7cd6\u5c3f\u75c5\uff0c\u751a\u81f3\u764c\u75c7\u3002\u800c\u8fd9\u4e9b\u75c5\u75c7\u672c\u6765\u53ef\u4ee5\u4e0d\u53d1\u751f\uff0c\u8fd9\u4e9b\u4eba\u672c\u6765\u53ef\u4ee5\u4e0d\u5fc5\u6b7b\u3002</p>\r\n\r\n<p>\u6b64\u5916\uff0c\u6587\u4ef6\u4e2d\u8fd8\u5f3a\u8c03\uff0c\u5316\u77f3\u71c3\u6599\u7684\u71c3\u70e7\u662f\u4e24\u79cd\u4e3b\u8981\u6e29\u5ba4\u6c14\u4f53&mdash;&mdash;\u7532\u70f7(CH4)\u548c\u4e8c\u6c27\u5316\u78b3(CO2)\u7684\u4e3b\u8981\u6765\u6e90\uff0c\u8fd9\u4e24\u79cd\u6c14\u4f53\u5728\u5927\u6c14\u4e2d\u7684\u7d2f\u79ef\u4e0e\u6c14\u5019\u53d8\u5316\u606f\u606f\u76f8\u5173\u3002\u5168\u7403\u53d8\u6696\u5bfc\u81f4\u7684\u6781\u7aef\u6c14\u5019\u548c\u5f02\u5e38\u5929\u6c14\u4e5f\u4f1a\u9020\u6210\u96be\u4ee5\u9884\u6599\u7684\u707e\u96be&mdash;&mdash;</p>\r\n\r\n<h2>\u5168\u7403\u516c\u7ea6\uff1a\u73b0\u72b6\u582a\u5fe7\uff0c\u672a\u6765\u53ef\u671f</h2>\r\n\r\n<p>\u5c3d\u7ba1\u6ca1\u6709\u53c2\u4e0e\u8fd9\u4efd\u62a5\u544a\u8c03\u67e5\uff0c\u4e5f\u575a\u4fe1\u8fd9\u6837\u7684\u5021\u8bae&ldquo;\u5bf9\u4eba\u7c7b\u798f\u7949\u662f\u5207\u5408\u65f6\u5b9c\uff0c\u751a\u81f3\u662f\u751f\u6b7b\u6538\u5173\u7684&rdquo;\u3002</p>\r\n\r\n<p>\u52b3\u4f26\u65af\u6307\u660e\u4e00\u70b9\uff0c\u4e00\u822c\u800c\u8a00\uff0c\u53d1\u5c55\u4e2d\u56fd\u5bb6\uff0c\u5c24\u5176\u662f\u4e00\u4e9b\u4e9a\u6d32\u57ce\u5e02\uff0c\u50cf\u662f\u5370\u5ea6\uff0c\u5df4\u57fa\u65af\u5766\u548c\u4e2d\u56fd\u7684\u4e00\u4e9b\u57ce\u5e02\uff0c\u7a7a\u6c14\u8d28\u91cf\u53ef\u4ee5\u7b97\u5f97\u4e0a\u5168\u7403\u6700\u5dee\u3002\u4ed6\u53d7\u8bbf\u65f6\u8bf4\uff0c&ldquo;\u751f\u6d3b\u5728\u8fd9\u4e9b\u5730\u65b9\uff0c\u548c\u6bcf\u5929\u62bd\u4e00\u5305\u70df\u4e00\u6837\u3002&rdquo;</p>\r\n\r\n<p>&ldquo;\u770b\u5230\u5df4\u897f\uff0c\u5fb7\u56fd\u548c\u7f8e\u56fd\u63a8\u52a8\u8fd9\u4e00\u5021\u8bae\uff0c\u975e\u5e38\u4ee4\u4eba\u9f13\u821e\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u4e5f\u5e0c\u671b\u66f4\u591a\u5f3a\u5927\u7684\u4e9a\u6d32\u540c\u76df\u56fd\u52a0\u5165\u3002&rdquo;\u4ed6\u6700\u540e\u8865\u5145\u9053\u3002</p>\r\n\r\n<p>&ldquo;\u5047\u5982\u6211\u4eec\u80fd\u591f\u63a7\u5236\u5316\u77f3\u71c3\u6599\u7684\u71c3\u70e7\uff0c\u6c14\u5019\u53d8\u5316\u4e5f\u4f1a\u968f\u4e4b\u7f13\u548c\u3002\u5c06\u5168\u7403\u53d8\u6696\u7684\u5e73\u5747\u6e29\u5ea6\u8303\u56f4\u63a7\u5236\u57281.5&deg;C\u4e4b\u5185\u7684\u76ee\u6807\u4e5f\u5c31\u6709\u671b\u8fbe\u6210\u3002&rdquo;\u53c2\u80032015\u5e74\u300a\u5df4\u9ece\u534f\u5b9a\u300b\u4e2d\u5bf9\u5168\u7403\u6c14\u5019\u8bbe\u5b9a\u7684\u76ee\u6807\uff0c\u5723\u4fdd\u7f57\u5927\u5b66\u7269\u7406\u7814\u7a76\u6240\u7684Paulo Artaxo\u8fd9\u6837\u8865\u5145\u9053\u3002\u62a5\u544a\u8fd8\u9884\u6d4b\uff0c\u8fd9\u4efd\u516c\u7ea6\u4ed8\u8bf8\u884c\u52a8\u4e4b\u540e\u5c06\u4f7f240\u4e07\u4eba\u514d\u4e8e\u6b7b\u4ea1\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://3-im.guokr.com/dq2oeIx7C-SinniBulqlo-hXpjCMaresf0fh2PgilZI4BAAA8gEAAEpQ.jpg\" style=\"height:295px; width:640px\" />\u5047\u5982\u6211\u4eec\u80fd\u591f\u63a7\u5236\u5316\u77f3\u71c3\u6599\u7684\u71c3\u70e7\uff0c\u6c14\u5019\u53d8\u5316\u4e5f\u4f1a\u968f\u4e4b\u7f13\u548c | pixabay</p>\r\n\r\n<p>\u8fbe\u7ef4\u591a\u7ef4\u5947\u6307\u51fa\uff0c\u6cbb\u7406\u7a7a\u6c14\u6c61\u67d3\u540c\u65f6\u4e5f\u4f1a\u4ea7\u751f\u660e\u663e\u7684\u7ecf\u6d4e\u6548\u76ca\u3002\u5b9e\u9645\u4e0a\uff0c\u636e\u58f0\u660e\u4e2d\u7684\u6570\u636e\u4f30\u8ba1\uff0c\u57282015\u5e74\uff0c\u56e0\u7a7a\u6c14\u6c61\u67d3\u5bfc\u81f4\u7684\u75be\u75c5\u9020\u6210\u4e86176\u4e2a\u56fd\u5bb6\u603b\u51713.8\u4e07\u4ebf\u7f8e\u5143\u7684\u7ecf\u6d4e\u635f\u5931\u3002\u6b64\u5916\uff0c\u8fd9\u79cd\u7ecf\u6d4e\u8d1f\u62c5\u4e5f\u4f1a\u589e\u52a0\u533b\u7597\u6210\u672c\uff0c\u751a\u81f3\u4f1a\u6d88\u8017\u6389\u53d1\u5c55\u4e2d\u56fd\u5bb6\u7684\u5168\u56fd\u536b\u751f\u9884\u7b97\u3002</p>\r\n\r\n<p>\u8fdb\u800c\u8fbe\u7ef4\u591a\u7ef4\u5947\u63d0\u51fa\uff0c\u9488\u5bf9\u51cf\u5c11\u53ef\u71c3\u4ea7\u54c1\u7684\u95ee\u9898\uff0c\u8fd8\u6709\u5176\u4ed6\u653f\u7b56\u548c\u89e3\u51b3\u529e\u6cd5\u53ef\u4f9b\u9009\u62e9\uff0c\u6bd4\u5982\uff1a\u5bf9\u4e8e\u5316\u77f3\u71c3\u6599\u7684\u751f\u4ea7\u548c\u6d88\u8d39\u4e0d\u518d\u8fdb\u884c\u7a0e\u6536\u8865\u8d34\u3002\u6839\u636e\u5df4\u897f\u5229\u4e9a\u7684\u793e\u4f1a\u7ecf\u6d4e\u7814\u7a76\u673a\u6784\u7684\u6570\u636e\uff0c\u4ec5\u5728\u5df4\u897f\uff0c2018\u5e74\u4e00\u5e74\uff0c\u8fd9\u4e9b\u7a0e\u6536\u8865\u8d34\u5c31\u8fbe\u5230\u4e86212\u4ebf\u7f8e\u5143\u5de6\u53f3\u3002</p>\r\n\r\n<p>\u8428\u5c14\u8fea\u74e6\uff08Saldiva\uff09\u5219\u7ed9\u51fa\u4e86\u66f4\u4e3a\u5177\u4f53\u7684\u63aa\u65bd&mdash;&mdash;\u53d6\u6d88\u5316\u77f3\u71c3\u6599\u8865\u8d34\u6240\u8282\u7701\u7684\u8d44\u91d1\u53ef\u4ee5\u88ab\u7528\u4e8e\u9f13\u52b1\u6027\u653f\u7b56\uff0c\u53ef\u4ee5\u4fc3\u8fdb\u6e05\u6d01\u80fd\u6e90\u751f\u4ea7\uff0c\u63a7\u5236\u6c61\u67d3\u6280\u672f\u5f00\u53d1\uff0c\u4ee5\u53ca\u76f8\u5173\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\u3002</p>\r\n\r\n<p>\u63a7\u5236\u7a7a\u6c14\u6c61\u67d3\uff0c\u4efb\u52a1\u8270\u5de8\uff0c\u8feb\u5728\u7709\u776b\uff0c\u9700\u8981\u5168\u7403\u901a\u529b\u5408\u4f5c\u3002\u5bf9\u4e8e\u6211\u4eec\u6765\u8bf4\uff0c\u8282\u80fd\u51cf\u6392\uff0c\u4f4e\u78b3\u751f\u6d3b\uff0c\u4ece\u5b66\u4f1a\u5783\u573e\u5206\u7c7b\u5f00\u59cb\u5427\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://2-im.guokr.com/tlcnEswtnZKVsAIsOi6r0QiGaPYjX60wYYWCEd0VSvc4BAAA0AIAAEpQ.jpg\" style=\"height:427px; width:640px\" />\u8282\u80fd\u51cf\u6392\uff0c\u63a7\u5236\u7a7a\u6c14\u6c61\u67d3 | pexels</p>\r\n\r\n<p>(\u8bd1\u8005\uff1a\u674e\u73e9\uff1b\u7f16\u8f91\uff1a\u60b2\u50ac\u7684\u94ca\u5b9d\u5b9d\uff1b\u5ba1\u6821\uff1aYuki)</p>\r\n\r\n<h2 class=\"references\"><span style=\"font-size:16px\"><span style=\"color:#696969\">\u6587\u7ae0\u6765\u6e90\uff1a</span></span></h2>\r\n\r\n<ol class=\"reference-item\">\r\n\t<li><span style=\"color:#696969\"><span style=\"font-size:14px\">https://www.scidev.net/global/pollution/news/global-pact-to-stop-air-pollution.html\uff08\u672c\u6587\u7531storythings\u4f9b\u7a3f\uff09</span></span></li>\r\n</ol>", "date_created": "2019-08-08T11:24:01.091194+08:00", "date_modified": "2019-08-08T11:29:09.109282+08:00", "date_published": "2019-08-08T11:29:19+08:00", "id": 454485, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "natural", "replies_count": 0, "small_image": "https://3-im.guokr.com/OFdL4yA_w2cTDtNjMl1Rw1tBy4grsgC-X-uDyEFMkt44BAAA0AIAAEpQ.jpg", "subject": {"articles_count": 417, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "environment", "minisite_key": "natural", "name": "\u73af\u5883", "sort_score": 3, "subject_type": "normal"}, "subject_key": "environment", "summary": "\u73b0\u72b6\u582a\u5fe7\uff0c\u672a\u6765\u53ef\u671f", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|2018\u5e746\u6708,\u751f\u6001\u73af\u5883\u90e8\u542f\u52a8\u4e862018-2019\u5e74\u5ea6\u84dd\u5929\u4fdd\u536b\u6218\uff0c\u5373\u5bf9\u91cd\u70b9\u533a\u57df\u5f3a\u5316\u76d1\u6d4b\u7a7a\u6c14\u6c61\u67d3\u72b6\u51b5\uff0c\u5e76\u76d1\u7763\u6cbb\u7406\u30022019\u5e745\u6708\uff0c\u65b0\u4e00\u8f6e\u201c\u84dd\u5929\u4fdd\u536b\u6218\u201d\u6253\u54cd\uff0c\u5404\u7701\u5e02\u79ef\u6781\u54cd\u5e94\uff0c\u52a0\u5f3a\u6cbb\u7406\u529b\u5ea6\uff1b\u4e0e\u6b64\u540c\u65f6\uff0c\u8054\u5408\u56fd\u73af\u5883\u89c4\u5212\u7f72\u4e3b\u6301\u7684\u5fae\u535a\u8bdd\u9898\u201c#\u84dd\u5929\u4fdd\u536b\u6218\u6211\u662f\u884c\u52a8\u8005#\u201d\u4e5f\u53d7\u5230\u5173\u6ce8\uff0c\u4e00\u4f17\u660e\u661f\u7eb7\u7eb7\u52a9\u529b\u2014\u2014\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\uff0c\u4e0d\u4ec5\u662f\u4e2d\u56fd\u9762\u4e34\u7684\u95ee\u9898\uff0c\u66f4\u662f\u5168\u7403\u9762\u4e34\u7684\u4e25\u5cfb\u95ee\u9898\u3002\u91cd\u89c6\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\uff0c\u4fdd\u536b\u84dd\u5929\uff0c\u5176\u5b9e\u6b63\u662f\u5bf9\u6211\u4eec\u6bcf\u4e2a\u4eba\u7684\u8eab\u4f53\u5065\u5eb7\u548c\u751f\u547d\u5b89\u5168\u7684\u91cd\u89c6\u548c\u4fdd\u62a4\u3002\u8054\u5408\u58f0\u660e\uff1a\u5e94\u5bf9\u7a7a\u6c14\u6c61\u67d3\uff0c\u9700\u8981\u5168\u7403\u534f\u4f5c\u5168\u7403\u6bcf\u5e74\u56e0\u7a7a\u6c14\u6c61\u67d3\u6b7b\u4ea1\u7684\u4eba\u6570\u9ad8\u8fbe\u6570\u767e\u4e07\uff0c\u4e3a\u6b64\uff0c5\u4e2a\u56fd\u5bb6\u7ea7\u7684\u79d1\u5b66\u9662\u548c\u533b\u5b66\u9662\u8054\u5408\u63d0\u8bae\uff0c\u6566\u4fc3\u5168\u4e16\u754c\u91c7\u53d6\u5177\u4f53\u63aa\u65bd\u5e94\u5bf9\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\u3002\u5177\u4f53\u8bf4\u6765\uff0c\u8fd9\u9879\u63d0\u8bae\u7531\u5fb7\u56fd\u3001\u5df4\u897f\u3001\u5357\u975e\u7684\u79d1\u5b66\u9662\u4ee5\u53ca\u7f8e\u56fd\u7684\u79d1\u5b66\u548c\u533b\u5b66\u9662\u63d0\u51fa\u3002\u8be5\u63d0\u8bae\u547c\u5401\u8fbe\u6210\u4e00\u9879\u5168\u7403\u516c\u7ea6\uff1a\u5728\u4e16\u754c\u8303\u56f4\u5185\u666e\u904d\u5b9e\u884c\u4e25\u63a7\u6c61\u67d3\u7684\u653f\u7b56\uff0c\u5e76\u91c7\u7528\u904f\u5236\u6c61\u67d3\u7684\u6280\u672f\u3002\u4e3a\u4e86\u8d77\u8349\u8fd9\u4efd\u516c\u7ea6\uff0c\u79d1\u5b66\u9662\u59d4\u4efb\u7684\u4e13\u5bb6\u4eec\u5206\u6790\u4e86\u8fd1\u51e0\u5341\u5e74\u6765\u516c\u5e03\u7684\u6709\u5173\u7a7a\u6c14\u6c61\u67d3\u5bf9\u4eba\u7c7b\u5065\u5eb7\u548c\u73af\u5883\u5f71\u54cd\u7684\u6570\u636e\uff0c\u786e\u4fdd\u58f0\u660e\u4e2d\u7684\u63d0\u8bae\u57fa\u4e8e\u79d1\u5b66\u5b9e\u8bc1\u3002\u8be5\u516c\u7ea6\u5c06\u4e8e9\u6708\u4efd\u5728\u7ebd\u7ea6\u4e3e\u884c\u7684\u8054\u5408\u56fd\u6c14\u5019\u5cf0\u4f1a\u4e0a\u63d0\u4ea4\uff0c\u5021\u8bae\u516c\u7ea6\u7684\u79d1\u5b66\u9662\u6566\u4fc3\u5168\u7403\u6240\u6709\u56fd\u5bb6\u90fd\u5bf9\u5de5\u4e1a\u6392\u653e\u8fdb\u884c\u63a7\u5236\uff0c\u5e76\u5efa\u7acb\u7a7a\u6c14\u76d1\u6d4b\u7cfb\u7edf\u3002\u4ed6\u4eec\u8fd8\u63d0\u8bae\uff0c\u6210\u529f\u63a7\u5236\u5de5\u4e1a\u6c61\u67d3\u7684\u57ce\u5e02\u548c\u56fd\u5bb6\u5e94\u5f53\u5c3d\u53ef\u80fd\u5730\u5206\u4eab\u7ecf\u9a8c\uff0c\u4ee5\u5e2e\u52a9\u90a3\u4e9b\u4ecd\u7136\u6323\u624e\u5728\u63d0\u5347\u7a7a\u6c14\u8d28\u91cf\u56f0\u5883\u4e2d\u7684\u5730\u533a\u3002\u5df4\u897f\u79d1\u5b66\u9662\u9662\u957f\u8def\u6613\u65af\u00b7\u8fbe\u7ef4\u591a\u7ef4\u5947\uff08Luiz Davidovich\uff09\u5728\u63a5\u53d7\u56fd\u9645\u79d1\u5b66\u4e0e\u53d1\u5c55\u7f51\uff08SciDev.Net\uff09\u91c7\u8bbf\u65f6\u8bf4\uff1a\u201c\u8be5\u516c\u7ea6\u8981\u6c42\u63d0\u9ad8\u5bf9\u6d01\u51c0\u7a7a\u6c14\u7684\u91cd\u89c6\uff0c\u5e76\u671f\u5f85\u9488\u5bf9\u63a7\u5236\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898\u5236\u5b9a\u66f4\u4e3a\u6709\u6548\u6e05\u6670\u7684\u53d1\u5c55\u7b56\u7565\uff0c\u4ee5\u6b64\u523a\u6fc0\u76f8\u5173\u4ea7\u4e1a\u5347\u7ea7\u7684\u6295\u8d44\uff0c\u4ece\u800c\u4fc3\u8fdb\u73af\u5883\u53cb\u597d\u578b\u7684\u53ef\u6301\u7eed\u53d1\u5c55\u653f\u7b56\u3002\u201d\u5723\u4fdd\u7f57\u5927\u5b66\u533b\u5b66\u9662\u7814\u7a76\u5458\u4fdd\u7f57\u00b7\u8428\u5c14\u8fea\u74e6\uff08Paulo Saldiva\uff09\u53c2\u4e0e\u8d77\u8349\u4e86\u8fd9\u4efd\u62a5\u544a\uff0c\u4ed6\u8868\u793a\uff0c\u8fd9\u662f\u4e0d\u540c\u56fd\u5bb6\u7684\u51e0\u4e2a\u79d1\u5b66\u9662\u9996\u6b21\u8054\u5408\u8d77\u6765\uff0c\u5728\u540c\u4e00\u4e2a\u9879\u76ee\u4e2d\u5171\u540c\u53d1\u58f0\u30025\u4e2a\u56fd\u5bb6\u7ea7\u7684\u79d1\u5b66\u9662\u548c\u533b\u5b66\u9662\u8054\u5408\u63d0\u8bae\uff0c\u6566\u4fc3\u5168\u4e16\u754c\u91c7\u53d6\u5177\u4f53\u63aa\u65bd\u5e94\u5bf9\u7a7a\u6c14\u6c61\u67d3\u95ee\u9898 | pixabay\u5927\u6570\u636e\u5b9e\u8bc1\uff1a\u7a7a\u6c14\u6c61\u67d3\u5371\u5bb3\u8d85\u4e4e\u4f60\u60f3\u8c61\u4eba\u7c7b\u7684\u65e5\u5e38\u751f\u6d3b\u9700\u8981\u80fd\u6e90\u652f\u6301\uff1a\u53d1\u7535\uff0c\u53d1\u70ed\uff0c\u8fd0\u8f93\u4ee5\u53ca\u519c\u4e1a\u7b49\u90fd\u5728\u5229\u7528\u5316\u77f3\u71c3\u6599\u548c\u751f\u7269\u8d28\u71c3\u70e7\u83b7\u53d6\u80fd\u6e90\u3002\u6839\u636e\u8054\u5408\u58f0\u660e\u7684\u8c03\u67e5\u7ed3\u679c\uff0c\u8fd9\u4e9b\u71c3\u6599\u7684\u5e7f\u6cdb\u4f7f\u7528\uff0c\u6b63\u662f\u9020\u6210\u7a7a\u6c14\u6c61\u67d3\u7684\u7f6a\u9b41\u7978\u9996\u3002\u5316\u77f3\u71c3\u6599\u9020\u6210\u7684\u6c61\u67d3\u5bf9\u4eba\u7c7b\u7684\u5371\u5bb3\u5c24\u5176\u4e25\u91cd\uff0c\u56e0\u4e3a\u5b83\u542b\u6709\u5927\u91cf\u9897\u7c92\u7269\uff0c\u5176\u4e2d\u76f4\u5f84\u5c0f\u4e8e2.5\u5fae\u7c73\uff08PM2.5\uff09\u7684\u9897\u7c92\u7269\u5728\u7a7a\u6c14\u4e2d\u505c\u7559\u7684\u65f6\u95f4\u5f80\u5f80\u6bd4\u8f83\u91cd\u7684\u9897\u7c92\u7269\u957f\u3002\u5f53\u8fd9\u4e9b\u9897\u7c92\u7269\u8fdb\u5165\u4eba\u4f53\u540e\uff0c\u5b83\u4eec\u53ef\u4ee5\u7a7f\u900f\u80ba\u90e8\uff0c\u751a\u81f3\u9010\u6e10\u6e17\u900f\u8fdb\u8840\u6db2\u4e2d\uff0c\u4ece\u800c\u589e\u52a0\u60a3\u75c5\u98ce\u9669\u3002\u5168\u7403\u73af\u5883\u6743\u5a01\u673a\u6784\u2014\u2014\u8054\u5408\u56fd\u73af\u5883\u7f72\uff08UN Environment\uff09\u7684\u8c03\u67e5\u6570\u636e\u663e\u793a\uff0c\u6bcf\u5e74\u5168\u7403\u4f1a\u6709\u5927\u7ea6700\u4e07\u4eba\u53e3\u6b7b\u4e8e\u7a7a\u6c14\u6c61\u67d3\uff0c\u4e3b\u8981\u53d7\u5bb3\u7fa4\u4f53\u5305\u62ec\u5987\u5973\u3001\u8001\u4eba\u3001\u513f\u7ae5\u4ee5\u53ca\u8d2b\u56f0\u4eba\u53e3\u3002\u7a7a\u6c14\u6c61\u67d3\u4f1a\u5f71\u54cd\u513f\u7ae5\u5927\u8111\u53d1\u80b2\uff0c\u8fd8\u4f1a\u5f15\u53d1\u4e25\u91cd\u7684\u5065\u5eb7\u95ee\u9898\u2014\u2014\u5305\u62ec\u5fc3\u810f\u75c5\u3001\u54ee\u5598\u548c\u7cd6\u5c3f\u75c5\uff0c\u751a\u81f3\u764c\u75c7\u3002\u800c\u8fd9\u4e9b\u75c5\u75c7\u672c\u6765\u53ef\u4ee5\u4e0d\u53d1\u751f\uff0c\u8fd9\u4e9b\u4eba\u672c\u6765\u53ef\u4ee5\u4e0d\u5fc5\u6b7b\u3002\u6b64\u5916\uff0c\u6587\u4ef6\u4e2d\u8fd8\u5f3a\u8c03\uff0c\u5316\u77f3\u71c3\u6599\u7684\u71c3\u70e7\u662f\u4e24\u79cd\u4e3b\u8981\u6e29\u5ba4\u6c14\u4f53\u2014\u2014\u7532\u70f7(CH4)\u548c\u4e8c\u6c27\u5316\u78b3(CO2)\u7684\u4e3b\u8981\u6765\u6e90\uff0c\u8fd9\u4e24\u79cd\u6c14\u4f53\u5728\u5927\u6c14\u4e2d\u7684\u7d2f\u79ef\u4e0e\u6c14\u5019\u53d8\u5316\u606f\u606f\u76f8\u5173\u3002\u5168\u7403\u53d8\u6696\u5bfc\u81f4\u7684\u6781\u7aef\u6c14\u5019\u548c\u5f02\u5e38\u5929\u6c14\u4e5f\u4f1a\u9020\u6210\u96be\u4ee5\u9884\u6599\u7684\u707e\u96be\u2014\u2014\u5168\u7403\u516c\u7ea6\uff1a\u73b0\u72b6\u582a\u5fe7\uff0c\u672a\u6765\u53ef\u671f\u5c3d\u7ba1\u6ca1\u6709\u53c2\u4e0e\u8fd9\u4efd\u62a5\u544a\u8c03\u67e5\uff0c\u4e5f\u575a\u4fe1\u8fd9\u6837\u7684\u5021\u8bae\u201c\u5bf9\u4eba\u7c7b\u798f\u7949\u662f\u5207\u5408\u65f6\u5b9c\uff0c\u751a\u81f3\u662f\u751f\u6b7b\u6538\u5173\u7684\u201d\u3002\u52b3\u4f26\u65af\u6307\u660e\u4e00\u70b9\uff0c\u4e00\u822c\u800c\u8a00\uff0c\u53d1\u5c55\u4e2d\u56fd\u5bb6\uff0c\u5c24\u5176\u662f\u4e00\u4e9b\u4e9a\u6d32\u57ce\u5e02\uff0c\u50cf\u662f\u5370\u5ea6\uff0c\u5df4\u57fa\u65af\u5766\u548c\u4e2d\u56fd\u7684\u4e00\u4e9b\u57ce\u5e02\uff0c\u7a7a\u6c14\u8d28\u91cf\u53ef\u4ee5\u7b97\u5f97\u4e0a\u5168\u7403\u6700\u5dee\u3002\u4ed6\u53d7\u8bbf\u65f6\u8bf4\uff0c\u201c\u751f\u6d3b\u5728\u8fd9\u4e9b\u5730\u65b9\uff0c\u548c\u6bcf\u5929\u62bd\u4e00\u5305\u70df\u4e00\u6837\u3002\u201d\u201c\u770b\u5230\u5df4\u897f\uff0c\u5fb7\u56fd\u548c\u7f8e\u56fd\u63a8\u52a8\u8fd9\u4e00\u5021\u8bae\uff0c\u975e\u5e38\u4ee4\u4eba\u9f13\u821e\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u4e5f\u5e0c\u671b\u66f4\u591a\u5f3a\u5927\u7684\u4e9a\u6d32\u540c\u76df\u56fd\u52a0\u5165\u3002\u201d\u4ed6\u6700\u540e\u8865\u5145\u9053\u3002\u201c\u5047\u5982\u6211\u4eec\u80fd\u591f\u63a7\u5236\u5316\u77f3\u71c3\u6599\u7684\u71c3\u70e7\uff0c\u6c14\u5019\u53d8\u5316\u4e5f\u4f1a\u968f\u4e4b\u7f13\u548c\u3002\u5c06\u5168\u7403\u53d8\u6696\u7684\u5e73\u5747\u6e29\u5ea6\u8303\u56f4\u63a7\u5236\u57281.5\u00b0C\u4e4b\u5185\u7684\u76ee\u6807\u4e5f\u5c31\u6709\u671b\u8fbe\u6210\u3002\u201d\u53c2\u80032015\u5e74\u300a\u5df4\u9ece\u534f\u5b9a\u300b\u4e2d\u5bf9\u5168\u7403\u6c14\u5019\u8bbe\u5b9a\u7684\u76ee\u6807\uff0c\u5723\u4fdd\u7f57\u5927\u5b66\u7269\u7406\u7814\u7a76\u6240\u7684Paulo Artaxo\u8fd9\u6837\u8865\u5145\u9053\u3002\u62a5\u544a\u8fd8\u9884\u6d4b\uff0c\u8fd9\u4efd\u516c\u7ea6\u4ed8\u8bf8\u884c\u52a8\u4e4b\u540e\u5c06\u4f7f240\u4e07\u4eba\u514d\u4e8e\u6b7b\u4ea1\u3002\u5047\u5982\u6211\u4eec\u80fd\u591f\u63a7\u5236\u5316\u77f3\u71c3\u6599\u7684\u71c3\u70e7\uff0c\u6c14\u5019\u53d8\u5316\u4e5f\u4f1a\u968f\u4e4b\u7f13\u548c | pixabay\u8fbe\u7ef4\u591a\u7ef4\u5947\u6307\u51fa\uff0c\u6cbb\u7406\u7a7a\u6c14\u6c61\u67d3\u540c\u65f6\u4e5f\u4f1a\u4ea7\u751f\u660e\u663e\u7684\u7ecf\u6d4e\u6548\u76ca\u3002\u5b9e\u9645\u4e0a\uff0c\u636e\u58f0\u660e\u4e2d\u7684\u6570\u636e\u4f30\u8ba1\uff0c\u57282015\u5e74\uff0c\u56e0\u7a7a\u6c14\u6c61\u67d3\u5bfc\u81f4\u7684\u75be\u75c5\u9020\u6210\u4e86176\u4e2a\u56fd\u5bb6\u603b\u51713.8\u4e07\u4ebf\u7f8e\u5143\u7684\u7ecf\u6d4e\u635f\u5931\u3002\u6b64\u5916\uff0c\u8fd9\u79cd\u7ecf\u6d4e\u8d1f\u62c5\u4e5f\u4f1a\u589e\u52a0\u533b\u7597\u6210\u672c\uff0c\u751a\u81f3\u4f1a\u6d88\u8017\u6389\u53d1\u5c55\u4e2d\u56fd\u5bb6\u7684\u5168\u56fd\u536b\u751f\u9884\u7b97\u3002\u8fdb\u800c\u8fbe\u7ef4\u591a\u7ef4\u5947\u63d0\u51fa\uff0c\u9488\u5bf9\u51cf\u5c11\u53ef\u71c3\u4ea7\u54c1\u7684\u95ee\u9898\uff0c\u8fd8\u6709\u5176\u4ed6\u653f\u7b56\u548c\u89e3\u51b3\u529e\u6cd5\u53ef\u4f9b\u9009\u62e9\uff0c\u6bd4\u5982\uff1a\u5bf9\u4e8e\u5316\u77f3\u71c3\u6599\u7684\u751f\u4ea7\u548c\u6d88\u8d39\u4e0d\u518d\u8fdb\u884c\u7a0e\u6536\u8865\u8d34\u3002\u6839\u636e\u5df4\u897f\u5229\u4e9a\u7684\u793e\u4f1a\u7ecf\u6d4e\u7814\u7a76\u673a\u6784\u7684\u6570\u636e\uff0c\u4ec5\u5728\u5df4\u897f\uff0c2018\u5e74\u4e00\u5e74\uff0c\u8fd9\u4e9b\u7a0e\u6536\u8865\u8d34\u5c31\u8fbe\u5230\u4e86212\u4ebf\u7f8e\u5143\u5de6\u53f3\u3002\u8428\u5c14\u8fea\u74e6\uff08Saldiva\uff09\u5219\u7ed9\u51fa\u4e86\u66f4\u4e3a\u5177\u4f53\u7684\u63aa\u65bd\u2014\u2014\u53d6\u6d88\u5316\u77f3\u71c3\u6599\u8865\u8d34\u6240\u8282\u7701\u7684\u8d44\u91d1\u53ef\u4ee5\u88ab\u7528\u4e8e\u9f13\u52b1\u6027\u653f\u7b56\uff0c\u53ef\u4ee5\u4fc3\u8fdb\u6e05\u6d01\u80fd\u6e90\u751f\u4ea7\uff0c\u63a7\u5236\u6c61\u67d3\u6280\u672f\u5f00\u53d1\uff0c\u4ee5\u53ca\u76f8\u5173\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\u3002\u63a7\u5236\u7a7a\u6c14\u6c61\u67d3\uff0c\u4efb\u52a1\u8270\u5de8\uff0c\u8feb\u5728\u7709\u776b\uff0c\u9700\u8981\u5168\u7403\u901a\u529b\u5408\u4f5c\u3002\u5bf9\u4e8e\u6211\u4eec\u6765\u8bf4\uff0c\u8282\u80fd\u51cf\u6392\uff0c\u4f4e\u78b3\u751f\u6d3b\uff0c\u4ece\u5b66\u4f1a\u5783\u573e\u5206\u7c7b\u5f00\u59cb\u5427\u3002\u8282\u80fd\u51cf\u6392\uff0c\u63a7\u5236\u7a7a\u6c14\u6c61\u67d3 | pexels(\u8bd1\u8005\uff1a\u674e\u73e9\uff1b\u7f16\u8f91\uff1a\u60b2\u50ac\u7684\u94ca\u5b9d\u5b9d\uff1b\u5ba1\u6821\uff1aYuki)\u6587\u7ae0\u6765\u6e90\uff1ahttps://www.scidev.net/global/pollution/news/global-pact-to-stop-air-pollution.html\uff08\u672c\u6587\u7531storythings\u4f9b\u7a3f\uff09", "title": "\u63a7\u5236\u7a7a\u6c14\u6c61\u67d3\uff0c\u6bd4\u4f60\u60f3\u5f97\u8fd8\u8981\u7d27\u8feb", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/wQNpx9IYFAX8SACJL8u-hhijaR7H0YtyIau4NIbX4Aj9AAAA_gAAAEpQ.jpg", "normal": "https://3-im.guokr.com/wQNpx9IYFAX8SACJL8u-hhijaR7H0YtyIau4NIbX4Aj9AAAA_gAAAEpQ.jpg", "small": "https://3-im.guokr.com/wQNpx9IYFAX8SACJL8u-hhijaR7H0YtyIau4NIbX4Aj9AAAA_gAAAEpQ.jpg"}, "introduction": "\u4e00\u4e2a\u7814\u7a76\u75c5\u539f\u5fae\u751f\u7269\u7684\u8425\u517b\u5b66\u535a\u58eb\uff0c\u4e00\u53ea\u5fd9\u788c\u7684\u79d1\u7814\u72d7\uff0c\u6df1\u591c\u62bd\u7a7a\u5199\u79d1\u666e\u3002", "nickname": "Nekout", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/wQNpx9IYFAX8SACJL8u-hhijaR7H0YtyIau4NIbX4Aj9AAAA_gAAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/wQNpx9IYFAX8SACJL8u-hhijaR7H0YtyIau4NIbX4Aj9AAAA_gAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/wQNpx9IYFAX8SACJL8u-hhijaR7H0YtyIau4NIbX4Aj9AAAA_gAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "introduction": "\u4e00\u4e2a\u7814\u7a76\u75c5\u539f\u5fae\u751f\u7269\u7684\u8425\u517b\u5b66\u535a\u58eb\uff0c\u4e00\u53ea\u5fd9\u788c\u7684\u79d1\u7814\u72d7\uff0c\u6df1\u591c\u62bd\u7a7a\u5199\u79d1\u666e\u3002", "nickname": "Nekout", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [{"articles_count": 4299, "channel_type": "normal", "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "frontier", "name": "\u524d\u6cbf", "sort_score": 1}], "content": "<p><!--StartFragment--><span style=\"font-size:14px\"><strong><span style=\"color:#696969\">|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</span></strong></span><!--EndFragment--></p>\r\n\r\n<p>\u8461\u8404\u5e94\u8be5\u662f\u5927\u5bb6\u6700\u719f\u6089\u7684\u6c34\u679c\u4e86\u3002\u4f60\u6216\u8bb8\u54c1\u5c1d\u7ea2\u8272\u3001\u767d\u8272\u3001\u7d2b\u8272\u3001\u7eff\u8272\u7684\u8461\u8404\uff0c\u6bcf\u4e00\u9897\u90fd\u6676\u83b9\u5254\u900f\u53c8\u9178\u751c\u53ef\u53e3\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://2-im.guokr.com/uzWMiGHoTzq_q28GCNPVVLP6xZfsGl3JJ6nm8vqOiY5UAwAAOQIAAEpQ.jpg\" style=\"height:427px; width:640px\" />\u4e0d\u540c\u989c\u8272\u7684\u8461\u8404\u4e32|solomonitg-com</p>\r\n\r\n<p>\u4e0d\u8fc7\uff0c\u6709\u8fd9\u6837\u4e00\u79cd\u91d1\u707f\u707f\u7684&ldquo;\u8461\u8404\u4e32&rdquo;\u5374\u5e76\u4e0d\u53ef\u53e3\uff0c\u4e0d\u5c0f\u5fc3\u5403\u4e0b\u53bb\u8fd8\u53ef\u80fd\u7ed9\u6211\u4eec\u7684\u5065\u5eb7\u5e26\u6765\u4e0d\u826f\u5f71\u54cd\u3002</p>\r\n\r\n<p>\u5b83\u5c31\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u3002</p>\r\n\r\n<h2>\u81ea\u5e26&ldquo;\u9ec4\u91d1\u7532&rdquo;\u7684&ldquo;\u8461\u8404\u4e32&rdquo;</h2>\r\n\r\n<p>19\u4e16\u7eaa80\u5e74\u4ee3\uff0c\u4e00\u4f4d\u540d\u53eb\u4e9a\u5386\u5c71\u5927&middot;\u5965\u53e4\u65af\u901a\uff08Alexander Ogston\uff09\u7684\u82f1\u56fd\u533b\u751f\uff0c\u4ece\u75c5\u4eba\u7684\u8113\u80bf\u4e2d\u5e38\u5e38\u5206\u79bb\u5f97\u5230\u4e00\u79cd\u5f62\u4f3c\u8461\u8404\u4e32\u7684\u7403\u83cc\uff0c\u8fd9\u79cd\u7ec6\u83cc\u5728\u542b\u6709\u52a8\u7269\u8840\u6db2\u7684\u743c\u8102\u5e73\u677f\u4e0a\u80fd\u5f62\u6210\u91d1\u9ec4\u8272\u7684\u83cc\u843d\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://3-im.guokr.com/ZvaS7NLWCcmGC8ZIw1_jwl7O6SrfNJUKHeZDfaZxxLOkAQAATQEAAEpQ.jpg\" style=\"height:333px; width:420px\" />\u8840\u5e73\u677f\u4e0a\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc | Wikimedia Commons</p>\r\n\r\n<p>\u4e4b\u540e\uff0c\u5fb7\u56fd\u5fae\u751f\u7269\u5b66\u5bb6\u5f17\u91cc\u5fb7\u91cc\u5e0c&middot;\u6731\u5229\u65af&middot;\u7f57\u68ee\u5df4\u8d6b\uff08Friedrich Julius Rosenbach\uff09\u5c06\u5176\u547d\u540d\u4e3a<em>Staphylococcus aureus</em>\uff08\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\uff09\uff0c\u5176\u4e2dStaphylococcus\u6765\u6e90\u4e8e\u5e0c\u814a\u6587\uff0c\u610f\u4e3a&ldquo;\u8461\u8404\u4e32&rdquo;\uff0caureus\u5219\u6765\u81ea\u4e8e\u62c9\u4e01\u6587\uff0c\u610f\u4e3a&ldquo;\u91d1\u9ec4\u8272\u7684&rdquo;\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://1-im.guokr.com/6Ta6dJE_bu2nEnBN0JpKfb6ALpZ0HaJrARTfksjG7YfAAwAAYwIAAEpQ.jpg\" style=\"height:407px; width:640px\" />\u7535\u955c\u7167\u4e0b\u7684&ldquo;\u91d1\u8461\u8404&rdquo; | CDC</p>\r\n\r\n<p>\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4e4b\u6240\u4ee5\u5448\u73b0\u51fa\u9c9c\u4eae\u7684\u91d1\u9ec4\u8272\uff0c\u662f\u56e0\u4e3a\u5b83\u80fd\u5408\u6210\u4e00\u79cd\u540d\u4e3a&ldquo;\u8461\u8404\u7403\u83cc\u9ec4\u7d20&rdquo;\uff08<em>Staphyloxanthin</em>\uff09\u7684\u7c7b\u80e1\u841d\u535c\u7d20\u8272\u7d20\uff0c\u5b83\u5927\u6982\u957f\u4e0b\u9762\u8fd9\u4e2a\u6837\u5b50\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://1-im.guokr.com/PHwMl6yCLNY1SdEcYY06eay2s7-URuNEbK2mn4Ik4-MABAAA5wAAAFBO.png\" style=\"height:144px; width:640px\" />\u8461\u8404\u7403\u83cc\u9ec4\u7d20\uff08<em>Staphyloxanthin</em>\uff09| Wikimedia Commons</p>\r\n\r\n<p>\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u957f\u957f\u7684\u758f\u6c34\u6027\u78b3\u94fe\uff0c\u4f7f\u5176\u53ef\u4ee5\u5d4c\u5165\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7684\u7ec6\u80de\u819c\u4e0a\u3002\u800c\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u4e0e\u5176\u4ed6\u7c7b\u80e1\u841d\u535c\u7d20\u4e00\u6837\uff0c\u90fd\u5177\u6709\u6297\u6c27\u5316\u7684\u4f5c\u7528\uff0c\u80fd\u62b5\u6297\u5bbf\u4e3b\u514d\u75ab\u7ec6\u80de\u5206\u6ccc\u7684\u5177\u6709\u6740\u83cc\u80fd\u529b\u7684\u5404\u79cd\u7c7b\u578b\u6d3b\u6027\u6c27\u3002\u56e0\u6b64\uff0c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u53ef\u8c13\u81ea\u5e26&ldquo;\u9ec4\u91d1\u7532&rdquo;\u795e\u88c5\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://2-im.guokr.com/faZ-87V6S7EXKMhg0FNlH46LxpZHynkB0CDOOQF3lgocAgAAjAAAAEpQ.jpg\" style=\"height:140px; width:540px\" />\u5404\u79cd\u7c7b\u578b\u7684\u6d3b\u6027\u6c27 | vivo.colostate.edu</p>\r\n\r\n<h2>\u91d1\u8272\u7684&ldquo;\u8461\u8404&rdquo;\u5371\u5bb3\u5e7f</h2>\r\n\r\n<p>\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u5bfc\u81f4\u7684\u75be\u75c5\u79cd\u7c7b\u8de8\u5ea6\u4e4b\u5e7f\uff0c\u4fb5\u67d3\u5bbf\u4e3b\u8303\u56f4\u4e4b\u5927\uff0c\u611f\u67d3\u6bd4\u4f8b\u4e4b\u9ad8\u5728\u5fae\u751f\u7269\u754c\u53ef\u8c13\u7fd8\u695a\u3002</p>\r\n\r\n<p>\u4eba\u7fa4\u4e2d\u6bcf\u4e09\u4e2a\u4eba\u4e2d\u5c31\u6709\u4e00\u4e2a\u662f\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7684\u957f\u671f\u643a\u5e26\u8005\u3002\u5b83\u4eec\u901a\u5e38\u5bc4\u5bbf\u5728\u4eba\u4f53\u4e0a\u6e29\u6696\u6f6e\u6e7f\u7684\u5730\u65b9\uff0c\u5982\u9f3b\u8154\u9ecf\u819c\u3001\u814b\u4e0b\u4ee5\u53ca\u8179\u80a1\u6c9f\u5904\u3002\u5e73\u65f6\u5b83\u4eec\u9759\u9759\u6f5c\u4f0f\uff0c\u5f53\u5bbf\u4e3b\u7684\u76ae\u80a4\u6216\u7c98\u819c\u53d7\u635f\u65f6\uff0c\u5b83\u4eec\u5c31\u4f1a\u5165\u4fb5\u5bbf\u4e3b\u60f9\u4e8b\u751f\u975e\u3002</p>\r\n\r\n<p>\u5b83\u80fd\u5bfc\u81f4\u7684\u75be\u75c5\u4e5f\u591a\u79cd\u591a\u6837\uff0c\u8f7b\u5ea6\u7684\u5982\u5e38\u89c1\u7684\u76ae\u80a4\u3001\u7c98\u819c\u7684\u611f\u67d3\uff0c\u8fd9\u65f6\u51e0\u5143\u94b1\u7684\u5916\u7528\u6297\u751f\u7d20\u8f6f\u818f\u5c31\u80fd\u5c06\u5b83\u4eec\u8f7b\u677e\u6d88\u706d\uff1b\u82e5\u5403\u4e0b\u4e86\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u6c61\u67d3\u7684\u98df\u7269\uff0c\u5c3d\u7ba1\u70f9\u996a\u65f6\u7684\u9ad8\u6e29\u4f1a\u5c3d\u6570\u6740\u6b7b\u5b83\uff0c\u7136\u800c\u5b83\u7559\u4e0b\u7684\u9057\u4ea7&mdash;&mdash;\u80a0\u6bd2\u7d20\uff0c\u5374\u4f1a\u8ba9\u4f60\u9a7b\u8db3\u9a6c\u6876\uff0c\u7528\u5c3d\u62bd\u7eb8\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://1-im.guokr.com/aP9-GdV_kzdPD4HP6WHVcBpFJNZGhpu4BGQK5whS-aqEAwAAWAIAAEpQ.jpg\" style=\"height:427px; width:640px\" />\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4ea7\u751f\u7684\u80a0\u6bd2\u7d20\u4f1a\u8ba9\u4f60\u9a7b\u8db3\u9a6c\u6876\u4e0d\u80fd\u81ea\u5df2 | positivelyaware</p>\r\n\r\n<p>\u91cd\u5ea6\u7684\u611f\u67d3\u75be\u75c5\u5219\u5341\u5206\u51f6\u9669\uff0c\u5728\u6297\u751f\u7d20\u88ab\u5e94\u7528\u4e8e\u4e34\u5e8a\u524d\uff0c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u5bfc\u81f4\u7684\u80ba\u708e\u6b7b\u4ea1\u7387\u9ad8\u8fbe30%\uff0c\u7136\u800c\u5b83\u8fd8\u5e76\u4e0d\u4f1a\u6b62\u6b65\u4e8e\u6b64\uff0c\u5b83\u8fd8\u80fd\u5bfc\u81f4\u5fc3\u5185\u819c\u708e\u3001\u83cc\u8840\u75c7\u3001\u4e2d\u6bd2\u6027\u4f11\u514b\u5f81\u7b49\u9ad8\u6b7b\u4ea1\u7387\u7684\u75be\u75c5\u3002</p>\r\n\r\n<p>\u636e\u7f8e\u56fd\u75be\u63a7\u4e2d\u5fc3\u7684\u7edf\u8ba1\uff0c\u6bcf\u5e74\u4ec5\u7f8e\u56fd\u5c31\u67098\u4e07\u4f59\u4eba\u4e25\u91cd\u611f\u67d3\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\uff0c\u5e76\u6700\u7ec8\u9020\u6210\u5176\u4e2d\u7ea61\u4e07\u4eba\u6b7b\u4ea1\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://1-im.guokr.com/afNsNISKmh-KymIaUbNJ7eYl1d8DrUzUJnl22kcw5qw4BAAArQEAAEpQ.jpg\" style=\"height:254px; width:640px\" />2013\u5e74\u7f8e\u56fd\u75be\u63a7\u4e2d\u5fc3\u5bf9\u7532\u6c27\u897f\u6797\u6297\u6027\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7684\u81f4\u75c5\u7edf\u8ba1\u7ed3\u679c | USCDC</p>\r\n\r\n<p>\u9664\u4e86\u611f\u67d3\u4eba\u7c7b\u5916\uff0c\u5176\u4ed6\u54fa\u4e73\u52a8\u7269\u4e5f\u90fd\u662f\u4ed6\u7684\u730e\u7269\u3002\u7279\u522b\u662f\u4eba\u7c7b\u96c6\u4e2d\u9972\u517b\u7684\u7ecf\u6d4e\u52a8\u7269\u66f4\u6df1\u53d7\u5176\u5bb3\u3002\u5728\u5976\u725b\u8eab\u4e0a\uff0c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4f1a\u5bfc\u81f4\u4e73\u817a\u708e\uff0c\u663e\u8457\u964d\u4f4e\u725b\u5976\u7684\u4ea7\u91cf\u548c\u8d28\u91cf\uff1b\u5728\u5bb6\u79bd\u4e0a\uff0c\u5b83\u80fd\u5bfc\u81f4\u8db3\u57ab\u708e\u548c\u5173\u8282\u708e\uff0c\u76f4\u767d\u7684\u8bf4\uff0c\u4f60\u7231\u5543\u7684\u9e21\u722a\u6b63\u597d\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4e5f\u7231\u5543\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://1-im.guokr.com/AsFyd3QgysflyxS29mxUFTbzibPel917eJC8IqfLYWYYAwAAEQIAAEpQ.jpg\" style=\"height:427px; width:640px\" />\u88ab\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc&ldquo;\u5543&rdquo;\u8fc7\u7684\u9e21\u722a | poultrydvm</p>\r\n\r\n<h2>\u65e0\u6cd5\u5c06\u6211\u7f6e\u4e8e\u6b7b\u5730\u7684\uff0c\u66f4\u4ee4\u6211\u575a\u5f3a</h2>\r\n\r\n<p>\u81ea20\u4e16\u7eaa40\u5e74\u4ee3\uff0c\u4eba\u7c7b\u53d1\u73b0\u7684\u7b2c\u4e00\u79cd\u6297\u751f\u7d20&mdash;&mdash;\u9752\u9709\u7d20\u5f00\u59cb\u5728\u4e34\u5e8a\u4e2d\u7528\u4e8e\u6cbb\u7597\u611f\u67d3\u75be\u75c5\uff0c\u8bb8\u591a\u8086\u8650\u6570\u4e16\u7eaa\u7684\u4f20\u67d3\u75c5\u90fd\u6216\u88ab\u63a7\u5236\u6216\u6d88\u706d\uff0c\u4eba\u7c7b\u7684\u5e73\u5747\u5bff\u547d\u81ea\u6b64\u4e5f\u6709\u4e86\u663e\u8457\u7684\u63d0\u5347\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://3-im.guokr.com/fYW4-Wmx_lvpVgAvshpUmvjH0OmpVWMrzPkG7aJIcdtsAQAABQIAAEpQ.jpg\" style=\"height:517px; width:364px\" />\u9752\u9709\u7d20\u53d1\u73b0\u8005\uff1a\u4e9a\u5386\u5c71\u5927&middot;\u5f17\u83b1\u660e\uff08Alexander Fleming\uff09| Wikimedia Commons</p>\r\n\r\n<p>\u7136\u800c\uff0c\u81f4\u75c5\u83cc\u4e5f\u968f\u5373\u53d1\u8d77\u53cd\u6297\u3002\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7387\u5148\u625b\u8d77\u4e86\u8010\u53d7\u6297\u751f\u7d20\u7684\u5927\u65d7&mdash;&mdash;\u51e0\u4e4e\u4e0e\u6297\u751f\u7d20\u5e94\u7528\u4e8e\u4e34\u5e8a\u540c\u65f6\uff0c\u533b\u9662\u4e2d\u5c31\u53d1\u73b0\u4e86\u8010\u53d7\u9752\u9709\u7d20\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u3002</p>\r\n\r\n<p>\u4e0d\u4ec5\u5982\u6b64\uff0c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u8fd8\u4f1a\u6177\u6168\u5730\u5c06\u8010\u6297\u751f\u7d20\u7684\u57fa\u56e0\u4f20\u9012\u7ed9\u5176\u4ed6\u540c\u7c7b\u6216\u4e0d\u540c\u7c7b\u7684\u7ec6\u83cc\u3002\u65f6\u81f3\u4eca\u65e5\uff0c\u5728\u533b\u9662\u4e2d\u5206\u79bb\u5f97\u5230\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4e2d\u8d85\u8fc790%\u90fd\u5177\u6709\u9752\u9709\u7d20\u7684\u8010\u53d7\u6027\uff0c\u800c\u5176\u4ed6\u7ec6\u83cc\u7684\u9752\u9709\u7d20\u8010\u53d7\u6027\u4ea6\u5341\u5206\u666e\u904d\u3002\u8fd9\u4e5f\u662f\u4e3a\u4f55\u73b0\u5728\u533b\u9662\u4e2d\u5df2\u5f88\u5c11\u4f7f\u7528\u9752\u9709\u7d20\u7684\u4e00\u4e2a\u91cd\u8981\u539f\u56e0\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://1-im.guokr.com/L4yMmRnZnY5mt7hscFi70bttzA0wFzJ5SfJ6lfQCG7ZkAwAAyAEAAEpQ.jpg\" style=\"height:336px; width:640px\" />\u7ec6\u83cc\u901a\u8fc7\u53d1\u670b\u53cb\u5708\u57fa\u56e0\u6c34\u5e73\u8f6c\u79fb\u6765\u5171\u4eab\u8010\u836f\u57fa\u56e0 | Yuki\u5c0f\u67d2</p>\r\n\r\n<p>\u81ea\u9752\u9709\u7d20\u4e4b\u540e\uff0c\u4eba\u7c7b\u5f00\u53d1\u4e86\u6570\u5343\u79cd\u6216\u5728\u4e34\u5e8a\u6216\u5728\u755c\u7267\u751f\u4ea7\u4e2d\u4f7f\u7528\u7684\u6297\u751f\u7d20\uff0c\u7136\u800c\u7ec6\u83cc\u4e5f\u6ca1\u95f2\u7740\uff0c\u5728\u4e00\u79cd\u65b0\u6297\u751f\u7d20\u5e94\u7528\u4e00\u6bb5\u65f6\u95f4\u540e\uff0c\u6211\u4eec\u603b\u80fd\u53d1\u73b0\u643a\u5e26\u4e86\u65b0\u6297\u751f\u7d20\u8010\u53d7\u57fa\u56e0\u7684\u7ec6\u83cc\u3002\u5c31\u8fde\u6297\u751f\u7d20\u4e2d\u7684\u7ec8\u6781\u5927\u62db&mdash;\u4e07\u53e4\u9709\u7d20\u3001\u8fbe\u6258\u9709\u7d20\u3001\u5229\u5948\u5511\u80fa\u4e5f\u5df2\u4e0d\u80fd\u4fdd\u8bc1\u80fd100%\u6740\u6b7b\u6240\u6709\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u3002</p>\r\n\r\n<h2>\u53e6\u8f9f\u8e4a\u5f84\uff1a\u7528CRISPR\u6765\u6740\u83cc</h2>\r\n\r\n<p>\u4e3a\u4e86\u9632\u6b62\u672a\u6765&ldquo;\u65e0\u836f\u53ef\u7528&rdquo;\u7684\u73b0\u8c61\u53d1\u751f\uff0c\u4eba\u4eec\u5f00\u59cb\u53e6\u8f9f\u8e4a\u5f84\u6765\u5bf9\u4ed8\u8010\u836f\u83cc\u3002</p>\r\n\r\n<p>CRISPR\u6280\u672f\u5728\u5f53\u4e0b\u53ef\u8c13\u57fa\u56e0\u7f16\u8f91\u9886\u57df\u7684\u5f53\u7ea2\u5ba0\u513f\u3002CRISPR\u7cfb\u7edf\u662f\u6765\u81ea\u4e8e\u7ec6\u83cc\u7684\u4e00\u5957\u514d\u75ab\u7cfb\u7edf\uff0c\u7528\u6765\u5207\u65ad\u5916\u6e90\u5165\u4fb5\u7ec6\u83cc\u7684\u566c\u83cc\u4f53\u6216\u8d28\u7c92\u6216\u5176\u4ed6DNA\u7247\u6bb5\uff0c\u4fdd\u62a4\u81ea\u8eabDNA\u7684\u5b8c\u6574\u3002\u4f46\u82e5\u4eba\u4e3a\u8bbe\u8ba1\u4e00\u6bb5\u5177\u6709\u81ea\u9776\u5411\u80fd\u529b\u7684CRISPR RNA\u8f6c\u5165\u7ec6\u83cc\u4e2d\uff0c\u7ec6\u83cc\u4fbf\u4f1a\u654c\u6211\u4e0d\u5206\uff0c\u5207\u65ad\u81ea\u5df1\u7684\u57fa\u56e0\u7ec4DNA\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://2-im.guokr.com/tJWY5OdKThG28UZTyTrVlNuT8RxDd-hv2PrLgRoFb1hIAgAAcwEAAEpQ.jpg\" style=\"height:371px; width:584px\" />CRISPR-Cas9\u7684\u539f\u7406\u793a\u610f\u56fe | jhu</p>\r\n\r\n<p>\u5728\u771f\u6838\u751f\u7269\u7ec6\u80de\u4e2d\u8fdb\u884c\u57fa\u56e0\u7f16\u8f91\u65f6\uff0c\u901a\u8fc7CRISPR RNA\u7684\u5f15\u5bfc\uff0cCas\u86cb\u767d\u80fd\u591f\u5207\u65adDNA\uff0c\u5f53\u4e0d\u63d0\u4f9b\u7528\u4e8e\u4fee\u590d\u65ad\u88c2\u7684\u4f9b\u4f53DNA\u65f6\uff0c\u771f\u6838\u751f\u7269\u7ec6\u80de\u4e2d\u6709\u4e00\u5957\u5e94\u6025\u7684DNA\u4fee\u590d\u673a\u5236\uff0c\u79f0\u4e3a\u975e\u540c\u6e90\u672b\u7aef\u91cd\u7ec4\uff08Non-homologous end joining, NHEJ\uff09\uff0c\u80fd\u591f\u5feb\u901f\u5c06\u65ad\u88c2\u5904\u4fee\u590d\u3002\u7136\u800c\uff0c\u7edd\u5927\u90e8\u5206\u7684\u7ec6\u83cc\u5e76\u6ca1\u6709\u50cf\u771f\u6838\u7ec6\u80de\u4e00\u6837\u5177\u6709\u975e\u540c\u6e90\u672b\u7aef\u91cd\u7ec4\u7684\u5feb\u901f\u5e94\u6025\u4fee\u590d\u7cfb\u7edf\uff0c\u65ad\u6389\u7684DNA\u4e0d\u80fd\u81ea\u5df1\u8fde\u8d77\u6765\uff0c\u7ec6\u83cc\u4e5f\u5c31\u56e0\u6b64\u6b7b\u6389\u4e86\u3002</p>\r\n\r\n<p>\u7528CRISPR\u7cfb\u7edf\u6740\u83cc\u7684\u6700\u5927\u4f18\u52bf\u662f\u53ef\u4ee5&ldquo;\u5b9a\u5411\u6253\u51fb&rdquo;\uff0c\u4eba\u4eec\u53ef\u4ee5\u6839\u636e\u75c5\u539f\u83cc\u7684\u7279\u5f02\u6027\u57fa\u56e0\u8bbe\u8ba1\u53ea\u6740\u75c5\u539f\u83cc\u7684\u6b66\u5668\uff0c\u800c\u4e0d\u4f1a\u8bef\u4f24\u5bf9\u4eba\u6709\u76ca\u7684\u5176\u4ed6\u5171\u751f\u7ec6\u83cc\u3002\u8fd9\u5c31\u907f\u514d\u4e86\u6297\u751f\u7d20\u90a3\u79cd&ldquo;\u65e0\u5dee\u522b\u8f70\u70b8&rdquo;\u7684\u6a21\u5f0f\uff0c\u4fdd\u62a4\u4e86\u4eba\u4f53\u5185\u7684\u5171\u751f\u83cc\u3002</p>\r\n\r\n<h2>&ldquo;\u9ec4\u91d1\u7532&rdquo;\u592a\u5f3a\uff0c\u4e0d\u5982\u7ed9\u5b83\u6f02\u4e2a\u767d\uff1f</h2>\r\n\r\n<p>\u524d\u9762\u8bf4\u8fc7\uff0c\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u5177\u6709\u6297\u6c27\u5316\u7684\u4f5c\u7528\uff0c\u80fd\u62b5\u6297\u5bbf\u4e3b\u5404\u7c7b\u6d3b\u6027\u6c27\u9632\u5fa1\u3002\u90a3\u4e48\uff0c\u5982\u679c\u60f3\u529e\u6cd5\u53bb\u6389\u8fd9\u5c42&ldquo;\u9ec4\u91d1\u7532&rdquo;\uff0c\u4f1a\u53d1\u751f\u4ec0\u4e48\u5462\uff1f\u8fd1\u671f\u53d1\u8868\u5728\u300a\u5c16\u7aef\u79d1\u5b66\u300b\uff08Advanced Science\uff09\u6742\u5fd7\u4e0a\u7684\u4e00\u9879\u7814\u7a76\u663e\u793a\uff0c\u7ed9&ldquo;\u91d1\u8461\u8404&rdquo;\u6f02\u767d\u540e\uff0c\u5b83\u4eec\u4f1a\u660e\u663e\u53d8\u5f31\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://3-im.guokr.com/s8osLeVvNHBRIG356UMrSvCbeLFsk_jk6hvcU2LQohhEAgAAfQEAAEpQ.jpg\" style=\"height:381px; width:580px\" />\u6ce2\u58eb\u987f\u5927\u5b66\u7684\u7814\u7a76\u56e2\u961f\u6b63\u5728\u5bf9\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u8fdb\u884c&ldquo;\u6f02\u767d&rdquo;\u5904\u7406 | BU</p>\r\n\r\n<p>\u8fd9\u7ec4\u6765\u81ea\u6ce2\u58eb\u987f\u5927\u5b66\u7684\u7814\u7a76\u56e2\u961f\u5bf9\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u8fdb\u884c\u663e\u5fae\u89c2\u5bdf\u65f6\uff0c\u53d1\u73b0\u4f7f\u7528\u7279\u5b9a\u6ce2\u957f\uff08450-470nm\uff09\u7684\u84dd\u5149\u7167\u5c04\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u65f6\u4f1a\u53d1\u751f\u5149\u6f02\u767d\u73b0\u8c61\u3002\u800c\u8fd9\u79cd\u5149\u6f02\u767d\u73b0\u8c61\u7684\u51fa\u73b0\u610f\u5473\u7740\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u7684\u964d\u89e3\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://2-im.guokr.com/mg7DhZ-3LjW3eMsLCzA88dUosp70UpvmIiBxDMysMwLJAgAA5gAAAFBO.png\" style=\"height:206px; width:640px\" />\u84dd\u5149\u4f7f\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u964d\u89e3 | \u53c2\u8003\u6587\u732e1</p>\r\n\r\n<p>\u7814\u7a76\u8005\u4eec\u540e\u6765\u53d1\u73b0\uff0c\u8fd9\u79cd\u84dd\u5149\u4e0d\u4ec5\u53ef\u4ee5\u964d\u89e3\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7ec6\u80de\u819c\u4e0a\u7684\u8461\u8404\u7403\u83cc\u9ec4\u7d20\uff0c\u8ba9\u5b83\u4eec\u4e22\u6389&ldquo;\u9ec4\u91d1\u7532&rdquo;\uff0c\u8fd8\u4f1a\u5728\u7ec6\u80de\u819c\u4e0a\u7559\u4e0b\u4e34\u65f6\u7684\u5b54\u6d1e\uff0c\u4f7f\u5176\u5728\u77ed\u65f6\u95f4\u5185&ldquo;\u95e8\u6237\u5927\u5f00&rdquo;\u3002</p>\r\n\r\n<p>\u4e22\u76d4\u5378\u7532\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\uff0c\u4e0d\u518d&ldquo;\u767e\u836f\u4e0d\u4fb5&rdquo;\uff0c\u8fd9\u65f6\u82e5\u914d\u5408\u8fc7\u6c27\u5316\u6c22\uff0c\u5219\u80fd\u591f\u6bd4\u6297\u751f\u7d20\u66f4\u52a0\u6709\u6548\u5730\u6740\u6b7b\u5b83\u4eec\u3002\u7814\u7a76\u8005\u4eec\u5df2\u7ecf\u901a\u8fc7\u8bd5\u9a8c\u8bc1\u660e\uff0c\u8fd9\u79cd\u6f02\u767d+\u8d81\u865a\u800c\u5165\u7684\u65b9\u6cd5\u53ef\u4ee5\u6740\u6b7b99.9\uff05\u7684\u8010\u7532\u6c27\u897f\u6797\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\uff08MRSA\uff09\uff0c\u8fd8\u80fd\u591f\u52a0\u901f\u611f\u67d3MRSA\u5c0f\u9f20\u76ae\u80a4\u4f24\u53e3\u7684\u6108\u5408\u3002</p>\r\n\r\n<p>\u8fd9\u9879\u7814\u7a76\u4e3a\u6cbb\u7597\u6297\u836f\u6027\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u5bfc\u81f4\u7684\u611f\u67d3\u75be\u75c5\uff0c\u63d0\u4f9b\u4e86\u4e00\u79cd\u5d2d\u65b0\u7684\u624b\u6bb5\uff0c\u5b83\u53ea\u5bf9\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u594f\u6548\uff0c\u4e0d\u5bb9\u6613\u8ba9\u7ec6\u83cc\u4ea7\u751f\u8010\u53d7\u6027\uff0c\u6cbb\u7597\u8fc7\u7a0b\u4e5f\u4e0d\u4f1a\u7ed9\u60a3\u8005\u9020\u6210\u521b\u4f24\u3002\u8bba\u6587\u4f5c\u8005Jie Hui\u8868\u793a\uff1a&ldquo;\u8fd9\u79cd\u6fc0\u5149\u7167\u5c04\u4e0d\u4f1a\u7ed9\u4eba\u4f53\u5e26\u6765\u75db\u89c9\uff0c\u975e\u5e38\u9002\u5408\u4e34\u5e8a\u5e94\u7528\u3002&rdquo;</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://3-im.guokr.com/gCPGcXU9RxedKn4ykVbjUBhvkY9D9V-PlsBsx5mk213kAwAAmAIAAEpQ.jpg\" style=\"height:427px; width:640px\" />\u7814\u7a76\u8005\u4eec\u6b63\u5728\u52aa\u529b\u5c06\u84dd\u5149\u7597\u6cd5\u8f6c\u5316\u4e3a\u4e34\u5e8a\uff0c\u5e0c\u671b\u5b83\u53ef\u4ee5\u5e2e\u52a9\u6cbb\u7597\u7cd6\u5c3f\u75c5\u6e83\u75a1\u60a3\u8005 | BU</p>\r\n\r\n<p>\u53ea\u8981\u8fd9\u4e9b\u91d1\u8272\u7684&ldquo;\u8461\u8404&rdquo;\u8fd8\u5728\u5a01\u80c1\u4eba\u4eec\u7684\u5065\u5eb7\uff0c\u79d1\u5b66\u5bb6\u4e0e\u5b83\u4eec\u7684\u8f83\u91cf\u5c31\u4e0d\u4f1a\u505c\u6b65\u3002\u6216\u8bb8\u6709\u4e00\u5929\uff0c\u5b83\u4eec\u5c06\u4e0d\u5f97\u4e0d\u629b\u5f03\u91d1\u707f\u707f\u7684\u5927\u540d\uff0c\u505a\u56de\u4e00\u4e32\u4eba\u755c\u65e0\u5bb3\u7684\u666e\u901a&ldquo;\u8461\u8404&rdquo;\u3002(\u7f16\u8f91\uff1aYuki)</p>\r\n\r\n<h2 class=\"references\">\u53c2\u8003\u6587\u732e\uff1a</h2>\r\n\r\n<ol class=\"reference-item\">\r\n\t<li>Dong P T, Mohammad H, Hui J, et al. Photolysis of Staphyloxanthin in Methicillin\u2010Resistant Staphylococcus aureus Potentiates Killing by Reactive Oxygen Species[J]. Advanced Science, 2019: 1900030.</li>\r\n</ol>\r\n\r\n<h2>\u4f5c\u8005\u540d\u7247</h2>\r\n\r\n<p><img alt=\"\" src=\"http://2-im.guokr.com/7Q9kFUbR8LWj2QJRG6NKoek5z01wSA9P_aWk9ZBaHog4BAAAkAIAAEpQ.jpg\" style=\"height:389px; width:640px\" /></p>", "date_created": "2019-07-23T11:21:09.554352+08:00", "date_modified": "2019-07-23T11:35:22.587147+08:00", "date_published": "2019-07-23T11:35:26+08:00", "id": 454355, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "sciblog", "replies_count": 20, "small_image": "https://3-im.guokr.com/z_OYMJtqdi4wz9YTt4AbWIH1mxdfAuuG8nP0L9-1CdTAAwAAYwIAAEpQ.jpg", "subject": {"articles_count": 2752, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "biology", "minisite_key": "sciblog", "name": "\u751f\u7269", "sort_score": 2, "subject_type": "normal"}, "subject_key": "biology", "summary": "\u5265\u53bb\u201c\u9ec4\u91d1\u7532\u201d\uff0c\u8ba9\u81f4\u547d\u201c\u8461\u8404\u201d\u4e0d\u518d\u9a84\u50b2", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u8461\u8404\u5e94\u8be5\u662f\u5927\u5bb6\u6700\u719f\u6089\u7684\u6c34\u679c\u4e86\u3002\u4f60\u6216\u8bb8\u54c1\u5c1d\u7ea2\u8272\u3001\u767d\u8272\u3001\u7d2b\u8272\u3001\u7eff\u8272\u7684\u8461\u8404\uff0c\u6bcf\u4e00\u9897\u90fd\u6676\u83b9\u5254\u900f\u53c8\u9178\u751c\u53ef\u53e3\u3002\u4e0d\u540c\u989c\u8272\u7684\u8461\u8404\u4e32|solomonitg-com\u4e0d\u8fc7\uff0c\u6709\u8fd9\u6837\u4e00\u79cd\u91d1\u707f\u707f\u7684\u201c\u8461\u8404\u4e32\u201d\u5374\u5e76\u4e0d\u53ef\u53e3\uff0c\u4e0d\u5c0f\u5fc3\u5403\u4e0b\u53bb\u8fd8\u53ef\u80fd\u7ed9\u6211\u4eec\u7684\u5065\u5eb7\u5e26\u6765\u4e0d\u826f\u5f71\u54cd\u3002\u5b83\u5c31\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u3002\u81ea\u5e26\u201c\u9ec4\u91d1\u7532\u201d\u7684\u201c\u8461\u8404\u4e32\u201d19\u4e16\u7eaa80\u5e74\u4ee3\uff0c\u4e00\u4f4d\u540d\u53eb\u4e9a\u5386\u5c71\u5927\u00b7\u5965\u53e4\u65af\u901a\uff08Alexander Ogston\uff09\u7684\u82f1\u56fd\u533b\u751f\uff0c\u4ece\u75c5\u4eba\u7684\u8113\u80bf\u4e2d\u5e38\u5e38\u5206\u79bb\u5f97\u5230\u4e00\u79cd\u5f62\u4f3c\u8461\u8404\u4e32\u7684\u7403\u83cc\uff0c\u8fd9\u79cd\u7ec6\u83cc\u5728\u542b\u6709\u52a8\u7269\u8840\u6db2\u7684\u743c\u8102\u5e73\u677f\u4e0a\u80fd\u5f62\u6210\u91d1\u9ec4\u8272\u7684\u83cc\u843d\u3002\u8840\u5e73\u677f\u4e0a\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc | Wikimedia Commons\u4e4b\u540e\uff0c\u5fb7\u56fd\u5fae\u751f\u7269\u5b66\u5bb6\u5f17\u91cc\u5fb7\u91cc\u5e0c\u00b7\u6731\u5229\u65af\u00b7\u7f57\u68ee\u5df4\u8d6b\uff08Friedrich Julius Rosenbach\uff09\u5c06\u5176\u547d\u540d\u4e3aStaphylococcus aureus\uff08\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\uff09\uff0c\u5176\u4e2dStaphylococcus\u6765\u6e90\u4e8e\u5e0c\u814a\u6587\uff0c\u610f\u4e3a\u201c\u8461\u8404\u4e32\u201d\uff0caureus\u5219\u6765\u81ea\u4e8e\u62c9\u4e01\u6587\uff0c\u610f\u4e3a\u201c\u91d1\u9ec4\u8272\u7684\u201d\u3002\u7535\u955c\u7167\u4e0b\u7684\u201c\u91d1\u8461\u8404\u201d | CDC\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4e4b\u6240\u4ee5\u5448\u73b0\u51fa\u9c9c\u4eae\u7684\u91d1\u9ec4\u8272\uff0c\u662f\u56e0\u4e3a\u5b83\u80fd\u5408\u6210\u4e00\u79cd\u540d\u4e3a\u201c\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u201d\uff08Staphyloxanthin\uff09\u7684\u7c7b\u80e1\u841d\u535c\u7d20\u8272\u7d20\uff0c\u5b83\u5927\u6982\u957f\u4e0b\u9762\u8fd9\u4e2a\u6837\u5b50\u3002\u8461\u8404\u7403\u83cc\u9ec4\u7d20\uff08Staphyloxanthin\uff09| Wikimedia Commons\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u957f\u957f\u7684\u758f\u6c34\u6027\u78b3\u94fe\uff0c\u4f7f\u5176\u53ef\u4ee5\u5d4c\u5165\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7684\u7ec6\u80de\u819c\u4e0a\u3002\u800c\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u4e0e\u5176\u4ed6\u7c7b\u80e1\u841d\u535c\u7d20\u4e00\u6837\uff0c\u90fd\u5177\u6709\u6297\u6c27\u5316\u7684\u4f5c\u7528\uff0c\u80fd\u62b5\u6297\u5bbf\u4e3b\u514d\u75ab\u7ec6\u80de\u5206\u6ccc\u7684\u5177\u6709\u6740\u83cc\u80fd\u529b\u7684\u5404\u79cd\u7c7b\u578b\u6d3b\u6027\u6c27\u3002\u56e0\u6b64\uff0c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u53ef\u8c13\u81ea\u5e26\u201c\u9ec4\u91d1\u7532\u201d\u795e\u88c5\u3002\u5404\u79cd\u7c7b\u578b\u7684\u6d3b\u6027\u6c27 | vivo.colostate.edu\u91d1\u8272\u7684\u201c\u8461\u8404\u201d\u5371\u5bb3\u5e7f\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u5bfc\u81f4\u7684\u75be\u75c5\u79cd\u7c7b\u8de8\u5ea6\u4e4b\u5e7f\uff0c\u4fb5\u67d3\u5bbf\u4e3b\u8303\u56f4\u4e4b\u5927\uff0c\u611f\u67d3\u6bd4\u4f8b\u4e4b\u9ad8\u5728\u5fae\u751f\u7269\u754c\u53ef\u8c13\u7fd8\u695a\u3002\u4eba\u7fa4\u4e2d\u6bcf\u4e09\u4e2a\u4eba\u4e2d\u5c31\u6709\u4e00\u4e2a\u662f\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7684\u957f\u671f\u643a\u5e26\u8005\u3002\u5b83\u4eec\u901a\u5e38\u5bc4\u5bbf\u5728\u4eba\u4f53\u4e0a\u6e29\u6696\u6f6e\u6e7f\u7684\u5730\u65b9\uff0c\u5982\u9f3b\u8154\u9ecf\u819c\u3001\u814b\u4e0b\u4ee5\u53ca\u8179\u80a1\u6c9f\u5904\u3002\u5e73\u65f6\u5b83\u4eec\u9759\u9759\u6f5c\u4f0f\uff0c\u5f53\u5bbf\u4e3b\u7684\u76ae\u80a4\u6216\u7c98\u819c\u53d7\u635f\u65f6\uff0c\u5b83\u4eec\u5c31\u4f1a\u5165\u4fb5\u5bbf\u4e3b\u60f9\u4e8b\u751f\u975e\u3002\u5b83\u80fd\u5bfc\u81f4\u7684\u75be\u75c5\u4e5f\u591a\u79cd\u591a\u6837\uff0c\u8f7b\u5ea6\u7684\u5982\u5e38\u89c1\u7684\u76ae\u80a4\u3001\u7c98\u819c\u7684\u611f\u67d3\uff0c\u8fd9\u65f6\u51e0\u5143\u94b1\u7684\u5916\u7528\u6297\u751f\u7d20\u8f6f\u818f\u5c31\u80fd\u5c06\u5b83\u4eec\u8f7b\u677e\u6d88\u706d\uff1b\u82e5\u5403\u4e0b\u4e86\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u6c61\u67d3\u7684\u98df\u7269\uff0c\u5c3d\u7ba1\u70f9\u996a\u65f6\u7684\u9ad8\u6e29\u4f1a\u5c3d\u6570\u6740\u6b7b\u5b83\uff0c\u7136\u800c\u5b83\u7559\u4e0b\u7684\u9057\u4ea7\u2014\u2014\u80a0\u6bd2\u7d20\uff0c\u5374\u4f1a\u8ba9\u4f60\u9a7b\u8db3\u9a6c\u6876\uff0c\u7528\u5c3d\u62bd\u7eb8\u3002\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4ea7\u751f\u7684\u80a0\u6bd2\u7d20\u4f1a\u8ba9\u4f60\u9a7b\u8db3\u9a6c\u6876\u4e0d\u80fd\u81ea\u5df2 | positivelyaware\u91cd\u5ea6\u7684\u611f\u67d3\u75be\u75c5\u5219\u5341\u5206\u51f6\u9669\uff0c\u5728\u6297\u751f\u7d20\u88ab\u5e94\u7528\u4e8e\u4e34\u5e8a\u524d\uff0c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u5bfc\u81f4\u7684\u80ba\u708e\u6b7b\u4ea1\u7387\u9ad8\u8fbe30%\uff0c\u7136\u800c\u5b83\u8fd8\u5e76\u4e0d\u4f1a\u6b62\u6b65\u4e8e\u6b64\uff0c\u5b83\u8fd8\u80fd\u5bfc\u81f4\u5fc3\u5185\u819c\u708e\u3001\u83cc\u8840\u75c7\u3001\u4e2d\u6bd2\u6027\u4f11\u514b\u5f81\u7b49\u9ad8\u6b7b\u4ea1\u7387\u7684\u75be\u75c5\u3002\u636e\u7f8e\u56fd\u75be\u63a7\u4e2d\u5fc3\u7684\u7edf\u8ba1\uff0c\u6bcf\u5e74\u4ec5\u7f8e\u56fd\u5c31\u67098\u4e07\u4f59\u4eba\u4e25\u91cd\u611f\u67d3\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\uff0c\u5e76\u6700\u7ec8\u9020\u6210\u5176\u4e2d\u7ea61\u4e07\u4eba\u6b7b\u4ea1\u30022013\u5e74\u7f8e\u56fd\u75be\u63a7\u4e2d\u5fc3\u5bf9\u7532\u6c27\u897f\u6797\u6297\u6027\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7684\u81f4\u75c5\u7edf\u8ba1\u7ed3\u679c | USCDC\u9664\u4e86\u611f\u67d3\u4eba\u7c7b\u5916\uff0c\u5176\u4ed6\u54fa\u4e73\u52a8\u7269\u4e5f\u90fd\u662f\u4ed6\u7684\u730e\u7269\u3002\u7279\u522b\u662f\u4eba\u7c7b\u96c6\u4e2d\u9972\u517b\u7684\u7ecf\u6d4e\u52a8\u7269\u66f4\u6df1\u53d7\u5176\u5bb3\u3002\u5728\u5976\u725b\u8eab\u4e0a\uff0c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4f1a\u5bfc\u81f4\u4e73\u817a\u708e\uff0c\u663e\u8457\u964d\u4f4e\u725b\u5976\u7684\u4ea7\u91cf\u548c\u8d28\u91cf\uff1b\u5728\u5bb6\u79bd\u4e0a\uff0c\u5b83\u80fd\u5bfc\u81f4\u8db3\u57ab\u708e\u548c\u5173\u8282\u708e\uff0c\u76f4\u767d\u7684\u8bf4\uff0c\u4f60\u7231\u5543\u7684\u9e21\u722a\u6b63\u597d\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4e5f\u7231\u5543\u3002\u88ab\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u201c\u5543\u201d\u8fc7\u7684\u9e21\u722a | poultrydvm\u65e0\u6cd5\u5c06\u6211\u7f6e\u4e8e\u6b7b\u5730\u7684\uff0c\u66f4\u4ee4\u6211\u575a\u5f3a\u81ea20\u4e16\u7eaa40\u5e74\u4ee3\uff0c\u4eba\u7c7b\u53d1\u73b0\u7684\u7b2c\u4e00\u79cd\u6297\u751f\u7d20\u2014\u2014\u9752\u9709\u7d20\u5f00\u59cb\u5728\u4e34\u5e8a\u4e2d\u7528\u4e8e\u6cbb\u7597\u611f\u67d3\u75be\u75c5\uff0c\u8bb8\u591a\u8086\u8650\u6570\u4e16\u7eaa\u7684\u4f20\u67d3\u75c5\u90fd\u6216\u88ab\u63a7\u5236\u6216\u6d88\u706d\uff0c\u4eba\u7c7b\u7684\u5e73\u5747\u5bff\u547d\u81ea\u6b64\u4e5f\u6709\u4e86\u663e\u8457\u7684\u63d0\u5347\u3002\u9752\u9709\u7d20\u53d1\u73b0\u8005\uff1a\u4e9a\u5386\u5c71\u5927\u00b7\u5f17\u83b1\u660e\uff08Alexander Fleming\uff09| Wikimedia Commons\u7136\u800c\uff0c\u81f4\u75c5\u83cc\u4e5f\u968f\u5373\u53d1\u8d77\u53cd\u6297\u3002\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7387\u5148\u625b\u8d77\u4e86\u8010\u53d7\u6297\u751f\u7d20\u7684\u5927\u65d7\u2014\u2014\u51e0\u4e4e\u4e0e\u6297\u751f\u7d20\u5e94\u7528\u4e8e\u4e34\u5e8a\u540c\u65f6\uff0c\u533b\u9662\u4e2d\u5c31\u53d1\u73b0\u4e86\u8010\u53d7\u9752\u9709\u7d20\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u3002\u4e0d\u4ec5\u5982\u6b64\uff0c\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u8fd8\u4f1a\u6177\u6168\u5730\u5c06\u8010\u6297\u751f\u7d20\u7684\u57fa\u56e0\u4f20\u9012\u7ed9\u5176\u4ed6\u540c\u7c7b\u6216\u4e0d\u540c\u7c7b\u7684\u7ec6\u83cc\u3002\u65f6\u81f3\u4eca\u65e5\uff0c\u5728\u533b\u9662\u4e2d\u5206\u79bb\u5f97\u5230\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u4e2d\u8d85\u8fc790%\u90fd\u5177\u6709\u9752\u9709\u7d20\u7684\u8010\u53d7\u6027\uff0c\u800c\u5176\u4ed6\u7ec6\u83cc\u7684\u9752\u9709\u7d20\u8010\u53d7\u6027\u4ea6\u5341\u5206\u666e\u904d\u3002\u8fd9\u4e5f\u662f\u4e3a\u4f55\u73b0\u5728\u533b\u9662\u4e2d\u5df2\u5f88\u5c11\u4f7f\u7528\u9752\u9709\u7d20\u7684\u4e00\u4e2a\u91cd\u8981\u539f\u56e0\u3002\u7ec6\u83cc\u901a\u8fc7\u53d1\u670b\u53cb\u5708\u57fa\u56e0\u6c34\u5e73\u8f6c\u79fb\u6765\u5171\u4eab\u8010\u836f\u57fa\u56e0 | Yuki\u5c0f\u67d2\u81ea\u9752\u9709\u7d20\u4e4b\u540e\uff0c\u4eba\u7c7b\u5f00\u53d1\u4e86\u6570\u5343\u79cd\u6216\u5728\u4e34\u5e8a\u6216\u5728\u755c\u7267\u751f\u4ea7\u4e2d\u4f7f\u7528\u7684\u6297\u751f\u7d20\uff0c\u7136\u800c\u7ec6\u83cc\u4e5f\u6ca1\u95f2\u7740\uff0c\u5728\u4e00\u79cd\u65b0\u6297\u751f\u7d20\u5e94\u7528\u4e00\u6bb5\u65f6\u95f4\u540e\uff0c\u6211\u4eec\u603b\u80fd\u53d1\u73b0\u643a\u5e26\u4e86\u65b0\u6297\u751f\u7d20\u8010\u53d7\u57fa\u56e0\u7684\u7ec6\u83cc\u3002\u5c31\u8fde\u6297\u751f\u7d20\u4e2d\u7684\u7ec8\u6781\u5927\u62db\u2014\u4e07\u53e4\u9709\u7d20\u3001\u8fbe\u6258\u9709\u7d20\u3001\u5229\u5948\u5511\u80fa\u4e5f\u5df2\u4e0d\u80fd\u4fdd\u8bc1\u80fd100%\u6740\u6b7b\u6240\u6709\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u3002\u53e6\u8f9f\u8e4a\u5f84\uff1a\u7528CRISPR\u6765\u6740\u83cc\u4e3a\u4e86\u9632\u6b62\u672a\u6765\u201c\u65e0\u836f\u53ef\u7528\u201d\u7684\u73b0\u8c61\u53d1\u751f\uff0c\u4eba\u4eec\u5f00\u59cb\u53e6\u8f9f\u8e4a\u5f84\u6765\u5bf9\u4ed8\u8010\u836f\u83cc\u3002CRISPR\u6280\u672f\u5728\u5f53\u4e0b\u53ef\u8c13\u57fa\u56e0\u7f16\u8f91\u9886\u57df\u7684\u5f53\u7ea2\u5ba0\u513f\u3002CRISPR\u7cfb\u7edf\u662f\u6765\u81ea\u4e8e\u7ec6\u83cc\u7684\u4e00\u5957\u514d\u75ab\u7cfb\u7edf\uff0c\u7528\u6765\u5207\u65ad\u5916\u6e90\u5165\u4fb5\u7ec6\u83cc\u7684\u566c\u83cc\u4f53\u6216\u8d28\u7c92\u6216\u5176\u4ed6DNA\u7247\u6bb5\uff0c\u4fdd\u62a4\u81ea\u8eabDNA\u7684\u5b8c\u6574\u3002\u4f46\u82e5\u4eba\u4e3a\u8bbe\u8ba1\u4e00\u6bb5\u5177\u6709\u81ea\u9776\u5411\u80fd\u529b\u7684CRISPR RNA\u8f6c\u5165\u7ec6\u83cc\u4e2d\uff0c\u7ec6\u83cc\u4fbf\u4f1a\u654c\u6211\u4e0d\u5206\uff0c\u5207\u65ad\u81ea\u5df1\u7684\u57fa\u56e0\u7ec4DNA\u3002CRISPR-Cas9\u7684\u539f\u7406\u793a\u610f\u56fe | jhu\u5728\u771f\u6838\u751f\u7269\u7ec6\u80de\u4e2d\u8fdb\u884c\u57fa\u56e0\u7f16\u8f91\u65f6\uff0c\u901a\u8fc7CRISPR RNA\u7684\u5f15\u5bfc\uff0cCas\u86cb\u767d\u80fd\u591f\u5207\u65adDNA\uff0c\u5f53\u4e0d\u63d0\u4f9b\u7528\u4e8e\u4fee\u590d\u65ad\u88c2\u7684\u4f9b\u4f53DNA\u65f6\uff0c\u771f\u6838\u751f\u7269\u7ec6\u80de\u4e2d\u6709\u4e00\u5957\u5e94\u6025\u7684DNA\u4fee\u590d\u673a\u5236\uff0c\u79f0\u4e3a\u975e\u540c\u6e90\u672b\u7aef\u91cd\u7ec4\uff08Non-homologous end joining, NHEJ\uff09\uff0c\u80fd\u591f\u5feb\u901f\u5c06\u65ad\u88c2\u5904\u4fee\u590d\u3002\u7136\u800c\uff0c\u7edd\u5927\u90e8\u5206\u7684\u7ec6\u83cc\u5e76\u6ca1\u6709\u50cf\u771f\u6838\u7ec6\u80de\u4e00\u6837\u5177\u6709\u975e\u540c\u6e90\u672b\u7aef\u91cd\u7ec4\u7684\u5feb\u901f\u5e94\u6025\u4fee\u590d\u7cfb\u7edf\uff0c\u65ad\u6389\u7684DNA\u4e0d\u80fd\u81ea\u5df1\u8fde\u8d77\u6765\uff0c\u7ec6\u83cc\u4e5f\u5c31\u56e0\u6b64\u6b7b\u6389\u4e86\u3002\u7528CRISPR\u7cfb\u7edf\u6740\u83cc\u7684\u6700\u5927\u4f18\u52bf\u662f\u53ef\u4ee5\u201c\u5b9a\u5411\u6253\u51fb\u201d\uff0c\u4eba\u4eec\u53ef\u4ee5\u6839\u636e\u75c5\u539f\u83cc\u7684\u7279\u5f02\u6027\u57fa\u56e0\u8bbe\u8ba1\u53ea\u6740\u75c5\u539f\u83cc\u7684\u6b66\u5668\uff0c\u800c\u4e0d\u4f1a\u8bef\u4f24\u5bf9\u4eba\u6709\u76ca\u7684\u5176\u4ed6\u5171\u751f\u7ec6\u83cc\u3002\u8fd9\u5c31\u907f\u514d\u4e86\u6297\u751f\u7d20\u90a3\u79cd\u201c\u65e0\u5dee\u522b\u8f70\u70b8\u201d\u7684\u6a21\u5f0f\uff0c\u4fdd\u62a4\u4e86\u4eba\u4f53\u5185\u7684\u5171\u751f\u83cc\u3002\u201c\u9ec4\u91d1\u7532\u201d\u592a\u5f3a\uff0c\u4e0d\u5982\u7ed9\u5b83\u6f02\u4e2a\u767d\uff1f\u524d\u9762\u8bf4\u8fc7\uff0c\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u5177\u6709\u6297\u6c27\u5316\u7684\u4f5c\u7528\uff0c\u80fd\u62b5\u6297\u5bbf\u4e3b\u5404\u7c7b\u6d3b\u6027\u6c27\u9632\u5fa1\u3002\u90a3\u4e48\uff0c\u5982\u679c\u60f3\u529e\u6cd5\u53bb\u6389\u8fd9\u5c42\u201c\u9ec4\u91d1\u7532\u201d\uff0c\u4f1a\u53d1\u751f\u4ec0\u4e48\u5462\uff1f\u8fd1\u671f\u53d1\u8868\u5728\u300a\u5c16\u7aef\u79d1\u5b66\u300b\uff08Advanced Science\uff09\u6742\u5fd7\u4e0a\u7684\u4e00\u9879\u7814\u7a76\u663e\u793a\uff0c\u7ed9\u201c\u91d1\u8461\u8404\u201d\u6f02\u767d\u540e\uff0c\u5b83\u4eec\u4f1a\u660e\u663e\u53d8\u5f31\u3002\u6ce2\u58eb\u987f\u5927\u5b66\u7684\u7814\u7a76\u56e2\u961f\u6b63\u5728\u5bf9\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u8fdb\u884c\u201c\u6f02\u767d\u201d\u5904\u7406 | BU\u8fd9\u7ec4\u6765\u81ea\u6ce2\u58eb\u987f\u5927\u5b66\u7684\u7814\u7a76\u56e2\u961f\u5bf9\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u8fdb\u884c\u663e\u5fae\u89c2\u5bdf\u65f6\uff0c\u53d1\u73b0\u4f7f\u7528\u7279\u5b9a\u6ce2\u957f\uff08450-470nm\uff09\u7684\u84dd\u5149\u7167\u5c04\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u65f6\u4f1a\u53d1\u751f\u5149\u6f02\u767d\u73b0\u8c61\u3002\u800c\u8fd9\u79cd\u5149\u6f02\u767d\u73b0\u8c61\u7684\u51fa\u73b0\u610f\u5473\u7740\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u7684\u964d\u89e3\u3002\u84dd\u5149\u4f7f\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u964d\u89e3 | \u53c2\u8003\u6587\u732e1\u7814\u7a76\u8005\u4eec\u540e\u6765\u53d1\u73b0\uff0c\u8fd9\u79cd\u84dd\u5149\u4e0d\u4ec5\u53ef\u4ee5\u964d\u89e3\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u7ec6\u80de\u819c\u4e0a\u7684\u8461\u8404\u7403\u83cc\u9ec4\u7d20\uff0c\u8ba9\u5b83\u4eec\u4e22\u6389\u201c\u9ec4\u91d1\u7532\u201d\uff0c\u8fd8\u4f1a\u5728\u7ec6\u80de\u819c\u4e0a\u7559\u4e0b\u4e34\u65f6\u7684\u5b54\u6d1e\uff0c\u4f7f\u5176\u5728\u77ed\u65f6\u95f4\u5185\u201c\u95e8\u6237\u5927\u5f00\u201d\u3002\u4e22\u76d4\u5378\u7532\u7684\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\uff0c\u4e0d\u518d\u201c\u767e\u836f\u4e0d\u4fb5\u201d\uff0c\u8fd9\u65f6\u82e5\u914d\u5408\u8fc7\u6c27\u5316\u6c22\uff0c\u5219\u80fd\u591f\u6bd4\u6297\u751f\u7d20\u66f4\u52a0\u6709\u6548\u5730\u6740\u6b7b\u5b83\u4eec\u3002\u7814\u7a76\u8005\u4eec\u5df2\u7ecf\u901a\u8fc7\u8bd5\u9a8c\u8bc1\u660e\uff0c\u8fd9\u79cd\u6f02\u767d+\u8d81\u865a\u800c\u5165\u7684\u65b9\u6cd5\u53ef\u4ee5\u6740\u6b7b99.9\uff05\u7684\u8010\u7532\u6c27\u897f\u6797\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\uff08MRSA\uff09\uff0c\u8fd8\u80fd\u591f\u52a0\u901f\u611f\u67d3MRSA\u5c0f\u9f20\u76ae\u80a4\u4f24\u53e3\u7684\u6108\u5408\u3002\u8fd9\u9879\u7814\u7a76\u4e3a\u6cbb\u7597\u6297\u836f\u6027\u91d1\u9ec4\u8272\u8461\u8404\u7403\u83cc\u5bfc\u81f4\u7684\u611f\u67d3\u75be\u75c5\uff0c\u63d0\u4f9b\u4e86\u4e00\u79cd\u5d2d\u65b0\u7684\u624b\u6bb5\uff0c\u5b83\u53ea\u5bf9\u8461\u8404\u7403\u83cc\u9ec4\u7d20\u594f\u6548\uff0c\u4e0d\u5bb9\u6613\u8ba9\u7ec6\u83cc\u4ea7\u751f\u8010\u53d7\u6027\uff0c\u6cbb\u7597\u8fc7\u7a0b\u4e5f\u4e0d\u4f1a\u7ed9\u60a3\u8005\u9020\u6210\u521b\u4f24\u3002\u8bba\u6587\u4f5c\u8005Jie Hui\u8868\u793a\uff1a\u201c\u8fd9\u79cd\u6fc0\u5149\u7167\u5c04\u4e0d\u4f1a\u7ed9\u4eba\u4f53\u5e26\u6765\u75db\u89c9\uff0c\u975e\u5e38\u9002\u5408\u4e34\u5e8a\u5e94\u7528\u3002\u201d\u7814\u7a76\u8005\u4eec\u6b63\u5728\u52aa\u529b\u5c06\u84dd\u5149\u7597\u6cd5\u8f6c\u5316\u4e3a\u4e34\u5e8a\uff0c\u5e0c\u671b\u5b83\u53ef\u4ee5\u5e2e\u52a9\u6cbb\u7597\u7cd6\u5c3f\u75c5\u6e83\u75a1\u60a3\u8005 | BU\u53ea\u8981\u8fd9\u4e9b\u91d1\u8272\u7684\u201c\u8461\u8404\u201d\u8fd8\u5728\u5a01\u80c1\u4eba\u4eec\u7684\u5065\u5eb7\uff0c\u79d1\u5b66\u5bb6\u4e0e\u5b83\u4eec\u7684\u8f83\u91cf\u5c31\u4e0d\u4f1a\u505c\u6b65\u3002\u6216\u8bb8\u6709\u4e00\u5929\uff0c\u5b83\u4eec\u5c06\u4e0d\u5f97\u4e0d\u629b\u5f03\u91d1\u707f\u707f\u7684\u5927\u540d\uff0c\u505a\u56de\u4e00\u4e32\u4eba\u755c\u65e0\u5bb3\u7684\u666e\u901a\u201c\u8461\u8404\u201d\u3002(\u7f16\u8f91\uff1aYuki)\u53c2\u8003\u6587\u732e\uff1aDong P T, Mohammad H, Hui J, et al. Photolysis of Staphyloxanthin in Methicillin\u2010Resistant Staphylococcus aureus Potentiates Killing by Reactive Oxygen Species[J]. Advanced Science, 2019: 1900030.\u4f5c\u8005\u540d\u7247", "title": "\u91d1\u8272\u7684\u201c\u8461\u8404\u201d\u4f1a\u81f4\u75c5\uff0c\u4e0d\u5982\u7ed9\u5b83\u6f02\u4e2a\u767d\uff1f", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [], "content": "<p><!--StartFragment--><strong><span style=\"color:#696969\"><span style=\"font-size:14px\">\u672c\u6587\u4e3a2019\u5e744\u670827\u65e5&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u671f&mdash;&mdash;\u5bf9\u8c23\u8a00\u8bf4&ldquo;\u4e0d&rdquo;| \u5b59\u7f8e\u5e73 \u6f14\u8bb2\u5b9e\u5f55\uff1a</span></span></strong><!--EndFragment--></p>\r\n\r\n<p class=\"editors-note\">\u75ab\u82d7\u8d1f\u9762\u4e8b\u4ef6\u7684\u53d1\u751f\u4e25\u91cd\u5730\u5f71\u54cd\u4e86\u516c\u4f17\u5bf9\u4e8e&ldquo;\u9884\u9632\u63a5\u79cd&rdquo;\u7684\u516c\u4fe1\u529b\u3002\u75ab\u82d7\u5b89\u5168\u5417\uff1f\u8fdb\u53e3\u75ab\u82d7\u662f\u4e0d\u662f\u6bd4\u56fd\u4ea7\u75ab\u82d7\u597d\uff1f &ldquo;\u4f60\u53ef\u80fd\u542c\u8fc7\u5f88\u591a\u75ab\u82d7\u8c23\u8a00\uff0c\u4f46\u6211\u8981\u8bf4\uff1a\u63a5\u79cd\u75ab\u82d7\u662f\u9632\u63a7\u4f20\u67d3\u75c5\u6700\u6709\u6548\u7684\u624b\u6bb5\u3002&rdquo;\u5317\u4eac\u5e02\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u4e3b\u4efb\u533b\u5e08\u3001\u4e5d\u4e09\u5b66\u793e\u4e2d\u592e\u533b\u836f\u59d4\u5458\u4f1a\u59d4\u5458\u5b59\u7f8e\u5e73\u8ddf\u5927\u5bb6\u804a\u804a\u75ab\u82d7\u4e0e\u9884\u9632\u63a5\u79cd\u7684\u6545\u4e8b\u3002</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"498\" scrolling=\"no\" src=\"https://v.qq.com/txp/iframe/player.html?vid=p08741ooamk\" width=\"640\"></iframe></p>\r\n\r\n<p>\u5927\u5bb6\u597d\uff0c\u6211\u662f\u5b59\u7f8e\u5e73\uff0c\u6765\u81ea\u5317\u4eac\u5e02\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\uff0c\u4ece\u4e8b\u9884\u9632\u63a5\u79cd\u5de5\u4f5c\u5df2\u7ecf36\u5e74\u4e86\u3002</p>\r\n\r\n<p>\u5173\u4e8e\u75ab\u82d7\uff0c\u5927\u5bb6\u80af\u5b9a\u542c\u5230\u8fc7\u5f88\u591a\u8c23\u4f20&mdash;&mdash;\u5c24\u5176\u662f2010\u5e74\u4ee5\u6765\u3002\u786e\u5b9e\uff0c\u8fd9\u4e9b\u5e74\u6211\u4eec\u56fd\u5bb6\u51fa\u73b0\u4e86\u4e00\u4e9b\u75ab\u82d7\u7684\u8d1f\u9762\u4e8b\u4ef6\uff0c\u4e5f\u6709\u5f88\u591a\u4f20\u8a00\uff0c\u4e25\u91cd\u5730\u5f71\u54cd\u4e86\u516c\u4f17\u5bf9\u4e8e&ldquo;\u9884\u9632\u63a5\u79cd&rdquo;\u7684\u516c\u4fe1\u529b\u3002</p>\r\n\r\n<p>\u6211\u521a\u53c2\u52a0\u5de5\u4f5c\u7684\u65f6\u5019\uff0c\u75ab\u82d7\u63a5\u79cd\u70b9\u90fd\u8bbe\u5728\u5c45\u59d4\u4f1a\u6216\u6751\u59d4\u4f1a\u3002\u6211\u5370\u8c61\u7279\u522b\u6df1\uff0c\u5f53\u65f6\u5728\u5d07\u6587\u533a\u536b\u751f\u9632\u75ab\u7ad9\u5de5\u4f5c\u65f6\uff0c\u968f\u524d\u95e8\u533b\u9662\u7684\u533b\u751f\u5165\u6237\u5230\u4e00\u4e2a\u56db\u5408\u9662\uff0c\u4e00\u8fdb\u53bb\uff0c\u5168\u9662\u7684\u4eba\u90fd\u7279\u522b\u4eb2\u5207\u5730\u8ddf\u5979\u6253\u62db\u547c\u3002\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u9662\u91cc\u7684\u6bcf\u4e2a\u5c0f\u5b69\u90fd\u662f\u5979\u6765\u8fdb\u884c\u9884\u9632\u63a5\u79cd\u7684\u3002</p>\r\n\r\n<p>\u53cd\u89c2\u8fd9\u4e9b\u5e74\u6211\u4eec\u9762\u4e34\u7684\u4e00\u4e9b\u95ee\u9898\uff0c\u662f\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u5a92\u4f53\u53d1\u8fbe\uff0c\u8d1f\u9762\u65b0\u95fb\u7684\u4f20\u64ad\u901f\u5ea6\u8fdc\u9ad8\u4e8e\u6b63\u9762\u65b0\u95fb\u3002</p>\r\n\r\n<p>\u4f46\u6211\u4ecd\u7136\u8981\u8bf4\uff1a\u63a5\u79cd\u75ab\u82d7\u662f\u9884\u9632\u4f20\u67d3\u75c5\u6700\u6709\u6548\u7684\u624b\u6bb5\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/pdqabup0qWuJTVzQgJcgitx8pKge9VU_lfWd8JuQhwY4BAAAXgIAAEpQ.jpg\" style=\"height:359px; width:640px\" /></p>\r\n\r\n<h2>01&mdash;\u7cd6\u4e38\u75ab\u82d7\u53bb\u54ea\u513f\u4e86\uff1f</h2>\r\n\r\n<p>\u6211\u4eec\u7684\u7f16\u8f91\u662f80\u540e\uff0c\u8bf4\uff0c\u5b59\u8001\u5e08\uff0c\u90a3\u4e2a\u7cd6\u4e38\u75ab\u82d7\u6211\u4eec\u73b0\u5728\u8fd8\u6709\u8bb0\u5fc6\u5462\u3002\u5176\u5b9e\u4e0d\u5149\u5979\uff0c\u6211\u662f50\u540e\uff0c\u6211\u4e5f\u8bb0\u5f97\u7cd6\u4e38\u75ab\u82d7\uff0c\u4e5f\u662f\u5403\u7cd6\u4e38\u75ab\u82d7\u957f\u5927\u7684\u3002</p>\r\n\r\n<p>\u5982\u679c\u6ca1\u6709\u7cd6\u4e38\u75ab\u82d7\uff0c\u6211\u4eec\u5f88\u591a\u4eba\u4e0d\u4f1a\u5065\u5eb7\u5730\u5750\u5728\u8fd9\u513f\u3002\u73b0\u572860\u5c81\u4ee5\u4e0b\u7684\u4eba\u4e2d\uff0c\u51e0\u4e4e\u89c1\u4e0d\u5230\u56e0\u4e3a\u5c0f\u513f\u9ebb\u75f9\u5bfc\u81f4\u7684\u8ddb\u884c\uff1b\u4f46\u662f\u5728\u8fc7\u53bb\uff0c\u8fd9\u79cd\u72b6\u51b5\u5e76\u4e0d\u7f55\u89c1\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/adx2NKl9vLx7JOiv68lKf3olfaYV2HqwP80I10EPpPY4BAAAXgIAAEpQ.jpg\" style=\"height:359px; width:640px\" /></p>\r\n\r\n<p>\u8bf4\u5230\u7cd6\u4e38\u75ab\u82d7\uff0c\u6211\u4eec\u4e0d\u5f97\u4e0d\u7f05\u6000\u4e00\u4f4d\u5927\u79d1\u5b66\u5bb6&mdash;&mdash;\u4e2d\u56fd\u533b\u79d1\u9662\u7684\u524d\u9662\u957f\uff0c\u987e\u65b9\u821f\u9662\u957f\u3002\u987e\u8001\u6700\u65e9\u8d74\u82cf\u8054\u53bb\u5b66\u4e60\u76f8\u5173\u6280\u672f\uff0c\u56de\u56fd\u540e\u7814\u53d1\u51fa\u4e86\u4e2d\u56fd\u7684\u810a\u9ad3\u7070\u8d28\u708e\u75ab\u82d7\u3002\u56e0\u4e3a\u513f\u7ae5\u670d\u7528\u810a\u9ad3\u7070\u8d28\u708e\u75ab\u82d7\uff0c\u4e2d\u56fd\u5728\u4e0a\u4e16\u7eaa90\u5e74\u4ee3\u5c31\u5df2\u7ecf\u6d88\u706d\u4e86\u810a\u9ad3\u7070\u8d28\u708e\uff1b\u4e2d\u56fd\u6240\u5728\u7684\u4e9a\u6d32\u592a\u5e73\u6d0b\u5730\u533a\uff0c\u4e5f\u57282000\u5e74\u88ab\u8bc1\u5b9e\u6d88\u706d\u4e86\u810a\u9ad3\u7070\u8d28\u708e\u3002\u6240\u4ee5\u987e\u65b9\u821f\u9662\u957f\u53c8\u88ab\u5927\u5bb6\u4eb2\u5207\u5730\u79f0\u4e3a&ldquo;\u7cd6\u4e38\u75ab\u82d7\u7237\u7237&rdquo;\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/dwtghc1M5zD4ysrf7H8bzWP3s0PYRMOZK61jl1a3KgI4BAAAXgIAAEpQ.jpg\" style=\"height:359px; width:640px\" /></p>\r\n\r\n<p>\u6709\u4eba\u53ef\u80fd\u8bf4\uff0c\u6211\u4eec\u5bb6\u6709\u4e2a\u5c0f\u5b9d\u5b9d\uff0c\u4e0d\u5230\u4e09\u5c81\uff0c\u6211\u600e\u4e48\u6ca1\u542c\u8bf4\u4ed6\u5403\u8fc7&ldquo;\u7cd6\u4e38&rdquo;\u5462\uff1f</p>\r\n\r\n<p>\u56e0\u4e3a\u73b0\u5728\u6ca1\u6709&ldquo;\u7cd6\u4e38&rdquo;\u4e86\u3002</p>\r\n\r\n<p>\u4e3a\u4ec0\u4e48\uff1f</p>\r\n\r\n<p>\u8fd9\u5c31\u8981\u8bf4\u5230\u75ab\u82d7\u7684\u4e0d\u826f\u53cd\u5e94\u3002\u7cd6\u4e38\u75ab\u82d7\u662f\u51cf\u6bd2\u6d3b\u75ab\u82d7\uff0c\u6240\u4ee5\u4e00\u4e9b\u7279\u6b8a\u7684\u5b69\u5b50&mdash;&mdash;\u6bd4\u5982\u6709\u4e25\u91cd\u514d\u75ab\u529f\u80fd\u7f3a\u9677\u7684\u65b0\u751f\u513f&mdash;&mdash;\u5982\u679c\u670d\u7cd6\u4e38\u524d\u6ca1\u6709\u53d1\u73b0\u9633\u6027\u7684\u75c7\u72b6\u4f53\u5f81\uff0c\u53ef\u80fd\u5c31\u670d\u7528\u4e86\uff1b\u670d\u7528\u4e4b\u540e\uff0c\u6781\u4e2a\u522b\u4f1a\u53d1\u751f\u80a2\u4f53\u7684\u9ebb\u75f9\u3002\u7528\u5bb6\u957f\u7684\u8bdd\u8bf4\uff0c\u6211\u7684\u5b69\u5b50&ldquo;\u4e0d\u5403\u7cd6\u4e38\u4e0d\u5f97\u75c5\uff0c\u5403\u4e86\u7cd6\u4e38\u9ebb\u75f9\u4e86&rdquo;\u3002</p>\r\n\r\n<p>\u901a\u8fc7\u5168\u4e16\u754c\u5927\u91cf\u7684\u6570\u636e\u89c2\u5bdf\uff0c\u53d1\u73b0&ldquo;\u4e0e\u75ab\u82d7\u76f8\u5173\u7684\u810a\u9ad3\u7070\u8d28\u708e\u9ebb\u75f9\u75c5\u4f8b&rdquo;\u7684\u53d1\u751f\u7387\u662f\u767e\u4e07\u5206\u4e4b\u4e00\uff0c\u5176\u5b9e\u5f88\u4f4e\u3002\u4f46\u5373\u4f7f\u8fd9\u6837\uff0c\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\uff08WHO\uff09\u8981\u6c42\uff0c2016\u5e745\u67081\u65e5\u5f00\u59cb\uff0c\u5168\u7403\u6240\u6709\u513f\u7ae5\u7b2c\u4e00\u5242\u4e0d\u518d\u670d\u7528\u810a\u9ad3\u7070\u8d28\u708e\u51cf\u6bd2\u6d3b\u75ab\u82d7\uff0c\u53ea\u80fd\u63a5\u79cd\u810a\u9ad3\u7070\u8d28\u708e\u706d\u6d3b\u75ab\u82d7\uff1b\u7b2c\u4e8c\u3001\u4e09\u5242\u4ecd\u7136\u53e3\u670d\u810a\u9ad3\u7070\u8d28\u708e\u51cf\u6bd2\u6d3b\u75ab\u82d7\uff0c\u4f46\u662f\u5242\u578b\u53d8\u4e86\uff0c\u53d8\u6210\u4e86\u6db2\u4f53\u7684\u6ef4\u5242\uff0c\u4e0d\u662f\u56fa\u4f53\u7684\u767d\u8272\u7cd6\u4e38\u3002\u6240\u4ee5\u73b0\u5728\u5e26\u4e09\u5c81\u4ee5\u4e0b\u7684\u5c0f\u5b69\u5230\u9884\u9632\u63a5\u79cd\u95e8\u8bca\u53bb\uff0c\u4f1a\u53d1\u73b0\u533b\u751f\u62ff\u4e00\u4e2a\u5851\u6599\u7684\u3001\u8f6f\u7684\u5bb9\u5668\uff08\u76db\u6709\u75ab\u82d7\uff09\uff0c\u5f80\u5b69\u5b50\u53e3\u4e2d\u6ef4\u4e24\u6ef4\u3002\u8fd9\u5c31\u662f&ldquo;\u7cd6\u4e38\u53d8\u8fc1&rdquo;\u7684\u6545\u4e8b\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/jtoYSlNEJ_U7zeCOxowRI4Mvhud5g1M2tNt4LqJOLS44BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<h2>02&mdash;\u75ab\u82d7\u5b89\u5168\u5417\uff1f</h2>\r\n\r\n<p>\u90a3\u75ab\u82d7\u5b89\u5168\u5417\uff1f</p>\r\n\r\n<p>\u6bcf\u4e00\u6b21\u75ab\u82d7\u7684\u8d1f\u9762\u4e8b\u4ef6\uff0c\u90fd\u4f1a\u5f15\u8d77\u516c\u4f17\u5bf9&ldquo;\u75ab\u82d7\u5b89\u5168&rdquo;\u7684\u6050\u614c\uff0c\u6bd4\u5982\u53bb\u5e74\u53d1\u751f\u7684&ldquo;\u957f\u6625\u957f\u751f\u75ab\u82d7\u4e8b\u4ef6&rdquo;&mdash;&mdash;\u8fd9\u662f\u4e2a\u7279\u4f8b\uff1a\u6240\u6d89\u53ca\u7684\u516c\u53f8\u662f\u4e0a\u5e02\u516c\u53f8\uff0c\u56fd\u5bb6\u5df2\u7ecf\u53d6\u6d88\u4e86\u5b83\u7684\u8d44\u8d28\uff0c\u800c\u4e14\u4fc3\u6210\u56fd\u5bb6\u62df\u5c06\u51fa\u53f0\u300a\u4e2d\u534e\u4eba\u6c11\u5171\u548c\u56fd\u75ab\u82d7\u7ba1\u7406\u6cd5\u300b\u3002\u5927\u5bb6\u4e0d\u8981\u56e0\u4e3a\u4e2a\u6848\uff0c\u5931\u53bb\u5bf9\u6211\u4eec\u56fd\u5bb6\u75ab\u82d7\u7684\u4fe1\u5fc3\u3002</p>\r\n\r\n<p>\u6211\u4eec\u56fd\u5bb6\u5bf9\u75ab\u82d7\u6267\u884c\u4e24\u4e2a\u975e\u5e38\u4e25\u683c\u7684\u6807\u51c6\uff1a\u4e00\u4e2a\u662f\u56fd\u5bb6\u836f\u5178\u6807\u51c6\uff0c\u53e6\u4e00\u4e2a\u662f\u75ab\u82d7\u7684\u6ce8\u518c\u6807\u51c6\uff08\u6bcf\u652f\u75ab\u82d7\u90fd\u6709\u552f\u4e00\u7684\u6ce8\u518c\u53f7\uff09\u3002\u56fd\u5bb6\u836f\u5178\u6bcf\u4e94\u5e74\u5347\u7ea7\u4e00\u7248\uff0c\u6bcf\u5347\u7ea7\u4e00\u7248\uff0c\u75ab\u82d7\u7684\u751f\u4ea7\u6807\u51c6\u5c31\u4f1a\u66f4\u52a0\u4e25\u683c\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/2lkohQD6J2aF7_UlpirfAcPDKCv_M48kqP54j831Tsk4BAAAtwIAAEpQ.jpg\" style=\"height:412px; width:640px\" />\u6f14\u8bb2\u5609\u5bbe\u5b59\u7f8e\u5e73\uff1a\u300a\u4f60\u53ef\u80fd\u542c\u8fc7\u5f88\u591a\u75ab\u82d7\u8c23\u8a00\uff0c\u4f46\u6211\u8981\u8bf4\uff1a\u63a5\u79cd\u75ab\u82d7\u662f\u9632\u63a7\u4f20\u67d3\u75c5\u6700\u6709\u6548\u7684\u624b\u6bb5\u300b</p>\r\n\r\n<h2>03&mdash;\u8fdb\u53e3\u75ab\u82d7\u662f\u4e0d\u662f\u6bd4\u56fd\u4ea7\u75ab\u82d7\u597d\uff1f</h2>\r\n\r\n<p>\u6211\u4eec\u7ecf\u5e38\u4f1a\u542c\u5230\u8fd9\u7c7b\u95ee\u9898\uff1a\u8fdb\u53e3\u75ab\u82d7\u662f\u4e0d\u662f\u6bd4\u56fd\u4ea7\u75ab\u82d7\u597d\uff1f</p>\r\n\r\n<p>\u4e0d\u4e00\u5b9a\u3002</p>\r\n\r\n<p>\u6211\u56fd2010\u5e74\u7248\u836f\u5178\u51fa\u53f0\u4e4b\u540e\uff0c\u6240\u6709\u8fdb\u53e3\u7684\u51cf\u6bd2\u6d3b\u75ab\u82d7\u90fd\u9646\u7eed\u9000\u51fa\u4e86\u4e2d\u56fd\u5e02\u573a\u3002\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u8fd9\u7c7b\u75ab\u82d7\u4e2d\u7684\u6297\u751f\u7d20\u7684\u6b8b\u7559\u91cf\u8d85\u8fc7\u4e86\u65b0\u7248\u6807\u51c6\u3002\u6b64\u5916\uff0c\u8fd8\u6709\u51e0\u79cd\u75ab\u82d7\u4e5f\u9000\u51fa\u4e86\u4e2d\u56fd\u5e02\u573a\uff1a\u5982\u4e00\u79cd\u5168\u7403\u77e5\u540d\u7684\u72c2\u72ac\u75c5\u75ab\u82d7\uff0c\u56e0\u7ec6\u80de\u4e2dDNA\u7684\u6b8b\u7559\u91cf\u8d85\u6807\uff1b\u4e00\u79cd\u65e0\u7ec6\u80de\u767e\u767d\u7834\u75ab\u82d7\uff0c\u56e0\u5438\u9644\u5242\u7684\u542b\u91cf\u8d85\u6807\u3002\u6240\u4ee5\uff0c\u4e0d\u80fd\u7b80\u5355\u5730\u8ba4\u4e3a\u8fdb\u53e3\u7684\u75ab\u82d7\u5c31\u6bd4\u56fd\u4ea7\u7684\u75ab\u82d7\u597d\u3002</p>\r\n\r\n<p>\u6211\u56fd\u75ab\u82d7\u603b\u4f53\u7684\u76d1\u7ba1\u6c34\u5e73\u5df2\u7ecf\u8fbe\u5230\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\u6807\u51c6\uff0c\u5e76\u4e8e2011\u5e74\u30012014\u5e74\u4e24\u6b21\u901a\u8fc7\u4e86\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\u7684\u75ab\u82d7\u7ba1\u7406\u9a8c\u8bc1\u3002\u6b64\u5916\uff0c\u6211\u56fd\u5df2\u7ecf\u83b7\u5f97\u4e86\u51e0\u79cd\u75ab\u82d7\u7684\u56fd\u9645\u91c7\u8d2d\u8d44\u8d28\uff0c\u53c2\u4e0e\u5230\u56fd\u9645\u7684\u75ab\u82d7\u62db\u6807\u91c7\u8d2d\u4e2d\uff0c\u6240\u4ee5\u5927\u5bb6\u4e00\u5b9a\u8981\u5bf9\u4e2d\u56fd\u7684\u75ab\u82d7\u6709\u4fe1\u5fc3\u3002</p>\r\n\r\n<h2>04&mdash;\u63a5\u79cd\u75ab\u82d7\u5bfc\u81f4\u5b64\u72ec\u75c7\uff1f</h2>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/MNblzlskZSJyMw1CHRpqgSH48SzQepAYQpw6tfeMWks4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u53e6\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u56fd\u9645\u4e0a\u5f88\u6709\u540d\u7684\u8c23\u8a00\uff1a\u63a5\u79cd\u9ebb\u98ce\u816e\u75ab\u82d7\u53ef\u80fd\u5bfc\u81f4\u513f\u7ae5\u5f97\u5b64\u72ec\u75c7\u3002\u4e3a\u4ec0\u4e48\u5f97\u5b64\u72ec\u75c7\u4f1a\u5f15\u8d77\u5173\u6ce8\u5462\uff1f\u56e0\u4e3a\u8fd1\u5341\u5e74\u6765\uff0c\u6839\u636e\u56fd\u9645\u4e0a\u5c24\u5176\u7f8e\u56fd\u7684\u7edf\u8ba1\uff0c\u5b64\u72ec\u75c7\u53d1\u75c5\u7387\u5feb\u901f\u4e0a\u5347\uff0c\u6211\u4eec\u56fd\u5bb6\u5b64\u72ec\u75c7\u513f\u7ae5\u4e5f\u8d8a\u6765\u8d8a\u591a\uff0c\u6240\u4ee5\u5927\u5bb6\u975e\u5e38\u5173\u6ce8\u3002\u76ee\u524d\uff0c\u5b64\u72ec\u75c7\u4ecd\u7136\u539f\u56e0\u4e0d\u660e\uff0c\u901a\u5e38\u5728\u5e7c\u513f\u65f6\u671f\u88ab\u8bca\u65ad\uff0c\u662f\u4e00\u4e2a\u5e7f\u6cdb\u7684\u3001\u53d1\u80b2\u8fdf\u7f13\u6027\u7684\u75be\u75c5\uff0c\u800c\u4e14\u8868\u73b0\u4e3a\u591a\u79cd\u3001\u4e00\u7cfb\u5217\u7684\u75c7\u72b6\u4f53\u5f81\u3002</p>\r\n\r\n<p>\u8fd9\u4e2a\u8c23\u8a00\u662f\u600e\u4e48\u6765\u7684\uff1f</p>\r\n\r\n<p>1998\u5e74\uff0c\u4e00\u4f4d\u82f1\u56fd\u533b\u751f\u548c\u4ed6\u768412\u4f4d\u540c\u4e8b\uff0c\u5728\u4e16\u754c\u4e0a\u975e\u5e38\u6709\u540d\u7684\u300a\u67f3\u53f6\u5200\uff08The Lancet\uff09\u300b\u671f\u520a\u4e0a\u53d1\u8868\u4e86\u4e00\u7bc7\u6587\u7ae0\u3002\u4ed6\u4eec\u89c2\u5bdf\u4e8612\u540d\u5b64\u72ec\u75c7\u513f\u7ae5\uff0c\u7ed3\u8bba\u662f12\u4f8b\u4e2d\uff0c\u67098\u4f8b\u662f\u6253\u4e86\u9ebb\u98ce\u816e\u75ab\u82d7\u4e4b\u540e\u5f97\u7684\u3002</p>\r\n\r\n<p>\u8fd9\u5728\u5168\u7403\u5f15\u8d77\u4e86\u8f69\u7136\u5927\u6ce2\uff0c\u6b27\u7f8e\u5f88\u591a\u6743\u5a01\u673a\u6784\u5c31\u4e8c\u8005\u76f8\u5173\u6027\u8fdb\u884c\u4e86\u5927\u91cf\u7684\u8c03\u67e5\u548c\u79d1\u5b66\u7814\u7a76\uff0c\u7ed3\u679c\u8bc1\u5b9e\uff1a\u7ed3\u8bba\u9519\u8bef\u3002\u4e3a\u6b64\uff0c\u300a\u67f3\u53f6\u5200\u300b\u57282010\u5e745\u6708\u64a4\u9500\u4e86\u8fd9\u7bc7\u8bba\u6587\uff0c\u800c\u4e14\u8fd9\u4f4d\u533b\u751f\u768412\u4f4d\u540c\u4e8b\u4e2d\u670910\u4f4d\u4e5f\u63d0\u51fa\u4e86\u64a4\u7a3f\u7533\u8bf7\uff0c\u4f46\u662f\u5230\u73b0\u5728\uff0c\u8fd9\u5219\u8c23\u8a00\u7684\u8d1f\u9762\u5f71\u54cd\u4ecd\u7136\u5b58\u5728\u3002</p>\r\n\r\n<p>\u9ebb\u98ce\u816e\u75ab\u82d7\u662f\u80fd\u9884\u9632\u9ebb\u75b9\u3001\u98ce\u75b9\u548c\u816e\u817a\u708e\u7684\u75ab\u82d7\u3002\u4ee5\u9ebb\u75b9\u4e3a\u4f8b\uff0c\u6700\u8fd1\u5a92\u4f53\u7ecf\u5e38\u62a5\u9053\u7f8e\u56fd\u9ebb\u75b9\u53d1\u75c5\u7387\u4e0a\u5347\uff1b\u5728\u975e\u6d32\u4e00\u4e2a\u56fd\u5bb6\uff0c\u9ebb\u75b9\u4f7f\u5f971500\u591a\u4f4d\u513f\u7ae5\u6b7b\u4ea1\u3002\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\u4e5f\u62a5\u9053\uff0c\u4eca\u5e74\u5168\u7403\u9ebb\u75b9\u53d1\u75c5\u7387\u5448\u73b0\u4e0a\u5347\u3002\u4f46\u6211\u4eec\u56fd\u5bb6\uff0c\u9ebb\u75b9\u53d1\u75c5\u7387\u6700\u9ad8\u7684\u4e00\u5e74\u662f60\u5e74\u524d\u76841959\u5e74\uff0c\u5f53\u65f6\u5168\u56fd\u7684\u4eba\u53e3\u53ea\u67096.5\u4ebf\u591a\uff0c\u9ebb\u75b9\u75c5\u4f8b\u662f900\u4e07\u4f8b&mdash;&mdash;\u4eca\u5929\u6211\u4eec\u5df2\u7ecf13\u591a\u4ebf\u4eba\u4e86\uff0c\u5982\u679c\u6ca1\u6709\u9ebb\u75b9\u75ab\u82d7\uff0c\u90a3\u5168\u56fd\u75c5\u4f8b\u5c06\u662f1800-1900\u4e07\u4f8b\u3002\u6240\u4ee5\uff0c\u4e0d\u80fd\u8f7b\u6613\u5730\u76f8\u4fe1\u8fd9\u79cd\u4f20\u8a00\u3002</p>\r\n\r\n<h2>05&mdash;\u9632\u63a7\u4f20\u67d3\u75c5\uff0c\u63a5\u79cd\u75ab\u82d7\u6700\u6709\u6548</h2>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/nn45T_L1V31_0x5gz-7NLqAr7j3dAlp9G0kYSbHl8NI4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8fd9\u4e5f\u6d89\u53ca\u5230\u4e00\u79cd\u7279\u522b\u7684\u73b0\u8c61\uff0c\u6709\u4e00\u4e2a\u65b0\u7684\u540d\u8bcd\u53eb&ldquo;\u75ab\u82d7\u72b9\u8c6b&rdquo;\u3002\u4ec0\u4e48\u662f&ldquo;\u75ab\u82d7\u72b9\u8c6b&rdquo;\uff1f\u5c31\u662f\uff0c\u6bcf\u5f53\u542c\u5230\u5173\u4e8e\u75ab\u82d7\u7684\u8f83\u5927\u8d1f\u9762\u65b0\u95fb\u7684\u65f6\u5019\uff0c\u5bb6\u957f\u5c31\u4e0d\u518d\u7ed9\u513f\u7ae5\u63a5\u79cd\u75ab\u82d7\uff0c\u6216\u8005\u5c31\u6000\u7591\u75ab\u82d7\u662f\u5426\u5b89\u5168&mdash;&mdash;\u8981\u7b49\u771f\u76f8\u51fa\u6765\u540e\uff0c\u786e\u8ba4\u75ab\u82d7\u5b89\u5168\uff0c\u518d\u5e26\u5b69\u5b50\u63a5\u79cd\u3002\u4f46\u662f\u75ab\u82d7\u9884\u9632\u7684\u5f88\u591a\u4f20\u67d3\u75c5\u662f\u6709\u5b63\u8282\u6027\u7684\uff0c\u5728\u7b49\u5f85\u7684\u8fc7\u7a0b\u4e2d\u5c31\u4f1a\u66b4\u9732\u5728\u75c5\u539f\u4e0b\uff0c\u6709\u53ef\u80fd\u5c31\u88ab\u611f\u67d3\u5f97\u75c5\u3002\u6240\u4ee5 &ldquo;\u75ab\u82d7\u72b9\u8c6b&rdquo;\u662f\u4e0d\u53ef\u53d6\u7684\uff0c\u53ef\u80fd\u9519\u5931\u63a5\u79cd\u7684\u673a\u4f1a\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/3oDwzg2Fap-6p9qdvfNYg8BFplOgLROOzSeOteqBiqE4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4eca\u5e74\u76844\u670825\u65e5\u662f\u5168\u56fd\u7b2c33\u4e2a\u5168\u56fd\u513f\u7ae5\u9884\u9632\u63a5\u79cd\u5ba3\u4f20\u65e5\uff0c\u4e3b\u9898\u662f&ldquo;\u9632\u63a7\u4f20\u67d3\u75c5\uff0c\u63a5\u79cd\u75ab\u82d7\u6700\u6709\u6548&rdquo;\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/3ev6JHUCrdgk0OQWtYQ1LeYvajhbgjFyOnr3LwP68II4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8fc7\u53bb\uff0c\u65b0\u751f\u513f\u51fa\u751f\u63a5\u79cd\u7684\u7b2c\u4e00\u9488\u75ab\u82d7\u662f\u5361\u4ecb\u82d7\uff08\u63a5\u79cd\u540e\u53ef\u9884\u9632\u513f\u7ae5\u7ed3\u6838\u75c5\uff09\u30021992\u5e74\uff0c\u6211\u56fd\u628a\u4e59\u809d\u75ab\u82d7\u7eb3\u5165\u4e86\u8ba1\u5212\u514d\u75ab\u7ba1\u7406\uff0c2002\u5e74\uff0c\u628a\u4e59\u809d\u75ab\u82d7\u5b8c\u5168\u514d\u8d39\u63d0\u4f9b\u7ed9\u513f\u7ae5\u63a5\u79cd\u3002\u6240\u4ee5\uff0c\u4ece\u90a3\u4ee5\u540e\uff0c\u513f\u7ae5\u51fa\u751f\u63a5\u79cd\u7684\u7b2c\u4e00\u652f\u75ab\u82d7\u5c31\u662f\u5361\u4ecb\u75ab\u82d7\u548c\u4e59\u809d\u75ab\u82d7\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/kmwGEfHk6utZqq1o9JO6oHTD-k1-XguS5IPeCtpnyF44BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u73b0\u5728\u513f\u7ae5\u51fa\u751f24\u5c0f\u65f6\u4e4b\u5185\u5c31\u8981\u63a5\u79cd\u8fd9\u4e24\u79cd\u75ab\u82d7\u3002\u90a3\u8fd9\u4e24\u9488\u75ab\u82d7\u8c01\u7b2c\u4e00\u3001\u8c01\u7b2c\u4e8c\u5462\uff1f\u539f\u5219\u4e0a\u53ef\u4ee5\u540c\u65f6\u63a5\u79cd\uff1a\u4e00\u652f\u63a5\u79cd\u5de6\u4fa7\u80f3\u818a\uff0c\u4e00\u652f\u63a5\u79cd\u53f3\u4fa7\u80f3\u818a\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/zAa4DT3CPUIrAxxRYm3cD1vnbiFwu9gm-5s9Jd_fd3k4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4e3a\u4ec0\u4e48\u6211\u56fd\u8981\u628a\u4e59\u809d\u75ab\u82d7\u7eb3\u5165\u514d\u75ab\u89c4\u5212\uff1f\u56e0\u4e3a\u6211\u56fd\u8fc7\u53bb\u662f\u4e59\u809d\u5927\u56fd\u3002\u5927\u5bb6\u770b\u8fd9\u5f20\u56fe\uff0c\u989c\u8272\u8d8a\u6df1\u4ee3\u8868\u53d1\u75c5\u7387\u8d8a\u9ad8\uff0c\u4e2d\u56fd\u5728\u4e00\u4e2a\u6700\u6df1\u84dd\u7684\u533a\u57df\u5185\uff0c\u5f53\u65f6\u6211\u4eec\u56fd\u5bb6\u611f\u67d3\u4e59\u809d\u7684\u4eba\u53ef\u4ee5\u4ee5\u4ebf\u4e3a\u5355\u4f4d\u8ba1\u7b97\u3002</p>\r\n\r\n<p>\u5f97\u4e59\u809d\u7684\u7ed3\u679c\u4f1a\u662f\u4ec0\u4e48\u6837\u5462\uff1f\u5982\u679c\u611f\u67d3\u4e86\u4e59\u809d\u75c5\u6bd2\uff0c\u53ef\u80fd\u4f1a\u53d1\u5c55\u6210\u4e3a\u4e59\u809d\u75c5\u4eba\uff1b\u4e59\u809d\u75c5\u4eba\u6ca1\u6709\u6cbb\u6108\uff0c\u5c31\u53d1\u5c55\u6210\u6162\u6027\u4e59\u809d\u75c5\u4eba\uff1b\u6162\u6027\u4e59\u809d\u7ecf\u8fc7\u957f\u671f\u53d1\u5c55\uff0c\u5c31\u53d8\u6210\u809d\u786c\u5316\uff1b\u809d\u786c\u5316\u518d\u7ecf\u8fc7\u957f\u671f\u53d1\u5c55\uff0c\u5c31\u53d8\u6210\u809d\u764c\uff0c</p>\r\n\r\n<p>\u6211\u8bb0\u5f97\u53bb\u5e74\u4eb2\u5386\u7684\u4e00\u4ef6\u4e8b\uff1a\u4e00\u4e2a\u5c71\u4e1c\u7684\u519c\u6c11\u4f01\u4e1a\u5bb6\u5f97\u4e86\u809d\u764c\uff0c\u5f53\u5730\u5df2\u7ecf\u6cbb\u4e0d\u4e86\u4e86\uff0c\u4ed6\u5c31\u8ddf\u6211\u8054\u7cfb\uff0c\u95ee\u6211\u80fd\u4e0d\u80fd\u5e2e\u5fd9\u5728\u5317\u4eac\u627e\u4e00\u4e2a\u533b\u9662\u53bb\u6cbb\u7597\u3002\u540e\u6765\u6211\u8054\u7cfb\u597d\u4e13\u5bb6\uff0c\u7b2c\u4e8c\u5929\u65e9\u6668\u7ed9\u4ed6\u5bb6\u4eba\u6253\u7535\u8bdd\uff0c\u4ed6\u5bb6\u4eba\u8bf4\u4ed6\u5df2\u7ecf\u809d\u660f\u8ff7\u4e86\u3002\u73b0\u5728\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u4e0d\u5728\u4e86\uff0c\u8fd9\u5c31\u662f\u611f\u67d3\u4e59\u809d\u7684\u7ed3\u5c40\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/M7nSaixrmEWNxFOtHkmtwIMHajn9L_YTtKN4nvkUwfU4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u56fd\u73b0\u5728\u91cd\u5927\u4f20\u67d3\u75c5\u7684\u9632\u63a7\uff0c\u5176\u4e2d\u5c31\u6709\u4e00\u4e2a\u4e59\u809d\u7684&ldquo;\u964d\u4e24\u7387&rdquo;\uff0c\u5373\u964d\u53d1\u75c5\u7387\u548c\u964d\u75c5\u6b7b\u7387\u3002</p>\r\n\r\n<p>\u6211\u662f1983\u5e74\u53c2\u52a0\u5de5\u4f5c\u7684\uff0c\u90a3\u65f6\u5019\u4e59\u809d\u75ab\u82d7\u8fd8\u6ca1\u6709\u95ee\u4e16\uff1b\u7ecf\u8fc7\u51e0\u5341\u5e74\u7684\u52aa\u529b\uff0c2014\u5e74\uff0c\u6211\u56fd\u7684\u513f\u7ae5\u4e59\u809d\u63a7\u5236\u53d7\u5230\u4e86\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\u7684\u8868\u5f70\u3002\u7528\u6570\u636e\u4e0a\u6765\u8bf4\uff0c\u6ca1\u6709\u4e59\u809d\u75ab\u82d7\u4e4b\u524d\uff0c\u6211\u56fd\u513f\u7ae5\u7684\u4e59\u809d\u611f\u67d3\u7387\u662f10%\u5de6\u53f3\uff0c\u73b0\u5728\u5df2\u7ecf\u964d\u52301%\u4ee5\u4e0b\u4e86\uff0c\u8bf4\u660e\u4e59\u809d\u75ab\u82d7\u5728\u4e59\u809d\u63a7\u5236\u65b9\u9762\u53d1\u6325\u4e86\u5de8\u5927\u4f5c\u7528\u3002</p>\r\n\r\n<p>\u6240\u4ee5\uff0c\u8981\u7528\u6b63\u786e\u7684\u75be\u75c5\u9884\u9632\u77e5\u8bc6\u6765\u6b66\u88c5\u81ea\u5df1\u3002\u6211\u4eca\u5929\u4e3e\u4e86\u810a\u9ad3\u7070\u8d28\u708e\u75ab\u82d7\u548c\u4e59\u578b\u809d\u708e\u75ab\u82d7\u4e24\u4e2a\u4f8b\u5b50\uff0c\u5e0c\u671b\u80fd\u5f3a\u5316&ldquo;\u9632\u63a7\u4f20\u67d3\u75c5\uff0c\u63a5\u79cd\u9884\u9632\u6700\u6709\u6548&rdquo;\u7684\u7406\u5ff5\uff0c\u8c22\u8c22\u5927\u5bb6\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/zCOxPSWgCnGctHA-U3kms_6ne6yeQ-d3jnjVqhePPCc4BAAArAIAAEpQ.jpg\" style=\"height:405px; width:640px\" />\u6f14\u8bb2\u5609\u5bbe\u5b59\u7f8e\u5e73\uff1a\u300a\u4f60\u53ef\u80fd\u542c\u8fc7\u5f88\u591a\u75ab\u82d7\u8c23\u8a00\uff0c\u4f46\u6211\u8981\u8bf4\uff1a\u63a5\u79cd\u75ab\u82d7\u662f\u9632\u63a7\u4f20\u67d3\u75c5\u6700\u6709\u6548\u7684\u624b\u6bb5\u300b</p>", "date_created": "2019-05-29T10:26:36.171668+08:00", "date_modified": "2019-05-29T10:46:06.972151+08:00", "date_published": "2019-05-29T10:45:35+08:00", "id": 452128, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "health", "replies_count": 3, "small_image": "https://1-im.guokr.com/RcphC21jiXfWmReCNplNlNTAXGgLOfIWi3O5EejCfbM4BAAAtwIAAEpQ.jpg", "subject": {"articles_count": 1946, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "medicine", "minisite_key": "health", "name": "\u533b\u5b66", "sort_score": 5, "subject_type": "normal"}, "subject_key": "medicine", "summary": "\u201c\u9632\u63a7\u4f20\u67d3\u75c5\uff0c\u63a5\u79cd\u9884\u9632\u6700\u6709\u6548\u201d", "text": "\u672c\u6587\u4e3a2019\u5e744\u670827\u65e5\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u671f\u2014\u2014\u5bf9\u8c23\u8a00\u8bf4\u201c\u4e0d\u201d| \u5b59\u7f8e\u5e73 \u6f14\u8bb2\u5b9e\u5f55\uff1a\u75ab\u82d7\u8d1f\u9762\u4e8b\u4ef6\u7684\u53d1\u751f\u4e25\u91cd\u5730\u5f71\u54cd\u4e86\u516c\u4f17\u5bf9\u4e8e\u201c\u9884\u9632\u63a5\u79cd\u201d\u7684\u516c\u4fe1\u529b\u3002\u75ab\u82d7\u5b89\u5168\u5417\uff1f\u8fdb\u53e3\u75ab\u82d7\u662f\u4e0d\u662f\u6bd4\u56fd\u4ea7\u75ab\u82d7\u597d\uff1f \u201c\u4f60\u53ef\u80fd\u542c\u8fc7\u5f88\u591a\u75ab\u82d7\u8c23\u8a00\uff0c\u4f46\u6211\u8981\u8bf4\uff1a\u63a5\u79cd\u75ab\u82d7\u662f\u9632\u63a7\u4f20\u67d3\u75c5\u6700\u6709\u6548\u7684\u624b\u6bb5\u3002\u201d\u5317\u4eac\u5e02\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u4e3b\u4efb\u533b\u5e08\u3001\u4e5d\u4e09\u5b66\u793e\u4e2d\u592e\u533b\u836f\u59d4\u5458\u4f1a\u59d4\u5458\u5b59\u7f8e\u5e73\u8ddf\u5927\u5bb6\u804a\u804a\u75ab\u82d7\u4e0e\u9884\u9632\u63a5\u79cd\u7684\u6545\u4e8b\u3002\u5927\u5bb6\u597d\uff0c\u6211\u662f\u5b59\u7f8e\u5e73\uff0c\u6765\u81ea\u5317\u4eac\u5e02\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\uff0c\u4ece\u4e8b\u9884\u9632\u63a5\u79cd\u5de5\u4f5c\u5df2\u7ecf36\u5e74\u4e86\u3002\u5173\u4e8e\u75ab\u82d7\uff0c\u5927\u5bb6\u80af\u5b9a\u542c\u5230\u8fc7\u5f88\u591a\u8c23\u4f20\u2014\u2014\u5c24\u5176\u662f2010\u5e74\u4ee5\u6765\u3002\u786e\u5b9e\uff0c\u8fd9\u4e9b\u5e74\u6211\u4eec\u56fd\u5bb6\u51fa\u73b0\u4e86\u4e00\u4e9b\u75ab\u82d7\u7684\u8d1f\u9762\u4e8b\u4ef6\uff0c\u4e5f\u6709\u5f88\u591a\u4f20\u8a00\uff0c\u4e25\u91cd\u5730\u5f71\u54cd\u4e86\u516c\u4f17\u5bf9\u4e8e\u201c\u9884\u9632\u63a5\u79cd\u201d\u7684\u516c\u4fe1\u529b\u3002\u6211\u521a\u53c2\u52a0\u5de5\u4f5c\u7684\u65f6\u5019\uff0c\u75ab\u82d7\u63a5\u79cd\u70b9\u90fd\u8bbe\u5728\u5c45\u59d4\u4f1a\u6216\u6751\u59d4\u4f1a\u3002\u6211\u5370\u8c61\u7279\u522b\u6df1\uff0c\u5f53\u65f6\u5728\u5d07\u6587\u533a\u536b\u751f\u9632\u75ab\u7ad9\u5de5\u4f5c\u65f6\uff0c\u968f\u524d\u95e8\u533b\u9662\u7684\u533b\u751f\u5165\u6237\u5230\u4e00\u4e2a\u56db\u5408\u9662\uff0c\u4e00\u8fdb\u53bb\uff0c\u5168\u9662\u7684\u4eba\u90fd\u7279\u522b\u4eb2\u5207\u5730\u8ddf\u5979\u6253\u62db\u547c\u3002\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u9662\u91cc\u7684\u6bcf\u4e2a\u5c0f\u5b69\u90fd\u662f\u5979\u6765\u8fdb\u884c\u9884\u9632\u63a5\u79cd\u7684\u3002\u53cd\u89c2\u8fd9\u4e9b\u5e74\u6211\u4eec\u9762\u4e34\u7684\u4e00\u4e9b\u95ee\u9898\uff0c\u662f\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u5a92\u4f53\u53d1\u8fbe\uff0c\u8d1f\u9762\u65b0\u95fb\u7684\u4f20\u64ad\u901f\u5ea6\u8fdc\u9ad8\u4e8e\u6b63\u9762\u65b0\u95fb\u3002\u4f46\u6211\u4ecd\u7136\u8981\u8bf4\uff1a\u63a5\u79cd\u75ab\u82d7\u662f\u9884\u9632\u4f20\u67d3\u75c5\u6700\u6709\u6548\u7684\u624b\u6bb5\u300201\u2014\u7cd6\u4e38\u75ab\u82d7\u53bb\u54ea\u513f\u4e86\uff1f\u6211\u4eec\u7684\u7f16\u8f91\u662f80\u540e\uff0c\u8bf4\uff0c\u5b59\u8001\u5e08\uff0c\u90a3\u4e2a\u7cd6\u4e38\u75ab\u82d7\u6211\u4eec\u73b0\u5728\u8fd8\u6709\u8bb0\u5fc6\u5462\u3002\u5176\u5b9e\u4e0d\u5149\u5979\uff0c\u6211\u662f50\u540e\uff0c\u6211\u4e5f\u8bb0\u5f97\u7cd6\u4e38\u75ab\u82d7\uff0c\u4e5f\u662f\u5403\u7cd6\u4e38\u75ab\u82d7\u957f\u5927\u7684\u3002\u5982\u679c\u6ca1\u6709\u7cd6\u4e38\u75ab\u82d7\uff0c\u6211\u4eec\u5f88\u591a\u4eba\u4e0d\u4f1a\u5065\u5eb7\u5730\u5750\u5728\u8fd9\u513f\u3002\u73b0\u572860\u5c81\u4ee5\u4e0b\u7684\u4eba\u4e2d\uff0c\u51e0\u4e4e\u89c1\u4e0d\u5230\u56e0\u4e3a\u5c0f\u513f\u9ebb\u75f9\u5bfc\u81f4\u7684\u8ddb\u884c\uff1b\u4f46\u662f\u5728\u8fc7\u53bb\uff0c\u8fd9\u79cd\u72b6\u51b5\u5e76\u4e0d\u7f55\u89c1\u3002\u8bf4\u5230\u7cd6\u4e38\u75ab\u82d7\uff0c\u6211\u4eec\u4e0d\u5f97\u4e0d\u7f05\u6000\u4e00\u4f4d\u5927\u79d1\u5b66\u5bb6\u2014\u2014\u4e2d\u56fd\u533b\u79d1\u9662\u7684\u524d\u9662\u957f\uff0c\u987e\u65b9\u821f\u9662\u957f\u3002\u987e\u8001\u6700\u65e9\u8d74\u82cf\u8054\u53bb\u5b66\u4e60\u76f8\u5173\u6280\u672f\uff0c\u56de\u56fd\u540e\u7814\u53d1\u51fa\u4e86\u4e2d\u56fd\u7684\u810a\u9ad3\u7070\u8d28\u708e\u75ab\u82d7\u3002\u56e0\u4e3a\u513f\u7ae5\u670d\u7528\u810a\u9ad3\u7070\u8d28\u708e\u75ab\u82d7\uff0c\u4e2d\u56fd\u5728\u4e0a\u4e16\u7eaa90\u5e74\u4ee3\u5c31\u5df2\u7ecf\u6d88\u706d\u4e86\u810a\u9ad3\u7070\u8d28\u708e\uff1b\u4e2d\u56fd\u6240\u5728\u7684\u4e9a\u6d32\u592a\u5e73\u6d0b\u5730\u533a\uff0c\u4e5f\u57282000\u5e74\u88ab\u8bc1\u5b9e\u6d88\u706d\u4e86\u810a\u9ad3\u7070\u8d28\u708e\u3002\u6240\u4ee5\u987e\u65b9\u821f\u9662\u957f\u53c8\u88ab\u5927\u5bb6\u4eb2\u5207\u5730\u79f0\u4e3a\u201c\u7cd6\u4e38\u75ab\u82d7\u7237\u7237\u201d\u3002\u6709\u4eba\u53ef\u80fd\u8bf4\uff0c\u6211\u4eec\u5bb6\u6709\u4e2a\u5c0f\u5b9d\u5b9d\uff0c\u4e0d\u5230\u4e09\u5c81\uff0c\u6211\u600e\u4e48\u6ca1\u542c\u8bf4\u4ed6\u5403\u8fc7\u201c\u7cd6\u4e38\u201d\u5462\uff1f\u56e0\u4e3a\u73b0\u5728\u6ca1\u6709\u201c\u7cd6\u4e38\u201d\u4e86\u3002\u4e3a\u4ec0\u4e48\uff1f\u8fd9\u5c31\u8981\u8bf4\u5230\u75ab\u82d7\u7684\u4e0d\u826f\u53cd\u5e94\u3002\u7cd6\u4e38\u75ab\u82d7\u662f\u51cf\u6bd2\u6d3b\u75ab\u82d7\uff0c\u6240\u4ee5\u4e00\u4e9b\u7279\u6b8a\u7684\u5b69\u5b50\u2014\u2014\u6bd4\u5982\u6709\u4e25\u91cd\u514d\u75ab\u529f\u80fd\u7f3a\u9677\u7684\u65b0\u751f\u513f\u2014\u2014\u5982\u679c\u670d\u7cd6\u4e38\u524d\u6ca1\u6709\u53d1\u73b0\u9633\u6027\u7684\u75c7\u72b6\u4f53\u5f81\uff0c\u53ef\u80fd\u5c31\u670d\u7528\u4e86\uff1b\u670d\u7528\u4e4b\u540e\uff0c\u6781\u4e2a\u522b\u4f1a\u53d1\u751f\u80a2\u4f53\u7684\u9ebb\u75f9\u3002\u7528\u5bb6\u957f\u7684\u8bdd\u8bf4\uff0c\u6211\u7684\u5b69\u5b50\u201c\u4e0d\u5403\u7cd6\u4e38\u4e0d\u5f97\u75c5\uff0c\u5403\u4e86\u7cd6\u4e38\u9ebb\u75f9\u4e86\u201d\u3002\u901a\u8fc7\u5168\u4e16\u754c\u5927\u91cf\u7684\u6570\u636e\u89c2\u5bdf\uff0c\u53d1\u73b0\u201c\u4e0e\u75ab\u82d7\u76f8\u5173\u7684\u810a\u9ad3\u7070\u8d28\u708e\u9ebb\u75f9\u75c5\u4f8b\u201d\u7684\u53d1\u751f\u7387\u662f\u767e\u4e07\u5206\u4e4b\u4e00\uff0c\u5176\u5b9e\u5f88\u4f4e\u3002\u4f46\u5373\u4f7f\u8fd9\u6837\uff0c\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\uff08WHO\uff09\u8981\u6c42\uff0c2016\u5e745\u67081\u65e5\u5f00\u59cb\uff0c\u5168\u7403\u6240\u6709\u513f\u7ae5\u7b2c\u4e00\u5242\u4e0d\u518d\u670d\u7528\u810a\u9ad3\u7070\u8d28\u708e\u51cf\u6bd2\u6d3b\u75ab\u82d7\uff0c\u53ea\u80fd\u63a5\u79cd\u810a\u9ad3\u7070\u8d28\u708e\u706d\u6d3b\u75ab\u82d7\uff1b\u7b2c\u4e8c\u3001\u4e09\u5242\u4ecd\u7136\u53e3\u670d\u810a\u9ad3\u7070\u8d28\u708e\u51cf\u6bd2\u6d3b\u75ab\u82d7\uff0c\u4f46\u662f\u5242\u578b\u53d8\u4e86\uff0c\u53d8\u6210\u4e86\u6db2\u4f53\u7684\u6ef4\u5242\uff0c\u4e0d\u662f\u56fa\u4f53\u7684\u767d\u8272\u7cd6\u4e38\u3002\u6240\u4ee5\u73b0\u5728\u5e26\u4e09\u5c81\u4ee5\u4e0b\u7684\u5c0f\u5b69\u5230\u9884\u9632\u63a5\u79cd\u95e8\u8bca\u53bb\uff0c\u4f1a\u53d1\u73b0\u533b\u751f\u62ff\u4e00\u4e2a\u5851\u6599\u7684\u3001\u8f6f\u7684\u5bb9\u5668\uff08\u76db\u6709\u75ab\u82d7\uff09\uff0c\u5f80\u5b69\u5b50\u53e3\u4e2d\u6ef4\u4e24\u6ef4\u3002\u8fd9\u5c31\u662f\u201c\u7cd6\u4e38\u53d8\u8fc1\u201d\u7684\u6545\u4e8b\u300202\u2014\u75ab\u82d7\u5b89\u5168\u5417\uff1f\u90a3\u75ab\u82d7\u5b89\u5168\u5417\uff1f\u6bcf\u4e00\u6b21\u75ab\u82d7\u7684\u8d1f\u9762\u4e8b\u4ef6\uff0c\u90fd\u4f1a\u5f15\u8d77\u516c\u4f17\u5bf9\u201c\u75ab\u82d7\u5b89\u5168\u201d\u7684\u6050\u614c\uff0c\u6bd4\u5982\u53bb\u5e74\u53d1\u751f\u7684\u201c\u957f\u6625\u957f\u751f\u75ab\u82d7\u4e8b\u4ef6\u201d\u2014\u2014\u8fd9\u662f\u4e2a\u7279\u4f8b\uff1a\u6240\u6d89\u53ca\u7684\u516c\u53f8\u662f\u4e0a\u5e02\u516c\u53f8\uff0c\u56fd\u5bb6\u5df2\u7ecf\u53d6\u6d88\u4e86\u5b83\u7684\u8d44\u8d28\uff0c\u800c\u4e14\u4fc3\u6210\u56fd\u5bb6\u62df\u5c06\u51fa\u53f0\u300a\u4e2d\u534e\u4eba\u6c11\u5171\u548c\u56fd\u75ab\u82d7\u7ba1\u7406\u6cd5\u300b\u3002\u5927\u5bb6\u4e0d\u8981\u56e0\u4e3a\u4e2a\u6848\uff0c\u5931\u53bb\u5bf9\u6211\u4eec\u56fd\u5bb6\u75ab\u82d7\u7684\u4fe1\u5fc3\u3002\u6211\u4eec\u56fd\u5bb6\u5bf9\u75ab\u82d7\u6267\u884c\u4e24\u4e2a\u975e\u5e38\u4e25\u683c\u7684\u6807\u51c6\uff1a\u4e00\u4e2a\u662f\u56fd\u5bb6\u836f\u5178\u6807\u51c6\uff0c\u53e6\u4e00\u4e2a\u662f\u75ab\u82d7\u7684\u6ce8\u518c\u6807\u51c6\uff08\u6bcf\u652f\u75ab\u82d7\u90fd\u6709\u552f\u4e00\u7684\u6ce8\u518c\u53f7\uff09\u3002\u56fd\u5bb6\u836f\u5178\u6bcf\u4e94\u5e74\u5347\u7ea7\u4e00\u7248\uff0c\u6bcf\u5347\u7ea7\u4e00\u7248\uff0c\u75ab\u82d7\u7684\u751f\u4ea7\u6807\u51c6\u5c31\u4f1a\u66f4\u52a0\u4e25\u683c\u3002\u6f14\u8bb2\u5609\u5bbe\u5b59\u7f8e\u5e73\uff1a\u300a\u4f60\u53ef\u80fd\u542c\u8fc7\u5f88\u591a\u75ab\u82d7\u8c23\u8a00\uff0c\u4f46\u6211\u8981\u8bf4\uff1a\u63a5\u79cd\u75ab\u82d7\u662f\u9632\u63a7\u4f20\u67d3\u75c5\u6700\u6709\u6548\u7684\u624b\u6bb5\u300b03\u2014\u8fdb\u53e3\u75ab\u82d7\u662f\u4e0d\u662f\u6bd4\u56fd\u4ea7\u75ab\u82d7\u597d\uff1f\u6211\u4eec\u7ecf\u5e38\u4f1a\u542c\u5230\u8fd9\u7c7b\u95ee\u9898\uff1a\u8fdb\u53e3\u75ab\u82d7\u662f\u4e0d\u662f\u6bd4\u56fd\u4ea7\u75ab\u82d7\u597d\uff1f\u4e0d\u4e00\u5b9a\u3002\u6211\u56fd2010\u5e74\u7248\u836f\u5178\u51fa\u53f0\u4e4b\u540e\uff0c\u6240\u6709\u8fdb\u53e3\u7684\u51cf\u6bd2\u6d3b\u75ab\u82d7\u90fd\u9646\u7eed\u9000\u51fa\u4e86\u4e2d\u56fd\u5e02\u573a\u3002\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u8fd9\u7c7b\u75ab\u82d7\u4e2d\u7684\u6297\u751f\u7d20\u7684\u6b8b\u7559\u91cf\u8d85\u8fc7\u4e86\u65b0\u7248\u6807\u51c6\u3002\u6b64\u5916\uff0c\u8fd8\u6709\u51e0\u79cd\u75ab\u82d7\u4e5f\u9000\u51fa\u4e86\u4e2d\u56fd\u5e02\u573a\uff1a\u5982\u4e00\u79cd\u5168\u7403\u77e5\u540d\u7684\u72c2\u72ac\u75c5\u75ab\u82d7\uff0c\u56e0\u7ec6\u80de\u4e2dDNA\u7684\u6b8b\u7559\u91cf\u8d85\u6807\uff1b\u4e00\u79cd\u65e0\u7ec6\u80de\u767e\u767d\u7834\u75ab\u82d7\uff0c\u56e0\u5438\u9644\u5242\u7684\u542b\u91cf\u8d85\u6807\u3002\u6240\u4ee5\uff0c\u4e0d\u80fd\u7b80\u5355\u5730\u8ba4\u4e3a\u8fdb\u53e3\u7684\u75ab\u82d7\u5c31\u6bd4\u56fd\u4ea7\u7684\u75ab\u82d7\u597d\u3002\u6211\u56fd\u75ab\u82d7\u603b\u4f53\u7684\u76d1\u7ba1\u6c34\u5e73\u5df2\u7ecf\u8fbe\u5230\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\u6807\u51c6\uff0c\u5e76\u4e8e2011\u5e74\u30012014\u5e74\u4e24\u6b21\u901a\u8fc7\u4e86\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\u7684\u75ab\u82d7\u7ba1\u7406\u9a8c\u8bc1\u3002\u6b64\u5916\uff0c\u6211\u56fd\u5df2\u7ecf\u83b7\u5f97\u4e86\u51e0\u79cd\u75ab\u82d7\u7684\u56fd\u9645\u91c7\u8d2d\u8d44\u8d28\uff0c\u53c2\u4e0e\u5230\u56fd\u9645\u7684\u75ab\u82d7\u62db\u6807\u91c7\u8d2d\u4e2d\uff0c\u6240\u4ee5\u5927\u5bb6\u4e00\u5b9a\u8981\u5bf9\u4e2d\u56fd\u7684\u75ab\u82d7\u6709\u4fe1\u5fc3\u300204\u2014\u63a5\u79cd\u75ab\u82d7\u5bfc\u81f4\u5b64\u72ec\u75c7\uff1f\u53e6\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u56fd\u9645\u4e0a\u5f88\u6709\u540d\u7684\u8c23\u8a00\uff1a\u63a5\u79cd\u9ebb\u98ce\u816e\u75ab\u82d7\u53ef\u80fd\u5bfc\u81f4\u513f\u7ae5\u5f97\u5b64\u72ec\u75c7\u3002\u4e3a\u4ec0\u4e48\u5f97\u5b64\u72ec\u75c7\u4f1a\u5f15\u8d77\u5173\u6ce8\u5462\uff1f\u56e0\u4e3a\u8fd1\u5341\u5e74\u6765\uff0c\u6839\u636e\u56fd\u9645\u4e0a\u5c24\u5176\u7f8e\u56fd\u7684\u7edf\u8ba1\uff0c\u5b64\u72ec\u75c7\u53d1\u75c5\u7387\u5feb\u901f\u4e0a\u5347\uff0c\u6211\u4eec\u56fd\u5bb6\u5b64\u72ec\u75c7\u513f\u7ae5\u4e5f\u8d8a\u6765\u8d8a\u591a\uff0c\u6240\u4ee5\u5927\u5bb6\u975e\u5e38\u5173\u6ce8\u3002\u76ee\u524d\uff0c\u5b64\u72ec\u75c7\u4ecd\u7136\u539f\u56e0\u4e0d\u660e\uff0c\u901a\u5e38\u5728\u5e7c\u513f\u65f6\u671f\u88ab\u8bca\u65ad\uff0c\u662f\u4e00\u4e2a\u5e7f\u6cdb\u7684\u3001\u53d1\u80b2\u8fdf\u7f13\u6027\u7684\u75be\u75c5\uff0c\u800c\u4e14\u8868\u73b0\u4e3a\u591a\u79cd\u3001\u4e00\u7cfb\u5217\u7684\u75c7\u72b6\u4f53\u5f81\u3002\u8fd9\u4e2a\u8c23\u8a00\u662f\u600e\u4e48\u6765\u7684\uff1f1998\u5e74\uff0c\u4e00\u4f4d\u82f1\u56fd\u533b\u751f\u548c\u4ed6\u768412\u4f4d\u540c\u4e8b\uff0c\u5728\u4e16\u754c\u4e0a\u975e\u5e38\u6709\u540d\u7684\u300a\u67f3\u53f6\u5200\uff08The Lancet\uff09\u300b\u671f\u520a\u4e0a\u53d1\u8868\u4e86\u4e00\u7bc7\u6587\u7ae0\u3002\u4ed6\u4eec\u89c2\u5bdf\u4e8612\u540d\u5b64\u72ec\u75c7\u513f\u7ae5\uff0c\u7ed3\u8bba\u662f12\u4f8b\u4e2d\uff0c\u67098\u4f8b\u662f\u6253\u4e86\u9ebb\u98ce\u816e\u75ab\u82d7\u4e4b\u540e\u5f97\u7684\u3002\u8fd9\u5728\u5168\u7403\u5f15\u8d77\u4e86\u8f69\u7136\u5927\u6ce2\uff0c\u6b27\u7f8e\u5f88\u591a\u6743\u5a01\u673a\u6784\u5c31\u4e8c\u8005\u76f8\u5173\u6027\u8fdb\u884c\u4e86\u5927\u91cf\u7684\u8c03\u67e5\u548c\u79d1\u5b66\u7814\u7a76\uff0c\u7ed3\u679c\u8bc1\u5b9e\uff1a\u7ed3\u8bba\u9519\u8bef\u3002\u4e3a\u6b64\uff0c\u300a\u67f3\u53f6\u5200\u300b\u57282010\u5e745\u6708\u64a4\u9500\u4e86\u8fd9\u7bc7\u8bba\u6587\uff0c\u800c\u4e14\u8fd9\u4f4d\u533b\u751f\u768412\u4f4d\u540c\u4e8b\u4e2d\u670910\u4f4d\u4e5f\u63d0\u51fa\u4e86\u64a4\u7a3f\u7533\u8bf7\uff0c\u4f46\u662f\u5230\u73b0\u5728\uff0c\u8fd9\u5219\u8c23\u8a00\u7684\u8d1f\u9762\u5f71\u54cd\u4ecd\u7136\u5b58\u5728\u3002\u9ebb\u98ce\u816e\u75ab\u82d7\u662f\u80fd\u9884\u9632\u9ebb\u75b9\u3001\u98ce\u75b9\u548c\u816e\u817a\u708e\u7684\u75ab\u82d7\u3002\u4ee5\u9ebb\u75b9\u4e3a\u4f8b\uff0c\u6700\u8fd1\u5a92\u4f53\u7ecf\u5e38\u62a5\u9053\u7f8e\u56fd\u9ebb\u75b9\u53d1\u75c5\u7387\u4e0a\u5347\uff1b\u5728\u975e\u6d32\u4e00\u4e2a\u56fd\u5bb6\uff0c\u9ebb\u75b9\u4f7f\u5f971500\u591a\u4f4d\u513f\u7ae5\u6b7b\u4ea1\u3002\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\u4e5f\u62a5\u9053\uff0c\u4eca\u5e74\u5168\u7403\u9ebb\u75b9\u53d1\u75c5\u7387\u5448\u73b0\u4e0a\u5347\u3002\u4f46\u6211\u4eec\u56fd\u5bb6\uff0c\u9ebb\u75b9\u53d1\u75c5\u7387\u6700\u9ad8\u7684\u4e00\u5e74\u662f60\u5e74\u524d\u76841959\u5e74\uff0c\u5f53\u65f6\u5168\u56fd\u7684\u4eba\u53e3\u53ea\u67096.5\u4ebf\u591a\uff0c\u9ebb\u75b9\u75c5\u4f8b\u662f900\u4e07\u4f8b\u2014\u2014\u4eca\u5929\u6211\u4eec\u5df2\u7ecf13\u591a\u4ebf\u4eba\u4e86\uff0c\u5982\u679c\u6ca1\u6709\u9ebb\u75b9\u75ab\u82d7\uff0c\u90a3\u5168\u56fd\u75c5\u4f8b\u5c06\u662f1800-1900\u4e07\u4f8b\u3002\u6240\u4ee5\uff0c\u4e0d\u80fd\u8f7b\u6613\u5730\u76f8\u4fe1\u8fd9\u79cd\u4f20\u8a00\u300205\u2014\u9632\u63a7\u4f20\u67d3\u75c5\uff0c\u63a5\u79cd\u75ab\u82d7\u6700\u6709\u6548\u8fd9\u4e5f\u6d89\u53ca\u5230\u4e00\u79cd\u7279\u522b\u7684\u73b0\u8c61\uff0c\u6709\u4e00\u4e2a\u65b0\u7684\u540d\u8bcd\u53eb\u201c\u75ab\u82d7\u72b9\u8c6b\u201d\u3002\u4ec0\u4e48\u662f\u201c\u75ab\u82d7\u72b9\u8c6b\u201d\uff1f\u5c31\u662f\uff0c\u6bcf\u5f53\u542c\u5230\u5173\u4e8e\u75ab\u82d7\u7684\u8f83\u5927\u8d1f\u9762\u65b0\u95fb\u7684\u65f6\u5019\uff0c\u5bb6\u957f\u5c31\u4e0d\u518d\u7ed9\u513f\u7ae5\u63a5\u79cd\u75ab\u82d7\uff0c\u6216\u8005\u5c31\u6000\u7591\u75ab\u82d7\u662f\u5426\u5b89\u5168\u2014\u2014\u8981\u7b49\u771f\u76f8\u51fa\u6765\u540e\uff0c\u786e\u8ba4\u75ab\u82d7\u5b89\u5168\uff0c\u518d\u5e26\u5b69\u5b50\u63a5\u79cd\u3002\u4f46\u662f\u75ab\u82d7\u9884\u9632\u7684\u5f88\u591a\u4f20\u67d3\u75c5\u662f\u6709\u5b63\u8282\u6027\u7684\uff0c\u5728\u7b49\u5f85\u7684\u8fc7\u7a0b\u4e2d\u5c31\u4f1a\u66b4\u9732\u5728\u75c5\u539f\u4e0b\uff0c\u6709\u53ef\u80fd\u5c31\u88ab\u611f\u67d3\u5f97\u75c5\u3002\u6240\u4ee5 \u201c\u75ab\u82d7\u72b9\u8c6b\u201d\u662f\u4e0d\u53ef\u53d6\u7684\uff0c\u53ef\u80fd\u9519\u5931\u63a5\u79cd\u7684\u673a\u4f1a\u3002\u4eca\u5e74\u76844\u670825\u65e5\u662f\u5168\u56fd\u7b2c33\u4e2a\u5168\u56fd\u513f\u7ae5\u9884\u9632\u63a5\u79cd\u5ba3\u4f20\u65e5\uff0c\u4e3b\u9898\u662f\u201c\u9632\u63a7\u4f20\u67d3\u75c5\uff0c\u63a5\u79cd\u75ab\u82d7\u6700\u6709\u6548\u201d\u3002\u8fc7\u53bb\uff0c\u65b0\u751f\u513f\u51fa\u751f\u63a5\u79cd\u7684\u7b2c\u4e00\u9488\u75ab\u82d7\u662f\u5361\u4ecb\u82d7\uff08\u63a5\u79cd\u540e\u53ef\u9884\u9632\u513f\u7ae5\u7ed3\u6838\u75c5\uff09\u30021992\u5e74\uff0c\u6211\u56fd\u628a\u4e59\u809d\u75ab\u82d7\u7eb3\u5165\u4e86\u8ba1\u5212\u514d\u75ab\u7ba1\u7406\uff0c2002\u5e74\uff0c\u628a\u4e59\u809d\u75ab\u82d7\u5b8c\u5168\u514d\u8d39\u63d0\u4f9b\u7ed9\u513f\u7ae5\u63a5\u79cd\u3002\u6240\u4ee5\uff0c\u4ece\u90a3\u4ee5\u540e\uff0c\u513f\u7ae5\u51fa\u751f\u63a5\u79cd\u7684\u7b2c\u4e00\u652f\u75ab\u82d7\u5c31\u662f\u5361\u4ecb\u75ab\u82d7\u548c\u4e59\u809d\u75ab\u82d7\u3002\u73b0\u5728\u513f\u7ae5\u51fa\u751f24\u5c0f\u65f6\u4e4b\u5185\u5c31\u8981\u63a5\u79cd\u8fd9\u4e24\u79cd\u75ab\u82d7\u3002\u90a3\u8fd9\u4e24\u9488\u75ab\u82d7\u8c01\u7b2c\u4e00\u3001\u8c01\u7b2c\u4e8c\u5462\uff1f\u539f\u5219\u4e0a\u53ef\u4ee5\u540c\u65f6\u63a5\u79cd\uff1a\u4e00\u652f\u63a5\u79cd\u5de6\u4fa7\u80f3\u818a\uff0c\u4e00\u652f\u63a5\u79cd\u53f3\u4fa7\u80f3\u818a\u3002\u4e3a\u4ec0\u4e48\u6211\u56fd\u8981\u628a\u4e59\u809d\u75ab\u82d7\u7eb3\u5165\u514d\u75ab\u89c4\u5212\uff1f\u56e0\u4e3a\u6211\u56fd\u8fc7\u53bb\u662f\u4e59\u809d\u5927\u56fd\u3002\u5927\u5bb6\u770b\u8fd9\u5f20\u56fe\uff0c\u989c\u8272\u8d8a\u6df1\u4ee3\u8868\u53d1\u75c5\u7387\u8d8a\u9ad8\uff0c\u4e2d\u56fd\u5728\u4e00\u4e2a\u6700\u6df1\u84dd\u7684\u533a\u57df\u5185\uff0c\u5f53\u65f6\u6211\u4eec\u56fd\u5bb6\u611f\u67d3\u4e59\u809d\u7684\u4eba\u53ef\u4ee5\u4ee5\u4ebf\u4e3a\u5355\u4f4d\u8ba1\u7b97\u3002\u5f97\u4e59\u809d\u7684\u7ed3\u679c\u4f1a\u662f\u4ec0\u4e48\u6837\u5462\uff1f\u5982\u679c\u611f\u67d3\u4e86\u4e59\u809d\u75c5\u6bd2\uff0c\u53ef\u80fd\u4f1a\u53d1\u5c55\u6210\u4e3a\u4e59\u809d\u75c5\u4eba\uff1b\u4e59\u809d\u75c5\u4eba\u6ca1\u6709\u6cbb\u6108\uff0c\u5c31\u53d1\u5c55\u6210\u6162\u6027\u4e59\u809d\u75c5\u4eba\uff1b\u6162\u6027\u4e59\u809d\u7ecf\u8fc7\u957f\u671f\u53d1\u5c55\uff0c\u5c31\u53d8\u6210\u809d\u786c\u5316\uff1b\u809d\u786c\u5316\u518d\u7ecf\u8fc7\u957f\u671f\u53d1\u5c55\uff0c\u5c31\u53d8\u6210\u809d\u764c\uff0c\u6211\u8bb0\u5f97\u53bb\u5e74\u4eb2\u5386\u7684\u4e00\u4ef6\u4e8b\uff1a\u4e00\u4e2a\u5c71\u4e1c\u7684\u519c\u6c11\u4f01\u4e1a\u5bb6\u5f97\u4e86\u809d\u764c\uff0c\u5f53\u5730\u5df2\u7ecf\u6cbb\u4e0d\u4e86\u4e86\uff0c\u4ed6\u5c31\u8ddf\u6211\u8054\u7cfb\uff0c\u95ee\u6211\u80fd\u4e0d\u80fd\u5e2e\u5fd9\u5728\u5317\u4eac\u627e\u4e00\u4e2a\u533b\u9662\u53bb\u6cbb\u7597\u3002\u540e\u6765\u6211\u8054\u7cfb\u597d\u4e13\u5bb6\uff0c\u7b2c\u4e8c\u5929\u65e9\u6668\u7ed9\u4ed6\u5bb6\u4eba\u6253\u7535\u8bdd\uff0c\u4ed6\u5bb6\u4eba\u8bf4\u4ed6\u5df2\u7ecf\u809d\u660f\u8ff7\u4e86\u3002\u73b0\u5728\u8fd9\u4e2a\u4eba\u5df2\u7ecf\u4e0d\u5728\u4e86\uff0c\u8fd9\u5c31\u662f\u611f\u67d3\u4e59\u809d\u7684\u7ed3\u5c40\u3002\u6211\u56fd\u73b0\u5728\u91cd\u5927\u4f20\u67d3\u75c5\u7684\u9632\u63a7\uff0c\u5176\u4e2d\u5c31\u6709\u4e00\u4e2a\u4e59\u809d\u7684\u201c\u964d\u4e24\u7387\u201d\uff0c\u5373\u964d\u53d1\u75c5\u7387\u548c\u964d\u75c5\u6b7b\u7387\u3002\u6211\u662f1983\u5e74\u53c2\u52a0\u5de5\u4f5c\u7684\uff0c\u90a3\u65f6\u5019\u4e59\u809d\u75ab\u82d7\u8fd8\u6ca1\u6709\u95ee\u4e16\uff1b\u7ecf\u8fc7\u51e0\u5341\u5e74\u7684\u52aa\u529b\uff0c2014\u5e74\uff0c\u6211\u56fd\u7684\u513f\u7ae5\u4e59\u809d\u63a7\u5236\u53d7\u5230\u4e86\u4e16\u754c\u536b\u751f\u7ec4\u7ec7\u7684\u8868\u5f70\u3002\u7528\u6570\u636e\u4e0a\u6765\u8bf4\uff0c\u6ca1\u6709\u4e59\u809d\u75ab\u82d7\u4e4b\u524d\uff0c\u6211\u56fd\u513f\u7ae5\u7684\u4e59\u809d\u611f\u67d3\u7387\u662f10%\u5de6\u53f3\uff0c\u73b0\u5728\u5df2\u7ecf\u964d\u52301%\u4ee5\u4e0b\u4e86\uff0c\u8bf4\u660e\u4e59\u809d\u75ab\u82d7\u5728\u4e59\u809d\u63a7\u5236\u65b9\u9762\u53d1\u6325\u4e86\u5de8\u5927\u4f5c\u7528\u3002\u6240\u4ee5\uff0c\u8981\u7528\u6b63\u786e\u7684\u75be\u75c5\u9884\u9632\u77e5\u8bc6\u6765\u6b66\u88c5\u81ea\u5df1\u3002\u6211\u4eca\u5929\u4e3e\u4e86\u810a\u9ad3\u7070\u8d28\u708e\u75ab\u82d7\u548c\u4e59\u578b\u809d\u708e\u75ab\u82d7\u4e24\u4e2a\u4f8b\u5b50\uff0c\u5e0c\u671b\u80fd\u5f3a\u5316\u201c\u9632\u63a7\u4f20\u67d3\u75c5\uff0c\u63a5\u79cd\u9884\u9632\u6700\u6709\u6548\u201d\u7684\u7406\u5ff5\uff0c\u8c22\u8c22\u5927\u5bb6\u3002\u6f14\u8bb2\u5609\u5bbe\u5b59\u7f8e\u5e73\uff1a\u300a\u4f60\u53ef\u80fd\u542c\u8fc7\u5f88\u591a\u75ab\u82d7\u8c23\u8a00\uff0c\u4f46\u6211\u8981\u8bf4\uff1a\u63a5\u79cd\u75ab\u82d7\u662f\u9632\u63a7\u4f20\u67d3\u75c5\u6700\u6709\u6548\u7684\u624b\u6bb5\u300b", "title": "\u75ab\u82d7\uff0c\u6253\u8fd8\u662f\u4e0d\u6253\uff1f", "ukey_author": null, "video_content": "<iframe frameborder=\"0\" src=\"https://v.qq.com/txp/iframe/player.html?vid=p08741ooamk\" allowFullScreen=\"true\"></iframe>"}, {"author": {"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [], "content": "<p><!--StartFragment--><strong><span style=\"color:#696969\"><span style=\"font-size:14px\">\u672c\u6587\u4e3a2019\u5e744\u670827\u65e5&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u671f&mdash;&mdash;\u5bf9\u8c23\u8a00\u8bf4&ldquo;\u4e0d&rdquo;| \u4f55\u4e3d \u6f14\u8bb2\u5b9e\u5f55\uff1a</span></span></strong><!--EndFragment--></p>\r\n\r\n<p class=\"editors-note\">\u9ec4\u74dc\u548c\u82b1\u751f\u4e0d\u80fd\u4e00\u8d77\u5403\uff1f\u9178\u6027\u98df\u7269\u5bf9\u5065\u5eb7\u6709\u5bb3\uff1f\u5173\u4e8e\u8425\u517b\u7684\u8c23\u8a00\uff0c\u4f60\u8e29\u96f7\u4e86\u5417\uff1f&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u7b2c\u5341\u671f\u6f14\u8bb2\u73b0\u573a\uff0c\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7814\u7a76\u5458\u4f55\u4e3d\u4e0d\u4ec5\u5e2e\u52a9\u6211\u4eec\u7c89\u788e\u4e86\u5f88\u591a\u65e5\u5e38\u542c\u5230\u7684\u8c23\u8a00\uff0c\u8fd8\u624b\u628a\u624b\u6559\u5927\u5bb6\u5065\u5eb7\u751f\u6d3b\u7684&ldquo;\u5341\u4e2a\u62f3\u5934&rdquo;\u539f\u5219\u3002</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"498\" scrolling=\"no\" src=\"https://v.qq.com/txp/iframe/player.html?vid=e0873tjohh2\" width=\"640\"></iframe></p>\r\n\r\n<p>\u6211\u662f\u6765\u81ea\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7684\u4f55\u4e3d\uff0c\u4e13\u95e8\u7814\u7a76\u8425\u517b\u5b66\u3002</p>\r\n\r\n<p>\u5b66\u8425\u517b\u6709\u597d\u5904&mdash;&mdash;\u4ec0\u4e48\u597d\u5904\u5462\uff1f</p>\r\n\r\n<p>\u9996\u5148\u5bf9\u81ea\u5df1\u597d\uff0c\u81ea\u5df1\u53d7\u76ca\uff1b\u5176\u6b21\uff0c\u5bb6\u4eba\u53d7\u76ca\uff1b\u518d\u5176\u6b21\uff0c\u8ba9\u6c11\u4f17\u53d7\u76ca\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/1Mz7cjwC5cgY8DKoHTGLcQQC6v72OlCiwdXcH8kmews4BAAAugIAAEpQ.jpg\" style=\"height:414px; width:640px\" />\u6f14\u8bb2\u5609\u5bbe\u4f55\u4e3d\uff1a\u300a\u751f\u916e\u996e\u98df\u51cf\u80a5\uff1f\u78b1\u6027\u98df\u7269\u517b\u751f\uff1f&hellip;&hellip;\u522b\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u9a97\u4e86\uff01\u300b</p>\r\n\r\n<p>\u4eca\u5929\u6211\u5c31\u60f3\u5206\u4eab\u4e00\u4e9b\u8425\u517b\u77e5\u8bc6\uff0c\u5e2e\u52a9\u5927\u5bb6\u8fa8\u522b\u4e00\u4e9b\u8c23\u8a00\uff0c\u4e0d\u8981\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u6b3a\u9a97\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/zj-5oun6V6hlTCrIjL6JoIqH3HLtkQYd4thnxh7saJA4BAAAXgIAAEpQ.jpg\" style=\"height:359px; width:640px\" /></p>\r\n\r\n<h2>01&mdash;\u98df\u7269\u76f8\u751f\u4e0e\u76f8\u514b\uff1f&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h2>\r\n\r\n<p>\u5f88\u591a\u4eba\u90fd\u542c\u8fc7\u4e00\u4e9b&ldquo;\u98df\u7269\u76f8\u751f\u76f8\u514b&rdquo;\u7684\u8bf4\u6cd5\u3002\u8fd9\u79cd\u8bf4\u6cd5\u7531\u6765\u5df2\u4e45\u3002</p>\r\n\r\n<p>\u6211\u5148\u7ed9\u5927\u5bb6\u5206\u4eab\u4e00\u4e2a\u5c0f\u6545\u4e8b&mdash;&mdash;</p>\r\n\r\n<p><img alt=\"\" src=\"http://2-im.guokr.com/mu-MCo2jgtIItqoEjOdsMFuKzTiRb185Zj7OVcLIjp44BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u5341\u6765\u5e74\u524d\uff0c\u6211\u53bb\u7f8e\u56fd\u4e00\u4e2a\u5e38\u9752\u85e4\u5927\u5b66\u8fdb\u4fee\u3002\u8d76\u4e0a\u5047\u671f\uff0c\u5c31\u548c\u670b\u53cb\u76f8\u7ea6\u5357\u90e8\u81ea\u9a7e\u6e38\u53bb\u5927\u6cbc\u6cfd\u5730\u56fd\u5bb6\u516c\u56ed\uff08Everglades National Park\uff09\uff0c\u90a3\u662f\u4e2a\u5f88\u539f\u751f\u6001\u7684\u516c\u56ed\uff0c\u91cc\u9762\u53ef\u4ee5\u770b\u9cc4\u9c7c\u3002</p>\r\n\r\n<p>\u5728\u6765\u7684\u8def\u4e0a\uff0c\u670b\u53cb\u7684\u5148\u751f\u5f00\u8f66\u3002\u6211\u548c\u5979\u5750\u5728\u540e\u9762\uff0c\u4e00\u8fb9\u804a\u5929\u4e00\u8fb9\u5403\u82b1\u751f\u7c73&mdash;&mdash;\u8fd9\u82b1\u751f\u7c73\u662f\u524d\u4e00\u5929\u665a\u4e0a\u6211\u4eec\u81ea\u5df1\u716e\u597d\u7684\u3002\u5230\u4e86\u516c\u56ed\u95e8\u53e3\uff0c\u770b\u89c1\u6709\u5356\u9ec4\u74dc\u7684\uff0c\u662f\u7f8e\u56fd\u519c\u6c11\u79cd\u7684\u9ec4\u74dc\u3002\u6211\u4eec\u6beb\u4e0d\u72b9\u8c6b\u5730\u4e70\u4e86\u3002</p>\r\n\r\n<p>\u670b\u53cb\u592b\u59bb\u5728\u7f8e\u56fd\u5f85\u7684\u65f6\u95f4\u957f\uff0c\u4ed6\u4eec\u89c9\u5f97\u7f8e\u56fd\u7684\u98df\u54c1\u5b89\u5168\u503c\u5f97\u4fe1\u8d56\uff1b\u6240\u4ee5\u4e70\u4e86\u9ec4\u74dc\uff0c\u62ff\u9910\u5dfe\u7eb8\u64e6\u4e86\u64e6\u5c31\u5403\u4e86\u3002\u6211\u7531\u4e8e\u4ece\u4e8b\u8425\u517b\u98df\u54c1\u5b89\u5168\u5de5\u4f5c\uff0c\u6ca1\u6d17\u7684\u9ec4\u74dc\u603b\u89c9\u5f97\u6709\u70b9\u5fd0\u5fd1\uff0c\u5c31\u62ff\u51fa\u4e00\u628a\u5c0f\u5200\u7ed9\u524a\u4e86\u76ae\uff0c\u4e5f\u5403\u4e86\u3002</p>\r\n\r\n<p>\u4e00\u8def\u8fdb\u53bb\u73a9\uff0c\u5c31\u4e0d\u8868\u4e86\u3002\u51e0\u4e2a\u5c0f\u65f6\u4ee5\u540e\u51fa\u6765\uff0c\u6211\u670b\u53cb\u8bf4\u4e0d\u884c\uff0c\u809a\u5b50\u5f88\u75bc\u3001\u80a0\u9e23\u97f3\u9635\u9635\uff0c\u5f97\u627e\u5395\u6240\u53bb\u3002\u6ca1\u529e\u6cd5\uff0c\u5f97\u8d76\u7d27\u3002\u867d\u7136\u6cb9\u633a\u6ee1\u7684\uff0c\u4f46\u8fd8\u662f\u5f97\u5954\u52a0\u6cb9\u7ad9&mdash;&mdash;\u53ea\u6709\u52a0\u6cb9\u7ad9\u624d\u6709\u5395\u6240\u3002\u5979\u51fa\u6765\u4e86\u8bf4\uff0c&ldquo;\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u9ec4\u74dc\u548c\u82b1\u751f\u7c73\u6839\u672c\u5c31\u4e0d\u80fd\u4e00\u8d77\u5403\uff0c\u662f\u76f8\u514b\u7684\u3002\u4f60\u770b\u6211\u8fd9\u8fd8\u6709\u4e00\u672c\u4e66\u3002&rdquo;</p>\r\n\r\n<p>\u5979\u786e\u5b9e\u76f8\u4fe1&ldquo;\u98df\u7269\u76f8\u514b&rdquo;\u8fd9\u4e2a\u4e1c\u897f\uff0c\u56e0\u4e3a\u6211\u670b\u53cb\u5728\u7f8e\u56fd\u662f\u505a\u9488\u7078\u7684\u3002\u5979\u7684\u8bdd\u97f3\u521a\u843d\uff0c\u5979\u5148\u751f\u8bf4&ldquo;\u4e0d\u884c\u4e0d\u884c\uff0c\u6211\u4e5f\u809a\u5b50\u75bc\u3002&rdquo;\u4e5f\u4e0a\u5395\u6240\u53bb\u4e86\u3002\u6211\u5f88\u5947\u602a\uff0c&ldquo;\u4f60\u5148\u751f\u4e0d\u662f\u6ca1\u6709\u5403\u82b1\u751f\u7c73\u5417\uff1f\u6211\u82b1\u751f\u7c73\u548c\u9ec4\u74dc\u90fd\u5403\u4e86\uff0c\u4e3a\u4ec0\u4e48\u6211\u6ca1\u6709\u4e8b\u3002&rdquo;\u7ed3\u679c\u90a3\u4e00\u8def\u4e0a\uff0c\u5c31\u4e0d\u505c\u5730\u4e0a\u52a0\u6cb9\u7ad9\uff0c\u4e0d\u505c\u5730\u627ebathroom\uff0c\u73a9\u4e5f\u6ca1\u73a9\u597d\u3002</p>\r\n\r\n<p>\u4f60\u4eec\u60f3\u60f3\uff0c\u95ee\u9898\u51fa\u5728\u54ea\uff1f</p>\r\n\r\n<p>\u9ec4\u74dc\uff0c\u6ca1\u6d17\u7684\u9ec4\u74dc\u3002</p>\r\n\r\n<p>\u5176\u5b9e\u6709\u5173&ldquo;\u98df\u7269\u76f8\u514b&rdquo;\u7684\u8bdd\u9898\uff0c80\u591a\u5e74\u524d\uff0c\u5c31\u5728\u5357\u4eac\u5e7f\u4e3a\u6d41\u4f20\uff1a&ldquo;\u5403\u9999\u8549\u518d\u5403\u828b\u5934\u4f1a\u76f8\u514b&rdquo;\uff0c\u8fd8\u6709&ldquo;\u9999\u8471\u548c\u871c\u4e0d\u80fd\u5171\u98df&rdquo;\uff0c\u751a\u81f3\u8fde\u7ef4\u751f\u7d20C\u548c\u867e\u4e5f\u4e0d\u80fd\u4e00\u5757\u5403\u3002</p>\r\n\r\n<p>\u5f53\u65f6\u5357\u4eac\u5927\u5b66\u6709\u4e00\u4f4d\u79d1\u5b66\u5bb6\uff0c\u90d1\u96c6\u6559\u6388\u3002\u4ed6\u4e5f\u662f\u6211\u4eec\u8425\u517b\u754c\u7684\u4e00\u4e2a\u5178\u8303&mdash;&mdash;\u6d3b\u5f97\u6700\u957f\u7684\u5178\u8303\u3002\u8001\u4eba\u5bb6\u4ece1900\u5e74\u6d3b\u5230\u4e862010\u5e74\uff0c\u6d3b\u4e86110\u5468\u5c81\u3001111\u865a\u5c81\u3002</p>\r\n\r\n<p>\u5f53\u65f6\uff0c\u8001\u4eba\u5bb6\u6536\u96c6\u4e86\u5f88\u591a&ldquo;\u98df\u7269\u76f8\u514b&rdquo;\u7684\u8bf4\u6cd5\uff0c\u6709180\u591a\u5bf9\u3002\u4ed6\u9009\u62e9\u4e86\u5f88\u591a\u52a8\u7269\uff0c\u6709\u8001\u9f20\u3001\u7334\u5b50\u3001\u72d7\uff0c\u8fdb\u884c\u5b9e\u9a8c\uff0c\u628a\u8fd9\u4e9b\u8c23\u4f20&ldquo;\u76f8\u514b&rdquo;\u7684\u98df\u7269\u5582\u7ed9\u5b83\u4eec\u5403\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5403\u4e86\u597d\u591a\u3002\u540e\u6765\u53d1\u73b0\uff0c\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\uff1b\u52a8\u7269\u548c\u4eba\u90fd\u597d\u7740\u5462\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://3-im.guokr.com/2BSqOjh-vK85Qi3nADqBWaIvEgLhL9WnrX0ZQJTxKo44BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u540e\u6765\u4ed6\u5c31\u5f97\u51fa\u4e00\u4e2a\u7ed3\u8bba\uff1a\u98df\u7269\u76f8\u514b\u53ef\u80fd\u662f\u56e0\u4e3a\u672c\u8eab\u5403\u7684\u4eba\u662f\u8fc7\u654f\u4f53\u8d28\u3002\u73b0\u5728\u6211\u4eec\u8fd8\u6709\u4e00\u4e2a\u65b0\u7684\u8bf4\u6cd5\uff0c\u4e0d\u77e5\u9053\u5927\u5bb6\u542c\u6ca1\u542c\u8fc7\uff1f\u53eb&ldquo;\u98df\u7269\u4e0d\u8010\u53d7&rdquo;&mdash;&mdash;\u6709\u4e9b\u98df\u7269\uff0c\u6709\u4eba\u5403\u5b8c\u4ee5\u540e\u5c31\u4f1a\u809a\u5b50\u75bc\u3001\u62c9\u809a\u5b50\u3002\u8fd8\u6709\u5c31\u662f\u98df\u7269\u4e0d\u5e72\u51c0&mdash;&mdash;\u6bd4\u5982\u6211\u521a\u624d\u8bf4\uff0c\u6211\u670b\u53cb\u5403\u4e86\u4e0d\u5e72\u51c0\u7684\u9ec4\u74dc\u3002</p>\r\n\r\n<p>\u8fd8\u6709\u4e00\u79cd\u53ef\u80fd\uff0c\u5c31\u662f\u98df\u7269\u672c\u8eab\u6709\u6bd2\u3002\u6bd4\u65b9\u6709\u4eba\u8bf4\uff0c\u9999\u8471\u548c\u8702\u871c\u4e0d\u80fd\u6401\u4e00\u5757\u5403\uff0c\u4f1a\u6b7b\u4eba\u7684\u3002\u4e5f\u6709\u53ef\u80fd\uff0c\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u8fd9\u4e2a\u8702\u871c\u672c\u6765\u5c31\u662f\u7531\u6709\u6bd2\u7684\u82b1\u871c\u917f\u9020\u51fa\u6765\u7684\u3002</p>\r\n\r\n<p>\u8fc7\u53bb\u7684\u5e74\u4ee3\uff0c\u6211\u4eec\u6ca1\u6709\u9ad8\u6df1\u7684\u79d1\u5b66\u6280\u672f\uff0c\u4e5f\u6ca1\u6709\u663e\u5fae\u955c\u53ef\u4ee5\u53bb\u770b\uff0c\u751a\u81f3\u53d6\u4e2a\u7caa\u4fbf\uff0c\u600e\u4e48\u67e5\u4e5f\u4e0d\u77e5\u9053\u3002\u540c\u6837\u7684\u4e8b\u53d1\u751f\u4e24\u904d\uff0c\u90ce\u4e2d\u6216\u8005\u6742\u5b66\u8005\u5c31\u8bb0\u5f55\u4e0b\u6765\u4e86&mdash;&mdash;\u5f88\u591a\u4eba\u8bf4\u674e\u65f6\u73cd\u5b9e\u9645\u4e0a\u662f\u4e00\u4e2a\u6742\u5b66\u8005\uff0c\u5404\u4e2a\u65b9\u9762\u7684\u77e5\u8bc6\u90fd\u61c2\uff0c\u4ed6\u5199\u7684\u300a\u672c\u8349\u7eb2\u76ee\u300b\u8bb0\u8f7d\u4e86&ldquo;\u98df\u7269\u76f8\u514b&rdquo;\u7406\u8bba\u3002\u4ee5\u8bb9\u4f20\u8bb9\uff0c\u5c31\u8fd9\u4e48\u6d41\u4f20\u4e0b\u6765\u4e86\u3002</p>\r\n\r\n<p>\u6240\u4ee5\u6211\u8981\u544a\u8bc9\u5927\u5bb6\uff0c\u5b9e\u9645\u4e0a\u5f88\u591a\u4e1c\u897f\u6ca1\u6709\u4f60\u60f3\u8c61\u7684\u90a3\u4e48\u5371\u9669\uff0c\u4e0d\u4f1a\u76f8\u514b\uff1b\u4f46\u662f\u98df\u7269\u8fc7\u654f\u8fd8\u662f\u8981\u5173\u6ce8\u4e00\u4e0b\u3002</p>\r\n\r\n<h2>02&mdash;&ldquo;\u9178\u6027\u4f53\u8d28&rdquo;\u4e0e&ldquo;\u78b1\u6027\u98df\u7269&rdquo;</h2>\r\n\r\n<p>\u8fd8\u6709\u4e00\u4e2a\uff0c\u5c31\u662f\u5927\u5bb6\u8bf4\u7684&ldquo;\u9178\u78b1\u4f53\u8d28&rdquo;\uff0c\u7531\u6765\u5df2\u4e45\u3002\u5b9e\u9645\u4e0a\uff0c\u5728\u5f88\u591a\u5e74\u524d\uff0c\u6211\u5c31\u5199\u8fc7\u76f8\u5173\u7684\u6587\u7ae0\u3002</p>\r\n\r\n<p>\u53bb\u5e74\u4e00\u4e2a\u53eb\u6b27\u9633&middot;\u7f57\u4f2f\u7279\uff08Robert O. Young\uff09\u7684\u4eba\u88ab\u6293\u8d77\u6765\u4e86\uff0c\u88ab\u7f5a\u4e86\u4e00\u4ebf\u7f8e\u5143\u3002\u4e3a\u4ec0\u4e48\u7f5a\u4ed6\uff1f</p>\r\n\r\n<p>\u4ed6\u9020\u5047\uff0c\u5bb3\u7684\u5f88\u591a\u764c\u75c7\u75c5\u4eba\u803d\u8bef\u4e86\u6cbb\u7597\uff0c\u751a\u81f3\u642d\u4e0a\u4e86\u6027\u547d\u3002</p>\r\n\r\n<p>\u5f88\u591a\u4eba\u8ff7\u4fe1&ldquo;\u78b1\u6027\u98df\u7269&rdquo;\uff0c\u89c9\u5f97\u5403\u78b1\u6027\u7684\u98df\u7269\u597d\uff0c\u5403\u9178\u6027\u7684\u98df\u7269\u4e0d\u597d\u3002\u5f88\u591a\u65f6\u5019\uff0c\u6211\u4eec\u5224\u65ad\u98df\u7269\u7684\u9178\u78b1\u6027\uff0c\u9996\u5148\u662f\u5c1d\u5473\u9053\uff1a\u5403\u8d77\u6765\u9178\u7684\uff0c\u662f\u9178\u6027\u7684\uff1b\u5403\u8d77\u6765\u5f88\u6da9\uff0c\u662f\u78b1\u6027\u7684\u3002\u8fd8\u6709\u4e00\u79cd\u65b9\u6cd5\uff0c\u5c31\u662f\u7528pH\u8bd5\u7eb8\uff1a\u5982\u679cpH\u8bd5\u7eb8\u53d8\u7ea2\uff0c\u5c31\u662f\u9178\u6027\u7684\uff1b\u53d8\u84dd\uff0c\u5c31\u662f\u78b1\u6027\u7684\u3002</p>\r\n\r\n<p>\u5176\u5b9e\u8fd9\u4e9b\u90fd\u4e0d\u51c6\u3002\u6211\u4eec\u4e13\u95e8\u6709\u4e00\u95e8\u5b66\u79d1\u53eb&ldquo;\u98df\u7269\u5316\u5b66&rdquo;\uff0c\u5c31\u628a\u98df\u7269\u5206\u6210\u5448\u78b1\u6027\u7684\u548c\u5448\u9178\u6027\u7684\u3002\u600e\u4e48\u5206\u7684\uff1f\u628a\u98df\u7269\u71c3\u70e7\u6216\u6d88\u5316\u5206\u89e3\u53d8\u6210\u7070\u5206\uff0c\u7136\u540e\u53bb\u6d4b\u5176\u4e2d\u7684\u77ff\u7269\u8d28\u6210\u5206\u3002\u5982\u679c\u4e00\u7c7b\u98df\u7269\uff0c\u7070\u5206\u4e2d\u6bd4\u8f83\u591a\u7684\u662f\u786b\u3001\u78f7\u3001\u6c2f\u7b49\u7b49\u975e\u91d1\u5c5e\u5143\u7d20\uff0c\u52a0\u6c34\u53d8\u6210\u9178\uff08\u786b\u9178\u3001\u78f7\u9178\u8fd8\u6709\u76d0\u9178\uff09\uff0c\u5c31\u79f0\u4e3a&ldquo;\u9178\u6027\u98df\u7269&rdquo;\u3002\u8fd8\u6709\u4e00\u7c7b\uff0c\u7559\u4e0b\u7684\u662f\u94be\u3001\u94a0\u3001\u94c1\u3001\u9541\u7b49\u7b49\u91d1\u5c5e\u5143\u7d20\uff0c\u52a0\u6c34\u53d8\u6210\u78b1\uff08\u5448\u78b1\u6027\u7684\u6eb6\u6db2\uff09\uff0c\u8fd9\u4e9b\u5c31\u79f0\u4e3a&ldquo;\u78b1\u6027\u98df\u7269&rdquo;\u3002</p>\r\n\r\n<p>\u8fd9\u6837\u4e00\u5206\u7c7b\uff0c\u6211\u4eec\u5403\u7684\u6240\u6709\u9c7c\u3001\u79bd\u3001\u8089\u3001\u86cb\u3001\u5976\uff0c\u4e00\u4e9b\u8c46\u8150\u3001\u8c46\u5236\u54c1\uff0c\u8fd8\u6709\u4e00\u4e9b\u4e3b\u98df&mdash;&mdash;\u56e0\u4e3a\u542b\u6709\u8f83\u591a\u786b\u3001\u78f7\u548c\u6c2f&mdash;&mdash;\u90fd\u53d8\u6210\u4e86&ldquo;\u9178\u6027\u98df\u7269&rdquo;\u3002\u690d\u7269\u6027\u7684\u98df\u7269\uff0c\u6bd4\u65b9\u8bf4\u852c\u83dc\u3001\u6c34\u679c\uff0c\u8fd8\u6709\u8336\u53f6\u3001\u8461\u8404\u9152\uff0c\u8fd9\u4e9b\u5c31\u53d8\u6210&ldquo;\u78b1\u6027\u98df\u7269&rdquo;\u3002</p>\r\n\r\n<p>\u5982\u679c\u6309\u7167&ldquo;\u9178\u6027\u98df\u7269\u90fd\u4e0d\u80fd\u5403\u3001\u4f1a\u53d8\u6210\u9178\u6027\u4f53\u8d28&rdquo;\u8bf4\u6cd5\u7684\u8bdd\uff0c\u90a3\u5f88\u591a\u98df\u7269\u90fd\u662f\u4e0d\u80fd\u5403\u7684\uff1b\u800c\u4e14\u98df\u7269\u7edd\u5bf9\u5f71\u54cd\u4e0d\u4e86\u8eab\u4f53\u7684pH\u503c\u3002\u4f60\u60f3\u60f3\u770b\uff0c\u8840\u6db2\uff0cpH\u6052\u5b9a\u4fdd\u6301\u57287.35-7.45\u4e4b\u95f4\uff1b\u80c3\uff0c\u603b\u662f\u5728\u5f88\u9178\u7684\u73af\u5883\u4e2d\uff1b\u80a0\u9053\u91cc\u7684\u6db2\u4f53\uff0c\u603b\u662f\u6bd4\u8f83\u78b1\u6027\u7684\u3002\u4e00\u65e6\u53d1\u751f\u4e86\u6539\u53d8\uff0c\u8eab\u4f53\u5404\u65b9\u9762\u90fd\u4f1a\u53d7\u5230\u5f71\u54cd\u3002</p>\r\n\r\n<p>\u5982\u679c\u4f60\u7684\u8eab\u4f53\u4ece\u5fae\u5fae\u78b1\u6027\u53d8\u6210\u9178\u6027\uff0c\u90a3\u4e0d\u662f\u8bf4\u8981\u5f97\u764c\u75c7\u4e86&mdash;&mdash;\u53ef\u80fd\u90fd\u7b49\u4e0d\u5230\u5f97\u764c\u75c7\uff0c\u8981\u9a6c\u4e0a\u53bb\u533b\u9662\u62a2\u6551\u3002\u4e3a\u4ec0\u4e48\uff1f\u90a3\u53eb&ldquo;\u547c\u5438\u6027\u9178\u4e2d\u6bd2&rdquo;\uff0c\u6216\u8005&ldquo;\u4ee3\u8c22\u6027\u9178\u4e2d\u6bd2&rdquo;\u3002\u540c\u6837\u7684\u9053\u7406\uff0c\u5982\u679c\u4f60\u7684\u4f53\u8d28\u7a81\u7136\u53d8\u6210\u78b1\u6027\uff0c\u4e5f\u662f\u8eab\u4f53\u51fa\u4e86\u95ee\u9898\u3002\u56e0\u4e3a\u6211\u4eec\u7684\u8eab\u4f53\u6709\u4e09\u5927\u7cfb\u7edf&mdash;&mdash;\u4f53\u6db2\u7f13\u51b2\u7cfb\u7edf\u3001\u547c\u5438\u7cfb\u7edf\u3001\u6ccc\u5c3f\u7cfb\u7edf&mdash;&mdash;\u6765\u8c03\u8282\u4f60\u4f53\u6db2\u7684\u9178\u78b1\u6027\uff0c\u4f7f\u4e4b\u7ef4\u6301\u5728\u6052\u5b9a\u7684\u8303\u56f4\u5185\u3002</p>\r\n\r\n<h2>03&mdash;\u725b\u5976\u5c06\u4eba\u7c7b\u9001\u8fdb\u764c\u75c7\u7684\u575f\u5893\uff1f</h2>\r\n\r\n<p>\u8bf4\u5b8c\u8fd9\u4e2a\uff0c\u6211\u4eec\u518d\u8bf4\u725b\u5976\u3002\u5176\u5b9e\u6211\u662f\u4e00\u4e2a\u725b\u5976\u63a7\uff0c\u5929\u5929\u90fd\u559d\u725b\u5976\u3002\u6211\u4ece30\u5c81\u5f00\u59cb\u559d\u725b\u5976\uff0c30\u5c81\u4ee5\u524d\u6211\u4e0d\u559d&mdash;&mdash;\u6211\u662f\u4e73\u7cd6\u4e0d\u8010\u53d7\u8005\uff0c\u6211\u4e00\u559d\u725b\u5976\u5c31\u809a\u5b50\u75bc\u3001\u809a\u5b50\u80c0\uff0c\u6240\u6709\u7684\u4e0d\u8010\u53d7\u75c7\u72b6\u90fd\u5728\u6211\u8eab\u4e0a\u51fa\u73b0\u3002</p>\r\n\r\n<p>\u540e\u6765\u6211\u5c31\u505a\u4e86\u4e00\u4e2a\u8bfe\u9898\uff0c\u5c31\u662f\u6d4b\u8001\u5e74\u4eba\u7684\u9aa8\u5bc6\u5ea6\u8ddf\u559d\u725b\u5976\u548c\u8865\u9499\u7684\u5173\u7cfb\u3002\u540e\u6765\u6211\u4e5f\u53bb\u6d4b\u4e86\u4e00\u4e0b\u81ea\u5df1\u7684\u9aa8\u5bc6\u5ea6\uff0c\u6211\u624d30\u5c81\uff0c\u4f46\u6211\u89c9\u5f97\u6211\u6bd4\u90a3\u4e9b\u8001\u4eba\u597d\u4e0d\u4e86\u591a\u5c11\u3002\u4ece\u6b64\u4ee5\u540e\u6211\u5c31\u6ce8\u610f\u591a\u505a\u4e00\u4e9b\u8db3\u8ddf\u51b2\u51fb\u8fd0\u52a8\uff0c\u7136\u540e\u52a0\u4e0a\u8865\u9499&mdash;&mdash;\u5c31\u662f\u559d\u725b\u5976\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://3-im.guokr.com/VBkRxFTjomKhOheP9vrVwWzUf2wzZY1fQExP3pjuEcw4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u505a\u8fc7\u5f88\u591a\u5e74\u6709\u5173\u9aa8\u9abc\u5065\u5eb7\u7684\u7814\u7a76\uff0c\u6211\u53d1\u73b0\u5f88\u591a\u7684\u9499\u8865\u5145\u5242\u90fd\u6ca1\u6709\u725b\u5976\u8865\u9499\u7684\u6548\u679c\u597d\uff1a\u56e0\u4e3a\u725b\u5976\u542b\u4e73\u9178\u9499\uff0c\u8fd8\u542b\u916a\u86cb\u767d\u78f7\u9178\u80bd\u6765\u4fc3\u8fdb\u9499\u7684\u5438\u6536\uff0c\u800c\u4e14\u8fd8\u542b\u6709\u4f18\u8d28\u86cb\u767d\u3002</p>\r\n\r\n<p>\u4f46\u662f\u504f\u504f\u6709\u4eba\u8bf4\uff0c&ldquo;\u725b\u5976\u5c06\u4eba\u7c7b\u9001\u8fdb\u4e86\u764c\u75c7\u7684\u575f\u5893&rdquo;&mdash;&mdash;\u5929\u54ea\uff0c\u8fd9\u8fd8\u80fd\u559d\u5417\uff1f\u597d\u591a\u4eba\u88ab\u5413\u4f4f\u4e86\uff0c\u5305\u62ec&ldquo;\u9ad8\u7aef\u4eba\u7fa4&rdquo;&mdash;&mdash;\u6587\u5316\u5c42\u5c42\u6b21\u5f88\u9ad8\u7684\u3001\u804c\u4f4d\u5f88\u9ad8\u7684\u4eba\u3002</p>\r\n\r\n<p>\u8fd9\u4e2a\u89c2\u70b9\u4ece\u54ea\u91cc\u6765\u7684\uff1f</p>\r\n\r\n<p>\u6709\u4e00\u672c\u4e66\uff0c\u53eb\u300a\u6551\u547d\u996e\u98df\uff1a\u4e2d\u56fd\u5065\u5eb7\u8c03\u67e5\u62a5\u544a\u300b\uff0c\u662f\u7f8e\u56fd\u7684\u67ef\u6797&middot;\u574e\u8d1d\u5c14\uff08T. Colin campbell\uff09\u5199\u7684\u3002\u4e8b\u5b9e\u662f\u4ed6\u505a\u4e86\u4e00\u4e2a\u5b9e\u9a8c\uff1a\u628a\u9ec4\u66f2\u9709\u6bd2\u7d20\u5582\u7ed9\u5c0f\u767d\u9f20\u5403\uff0c\u8bf1\u5bfc\u51fa\u6765\u809d\u764c\uff1b\u7136\u540e\u628a\u5c0f\u767d\u9f20\u9972\u6599\u4e2d\u7684\u86cb\u767d\u8d28\u5168\u90e8\u6362\u6210\u725b\u5976\u4e2d\u7684\u916a\u86cb\u767d\u3002\u4e4b\u540e\u53d1\u73b0\uff0c\u8ddf\u90a3\u4e9b\u4e0d\u5403\u8fd9\u4e48\u591a\u916a\u86cb\u767d\u7684\u5c0f\u9f20\u6bd4\uff0c\u80bf\u7624\u957f\u5f97\u66f4\u5927\u4e86\u3002\u4ed6\u5c31\u5f97\u51fa\u4e00\u4e2a\u7ed3\u8bba\uff1a\u725b\u5976\u4e2d\u7684\u916a\u86cb\u767d\u53ef\u4ee5\u4fc3\u8fdb\u764c\u75c7\u7684\u53d1\u751f\u3002</p>\r\n\r\n<p>\u8fd9\u4e2a\u53ef\u4fe1\u5417\uff1f</p>\r\n\r\n<p>\u4e0d\u53ef\u4fe1\u3002</p>\r\n\r\n<p>\u9996\u5148\uff0c\u4eba\u548c\u8017\u5b50\u662f\u4e24\u56de\u4e8b\uff0c\u800c\u4e14\u6211\u4eec\u4eba\u4e0d\u4f1a\u6240\u6709\u9700\u8981\u7684\u86cb\u767d\u8d28\u90fd\u6765\u81ea\u4e8e\u725b\u5976\u3002\u6700\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u5b9e\u9a8c\u5bf9\u8c61\u672c\u8eab\u5c31\u662f\u764c\u75c7\u7684\u5c0f\u9f20\u3002\u6240\u4ee5\u8fd9\u4e2a\u8bf4\u6cd5\u662f\u4e0d\u53ef\u4fe1\u7684\uff0c\u8be5\u559d\u8fd8\u5f97\u559d\uff0c\u8865\u9499\u6548\u679c\u66f4\u597d\u3002</p>\r\n\r\n<h2>04&mdash;\u7ef4D\u548c\u9499\u7247\u957f\u671f\u670d\u7528\u81f4\u764c\uff1f</h2>\r\n\r\n<p><img alt=\"\" src=\"http://2-im.guokr.com/xtVUuWZrCN3QqSZbgM_6kRRkmAhioj_Ds2cE9dY7YAw4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6700\u8fd1\u53d1\u751f\u4e86\u4e00\u4ef6\u4e8b\uff1a\u300a\u60ca\u609a\uff1a\u7ef4\u751f\u7d20D\u548c\u9499\u7247\u8dcc\u4e0b\u795e\u575b\uff01\u88ab\u66dd\u957f\u671f\u670d\u7528\u81f4\u764c\uff01\u300b\uff0c\u53c8\u5413\u4f60\u4e00\u4e0b\u3002\u8fd9\u4e2a\u6587\u7ae0\u662f\u7f8e\u56fd\u6ce2\u58eb\u987f\u7684\u5854\u592b\u8328\uff08Tufts University\uff09\u5927\u5b66\uff0c\u4e00\u4e2a\u53eb\u5f20\u82b3\u82b3\uff08Fangfang Zhang\uff09\u7684\u7814\u7a76\u4eba\u5458\u8ddf\u54c8\u4f5b\u5927\u5b66\u8054\u5408\u53d1\u8868\u7684\u3002</p>\r\n\r\n<p>\u6587\u7ae0\u7ed3\u8bba\u5176\u5b9e\u662f\u8fd9\u6837\u7684\uff1a\u5728\u9499\u548c\u7ef4\u751f\u7d20D\u6444\u5165\u5145\u8db3\u7684\u60c5\u51b5\u4e0b\uff0c\u989d\u5916\u670d\u7528\u8865\u5145\u5242\u53ef\u80fd\u6ca1\u6709\u4ec0\u4e48\u597d\u5904\uff0c\u5982\u679c\u5403\u4e86\u592a\u591a\u7ef4\u751f\u7d20D\uff0c\u53ef\u80fd\u4f1a\u5bf9\u764c\u75c7\u6709\u4e00\u5b9a\u4fc3\u8fdb\u4f5c\u7528\u3002\u8fd9\u4e2a\u7ed3\u8bba\uff0c\u662f\u9f13\u52b1\u4f60\u591a\u4ece\u98df\u7269\u91cc\u6444\u98df\u4e00\u4e9b\u7ef4\u751f\u7d20\u548c\u9499\uff0c\u800c\u4e0d\u8981\u670d\u7528\u592a\u591a\u8865\u5145\u5242\u3002</p>\r\n\r\n<p>\u6709\u4eba\u5c31\u52a0\u4ee5\u6f14\u7ece\uff0c\u8bf4&ldquo;\u4f60\u770b\u6559\u6388\u90fd\u8bf4\u4e86\uff0c\u5403\u591a\u4e86\u7ef4\u751f\u7d20\u548c\u8865\u5145\u5242\u4f1a\u5f97\u764c\u75c7\u3002&rdquo;\u4e2d\u56fd\u4eba\u7ffb\u8bd1\u8d77\u6765\u5c31\u53d8\u6210&ldquo;\u8dcc\u4e0b\u795e\u575b&rdquo;&mdash;&mdash;\u4f46\u5b83\u672c\u6765\u5c31\u4e0d\u5728\u795e\u575b\u4e0a\u9762\u3002</p>\r\n\r\n<p>\u6211\u8981\u544a\u8bc9\u5927\u5bb6\uff1a\u7ef4\u751f\u7d20D\u5f88\u597d\u3002\u4fc3\u8fdb\u9499\u7684\u5438\u6536\uff0c\u5bf9\u4eba\u4ece\u5934\u5230\u811a\u90fd\u6709\u5f88\u597d\u7684\u5065\u5eb7\u6548\u76ca\uff1b\u9499\u7247\u8be5\u5403\u4e5f\u8fd8\u5f97\u5403\u3002\u6211\u4eec\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\uff0c\u6bcf\u4e94\u5e74\u5c31\u5bf9\u5168\u56fd\u4eba\u6c11\u7684\u8425\u517b\u5065\u5eb7\u72b6\u51b5\u505a\u4e00\u6b21\u76d1\u6d4b\uff0c\u53d1\u73b0\u6211\u4eec\u56fd\u4eba\u67d0\u4e9b\u5fae\u91cf\u8425\u517b\u7d20\u662f\u6444\u5165\u4e0d\u8db3\u7684\u3002</p>\r\n\r\n<p>\u6211\u4eec\u56fd\u5bb6\uff08\u5c24\u5176\u662f\u5317\u4eac\u548c\u4e1c\u5317\uff09\u7eac\u5ea6\u6bd4\u8f83\u9ad8\uff0c\u51ac\u6625\u5b63\u8282\u7684\u65f6\u5019\uff0c\u9633\u5149\u4e2d\u7684\u7d2b\u5916\u7ebf\u6ca1\u6709UVB\uff0c\u6211\u4eec\u7684\u8eab\u4f53\u76ae\u80a4\u4e0b\u76847-\u8131\u6c22\u80c6\u56fa\u9187\u5f88\u96be\u751f\u6210\u7ef4\u751f\u7d20D3\u3002\u8fd9\u4e2a\u65f6\u5019\uff0c\u6211\u5e0c\u671b\u5927\u5bb6\u80fd\u8865\u5145\u4e00\u4e9b\u7ef4\u751f\u7d20D\uff0c\u5c24\u5176\u662f\u8001\u5e74\u4eba\u3001\u5c0f\u670b\u53cb\uff0c\u8fd8\u6709\u90a3\u4e9boffice lady&mdash;&mdash;\u4e0d\u6652\u592a\u9633\u7684\u3001\u51fa\u95e8\u4e09\u6b65\u8981\u6253\u4f1e\u7684\u4eba\uff0c\u5927\u5bb6\u4e00\u5b9a\u8981\u6ce8\u610f\u8865\u5145\u3002</p>\r\n\r\n<h2>05&mdash;\u98ce\u9761\u4e00\u65f6\u7684&ldquo;\u751f\u916e\u996e\u98df&rdquo;</h2>\r\n\r\n<p>\u6211\u4eec\u518d\u6765\u770b\u8fd9\u4e2a\u98ce\u9761\u4e00\u65f6\u7684&ldquo;\u751f\u916e\u996e\u98df&rdquo;&mdash;&mdash;</p>\r\n\r\n<p><img alt=\"\" src=\"http://3-im.guokr.com/OG9CeSVSKMnDPK6hZCbA5cq_CiNSc5vaRrziU9384NY4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://3-im.guokr.com/Ed8Npd9K3umTn9VDBXe1WA1EBhELSrqW1S5K9bovd3g4BAAAXgIAAEpQ.jpg\" style=\"height:359px; width:640px\" /></p>\r\n\r\n<p>\u5728\u8425\u517b\u5b66\u754c\uff0c\u6211\u4eec\u63d0\u5021\u7684&ldquo;\u5e73\u8861\u81b3\u98df&rdquo;\uff0c\u4e09\u5927\u5b8f\u91cf\u8425\u517b\u7d20\u7684\u4f9b\u80fd\u6bd4\u662f\u8fd9\u6837\u7684\uff08\u5982\u56fe\u5de6\uff09\uff1a\u8102\u80aa\u5360\u523020%-30%\uff0c\u86cb\u767d\u8d2810%-15%\uff0c\u78b3\u6c34\u5316\u5408\u7269&mdash;&mdash;\u5c31\u662f\u6211\u4eec\u7684\u4e3b\u98df\uff0c\u8981\u5360\u523050%\u523065%\u3002</p>\r\n\r\n<p>\u751f\u916e\u996e\u98df\u5462\uff1f\u770b\u770b\u53f3\u56fe\uff0c\u8102\u80aa\u4f9b\u80fd\u6bd4\u5360\u523080%\uff0c\u86cb\u767d\u8d28\u5360\u523015%\uff0c\u8fd8OK\uff0c\u4f46\u78b3\u6c34\u5316\u5408\u7269\u53ea\u67095%\uff0c\u76f8\u5f53\u4e8e\u4f60\u6839\u672c\u5c31\u4e0d\u80fd\u5403\u4e3b\u98df&mdash;&mdash;5%\u6298\u5408\u6210\u4e3b\u98df\u53ea\u670925\u514b\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u6240\u6709\u7684\u4f9b\u80fd\u90fd\u8981\u7531\u8102\u80aa\u6765\u63d0\u4f9b\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://2-im.guokr.com/CylZboLp048aJG7_d1PbyduJ4i739NMtjS5rVkXlL384BAAAXgIAAEpQ.jpg\" style=\"height:359px; width:640px\" /></p>\r\n\r\n<p>\u5de6\u8fb9\u8fd9\u4e2a\u4eba\u5927\u5bb6\u90fd\u8ba4\u8bc6\uff0c\u827a\u4eba\u949f\u4e3d\u7f07\u3002\u5979\u8bf4\u5e74\u524d\u5e26\u7740\u5979\u7684\u516c\u516c\u5a46\u5a46\u8fd8\u6709\u5979\u7238\u7238\uff0c\u53bb\u6cf0\u56fd\u7684\u82cf\u6885\u5c9b\u6392\u6bd2\uff0c\u5929\u5929\u5c31\u559d\u6930\u5b50\u6cb9\uff0c\u559d\u4e8611\u5929\u3002\u5979\u5728\u65b0\u6d6a\u5fae\u535a\u4e0a\u9762\u6709400\u591a\u4e07\u7c89\u4e1d\uff0c\u5f71\u54cd\u5f88\u4e0d\u597d\u3002\u4f46\u5176\u5b9e\u7c89\u4e1d\u5927\u90e8\u5206\u90fd\u6279\u8bc4\u5979\u7684\u89c2\u70b9\uff0c\u6240\u4ee5\u6211\u89c9\u5f97\u53ef\u4ee5\u5b89\u5fc3\u4e86\u3002</p>\r\n\r\n<p>\u53f3\u8fb9\u8fd9\u4e2a\u4eba\uff0c\u5361\u4f26&middot;\u7c73\u6b47\u5c14\u65af\uff08Karin Michels\uff09\uff0c\u5979\u6765\u81ea\u5fb7\u56fd\u7684\u5f17\u83b1\u5821\u5927\u5b66\uff08Universit&auml;t Freiburg\uff09\uff0c\u662f\u9884\u9632\u548c\u80bf\u7624\u6d41\u884c\u75c5\u5b66\u7814\u7a76\u6240\u7684\u6240\u957f\uff1b\u4e5f\u662f\u54c8\u4f5b\u5927\u5b66\u9648\u66fe\u7199\u516c\u5171\u536b\u751f\u5b66\u9662\uff08Harvard T.H.Chan School of Public Health\uff09\u7684\u6559\u6388\u3002</p>\r\n\r\n<p>\u5979\u5728\u7f51\u4e0a\u53d1\u8868\u4e86\u4e00\u4e2a50\u5206\u949f\u7684\u5fb7\u8bed\u6f14\u8bb2\uff0c\u8bf4\u6930\u5b50\u6cb9\u6839\u672c\u5c31\u662f\u4e2a\u6bd2\u836f\uff0c\u6bd4\u732a\u6cb9\u8fd8\u574f\uff0c\u4e0d\u8981\u5403\u8fd9\u4e2a\u3002\u56e0\u4e3a\u6930\u5b50\u6cb9\u91cc\u8fb982%\u662f\u9971\u548c\u8102\u80aa\u9178\uff0c\u800c\u732a\u6cb9\u91cc\u8fb9\u7684\u9971\u548c\u8102\u80aa\u9178\u53ea\u670940%\u5de6\u53f3\uff0c\u53e6\u5916\u8fd8\u6709\u4e0d\u523040%\u7684\u4e0d\u9971\u548c\u8102\u80aa\u9178\uff08\u5373\u5355\u4e0d\u9971\u548c\u8102\u80aa\u9178\uff0c\u4e5f\u5373\u6cb9\u9178\uff09\u3002</p>\r\n\r\n<p>\u7f8e\u56fd\u7684\u5fc3\u810f\u534f\u4f1aAHA\uff08American Heart Association\uff09\uff0c\u57282017\u5e74\u4e5f\u5f97\u51fa\u8fc7\u6b64\u7ed3\u8bba\u3002\u4e8b\u5b9e\u4e0a\uff0c\u8981\u9884\u9632\u5fc3\u810f\u75c5\uff0c\u9971\u548c\u8102\u80aa\u9178\u7684\u91cf\u5e94\u8be5\u63a7\u5236\u5728\u4f9b\u80fd\u6bd4\u76846%\u4ee5\u4e0b\u3002</p>\r\n\r\n<p>\u6211\u7b97\u4e86\u4e00\u4e0b\uff0c\u6839\u636e\u4e00\u4e2a\u4eba\u6bcf\u5929\u54032000\u5343\u5361\u80fd\u91cf\u7684\u6807\u51c6\uff0c\u90a3\u4e48\u9971\u548c\u8102\u80aa\u9178\u4e0d\u80fd\u8d85\u8fc711\u514b\u3002\u5982\u679c\u8bf4\u5403\u6930\u5b50\u6cb9\uff0c\u5927\u6982\u5403\u4e00\u52fa\u5c31\u80fd\u591f\u63d0\u4f9b11\u514b\u7684\u9971\u548c\u8102\u80aa\u9178\uff0c\u5c31\u591f\u591a\u4e86\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://1-im.guokr.com/YvuPld7ISDPyFnvkyYkReMW8M1aEgu6_gkjbzglceM04BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u5927\u5bb6\u8bf7\u6ce8\u610f\uff0c&ldquo;\u751f\u916e\u996e\u98df&rdquo;\u600e\u4e48\u6765\u7684\uff1f</p>\r\n\r\n<p>\u5176\u5b9e\uff0c\u5728\u53e4\u5e0c\u814a\u5e0c\u6ce2\u514b\u62c9\u5e95\u65f6\u4ee3\uff0c\u5c31\u6709&ldquo;\u751f\u916e\u996e\u98df&rdquo;&mdash;&mdash;\u5f53\u65f6\u53eb&ldquo;\u9965\u997f\u7597\u6cd5&rdquo;\uff0c\u7528\u6765\u6cbb\u7597\u766b\u75eb\u3002\u4e0a\u4e2a\u4e16\u7eaa\u768420\u5e74\u4ee3\uff0c\u7f8e\u56fd\u7684\u6885\u5965\u8bca\u6240\uff08Mayo clinic\uff09\u8fdb\u4e00\u6b65\u53d1\u73b0\uff0c&ldquo;\u751f\u916e\u996e\u98df&rdquo;\u5bf9\u513f\u7ae5\u96be\u6cbb\u6027\u7684\u766b\u75eb\u6709\u4e00\u5b9a\u7684\u6cbb\u7597\u6548\u679c\u3002</p>\r\n\r\n<p>\u539f\u7406\u662f\uff1a\u5e73\u65f6\u8eab\u4f53\u6240\u9700\u80fd\u91cf\u4e3b\u8981\u7531\u8461\u8404\u7cd6\u6765\u63d0\u4f9b\uff0c\u5982\u679c\u4e0d\u5403\u78b3\u6c34\u5316\u5408\u7269\uff0c\u5c31\u6539\u53d8\u4e86\u8111\u7684\u80fd\u91cf\u4ee3\u8c22\u65b9\u5f0f\uff0c\u6539\u53d8\u4e86\u7ec6\u80de\u7684\u7279\u6027\uff0c\u964d\u4f4e\u5174\u594b\u6027\u548c\u7f13\u51b2\u766b\u75eb\u6837\u7684\u653e\u7535\uff0c\u6539\u53d8\u4e86\u795e\u7ecf\u9012\u8d28\u548c\u7a81\u89e6\u4f20\u9012\u7684\u529f\u80fd\u3002\u8fd9\u79cd\u7597\u6cd5\u73b0\u5728\u5f88\u591a\u56fd\u5bb6\u8fd8\u5728\u7528\uff0c\u4e5f\u5305\u62ec\u6211\u4eec\u56fd\u5bb6\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://1-im.guokr.com/00uiuf9x5AbYPq1dOVqQfPpUVJNFD56LpJZQjP9rCg44BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4f46\u662f\u5f88\u591a\u4eba\u5c31\u5229\u7528\u8fd9\u4e2a\u673a\u4f1a\u53d1\u8d22\u3002\u5927\u5bb6\u770b\u8fd9\u4e2a\u4eba\uff0c\u53eb\u7f57\u4f2f\u7279&middot;\u963f\u7279\u91d1\u65af\uff08Robert Atkins\uff09\uff0c\u4ed6\u5176\u5b9e\u4e0d\u662f\u641e\u8425\u517b\u7684\uff0c\u662f\u4e2a\u4e34\u5e8a\u5927\u592b\u3002\u4ed6\u4ee5\u5f88\u5feb\u7684\u901f\u5ea6\u6539\u826f\u4e86\u4e00\u7248&ldquo;\u751f\u916e\u996e\u98df&rdquo;\uff0c\u53d8\u6210&ldquo;\u963f\u7279\u91d1\u65af\u51cf\u80a5\u6cd5&rdquo;\uff1a\u628a\u86cb\u767d\u8d28\u4f9b\u80fd\u6bd4\u7a0d\u5fae\u63d0\u9ad8\u4e00\u70b9\uff0c\u7136\u540e\u628a\u8102\u80aa\u7684\u4e0b\u964d\u4e00\u70b9\u70b9\uff0c\u78b3\u6c34\u5316\u5408\u7269\u7684\u518d\u52a0\u4e00\u70b9\u70b9\u3002\u6211\u77e5\u9053\u597d\u591a\u4eba\u90fd\u5728\u7528\u8fd9\u79cd\u65b9\u6cd5\u51cf\u80a5\u3002</p>\r\n\r\n<p>\u963f\u7279\u91d1\u65af\u7684\u516c\u53f8\u4ece\u96f6\u70b9\u8d77\u6b65\uff0c\u5230\u4ed6\u53bb\u4e16\u7684\u524d\u4e00\u5e74\uff0c\u8425\u4e1a\u989d\u5df2\u7ecf\u8fbe\u5230\u4e861\u4ebf\u591a\u7f8e\u91d1\u3002\u4f46\u662f\u4ed6\u5f88\u4e0d\u5e78\uff0c\u4ed6\u5728\u51b0\u9762\u4e0a\u6454\u4e86\u4e00\u8de4\uff0c\u53bb\u4e16\u4e86\u3002\u5f53\u65f6\u7684\u7ebd\u7ea6\u5e02\u957f\u8fc8\u514b\u5c14&middot;\u5e03\u9686\u4f2f\u683c\uff08Michael Bloomberg\uff09\u8ba4\u4e3a\uff0c\u4ed6\u7684\u6b7b\u56e0\u53ef\u80fd\u662f\u80a5\u80d6\u5bfc\u81f4\u5fc3\u810f\u8d1f\u62c5\u8fc7\u91cd\u3002</p>\r\n\r\n<p>\u6211\u4eec\u73b0\u5728\u56fd\u5185\u6709\u5f88\u591a\u8425\u9500\u53f7\uff0c\u4ece\u56fd\u5916\u8db8\u6765\u4e00\u4e9b\u4e0d\u90a3\u4e48\u79d1\u5b66\u7684\u77e5\u8bc6\uff0c\u53d1\u5728\u81ea\u5df1\u7684\u516c\u4f17\u53f7\u91cc\u3002\u63a8\u9500\u597d\u591a\u4e1c\u897f\uff0c\u8ba9\u4f60\u53bb&ldquo;\u4e70\u4e70\u4e70&rdquo;&mdash;&mdash;\u4e00\u70b9\u70b9\u6930\u5b50\u6cb9\uff0c\u5356\u5f97\u5f88\u8d35\uff0c\u5927\u5bb6\u5343\u4e07\u4e0d\u8981\u4e0a\u5f53\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://3-im.guokr.com/tohuG6ehrLrlMj7-QBuLj1EIwSqs7OmE99bR_N0uTDo4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u7531\u4e8e\u4e2d\u56fd&ldquo;\u751f\u916e\u996e\u98df&rdquo;\u7684\u4e13\u4e1a\u4eba\u5458\u6570\u91cf\u6709\u9650\uff0c\u6240\u4ee5\u5b83\u4e0d\u5b9c\u5168\u9762\u63a8\u5e7f\uff0c\u5176\u9002\u5e94\u75c7\u6682\u65f6\u5b9a\u4e3a\uff1aBMI\u5927\u4e8e28\u3001\u809d\u80be\u529f\u80fd\u6b63\u5e38\u7684\u5355\u7eaf\u6027\u80a5\u80d6\u4eba\u7fa4\u3002\u6b64\u5916\uff0c&ldquo;\u751f\u916e\u996e\u98df&rdquo;\u8fd8\u6ca1\u6709\u957f\u671f\u7684\u6548\u679c\u68c0\u9a8c\uff0c\u6240\u4ee5\u5982\u679c\u7cd6\u5c3f\u75c5\u4eba\u60f3\u8bd5\u7684\u8bdd\uff0c\u4e00\u5b9a\u8981\u53bb\u533b\u9662\uff0c\u5207\u52ff\u64c5\u81ea\u5c1d\u8bd5\u3002\u800c\u4e14\u5927\u5bb6\u8981\u8bb0\u4f4f\uff0c&ldquo;\u751f\u916e\u996e\u98df&rdquo;\u662f\u4e00\u4e2a\u6cbb\u7597\u7684\u996e\u98df\uff0c\u4e0d\u662f\u4e00\u5929\u5230\u665a\u53ef\u4ee5\u7528\u7684\u3002</p>\r\n\r\n<p>\u78b3\u6c34\u5316\u5408\u7269\u6709\u5f88\u591a\uff0c\u6bd4\u65b9\u8bf4\u5168\u8c37\u7c7b\uff0c\u8fd8\u6709\u4e00\u4e9b\u8c46\u7c7b\uff0c\u5bf9\u6211\u4eec\u5f88\u597d\u3002\u90a3\u5230\u5e95\u5403\u591a\u5c11\u5408\u9002\uff1f50%\u5de6\u53f3\u7684\u4f9b\u80fd\u6bd4\uff0c\u4e5f\u5c31\u662f\u4e00\u5929\u5403\u534a\u65a4\u5230\u516b\u4e24\u5de6\u53f3\u7684\u4e3b\u98df&mdash;&mdash;\u50cf\u6211\u8fd9\u6837\u7684\u5973\u6027\u53ef\u80fd\u534a\u65a4\u5c31\u591f\u4e86\u3002\u6211\u6307\u7684\u662f\u751f\u91cd\uff0c\u8fd9\u6837\u4eba\u7684\u6b7b\u4ea1\u7387\u662f\u6700\u4f4e\u7684\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://3-im.guokr.com/EOLG5aKWyKQ8JPc2_JUjHm4NvLLXcGfEoVDQf6XKnpA4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4eba\u4e00\u8f88\u5b50\u8981\u540360-70\u5428\u98df\u7269\uff0c\u4e00\u5929\u8981\u540340\u591a\u79cd\u8425\u517b\u6210\u5206\u3002\u5982\u679c\u60f3\u6d3b\u5f97\u5065\u5eb7\u3001\u957f\u5bff\uff0c\u90a3\u4f60\u6240\u9700\u7684\u8425\u517b\u6210\u5206\uff0c\u4e00\u79cd\u4e5f\u4e0d\u80fd\u5c11\uff1b\u5c11\u4e86\u54ea\u4e00\u6837\uff0c\u8eab\u4f53\u5c31\u4f1a\u51fa\u73b0\u72b6\u51b5\u3002\u6240\u4ee5\u90a3\u4e9b\u91c7\u7528&ldquo;\u751f\u916e\u996e\u98df&rdquo;\u7684\u4eba\uff0c\u4ed6\u4f1a\u55dc\u7761\u3001\u4f1a\u75b2\u5026\uff0c\u547c\u5438\u65f6\u5473\u9053\u4e0d\u597d\u95fb\uff0c\u8eab\u4e0a\u4e5f\u4f1a\u53d1\u51fa\u4f53\u81ed\uff0c\u8fd8\u4f1a\u4fbf\u79d8\uff0c\u8981\u7ecf\u8fc7\u5f88\u957f\u7684\u4e00\u6bb5\u9002\u5e94\u671f\u3002\u6211\u4eec\u8fd8\u662f\u8981\u7167\u7740\u5730\u4e2d\u6d77\u996e\u98df\uff08Mediterranean diet\uff09\u3001\u5e73\u8861\u81b3\u98df\u5b9d\u5854\u53bb\u5403\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://1-im.guokr.com/GspR5V2jqiw_OaQZTFExfU3fFLTVPPt0Medl4Q_ulyk4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u81ea\u5df1\u7ed9\u5927\u5bb6\u603b\u7ed3\u4e86\u4e00\u4e2a\u5e73\u8861\u81b3\u98df\u7684&ldquo;\u5341\u4e2a\u62f3\u5934\u539f\u5219&rdquo;\uff08\u5982\u56fe\u5de6\uff09\uff1a\u6bcf\u4eba\u6bcf\u5929\u5403\u7684\u6240\u6709\u7684\u98df\u7269\uff08\u98df\u7269\u751f\u91cd\uff0c\u5228\u53bb\u4e86\u4e0d\u53ef\u98df\u7528\u7684\u90e8\u5206\uff09\u52a0\u8d77\u6765\uff0c\u5c31\u662f\u5341\u4e2a\u62f3\u5934\u5927\u5c0f\uff0c\u6211\u6bcf\u5929\u5c31\u7167\u7740\u8fd9\u4e2a\u6807\u51c6\u53bb\u5403\u3002\u8fd9\u91cc\u8fd8\u6709\u4e00\u4e2a\u7f8e\u56fd\u7684\u5e73\u8861\u81b3\u98df\u9910\u76d8\uff08\u5982\u56fe\u53f3\u4e0a\uff09\uff0c\u4e5f\u6709\u4e00\u4e2a\u6211\u4eec\u4e2d\u56fd\u7684\uff08\u5982\u56fe\u53f3\u4e0b\uff09\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://3-im.guokr.com/mNAG9lfAhFHt_XxRh6i57tGIZUF4an5czqITH8CGKs44BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8fd8\u6709\uff0c\u4f60\u60f3\u8ba9\u81ea\u5df1\u57fa\u7840\u4ee3\u8c22\u589e\u9ad8\uff0c\u90a3\u5c31\u8981\u505a\u8fd0\u52a8\uff0c\u4f7f\u4f60\u7684\u4f53\u6210\u5206\u53d1\u751f\u4e00\u4e2a\u6539\u53d8\uff1a\u6bcf\u589e\u52a01kg\u808c\u8089\uff0c\u673a\u4f53\u5c31\u589e\u52a0110kcal\u7684\u80fd\u91cf\u6d88\u8017\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"http://1-im.guokr.com/22bAeWUo2xhH_KhwFo47P0vYW5SD7ksILEMOmknVfJo4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u628a\u6211\u4eec\u56fd\u5bb6\u57281930\u5e74\u4ee5\u524d\u51fa\u751f\u7684\u8425\u517b\u4e13\u5bb6\u90fd\u5217\u4e86\u51fa\u6765\uff0c\u90fd\u662f\u975e\u5e38\u6709\u540d\u7684\u5927\u5496\u3002\u5927\u5bb6\u53ef\u4ee5\u770b\u5230\uff0c\u4ed6\u4eec\u90fd\u6d3b\u5f97\u6bd4\u8f83\u957f\u3002\u6700\u957f\u7684\u662f\u90d1\u96c6\u6559\u6388\uff0c110\u5c81\u3002\u53f3\u8fb9\u8fd9\u4e24\u4f4d\uff0c\u4e00\u4f4d\u662f\u6211\u7684\u540c\u4e8b\uff0c\u53e6\u4e00\u4f4d\u662f\u6211\u656c\u91cd\u7684\u957f\u8f88\uff0c\u4ed6\u4eec\u73b0\u5728\u7684\u5e74\u9f84\u5206\u522b\u662f102\u5468\u5c81\u548c103\u5468\u5c81\u3002\u4ed6\u4eec\u8fd8\u5065\u5728\uff0c\u601d\u7ef4\u6e05\u6670\uff0c\u751f\u6d3b\u80fd\u591f\u81ea\u7406\u3002</p>\r\n\r\n<p>\u5982\u679c\u4f60\u60f3\u50cf\u8425\u517b\u4e13\u5bb6\u6d3b\u5f97\u4e00\u6837\u957f\uff0c\u5c31\u8981\u5747\u8861\u81b3\u98df\u3002\u5927\u5bb6\u4e00\u5b9a\u8981\u542c\u6211\u7684\u5efa\u8bae\uff0c\u5e73\u8861\u81b3\u98df\uff0c\u6bcf\u5929&ldquo;\u5341\u4e2a\u62f3\u5934\u539f\u5219&rdquo;\u3002</p>\r\n\r\n<p>\u8c22\u8c22\u5927\u5bb6\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://1-im.guokr.com/eP3Sqcojd59BMgM1twuycS4Ok7SFNq4_ZksJCzfDHJ84BAAAUAIAAEpQ.jpg\" style=\"height:351px; width:640px\" />\u6f14\u8bb2\u5609\u5bbe\u4f55\u4e3d\uff1a\u300a\u751f\u916e\u996e\u98df\u51cf\u80a5\uff1f\u78b1\u6027\u98df\u7269\u517b\u751f\uff1f&hellip;&hellip;\u522b\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u9a97\u4e86\uff01\u300b</p>", "date_created": "2019-05-23T09:56:57.833123+08:00", "date_modified": "2019-05-23T11:24:14.061769+08:00", "date_published": "2019-05-23T11:24:17+08:00", "id": 452085, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "health", "replies_count": 12, "small_image": "https://1-im.guokr.com/qMCMceg1QGiK1dQUzQ2YkXJYsa7_kk7x2XYYkqiokL84BAAAugIAAEpQ.jpg", "subject": {"articles_count": 615, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "food", "minisite_key": "health", "name": "\u98df\u7269", "sort_score": 6, "subject_type": "normal"}, "subject_key": "food", "summary": "\u90a3\u4e9b\u5ffd\u60a0\u8fc7\u4f60\u7684\u8425\u517b\u8c23\u8a00\uff0c\u8bf7\u628a\u5b83\u4eec\u6307\u8ba4\u51fa\u6765", "text": "\u672c\u6587\u4e3a2019\u5e744\u670827\u65e5\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u671f\u2014\u2014\u5bf9\u8c23\u8a00\u8bf4\u201c\u4e0d\u201d| \u4f55\u4e3d \u6f14\u8bb2\u5b9e\u5f55\uff1a\u9ec4\u74dc\u548c\u82b1\u751f\u4e0d\u80fd\u4e00\u8d77\u5403\uff1f\u9178\u6027\u98df\u7269\u5bf9\u5065\u5eb7\u6709\u5bb3\uff1f\u5173\u4e8e\u8425\u517b\u7684\u8c23\u8a00\uff0c\u4f60\u8e29\u96f7\u4e86\u5417\uff1f\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u7b2c\u5341\u671f\u6f14\u8bb2\u73b0\u573a\uff0c\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7814\u7a76\u5458\u4f55\u4e3d\u4e0d\u4ec5\u5e2e\u52a9\u6211\u4eec\u7c89\u788e\u4e86\u5f88\u591a\u65e5\u5e38\u542c\u5230\u7684\u8c23\u8a00\uff0c\u8fd8\u624b\u628a\u624b\u6559\u5927\u5bb6\u5065\u5eb7\u751f\u6d3b\u7684\u201c\u5341\u4e2a\u62f3\u5934\u201d\u539f\u5219\u3002\u6211\u662f\u6765\u81ea\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7684\u4f55\u4e3d\uff0c\u4e13\u95e8\u7814\u7a76\u8425\u517b\u5b66\u3002\u5b66\u8425\u517b\u6709\u597d\u5904\u2014\u2014\u4ec0\u4e48\u597d\u5904\u5462\uff1f\u9996\u5148\u5bf9\u81ea\u5df1\u597d\uff0c\u81ea\u5df1\u53d7\u76ca\uff1b\u5176\u6b21\uff0c\u5bb6\u4eba\u53d7\u76ca\uff1b\u518d\u5176\u6b21\uff0c\u8ba9\u6c11\u4f17\u53d7\u76ca\u3002\u6f14\u8bb2\u5609\u5bbe\u4f55\u4e3d\uff1a\u300a\u751f\u916e\u996e\u98df\u51cf\u80a5\uff1f\u78b1\u6027\u98df\u7269\u517b\u751f\uff1f\u2026\u2026\u522b\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u9a97\u4e86\uff01\u300b\u4eca\u5929\u6211\u5c31\u60f3\u5206\u4eab\u4e00\u4e9b\u8425\u517b\u77e5\u8bc6\uff0c\u5e2e\u52a9\u5927\u5bb6\u8fa8\u522b\u4e00\u4e9b\u8c23\u8a00\uff0c\u4e0d\u8981\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u6b3a\u9a97\u300201\u2014\u98df\u7269\u76f8\u751f\u4e0e\u76f8\u514b\uff1f\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u5f88\u591a\u4eba\u90fd\u542c\u8fc7\u4e00\u4e9b\u201c\u98df\u7269\u76f8\u751f\u76f8\u514b\u201d\u7684\u8bf4\u6cd5\u3002\u8fd9\u79cd\u8bf4\u6cd5\u7531\u6765\u5df2\u4e45\u3002\u6211\u5148\u7ed9\u5927\u5bb6\u5206\u4eab\u4e00\u4e2a\u5c0f\u6545\u4e8b\u2014\u2014\u5341\u6765\u5e74\u524d\uff0c\u6211\u53bb\u7f8e\u56fd\u4e00\u4e2a\u5e38\u9752\u85e4\u5927\u5b66\u8fdb\u4fee\u3002\u8d76\u4e0a\u5047\u671f\uff0c\u5c31\u548c\u670b\u53cb\u76f8\u7ea6\u5357\u90e8\u81ea\u9a7e\u6e38\u53bb\u5927\u6cbc\u6cfd\u5730\u56fd\u5bb6\u516c\u56ed\uff08Everglades National Park\uff09\uff0c\u90a3\u662f\u4e2a\u5f88\u539f\u751f\u6001\u7684\u516c\u56ed\uff0c\u91cc\u9762\u53ef\u4ee5\u770b\u9cc4\u9c7c\u3002\u5728\u6765\u7684\u8def\u4e0a\uff0c\u670b\u53cb\u7684\u5148\u751f\u5f00\u8f66\u3002\u6211\u548c\u5979\u5750\u5728\u540e\u9762\uff0c\u4e00\u8fb9\u804a\u5929\u4e00\u8fb9\u5403\u82b1\u751f\u7c73\u2014\u2014\u8fd9\u82b1\u751f\u7c73\u662f\u524d\u4e00\u5929\u665a\u4e0a\u6211\u4eec\u81ea\u5df1\u716e\u597d\u7684\u3002\u5230\u4e86\u516c\u56ed\u95e8\u53e3\uff0c\u770b\u89c1\u6709\u5356\u9ec4\u74dc\u7684\uff0c\u662f\u7f8e\u56fd\u519c\u6c11\u79cd\u7684\u9ec4\u74dc\u3002\u6211\u4eec\u6beb\u4e0d\u72b9\u8c6b\u5730\u4e70\u4e86\u3002\u670b\u53cb\u592b\u59bb\u5728\u7f8e\u56fd\u5f85\u7684\u65f6\u95f4\u957f\uff0c\u4ed6\u4eec\u89c9\u5f97\u7f8e\u56fd\u7684\u98df\u54c1\u5b89\u5168\u503c\u5f97\u4fe1\u8d56\uff1b\u6240\u4ee5\u4e70\u4e86\u9ec4\u74dc\uff0c\u62ff\u9910\u5dfe\u7eb8\u64e6\u4e86\u64e6\u5c31\u5403\u4e86\u3002\u6211\u7531\u4e8e\u4ece\u4e8b\u8425\u517b\u98df\u54c1\u5b89\u5168\u5de5\u4f5c\uff0c\u6ca1\u6d17\u7684\u9ec4\u74dc\u603b\u89c9\u5f97\u6709\u70b9\u5fd0\u5fd1\uff0c\u5c31\u62ff\u51fa\u4e00\u628a\u5c0f\u5200\u7ed9\u524a\u4e86\u76ae\uff0c\u4e5f\u5403\u4e86\u3002\u4e00\u8def\u8fdb\u53bb\u73a9\uff0c\u5c31\u4e0d\u8868\u4e86\u3002\u51e0\u4e2a\u5c0f\u65f6\u4ee5\u540e\u51fa\u6765\uff0c\u6211\u670b\u53cb\u8bf4\u4e0d\u884c\uff0c\u809a\u5b50\u5f88\u75bc\u3001\u80a0\u9e23\u97f3\u9635\u9635\uff0c\u5f97\u627e\u5395\u6240\u53bb\u3002\u6ca1\u529e\u6cd5\uff0c\u5f97\u8d76\u7d27\u3002\u867d\u7136\u6cb9\u633a\u6ee1\u7684\uff0c\u4f46\u8fd8\u662f\u5f97\u5954\u52a0\u6cb9\u7ad9\u2014\u2014\u53ea\u6709\u52a0\u6cb9\u7ad9\u624d\u6709\u5395\u6240\u3002\u5979\u51fa\u6765\u4e86\u8bf4\uff0c\u201c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u9ec4\u74dc\u548c\u82b1\u751f\u7c73\u6839\u672c\u5c31\u4e0d\u80fd\u4e00\u8d77\u5403\uff0c\u662f\u76f8\u514b\u7684\u3002\u4f60\u770b\u6211\u8fd9\u8fd8\u6709\u4e00\u672c\u4e66\u3002\u201d\u5979\u786e\u5b9e\u76f8\u4fe1\u201c\u98df\u7269\u76f8\u514b\u201d\u8fd9\u4e2a\u4e1c\u897f\uff0c\u56e0\u4e3a\u6211\u670b\u53cb\u5728\u7f8e\u56fd\u662f\u505a\u9488\u7078\u7684\u3002\u5979\u7684\u8bdd\u97f3\u521a\u843d\uff0c\u5979\u5148\u751f\u8bf4\u201c\u4e0d\u884c\u4e0d\u884c\uff0c\u6211\u4e5f\u809a\u5b50\u75bc\u3002\u201d\u4e5f\u4e0a\u5395\u6240\u53bb\u4e86\u3002\u6211\u5f88\u5947\u602a\uff0c\u201c\u4f60\u5148\u751f\u4e0d\u662f\u6ca1\u6709\u5403\u82b1\u751f\u7c73\u5417\uff1f\u6211\u82b1\u751f\u7c73\u548c\u9ec4\u74dc\u90fd\u5403\u4e86\uff0c\u4e3a\u4ec0\u4e48\u6211\u6ca1\u6709\u4e8b\u3002\u201d\u7ed3\u679c\u90a3\u4e00\u8def\u4e0a\uff0c\u5c31\u4e0d\u505c\u5730\u4e0a\u52a0\u6cb9\u7ad9\uff0c\u4e0d\u505c\u5730\u627ebathroom\uff0c\u73a9\u4e5f\u6ca1\u73a9\u597d\u3002\u4f60\u4eec\u60f3\u60f3\uff0c\u95ee\u9898\u51fa\u5728\u54ea\uff1f\u9ec4\u74dc\uff0c\u6ca1\u6d17\u7684\u9ec4\u74dc\u3002\u5176\u5b9e\u6709\u5173\u201c\u98df\u7269\u76f8\u514b\u201d\u7684\u8bdd\u9898\uff0c80\u591a\u5e74\u524d\uff0c\u5c31\u5728\u5357\u4eac\u5e7f\u4e3a\u6d41\u4f20\uff1a\u201c\u5403\u9999\u8549\u518d\u5403\u828b\u5934\u4f1a\u76f8\u514b\u201d\uff0c\u8fd8\u6709\u201c\u9999\u8471\u548c\u871c\u4e0d\u80fd\u5171\u98df\u201d\uff0c\u751a\u81f3\u8fde\u7ef4\u751f\u7d20C\u548c\u867e\u4e5f\u4e0d\u80fd\u4e00\u5757\u5403\u3002\u5f53\u65f6\u5357\u4eac\u5927\u5b66\u6709\u4e00\u4f4d\u79d1\u5b66\u5bb6\uff0c\u90d1\u96c6\u6559\u6388\u3002\u4ed6\u4e5f\u662f\u6211\u4eec\u8425\u517b\u754c\u7684\u4e00\u4e2a\u5178\u8303\u2014\u2014\u6d3b\u5f97\u6700\u957f\u7684\u5178\u8303\u3002\u8001\u4eba\u5bb6\u4ece1900\u5e74\u6d3b\u5230\u4e862010\u5e74\uff0c\u6d3b\u4e86110\u5468\u5c81\u3001111\u865a\u5c81\u3002\u5f53\u65f6\uff0c\u8001\u4eba\u5bb6\u6536\u96c6\u4e86\u5f88\u591a\u201c\u98df\u7269\u76f8\u514b\u201d\u7684\u8bf4\u6cd5\uff0c\u6709180\u591a\u5bf9\u3002\u4ed6\u9009\u62e9\u4e86\u5f88\u591a\u52a8\u7269\uff0c\u6709\u8001\u9f20\u3001\u7334\u5b50\u3001\u72d7\uff0c\u8fdb\u884c\u5b9e\u9a8c\uff0c\u628a\u8fd9\u4e9b\u8c23\u4f20\u201c\u76f8\u514b\u201d\u7684\u98df\u7269\u5582\u7ed9\u5b83\u4eec\u5403\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5403\u4e86\u597d\u591a\u3002\u540e\u6765\u53d1\u73b0\uff0c\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\uff1b\u52a8\u7269\u548c\u4eba\u90fd\u597d\u7740\u5462\u3002\u540e\u6765\u4ed6\u5c31\u5f97\u51fa\u4e00\u4e2a\u7ed3\u8bba\uff1a\u98df\u7269\u76f8\u514b\u53ef\u80fd\u662f\u56e0\u4e3a\u672c\u8eab\u5403\u7684\u4eba\u662f\u8fc7\u654f\u4f53\u8d28\u3002\u73b0\u5728\u6211\u4eec\u8fd8\u6709\u4e00\u4e2a\u65b0\u7684\u8bf4\u6cd5\uff0c\u4e0d\u77e5\u9053\u5927\u5bb6\u542c\u6ca1\u542c\u8fc7\uff1f\u53eb\u201c\u98df\u7269\u4e0d\u8010\u53d7\u201d\u2014\u2014\u6709\u4e9b\u98df\u7269\uff0c\u6709\u4eba\u5403\u5b8c\u4ee5\u540e\u5c31\u4f1a\u809a\u5b50\u75bc\u3001\u62c9\u809a\u5b50\u3002\u8fd8\u6709\u5c31\u662f\u98df\u7269\u4e0d\u5e72\u51c0\u2014\u2014\u6bd4\u5982\u6211\u521a\u624d\u8bf4\uff0c\u6211\u670b\u53cb\u5403\u4e86\u4e0d\u5e72\u51c0\u7684\u9ec4\u74dc\u3002\u8fd8\u6709\u4e00\u79cd\u53ef\u80fd\uff0c\u5c31\u662f\u98df\u7269\u672c\u8eab\u6709\u6bd2\u3002\u6bd4\u65b9\u6709\u4eba\u8bf4\uff0c\u9999\u8471\u548c\u8702\u871c\u4e0d\u80fd\u6401\u4e00\u5757\u5403\uff0c\u4f1a\u6b7b\u4eba\u7684\u3002\u4e5f\u6709\u53ef\u80fd\uff0c\u4e3a\u4ec0\u4e48\uff1f\u56e0\u4e3a\u8fd9\u4e2a\u8702\u871c\u672c\u6765\u5c31\u662f\u7531\u6709\u6bd2\u7684\u82b1\u871c\u917f\u9020\u51fa\u6765\u7684\u3002\u8fc7\u53bb\u7684\u5e74\u4ee3\uff0c\u6211\u4eec\u6ca1\u6709\u9ad8\u6df1\u7684\u79d1\u5b66\u6280\u672f\uff0c\u4e5f\u6ca1\u6709\u663e\u5fae\u955c\u53ef\u4ee5\u53bb\u770b\uff0c\u751a\u81f3\u53d6\u4e2a\u7caa\u4fbf\uff0c\u600e\u4e48\u67e5\u4e5f\u4e0d\u77e5\u9053\u3002\u540c\u6837\u7684\u4e8b\u53d1\u751f\u4e24\u904d\uff0c\u90ce\u4e2d\u6216\u8005\u6742\u5b66\u8005\u5c31\u8bb0\u5f55\u4e0b\u6765\u4e86\u2014\u2014\u5f88\u591a\u4eba\u8bf4\u674e\u65f6\u73cd\u5b9e\u9645\u4e0a\u662f\u4e00\u4e2a\u6742\u5b66\u8005\uff0c\u5404\u4e2a\u65b9\u9762\u7684\u77e5\u8bc6\u90fd\u61c2\uff0c\u4ed6\u5199\u7684\u300a\u672c\u8349\u7eb2\u76ee\u300b\u8bb0\u8f7d\u4e86\u201c\u98df\u7269\u76f8\u514b\u201d\u7406\u8bba\u3002\u4ee5\u8bb9\u4f20\u8bb9\uff0c\u5c31\u8fd9\u4e48\u6d41\u4f20\u4e0b\u6765\u4e86\u3002\u6240\u4ee5\u6211\u8981\u544a\u8bc9\u5927\u5bb6\uff0c\u5b9e\u9645\u4e0a\u5f88\u591a\u4e1c\u897f\u6ca1\u6709\u4f60\u60f3\u8c61\u7684\u90a3\u4e48\u5371\u9669\uff0c\u4e0d\u4f1a\u76f8\u514b\uff1b\u4f46\u662f\u98df\u7269\u8fc7\u654f\u8fd8\u662f\u8981\u5173\u6ce8\u4e00\u4e0b\u300202\u2014\u201c\u9178\u6027\u4f53\u8d28\u201d\u4e0e\u201c\u78b1\u6027\u98df\u7269\u201d\u8fd8\u6709\u4e00\u4e2a\uff0c\u5c31\u662f\u5927\u5bb6\u8bf4\u7684\u201c\u9178\u78b1\u4f53\u8d28\u201d\uff0c\u7531\u6765\u5df2\u4e45\u3002\u5b9e\u9645\u4e0a\uff0c\u5728\u5f88\u591a\u5e74\u524d\uff0c\u6211\u5c31\u5199\u8fc7\u76f8\u5173\u7684\u6587\u7ae0\u3002\u53bb\u5e74\u4e00\u4e2a\u53eb\u6b27\u9633\u00b7\u7f57\u4f2f\u7279\uff08Robert O. Young\uff09\u7684\u4eba\u88ab\u6293\u8d77\u6765\u4e86\uff0c\u88ab\u7f5a\u4e86\u4e00\u4ebf\u7f8e\u5143\u3002\u4e3a\u4ec0\u4e48\u7f5a\u4ed6\uff1f\u4ed6\u9020\u5047\uff0c\u5bb3\u7684\u5f88\u591a\u764c\u75c7\u75c5\u4eba\u803d\u8bef\u4e86\u6cbb\u7597\uff0c\u751a\u81f3\u642d\u4e0a\u4e86\u6027\u547d\u3002\u5f88\u591a\u4eba\u8ff7\u4fe1\u201c\u78b1\u6027\u98df\u7269\u201d\uff0c\u89c9\u5f97\u5403\u78b1\u6027\u7684\u98df\u7269\u597d\uff0c\u5403\u9178\u6027\u7684\u98df\u7269\u4e0d\u597d\u3002\u5f88\u591a\u65f6\u5019\uff0c\u6211\u4eec\u5224\u65ad\u98df\u7269\u7684\u9178\u78b1\u6027\uff0c\u9996\u5148\u662f\u5c1d\u5473\u9053\uff1a\u5403\u8d77\u6765\u9178\u7684\uff0c\u662f\u9178\u6027\u7684\uff1b\u5403\u8d77\u6765\u5f88\u6da9\uff0c\u662f\u78b1\u6027\u7684\u3002\u8fd8\u6709\u4e00\u79cd\u65b9\u6cd5\uff0c\u5c31\u662f\u7528pH\u8bd5\u7eb8\uff1a\u5982\u679cpH\u8bd5\u7eb8\u53d8\u7ea2\uff0c\u5c31\u662f\u9178\u6027\u7684\uff1b\u53d8\u84dd\uff0c\u5c31\u662f\u78b1\u6027\u7684\u3002\u5176\u5b9e\u8fd9\u4e9b\u90fd\u4e0d\u51c6\u3002\u6211\u4eec\u4e13\u95e8\u6709\u4e00\u95e8\u5b66\u79d1\u53eb\u201c\u98df\u7269\u5316\u5b66\u201d\uff0c\u5c31\u628a\u98df\u7269\u5206\u6210\u5448\u78b1\u6027\u7684\u548c\u5448\u9178\u6027\u7684\u3002\u600e\u4e48\u5206\u7684\uff1f\u628a\u98df\u7269\u71c3\u70e7\u6216\u6d88\u5316\u5206\u89e3\u53d8\u6210\u7070\u5206\uff0c\u7136\u540e\u53bb\u6d4b\u5176\u4e2d\u7684\u77ff\u7269\u8d28\u6210\u5206\u3002\u5982\u679c\u4e00\u7c7b\u98df\u7269\uff0c\u7070\u5206\u4e2d\u6bd4\u8f83\u591a\u7684\u662f\u786b\u3001\u78f7\u3001\u6c2f\u7b49\u7b49\u975e\u91d1\u5c5e\u5143\u7d20\uff0c\u52a0\u6c34\u53d8\u6210\u9178\uff08\u786b\u9178\u3001\u78f7\u9178\u8fd8\u6709\u76d0\u9178\uff09\uff0c\u5c31\u79f0\u4e3a\u201c\u9178\u6027\u98df\u7269\u201d\u3002\u8fd8\u6709\u4e00\u7c7b\uff0c\u7559\u4e0b\u7684\u662f\u94be\u3001\u94a0\u3001\u94c1\u3001\u9541\u7b49\u7b49\u91d1\u5c5e\u5143\u7d20\uff0c\u52a0\u6c34\u53d8\u6210\u78b1\uff08\u5448\u78b1\u6027\u7684\u6eb6\u6db2\uff09\uff0c\u8fd9\u4e9b\u5c31\u79f0\u4e3a\u201c\u78b1\u6027\u98df\u7269\u201d\u3002\u8fd9\u6837\u4e00\u5206\u7c7b\uff0c\u6211\u4eec\u5403\u7684\u6240\u6709\u9c7c\u3001\u79bd\u3001\u8089\u3001\u86cb\u3001\u5976\uff0c\u4e00\u4e9b\u8c46\u8150\u3001\u8c46\u5236\u54c1\uff0c\u8fd8\u6709\u4e00\u4e9b\u4e3b\u98df\u2014\u2014\u56e0\u4e3a\u542b\u6709\u8f83\u591a\u786b\u3001\u78f7\u548c\u6c2f\u2014\u2014\u90fd\u53d8\u6210\u4e86\u201c\u9178\u6027\u98df\u7269\u201d\u3002\u690d\u7269\u6027\u7684\u98df\u7269\uff0c\u6bd4\u65b9\u8bf4\u852c\u83dc\u3001\u6c34\u679c\uff0c\u8fd8\u6709\u8336\u53f6\u3001\u8461\u8404\u9152\uff0c\u8fd9\u4e9b\u5c31\u53d8\u6210\u201c\u78b1\u6027\u98df\u7269\u201d\u3002\u5982\u679c\u6309\u7167\u201c\u9178\u6027\u98df\u7269\u90fd\u4e0d\u80fd\u5403\u3001\u4f1a\u53d8\u6210\u9178\u6027\u4f53\u8d28\u201d\u8bf4\u6cd5\u7684\u8bdd\uff0c\u90a3\u5f88\u591a\u98df\u7269\u90fd\u662f\u4e0d\u80fd\u5403\u7684\uff1b\u800c\u4e14\u98df\u7269\u7edd\u5bf9\u5f71\u54cd\u4e0d\u4e86\u8eab\u4f53\u7684pH\u503c\u3002\u4f60\u60f3\u60f3\u770b\uff0c\u8840\u6db2\uff0cpH\u6052\u5b9a\u4fdd\u6301\u57287.35-7.45\u4e4b\u95f4\uff1b\u80c3\uff0c\u603b\u662f\u5728\u5f88\u9178\u7684\u73af\u5883\u4e2d\uff1b\u80a0\u9053\u91cc\u7684\u6db2\u4f53\uff0c\u603b\u662f\u6bd4\u8f83\u78b1\u6027\u7684\u3002\u4e00\u65e6\u53d1\u751f\u4e86\u6539\u53d8\uff0c\u8eab\u4f53\u5404\u65b9\u9762\u90fd\u4f1a\u53d7\u5230\u5f71\u54cd\u3002\u5982\u679c\u4f60\u7684\u8eab\u4f53\u4ece\u5fae\u5fae\u78b1\u6027\u53d8\u6210\u9178\u6027\uff0c\u90a3\u4e0d\u662f\u8bf4\u8981\u5f97\u764c\u75c7\u4e86\u2014\u2014\u53ef\u80fd\u90fd\u7b49\u4e0d\u5230\u5f97\u764c\u75c7\uff0c\u8981\u9a6c\u4e0a\u53bb\u533b\u9662\u62a2\u6551\u3002\u4e3a\u4ec0\u4e48\uff1f\u90a3\u53eb\u201c\u547c\u5438\u6027\u9178\u4e2d\u6bd2\u201d\uff0c\u6216\u8005\u201c\u4ee3\u8c22\u6027\u9178\u4e2d\u6bd2\u201d\u3002\u540c\u6837\u7684\u9053\u7406\uff0c\u5982\u679c\u4f60\u7684\u4f53\u8d28\u7a81\u7136\u53d8\u6210\u78b1\u6027\uff0c\u4e5f\u662f\u8eab\u4f53\u51fa\u4e86\u95ee\u9898\u3002\u56e0\u4e3a\u6211\u4eec\u7684\u8eab\u4f53\u6709\u4e09\u5927\u7cfb\u7edf\u2014\u2014\u4f53\u6db2\u7f13\u51b2\u7cfb\u7edf\u3001\u547c\u5438\u7cfb\u7edf\u3001\u6ccc\u5c3f\u7cfb\u7edf\u2014\u2014\u6765\u8c03\u8282\u4f60\u4f53\u6db2\u7684\u9178\u78b1\u6027\uff0c\u4f7f\u4e4b\u7ef4\u6301\u5728\u6052\u5b9a\u7684\u8303\u56f4\u5185\u300203\u2014\u725b\u5976\u5c06\u4eba\u7c7b\u9001\u8fdb\u764c\u75c7\u7684\u575f\u5893\uff1f\u8bf4\u5b8c\u8fd9\u4e2a\uff0c\u6211\u4eec\u518d\u8bf4\u725b\u5976\u3002\u5176\u5b9e\u6211\u662f\u4e00\u4e2a\u725b\u5976\u63a7\uff0c\u5929\u5929\u90fd\u559d\u725b\u5976\u3002\u6211\u4ece30\u5c81\u5f00\u59cb\u559d\u725b\u5976\uff0c30\u5c81\u4ee5\u524d\u6211\u4e0d\u559d\u2014\u2014\u6211\u662f\u4e73\u7cd6\u4e0d\u8010\u53d7\u8005\uff0c\u6211\u4e00\u559d\u725b\u5976\u5c31\u809a\u5b50\u75bc\u3001\u809a\u5b50\u80c0\uff0c\u6240\u6709\u7684\u4e0d\u8010\u53d7\u75c7\u72b6\u90fd\u5728\u6211\u8eab\u4e0a\u51fa\u73b0\u3002\u540e\u6765\u6211\u5c31\u505a\u4e86\u4e00\u4e2a\u8bfe\u9898\uff0c\u5c31\u662f\u6d4b\u8001\u5e74\u4eba\u7684\u9aa8\u5bc6\u5ea6\u8ddf\u559d\u725b\u5976\u548c\u8865\u9499\u7684\u5173\u7cfb\u3002\u540e\u6765\u6211\u4e5f\u53bb\u6d4b\u4e86\u4e00\u4e0b\u81ea\u5df1\u7684\u9aa8\u5bc6\u5ea6\uff0c\u6211\u624d30\u5c81\uff0c\u4f46\u6211\u89c9\u5f97\u6211\u6bd4\u90a3\u4e9b\u8001\u4eba\u597d\u4e0d\u4e86\u591a\u5c11\u3002\u4ece\u6b64\u4ee5\u540e\u6211\u5c31\u6ce8\u610f\u591a\u505a\u4e00\u4e9b\u8db3\u8ddf\u51b2\u51fb\u8fd0\u52a8\uff0c\u7136\u540e\u52a0\u4e0a\u8865\u9499\u2014\u2014\u5c31\u662f\u559d\u725b\u5976\u3002\u6211\u505a\u8fc7\u5f88\u591a\u5e74\u6709\u5173\u9aa8\u9abc\u5065\u5eb7\u7684\u7814\u7a76\uff0c\u6211\u53d1\u73b0\u5f88\u591a\u7684\u9499\u8865\u5145\u5242\u90fd\u6ca1\u6709\u725b\u5976\u8865\u9499\u7684\u6548\u679c\u597d\uff1a\u56e0\u4e3a\u725b\u5976\u542b\u4e73\u9178\u9499\uff0c\u8fd8\u542b\u916a\u86cb\u767d\u78f7\u9178\u80bd\u6765\u4fc3\u8fdb\u9499\u7684\u5438\u6536\uff0c\u800c\u4e14\u8fd8\u542b\u6709\u4f18\u8d28\u86cb\u767d\u3002\u4f46\u662f\u504f\u504f\u6709\u4eba\u8bf4\uff0c\u201c\u725b\u5976\u5c06\u4eba\u7c7b\u9001\u8fdb\u4e86\u764c\u75c7\u7684\u575f\u5893\u201d\u2014\u2014\u5929\u54ea\uff0c\u8fd9\u8fd8\u80fd\u559d\u5417\uff1f\u597d\u591a\u4eba\u88ab\u5413\u4f4f\u4e86\uff0c\u5305\u62ec\u201c\u9ad8\u7aef\u4eba\u7fa4\u201d\u2014\u2014\u6587\u5316\u5c42\u5c42\u6b21\u5f88\u9ad8\u7684\u3001\u804c\u4f4d\u5f88\u9ad8\u7684\u4eba\u3002\u8fd9\u4e2a\u89c2\u70b9\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u6709\u4e00\u672c\u4e66\uff0c\u53eb\u300a\u6551\u547d\u996e\u98df\uff1a\u4e2d\u56fd\u5065\u5eb7\u8c03\u67e5\u62a5\u544a\u300b\uff0c\u662f\u7f8e\u56fd\u7684\u67ef\u6797\u00b7\u574e\u8d1d\u5c14\uff08T. Colin campbell\uff09\u5199\u7684\u3002\u4e8b\u5b9e\u662f\u4ed6\u505a\u4e86\u4e00\u4e2a\u5b9e\u9a8c\uff1a\u628a\u9ec4\u66f2\u9709\u6bd2\u7d20\u5582\u7ed9\u5c0f\u767d\u9f20\u5403\uff0c\u8bf1\u5bfc\u51fa\u6765\u809d\u764c\uff1b\u7136\u540e\u628a\u5c0f\u767d\u9f20\u9972\u6599\u4e2d\u7684\u86cb\u767d\u8d28\u5168\u90e8\u6362\u6210\u725b\u5976\u4e2d\u7684\u916a\u86cb\u767d\u3002\u4e4b\u540e\u53d1\u73b0\uff0c\u8ddf\u90a3\u4e9b\u4e0d\u5403\u8fd9\u4e48\u591a\u916a\u86cb\u767d\u7684\u5c0f\u9f20\u6bd4\uff0c\u80bf\u7624\u957f\u5f97\u66f4\u5927\u4e86\u3002\u4ed6\u5c31\u5f97\u51fa\u4e00\u4e2a\u7ed3\u8bba\uff1a\u725b\u5976\u4e2d\u7684\u916a\u86cb\u767d\u53ef\u4ee5\u4fc3\u8fdb\u764c\u75c7\u7684\u53d1\u751f\u3002\u8fd9\u4e2a\u53ef\u4fe1\u5417\uff1f\u4e0d\u53ef\u4fe1\u3002\u9996\u5148\uff0c\u4eba\u548c\u8017\u5b50\u662f\u4e24\u56de\u4e8b\uff0c\u800c\u4e14\u6211\u4eec\u4eba\u4e0d\u4f1a\u6240\u6709\u9700\u8981\u7684\u86cb\u767d\u8d28\u90fd\u6765\u81ea\u4e8e\u725b\u5976\u3002\u6700\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u5b9e\u9a8c\u5bf9\u8c61\u672c\u8eab\u5c31\u662f\u764c\u75c7\u7684\u5c0f\u9f20\u3002\u6240\u4ee5\u8fd9\u4e2a\u8bf4\u6cd5\u662f\u4e0d\u53ef\u4fe1\u7684\uff0c\u8be5\u559d\u8fd8\u5f97\u559d\uff0c\u8865\u9499\u6548\u679c\u66f4\u597d\u300204\u2014\u7ef4D\u548c\u9499\u7247\u957f\u671f\u670d\u7528\u81f4\u764c\uff1f\u6700\u8fd1\u53d1\u751f\u4e86\u4e00\u4ef6\u4e8b\uff1a\u300a\u60ca\u609a\uff1a\u7ef4\u751f\u7d20D\u548c\u9499\u7247\u8dcc\u4e0b\u795e\u575b\uff01\u88ab\u66dd\u957f\u671f\u670d\u7528\u81f4\u764c\uff01\u300b\uff0c\u53c8\u5413\u4f60\u4e00\u4e0b\u3002\u8fd9\u4e2a\u6587\u7ae0\u662f\u7f8e\u56fd\u6ce2\u58eb\u987f\u7684\u5854\u592b\u8328\uff08Tufts University\uff09\u5927\u5b66\uff0c\u4e00\u4e2a\u53eb\u5f20\u82b3\u82b3\uff08Fangfang Zhang\uff09\u7684\u7814\u7a76\u4eba\u5458\u8ddf\u54c8\u4f5b\u5927\u5b66\u8054\u5408\u53d1\u8868\u7684\u3002\u6587\u7ae0\u7ed3\u8bba\u5176\u5b9e\u662f\u8fd9\u6837\u7684\uff1a\u5728\u9499\u548c\u7ef4\u751f\u7d20D\u6444\u5165\u5145\u8db3\u7684\u60c5\u51b5\u4e0b\uff0c\u989d\u5916\u670d\u7528\u8865\u5145\u5242\u53ef\u80fd\u6ca1\u6709\u4ec0\u4e48\u597d\u5904\uff0c\u5982\u679c\u5403\u4e86\u592a\u591a\u7ef4\u751f\u7d20D\uff0c\u53ef\u80fd\u4f1a\u5bf9\u764c\u75c7\u6709\u4e00\u5b9a\u4fc3\u8fdb\u4f5c\u7528\u3002\u8fd9\u4e2a\u7ed3\u8bba\uff0c\u662f\u9f13\u52b1\u4f60\u591a\u4ece\u98df\u7269\u91cc\u6444\u98df\u4e00\u4e9b\u7ef4\u751f\u7d20\u548c\u9499\uff0c\u800c\u4e0d\u8981\u670d\u7528\u592a\u591a\u8865\u5145\u5242\u3002\u6709\u4eba\u5c31\u52a0\u4ee5\u6f14\u7ece\uff0c\u8bf4\u201c\u4f60\u770b\u6559\u6388\u90fd\u8bf4\u4e86\uff0c\u5403\u591a\u4e86\u7ef4\u751f\u7d20\u548c\u8865\u5145\u5242\u4f1a\u5f97\u764c\u75c7\u3002\u201d\u4e2d\u56fd\u4eba\u7ffb\u8bd1\u8d77\u6765\u5c31\u53d8\u6210\u201c\u8dcc\u4e0b\u795e\u575b\u201d\u2014\u2014\u4f46\u5b83\u672c\u6765\u5c31\u4e0d\u5728\u795e\u575b\u4e0a\u9762\u3002\u6211\u8981\u544a\u8bc9\u5927\u5bb6\uff1a\u7ef4\u751f\u7d20D\u5f88\u597d\u3002\u4fc3\u8fdb\u9499\u7684\u5438\u6536\uff0c\u5bf9\u4eba\u4ece\u5934\u5230\u811a\u90fd\u6709\u5f88\u597d\u7684\u5065\u5eb7\u6548\u76ca\uff1b\u9499\u7247\u8be5\u5403\u4e5f\u8fd8\u5f97\u5403\u3002\u6211\u4eec\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\uff0c\u6bcf\u4e94\u5e74\u5c31\u5bf9\u5168\u56fd\u4eba\u6c11\u7684\u8425\u517b\u5065\u5eb7\u72b6\u51b5\u505a\u4e00\u6b21\u76d1\u6d4b\uff0c\u53d1\u73b0\u6211\u4eec\u56fd\u4eba\u67d0\u4e9b\u5fae\u91cf\u8425\u517b\u7d20\u662f\u6444\u5165\u4e0d\u8db3\u7684\u3002\u6211\u4eec\u56fd\u5bb6\uff08\u5c24\u5176\u662f\u5317\u4eac\u548c\u4e1c\u5317\uff09\u7eac\u5ea6\u6bd4\u8f83\u9ad8\uff0c\u51ac\u6625\u5b63\u8282\u7684\u65f6\u5019\uff0c\u9633\u5149\u4e2d\u7684\u7d2b\u5916\u7ebf\u6ca1\u6709UVB\uff0c\u6211\u4eec\u7684\u8eab\u4f53\u76ae\u80a4\u4e0b\u76847-\u8131\u6c22\u80c6\u56fa\u9187\u5f88\u96be\u751f\u6210\u7ef4\u751f\u7d20D3\u3002\u8fd9\u4e2a\u65f6\u5019\uff0c\u6211\u5e0c\u671b\u5927\u5bb6\u80fd\u8865\u5145\u4e00\u4e9b\u7ef4\u751f\u7d20D\uff0c\u5c24\u5176\u662f\u8001\u5e74\u4eba\u3001\u5c0f\u670b\u53cb\uff0c\u8fd8\u6709\u90a3\u4e9boffice lady\u2014\u2014\u4e0d\u6652\u592a\u9633\u7684\u3001\u51fa\u95e8\u4e09\u6b65\u8981\u6253\u4f1e\u7684\u4eba\uff0c\u5927\u5bb6\u4e00\u5b9a\u8981\u6ce8\u610f\u8865\u5145\u300205\u2014\u98ce\u9761\u4e00\u65f6\u7684\u201c\u751f\u916e\u996e\u98df\u201d\u6211\u4eec\u518d\u6765\u770b\u8fd9\u4e2a\u98ce\u9761\u4e00\u65f6\u7684\u201c\u751f\u916e\u996e\u98df\u201d\u2014\u2014\u5728\u8425\u517b\u5b66\u754c\uff0c\u6211\u4eec\u63d0\u5021\u7684\u201c\u5e73\u8861\u81b3\u98df\u201d\uff0c\u4e09\u5927\u5b8f\u91cf\u8425\u517b\u7d20\u7684\u4f9b\u80fd\u6bd4\u662f\u8fd9\u6837\u7684\uff08\u5982\u56fe\u5de6\uff09\uff1a\u8102\u80aa\u5360\u523020%-30%\uff0c\u86cb\u767d\u8d2810%-15%\uff0c\u78b3\u6c34\u5316\u5408\u7269\u2014\u2014\u5c31\u662f\u6211\u4eec\u7684\u4e3b\u98df\uff0c\u8981\u5360\u523050%\u523065%\u3002\u751f\u916e\u996e\u98df\u5462\uff1f\u770b\u770b\u53f3\u56fe\uff0c\u8102\u80aa\u4f9b\u80fd\u6bd4\u5360\u523080%\uff0c\u86cb\u767d\u8d28\u5360\u523015%\uff0c\u8fd8OK\uff0c\u4f46\u78b3\u6c34\u5316\u5408\u7269\u53ea\u67095%\uff0c\u76f8\u5f53\u4e8e\u4f60\u6839\u672c\u5c31\u4e0d\u80fd\u5403\u4e3b\u98df\u2014\u20145%\u6298\u5408\u6210\u4e3b\u98df\u53ea\u670925\u514b\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u6240\u6709\u7684\u4f9b\u80fd\u90fd\u8981\u7531\u8102\u80aa\u6765\u63d0\u4f9b\u3002\u5de6\u8fb9\u8fd9\u4e2a\u4eba\u5927\u5bb6\u90fd\u8ba4\u8bc6\uff0c\u827a\u4eba\u949f\u4e3d\u7f07\u3002\u5979\u8bf4\u5e74\u524d\u5e26\u7740\u5979\u7684\u516c\u516c\u5a46\u5a46\u8fd8\u6709\u5979\u7238\u7238\uff0c\u53bb\u6cf0\u56fd\u7684\u82cf\u6885\u5c9b\u6392\u6bd2\uff0c\u5929\u5929\u5c31\u559d\u6930\u5b50\u6cb9\uff0c\u559d\u4e8611\u5929\u3002\u5979\u5728\u65b0\u6d6a\u5fae\u535a\u4e0a\u9762\u6709400\u591a\u4e07\u7c89\u4e1d\uff0c\u5f71\u54cd\u5f88\u4e0d\u597d\u3002\u4f46\u5176\u5b9e\u7c89\u4e1d\u5927\u90e8\u5206\u90fd\u6279\u8bc4\u5979\u7684\u89c2\u70b9\uff0c\u6240\u4ee5\u6211\u89c9\u5f97\u53ef\u4ee5\u5b89\u5fc3\u4e86\u3002\u53f3\u8fb9\u8fd9\u4e2a\u4eba\uff0c\u5361\u4f26\u00b7\u7c73\u6b47\u5c14\u65af\uff08Karin Michels\uff09\uff0c\u5979\u6765\u81ea\u5fb7\u56fd\u7684\u5f17\u83b1\u5821\u5927\u5b66\uff08Universit\u00e4t Freiburg\uff09\uff0c\u662f\u9884\u9632\u548c\u80bf\u7624\u6d41\u884c\u75c5\u5b66\u7814\u7a76\u6240\u7684\u6240\u957f\uff1b\u4e5f\u662f\u54c8\u4f5b\u5927\u5b66\u9648\u66fe\u7199\u516c\u5171\u536b\u751f\u5b66\u9662\uff08Harvard T.H.Chan School of Public Health\uff09\u7684\u6559\u6388\u3002\u5979\u5728\u7f51\u4e0a\u53d1\u8868\u4e86\u4e00\u4e2a50\u5206\u949f\u7684\u5fb7\u8bed\u6f14\u8bb2\uff0c\u8bf4\u6930\u5b50\u6cb9\u6839\u672c\u5c31\u662f\u4e2a\u6bd2\u836f\uff0c\u6bd4\u732a\u6cb9\u8fd8\u574f\uff0c\u4e0d\u8981\u5403\u8fd9\u4e2a\u3002\u56e0\u4e3a\u6930\u5b50\u6cb9\u91cc\u8fb982%\u662f\u9971\u548c\u8102\u80aa\u9178\uff0c\u800c\u732a\u6cb9\u91cc\u8fb9\u7684\u9971\u548c\u8102\u80aa\u9178\u53ea\u670940%\u5de6\u53f3\uff0c\u53e6\u5916\u8fd8\u6709\u4e0d\u523040%\u7684\u4e0d\u9971\u548c\u8102\u80aa\u9178\uff08\u5373\u5355\u4e0d\u9971\u548c\u8102\u80aa\u9178\uff0c\u4e5f\u5373\u6cb9\u9178\uff09\u3002\u7f8e\u56fd\u7684\u5fc3\u810f\u534f\u4f1aAHA\uff08American Heart Association\uff09\uff0c\u57282017\u5e74\u4e5f\u5f97\u51fa\u8fc7\u6b64\u7ed3\u8bba\u3002\u4e8b\u5b9e\u4e0a\uff0c\u8981\u9884\u9632\u5fc3\u810f\u75c5\uff0c\u9971\u548c\u8102\u80aa\u9178\u7684\u91cf\u5e94\u8be5\u63a7\u5236\u5728\u4f9b\u80fd\u6bd4\u76846%\u4ee5\u4e0b\u3002\u6211\u7b97\u4e86\u4e00\u4e0b\uff0c\u6839\u636e\u4e00\u4e2a\u4eba\u6bcf\u5929\u54032000\u5343\u5361\u80fd\u91cf\u7684\u6807\u51c6\uff0c\u90a3\u4e48\u9971\u548c\u8102\u80aa\u9178\u4e0d\u80fd\u8d85\u8fc711\u514b\u3002\u5982\u679c\u8bf4\u5403\u6930\u5b50\u6cb9\uff0c\u5927\u6982\u5403\u4e00\u52fa\u5c31\u80fd\u591f\u63d0\u4f9b11\u514b\u7684\u9971\u548c\u8102\u80aa\u9178\uff0c\u5c31\u591f\u591a\u4e86\u3002\u5927\u5bb6\u8bf7\u6ce8\u610f\uff0c\u201c\u751f\u916e\u996e\u98df\u201d\u600e\u4e48\u6765\u7684\uff1f\u5176\u5b9e\uff0c\u5728\u53e4\u5e0c\u814a\u5e0c\u6ce2\u514b\u62c9\u5e95\u65f6\u4ee3\uff0c\u5c31\u6709\u201c\u751f\u916e\u996e\u98df\u201d\u2014\u2014\u5f53\u65f6\u53eb\u201c\u9965\u997f\u7597\u6cd5\u201d\uff0c\u7528\u6765\u6cbb\u7597\u766b\u75eb\u3002\u4e0a\u4e2a\u4e16\u7eaa\u768420\u5e74\u4ee3\uff0c\u7f8e\u56fd\u7684\u6885\u5965\u8bca\u6240\uff08Mayo clinic\uff09\u8fdb\u4e00\u6b65\u53d1\u73b0\uff0c\u201c\u751f\u916e\u996e\u98df\u201d\u5bf9\u513f\u7ae5\u96be\u6cbb\u6027\u7684\u766b\u75eb\u6709\u4e00\u5b9a\u7684\u6cbb\u7597\u6548\u679c\u3002\u539f\u7406\u662f\uff1a\u5e73\u65f6\u8eab\u4f53\u6240\u9700\u80fd\u91cf\u4e3b\u8981\u7531\u8461\u8404\u7cd6\u6765\u63d0\u4f9b\uff0c\u5982\u679c\u4e0d\u5403\u78b3\u6c34\u5316\u5408\u7269\uff0c\u5c31\u6539\u53d8\u4e86\u8111\u7684\u80fd\u91cf\u4ee3\u8c22\u65b9\u5f0f\uff0c\u6539\u53d8\u4e86\u7ec6\u80de\u7684\u7279\u6027\uff0c\u964d\u4f4e\u5174\u594b\u6027\u548c\u7f13\u51b2\u766b\u75eb\u6837\u7684\u653e\u7535\uff0c\u6539\u53d8\u4e86\u795e\u7ecf\u9012\u8d28\u548c\u7a81\u89e6\u4f20\u9012\u7684\u529f\u80fd\u3002\u8fd9\u79cd\u7597\u6cd5\u73b0\u5728\u5f88\u591a\u56fd\u5bb6\u8fd8\u5728\u7528\uff0c\u4e5f\u5305\u62ec\u6211\u4eec\u56fd\u5bb6\u3002\u4f46\u662f\u5f88\u591a\u4eba\u5c31\u5229\u7528\u8fd9\u4e2a\u673a\u4f1a\u53d1\u8d22\u3002\u5927\u5bb6\u770b\u8fd9\u4e2a\u4eba\uff0c\u53eb\u7f57\u4f2f\u7279\u00b7\u963f\u7279\u91d1\u65af\uff08Robert Atkins\uff09\uff0c\u4ed6\u5176\u5b9e\u4e0d\u662f\u641e\u8425\u517b\u7684\uff0c\u662f\u4e2a\u4e34\u5e8a\u5927\u592b\u3002\u4ed6\u4ee5\u5f88\u5feb\u7684\u901f\u5ea6\u6539\u826f\u4e86\u4e00\u7248\u201c\u751f\u916e\u996e\u98df\u201d\uff0c\u53d8\u6210\u201c\u963f\u7279\u91d1\u65af\u51cf\u80a5\u6cd5\u201d\uff1a\u628a\u86cb\u767d\u8d28\u4f9b\u80fd\u6bd4\u7a0d\u5fae\u63d0\u9ad8\u4e00\u70b9\uff0c\u7136\u540e\u628a\u8102\u80aa\u7684\u4e0b\u964d\u4e00\u70b9\u70b9\uff0c\u78b3\u6c34\u5316\u5408\u7269\u7684\u518d\u52a0\u4e00\u70b9\u70b9\u3002\u6211\u77e5\u9053\u597d\u591a\u4eba\u90fd\u5728\u7528\u8fd9\u79cd\u65b9\u6cd5\u51cf\u80a5\u3002\u963f\u7279\u91d1\u65af\u7684\u516c\u53f8\u4ece\u96f6\u70b9\u8d77\u6b65\uff0c\u5230\u4ed6\u53bb\u4e16\u7684\u524d\u4e00\u5e74\uff0c\u8425\u4e1a\u989d\u5df2\u7ecf\u8fbe\u5230\u4e861\u4ebf\u591a\u7f8e\u91d1\u3002\u4f46\u662f\u4ed6\u5f88\u4e0d\u5e78\uff0c\u4ed6\u5728\u51b0\u9762\u4e0a\u6454\u4e86\u4e00\u8de4\uff0c\u53bb\u4e16\u4e86\u3002\u5f53\u65f6\u7684\u7ebd\u7ea6\u5e02\u957f\u8fc8\u514b\u5c14\u00b7\u5e03\u9686\u4f2f\u683c\uff08Michael Bloomberg\uff09\u8ba4\u4e3a\uff0c\u4ed6\u7684\u6b7b\u56e0\u53ef\u80fd\u662f\u80a5\u80d6\u5bfc\u81f4\u5fc3\u810f\u8d1f\u62c5\u8fc7\u91cd\u3002\u6211\u4eec\u73b0\u5728\u56fd\u5185\u6709\u5f88\u591a\u8425\u9500\u53f7\uff0c\u4ece\u56fd\u5916\u8db8\u6765\u4e00\u4e9b\u4e0d\u90a3\u4e48\u79d1\u5b66\u7684\u77e5\u8bc6\uff0c\u53d1\u5728\u81ea\u5df1\u7684\u516c\u4f17\u53f7\u91cc\u3002\u63a8\u9500\u597d\u591a\u4e1c\u897f\uff0c\u8ba9\u4f60\u53bb\u201c\u4e70\u4e70\u4e70\u201d\u2014\u2014\u4e00\u70b9\u70b9\u6930\u5b50\u6cb9\uff0c\u5356\u5f97\u5f88\u8d35\uff0c\u5927\u5bb6\u5343\u4e07\u4e0d\u8981\u4e0a\u5f53\u3002\u7531\u4e8e\u4e2d\u56fd\u201c\u751f\u916e\u996e\u98df\u201d\u7684\u4e13\u4e1a\u4eba\u5458\u6570\u91cf\u6709\u9650\uff0c\u6240\u4ee5\u5b83\u4e0d\u5b9c\u5168\u9762\u63a8\u5e7f\uff0c\u5176\u9002\u5e94\u75c7\u6682\u65f6\u5b9a\u4e3a\uff1aBMI\u5927\u4e8e28\u3001\u809d\u80be\u529f\u80fd\u6b63\u5e38\u7684\u5355\u7eaf\u6027\u80a5\u80d6\u4eba\u7fa4\u3002\u6b64\u5916\uff0c\u201c\u751f\u916e\u996e\u98df\u201d\u8fd8\u6ca1\u6709\u957f\u671f\u7684\u6548\u679c\u68c0\u9a8c\uff0c\u6240\u4ee5\u5982\u679c\u7cd6\u5c3f\u75c5\u4eba\u60f3\u8bd5\u7684\u8bdd\uff0c\u4e00\u5b9a\u8981\u53bb\u533b\u9662\uff0c\u5207\u52ff\u64c5\u81ea\u5c1d\u8bd5\u3002\u800c\u4e14\u5927\u5bb6\u8981\u8bb0\u4f4f\uff0c\u201c\u751f\u916e\u996e\u98df\u201d\u662f\u4e00\u4e2a\u6cbb\u7597\u7684\u996e\u98df\uff0c\u4e0d\u662f\u4e00\u5929\u5230\u665a\u53ef\u4ee5\u7528\u7684\u3002\u78b3\u6c34\u5316\u5408\u7269\u6709\u5f88\u591a\uff0c\u6bd4\u65b9\u8bf4\u5168\u8c37\u7c7b\uff0c\u8fd8\u6709\u4e00\u4e9b\u8c46\u7c7b\uff0c\u5bf9\u6211\u4eec\u5f88\u597d\u3002\u90a3\u5230\u5e95\u5403\u591a\u5c11\u5408\u9002\uff1f50%\u5de6\u53f3\u7684\u4f9b\u80fd\u6bd4\uff0c\u4e5f\u5c31\u662f\u4e00\u5929\u5403\u534a\u65a4\u5230\u516b\u4e24\u5de6\u53f3\u7684\u4e3b\u98df\u2014\u2014\u50cf\u6211\u8fd9\u6837\u7684\u5973\u6027\u53ef\u80fd\u534a\u65a4\u5c31\u591f\u4e86\u3002\u6211\u6307\u7684\u662f\u751f\u91cd\uff0c\u8fd9\u6837\u4eba\u7684\u6b7b\u4ea1\u7387\u662f\u6700\u4f4e\u7684\u3002\u4eba\u4e00\u8f88\u5b50\u8981\u540360-70\u5428\u98df\u7269\uff0c\u4e00\u5929\u8981\u540340\u591a\u79cd\u8425\u517b\u6210\u5206\u3002\u5982\u679c\u60f3\u6d3b\u5f97\u5065\u5eb7\u3001\u957f\u5bff\uff0c\u90a3\u4f60\u6240\u9700\u7684\u8425\u517b\u6210\u5206\uff0c\u4e00\u79cd\u4e5f\u4e0d\u80fd\u5c11\uff1b\u5c11\u4e86\u54ea\u4e00\u6837\uff0c\u8eab\u4f53\u5c31\u4f1a\u51fa\u73b0\u72b6\u51b5\u3002\u6240\u4ee5\u90a3\u4e9b\u91c7\u7528\u201c\u751f\u916e\u996e\u98df\u201d\u7684\u4eba\uff0c\u4ed6\u4f1a\u55dc\u7761\u3001\u4f1a\u75b2\u5026\uff0c\u547c\u5438\u65f6\u5473\u9053\u4e0d\u597d\u95fb\uff0c\u8eab\u4e0a\u4e5f\u4f1a\u53d1\u51fa\u4f53\u81ed\uff0c\u8fd8\u4f1a\u4fbf\u79d8\uff0c\u8981\u7ecf\u8fc7\u5f88\u957f\u7684\u4e00\u6bb5\u9002\u5e94\u671f\u3002\u6211\u4eec\u8fd8\u662f\u8981\u7167\u7740\u5730\u4e2d\u6d77\u996e\u98df\uff08Mediterranean diet\uff09\u3001\u5e73\u8861\u81b3\u98df\u5b9d\u5854\u53bb\u5403\u3002\u6211\u81ea\u5df1\u7ed9\u5927\u5bb6\u603b\u7ed3\u4e86\u4e00\u4e2a\u5e73\u8861\u81b3\u98df\u7684\u201c\u5341\u4e2a\u62f3\u5934\u539f\u5219\u201d\uff08\u5982\u56fe\u5de6\uff09\uff1a\u6bcf\u4eba\u6bcf\u5929\u5403\u7684\u6240\u6709\u7684\u98df\u7269\uff08\u98df\u7269\u751f\u91cd\uff0c\u5228\u53bb\u4e86\u4e0d\u53ef\u98df\u7528\u7684\u90e8\u5206\uff09\u52a0\u8d77\u6765\uff0c\u5c31\u662f\u5341\u4e2a\u62f3\u5934\u5927\u5c0f\uff0c\u6211\u6bcf\u5929\u5c31\u7167\u7740\u8fd9\u4e2a\u6807\u51c6\u53bb\u5403\u3002\u8fd9\u91cc\u8fd8\u6709\u4e00\u4e2a\u7f8e\u56fd\u7684\u5e73\u8861\u81b3\u98df\u9910\u76d8\uff08\u5982\u56fe\u53f3\u4e0a\uff09\uff0c\u4e5f\u6709\u4e00\u4e2a\u6211\u4eec\u4e2d\u56fd\u7684\uff08\u5982\u56fe\u53f3\u4e0b\uff09\u3002\u8fd8\u6709\uff0c\u4f60\u60f3\u8ba9\u81ea\u5df1\u57fa\u7840\u4ee3\u8c22\u589e\u9ad8\uff0c\u90a3\u5c31\u8981\u505a\u8fd0\u52a8\uff0c\u4f7f\u4f60\u7684\u4f53\u6210\u5206\u53d1\u751f\u4e00\u4e2a\u6539\u53d8\uff1a\u6bcf\u589e\u52a01kg\u808c\u8089\uff0c\u673a\u4f53\u5c31\u589e\u52a0110kcal\u7684\u80fd\u91cf\u6d88\u8017\u3002\u6211\u628a\u6211\u4eec\u56fd\u5bb6\u57281930\u5e74\u4ee5\u524d\u51fa\u751f\u7684\u8425\u517b\u4e13\u5bb6\u90fd\u5217\u4e86\u51fa\u6765\uff0c\u90fd\u662f\u975e\u5e38\u6709\u540d\u7684\u5927\u5496\u3002\u5927\u5bb6\u53ef\u4ee5\u770b\u5230\uff0c\u4ed6\u4eec\u90fd\u6d3b\u5f97\u6bd4\u8f83\u957f\u3002\u6700\u957f\u7684\u662f\u90d1\u96c6\u6559\u6388\uff0c110\u5c81\u3002\u53f3\u8fb9\u8fd9\u4e24\u4f4d\uff0c\u4e00\u4f4d\u662f\u6211\u7684\u540c\u4e8b\uff0c\u53e6\u4e00\u4f4d\u662f\u6211\u656c\u91cd\u7684\u957f\u8f88\uff0c\u4ed6\u4eec\u73b0\u5728\u7684\u5e74\u9f84\u5206\u522b\u662f102\u5468\u5c81\u548c103\u5468\u5c81\u3002\u4ed6\u4eec\u8fd8\u5065\u5728\uff0c\u601d\u7ef4\u6e05\u6670\uff0c\u751f\u6d3b\u80fd\u591f\u81ea\u7406\u3002\u5982\u679c\u4f60\u60f3\u50cf\u8425\u517b\u4e13\u5bb6\u6d3b\u5f97\u4e00\u6837\u957f\uff0c\u5c31\u8981\u5747\u8861\u81b3\u98df\u3002\u5927\u5bb6\u4e00\u5b9a\u8981\u542c\u6211\u7684\u5efa\u8bae\uff0c\u5e73\u8861\u81b3\u98df\uff0c\u6bcf\u5929\u201c\u5341\u4e2a\u62f3\u5934\u539f\u5219\u201d\u3002\u8c22\u8c22\u5927\u5bb6\u3002\u6f14\u8bb2\u5609\u5bbe\u4f55\u4e3d\uff1a\u300a\u751f\u916e\u996e\u98df\u51cf\u80a5\uff1f\u78b1\u6027\u98df\u7269\u517b\u751f\uff1f\u2026\u2026\u522b\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u9a97\u4e86\uff01\u300b", "title": "\u4f55\u4e3d\uff1a\u751f\u916e\u996e\u98df\u51cf\u80a5\uff1f\u78b1\u6027\u98df\u7269\u517b\u751f\uff1f5\u5927\u8425\u517b\u8c23\u8a00\u9010\u6761\u7c89\u788e\uff01", "ukey_author": null, "video_content": "<iframe frameborder=\"0\" src=\"https://v.qq.com/txp/iframe/player.html?vid=e0873tjohh2\" allowFullScreen=\"true\"></iframe>"}, {"author": {"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [], "content": "<p><!--StartFragment--><strong><span style=\"color:#696969\"><span style=\"font-size:14px\">\u672c\u6587\u4e3a2019\u5e744\u670827\u65e5&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u671f&mdash;&mdash;\u5bf9\u8c23\u8a00\u8bf4&ldquo;\u4e0d&rdquo;| \u8d75\u7ea2 \u6f14\u8bb2\u5b9e\u5f55\uff1a</span></span></strong><!--EndFragment--></p>\r\n\r\n<p class=\"editors-note\">&ldquo;\u5c11\u6d3b\u52a8\uff0c\u514d\u5f97\u52a8\u4e86\u80ce\u6c14\u3002&rdquo; &ldquo;\u4e00\u4e2a\u4eba\u8981\u5403\u4e24\u4e2a\u4eba\u7684\u996d\u3002&rdquo;\u60f3\u8981\u987a\u5229\u5ea6\u8fc7\u5b55\u4ea7\u671f\uff1f&ldquo;\u591a\u5403\u5c11\u52a8&rdquo;\u53ef\u4e0d\u884c\u3002&ldquo;\u4f20\u7edf&lsquo;\u5750\u6708\u5b50&rsquo;\u7406\u5ff5\u4e2d\uff0c\u5f88\u591a\u505a\u6cd5\u7684\u521d\u8877\u662f\u4e3a\u4e86\u4ea7\u5987\u5065\u5eb7\uff0c\u4f46\u4ece\u73b0\u4ee3\u533b\u5b66\u89c6\u89d2\u51fa\u53d1\uff0c\u8fd9\u4e9b\u505a\u6cd5\u53cd\u800c\u4f1a\u5e26\u6765\u5371\u5bb3\u3002&rdquo;&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u7b2c\u5341\u671f\u6f14\u8bb2\uff0c\u5317\u4eac\u534f\u548c\u533b\u5b66\u9662\u62a4\u7406\u5b66\u9662\u6559\u6388\u3001\u4e34\u5e8a\u62a4\u7406\u5b66\u7cfb\u526f\u4e3b\u4efb\u8d75\u7ea2\u544a\u8bc9\u4f60\uff0c\u638c\u63e1\u5bf9\u65b9\u6cd5\uff0c\u666e\u901a\u4eba\u7684\u5b55\u4ea7\u671f\u4e5f\u53ef\u4ee5\u50cf\u516c\u4f17\u4eba\u7269\u90a3\u6837\u4f18\u96c5\u3002</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"498\" scrolling=\"no\" src=\"https://v.qq.com/txp/iframe/player.html?vid=s0870cj420t\" width=\"640\"></iframe></p>\r\n\r\n<p>\u5927\u5bb6\u597d\uff0c\u6211\u662f\u8d75\u7ea2\uff0c\u6211\u662f\u5317\u4eac\u534f\u548c\u533b\u5b66\u9662\u62a4\u7406\u5b66\u9662\u7684\u6559\u6388\u3002\u5f88\u9ad8\u5174\u4eca\u5929\u80fd\u5728\u8fd9\u91cc\uff0c\u8ddf\u5927\u5bb6\u5206\u4eab\u4e00\u4e9b\u6211\u505a\u7684\u5de5\u4f5c\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/r7035nQDf3XtXf1lDKDATbZZwMr2C0C6Dg9uFulJjKo4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8fd9\u662f\u4e00\u5f20\u53bb\u5e74\u5728\u7f51\u7edc\u4e0a\u5f15\u8d77\u70ed\u8bae\u7684\u7167\u7247\uff0c\u76f8\u4fe1\u5927\u5bb6\u90fd\u770b\u5230\u8fc7\u3002\u53bb\u5e744\u6708\uff0c\u82f1\u56fd\u7684\u51ef\u7279\u738b\u5983\u5728\u751f\u5b8c\u7b2c\u4e09\u4e2a\u5b69\u5b50\u4ee5\u540e\u7684\u4e03\u4e2a\u5c0f\u65f6\uff0c\u5c31\u795e\u91c7\u5955\u5955\u5730\u51fa\u73b0\u5728\u4e86\u516c\u4f17\u7684\u9762\u524d\uff0c\u5986\u5bb9\u7cbe\u81f4\uff0c\u8fd8\u7a7f\u7740\u8fde\u8863\u88d9\uff0c\u8fd8\u7a7f\u7740\u9ad8\u8ddf\u978b\u3002\u56db\u6708\u4efd\u5929\u6c14\u8fd8\u662f\u6bd4\u8f83\u51c9\u7684\u3002\u6211\u5173\u6ce8\u4e86\u4e00\u4e0b\u8fd9\u6761\u65b0\u95fb\u540e\u9762\u5927\u5bb6\u7684\u7559\u8a00\uff0c\u6709\u8d5e\u6210\uff0c\u6709\u53cd\u5bf9\uff0c\u6709\u7fa1\u6155\uff0c\u66f4\u591a\u7684\u662f\u8d28\u7591&mdash;&mdash;\u5f88\u591a\u4eba\u90fd\u5728\u95ee\uff0c\u5750\u6708\u5b50\u80fd\u8fd9\u6837\u5417\uff1f</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/5ASujjPnz_KfU7dj1RHM-hC1w6w_rOB8g2wsCm3DzO84BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u518d\u6765\u770b\u8fd9\u5f20\u7167\u7247\uff0c\u76f8\u4fe1\u5927\u5bb6\u4e5f\u8ba4\u8bc6\u8fd9\u4e24\u4f4d\uff0c\u5f71\u89c6\u660e\u661f\u9ec4\u6653\u660e\u548cAngelababy\uff08\u6768\u9896\uff09\u3002\u8fd9\u5f20\u7167\u7247\u662fAngelababy\u751f\u5b8c\u5b69\u5b50\u4e09\u5929\u51fa\u9662\u7684\u65f6\u5019\u62cd\u7684\u7167\u7247\uff0c\u540c\u6837\u5f88\u7cbe\u795e\u3001\u5f88\u6f02\u4eae\u3002</p>\r\n\r\n<p>\u90a3\u53ef\u80fd\u6709\u670b\u53cb\u4f1a\u8bf4\uff0c\u51ef\u7279\u738b\u5983\u662f\u5916\u56fd\u4eba\uff0cAngelababy\u662f\u660e\u661f\uff0c\u5979\u4eec\u548c\u6211\u4eec\u666e\u901a\u4eba\u4e0d\u4e00\u6837\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/Bo0lW5uw3N7CuNRxo9TNrD96uHGakyUQuBJd59sRyfc4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6211\u4eec\u666e\u901a\u4eba\u5750\u6708\u5b50\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u4e0a\u56fe\u5c31\u662f\u4f20\u8bf4\u4e2d\u5750\u6708\u5b50\u7684\u5404\u79cd\u7981\u5fcc\u3002\u5f88\u591a\u670b\u53cb\u8bf4\uff0c\u6211\u542c\u5230\u8fd9\u4e9b\u90fd\u8981\u6291\u90c1\u4e86\uff0c\u8fd9\u4e00\u4e2a\u6708\u600e\u4e48\u8fc7\u5462\uff1f</p>\r\n\r\n<p>20\u591a\u5e74\u524d\uff0c\u6211\u5728\u533b\u9662\u5de5\u4f5c\u7684\u65f6\u5019\uff0c\u8fd9\u4e9b\u7981\u5fcc\u5f71\u54cd\u5f88\u6df1\u3002\u6bd4\u5982\u8bf4\u75c5\u623f\u6bcf\u5929\u8981\u901a\u98ce\u6362\u6c14\uff0c\u4f46\u662f\u4ea7\u79d1\u75c5\u623f\u5f88\u96be\u505a\u5230\u3002\u590f\u5929\u8981\u60f3\u5f00\u7a7a\u8c03\uff0c\u4e5f\u4f1a\u906d\u5230\u4e00\u81f4\u53cd\u5bf9\u3002</p>\r\n\r\n<p>\u6709\u4e00\u5e74\u590f\u5929\uff0c\u5929\u6c14\u5f88\u70ed\uff0c\u75c5\u623f\u91cc\u6709\u4ea7\u5987\u3001\u5b69\u5b50\uff0c\u8fd8\u6709\u6765\u63a2\u89c6\u7684\u5bb6\u5c5e\u3002\u4e00\u5c4b\u5b50\u4eba\uff0c\u4e0d\u8ba9\u5f00\u7a7a\u8c03\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6709\u4e00\u4e2a\u5bb6\u5c5e\u4ece\u623f\u95f4\u91cc\u6643\u6643\u60a0\u60a0\u5730\u51fa\u6765\uff0c\u6211\u5c31\u8d76\u5feb\u8fc7\u53bb\u67e5\u770b\uff0c\u53d1\u73b0\u4ed6\u4e2d\u6691\u4e86\u3002</p>\r\n\r\n<p>\u6211\u5f53\u65f6\u5c31\u6025\u4e86\uff0c\u8d76\u5feb\u628a\u4ed6\u5b89\u7f6e\u7ed9\u6211\u7684\u540c\u4e8b\uff0c\u7136\u540e\u8dd1\u5230\u75c5\u623f\u91cc\u8fb9\u53bb\u8ddf\u5bb6\u5c5e\u8fd8\u6709\u4ea7\u5987\u8bf4\uff1a\u4e00\u5b9a\u8981\u5f00\u7a7a\u8c03\uff0c\u56e0\u4e3a\u623f\u95f4\u91cc\u6e29\u5ea6\u5b9e\u5728\u592a\u9ad8\u4e86\uff0c\u521a\u624d\u6765\u63a2\u89c6\u7684\u5bb6\u5c5e\u90fd\u4e2d\u6691\u4e86\uff0c\u90a3\u4ea7\u5987\u3001\u65b0\u751f\u513f\u600e\u4e48\u53d7\u5f97\u4e86\uff1f</p>\r\n\r\n<p>\u5927\u5bb6\u8981\u77e5\u9053\uff0c\u65b0\u751f\u513f\u4f53\u6e29\u8c03\u8282\u4e2d\u67a2\u53d1\u80b2\u8fd8\u4e0d\u5b8c\u5584\uff0c\u5f88\u5bb9\u6613\u53d7\u5230\u5916\u754c\u6e29\u5ea6\u5f71\u54cd\u800c\u53d1\u70e7\u3002\u6211\u8fd9\u6837\u89e3\u91ca\u4ee5\u540e\uff0c\u4ea7\u5987\u548c\u5bb6\u5c5e\u90fd\u8868\u793a\u7406\u89e3\uff0c\u5c31\u540c\u610f\u5f00\u7a97\u4e86\u3002</p>\r\n\r\n<p>\u4f20\u7edf\u4e2d&ldquo;\u5750\u6708\u5b50&rdquo;\u7684\u4e00\u4e9b\u7981\u5fcc\uff0c\u5176\u5b9e\u548c\u6211\u4eec\u8fc7\u53bb\u7684\u751f\u6d3b\u6761\u4ef6\u3001\u751f\u6d3b\u73af\u5883\u6709\u5173\u3002\u8fc7\u53bb\u623f\u5b50\u5bc6\u95ed\u6027\u4e0d\u597d\uff0c\u70ed\u6c34\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u4ea7\u5987\u51fa\u6c57\u6bd4\u8f83\u591a\uff0c\u5982\u679c\u6d17\u6fa1\u6e29\u5ea6\u8fbe\u4e0d\u5230\u8981\u6c42\uff0c\u5c31\u5bb9\u6613\u53d7\u51c9\u3002\u4f46\u662f\u73b0\u5728\u751f\u6d3b\u6761\u4ef6\u6539\u5584\u4e86\uff0c\u6240\u4ee5\u6d17\u5934\u3001\u6d17\u6fa1\u6ca1\u6709\u95ee\u9898\u3002</p>\r\n\r\n<p>\u73b0\u4ee3\u533b\u5b66\u8bb2\u5750\u6708\u5b50\u5417\uff1f\u4e5f\u8bb2\uff0c\u53eb&ldquo;\u4ea7\u8925\u671f&rdquo;\u3002\u6240\u8c13&ldquo;\u4ea7\u8925\u671f&rdquo;\uff0c\u662f\u6307\u80ce\u76d8\u5a29\u51fa\u4ee5\u540e\uff0c\u9664\u4e73\u817a\u5916\u8eab\u4f53\u5404\u5668\u5b98\u6062\u590d\u5230\u6000\u5b55\u524d\u72b6\u6001\u7684\u4e00\u6bb5\u65f6\u95f4\uff0c\u901a\u5e38\u662f\u516d\u5468\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/L7UD_GQEnuDwKd-gVXf-0bmFxVePM_LLDb3hm9govSk4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u5728\u6b64\u671f\u95f4\uff0c\u4ea7\u5987\u7684\u8eab\u4f53\u4f1a\u53d1\u751f\u5f88\u591a\u53d8\u5316\uff0c\u5176\u4e2d\u6700\u4e3b\u8981\u3001\u6700\u660e\u663e\u7684\u662f\u5b50\u5bab\u7684\u53d8\u5316\u3002\u53ef\u4ee5\u770b\u5230\uff08\u5982\u4e0a\u56fe\uff09\uff0c\u4ea7\u540e\u5b50\u5bab\u4f1a\u9010\u6e10\u6536\u7f29\u3001\u53d8\u5c0f\uff1b\u6bcf\u5929\u5b50\u5bab\u5e95\u7684\u9ad8\u5ea6\u4f1a\u4e0b\u964d\u5927\u7ea6\u4e00\u5398\u7c73\uff1b\u5341\u5929\u4e4b\u540e\uff0c\u57fa\u672c\u4e0a\u5c31\u964d\u56de\u76c6\u8154\u3002\u80ce\u76d8\u5265\u79bb\u540e\uff0c\u9644\u7740\u90e8\u4f4d\u7684\u5b50\u5bab\u5185\u819c\u4e5f\u9700\u8981\u4fee\u590d\uff0c\u5927\u6982\u9700\u8981\u516d\u5468\uff0c\u4e00\u4e9b\u8840\u6db2\u8fd8\u6709\u5b50\u5bab\u5185\u819c\u7684\u788e\u7247\uff0c\u4f1a\u5f62\u6210\u6076\u9732\u6392\u51fa\u4f53\u5916\u3002</p>\r\n\r\n<p>\u9664\u751f\u6b96\u7cfb\u7edf\u5916\uff0c\u4ea7\u5987\u7684\u5fc3\u8840\u7ba1\u7cfb\u7edf\u3001\u6ccc\u5c3f\u7cfb\u7edf\u8fd8\u6709\u5185\u5206\u6ccc\u7cfb\u7edf\u7b49\u4e5f\u4f1a\u53d1\u751f\u4e00\u7cfb\u5217\u53d8\u5316\u3002</p>\r\n\r\n<p>\u9664\u8eab\u4f53\u6062\u590d\u5916\uff0c\u4ea7\u5987\u4e5f\u9700\u8981\u4f11\u606f\uff1a\u5206\u5a29\u6d88\u8017\u4e86\u5979\u4eec\u5927\u91cf\u4f53\u529b\uff0c\u4ea7\u540e\u8fd8\u8981\u6bcd\u4e73\u5582\u517b\uff0c\u5341\u5206\u8f9b\u82e6\u3002</p>\r\n\r\n<p>\u662f\u4e0d\u662f\u4ea7\u540e30\u5929\u5fc5\u987b\u4e00\u76f4\u8eba\u5728\u5e8a\u4e0a\uff0c\u4e0d\u80fd\u4e0b\u5730\u3001\u4e0d\u80fd\u6d3b\u52a8\uff1f\u56de\u7b54\u662f\u5426\u5b9a\u7684\uff0c\u4ea7\u540e\u7684\u8fd0\u52a8\u975e\u5e38\u5fc5\u8981\uff0c\u800c\u4e14\u6709\u76ca&mdash;&mdash;</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/YQSv54BsTXb2CvjOdrDB5PYGgGWD8pVvNbp3vqmyuiU4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4f46\u662f\u4ea7\u540e\u8fd0\u52a8\u4e5f\u8981\u8bb2\u7a76\u5faa\u5e8f\u6e10\u8fdb\u3002</p>\r\n\r\n<p>\u6211\u5728\u8fd9\u60f3\u5f3a\u8c03\uff1a\u4ea7\u540e\u5934\u51e0\u5929\uff0c\u8fd0\u52a8\u8981\u6ce8\u610f\u5b89\u5168&mdash;&mdash;\u7279\u522b\u662f\u4ea7\u540e\u7b2c\u4e00\u6b21\u4e0b\u5730\u6d3b\u52a8\uff0c\u4e00\u5b9a\u8981\u6709\u4eba\u966a\u4f34\uff0c\u907f\u514d\u56e0\u4e3a\u8eab\u4f53\u865a\u5f31\u800c\u8dcc\u5012\uff0c\u51fa\u73b0\u5371\u9669\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/5ebJGmlj1cCxBNeqkDFbfLsYzP2sY0iG6JS_5CuhaYw4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4ea7\u540e\u5934\u51e0\u5929\uff0c\u4ea7\u5987\u53ef\u4ee5\u5728\u5e8a\u4e0a\u505a\u4e00\u4e9b\u7b80\u5355\u7684\u8fd0\u52a8\uff08\u5982\u4e0a\u56fe\uff09\u3002\u6bd4\u5982\u8bf4\uff0c\u8eba\u5728\u5e8a\u4e0a\u52fe\u811a\u3001\u7ef7\u811a\uff0c\u4fc3\u8fdb\u5c0f\u817f\u808c\u8089\u6536\u7f29\u653e\u677e\uff0c\u6709\u5229\u4e8e\u8840\u6db2\u5faa\u73af\uff0c\u4e5f\u53ef\u4ee5\u505a\u4e3e\u817f\u548c\u62ac\u81c0\u7684\u8fd0\u52a8\uff0c\u4fc3\u8fdb\u817f\u90e8\u3001\u8179\u90e8\u808c\u8089\u7684\u6536\u7f29\uff0c\u6709\u52a9\u4e8e\u4f53\u578b\u91cd\u5851\u3002</p>\r\n\r\n<p>\u9664\u4e86\u8981\u5faa\u5e8f\u6e10\u8fdb\uff0c\u907f\u514d\u6025\u4e8e\u6c42\u6210\u4ee5\u5916\uff0c\u6211\u8fd8\u8981\u63d0\u9192\u4e00\u70b9\uff1a\u4ea7\u540e\u65e9\u671f\u8981\u907f\u514d\u5267\u70c8\u8fd0\u52a8\u548c\u8fc7\u91cd\u7684\u4f53\u529b\u52b3\u52a8\uff08\u5982\u63d0\u91cd\u7269\u3001\u642c\u91cd\u7269\uff09\uff0c\u56e0\u4e3a\u4f1a\u5f71\u54cd\u76c6\u5e95\u808c\u5f20\u529b\u7684\u6062\u590d\uff0c\u5bb9\u6613\u9020\u6210\u76c6\u8154\u5668\u5b98\u7684\u8131\u5782\uff08\u6bd4\u5982\u5e38\u89c1\u7684\u5b50\u5bab\u8131\u5782\uff09\u3002</p>\r\n\r\n<p>\u521a\u624d\u8bb2\u7684\u662f\u4ea7\u540e\u4e00\u4e9b\u5750\u6708\u5b50\u7684\u7981\u5fcc\u3002\u5176\u5b9e\uff0c\u5173\u4e8e&ldquo;\u5b55\u671f&rdquo;\uff0c\u6211\u4eec\u4e5f\u6709\u4e00\u4e9b\u8bef\u533a\uff0c\u6bd4\u5982&ldquo;\u5b55\u5987\u8981\u591a\u5403&rdquo;\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/bzKrD51IevEelfLvH0eDVvmSATbx_13HvbmHOCNK78A4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8bb0\u5f97\u6211\u6000\u5b55\u7684\u65f6\u5019\uff0c\u72ec\u81ea\u5728\u65e5\u672c\u8bfb\u535a\u3002\u5f53\u65f6\u770b\u8fc7\u4e00\u4e2a\u7535\u89c6\u8282\u76ee\uff0c\u8282\u76ee\u91c7\u8bbf\u4e86\u4e00\u4e9b\u5e74\u7eaa\u5927\u7684\u4eba\uff0c\u8bf4\u4e00\u4e9b\u5173\u4e8e\u5b55\u671f\u7684\u8bb2\u7a76\u3002\u5979\u4eec\u8bf4\uff0c\u73b0\u5728\u6000\u5b55\u7684\u5e74\u8f7b\u4eba\u90fd\u5403\u5f97\u592a\u5c11\uff0c\u5979\u4eec\u5f53\u5e74\u8981&ldquo;\u4e00\u4e2a\u4eba\u8981\u5403\u4e24\u4e2a\u4eba\u7684\u996d&rdquo;&mdash;&mdash;\u65e5\u672c\u4e5f\u6709\u8fd9\u79cd\u8bf4\u6cd5\u3002</p>\r\n\r\n<p>\u4f46\u662f\u6211\u7684\u4ea7\u79d1\u533b\u751f\uff0c\u4ece\u6765\u6ca1\u6709\u8ddf\u6211\u8bf4\u8fc7\u8981&ldquo;\u4e00\u4e2a\u4eba\u5403\u4e24\u4e2a\u4eba\u7684\u996d&rdquo;\uff0c\u800c\u662f\u8981\u8425\u517b\u5747\u8861\uff0c\u7279\u522b\u8981\u6ce8\u610f\u6444\u5165\u4e00\u4e9b\u5fae\u91cf\u5143\u7d20\uff0c\u50cf\u94c1\u3001\u9499\uff0c\u907f\u514d\u8d2b\u8840\u7684\u51fa\u73b0\u3002\u8fd8\u5631\u5490\u6211\u8981\u6709\u9002\u5f53\u7684\u6d3b\u52a8\uff0c\u8981\u63a7\u5236\u4f53\u91cd\u3002</p>\r\n\r\n<p>\u6211\u5c31\u6309\u7167\u4ed6\u8bf4\u7684\u53bb\u505a\u3002\u5f53\u65f6\u4e5f\u6ca1\u6709\u4ec0\u4e48&ldquo;\u5b55\u5987\u4f53\u64cd&rdquo;\u3001&ldquo;\u5b55\u5987\u745c\u4f3d&rdquo;\uff0c\u6ca1\u6709\u4eba\u6765\u6307\u5bfc\u3002\u6211\u5c31\u91c7\u53d6\u4e86\u4e00\u4e2a\u6700\u7b80\u5355\u7684\u529e\u6cd5\uff1a\u5929\u6c14\u597d\u7684\u65f6\u5019\uff0c\u4ece\u5bbf\u820d\u5230\u5b66\u6821\uff0c\u6765\u56de\u8def\u7a0b\u6b65\u884c&mdash;&mdash;\u56e0\u4e3a\u8ddd\u79bb\u4e5f\u4e0d\u8fdc\u3002\u6211\u5728\u6574\u4e2a\u6000\u5b55\u671f\u95f4\uff0c\u4f53\u91cd\u63a7\u5236\u5f97\u5f88\u597d\uff1b\u540e\u6765\u6211\u5973\u513f\u51fa\u751f\uff0c\u4f53\u91cd\u4e5f\u4e0d\u662f\u5f88\u91cd\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/q2dVRmDKiyHpBJYutqkFle5Htx9zRmgF5DnUbAtqalk4BAAAewIAAEpQ.jpg\" style=\"height:376px; width:640px\" />\u6f14\u8bb2\u5609\u5bbe\u8d75\u7ea2\uff1a\u300a\u751f\u5a03\u5fc5\u987b\u5750\u6708\u5b50\uff1f\u804a\u804a\u90a3\u4e9b\u5973\u6027\u5b55\u4ea7\u5065\u5eb7\u7684\u8bef\u533a\u300b</p>\r\n\r\n<p>\u90a3\u6211\u4eec\u770b\u4e00\u770b\uff0c\u5982\u679c&ldquo;\u4e00\u4e2a\u4eba\u5403\u4e24\u4e2a\u4eba\u7684\u996d&rdquo;\uff0c\u7136\u540e\u53c8\u5c11\u6d3b\u52a8&mdash;&mdash;\u6211\u628a\u5b83\u6982\u62ec\u6210&ldquo;\u591a\u5403\u5c11\u52a8&rdquo;\uff0c\u5c31\u4f1a\u51fa\u73b0\u4e00\u4e9b\u95ee\u9898&mdash;&mdash;</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/Ix0PR4nqlWtfK82pz7q-r0IXI5CLFC8U0VLtNrKyW-E4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u6240\u4ee5\u5728\u4e34\u5e8a\u3001\u5728\u4ea7\u79d1\uff0c\u5bf9\u5b55\u5987\u7684\u5b55\u671f\u4f53\u91cd\u7ba1\u7406\u662f\u6bd4\u8f83\u4e25\u683c\u7684\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/v4Gq2VAMAsr02L7eCwq1x0ODpyPQoS1V8l-OfoAlHZ84BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u4ee5\u524d\u6211\u4eec\u4e00\u76f4\u662f\u7528\u8fd9\u4e2a\u6807\u51c6\uff1a\u5b55\u5987\u6574\u4e2a\u5b55\u671f\u4f53\u91cd\u589e\u52a0\u4e0d\u80fd\u8d85\u8fc712.5kg\uff0c\u5b55\u665a\u671f\u6bcf\u5468\u4e0d\u8d85\u8fc70.5kg\u3002\u4f46\u662f\u7531\u4e8e\u6bd4\u8f83\u7c97\u7565\uff0c\u73b0\u5728\u5f88\u591a\u533b\u9662\u662f\u53c2\u8003\u7f8e\u56fd\u533b\u5b66\u7814\u7a76\u9662\uff08IMO\uff09\u5236\u5b9a\u7684\u5b55\u671f\u4f53\u91cd\u589e\u52a0\u6307\u5357&mdash;&mdash;\u5b83\u662f\u6309\u7167\u5b55\u524d\u7684BMI\uff08Body Mass Index\uff0c\u4f53\u91cd\u5343\u514b\u6570\u9664\u4ee5\u8eab\u9ad8\u7c73\u6570\u7684\u5e73\u65b9\u5f97\u51fa\u7684\u6570\u5b57,\u662f\u56fd\u9645\u4e0a\u5e38\u7528\u7684\u8861\u91cf\u4eba\u4f53\u80d6\u7626\u7a0b\u5ea6\u4ee5\u53ca\u662f\u5426\u5065\u5eb7\u7684\u4e00\u4e2a\u6807\u51c6\uff09\u6765\u8fdb\u884c\u7ba1\u7406\uff0c\u66f4\u52a0\u7ec6\u81f4\u3002</p>\r\n\r\n<p>\u4e2d\u56fd\u76ee\u524d\u8fd8\u6ca1\u6709\u8fd9\u7c7b\u6307\u5357\uff0c\u4e0d\u8fc7\u6709\u4e00\u4e2a\u597d\u6d88\u606f&mdash;&mdash;\u6211\u4eec\u56fd\u5bb6\u4e00\u4e9b\u4e34\u5e8a\u533b\u751f\u548c\u8425\u517b\u5b66\u5bb6\uff0c\u6b63\u5728\u5236\u5b9a\u4e2d\u56fd\u5b55\u671f\u4f53\u91cd\u589e\u52a0\u6307\u5357\u3002\u5e0c\u671b\u4e0d\u4e45\u7684\u5c06\u6765\uff0c\u5c31\u53ef\u4ee5\u8fd0\u7528\u5230\u4e34\u5e8a\u4e0a\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/-2zvwcG84s467DT20gmNEquaDS_9xowFSlxmsjLBx5w4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u9664\u4e86\u4f53\u91cd\u7684\u70e6\u607c\u5916\uff0c\u5176\u5b9e\u5b55\u5987\u8fd8\u9762\u4e34\u7740\u9888\u80a9\u75db\u3001\u8170\u80cc\u75db\u7684\u56f0\u6270\u3002</p>\r\n\r\n<p>\u9888\u80a9\u75db\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u957f\u671f\u4f0f\u6848\u5de5\u4f5c\uff0c\u6216\u8005\u4e60\u60ef\u4f4e\u5934\u542b\u80f8\uff0c\u518d\u6709\u5c31\u662f\u559c\u6b22\u4f4e\u5934\u770b\u624b\u673a\u3002\u6b64\u5916\uff0c\u6000\u5b55\u4ee5\u540e\u4e73\u623f\u589e\u5927\uff0c\u4e5f\u52a0\u91cd\u4e86\u9888\u90e8\u53ca\u80a9\u80db\u90e8\u7684\u4e00\u4e9b\u808c\u7fa4\u7684\u8d1f\u62c5\uff0c\u65f6\u95f4\u957f\u4e86\u5c31\u4f1a\u51fa\u73b0\u75bc\u75db\u3002</p>\r\n\r\n<p>\u8170\u80cc\u75db\uff0c\u4e3b\u8981\u548c\u7ad9\u59ff\u3001\u5750\u59ff\u4e0d\u6b63\u786e\u6709\u5173\u3002\u6000\u5b55\u4ee5\u540e\u5b50\u5bab\u8d8a\u6765\u8d8a\u5927\uff0c\u8eab\u4f53\u7684\u91cd\u5fc3\u4e5f\u53d1\u751f\u53d8\u5316\uff0c\u52a0\u91cd\u4e86\u8170\u80cc\u90e8\u808c\u8089\u7684\u8d1f\u62c5\uff0c\u5bfc\u81f4\u75bc\u75db\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/sWwd01qG5sPplJY8WETjU1LOV9gfD_SJBUNlqNzuJf84BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u63a7\u5236\u4f53\u91cd\u548c\u7f13\u89e3\u9888\u80a9\u75db\u3001\u8170\u80cc\u75db\uff0c\u6700\u597d\u7684\u65b9\u6cd5\u5c31\u662f\u8981\u6709\u9002\u5f53\u7684\u8fd0\u52a8\u3002\u8fd9\u662f\u6211\u4eec\u5728\u5b55\u5987\u5b66\u6821\u7ed9\u5b55\u5987\u8fdb\u884c\u8fd0\u52a8\u6307\u5bfc\u7528\u7684\u4e00\u5f20\u5e7b\u706f\u7247\uff0c\u662f\u6559\u5979\u4eec&ldquo;\u6709\u6548\u8d70\u8def&rdquo;\uff08\u4e5f\u79f0&ldquo;\u516d\u6b65\u8d70\u8def\u6cd5&rdquo;\uff09\uff1a</p>\r\n\r\n<p>1\u3001\u8f7b\u677e\u3001\u81ea\u7136\u5730\u8d70</p>\r\n\r\n<p>2\u3001\u589e\u5927\u6b65\u5e45\uff0c\u4e00\u6b65\u5927\u6982\u670955-60\u5398\u7c73</p>\r\n\r\n<p>3\u3001\u5e45\u5ea6\u8f83\u5927\u5730\u6446\u81c2</p>\r\n\r\n<p>4\u3001\u914d\u5408\u4e0a\u6df1\u7f13\u7684\u547c\u5438\u3002\u4e24\u6b65\u4e00\u547c\uff0c\u4e24\u6b65\u4e00\u5438</p>\r\n\r\n<p>5\u3001\u52a0\u4e0a\u4e00\u4e9b\u4e0a\u80a2\u8fd0\u52a8\uff0c\u6bd4\u5982\u8bf4\u62ac\u81c2\u3001\u6269\u80f8</p>\r\n\r\n<p>6\u3001\u589e\u52a0\u4e0a\u80a2\u7684\u8d1f\u91cd\u3002\u6bd4\u5982\u4e24\u624b\u5404\u63e1\u4e00\u74f6\u77ff\u6cc9\u6c34\uff0c\u4e5f\u53ef\u4ee5\u7528\u5f39\u529b\u5e26\u6765\u505a\u4e0a\u80a2\u6297\u963b\u8fd0\u52a8</p>\r\n\r\n<p>\u8fd9\u6837\u8d70\u8def\u53ef\u4ee5\u6709\u6548\u953b\u70bc\u8eab\u4f53\u7684\u4e00\u4e9b\u808c\u7fa4\uff0c\u589e\u52a0\u808c\u8089\u7684\u529b\u91cf\uff0c\u6709\u52a9\u4e8e\u63a7\u5236\u4f53\u91cd\uff0c\u4e5f\u6709\u52a9\u4e8e\u4ee5\u540e\u7684\u5206\u5a29\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/dB7MOHwyiW5_PexwrCtPt4lUXLNOSUZ8sGzJmwzvRM44BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u9488\u5bf9\u9888\u80a9\u75db\u548c\u8170\u80cc\u75db\uff0c\u6211\u4eec\u8bf7\u6559\u4e86\u533b\u5b66\u8fd0\u52a8\u7684\u4e13\u5bb6\uff0c\u7ed9\u5b55\u5987\u8bbe\u8ba1\u4e86\u4e00\u4e9b\u7b80\u5355\u7684\u4f53\u64cd\u52a8\u4f5c\u3002\u7f13\u89e3\u80a9\u9888\u75db\uff0c\u53ef\u4ee5\u7528\u5de6\u56fe\u7684&ldquo;\u62db\u8d22\u732b&rdquo;\u52a8\u4f5c\uff1a\u80f3\u818a\u62ac\u8d77\u6765\uff0c\u5927\u81c2\u548c\u5730\u9762\u5e73\u884c\uff1b\u5c0f\u81c2\u548c\u5927\u81c2\u5782\u76f4\uff0c\u4ee5\u5927\u81c2\u4e3a\u8f74\u4e0a\u4e0b\u8f6c\u52a8\u3002\u5176\u5b9e\u4e0d\u5149\u662f\u5b55\u5987\uff0c\u7ecf\u5e38\u4f0f\u6848\u5de5\u4f5c\u7684\u670b\u53cb\u90fd\u53ef\u4ee5\u5c1d\u8bd5\uff0c\u5f88\u6709\u6548\u3002\u7f13\u89e3\u8170\u80cc\u75db\uff0c\u4e3b\u8981\u662f\u7528\u745c\u4f3d\u91cc\u8fb9\u7684&ldquo;\u732b\u5f0f\u4f38\u5c55&rdquo;\u4f53\u5f0f\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/8VFgpcfFHYaDyoGSCl0rZy3ngwjlxygjBoH1RmlcRs04BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8fd9\u51e0\u5f20\u7167\u7247\uff0c\u662f\u6211\u4eec\u5728\u5b55\u5987\u5b66\u6821\u6307\u5bfc\u5b55\u5987\u8fdb\u884c\u953b\u70bc\uff0c\u7528\u4e00\u4e9b\u7b80\u5355\u7684\u745c\u4f3d\u52a8\u4f5c\uff0c\u6216\u8005\u501f\u52a9\u5f39\u529b\u5e26\u6765\u953b\u70bc\u808c\u8089\u7684\u529b\u91cf\u3002\u5979\u4eec\u6765\u7684\u65f6\u5019\uff0c\u4e08\u592b\u90fd\u966a\u7740&mdash;&mdash;\u8fd8\u6709\u4e00\u4f4d\u5b55\u5987\u7684\u5988\u5988\u4e5f\u966a\u7740\u3002\u5979\u4eec\u63a5\u53d7\u5ea6\u5f88\u9ad8\uff0c\u8fd8\u7528\u624b\u673a\u5f55\u4e0b\u6765\uff0c\u56de\u53bb\u518d\u7ec3\u3002</p>\r\n\r\n<p>\u5927\u5bb6\u53ef\u80fd\u4f1a\u5bf9\u5b55\u671f\u7684\u8fd0\u52a8\u5b89\u5168\u6709\u7591\u95ee\u3002\u5176\u5b9e\uff0c\u4e0d\u662f\u6240\u6709\u7684\u5b55\u5987\u90fd\u9002\u5408\u505a\u8fd0\u52a8\uff0c\u6709\u4e00\u4e9b\u7981\u5fcc\u75c7\uff1a\u6709\u4e25\u91cd\u7684\u5fc3\u810f\u75c5\u3001\u80ba\u90e8\u75be\u75c5\uff0c\u6216\u8005\u6709\u591a\u6b21\u65e9\u4ea7\u53f2\uff0c\u80ce\u819c\u65e9\u7834\uff0c\u8fd8\u6709\u598a\u5a20\u671f\u9ad8\u8840\u538b\u7684\u5b55\u5987\uff0c\u662f\u4e0d\u5efa\u8bae\u8fd0\u52a8\u7684\u3002\u53e6\u5916\uff0c\u6211\u4eec\u8981\u9075\u4ece\u81ea\u5df1\u7684\u4e3b\u89c2\u611f\u89c9\uff0c\u5982\u679c\u6709\u547c\u5438\u56f0\u96be\u6216\u8005\u5fc3\u614c\uff0c\u90a3\u5c31\u505c\u4e0b\u6765\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/sxcLwhI7v8Ouja47SuC4v_pekvumKSTDS0Yd8a6Wl6g4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u518d\u6709\u5c31\u662f\uff0c\u53ef\u4ee5\u5bf9\u7167\u8fd9\u4e2a\u4e3b\u89c2\u611f\u89c9\u91cf\u8868&mdash;&mdash;\u5de6\u8fb9\u7684\u6570\u5b57\u4e58\u4ee510\uff0c\u57fa\u672c\u4e0a\u5c31\u662f\u8fd0\u52a8\u540e\u8fbe\u5230\u7684\u76ee\u6807\u5fc3\u7387\u3002\u53ea\u8981\u6211\u4eec\u7684\u611f\u89c9\u5728\u8fd9\u4e2a\u7ea2\u6846\u4e4b\u5185\uff0c\u90a3\u5c31\u662f\u76f8\u5bf9\u5b89\u5168\u7684\u3002</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/_bTU2kX3VPJqEm2sJ6zSOl99w97YgEjWhHmVLjlM_Pc4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8fd9\u662f\u6211\u975e\u5e38\u559c\u6b22\u7684\u4e00\u5f20\u7167\u7247\uff0c\u662f\u534f\u548c\u533b\u9662\u4ea7\u79d1\u7684\u9a6c\u826f\u5764\u5927\u592b\uff0c\u5979\u662f\u4e00\u4f4d\u9ad8\u9f84\u4e8c\u80ce\u7684\u5b55\u5987\u3002\u5979\u5728\u7e41\u91cd\u7684\u533b\u7597\u79d1\u7814\u3001\u6559\u5b66\u5de5\u4f5c\u7684\u540c\u65f6\uff0c\u8fd8\u575a\u6301\u5b55\u671f\u953b\u70bc\uff0c\u4f53\u91cd\u63a7\u5236\u5f97\u5f88\u597d\u3002\u5206\u5a29\u5f88\u987a\u5229\uff0c\u4ea7\u540e\u4e5f\u79ef\u6781\u953b\u70bc\uff0c\u5f88\u5feb\u5c31\u53c8\u6295\u8eab\u5230\u5de5\u4f5c\u5f53\u4e2d\u3002</p>\r\n\r\n<p>\u5927\u5bb6\u53ef\u80fd\u4f1a\u8bf4\uff0c\u9a6c\u5927\u592b\u662f\u4ea7\u79d1\u533b\u751f\uff0c\u548c\u6211\u4eec\u666e\u901a\u4eba\u4e0d\u4e00\u6837\u3002\u90a3\u6211\u4eec\u4e5f\u53ef\u4ee5\u770b\u666e\u901a\u4eba\u7684\u4f8b\u5b50&mdash;&mdash;</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/0HCK8cAnQe3omaHzkrzW4NXQufnA8xhAtrX8BEKhihs4BAAAXwIAAEpQ.jpg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p>\u8fd9\u662f\u6211\u4eec\u5b55\u5987\u5b66\u6821\u91cc\u4e00\u4f4d\u5e74\u8f7b\u7684\u5988\u5988\uff0c\u5979\u5728\u5b55\u671f\u6309\u7167\u6211\u4eec\u7684\u6307\u5bfc\u575a\u6301\u953b\u70bc\uff0c\u6708\u5b50\u671f\u95f4\u4e5f\u505a\u4e00\u4e9b\u7b80\u5355\u7684\u745c\u4f3d\u52a8\u4f5c\u3002\u5de6\u8fb9\u8fd9\u5f20\u662f\u5979\u5728\u5b69\u5b50\u6ee1\u6708\u65f6\u5019\u7684\u7167\u7247\uff0c\u7cbe\u795e\u72b6\u6001\u5f88\u597d\u3002\u516b\u4e2a\u6708\u7684\u65f6\u5019\uff0c\u5979\u5f00\u59cb\u505a\u745c\u4f3d\uff1b\u4e00\u5e74\u4e4b\u5185\uff0c\u4f53\u91cd\u5c31\u6062\u590d\u5230\u4e86\u5b55\u524d\u7684\u72b6\u51b5\u3002</p>\r\n\r\n<p>\u6211\u4e5f\u5e0c\u671b\u6709\u66f4\u591a\u7684\u5b55\u4ea7\u5987\uff0c\u80fd\u50cf\u5979\u4e00\u6837\u987a\u5229\u5730\u5ea6\u8fc7\u5b55\u4ea7\u671f\uff0c\u505a\u4e00\u4e2a\u5065\u5eb7\u7f8e\u4e3d\u5feb\u4e50\u7684\u5988\u5988\u3002</p>\r\n\r\n<p>\u6211\u7684\u5206\u4eab\u5c31\u5230\u8fd9\u91cc\uff0c\u8c22\u8c22\u5927\u5bb6\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/741fnis6MxMRXRQmY6W_q537-Jftd6aMnOTDtPXIvp44BAAAuwIAAEpQ.jpg\" style=\"height:414px; width:640px\" />\u6f14\u8bb2\u5609\u5bbe\u8d75\u7ea2\uff1a\u300a\u751f\u5a03\u5fc5\u987b\u5750\u6708\u5b50\uff1f\u804a\u804a\u90a3\u4e9b\u5973\u6027\u5b55\u4ea7\u5065\u5eb7\u7684\u8bef\u533a\u300b</p>", "date_created": "2019-05-21T11:05:13.298643+08:00", "date_modified": "2019-05-21T16:41:07.669226+08:00", "date_published": "2019-05-21T16:39:57+08:00", "id": 452068, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "health", "replies_count": 10, "small_image": "https://1-im.guokr.com/k0SJrRje_d669u4F4vT0kMh5MsDEzoP0jraubqzIY2U4BAAAuwIAAEpQ.jpg", "subject": {"articles_count": 1946, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "medicine", "minisite_key": "health", "name": "\u533b\u5b66", "sort_score": 5, "subject_type": "normal"}, "subject_key": "medicine", "summary": "\u638c\u63e1\u5bf9\u7684\u65b9\u6cd5\uff0c\u666e\u901a\u4eba\u4e5f\u53ef\u4ee5\u50cf\u516c\u4f17\u4eba\u7269\u4e00\u6837\u4f18\u96c5\u3002", "text": "\u672c\u6587\u4e3a2019\u5e744\u670827\u65e5\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u6f14\u8bb2\u6d3b\u52a8\u7b2c\u5341\u671f\u2014\u2014\u5bf9\u8c23\u8a00\u8bf4\u201c\u4e0d\u201d| \u8d75\u7ea2 \u6f14\u8bb2\u5b9e\u5f55\uff1a\u201c\u5c11\u6d3b\u52a8\uff0c\u514d\u5f97\u52a8\u4e86\u80ce\u6c14\u3002\u201d \u201c\u4e00\u4e2a\u4eba\u8981\u5403\u4e24\u4e2a\u4eba\u7684\u996d\u3002\u201d\u60f3\u8981\u987a\u5229\u5ea6\u8fc7\u5b55\u4ea7\u671f\uff1f\u201c\u591a\u5403\u5c11\u52a8\u201d\u53ef\u4e0d\u884c\u3002\u201c\u4f20\u7edf\u2018\u5750\u6708\u5b50\u2019\u7406\u5ff5\u4e2d\uff0c\u5f88\u591a\u505a\u6cd5\u7684\u521d\u8877\u662f\u4e3a\u4e86\u4ea7\u5987\u5065\u5eb7\uff0c\u4f46\u4ece\u73b0\u4ee3\u533b\u5b66\u89c6\u89d2\u51fa\u53d1\uff0c\u8fd9\u4e9b\u505a\u6cd5\u53cd\u800c\u4f1a\u5e26\u6765\u5371\u5bb3\u3002\u201d\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u7b2c\u5341\u671f\u6f14\u8bb2\uff0c\u5317\u4eac\u534f\u548c\u533b\u5b66\u9662\u62a4\u7406\u5b66\u9662\u6559\u6388\u3001\u4e34\u5e8a\u62a4\u7406\u5b66\u7cfb\u526f\u4e3b\u4efb\u8d75\u7ea2\u544a\u8bc9\u4f60\uff0c\u638c\u63e1\u5bf9\u65b9\u6cd5\uff0c\u666e\u901a\u4eba\u7684\u5b55\u4ea7\u671f\u4e5f\u53ef\u4ee5\u50cf\u516c\u4f17\u4eba\u7269\u90a3\u6837\u4f18\u96c5\u3002\u5927\u5bb6\u597d\uff0c\u6211\u662f\u8d75\u7ea2\uff0c\u6211\u662f\u5317\u4eac\u534f\u548c\u533b\u5b66\u9662\u62a4\u7406\u5b66\u9662\u7684\u6559\u6388\u3002\u5f88\u9ad8\u5174\u4eca\u5929\u80fd\u5728\u8fd9\u91cc\uff0c\u8ddf\u5927\u5bb6\u5206\u4eab\u4e00\u4e9b\u6211\u505a\u7684\u5de5\u4f5c\u3002\u8fd9\u662f\u4e00\u5f20\u53bb\u5e74\u5728\u7f51\u7edc\u4e0a\u5f15\u8d77\u70ed\u8bae\u7684\u7167\u7247\uff0c\u76f8\u4fe1\u5927\u5bb6\u90fd\u770b\u5230\u8fc7\u3002\u53bb\u5e744\u6708\uff0c\u82f1\u56fd\u7684\u51ef\u7279\u738b\u5983\u5728\u751f\u5b8c\u7b2c\u4e09\u4e2a\u5b69\u5b50\u4ee5\u540e\u7684\u4e03\u4e2a\u5c0f\u65f6\uff0c\u5c31\u795e\u91c7\u5955\u5955\u5730\u51fa\u73b0\u5728\u4e86\u516c\u4f17\u7684\u9762\u524d\uff0c\u5986\u5bb9\u7cbe\u81f4\uff0c\u8fd8\u7a7f\u7740\u8fde\u8863\u88d9\uff0c\u8fd8\u7a7f\u7740\u9ad8\u8ddf\u978b\u3002\u56db\u6708\u4efd\u5929\u6c14\u8fd8\u662f\u6bd4\u8f83\u51c9\u7684\u3002\u6211\u5173\u6ce8\u4e86\u4e00\u4e0b\u8fd9\u6761\u65b0\u95fb\u540e\u9762\u5927\u5bb6\u7684\u7559\u8a00\uff0c\u6709\u8d5e\u6210\uff0c\u6709\u53cd\u5bf9\uff0c\u6709\u7fa1\u6155\uff0c\u66f4\u591a\u7684\u662f\u8d28\u7591\u2014\u2014\u5f88\u591a\u4eba\u90fd\u5728\u95ee\uff0c\u5750\u6708\u5b50\u80fd\u8fd9\u6837\u5417\uff1f\u518d\u6765\u770b\u8fd9\u5f20\u7167\u7247\uff0c\u76f8\u4fe1\u5927\u5bb6\u4e5f\u8ba4\u8bc6\u8fd9\u4e24\u4f4d\uff0c\u5f71\u89c6\u660e\u661f\u9ec4\u6653\u660e\u548cAngelababy\uff08\u6768\u9896\uff09\u3002\u8fd9\u5f20\u7167\u7247\u662fAngelababy\u751f\u5b8c\u5b69\u5b50\u4e09\u5929\u51fa\u9662\u7684\u65f6\u5019\u62cd\u7684\u7167\u7247\uff0c\u540c\u6837\u5f88\u7cbe\u795e\u3001\u5f88\u6f02\u4eae\u3002\u90a3\u53ef\u80fd\u6709\u670b\u53cb\u4f1a\u8bf4\uff0c\u51ef\u7279\u738b\u5983\u662f\u5916\u56fd\u4eba\uff0cAngelababy\u662f\u660e\u661f\uff0c\u5979\u4eec\u548c\u6211\u4eec\u666e\u901a\u4eba\u4e0d\u4e00\u6837\u3002\u6211\u4eec\u666e\u901a\u4eba\u5750\u6708\u5b50\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u4e0a\u56fe\u5c31\u662f\u4f20\u8bf4\u4e2d\u5750\u6708\u5b50\u7684\u5404\u79cd\u7981\u5fcc\u3002\u5f88\u591a\u670b\u53cb\u8bf4\uff0c\u6211\u542c\u5230\u8fd9\u4e9b\u90fd\u8981\u6291\u90c1\u4e86\uff0c\u8fd9\u4e00\u4e2a\u6708\u600e\u4e48\u8fc7\u5462\uff1f20\u591a\u5e74\u524d\uff0c\u6211\u5728\u533b\u9662\u5de5\u4f5c\u7684\u65f6\u5019\uff0c\u8fd9\u4e9b\u7981\u5fcc\u5f71\u54cd\u5f88\u6df1\u3002\u6bd4\u5982\u8bf4\u75c5\u623f\u6bcf\u5929\u8981\u901a\u98ce\u6362\u6c14\uff0c\u4f46\u662f\u4ea7\u79d1\u75c5\u623f\u5f88\u96be\u505a\u5230\u3002\u590f\u5929\u8981\u60f3\u5f00\u7a7a\u8c03\uff0c\u4e5f\u4f1a\u906d\u5230\u4e00\u81f4\u53cd\u5bf9\u3002\u6709\u4e00\u5e74\u590f\u5929\uff0c\u5929\u6c14\u5f88\u70ed\uff0c\u75c5\u623f\u91cc\u6709\u4ea7\u5987\u3001\u5b69\u5b50\uff0c\u8fd8\u6709\u6765\u63a2\u89c6\u7684\u5bb6\u5c5e\u3002\u4e00\u5c4b\u5b50\u4eba\uff0c\u4e0d\u8ba9\u5f00\u7a7a\u8c03\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6709\u4e00\u4e2a\u5bb6\u5c5e\u4ece\u623f\u95f4\u91cc\u6643\u6643\u60a0\u60a0\u5730\u51fa\u6765\uff0c\u6211\u5c31\u8d76\u5feb\u8fc7\u53bb\u67e5\u770b\uff0c\u53d1\u73b0\u4ed6\u4e2d\u6691\u4e86\u3002\u6211\u5f53\u65f6\u5c31\u6025\u4e86\uff0c\u8d76\u5feb\u628a\u4ed6\u5b89\u7f6e\u7ed9\u6211\u7684\u540c\u4e8b\uff0c\u7136\u540e\u8dd1\u5230\u75c5\u623f\u91cc\u8fb9\u53bb\u8ddf\u5bb6\u5c5e\u8fd8\u6709\u4ea7\u5987\u8bf4\uff1a\u4e00\u5b9a\u8981\u5f00\u7a7a\u8c03\uff0c\u56e0\u4e3a\u623f\u95f4\u91cc\u6e29\u5ea6\u5b9e\u5728\u592a\u9ad8\u4e86\uff0c\u521a\u624d\u6765\u63a2\u89c6\u7684\u5bb6\u5c5e\u90fd\u4e2d\u6691\u4e86\uff0c\u90a3\u4ea7\u5987\u3001\u65b0\u751f\u513f\u600e\u4e48\u53d7\u5f97\u4e86\uff1f\u5927\u5bb6\u8981\u77e5\u9053\uff0c\u65b0\u751f\u513f\u4f53\u6e29\u8c03\u8282\u4e2d\u67a2\u53d1\u80b2\u8fd8\u4e0d\u5b8c\u5584\uff0c\u5f88\u5bb9\u6613\u53d7\u5230\u5916\u754c\u6e29\u5ea6\u5f71\u54cd\u800c\u53d1\u70e7\u3002\u6211\u8fd9\u6837\u89e3\u91ca\u4ee5\u540e\uff0c\u4ea7\u5987\u548c\u5bb6\u5c5e\u90fd\u8868\u793a\u7406\u89e3\uff0c\u5c31\u540c\u610f\u5f00\u7a97\u4e86\u3002\u4f20\u7edf\u4e2d\u201c\u5750\u6708\u5b50\u201d\u7684\u4e00\u4e9b\u7981\u5fcc\uff0c\u5176\u5b9e\u548c\u6211\u4eec\u8fc7\u53bb\u7684\u751f\u6d3b\u6761\u4ef6\u3001\u751f\u6d3b\u73af\u5883\u6709\u5173\u3002\u8fc7\u53bb\u623f\u5b50\u5bc6\u95ed\u6027\u4e0d\u597d\uff0c\u70ed\u6c34\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u4ea7\u5987\u51fa\u6c57\u6bd4\u8f83\u591a\uff0c\u5982\u679c\u6d17\u6fa1\u6e29\u5ea6\u8fbe\u4e0d\u5230\u8981\u6c42\uff0c\u5c31\u5bb9\u6613\u53d7\u51c9\u3002\u4f46\u662f\u73b0\u5728\u751f\u6d3b\u6761\u4ef6\u6539\u5584\u4e86\uff0c\u6240\u4ee5\u6d17\u5934\u3001\u6d17\u6fa1\u6ca1\u6709\u95ee\u9898\u3002\u73b0\u4ee3\u533b\u5b66\u8bb2\u5750\u6708\u5b50\u5417\uff1f\u4e5f\u8bb2\uff0c\u53eb\u201c\u4ea7\u8925\u671f\u201d\u3002\u6240\u8c13\u201c\u4ea7\u8925\u671f\u201d\uff0c\u662f\u6307\u80ce\u76d8\u5a29\u51fa\u4ee5\u540e\uff0c\u9664\u4e73\u817a\u5916\u8eab\u4f53\u5404\u5668\u5b98\u6062\u590d\u5230\u6000\u5b55\u524d\u72b6\u6001\u7684\u4e00\u6bb5\u65f6\u95f4\uff0c\u901a\u5e38\u662f\u516d\u5468\u3002\u5728\u6b64\u671f\u95f4\uff0c\u4ea7\u5987\u7684\u8eab\u4f53\u4f1a\u53d1\u751f\u5f88\u591a\u53d8\u5316\uff0c\u5176\u4e2d\u6700\u4e3b\u8981\u3001\u6700\u660e\u663e\u7684\u662f\u5b50\u5bab\u7684\u53d8\u5316\u3002\u53ef\u4ee5\u770b\u5230\uff08\u5982\u4e0a\u56fe\uff09\uff0c\u4ea7\u540e\u5b50\u5bab\u4f1a\u9010\u6e10\u6536\u7f29\u3001\u53d8\u5c0f\uff1b\u6bcf\u5929\u5b50\u5bab\u5e95\u7684\u9ad8\u5ea6\u4f1a\u4e0b\u964d\u5927\u7ea6\u4e00\u5398\u7c73\uff1b\u5341\u5929\u4e4b\u540e\uff0c\u57fa\u672c\u4e0a\u5c31\u964d\u56de\u76c6\u8154\u3002\u80ce\u76d8\u5265\u79bb\u540e\uff0c\u9644\u7740\u90e8\u4f4d\u7684\u5b50\u5bab\u5185\u819c\u4e5f\u9700\u8981\u4fee\u590d\uff0c\u5927\u6982\u9700\u8981\u516d\u5468\uff0c\u4e00\u4e9b\u8840\u6db2\u8fd8\u6709\u5b50\u5bab\u5185\u819c\u7684\u788e\u7247\uff0c\u4f1a\u5f62\u6210\u6076\u9732\u6392\u51fa\u4f53\u5916\u3002\u9664\u751f\u6b96\u7cfb\u7edf\u5916\uff0c\u4ea7\u5987\u7684\u5fc3\u8840\u7ba1\u7cfb\u7edf\u3001\u6ccc\u5c3f\u7cfb\u7edf\u8fd8\u6709\u5185\u5206\u6ccc\u7cfb\u7edf\u7b49\u4e5f\u4f1a\u53d1\u751f\u4e00\u7cfb\u5217\u53d8\u5316\u3002\u9664\u8eab\u4f53\u6062\u590d\u5916\uff0c\u4ea7\u5987\u4e5f\u9700\u8981\u4f11\u606f\uff1a\u5206\u5a29\u6d88\u8017\u4e86\u5979\u4eec\u5927\u91cf\u4f53\u529b\uff0c\u4ea7\u540e\u8fd8\u8981\u6bcd\u4e73\u5582\u517b\uff0c\u5341\u5206\u8f9b\u82e6\u3002\u662f\u4e0d\u662f\u4ea7\u540e30\u5929\u5fc5\u987b\u4e00\u76f4\u8eba\u5728\u5e8a\u4e0a\uff0c\u4e0d\u80fd\u4e0b\u5730\u3001\u4e0d\u80fd\u6d3b\u52a8\uff1f\u56de\u7b54\u662f\u5426\u5b9a\u7684\uff0c\u4ea7\u540e\u7684\u8fd0\u52a8\u975e\u5e38\u5fc5\u8981\uff0c\u800c\u4e14\u6709\u76ca\u2014\u2014\u4f46\u662f\u4ea7\u540e\u8fd0\u52a8\u4e5f\u8981\u8bb2\u7a76\u5faa\u5e8f\u6e10\u8fdb\u3002\u6211\u5728\u8fd9\u60f3\u5f3a\u8c03\uff1a\u4ea7\u540e\u5934\u51e0\u5929\uff0c\u8fd0\u52a8\u8981\u6ce8\u610f\u5b89\u5168\u2014\u2014\u7279\u522b\u662f\u4ea7\u540e\u7b2c\u4e00\u6b21\u4e0b\u5730\u6d3b\u52a8\uff0c\u4e00\u5b9a\u8981\u6709\u4eba\u966a\u4f34\uff0c\u907f\u514d\u56e0\u4e3a\u8eab\u4f53\u865a\u5f31\u800c\u8dcc\u5012\uff0c\u51fa\u73b0\u5371\u9669\u3002\u4ea7\u540e\u5934\u51e0\u5929\uff0c\u4ea7\u5987\u53ef\u4ee5\u5728\u5e8a\u4e0a\u505a\u4e00\u4e9b\u7b80\u5355\u7684\u8fd0\u52a8\uff08\u5982\u4e0a\u56fe\uff09\u3002\u6bd4\u5982\u8bf4\uff0c\u8eba\u5728\u5e8a\u4e0a\u52fe\u811a\u3001\u7ef7\u811a\uff0c\u4fc3\u8fdb\u5c0f\u817f\u808c\u8089\u6536\u7f29\u653e\u677e\uff0c\u6709\u5229\u4e8e\u8840\u6db2\u5faa\u73af\uff0c\u4e5f\u53ef\u4ee5\u505a\u4e3e\u817f\u548c\u62ac\u81c0\u7684\u8fd0\u52a8\uff0c\u4fc3\u8fdb\u817f\u90e8\u3001\u8179\u90e8\u808c\u8089\u7684\u6536\u7f29\uff0c\u6709\u52a9\u4e8e\u4f53\u578b\u91cd\u5851\u3002\u9664\u4e86\u8981\u5faa\u5e8f\u6e10\u8fdb\uff0c\u907f\u514d\u6025\u4e8e\u6c42\u6210\u4ee5\u5916\uff0c\u6211\u8fd8\u8981\u63d0\u9192\u4e00\u70b9\uff1a\u4ea7\u540e\u65e9\u671f\u8981\u907f\u514d\u5267\u70c8\u8fd0\u52a8\u548c\u8fc7\u91cd\u7684\u4f53\u529b\u52b3\u52a8\uff08\u5982\u63d0\u91cd\u7269\u3001\u642c\u91cd\u7269\uff09\uff0c\u56e0\u4e3a\u4f1a\u5f71\u54cd\u76c6\u5e95\u808c\u5f20\u529b\u7684\u6062\u590d\uff0c\u5bb9\u6613\u9020\u6210\u76c6\u8154\u5668\u5b98\u7684\u8131\u5782\uff08\u6bd4\u5982\u5e38\u89c1\u7684\u5b50\u5bab\u8131\u5782\uff09\u3002\u521a\u624d\u8bb2\u7684\u662f\u4ea7\u540e\u4e00\u4e9b\u5750\u6708\u5b50\u7684\u7981\u5fcc\u3002\u5176\u5b9e\uff0c\u5173\u4e8e\u201c\u5b55\u671f\u201d\uff0c\u6211\u4eec\u4e5f\u6709\u4e00\u4e9b\u8bef\u533a\uff0c\u6bd4\u5982\u201c\u5b55\u5987\u8981\u591a\u5403\u201d\u3002\u8bb0\u5f97\u6211\u6000\u5b55\u7684\u65f6\u5019\uff0c\u72ec\u81ea\u5728\u65e5\u672c\u8bfb\u535a\u3002\u5f53\u65f6\u770b\u8fc7\u4e00\u4e2a\u7535\u89c6\u8282\u76ee\uff0c\u8282\u76ee\u91c7\u8bbf\u4e86\u4e00\u4e9b\u5e74\u7eaa\u5927\u7684\u4eba\uff0c\u8bf4\u4e00\u4e9b\u5173\u4e8e\u5b55\u671f\u7684\u8bb2\u7a76\u3002\u5979\u4eec\u8bf4\uff0c\u73b0\u5728\u6000\u5b55\u7684\u5e74\u8f7b\u4eba\u90fd\u5403\u5f97\u592a\u5c11\uff0c\u5979\u4eec\u5f53\u5e74\u8981\u201c\u4e00\u4e2a\u4eba\u8981\u5403\u4e24\u4e2a\u4eba\u7684\u996d\u201d\u2014\u2014\u65e5\u672c\u4e5f\u6709\u8fd9\u79cd\u8bf4\u6cd5\u3002\u4f46\u662f\u6211\u7684\u4ea7\u79d1\u533b\u751f\uff0c\u4ece\u6765\u6ca1\u6709\u8ddf\u6211\u8bf4\u8fc7\u8981\u201c\u4e00\u4e2a\u4eba\u5403\u4e24\u4e2a\u4eba\u7684\u996d\u201d\uff0c\u800c\u662f\u8981\u8425\u517b\u5747\u8861\uff0c\u7279\u522b\u8981\u6ce8\u610f\u6444\u5165\u4e00\u4e9b\u5fae\u91cf\u5143\u7d20\uff0c\u50cf\u94c1\u3001\u9499\uff0c\u907f\u514d\u8d2b\u8840\u7684\u51fa\u73b0\u3002\u8fd8\u5631\u5490\u6211\u8981\u6709\u9002\u5f53\u7684\u6d3b\u52a8\uff0c\u8981\u63a7\u5236\u4f53\u91cd\u3002\u6211\u5c31\u6309\u7167\u4ed6\u8bf4\u7684\u53bb\u505a\u3002\u5f53\u65f6\u4e5f\u6ca1\u6709\u4ec0\u4e48\u201c\u5b55\u5987\u4f53\u64cd\u201d\u3001\u201c\u5b55\u5987\u745c\u4f3d\u201d\uff0c\u6ca1\u6709\u4eba\u6765\u6307\u5bfc\u3002\u6211\u5c31\u91c7\u53d6\u4e86\u4e00\u4e2a\u6700\u7b80\u5355\u7684\u529e\u6cd5\uff1a\u5929\u6c14\u597d\u7684\u65f6\u5019\uff0c\u4ece\u5bbf\u820d\u5230\u5b66\u6821\uff0c\u6765\u56de\u8def\u7a0b\u6b65\u884c\u2014\u2014\u56e0\u4e3a\u8ddd\u79bb\u4e5f\u4e0d\u8fdc\u3002\u6211\u5728\u6574\u4e2a\u6000\u5b55\u671f\u95f4\uff0c\u4f53\u91cd\u63a7\u5236\u5f97\u5f88\u597d\uff1b\u540e\u6765\u6211\u5973\u513f\u51fa\u751f\uff0c\u4f53\u91cd\u4e5f\u4e0d\u662f\u5f88\u91cd\u3002\u6f14\u8bb2\u5609\u5bbe\u8d75\u7ea2\uff1a\u300a\u751f\u5a03\u5fc5\u987b\u5750\u6708\u5b50\uff1f\u804a\u804a\u90a3\u4e9b\u5973\u6027\u5b55\u4ea7\u5065\u5eb7\u7684\u8bef\u533a\u300b\u90a3\u6211\u4eec\u770b\u4e00\u770b\uff0c\u5982\u679c\u201c\u4e00\u4e2a\u4eba\u5403\u4e24\u4e2a\u4eba\u7684\u996d\u201d\uff0c\u7136\u540e\u53c8\u5c11\u6d3b\u52a8\u2014\u2014\u6211\u628a\u5b83\u6982\u62ec\u6210\u201c\u591a\u5403\u5c11\u52a8\u201d\uff0c\u5c31\u4f1a\u51fa\u73b0\u4e00\u4e9b\u95ee\u9898\u2014\u2014\u6240\u4ee5\u5728\u4e34\u5e8a\u3001\u5728\u4ea7\u79d1\uff0c\u5bf9\u5b55\u5987\u7684\u5b55\u671f\u4f53\u91cd\u7ba1\u7406\u662f\u6bd4\u8f83\u4e25\u683c\u7684\u3002\u4ee5\u524d\u6211\u4eec\u4e00\u76f4\u662f\u7528\u8fd9\u4e2a\u6807\u51c6\uff1a\u5b55\u5987\u6574\u4e2a\u5b55\u671f\u4f53\u91cd\u589e\u52a0\u4e0d\u80fd\u8d85\u8fc712.5kg\uff0c\u5b55\u665a\u671f\u6bcf\u5468\u4e0d\u8d85\u8fc70.5kg\u3002\u4f46\u662f\u7531\u4e8e\u6bd4\u8f83\u7c97\u7565\uff0c\u73b0\u5728\u5f88\u591a\u533b\u9662\u662f\u53c2\u8003\u7f8e\u56fd\u533b\u5b66\u7814\u7a76\u9662\uff08IMO\uff09\u5236\u5b9a\u7684\u5b55\u671f\u4f53\u91cd\u589e\u52a0\u6307\u5357\u2014\u2014\u5b83\u662f\u6309\u7167\u5b55\u524d\u7684BMI\uff08Body Mass Index\uff0c\u4f53\u91cd\u5343\u514b\u6570\u9664\u4ee5\u8eab\u9ad8\u7c73\u6570\u7684\u5e73\u65b9\u5f97\u51fa\u7684\u6570\u5b57,\u662f\u56fd\u9645\u4e0a\u5e38\u7528\u7684\u8861\u91cf\u4eba\u4f53\u80d6\u7626\u7a0b\u5ea6\u4ee5\u53ca\u662f\u5426\u5065\u5eb7\u7684\u4e00\u4e2a\u6807\u51c6\uff09\u6765\u8fdb\u884c\u7ba1\u7406\uff0c\u66f4\u52a0\u7ec6\u81f4\u3002\u4e2d\u56fd\u76ee\u524d\u8fd8\u6ca1\u6709\u8fd9\u7c7b\u6307\u5357\uff0c\u4e0d\u8fc7\u6709\u4e00\u4e2a\u597d\u6d88\u606f\u2014\u2014\u6211\u4eec\u56fd\u5bb6\u4e00\u4e9b\u4e34\u5e8a\u533b\u751f\u548c\u8425\u517b\u5b66\u5bb6\uff0c\u6b63\u5728\u5236\u5b9a\u4e2d\u56fd\u5b55\u671f\u4f53\u91cd\u589e\u52a0\u6307\u5357\u3002\u5e0c\u671b\u4e0d\u4e45\u7684\u5c06\u6765\uff0c\u5c31\u53ef\u4ee5\u8fd0\u7528\u5230\u4e34\u5e8a\u4e0a\u3002\u9664\u4e86\u4f53\u91cd\u7684\u70e6\u607c\u5916\uff0c\u5176\u5b9e\u5b55\u5987\u8fd8\u9762\u4e34\u7740\u9888\u80a9\u75db\u3001\u8170\u80cc\u75db\u7684\u56f0\u6270\u3002\u9888\u80a9\u75db\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u957f\u671f\u4f0f\u6848\u5de5\u4f5c\uff0c\u6216\u8005\u4e60\u60ef\u4f4e\u5934\u542b\u80f8\uff0c\u518d\u6709\u5c31\u662f\u559c\u6b22\u4f4e\u5934\u770b\u624b\u673a\u3002\u6b64\u5916\uff0c\u6000\u5b55\u4ee5\u540e\u4e73\u623f\u589e\u5927\uff0c\u4e5f\u52a0\u91cd\u4e86\u9888\u90e8\u53ca\u80a9\u80db\u90e8\u7684\u4e00\u4e9b\u808c\u7fa4\u7684\u8d1f\u62c5\uff0c\u65f6\u95f4\u957f\u4e86\u5c31\u4f1a\u51fa\u73b0\u75bc\u75db\u3002\u8170\u80cc\u75db\uff0c\u4e3b\u8981\u548c\u7ad9\u59ff\u3001\u5750\u59ff\u4e0d\u6b63\u786e\u6709\u5173\u3002\u6000\u5b55\u4ee5\u540e\u5b50\u5bab\u8d8a\u6765\u8d8a\u5927\uff0c\u8eab\u4f53\u7684\u91cd\u5fc3\u4e5f\u53d1\u751f\u53d8\u5316\uff0c\u52a0\u91cd\u4e86\u8170\u80cc\u90e8\u808c\u8089\u7684\u8d1f\u62c5\uff0c\u5bfc\u81f4\u75bc\u75db\u3002\u63a7\u5236\u4f53\u91cd\u548c\u7f13\u89e3\u9888\u80a9\u75db\u3001\u8170\u80cc\u75db\uff0c\u6700\u597d\u7684\u65b9\u6cd5\u5c31\u662f\u8981\u6709\u9002\u5f53\u7684\u8fd0\u52a8\u3002\u8fd9\u662f\u6211\u4eec\u5728\u5b55\u5987\u5b66\u6821\u7ed9\u5b55\u5987\u8fdb\u884c\u8fd0\u52a8\u6307\u5bfc\u7528\u7684\u4e00\u5f20\u5e7b\u706f\u7247\uff0c\u662f\u6559\u5979\u4eec\u201c\u6709\u6548\u8d70\u8def\u201d\uff08\u4e5f\u79f0\u201c\u516d\u6b65\u8d70\u8def\u6cd5\u201d\uff09\uff1a1\u3001\u8f7b\u677e\u3001\u81ea\u7136\u5730\u8d702\u3001\u589e\u5927\u6b65\u5e45\uff0c\u4e00\u6b65\u5927\u6982\u670955-60\u5398\u7c733\u3001\u5e45\u5ea6\u8f83\u5927\u5730\u6446\u81c24\u3001\u914d\u5408\u4e0a\u6df1\u7f13\u7684\u547c\u5438\u3002\u4e24\u6b65\u4e00\u547c\uff0c\u4e24\u6b65\u4e00\u54385\u3001\u52a0\u4e0a\u4e00\u4e9b\u4e0a\u80a2\u8fd0\u52a8\uff0c\u6bd4\u5982\u8bf4\u62ac\u81c2\u3001\u6269\u80f86\u3001\u589e\u52a0\u4e0a\u80a2\u7684\u8d1f\u91cd\u3002\u6bd4\u5982\u4e24\u624b\u5404\u63e1\u4e00\u74f6\u77ff\u6cc9\u6c34\uff0c\u4e5f\u53ef\u4ee5\u7528\u5f39\u529b\u5e26\u6765\u505a\u4e0a\u80a2\u6297\u963b\u8fd0\u52a8\u8fd9\u6837\u8d70\u8def\u53ef\u4ee5\u6709\u6548\u953b\u70bc\u8eab\u4f53\u7684\u4e00\u4e9b\u808c\u7fa4\uff0c\u589e\u52a0\u808c\u8089\u7684\u529b\u91cf\uff0c\u6709\u52a9\u4e8e\u63a7\u5236\u4f53\u91cd\uff0c\u4e5f\u6709\u52a9\u4e8e\u4ee5\u540e\u7684\u5206\u5a29\u3002\u9488\u5bf9\u9888\u80a9\u75db\u548c\u8170\u80cc\u75db\uff0c\u6211\u4eec\u8bf7\u6559\u4e86\u533b\u5b66\u8fd0\u52a8\u7684\u4e13\u5bb6\uff0c\u7ed9\u5b55\u5987\u8bbe\u8ba1\u4e86\u4e00\u4e9b\u7b80\u5355\u7684\u4f53\u64cd\u52a8\u4f5c\u3002\u7f13\u89e3\u80a9\u9888\u75db\uff0c\u53ef\u4ee5\u7528\u5de6\u56fe\u7684\u201c\u62db\u8d22\u732b\u201d\u52a8\u4f5c\uff1a\u80f3\u818a\u62ac\u8d77\u6765\uff0c\u5927\u81c2\u548c\u5730\u9762\u5e73\u884c\uff1b\u5c0f\u81c2\u548c\u5927\u81c2\u5782\u76f4\uff0c\u4ee5\u5927\u81c2\u4e3a\u8f74\u4e0a\u4e0b\u8f6c\u52a8\u3002\u5176\u5b9e\u4e0d\u5149\u662f\u5b55\u5987\uff0c\u7ecf\u5e38\u4f0f\u6848\u5de5\u4f5c\u7684\u670b\u53cb\u90fd\u53ef\u4ee5\u5c1d\u8bd5\uff0c\u5f88\u6709\u6548\u3002\u7f13\u89e3\u8170\u80cc\u75db\uff0c\u4e3b\u8981\u662f\u7528\u745c\u4f3d\u91cc\u8fb9\u7684\u201c\u732b\u5f0f\u4f38\u5c55\u201d\u4f53\u5f0f\u3002\u8fd9\u51e0\u5f20\u7167\u7247\uff0c\u662f\u6211\u4eec\u5728\u5b55\u5987\u5b66\u6821\u6307\u5bfc\u5b55\u5987\u8fdb\u884c\u953b\u70bc\uff0c\u7528\u4e00\u4e9b\u7b80\u5355\u7684\u745c\u4f3d\u52a8\u4f5c\uff0c\u6216\u8005\u501f\u52a9\u5f39\u529b\u5e26\u6765\u953b\u70bc\u808c\u8089\u7684\u529b\u91cf\u3002\u5979\u4eec\u6765\u7684\u65f6\u5019\uff0c\u4e08\u592b\u90fd\u966a\u7740\u2014\u2014\u8fd8\u6709\u4e00\u4f4d\u5b55\u5987\u7684\u5988\u5988\u4e5f\u966a\u7740\u3002\u5979\u4eec\u63a5\u53d7\u5ea6\u5f88\u9ad8\uff0c\u8fd8\u7528\u624b\u673a\u5f55\u4e0b\u6765\uff0c\u56de\u53bb\u518d\u7ec3\u3002\u5927\u5bb6\u53ef\u80fd\u4f1a\u5bf9\u5b55\u671f\u7684\u8fd0\u52a8\u5b89\u5168\u6709\u7591\u95ee\u3002\u5176\u5b9e\uff0c\u4e0d\u662f\u6240\u6709\u7684\u5b55\u5987\u90fd\u9002\u5408\u505a\u8fd0\u52a8\uff0c\u6709\u4e00\u4e9b\u7981\u5fcc\u75c7\uff1a\u6709\u4e25\u91cd\u7684\u5fc3\u810f\u75c5\u3001\u80ba\u90e8\u75be\u75c5\uff0c\u6216\u8005\u6709\u591a\u6b21\u65e9\u4ea7\u53f2\uff0c\u80ce\u819c\u65e9\u7834\uff0c\u8fd8\u6709\u598a\u5a20\u671f\u9ad8\u8840\u538b\u7684\u5b55\u5987\uff0c\u662f\u4e0d\u5efa\u8bae\u8fd0\u52a8\u7684\u3002\u53e6\u5916\uff0c\u6211\u4eec\u8981\u9075\u4ece\u81ea\u5df1\u7684\u4e3b\u89c2\u611f\u89c9\uff0c\u5982\u679c\u6709\u547c\u5438\u56f0\u96be\u6216\u8005\u5fc3\u614c\uff0c\u90a3\u5c31\u505c\u4e0b\u6765\u3002\u518d\u6709\u5c31\u662f\uff0c\u53ef\u4ee5\u5bf9\u7167\u8fd9\u4e2a\u4e3b\u89c2\u611f\u89c9\u91cf\u8868\u2014\u2014\u5de6\u8fb9\u7684\u6570\u5b57\u4e58\u4ee510\uff0c\u57fa\u672c\u4e0a\u5c31\u662f\u8fd0\u52a8\u540e\u8fbe\u5230\u7684\u76ee\u6807\u5fc3\u7387\u3002\u53ea\u8981\u6211\u4eec\u7684\u611f\u89c9\u5728\u8fd9\u4e2a\u7ea2\u6846\u4e4b\u5185\uff0c\u90a3\u5c31\u662f\u76f8\u5bf9\u5b89\u5168\u7684\u3002\u8fd9\u662f\u6211\u975e\u5e38\u559c\u6b22\u7684\u4e00\u5f20\u7167\u7247\uff0c\u662f\u534f\u548c\u533b\u9662\u4ea7\u79d1\u7684\u9a6c\u826f\u5764\u5927\u592b\uff0c\u5979\u662f\u4e00\u4f4d\u9ad8\u9f84\u4e8c\u80ce\u7684\u5b55\u5987\u3002\u5979\u5728\u7e41\u91cd\u7684\u533b\u7597\u79d1\u7814\u3001\u6559\u5b66\u5de5\u4f5c\u7684\u540c\u65f6\uff0c\u8fd8\u575a\u6301\u5b55\u671f\u953b\u70bc\uff0c\u4f53\u91cd\u63a7\u5236\u5f97\u5f88\u597d\u3002\u5206\u5a29\u5f88\u987a\u5229\uff0c\u4ea7\u540e\u4e5f\u79ef\u6781\u953b\u70bc\uff0c\u5f88\u5feb\u5c31\u53c8\u6295\u8eab\u5230\u5de5\u4f5c\u5f53\u4e2d\u3002\u5927\u5bb6\u53ef\u80fd\u4f1a\u8bf4\uff0c\u9a6c\u5927\u592b\u662f\u4ea7\u79d1\u533b\u751f\uff0c\u548c\u6211\u4eec\u666e\u901a\u4eba\u4e0d\u4e00\u6837\u3002\u90a3\u6211\u4eec\u4e5f\u53ef\u4ee5\u770b\u666e\u901a\u4eba\u7684\u4f8b\u5b50\u2014\u2014\u8fd9\u662f\u6211\u4eec\u5b55\u5987\u5b66\u6821\u91cc\u4e00\u4f4d\u5e74\u8f7b\u7684\u5988\u5988\uff0c\u5979\u5728\u5b55\u671f\u6309\u7167\u6211\u4eec\u7684\u6307\u5bfc\u575a\u6301\u953b\u70bc\uff0c\u6708\u5b50\u671f\u95f4\u4e5f\u505a\u4e00\u4e9b\u7b80\u5355\u7684\u745c\u4f3d\u52a8\u4f5c\u3002\u5de6\u8fb9\u8fd9\u5f20\u662f\u5979\u5728\u5b69\u5b50\u6ee1\u6708\u65f6\u5019\u7684\u7167\u7247\uff0c\u7cbe\u795e\u72b6\u6001\u5f88\u597d\u3002\u516b\u4e2a\u6708\u7684\u65f6\u5019\uff0c\u5979\u5f00\u59cb\u505a\u745c\u4f3d\uff1b\u4e00\u5e74\u4e4b\u5185\uff0c\u4f53\u91cd\u5c31\u6062\u590d\u5230\u4e86\u5b55\u524d\u7684\u72b6\u51b5\u3002\u6211\u4e5f\u5e0c\u671b\u6709\u66f4\u591a\u7684\u5b55\u4ea7\u5987\uff0c\u80fd\u50cf\u5979\u4e00\u6837\u987a\u5229\u5730\u5ea6\u8fc7\u5b55\u4ea7\u671f\uff0c\u505a\u4e00\u4e2a\u5065\u5eb7\u7f8e\u4e3d\u5feb\u4e50\u7684\u5988\u5988\u3002\u6211\u7684\u5206\u4eab\u5c31\u5230\u8fd9\u91cc\uff0c\u8c22\u8c22\u5927\u5bb6\u3002\u6f14\u8bb2\u5609\u5bbe\u8d75\u7ea2\uff1a\u300a\u751f\u5a03\u5fc5\u987b\u5750\u6708\u5b50\uff1f\u804a\u804a\u90a3\u4e9b\u5973\u6027\u5b55\u4ea7\u5065\u5eb7\u7684\u8bef\u533a\u300b", "title": "\u8d75\u7ea2\uff1a\u60f3\u8981\u987a\u5229\u5ea6\u8fc7\u5b55\u4ea7\u671f\uff1f\u201c\u591a\u5403\u5c11\u52a8\u201d\u53ef\u4e0d\u884c", "ukey_author": null, "video_content": "<iframe frameborder=\"0\" src=\"https://v.qq.com/txp/iframe/player.html?vid=s0870cj420t\" allowFullScreen=\"true\"></iframe>"}, {"author": {"avatar": {"large": "https://1-im.guokr.com/fZ1vOyYi2wAcki-rnLC1DQtpKknfRkAkd2n3n2rETPOzAAAAtwAAAEpQ.jpg", "normal": "https://1-im.guokr.com/fZ1vOyYi2wAcki-rnLC1DQtpKknfRkAkd2n3n2rETPOzAAAAtwAAAEpQ.jpg", "small": "https://1-im.guokr.com/fZ1vOyYi2wAcki-rnLC1DQtpKknfRkAkd2n3n2rETPOzAAAAtwAAAEpQ.jpg"}, "introduction": "\u5df2\u7ecf\u8131\u5751\u7684\u5236\u836fPhD\u3002", "nickname": "Dex", "url": ""}, "authors": [{"avatar": {"large": "https://1-im.guokr.com/fZ1vOyYi2wAcki-rnLC1DQtpKknfRkAkd2n3n2rETPOzAAAAtwAAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://1-im.guokr.com/fZ1vOyYi2wAcki-rnLC1DQtpKknfRkAkd2n3n2rETPOzAAAAtwAAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://1-im.guokr.com/fZ1vOyYi2wAcki-rnLC1DQtpKknfRkAkd2n3n2rETPOzAAAAtwAAAEpQ.jpg?imageView2/1/w/24/h/24"}, "introduction": "\u5df2\u7ecf\u8131\u5751\u7684\u5236\u836fPhD\u3002", "nickname": "Dex", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [], "content": "<p><!--StartFragment--><strong><span style=\"color:#696969\"><span style=\"font-size:14px\">|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</span></span></strong><!--EndFragment--></p>\r\n\r\n<p>\u53ef\u4ee5\u8bf4\uff0c\u6bcf\u4e2a\u60f3\u7528\u5236\u6bd2\u8d29\u6bd2\u53d1\u8d22\u7684\u4eba\uff0c\u90fd\u6e05\u695a\u81ea\u5df1\u53ef\u80fd\u9762\u4e34\u600e\u6837\u7684\u5236\u88c1\u3002\u56e0\u6b64\uff0c\u4e00\u4e9b&ldquo;\u806a\u660e&rdquo;\u7684\u4e0d\u6cd5\u5206\u5b50\u5f00\u59cb&ldquo;\u94bb\u7a7a\u5b50&rdquo;\uff0c\u901a\u8fc7\u5408\u6210\u4e00\u4e9b\u5c1a\u4e0d\u5728\u7ba1\u5236\u8303\u56f4\u7684\u6bd2\u54c1\u6765\u8eb2\u907f\u5236\u88c1\u3002</p>\r\n\r\n<p>\u5236\u9020\u82ac\u592a\u5c3c\u7c7b\u4f3c\u7269\u5c31\u662f\u4ed6\u4eec\u60ef\u7528\u7684\u624b\u6bb5\u3002</p>\r\n\r\n<p>\u800c\u8fd1\u65e5\uff0c\u516c\u5b89\u90e8\u3001\u56fd\u5bb6\u536b\u5065\u59d4\u3001\u56fd\u5bb6\u836f\u76d1\u5c40\u8054\u5408\u53d1\u5e03\u516c\u544a\u5ba3\u5e03\uff0c\u81ea2019\u5e745\u67081\u65e5\u8d77\uff0c\u4e2d\u56fd\u5c06\u628a\u82ac\u592a\u5c3c\u7c7b\u7269\u8d28\u5217\u5165\u300a\u975e\u836f\u7528\u7c7b\u9ebb\u9189\u836f\u54c1\u548c\u7cbe\u795e\u836f\u54c1\u7ba1\u5236\u54c1\u79cd\u589e\u8865\u76ee\u5f55\u300b\uff0c\u65bd\u884c&ldquo;\u6574\u7c7b\u5217\u7ba1&rdquo;\u3002</p>\r\n\r\n<p>\u8fd9\u610f\u5473\u7740\u4eca\u540e\u6240\u6709\u4e0e\u82ac\u592a\u5c3c\u7ed3\u6784\u7c7b\u4f3c\u7684\u884d\u751f\u5316\u5408\u7269\uff0c\u90fd\u5c06\u88ab\u5217\u5165\u7ba1\u5236\u8303\u56f4\uff0c\u90a3\u4e9b\u4e4b\u524d\u4f9d\u9760\u5408\u6210\u65b0\u7684\u82ac\u592a\u5c3c\u7c7b\u4f3c\u7269\u6253\u6cd5\u5f8b\u64e6\u8fb9\u7403\u7684\u5236\u6bd2\u8005\u4eec\u4e5f\u5c06\u53d7\u5230\u4e25\u60e9\u3002\u53ef\u4ee5\u8bf4\uff0c\u8fd9\u6b21\u7684\u65b0\u89c4\u5b9a\u662f\u4e2d\u56fd\u7981\u6bd2\u6cd5\u5236\u5efa\u8bbe\u5386\u7a0b\u4e2d\u7684\u91cd\u5927\u521b\u65b0\u6027\u4e3e\u63aa\uff0c\u662f\u7279\u6b8a\u836f\u54c1\u7ba1\u7406\u65b9\u9762\u7684\u5de8\u5927\u8fdb\u6b65\u3002</p>\r\n\r\n<h2>\u82ac\u592a\u5c3c\u662f\u4f55\u6765\u5934\uff1f</h2>\r\n\r\n<p>\u82ac\u592a\u5c3c\u672c\u662f\u4e00\u79cd\u5f3a\u6548\u9547\u75db\u836f\uff08\u963f\u7247\u7c7b\u836f\u7269\uff09\uff0c\u8fd1\u5e74\u6765\u5374\u5728\u7f8e\u56fd\u3001\u52a0\u62ff\u5927\u7b49\u56fd\u88ab\u975e\u6cd5\u6cdb\u6ee5\u7528\uff0c\u6210\u4e3a&ldquo;\u65b0\u578b\u6bd2\u54c1&rdquo;\u3002\u751a\u81f3\u6709\u4eba\u5c06\u5176\u5f62\u5bb9\u4e3a&ldquo;\u65b0\u4e00\u4ee3\u6bd2\u54c1\u4e4b\u738b&rdquo;&ldquo;21\u4e16\u7eaa\u7684\u9e26\u7247\u6218\u4e89&rdquo;\u3002</p>\r\n\r\n<p>\u90a3\u4e48\u8fd9\u79cd\u836f\u7269\u7a76\u7adf\u662f\u4f55\u6765\u5934\uff1f\u4e3a\u4f55\u4ece\u836f\u7269\u53d8\u4e3a\u6bd2\u54c1\uff1f\u8fd9\u5c31\u8981\u8bf4\u5230\u5b83\u9887\u4e3a\u4f20\u5947\u7684\u524d\u4e16\u4eca\u751f\u4e86\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/vhbWK9d6yTbNPeBuNiyb2qMhLH_ZRWzEtJeYHydF-SI4BAAAlQMAAEpQ.jpg\" style=\"height:543px; width:640px\" />\u82ac\u592a\u5c3c\u3002\u56fe\u7247\u6765\u6e90\uff1aWikimedia Commons</p>\r\n\r\n<p>\u82ac\u592a\u5c3c\u7684\u540d\u5b57\u4f3c\u4e4e\u5bf9\u5f88\u591a\u4eba\u6765\u8bf4\u6709\u4e9b\u964c\u751f\uff0c\u4f46\u8bf4\u5230\u5b83\u7684\u9f3b\u7956&mdash;&mdash;\u7f42\u7c9f\uff0c\u5c31\u51e0\u4e4e\u65e0\u4eba\u4e0d\u77e5\u4e86\u3002</p>\r\n\r\n<p>\u4eba\u4eec\u5f88\u65e9\u5c31\u5df2\u7ecf\u610f\u8bc6\u5230\uff0c\u7f42\u7c9f\u9664\u4e86\u82b1\u59ff\u4ee4\u4eba\u5fc3\u9189\uff0c\u5b83\u7684\u679c\u5b9e\u5272\u7834\u4e4b\u540e\u6d41\u51fa\u7684\u4e73\u767d\u8272\u6c41\u6db2\uff0c\u66f4\u662f\u5177\u6709\u73b0\u5b9e\u610f\u4e49\u4e0a\u6444\u4eba\u5fc3\u9b44\u7684\u9b54\u529b\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/8R7SV_MU98tGo_2zhUtY8sSRxmQtOfDCmVk2sw6QIl44BAAAqAUAAEpQ.jpg\" style=\"height:429px; width:320px\" />\u7f42\u7c9f\u7684\u4eb2\u621a\u865e\u7f8e\u4eba\uff0c\u82b1\u59ff\u540c\u6837\u4ee4\u4eba\u5fc3\u9189\u3002\u56fe\u7247\u6765\u6e90\uff1aWikimedia Commons</p>\r\n\r\n<p>\u5c06\u8fd9\u79cd\u767d\u8272\u6c41\u6db2\u667e\u6652\u548c\u6d53\u7f29\uff0c\u4f1a\u5f97\u5230\u4e00\u79cd\u9ed1\u8272\u7684\u56fa\u4f53\uff0c\u6b64\u65f6\u5b83\u6709\u4e86\u4e00\u4e2a\u4f17\u4eba\u7686\u77e5\u7684\u540d\u5b57\uff1a\u9e26\u7247\uff08\u4e5f\u53eb\u963f\u7247\uff09\u3002</p>\r\n\r\n<p>\u9e26\u7247\u5bb9\u6613\u6210\u763e\uff0c\u526f\u4f5c\u7528\u5f88\u591a\uff0c\u53ef\u80fd\u635f\u5bb3\u5065\u5eb7\u751a\u81f3\u5bfc\u81f4\u6b7b\u4ea1\uff0c\u4ee4\u4eba\u8c08\u864e\u8272\u53d8\u3002\u7136\u800c\u4e0e\u6b64\u540c\u65f6\uff0c\u5b83\u4e5f\u5177\u6709\u5f88\u9ad8\u7684\u836f\u7528\u4ef7\u503c&mdash;&mdash;\u8fd9\u51e0\u4e4e\u662f\u4eba\u7c7b\u4ece\u81ea\u7136\u4e2d\u83b7\u53d6\u5230\u7684\u6700\u5f3a\u6548\u9547\u75db\u836f\u548c\u9547\u54b3\u836f\uff0c\u67d0\u79cd\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u786e\u5b9e\u662f\u81ea\u7136\u5bf9\u4eba\u7c7b\u7684\u9988\u8d60\u3002</p>\r\n\r\n<p>\u9e26\u7247\u7684\u8fd9\u79cd\u80fd\u529b\u4f7f\u4eba\u4eec\u4ece\u4e00\u5f00\u59cb\u5c31\u65e0\u6cd5\u5b8c\u5168\u653e\u5f03\u4f7f\u7528\u5b83\u7684\u61a7\u61ac\uff0c\u533b\u751f\u548c\u836f\u5b66\u5bb6\u5e0c\u671b\u6709\u4e00\u5929\u80fd\u591f\u5177\u5907\u9a7e\u9a6d\u5b83\u7684\u80fd\u529b\u3002\u56e0\u6b64\uff0c\u51e0\u4e2a\u4e16\u7eaa\u4ee5\u6765\uff0c\u4eba\u4eec\u4e00\u76f4\u8bd5\u56fe\u9a6f\u670d\u5b83\uff0c\u7136\u800c\u4e00\u4e2a\u4e0d\u7ecf\u610f\uff0c\u5374\u53c8\u91ca\u653e\u51fa\u9b54\u9b3c\u88ab\u5b83\u6240\u4f24\u3002</p>\r\n\r\n<p>\u5728\u6f2b\u957f\u7684\u65f6\u95f4\u4e2d\uff0c\u4eba\u4eec\u5e76\u4e0d\u6e05\u695a\u9e26\u7247\u8fd9\u79cd\u9547\u75db\u548c\u4ee4\u4eba\u6109\u60a6\u7684\u5f3a\u5927\u529b\u91cf\u6765\u81ea\u54ea\u91cc\uff0c\u76f4\u81f31804\u5e74\uff0c\u6709\u4eba\u4ece\u9e26\u7247\u4e2d\u63d0\u53d6\u83b7\u5f97\u4e86\u4e00\u79cd\u88ab\u79f0\u4e3a\u5417\u5561\u7684\u767d\u8272\u7ed3\u6676\uff0c\u624d\u7b97\u662f\u63ed\u5f00\u4e86\u5b83\u7684\u9762\u7eb1\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/mFEkF_Mhl6W1iWEQQHHqEp6baWLhhkFNaYTvLhxZfTGkAgAAUAIAAEpQ.jpg\" style=\"height:280px; width:320px\" />\u5417\u5561\u7684\u5316\u5b66\u7ed3\u6784\u5f0f\u3002</p>\r\n\r\n<p>\u5728\u4e00\u4ee3\u4ee3\u836f\u5b66\u5bb6\u7684\u52aa\u529b\u4e0b\uff0c\u5417\u5561\u7684\u5316\u5b66\u7ed3\u6784\u548c\u4f5c\u7528\u673a\u5236\u88ab\u9010\u6e10\u6478\u7d22\u51fa\u6765\u3002\u4eba\u4eec\u5f00\u59cb\u4f9d\u636e\u5df2\u6709\u7814\u7a76\u7ed3\u679c\uff0c\u5bf9\u5417\u5561\u8fdb\u884c\u4e86\u6539\u9020\u3002</p>\r\n\r\n<p>\u836f\u5b66\u5bb6\u7684\u76ee\u6807\uff0c\u662f\u5c3d\u53ef\u80fd\u53bb\u6389\u5417\u5561\u7684\u6210\u763e\u6027\u3002\u4f46\u662f\u4e8b\u60c5\u5e76\u4e0d\u4f1a\u4e00\u5e06\u98ce\u987a&mdash;&mdash;\u628a\u5417\u5561\u7684\u9547\u75db\u9547\u54b3\u836f\u6548\u548c\u6210\u763e\u6027\u7b49\u526f\u4f5c\u7528\u62c6\u89e3\u5f00\u6765\uff0c\u662f\u5341\u5206\u56f0\u96be\u7684\u3002\u5f88\u591a\u65f6\u5019\u836f\u5b66\u5bb6\u62ff\u5230\u4e86\u4e00\u79cd\u65b0\u7684\u5316\u5408\u7269\u521a\u521a\u611f\u53f9&ldquo;\u54c7\u6210\u529f\u4e86\uff01\u9547\u75db\u6548\u679c\u8fd9\u4e48\u597d\uff1f&rdquo;\u5374\u53c8\u9a6c\u4e0a\u53f9\u6c14&ldquo;\u54e6\uff0c\u6210\u763e\u6027\u4e5f\u633a\u597d\u7684&hellip;&hellip;&rdquo;\uff0c\u7136\u540e\u518d\u5f00\u59cb\u4e0b\u4e00\u8f6e\u7684\u52aa\u529b\u3002</p>\r\n\r\n<h2>\u65e9\u671f\u8d70\u5c94\u8def\u9047\u5230\u7684\u9b54\u9b3c</h2>\r\n\r\n<p>\u7136\u800c\u5728\u65e9\u671f\u63a2\u7d22\u8fc7\u7a0b\u4e2d\uff0c\u4eba\u4eec\u66fe\u4e00\u4e2a\u4e0d\u7559\u795e\uff0c\u91ca\u653e\u51fa\u4e00\u4e2a\u81ed\u540d\u662d\u8457\u7684\u6076\u9b54&mdash;&mdash;\u6ca1\u9519\uff0c\u5c31\u662f\u90a3\u4e2a\u5815\u843d\u7684&ldquo;\u5973\u82f1\u96c4&rdquo;\uff0c\u6d77\u6d1b\u56e0\uff08Heroin\uff09\u3002</p>\r\n\r\n<p>19\u4e16\u7eaa\u672b\uff0c\u6709\u4eba\u53d1\u73b0\u5c06\u5417\u5561\u7ed3\u6784\u4e2d\u7684\u4e24\u4e2a\u4eb2\u6c34\u6027\u7f9f\u57fa\u5206\u522b\u63a5\u4e0a\u4e59\u9170\u57fa\u56e2\u540e\uff0c\u5f97\u5230\u7684\u4ea7\u7269\u8102\u6eb6\u6027\u66f4\u5f3a\u3002\u5b83\u66f4\u5bb9\u6613\u900f\u8fc7\u5404\u79cd\u8102\u8d28\u7684\u4eba\u4f53\u7c98\u819c\uff0c\u8fdb\u5165\u4e2d\u67a2\u795e\u7ecf\u7cfb\u7edf\uff0c\u53d1\u6325\u66f4\u5f3a\u7684\u4f5c\u7528\u3002\u540c\u65f6\u4e0e\u5417\u5561\u76f8\u6bd4\uff0c\u5b83\u4f3c\u4e4e\u5c11\u4e86\u5f88\u591a\u4e0d\u9002\u611f\u548c\u526f\u4f5c\u7528\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/uURDS2mSa-0iofqQLh8GlJBxmWjOmLFQhah9H8v7qBccAgAAigIAAEpQ.jpg\" style=\"height:385px; width:320px\" />\u4e00\u74f6\u6d77\u6d1b\u56e0\u3002\u56fe\u7247\u6765\u6e90\uff1aWikimedia Commons</p>\r\n\r\n<p>\u836f\u5382\u7684\u5316\u5b66\u5bb6\u5c06\u8fd9\u79cd\u4ea7\u7269\u5236\u6210\u4e86\u836f\u7269\uff0c\u5176\u9547\u75db\u6548\u529b\u8fdc\u9ad8\u4e8e\u5417\u5561\uff0c\u4e5f\u53ef\u4ee5&ldquo;\u65e0\u526f\u4f5c\u7528\u5730&rdquo;\u5e94\u7528\u4e8e\u9547\u54b3\u6cbb\u7597\u3002\u968f\u540e\u63a8\u51fa\u4e0a\u5e02\uff0c\u751a\u81f3\u66fe\u4f5c\u4e3a\u975e\u5904\u65b9\u7684\u9547\u54b3\u836f\u5e94\u7528\u4e8e\u513f\u7ae5\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/pTG8yS9D73UPw0CxOFZZL_vJwVvqM2sezwL7-2xr-5NMAgAAUgMAAEpQ.jpg\" style=\"height:463px; width:320px\" />\u5f53\u5e74\u7684\u5e7f\u544a\u4e2d\uff0c\u4e00\u4f4d\u6148\u7231\u7684\u5988\u5988\u6b63\u5728\u7ed9\u5979\u53ef\u7231\u7684\u5973\u513f\u5582\u98df\u6d77\u6d1b\u56e0\uff0c\u8fd9\u4e2a\u573a\u666f\u5728\u4eca\u5929\u770b\u6765\u7b80\u76f4\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\u3002\u56fe\u7247\u6765\u6e90\uff1aCarlosbua.com</p>\r\n\r\n<p>\u7531\u4e8e\u6d77\u6d1b\u56e0\u51fa\u73b0\u4ee5\u6765\u9020\u6210\u7684\u4e0d\u826f\u5f71\u54cd\uff0c\u5b83\u7684\u5371\u5bb3\u8fdc\u8fdc\u8d85\u8fc7\u4e86\u6240\u80fd\u5e26\u6765\u7684\u6536\u76ca\uff0c\u4f7f\u5b83\u5931\u53bb\u4e86\u4f5c\u4e3a\u836f\u54c1\u5b58\u5728\u7684\u8d44\u683c\u3002\u81ea\u4e8c\u5341\u4e16\u7eaa\u521d\u671f\uff0c\u5404\u56fd\u653f\u5e9c\u548c\u673a\u6784\u9010\u6e10\u5c06\u5b83\u9010\u51fa\u4e34\u5e8a\u5e94\u7528\uff0c\u81f3\u4e94\u5341\u5e74\u4ee3\uff0c\u6d77\u6d1b\u56e0\u88ab\u6b63\u5f0f\u5730\u4ece\u836f\u5178\u4e2d\u9664\u53bb&mdash;&mdash;\u81f3\u6b64\uff0c\u5b83\u5b8c\u5b8c\u5168\u5168\u5730\u3001\u53ea\u80fd\u88ab\u79f0\u4e3a&ldquo;\u6bd2\u54c1&rdquo;\u3002</p>\r\n\r\n<h2>\u7ec8\u4e8e\u62ff\u5230\u6b63\u786e\u7684\u95e8\u94a5\u5319</h2>\r\n\r\n<p>\u8fd8\u597d\uff0c\u5728\u4e0e\u5417\u5561\u7684\u535a\u5f08\u4e2d\uff0c\u4eba\u4eec\u5e76\u4e0d\u603b\u662f\u5904\u4e8e\u4e0b\u98ce\u3002\u968f\u7740\u79d1\u5b66\u7684\u8fdb\u6b65\uff0c\u4eba\u7c7b\u79ef\u7d2f\u7684\u77e5\u8bc6\u8d8a\u591a\uff0c\u5c31\u8d8a\u80fd\u591f\u6709\u7684\u653e\u77e2\u5730\u8fdb\u884c\u64cd\u4f5c\u3002\u5176\u4e2d\uff0c&ldquo;\u9501\u94a5\u5047\u8bf4&rdquo;\u8d77\u5230\u4e86\u81f3\u5173\u91cd\u8981\u7684\u4f5c\u7528\u3002</p>\r\n\r\n<p>\u5417\u5561\u5c31\u76f8\u5f53\u4e8e\u662f\u4e00\u79cd\u80fd\u591f\u6253\u5f00\u7279\u5b9a\u751f\u7406\u529f\u80fd\u7684\u5916\u6765&ldquo;\u5f00\u9501\u94a5\u5319&rdquo;\uff08\u914d\u4f53\uff09\uff0c\u5b83\u8981\u6253\u5f00\u7684\u9501\uff08\u53d7\u4f53\uff09\u540e\u6765\u4e5f\u76f4\u63a5\u88ab\u547d\u540d\u4e3a&ldquo;\u963f\u7247\u53d7\u4f53&rdquo;\u3002</p>\r\n\r\n<p>\u4e3a\u907f\u514d\u91cd\u8d70\u6d77\u6d1b\u56e0\u7684\u9519\u8def\uff0c\u4eba\u4eec\u53d8\u5f97\u66f4\u52a0\u8c28\u614e\u3002\u5728\u8fdb\u884c\u7814\u7a76\u65f6\uff0c\u5373\u4f7f\u727a\u7272\u4e00\u4e9b\u9547\u75db\u7684\u836f\u6548\u4f5c\u7528\uff0c\u4e5f\u66f4\u503e\u5411\u4e8e\u628a\u6210\u763e\u6027\u4f4e\u7684\u4ea7\u7269\u63a8\u5411\u4e34\u5e8a\u3002\u4f34\u968f\u7740\u5bf9\u4e8e\u963f\u7247\u53d7\u4f53\u5bb6\u65cf\u7684\u8fdb\u4e00\u6b65\u7814\u7a76\uff0c\u4eba\u4eec\u53d1\u73b0\u51e0\u79cd\u53d7\u4f53\u5b58\u5728\u7740\u7ec6\u5fae\u7684\u5dee\u522b\uff0c\u73b0\u5728\u7684\u4e00\u4e9b\u7814\u7a76\u8ba4\u4e3a\uff0c\u5982\u679c\u5bfb\u627e\u5230\u4e13\u95e8\u4f5c\u7528\u4e8e&kappa;\u53d7\u4f53\u7684\u836f\u7269\uff0c\u5c31\u53ef\u80fd\u4f1a\u5f97\u5230\u65e0\u6210\u763e\u6027\u7684\u9547\u75db\u836f\u4e86\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/DpfesSlJiWaEFhMsaulRX_YlEsSsLuvp_yo0weA42k30AwAAOgEAAEpQ.jpg\" style=\"height:199px; width:640px\" />\u963f\u7247\u53d7\u4f53\u5bb6\u65cf\u53ca\u5176\u751f\u7406\u6548\u679c\uff0c\u5176\u4e2d\u4e13\u95e8\u4f5c\u7528\u4e8e&kappa;\u53d7\u4f53\u7684\u6fc0\u52a8\u5242\uff0c\u53ef\u80fd\u4f1a\u5f97\u5230\u65e0\u6210\u763e\u6027\u7684\u9547\u75db\u836f\u3002\u6765\u6e90\uff1a\u53c2\u8003\u6587\u732e[1]</p>\r\n\r\n<p>\u65e9\u671f\u6bd4\u8f83\u6709\u540d\u7684\u5417\u5561\u7ed3\u6784\u6539\u9020\u836f\u7269\u5c31\u5305\u62ec\u54cc\u66ff\u5576\uff08\u5373\u675c\u51b7\u4e01\uff0c\u9547\u75db\u4f5c\u7528\u7ea6\u4e3a\u5417\u5561\u76841/10\uff09\u3001\u66f2\u9a6c\u6735\uff08\u9547\u75db\u4f5c\u7528\u7ea6\u4e3a\u5417\u5561\u76841/10\uff09\u3001\u55b7\u4ed6\u4f50\u8f9b\uff08\u9547\u75db\u4f5c\u7528\u7ea6\u4e3a\u5417\u5561\u76841/6\uff09\u7b49\u3002</p>\r\n\r\n<p>\u540e\u6765\u4e00\u4e9b\u66f4\u4e3a\u6210\u529f\u7684\u5c31\u7c7b\u4f3c\u5e03\u6258\u5561\u8bfa\u3001\u4e01\u4e19\u8bfa\u5561\u548c\u82ac\u592a\u5c3c\u4e86\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/uHPqTdWe8E4R7b13kIWoIzMsALM8p88F4_mKRR2Ip404BAAA3QEAAEpQ.jpg\" style=\"height:283px; width:640px\" />\u5417\u5561\u53ca\u5176\u6539\u9020\u7269\u7684\u5316\u5b66\u7ed3\u6784\u5f0f\u3002</p>\r\n\r\n<p>\u82ac\u592a\u5c3c\u7c7b\u53ef\u8c13\u5f88\u6210\u529f\u7684\u9547\u75db\u836f\u7269\u3002\u5b83\u4eec\u7684\u9547\u75db\u4f5c\u7528\u6548\u679c\u5f3a\uff08\u9547\u75db\u4f5c\u7528\u7ea6\u4e3a\u5417\u5561\u768480~100\u500d\uff09\uff0c\u53d1\u751f\u4f5c\u7528\u7684\u901f\u5ea6\u5feb\uff0c\u6301\u7eed\u65f6\u95f4\u77ed\uff0c\u4e34\u5e8a\u4e0a\u53ef\u4ee5\u4e0e\u9ebb\u9189\u836f\u5408\u7528\u4f5c\u4e3a\u8f85\u52a9\u9ebb\u9189\u7528\u836f\uff0c\u4e5f\u5e38\u5e38\u5236\u5907\u4e3a\u7ecf\u76ae\u7ed9\u836f\u7cfb\u7edf\u3002</p>\r\n\r\n<p>\u8d34\u6577\u4e8e\u76ae\u80a4\u540e\uff0c\u5c31\u53ef\u4ee5\u57283~7\u5929\u5185\u4ee5\u6052\u5b9a\u7684\u901f\u7387\u91ca\u653e\u836f\u7269\u91cc\uff0c\u975e\u5e38\u65b9\u4fbf\u4f7f\u7528\uff0c\u9002\u5408\u7528\u4e8e\u624b\u672f\u540e\u548c\u4e00\u4e9b\u91cd\u5ea6\u75bc\u75db\u60a3\u8005\u7684\u9547\u75db\u6cbb\u7597\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/jdQOG94_HS08YEdky9rfeXQ3h3JCKfd0z0c7WRuMaUgYAQAAtAAAAEpQ.jpg\" style=\"height:180px; width:280px\" />\u82ac\u592a\u5c3c\u8d34\u5242\u81ea90\u5e74\u4ee3\u4e0a\u5e02\u4ee5\u6765\uff0c\u6709\u7740\u826f\u597d\u7684\u4e34\u5e8a\u5e94\u7528\uff0c\u76ee\u524d\u5df2\u7ecf\u662f\u4e00\u4e2a\u4ef7\u503c\u5de8\u5927\u7684\u836f\u7269\u54c1\u7c7b\u3002\u56fe\u7247\u6765\u6e90\uff1asohu.com &amp; mylan.ca</p>\r\n\r\n<h2>\u82ac\u592a\u5c3c\uff0c\u662f\u5929\u4f7f\u8fd8\u662f\u9b54\u9b3c\uff1f</h2>\r\n\r\n<p>\u82ac\u592a\u5c3c\u7b49\u963f\u7247\u7c7b\u836f\u7269\u5728\u53d7\u63a7\u5236\u7684\u6761\u4ef6\u4e0b\u5408\u7406\u4f7f\u7528\uff0c\u53ef\u4ee5\u7f13\u89e3\u60a3\u8005\u7684\u75db\u82e6\uff0c\u4e3a\u533b\u7597\u4e8b\u4e1a\u5e26\u6765\u53ef\u89c2\u7684\u76ca\u5904\u3002\u800c\u5728\u5931\u63a7\u65f6\uff0c\u5219\u4f1a\u5c55\u9732\u51fa\u72f0\u72de\u7684\u4e00\u9762\uff0c\u7ed9\u6574\u4e2a\u793e\u4f1a\u9020\u6210\u5de8\u5927\u7684\u8d1f\u9762\u5f71\u54cd\u3002\u6bd4\u5982\u8fd1\u5e74\u6765\uff0c\u82ac\u592a\u5c3c\u3001\u5361\u82ac\u592a\u5c3c\u7b49\u5728\u7f8e\u56fd\u3001\u52a0\u62ff\u5927\u7b49\u56fd\u88ab\u975e\u6cd5\u6cdb\u6ee5\u7528\uff0c\u5bfc\u81f4\u6570\u4e07\u4eba\u6b7b\u4ea1\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://2-im.guokr.com/lE7kcoIwVR9aEzgRS4IOBJpRJkDhdmwSyWq3U9ANE7nwAQAAWwEAAEpQ.jpg\" style=\"height:347px; width:496px\" />\u8fd1\u5e74\u6765\uff0c\u975e\u5904\u65b9\u963f\u7247\u7c7b\u836f\u7269\uff08\u5982\u82ac\u592a\u5c3c\u3001\u5361\u82ac\u592a\u5c3c\u7b49\uff09\u6ee5\u7528\u5728\u7f8e\u56fd\u5bfc\u81f4\u7684\u6b7b\u4ea1\u4eba\u6570\u6025\u5267\u4e0a\u5347\u3002\u56fe\u7247\u6765\u6e90\uff1aCenters for Disease Control and Prevention</p>\r\n\r\n<p>\u4e3a\u4ec0\u4e48\u82ac\u592a\u5c3c\u53ca\u5176\u7c7b\u4f3c\u7269\u4f1a\u88ab\u6ee5\u7528\uff0c\u5e76\u4e14\u5e26\u6765\u5982\u6b64\u5927\u7684\u5371\u5bb3\uff1f</p>\r\n\r\n<p>\u6bd4\u5bf9\u4e00\u4e0b\u5316\u5408\u7269\u7684\u7ed3\u6784\u5f0f\u6211\u4eec\u53ef\u4ee5\u53d1\u73b0\uff0c\u82ac\u592a\u5c3c\u7684\u5316\u5b66\u7ed3\u6784\u660e\u663e\u6bd4\u5417\u5561\u7b80\u6d01\u3002\u4e8b\u5b9e\u4e0a\uff0c\u73b0\u5728\u4e34\u5e8a\u4e0a\u4f7f\u7528\u7684\u5417\u5561\u8fd8\u9700\u8981\u4ece\u7f42\u7c9f\u4e2d\u63d0\u53d6\uff0c\u800c\u82ac\u592a\u5c3c\uff0c\u662f\u901a\u8fc7\u4eba\u5de5\u5168\u5408\u6210\u6765\u83b7\u5f97\u7684\uff08\u968f\u4fbf\u7ffb\u7ffb\u5404\u79cd\u5404\u79cd\u6587\u732e\u6570\u636e\u5e93\uff0c\u5c31\u80fd\u627e\u5230\u5927\u91cf\u7684\u7814\u7a76\u62a5\u9053\uff09\uff0c\u5b83\u7684\u5236\u53d6\u65b9\u5f0f\u663e\u7136\u5bb9\u6613\u4e86\u5f88\u591a\u3002\u5ec9\u4ef7\u3001\u6709\u6548\u5242\u91cf\u4f4e\u3001\u5bb9\u6613\u83b7\u5f97\uff0c\u5f53\u8fd9\u4e9b\u8bcd\u653e\u5728\u4e00\u79cd\u5177\u6709\u6210\u763e\u6027\u548c\u8010\u53d7\u6027\u7684\u963f\u7247\u7c7b\u836f\u7269\u524d\u9762\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u5b83\u6709\u4e86\u6cdb\u6ee5\u7684\u98ce\u9669\u3002</p>\r\n\r\n<p>\u4e34\u5e8a\u5e94\u7528\u65f6\uff0c\u533b\u62a4\u4eba\u5458\u5c1a\u53ef\u6839\u636e\u7cbe\u786e\u7684\u8ba1\u7b97\u548c\u6089\u5fc3\u770b\u987e\u6765\u4fdd\u8bc1\u60a3\u8005\u7684\u7528\u836f\u5b89\u5168\uff0c\u4f46\u5f53\u5b83\u6765\u5230\u8857\u5934\uff0c\u763e\u541b\u5b50\u4eec\u4e00\u6765\u6025\u5207\uff0c\u4e8c\u6765\u628a\u63e1\u4e0d\u597d\u8ba1\u91cf\uff0c\u6444\u5165\u836f\u7269\u540e\uff0c\u5c31\u975e\u5e38\u5bb9\u6613\u51fa\u73b0\u6025\u6027\u3001\u8fc5\u731b\u7684\u8fc7\u91cf\u4f7f\u7528\u3002\u8840\u6db2\u4e2d\u7684\u836f\u7269\u6d53\u5ea6\u6025\u5267\u5347\u9ad8\uff0c\u8d85\u8fc7\u4e86\u5b89\u5168\u5242\u91cf\u4e0a\u7ebf\uff0c\u5bfc\u81f4\u5404\u79cd\u4e25\u91cd\u7684\u526f\u4f5c\u7528\uff0c\u751a\u81f3\u6b7b\u4ea1\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://2-im.guokr.com/U77NW4k36fyAGPZXYx5HZ6Fj88qGxs9fwvFi95qzIOo4BAAA4AIAAEpQ.jpg\" style=\"height:436px; width:640px\" />\u836f\u7269\u5316\u5b66\u5bb6\u4eec\u81f4\u529b\u4e8e\u9632\u6b62\u836f\u7269\u6ee5\u7528\u3002\u56fe\u7247\u6765\u6e90\uff1aUnsplash</p>\r\n\r\n<p>\u5728\u9e26\u7247\u6216\u6d77\u6d1b\u56e0\u6cdb\u6ee5\u7684\u65f6\u4ee3\uff0c\u8fd9\u7c7b\u6bd2\u54c1\u9700\u8981\u79cd\u690d\u7f42\u7c9f\u624d\u80fd\u5236\u53d6\uff0c\u5c1a\u53ef\u901a\u8fc7\u5bf9\u79cd\u690d\u7684\u4e25\u683c\u7ba1\u7406\u6765\u8fbe\u5230\u63a7\u5236\u7684\u76ee\u7684\u3002\u7136\u800c\uff0c\u5f53\u4eba\u4eec\u7684\u8ba4\u8bc6\u5df2\u7ecf\u6df1\u5165\u5230\u82ac\u592a\u5c3c\u65f6\uff0c\u5c31\u4e0d\u80fd\u4ec5\u4ec5\u4f7f\u7528\u8fd9\u79cd\u7b80\u5355\u76f4\u767d\u7684\u65b9\u5f0f\u4e86\u3002</p>\r\n\r\n<p>\u4e00\u65b9\u9762\u82ac\u592a\u5c3c\u7c7b\u7269\u8d28\u7684\u7ed3\u6784\u5f97\u5230\u4e86\u6781\u5927\u7b80\u5316\uff0c\u53ef\u4ee5\u5b8c\u6574\u5730\u5728\u5b9e\u9a8c\u5ba4\u4e2d\u5236\u4f5c\uff0c\u4e0d\u987b\u4f9d\u8d56\u7f42\u7c9f\u79cd\u690d\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u6b63\u5982\u524d\u6587\u6240\u8bf4\uff0c\u57fa\u4e8e\u9501\u94a5\u5047\u8bf4\u548c\u6784\u6548\u5173\u7cfb\u7814\u7a76\uff0c\u5982\u679c\u4ec5\u4ec5\u5c06\u7279\u5b9a\u7684\u51e0\u79cd\u5316\u5408\u7269\u5217\u5165\u7ba1\u7406\u540d\u5f55\uff0c&ldquo;\u61c2\u884c&rdquo;\u7684\u4eba\u5b8c\u5168\u53ef\u4ee5\u901a\u8fc7\u52a0\u51cf\u4e00\u4e9b\u7b80\u5355\u7684\u5c0f\u7ed3\u6784\u6765\u5236\u9020\u51fa&ldquo;\u4e0d\u8fdd\u6cd5\u7684\u6bd2\u54c1&rdquo;\uff0c\u4f7f\u6267\u6cd5\u8005\u9677\u5165\u5c34\u5c2c\u7684\u5883\u5730\u3002</p>\r\n\r\n<p>\u800c\u8fd9\u6b21\u5373\u5c06\u5b9e\u884c\u7684&ldquo;\u6574\u7c7b\u5217\u7ba1&rdquo;\u6b63\u662f\u4e3a\u4e86\u9488\u5bf9\u8fd9\u79cd\u60c5\u51b5\uff0c\u4f5c\u51fa\u7684\u65b0\u5c1d\u8bd5&mdash;&mdash;\u5c06\u7ed3\u6784\u548c\u82ac\u592a\u5c3c\u7c7b\u4f3c\u7684\u5316\u5408\u7269\u5168\u90e8\u5217\u5165\u7ba1\u5236\u8303\u56f4\uff0c\u800c\u4e0d\u518d\u662f\u53d1\u73b0\u4e00\u79cd\u7ba1\u5236\u4e00\u79cd\u3002\u90a3\u4e9b\u4e4b\u524d\u4e00\u76f4\u94bb\u6cd5\u5f8b\u7a7a\u5b50\u7684\u4eba\u4e5f\u5c06\u53d7\u5230\u4e25\u60e9\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"http://2-im.guokr.com/dsZrnoWLdYerB-SiGW2nqTLVWfg9Cyl_skRlH82VBTQ4BAAA0AIAAEpQ.jpg\" style=\"height:427px; width:640px\" />&ldquo;\u6574\u7c7b\u5217\u7ba1&rdquo;\u80fd\u591f\u907f\u514d\u6267\u6cd5\u8005\u9677\u5165\u5c34\u5c2c\u5883\u5730\u3002\u56fe\u7247\u6765\u6e90\uff1aUnsplash</p>\r\n\r\n<p>\u51e0\u767e\u5e74\u6765\uff0c\u4eba\u4eec\u4e0e\u8fd9\u7c7b\u836f\u7269\u7684\u5173\u7cfb\u4e00\u76f4\u662f\u9b54\u9ad8\u4e00\u5c3a\uff0c\u9053\u9ad8\u4e00\u4e08\u3002\u4eba\u4eec\u60f3\u8981\u9a7e\u9a6d\u5b83\u7684\u5f3a\u5927\u836f\u6548\uff0c\u53c8\u8981\u7ede\u5c3d\u8111\u6c41\u907f\u514d\u5b83\u53d8\u4e3a\u9b54\u9b3c\u4e3a\u7978\u4eba\u95f4\u3002\u65e2\u8981\u901a\u8fc7\u79d1\u5b66\u624b\u6bb5\u51cf\u5c11\u6ee5\u7528\uff0c\u53c8\u8981\u653f\u7b56\u548c\u7ba1\u7406\u7b49\u7b49\u5404\u65b9\u9762\u7684\u590d\u6742\u914d\u5408\u3002\u4ece\u53e4\u4ee3\u7684\u9e26\u7247\u5230\u8fd1\u4ee3\u7684\u5417\u5561\uff0c\u518d\u5230\u66f4\u8fd1\u7684\u82ac\u592a\u5c3c&mdash;&mdash;\u65e0\u4e00\u4e0d\u9075\u5faa\u8fd9\u4e2a\u89c4\u5f8b\u3002</p>\r\n\r\n<p>\u6bd5\u7adf\uff0c\u6740\u4eba\u7684\u4ece\u6765\u4e0d\u662f\u5200\uff0c\u800c\u662f\u6301\u5200\u7684\u4eba\u3002\uff08\u7f16\u8f91\uff1aYuki\uff09</p>\r\n\r\n<h2 class=\"references\">\u53c2\u8003\u6587\u732e\uff1a</h2>\r\n\r\n<ol class=\"reference-item\">\r\n\t<li>\u90d1\u864e \u4e3b\u7f16. \u836f\u7269\u5316\u5b66[M]. \u7b2c\u516d\u7248. \u5317\u4eac\uff1a\u4eba\u6c11\u536b\u751f\u51fa\u7248\u793e, 2008</li>\r\n\t<li>\u4ec9\u6587\u5347, \u674e\u5b89\u826f \u4e3b\u7f16. \u836f\u7269\u5316\u5b66[M]. \u7b2c\u4e8c\u7248. \u5317\u4eac\uff1a\u9ad8\u7b49\u6559\u80b2\u51fa\u7248\u793e, 2005</li>\r\n\t<li>Majella E. Lane, The Transdermal Delivery of Dentanyl [J], European Journal of Pharmaceutics and Biopharmaceutics, 2013, 84: 449-455</li>\r\n</ol>\r\n\r\n<h2>\u4f5c\u8005\u540d\u7247</h2>\r\n\r\n<p><img alt=\"\" src=\"http://1-im.guokr.com/fXBSbKO9aQXwowKLvuZ8dar8mc3HVIRtfhPCeoldauE4BAAAkAIAAEpQ.jpg\" style=\"height:389px; width:640px\" /></p>", "date_created": "2019-05-08T11:16:49.137898+08:00", "date_modified": "2019-05-08T11:32:11.255862+08:00", "date_published": "2019-05-08T11:32:14+08:00", "id": 451977, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": null, "replies_count": 7, "small_image": "https://2-im.guokr.com/oOdOHSjJXniTg1BkIlZ0BjVNRuXVsDXIkrB0cZRAnyo4BAAAlQMAAEpQ.jpg", "subject": {"articles_count": 251, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "chemistry", "minisite_key": "None", "name": "\u5316\u5b66", "sort_score": 11, "subject_type": "normal"}, "subject_key": "chemistry", "summary": "\u82ac\u592a\u5c3c\u7c7b\u7269\u8d28\u5373\u65e5\u8d77\u88ab\u4e25\u683c\u7ba1\u5236\uff0c\u6b62\u75db\u836f\u5982\u4f55\u53d8\u4e3a\u201c\u81f4\u547d\u6bd2\u54c1\u201d\uff1f", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|\u53ef\u4ee5\u8bf4\uff0c\u6bcf\u4e2a\u60f3\u7528\u5236\u6bd2\u8d29\u6bd2\u53d1\u8d22\u7684\u4eba\uff0c\u90fd\u6e05\u695a\u81ea\u5df1\u53ef\u80fd\u9762\u4e34\u600e\u6837\u7684\u5236\u88c1\u3002\u56e0\u6b64\uff0c\u4e00\u4e9b\u201c\u806a\u660e\u201d\u7684\u4e0d\u6cd5\u5206\u5b50\u5f00\u59cb\u201c\u94bb\u7a7a\u5b50\u201d\uff0c\u901a\u8fc7\u5408\u6210\u4e00\u4e9b\u5c1a\u4e0d\u5728\u7ba1\u5236\u8303\u56f4\u7684\u6bd2\u54c1\u6765\u8eb2\u907f\u5236\u88c1\u3002\u5236\u9020\u82ac\u592a\u5c3c\u7c7b\u4f3c\u7269\u5c31\u662f\u4ed6\u4eec\u60ef\u7528\u7684\u624b\u6bb5\u3002\u800c\u8fd1\u65e5\uff0c\u516c\u5b89\u90e8\u3001\u56fd\u5bb6\u536b\u5065\u59d4\u3001\u56fd\u5bb6\u836f\u76d1\u5c40\u8054\u5408\u53d1\u5e03\u516c\u544a\u5ba3\u5e03\uff0c\u81ea2019\u5e745\u67081\u65e5\u8d77\uff0c\u4e2d\u56fd\u5c06\u628a\u82ac\u592a\u5c3c\u7c7b\u7269\u8d28\u5217\u5165\u300a\u975e\u836f\u7528\u7c7b\u9ebb\u9189\u836f\u54c1\u548c\u7cbe\u795e\u836f\u54c1\u7ba1\u5236\u54c1\u79cd\u589e\u8865\u76ee\u5f55\u300b\uff0c\u65bd\u884c\u201c\u6574\u7c7b\u5217\u7ba1\u201d\u3002\u8fd9\u610f\u5473\u7740\u4eca\u540e\u6240\u6709\u4e0e\u82ac\u592a\u5c3c\u7ed3\u6784\u7c7b\u4f3c\u7684\u884d\u751f\u5316\u5408\u7269\uff0c\u90fd\u5c06\u88ab\u5217\u5165\u7ba1\u5236\u8303\u56f4\uff0c\u90a3\u4e9b\u4e4b\u524d\u4f9d\u9760\u5408\u6210\u65b0\u7684\u82ac\u592a\u5c3c\u7c7b\u4f3c\u7269\u6253\u6cd5\u5f8b\u64e6\u8fb9\u7403\u7684\u5236\u6bd2\u8005\u4eec\u4e5f\u5c06\u53d7\u5230\u4e25\u60e9\u3002\u53ef\u4ee5\u8bf4\uff0c\u8fd9\u6b21\u7684\u65b0\u89c4\u5b9a\u662f\u4e2d\u56fd\u7981\u6bd2\u6cd5\u5236\u5efa\u8bbe\u5386\u7a0b\u4e2d\u7684\u91cd\u5927\u521b\u65b0\u6027\u4e3e\u63aa\uff0c\u662f\u7279\u6b8a\u836f\u54c1\u7ba1\u7406\u65b9\u9762\u7684\u5de8\u5927\u8fdb\u6b65\u3002\u82ac\u592a\u5c3c\u662f\u4f55\u6765\u5934\uff1f\u82ac\u592a\u5c3c\u672c\u662f\u4e00\u79cd\u5f3a\u6548\u9547\u75db\u836f\uff08\u963f\u7247\u7c7b\u836f\u7269\uff09\uff0c\u8fd1\u5e74\u6765\u5374\u5728\u7f8e\u56fd\u3001\u52a0\u62ff\u5927\u7b49\u56fd\u88ab\u975e\u6cd5\u6cdb\u6ee5\u7528\uff0c\u6210\u4e3a\u201c\u65b0\u578b\u6bd2\u54c1\u201d\u3002\u751a\u81f3\u6709\u4eba\u5c06\u5176\u5f62\u5bb9\u4e3a\u201c\u65b0\u4e00\u4ee3\u6bd2\u54c1\u4e4b\u738b\u201d\u201c21\u4e16\u7eaa\u7684\u9e26\u7247\u6218\u4e89\u201d\u3002\u90a3\u4e48\u8fd9\u79cd\u836f\u7269\u7a76\u7adf\u662f\u4f55\u6765\u5934\uff1f\u4e3a\u4f55\u4ece\u836f\u7269\u53d8\u4e3a\u6bd2\u54c1\uff1f\u8fd9\u5c31\u8981\u8bf4\u5230\u5b83\u9887\u4e3a\u4f20\u5947\u7684\u524d\u4e16\u4eca\u751f\u4e86\u3002\u82ac\u592a\u5c3c\u3002\u56fe\u7247\u6765\u6e90\uff1aWikimedia Commons\u82ac\u592a\u5c3c\u7684\u540d\u5b57\u4f3c\u4e4e\u5bf9\u5f88\u591a\u4eba\u6765\u8bf4\u6709\u4e9b\u964c\u751f\uff0c\u4f46\u8bf4\u5230\u5b83\u7684\u9f3b\u7956\u2014\u2014\u7f42\u7c9f\uff0c\u5c31\u51e0\u4e4e\u65e0\u4eba\u4e0d\u77e5\u4e86\u3002\u4eba\u4eec\u5f88\u65e9\u5c31\u5df2\u7ecf\u610f\u8bc6\u5230\uff0c\u7f42\u7c9f\u9664\u4e86\u82b1\u59ff\u4ee4\u4eba\u5fc3\u9189\uff0c\u5b83\u7684\u679c\u5b9e\u5272\u7834\u4e4b\u540e\u6d41\u51fa\u7684\u4e73\u767d\u8272\u6c41\u6db2\uff0c\u66f4\u662f\u5177\u6709\u73b0\u5b9e\u610f\u4e49\u4e0a\u6444\u4eba\u5fc3\u9b44\u7684\u9b54\u529b\u3002\u7f42\u7c9f\u7684\u4eb2\u621a\u865e\u7f8e\u4eba\uff0c\u82b1\u59ff\u540c\u6837\u4ee4\u4eba\u5fc3\u9189\u3002\u56fe\u7247\u6765\u6e90\uff1aWikimedia Commons\u5c06\u8fd9\u79cd\u767d\u8272\u6c41\u6db2\u667e\u6652\u548c\u6d53\u7f29\uff0c\u4f1a\u5f97\u5230\u4e00\u79cd\u9ed1\u8272\u7684\u56fa\u4f53\uff0c\u6b64\u65f6\u5b83\u6709\u4e86\u4e00\u4e2a\u4f17\u4eba\u7686\u77e5\u7684\u540d\u5b57\uff1a\u9e26\u7247\uff08\u4e5f\u53eb\u963f\u7247\uff09\u3002\u9e26\u7247\u5bb9\u6613\u6210\u763e\uff0c\u526f\u4f5c\u7528\u5f88\u591a\uff0c\u53ef\u80fd\u635f\u5bb3\u5065\u5eb7\u751a\u81f3\u5bfc\u81f4\u6b7b\u4ea1\uff0c\u4ee4\u4eba\u8c08\u864e\u8272\u53d8\u3002\u7136\u800c\u4e0e\u6b64\u540c\u65f6\uff0c\u5b83\u4e5f\u5177\u6709\u5f88\u9ad8\u7684\u836f\u7528\u4ef7\u503c\u2014\u2014\u8fd9\u51e0\u4e4e\u662f\u4eba\u7c7b\u4ece\u81ea\u7136\u4e2d\u83b7\u53d6\u5230\u7684\u6700\u5f3a\u6548\u9547\u75db\u836f\u548c\u9547\u54b3\u836f\uff0c\u67d0\u79cd\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u786e\u5b9e\u662f\u81ea\u7136\u5bf9\u4eba\u7c7b\u7684\u9988\u8d60\u3002\u9e26\u7247\u7684\u8fd9\u79cd\u80fd\u529b\u4f7f\u4eba\u4eec\u4ece\u4e00\u5f00\u59cb\u5c31\u65e0\u6cd5\u5b8c\u5168\u653e\u5f03\u4f7f\u7528\u5b83\u7684\u61a7\u61ac\uff0c\u533b\u751f\u548c\u836f\u5b66\u5bb6\u5e0c\u671b\u6709\u4e00\u5929\u80fd\u591f\u5177\u5907\u9a7e\u9a6d\u5b83\u7684\u80fd\u529b\u3002\u56e0\u6b64\uff0c\u51e0\u4e2a\u4e16\u7eaa\u4ee5\u6765\uff0c\u4eba\u4eec\u4e00\u76f4\u8bd5\u56fe\u9a6f\u670d\u5b83\uff0c\u7136\u800c\u4e00\u4e2a\u4e0d\u7ecf\u610f\uff0c\u5374\u53c8\u91ca\u653e\u51fa\u9b54\u9b3c\u88ab\u5b83\u6240\u4f24\u3002\u5728\u6f2b\u957f\u7684\u65f6\u95f4\u4e2d\uff0c\u4eba\u4eec\u5e76\u4e0d\u6e05\u695a\u9e26\u7247\u8fd9\u79cd\u9547\u75db\u548c\u4ee4\u4eba\u6109\u60a6\u7684\u5f3a\u5927\u529b\u91cf\u6765\u81ea\u54ea\u91cc\uff0c\u76f4\u81f31804\u5e74\uff0c\u6709\u4eba\u4ece\u9e26\u7247\u4e2d\u63d0\u53d6\u83b7\u5f97\u4e86\u4e00\u79cd\u88ab\u79f0\u4e3a\u5417\u5561\u7684\u767d\u8272\u7ed3\u6676\uff0c\u624d\u7b97\u662f\u63ed\u5f00\u4e86\u5b83\u7684\u9762\u7eb1\u3002\u5417\u5561\u7684\u5316\u5b66\u7ed3\u6784\u5f0f\u3002\u5728\u4e00\u4ee3\u4ee3\u836f\u5b66\u5bb6\u7684\u52aa\u529b\u4e0b\uff0c\u5417\u5561\u7684\u5316\u5b66\u7ed3\u6784\u548c\u4f5c\u7528\u673a\u5236\u88ab\u9010\u6e10\u6478\u7d22\u51fa\u6765\u3002\u4eba\u4eec\u5f00\u59cb\u4f9d\u636e\u5df2\u6709\u7814\u7a76\u7ed3\u679c\uff0c\u5bf9\u5417\u5561\u8fdb\u884c\u4e86\u6539\u9020\u3002\u836f\u5b66\u5bb6\u7684\u76ee\u6807\uff0c\u662f\u5c3d\u53ef\u80fd\u53bb\u6389\u5417\u5561\u7684\u6210\u763e\u6027\u3002\u4f46\u662f\u4e8b\u60c5\u5e76\u4e0d\u4f1a\u4e00\u5e06\u98ce\u987a\u2014\u2014\u628a\u5417\u5561\u7684\u9547\u75db\u9547\u54b3\u836f\u6548\u548c\u6210\u763e\u6027\u7b49\u526f\u4f5c\u7528\u62c6\u89e3\u5f00\u6765\uff0c\u662f\u5341\u5206\u56f0\u96be\u7684\u3002\u5f88\u591a\u65f6\u5019\u836f\u5b66\u5bb6\u62ff\u5230\u4e86\u4e00\u79cd\u65b0\u7684\u5316\u5408\u7269\u521a\u521a\u611f\u53f9\u201c\u54c7\u6210\u529f\u4e86\uff01\u9547\u75db\u6548\u679c\u8fd9\u4e48\u597d\uff1f\u201d\u5374\u53c8\u9a6c\u4e0a\u53f9\u6c14\u201c\u54e6\uff0c\u6210\u763e\u6027\u4e5f\u633a\u597d\u7684\u2026\u2026\u201d\uff0c\u7136\u540e\u518d\u5f00\u59cb\u4e0b\u4e00\u8f6e\u7684\u52aa\u529b\u3002\u65e9\u671f\u8d70\u5c94\u8def\u9047\u5230\u7684\u9b54\u9b3c\u7136\u800c\u5728\u65e9\u671f\u63a2\u7d22\u8fc7\u7a0b\u4e2d\uff0c\u4eba\u4eec\u66fe\u4e00\u4e2a\u4e0d\u7559\u795e\uff0c\u91ca\u653e\u51fa\u4e00\u4e2a\u81ed\u540d\u662d\u8457\u7684\u6076\u9b54\u2014\u2014\u6ca1\u9519\uff0c\u5c31\u662f\u90a3\u4e2a\u5815\u843d\u7684\u201c\u5973\u82f1\u96c4\u201d\uff0c\u6d77\u6d1b\u56e0\uff08Heroin\uff09\u300219\u4e16\u7eaa\u672b\uff0c\u6709\u4eba\u53d1\u73b0\u5c06\u5417\u5561\u7ed3\u6784\u4e2d\u7684\u4e24\u4e2a\u4eb2\u6c34\u6027\u7f9f\u57fa\u5206\u522b\u63a5\u4e0a\u4e59\u9170\u57fa\u56e2\u540e\uff0c\u5f97\u5230\u7684\u4ea7\u7269\u8102\u6eb6\u6027\u66f4\u5f3a\u3002\u5b83\u66f4\u5bb9\u6613\u900f\u8fc7\u5404\u79cd\u8102\u8d28\u7684\u4eba\u4f53\u7c98\u819c\uff0c\u8fdb\u5165\u4e2d\u67a2\u795e\u7ecf\u7cfb\u7edf\uff0c\u53d1\u6325\u66f4\u5f3a\u7684\u4f5c\u7528\u3002\u540c\u65f6\u4e0e\u5417\u5561\u76f8\u6bd4\uff0c\u5b83\u4f3c\u4e4e\u5c11\u4e86\u5f88\u591a\u4e0d\u9002\u611f\u548c\u526f\u4f5c\u7528\u3002\u4e00\u74f6\u6d77\u6d1b\u56e0\u3002\u56fe\u7247\u6765\u6e90\uff1aWikimedia Commons\u836f\u5382\u7684\u5316\u5b66\u5bb6\u5c06\u8fd9\u79cd\u4ea7\u7269\u5236\u6210\u4e86\u836f\u7269\uff0c\u5176\u9547\u75db\u6548\u529b\u8fdc\u9ad8\u4e8e\u5417\u5561\uff0c\u4e5f\u53ef\u4ee5\u201c\u65e0\u526f\u4f5c\u7528\u5730\u201d\u5e94\u7528\u4e8e\u9547\u54b3\u6cbb\u7597\u3002\u968f\u540e\u63a8\u51fa\u4e0a\u5e02\uff0c\u751a\u81f3\u66fe\u4f5c\u4e3a\u975e\u5904\u65b9\u7684\u9547\u54b3\u836f\u5e94\u7528\u4e8e\u513f\u7ae5\u3002\u5f53\u5e74\u7684\u5e7f\u544a\u4e2d\uff0c\u4e00\u4f4d\u6148\u7231\u7684\u5988\u5988\u6b63\u5728\u7ed9\u5979\u53ef\u7231\u7684\u5973\u513f\u5582\u98df\u6d77\u6d1b\u56e0\uff0c\u8fd9\u4e2a\u573a\u666f\u5728\u4eca\u5929\u770b\u6765\u7b80\u76f4\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\u3002\u56fe\u7247\u6765\u6e90\uff1aCarlosbua.com\u7531\u4e8e\u6d77\u6d1b\u56e0\u51fa\u73b0\u4ee5\u6765\u9020\u6210\u7684\u4e0d\u826f\u5f71\u54cd\uff0c\u5b83\u7684\u5371\u5bb3\u8fdc\u8fdc\u8d85\u8fc7\u4e86\u6240\u80fd\u5e26\u6765\u7684\u6536\u76ca\uff0c\u4f7f\u5b83\u5931\u53bb\u4e86\u4f5c\u4e3a\u836f\u54c1\u5b58\u5728\u7684\u8d44\u683c\u3002\u81ea\u4e8c\u5341\u4e16\u7eaa\u521d\u671f\uff0c\u5404\u56fd\u653f\u5e9c\u548c\u673a\u6784\u9010\u6e10\u5c06\u5b83\u9010\u51fa\u4e34\u5e8a\u5e94\u7528\uff0c\u81f3\u4e94\u5341\u5e74\u4ee3\uff0c\u6d77\u6d1b\u56e0\u88ab\u6b63\u5f0f\u5730\u4ece\u836f\u5178\u4e2d\u9664\u53bb\u2014\u2014\u81f3\u6b64\uff0c\u5b83\u5b8c\u5b8c\u5168\u5168\u5730\u3001\u53ea\u80fd\u88ab\u79f0\u4e3a\u201c\u6bd2\u54c1\u201d\u3002\u7ec8\u4e8e\u62ff\u5230\u6b63\u786e\u7684\u95e8\u94a5\u5319\u8fd8\u597d\uff0c\u5728\u4e0e\u5417\u5561\u7684\u535a\u5f08\u4e2d\uff0c\u4eba\u4eec\u5e76\u4e0d\u603b\u662f\u5904\u4e8e\u4e0b\u98ce\u3002\u968f\u7740\u79d1\u5b66\u7684\u8fdb\u6b65\uff0c\u4eba\u7c7b\u79ef\u7d2f\u7684\u77e5\u8bc6\u8d8a\u591a\uff0c\u5c31\u8d8a\u80fd\u591f\u6709\u7684\u653e\u77e2\u5730\u8fdb\u884c\u64cd\u4f5c\u3002\u5176\u4e2d\uff0c\u201c\u9501\u94a5\u5047\u8bf4\u201d\u8d77\u5230\u4e86\u81f3\u5173\u91cd\u8981\u7684\u4f5c\u7528\u3002\u5417\u5561\u5c31\u76f8\u5f53\u4e8e\u662f\u4e00\u79cd\u80fd\u591f\u6253\u5f00\u7279\u5b9a\u751f\u7406\u529f\u80fd\u7684\u5916\u6765\u201c\u5f00\u9501\u94a5\u5319\u201d\uff08\u914d\u4f53\uff09\uff0c\u5b83\u8981\u6253\u5f00\u7684\u9501\uff08\u53d7\u4f53\uff09\u540e\u6765\u4e5f\u76f4\u63a5\u88ab\u547d\u540d\u4e3a\u201c\u963f\u7247\u53d7\u4f53\u201d\u3002\u4e3a\u907f\u514d\u91cd\u8d70\u6d77\u6d1b\u56e0\u7684\u9519\u8def\uff0c\u4eba\u4eec\u53d8\u5f97\u66f4\u52a0\u8c28\u614e\u3002\u5728\u8fdb\u884c\u7814\u7a76\u65f6\uff0c\u5373\u4f7f\u727a\u7272\u4e00\u4e9b\u9547\u75db\u7684\u836f\u6548\u4f5c\u7528\uff0c\u4e5f\u66f4\u503e\u5411\u4e8e\u628a\u6210\u763e\u6027\u4f4e\u7684\u4ea7\u7269\u63a8\u5411\u4e34\u5e8a\u3002\u4f34\u968f\u7740\u5bf9\u4e8e\u963f\u7247\u53d7\u4f53\u5bb6\u65cf\u7684\u8fdb\u4e00\u6b65\u7814\u7a76\uff0c\u4eba\u4eec\u53d1\u73b0\u51e0\u79cd\u53d7\u4f53\u5b58\u5728\u7740\u7ec6\u5fae\u7684\u5dee\u522b\uff0c\u73b0\u5728\u7684\u4e00\u4e9b\u7814\u7a76\u8ba4\u4e3a\uff0c\u5982\u679c\u5bfb\u627e\u5230\u4e13\u95e8\u4f5c\u7528\u4e8e\u03ba\u53d7\u4f53\u7684\u836f\u7269\uff0c\u5c31\u53ef\u80fd\u4f1a\u5f97\u5230\u65e0\u6210\u763e\u6027\u7684\u9547\u75db\u836f\u4e86\u3002\u963f\u7247\u53d7\u4f53\u5bb6\u65cf\u53ca\u5176\u751f\u7406\u6548\u679c\uff0c\u5176\u4e2d\u4e13\u95e8\u4f5c\u7528\u4e8e\u03ba\u53d7\u4f53\u7684\u6fc0\u52a8\u5242\uff0c\u53ef\u80fd\u4f1a\u5f97\u5230\u65e0\u6210\u763e\u6027\u7684\u9547\u75db\u836f\u3002\u6765\u6e90\uff1a\u53c2\u8003\u6587\u732e[1]\u65e9\u671f\u6bd4\u8f83\u6709\u540d\u7684\u5417\u5561\u7ed3\u6784\u6539\u9020\u836f\u7269\u5c31\u5305\u62ec\u54cc\u66ff\u5576\uff08\u5373\u675c\u51b7\u4e01\uff0c\u9547\u75db\u4f5c\u7528\u7ea6\u4e3a\u5417\u5561\u76841/10\uff09\u3001\u66f2\u9a6c\u6735\uff08\u9547\u75db\u4f5c\u7528\u7ea6\u4e3a\u5417\u5561\u76841/10\uff09\u3001\u55b7\u4ed6\u4f50\u8f9b\uff08\u9547\u75db\u4f5c\u7528\u7ea6\u4e3a\u5417\u5561\u76841/6\uff09\u7b49\u3002\u540e\u6765\u4e00\u4e9b\u66f4\u4e3a\u6210\u529f\u7684\u5c31\u7c7b\u4f3c\u5e03\u6258\u5561\u8bfa\u3001\u4e01\u4e19\u8bfa\u5561\u548c\u82ac\u592a\u5c3c\u4e86\u3002\u5417\u5561\u53ca\u5176\u6539\u9020\u7269\u7684\u5316\u5b66\u7ed3\u6784\u5f0f\u3002\u82ac\u592a\u5c3c\u7c7b\u53ef\u8c13\u5f88\u6210\u529f\u7684\u9547\u75db\u836f\u7269\u3002\u5b83\u4eec\u7684\u9547\u75db\u4f5c\u7528\u6548\u679c\u5f3a\uff08\u9547\u75db\u4f5c\u7528\u7ea6\u4e3a\u5417\u5561\u768480~100\u500d\uff09\uff0c\u53d1\u751f\u4f5c\u7528\u7684\u901f\u5ea6\u5feb\uff0c\u6301\u7eed\u65f6\u95f4\u77ed\uff0c\u4e34\u5e8a\u4e0a\u53ef\u4ee5\u4e0e\u9ebb\u9189\u836f\u5408\u7528\u4f5c\u4e3a\u8f85\u52a9\u9ebb\u9189\u7528\u836f\uff0c\u4e5f\u5e38\u5e38\u5236\u5907\u4e3a\u7ecf\u76ae\u7ed9\u836f\u7cfb\u7edf\u3002\u8d34\u6577\u4e8e\u76ae\u80a4\u540e\uff0c\u5c31\u53ef\u4ee5\u57283~7\u5929\u5185\u4ee5\u6052\u5b9a\u7684\u901f\u7387\u91ca\u653e\u836f\u7269\u91cc\uff0c\u975e\u5e38\u65b9\u4fbf\u4f7f\u7528\uff0c\u9002\u5408\u7528\u4e8e\u624b\u672f\u540e\u548c\u4e00\u4e9b\u91cd\u5ea6\u75bc\u75db\u60a3\u8005\u7684\u9547\u75db\u6cbb\u7597\u3002\u82ac\u592a\u5c3c\u8d34\u5242\u81ea90\u5e74\u4ee3\u4e0a\u5e02\u4ee5\u6765\uff0c\u6709\u7740\u826f\u597d\u7684\u4e34\u5e8a\u5e94\u7528\uff0c\u76ee\u524d\u5df2\u7ecf\u662f\u4e00\u4e2a\u4ef7\u503c\u5de8\u5927\u7684\u836f\u7269\u54c1\u7c7b\u3002\u56fe\u7247\u6765\u6e90\uff1asohu.com & mylan.ca\u82ac\u592a\u5c3c\uff0c\u662f\u5929\u4f7f\u8fd8\u662f\u9b54\u9b3c\uff1f\u82ac\u592a\u5c3c\u7b49\u963f\u7247\u7c7b\u836f\u7269\u5728\u53d7\u63a7\u5236\u7684\u6761\u4ef6\u4e0b\u5408\u7406\u4f7f\u7528\uff0c\u53ef\u4ee5\u7f13\u89e3\u60a3\u8005\u7684\u75db\u82e6\uff0c\u4e3a\u533b\u7597\u4e8b\u4e1a\u5e26\u6765\u53ef\u89c2\u7684\u76ca\u5904\u3002\u800c\u5728\u5931\u63a7\u65f6\uff0c\u5219\u4f1a\u5c55\u9732\u51fa\u72f0\u72de\u7684\u4e00\u9762\uff0c\u7ed9\u6574\u4e2a\u793e\u4f1a\u9020\u6210\u5de8\u5927\u7684\u8d1f\u9762\u5f71\u54cd\u3002\u6bd4\u5982\u8fd1\u5e74\u6765\uff0c\u82ac\u592a\u5c3c\u3001\u5361\u82ac\u592a\u5c3c\u7b49\u5728\u7f8e\u56fd\u3001\u52a0\u62ff\u5927\u7b49\u56fd\u88ab\u975e\u6cd5\u6cdb\u6ee5\u7528\uff0c\u5bfc\u81f4\u6570\u4e07\u4eba\u6b7b\u4ea1\u3002\u8fd1\u5e74\u6765\uff0c\u975e\u5904\u65b9\u963f\u7247\u7c7b\u836f\u7269\uff08\u5982\u82ac\u592a\u5c3c\u3001\u5361\u82ac\u592a\u5c3c\u7b49\uff09\u6ee5\u7528\u5728\u7f8e\u56fd\u5bfc\u81f4\u7684\u6b7b\u4ea1\u4eba\u6570\u6025\u5267\u4e0a\u5347\u3002\u56fe\u7247\u6765\u6e90\uff1aCenters for Disease Control and Prevention\u4e3a\u4ec0\u4e48\u82ac\u592a\u5c3c\u53ca\u5176\u7c7b\u4f3c\u7269\u4f1a\u88ab\u6ee5\u7528\uff0c\u5e76\u4e14\u5e26\u6765\u5982\u6b64\u5927\u7684\u5371\u5bb3\uff1f\u6bd4\u5bf9\u4e00\u4e0b\u5316\u5408\u7269\u7684\u7ed3\u6784\u5f0f\u6211\u4eec\u53ef\u4ee5\u53d1\u73b0\uff0c\u82ac\u592a\u5c3c\u7684\u5316\u5b66\u7ed3\u6784\u660e\u663e\u6bd4\u5417\u5561\u7b80\u6d01\u3002\u4e8b\u5b9e\u4e0a\uff0c\u73b0\u5728\u4e34\u5e8a\u4e0a\u4f7f\u7528\u7684\u5417\u5561\u8fd8\u9700\u8981\u4ece\u7f42\u7c9f\u4e2d\u63d0\u53d6\uff0c\u800c\u82ac\u592a\u5c3c\uff0c\u662f\u901a\u8fc7\u4eba\u5de5\u5168\u5408\u6210\u6765\u83b7\u5f97\u7684\uff08\u968f\u4fbf\u7ffb\u7ffb\u5404\u79cd\u5404\u79cd\u6587\u732e\u6570\u636e\u5e93\uff0c\u5c31\u80fd\u627e\u5230\u5927\u91cf\u7684\u7814\u7a76\u62a5\u9053\uff09\uff0c\u5b83\u7684\u5236\u53d6\u65b9\u5f0f\u663e\u7136\u5bb9\u6613\u4e86\u5f88\u591a\u3002\u5ec9\u4ef7\u3001\u6709\u6548\u5242\u91cf\u4f4e\u3001\u5bb9\u6613\u83b7\u5f97\uff0c\u5f53\u8fd9\u4e9b\u8bcd\u653e\u5728\u4e00\u79cd\u5177\u6709\u6210\u763e\u6027\u548c\u8010\u53d7\u6027\u7684\u963f\u7247\u7c7b\u836f\u7269\u524d\u9762\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u5b83\u6709\u4e86\u6cdb\u6ee5\u7684\u98ce\u9669\u3002\u4e34\u5e8a\u5e94\u7528\u65f6\uff0c\u533b\u62a4\u4eba\u5458\u5c1a\u53ef\u6839\u636e\u7cbe\u786e\u7684\u8ba1\u7b97\u548c\u6089\u5fc3\u770b\u987e\u6765\u4fdd\u8bc1\u60a3\u8005\u7684\u7528\u836f\u5b89\u5168\uff0c\u4f46\u5f53\u5b83\u6765\u5230\u8857\u5934\uff0c\u763e\u541b\u5b50\u4eec\u4e00\u6765\u6025\u5207\uff0c\u4e8c\u6765\u628a\u63e1\u4e0d\u597d\u8ba1\u91cf\uff0c\u6444\u5165\u836f\u7269\u540e\uff0c\u5c31\u975e\u5e38\u5bb9\u6613\u51fa\u73b0\u6025\u6027\u3001\u8fc5\u731b\u7684\u8fc7\u91cf\u4f7f\u7528\u3002\u8840\u6db2\u4e2d\u7684\u836f\u7269\u6d53\u5ea6\u6025\u5267\u5347\u9ad8\uff0c\u8d85\u8fc7\u4e86\u5b89\u5168\u5242\u91cf\u4e0a\u7ebf\uff0c\u5bfc\u81f4\u5404\u79cd\u4e25\u91cd\u7684\u526f\u4f5c\u7528\uff0c\u751a\u81f3\u6b7b\u4ea1\u3002\u836f\u7269\u5316\u5b66\u5bb6\u4eec\u81f4\u529b\u4e8e\u9632\u6b62\u836f\u7269\u6ee5\u7528\u3002\u56fe\u7247\u6765\u6e90\uff1aUnsplash\u5728\u9e26\u7247\u6216\u6d77\u6d1b\u56e0\u6cdb\u6ee5\u7684\u65f6\u4ee3\uff0c\u8fd9\u7c7b\u6bd2\u54c1\u9700\u8981\u79cd\u690d\u7f42\u7c9f\u624d\u80fd\u5236\u53d6\uff0c\u5c1a\u53ef\u901a\u8fc7\u5bf9\u79cd\u690d\u7684\u4e25\u683c\u7ba1\u7406\u6765\u8fbe\u5230\u63a7\u5236\u7684\u76ee\u7684\u3002\u7136\u800c\uff0c\u5f53\u4eba\u4eec\u7684\u8ba4\u8bc6\u5df2\u7ecf\u6df1\u5165\u5230\u82ac\u592a\u5c3c\u65f6\uff0c\u5c31\u4e0d\u80fd\u4ec5\u4ec5\u4f7f\u7528\u8fd9\u79cd\u7b80\u5355\u76f4\u767d\u7684\u65b9\u5f0f\u4e86\u3002\u4e00\u65b9\u9762\u82ac\u592a\u5c3c\u7c7b\u7269\u8d28\u7684\u7ed3\u6784\u5f97\u5230\u4e86\u6781\u5927\u7b80\u5316\uff0c\u53ef\u4ee5\u5b8c\u6574\u5730\u5728\u5b9e\u9a8c\u5ba4\u4e2d\u5236\u4f5c\uff0c\u4e0d\u987b\u4f9d\u8d56\u7f42\u7c9f\u79cd\u690d\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u6b63\u5982\u524d\u6587\u6240\u8bf4\uff0c\u57fa\u4e8e\u9501\u94a5\u5047\u8bf4\u548c\u6784\u6548\u5173\u7cfb\u7814\u7a76\uff0c\u5982\u679c\u4ec5\u4ec5\u5c06\u7279\u5b9a\u7684\u51e0\u79cd\u5316\u5408\u7269\u5217\u5165\u7ba1\u7406\u540d\u5f55\uff0c\u201c\u61c2\u884c\u201d\u7684\u4eba\u5b8c\u5168\u53ef\u4ee5\u901a\u8fc7\u52a0\u51cf\u4e00\u4e9b\u7b80\u5355\u7684\u5c0f\u7ed3\u6784\u6765\u5236\u9020\u51fa\u201c\u4e0d\u8fdd\u6cd5\u7684\u6bd2\u54c1\u201d\uff0c\u4f7f\u6267\u6cd5\u8005\u9677\u5165\u5c34\u5c2c\u7684\u5883\u5730\u3002\u800c\u8fd9\u6b21\u5373\u5c06\u5b9e\u884c\u7684\u201c\u6574\u7c7b\u5217\u7ba1\u201d\u6b63\u662f\u4e3a\u4e86\u9488\u5bf9\u8fd9\u79cd\u60c5\u51b5\uff0c\u4f5c\u51fa\u7684\u65b0\u5c1d\u8bd5\u2014\u2014\u5c06\u7ed3\u6784\u548c\u82ac\u592a\u5c3c\u7c7b\u4f3c\u7684\u5316\u5408\u7269\u5168\u90e8\u5217\u5165\u7ba1\u5236\u8303\u56f4\uff0c\u800c\u4e0d\u518d\u662f\u53d1\u73b0\u4e00\u79cd\u7ba1\u5236\u4e00\u79cd\u3002\u90a3\u4e9b\u4e4b\u524d\u4e00\u76f4\u94bb\u6cd5\u5f8b\u7a7a\u5b50\u7684\u4eba\u4e5f\u5c06\u53d7\u5230\u4e25\u60e9\u3002\u201c\u6574\u7c7b\u5217\u7ba1\u201d\u80fd\u591f\u907f\u514d\u6267\u6cd5\u8005\u9677\u5165\u5c34\u5c2c\u5883\u5730\u3002\u56fe\u7247\u6765\u6e90\uff1aUnsplash\u51e0\u767e\u5e74\u6765\uff0c\u4eba\u4eec\u4e0e\u8fd9\u7c7b\u836f\u7269\u7684\u5173\u7cfb\u4e00\u76f4\u662f\u9b54\u9ad8\u4e00\u5c3a\uff0c\u9053\u9ad8\u4e00\u4e08\u3002\u4eba\u4eec\u60f3\u8981\u9a7e\u9a6d\u5b83\u7684\u5f3a\u5927\u836f\u6548\uff0c\u53c8\u8981\u7ede\u5c3d\u8111\u6c41\u907f\u514d\u5b83\u53d8\u4e3a\u9b54\u9b3c\u4e3a\u7978\u4eba\u95f4\u3002\u65e2\u8981\u901a\u8fc7\u79d1\u5b66\u624b\u6bb5\u51cf\u5c11\u6ee5\u7528\uff0c\u53c8\u8981\u653f\u7b56\u548c\u7ba1\u7406\u7b49\u7b49\u5404\u65b9\u9762\u7684\u590d\u6742\u914d\u5408\u3002\u4ece\u53e4\u4ee3\u7684\u9e26\u7247\u5230\u8fd1\u4ee3\u7684\u5417\u5561\uff0c\u518d\u5230\u66f4\u8fd1\u7684\u82ac\u592a\u5c3c\u2014\u2014\u65e0\u4e00\u4e0d\u9075\u5faa\u8fd9\u4e2a\u89c4\u5f8b\u3002\u6bd5\u7adf\uff0c\u6740\u4eba\u7684\u4ece\u6765\u4e0d\u662f\u5200\uff0c\u800c\u662f\u6301\u5200\u7684\u4eba\u3002\uff08\u7f16\u8f91\uff1aYuki\uff09\u53c2\u8003\u6587\u732e\uff1a\u90d1\u864e \u4e3b\u7f16. \u836f\u7269\u5316\u5b66[M]. \u7b2c\u516d\u7248. \u5317\u4eac\uff1a\u4eba\u6c11\u536b\u751f\u51fa\u7248\u793e, 2008\u4ec9\u6587\u5347, \u674e\u5b89\u826f \u4e3b\u7f16. \u836f\u7269\u5316\u5b66[M]. \u7b2c\u4e8c\u7248. \u5317\u4eac\uff1a\u9ad8\u7b49\u6559\u80b2\u51fa\u7248\u793e, 2005Majella E. Lane, The Transdermal Delivery of Dentanyl [J], European Journal of Pharmaceutics and Biopharmaceutics, 2013, 84: 449-455\u4f5c\u8005\u540d\u7247", "title": "\u60f3\u9760\u5236\u6bd2\u81f4\u5bcc\uff1f\u4e94\u4e00\u7684\u8fd9\u9879\u65b0\u89c4\u5b9a\uff0c\u8981\u8ba9\u4e00\u6279\u4e0d\u6cd5\u5206\u5b50\u51c9\u51c9\u4e86", "ukey_author": null, "video_content": ""}, {"author": {"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "url": ""}, "authors": [{"avatar": {"large": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/160/h/160", "normal": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/48/h/48", "small": "https://3-im.guokr.com/ZPvCKBnqIqV3nrXl2jr7nYBLvwI-IfsEJ1SYwK5LjCz0AQAA9AEAAEpQ.jpg?imageView2/1/w/24/h/24"}, "introduction": "", "nickname": "\u6211\u662f\u79d1\u5b66\u5bb6iScientist", "ukey": null, "url": ""}], "baidu_editor": {}, "category": {"articles_count": 188, "date_created": "2018-12-04T18:07:22.219425+08:00", "id": 4, "key": "health", "name": "\u5065\u5eb7", "sort_score": 3}, "channels": [], "content": "<p><!--StartFragment--><strong><span style=\"color:#696969\"><span style=\"font-size:14px\">|&middot;&nbsp;\u672c\u6587\u6765\u81ea&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;&middot;|</span></span></strong><!--EndFragment--></p>\r\n\r\n<p class=\"editors-note\">4\u670827\u65e5&ldquo;\u6211\u662f\u79d1\u5b66\u5bb6&rdquo;\u5c06\u4e3e\u529e\u7b2c\u5341\u671f\u6f14\u8bb2\u6d3b\u52a8\u300a\u5bf9\u8c23\u8a00\u8bf4&ldquo;\u4e0d&rdquo;\u300b</p>\r\n\r\n<p><img alt=\"\" src=\"https://3-im.guokr.com/ioaGAYqDlzCju_9HLORrLFbQIGkUBtJ9KFrlJTGvALU4BAAA9wUAAEpQ.jpg\" style=\"height:905px; width:640px\" /></p>\r\n\r\n<h2 style=\"text-align:center\">\u672c\u671f\u6f14\u8bb2\u5609\u5bbe</h2>\r\n\r\n<p style=\"text-align:center\">\u4f55\u4e3d</p>\r\n\r\n<p style=\"text-align:center\">\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7814\u7a76\u5458</p>\r\n\r\n<p style=\"text-align:center\">\u751f\u916e\u996e\u98df\u51cf\u80a5\uff1f\u78b1\u6027\u98df\u7269\u517b\u751f\uff1f</p>\r\n\r\n<p style=\"text-align:center\">&hellip;&hellip;\u522b\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u9a97\u4e86\uff01</p>\r\n\r\n<p><img alt=\"\" src=\"https://2-im.guokr.com/w-kB3Tfc3YzXK_hbCd5KG2enYyuHLIjv1ePzT4RJrzo4BAAA9wUAAEpQ.jpg\" style=\"height:905px; width:640px\" /></p>\r\n\r\n<p>\u4f55\u4e3d\u91c7\u8bbf\u89c6\u9891\uff1a</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"498\" scrolling=\"no\" src=\"https://v.qq.com/txp/iframe/player.html?vid=t0863cj0x17\" width=\"640\"></iframe></p>\r\n\r\n<p>\u4fd7\u8bdd\u8bf4\uff0c\u6c11\u4ee5\u98df\u4e3a\u5929\u3002\u53c8\u8bf4\uff0c\u5f00\u95e8\u4e03\u4ef6\u4e8b\uff0c\u67f4\u7c73\u6cb9\u76d0\u9171\u918b\u8336\u3002</p>\r\n\r\n<p>\u82e5\u5403\u4e0d\u5bf9\uff0c\u95ee\u9898\u4e5f\u5927\u3002</p>\r\n\r\n<p>&ldquo;\u5728\u5bfc\u81f4\u6162\u75c5\u7684\u5371\u9669\u56e0\u7d20\u5f53\u4e2d\uff0c&lsquo;\u81b3\u98df\u56e0\u7d20&rsquo;\u975e\u5e38\u91cd\u8981\uff0c\u4e0d\u5065\u5eb7\u7684\u751f\u6d3b\u65b9\u5f0f\uff0c\u5c24\u5176\u662f\u81b3\u98df\u4e0d\u5747\u8861\uff0c\u662f\u6162\u75c5\u7684\u4e3b\u8981\u4fc3\u8fdb\u56e0\u7d20\u3002&rdquo;</p>\r\n\r\n<p>\u8bf4\u8fd9\u8bdd\u7684\u4eba\uff0c\u53eb\u4f55\u4e3d\uff0c\u662f\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7814\u7a76\u5458\u3002\u5927\u5b66\u6bd5\u4e1a\u540e\uff0c\u5979\u4ece\u4e8b\u4e867\u5e74\u4e34\u5e8a\u5de5\u4f5c\uff0c\u7136\u540e\u662f23\u5e74\u7684\u8425\u517b\u79d1\u7814\u3002&ldquo;\u6700\u8fd1\u51e0\u5e74\u6211\u7684\u7814\u7a76\u65b9\u5411\u4e3b\u8981\u96c6\u4e2d\u5728\u8425\u517b\u548c\u6162\u6027\u975e\u4f20\u67d3\u75c5\u7684\u9884\u9632\u548c\u63a7\u5236\uff0c\u4e3b\u8981\u7814\u7a76\u9aa8\u8d28\u758f\u677e\u3001\u7cd6\u5c3f\u75c5\u3001\u9ad8\u8840\u538b\u3001\u80a5\u80d6\u8ddf\u8425\u517b\u7684\u5173\u7cfb\u3002&rdquo;</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://3-im.guokr.com/M3zQewCa_xhNiLXJeIwL9eR0IPhvinjCdW0CuHYsqlmAAgAAhgEAAEpQ.jpg\" style=\"height:390px; width:640px\" />\u996e\u98df\uff0c\u5e73\u8861\u624d\u662f\u53ef\u53d6\u4e4b\u6cd5\u3002\u56fe\u7247\u6765\u6e90\uff1a\u56fe\u866b\u521b\u610f</p>\r\n\r\n<p>&ldquo;\u5e73\u8861\u81b3\u98df&rdquo;\u662f\u5979\u548c\u540c\u4e8b\u5e0c\u671b\u5ba3\u4f20\u7684\u89c2\u70b9\u3002\u53ef\u6709\u65f6\u5019\uff0c\u771f\u76f8\u8dd1\u4e0d\u8fc7\u8c23\u8a00\u3002&ldquo;\u5c24\u5176\u662f\uff0c\u4e00\u4e9b\u6162\u6027\u75c5\u4eba\uff0c\u4ed6\u60f3\u5065\u5eb7\u8d77\u6765\uff0c\u60f3\u6d3b\u5f97\u66f4\u4e45\u4e00\u70b9\uff0c\u5c31\u4f1a\u76f2\u76ee\u5730\u76f8\u4fe1\u7f51\u7edc\u4e0a\u4e00\u4e9b&lsquo;\u8c8c\u4f3c\u5f88\u5173\u5fc3\u4f60\uff0c\u7ed9\u4f60\u4e00\u4e9b\u77e5\u8bc6\u4f46\u5b9e\u9645\u4e0a\u662f\u8c23\u8a00&rsquo;\u7684\u4e1c\u897f\uff0c\u4f46\u5b9e\u9645\u4e0a\uff0c\u5bf9\u75be\u75c5\u7684\u6108\u540e\u8d77\u5230\u4e00\u4e2a\u4e0d\u597d\u7684\u4f5c\u7528\u3002&rdquo;</p>\r\n\r\n<p>&ldquo;\u6bd4\u5982\uff0c&lsquo;\u751f\u916e\u996e\u98df&rsquo;\u5728\u6700\u8fd1\u51e0\u5e74\u7279\u522b\u706b&mdash;&mdash;\u6211\u4eec\u4e2d\u56fd\u4eba\u73b0\u5728\u8d85\u91cd\u548c\u80a5\u80d6\u7684\u6bd4\u4f8b\u5df2\u7ecf\u8fbe\u523042%\uff0c\u4eba\u4eec\u60f3\u5065\u5eb7\u957f\u5bff\uff0c\u9700\u8981\u63a7\u5236\u4f53\u91cd\u3002&rdquo;\u5728\u8fd9\u79cd\u5f62\u52bf\u4e0b\uff0c&ldquo;\u53f7\u79f0\u4f4e\u78b3\u6c34\u3001\u53ef\u4ee5\u63a7\u5236\u4f53\u91cd&rdquo;\u7684\u751f\u916e\u996e\u98df\u6982\u5ff5\u5c31\u8fce\u5408\u4e86\u5f88\u591a\u4eba\u7684\u9700\u6c42\uff0c\u4e5f\u6210\u4e3a\u4e00\u4e9b\u5546\u4eba\u7684\u655b\u8d22\u5de5\u5177\u3002</p>\r\n\r\n<p>\u4f46\u5728\u4f55\u4e3d\u770b\u6765\uff0c&ldquo;\u751f\u916e\u996e\u98df&rdquo;\u8fd9\u79cd\u505a\u6cd5\u4e0d\u4f46\u65e0\u52a9\u4e8e\u5065\u5eb7\uff0c\u53cd\u800c\u4f1a\u5e26\u6765\u5f88\u5927\u7684\u5065\u5eb7\u9690\u60a3\u3002</p>\r\n\r\n<p class=\"img-caption\"><img alt=\"\" src=\"https://1-im.guokr.com/Ko1vFJwBqz_9mVkFK4GS4ZPF7IKlr781vIugbk525tEAAgAAVAEAAEpQ.jpg\" style=\"height:340px; width:512px\" />&ldquo;\u751f\u916e\u996e\u98df&rdquo;\u6307\u7684\u662f\u4e00\u79cd\u6bd4\u8f83\u6781\u7aef\u7684\u9ad8\u8102\u3001\u4f4e\u78b3\u6c34\u5316\u5408\u7269\u996e\u98df\u3002\u56fe\u7247\u6765\u6e90\uff1aPixabay</p>\r\n\r\n<p>&ldquo;\u751f\u916e\u996e\u98df\uff0c\u8bf4\u767d\u4e86\u5c31\u662f\uff1a\u4f60\u7684\u4e00\u65e5\u4e09\u9910\u5f53\u4e2d\uff0c\u63d0\u4f9b\u80fd\u91cf\u7684\u4e0d\u662f\u78b3\u6c34\u5316\u5408\u7269\u4e3a\u4e3b\uff0c\u800c\u662f\u7528\u8102\u80aa\u548c\u86cb\u767d\u8d28\u6765\u4f9b\u80fd\uff0c\u90a3\u5728\u8fd9\u4e2a\u4f9b\u80fd\u7684\u8fc7\u7a0b\u4e2d\u53ef\u80fd\u4f1a\u9020\u6210\u4f60\u8eab\u4f53\u5185\u7684\u916e\u4f53\u589e\u52a0\uff0c\u901a\u8fc7\u916e\u4f53\u7684\u5206\u89e3\u6765\u4f9b\u80fd\uff0c\u8fdb\u800c\u4e5f\u4f1a\u9020\u6210\u5c3f\u916e\u4f53\u589e\u52a0\uff0c\u957f\u6b64\u4ee5\u5f80\uff0c\u5bf9\u4eba\u4f53\u8fd8\u662f\u5b58\u5728\u4e00\u5b9a\u7684\u526f\u4f5c\u7528\u7684&mdash;&mdash;\u56e0\u4e3a\uff0c\u73b0\u5728\u6ca1\u6709\u4efb\u4f55\u7684\u6d41\u884c\u75c5\u5b66\u8bc1\u636e\u8868\u660e&lsquo;\u751f\u916e\u996e\u98df&rsquo;\u53ef\u4ee5\u8d77\u5230\u957f\u671f\u63a7\u5236\u4f53\u91cd\u3001\u957f\u671f\u964d\u4f4e\u6162\u6027\u75c5\u6bd4\u5982\u7cd6\u5c3f\u75c5\u3001\u9ad8\u8840\u538b\u7684\u60a3\u75c5\u7387\u6216\u8005\u6b7b\u4ea1\u7387\u3002&rdquo;</p>\r\n\r\n<p>\u6700\u8fd1\u5341\u591a\u5e74\uff0c\u9664\u4e86\u79d1\u7814\u5de5\u4f5c\uff0c\u4f55\u4e3d\u82b1\u4e86\u5f88\u591a\u4e1a\u4f59\u65f6\u95f4\u6295\u5165\u5230\u79d1\u666e\u3002\u628a\u4e00\u4e9b\u6666\u6da9\u96be\u61c2\u7684\u77e5\u8bc6\uff0c\u7ffb\u8bd1\u6210\u8001\u767e\u59d3\u7231\u542c\u7684\u767d\u8bdd\uff0c\u8bf4\u7ed9\u5927\u5bb6\u542c\uff0c\u5199\u7ed9\u5927\u5bb6\u770b\u3002</p>\r\n\r\n<p>&ldquo;\u6211\u89c9\u5f97\uff0c\u6700\u91cd\u8981\u7684\u5c31\u662f\u8981\u62b5\u5236\u8fd9\u4e9b\u8c23\u8a00\uff0c\u4e0d\u542c\u4e0d\u770b\u4e0d\u4f20\uff0c\u66f4\u4e0d\u8981\u53bb\u76f8\u4fe1\uff0c\u5982\u679c\u6211\u4eec\u7684\u5065\u5eb7\u7d20\u517b\u90fd\u63d0\u9ad8\u4e86\uff0c\u4e0d\u4fe1\u8fd9\u4e9b\u4e1c\u897f\uff0c\u7528\u79d1\u5b66\u7684\u5065\u5eb7\u7d20\u517b\u6765\u6b66\u88c5\u81ea\u5df1\uff0c\u6307\u5bfc\u81ea\u5df1\u7684\u5065\u5eb7\u751f\u6d3b\uff0c\u751f\u547d\u7684\u7ed3\u5c40\u4e5f\u4f1a\u662f\u4e0d\u4e00\u6837\u7684\u3002&rdquo;</p>\r\n\r\n<p>4\u670827\u65e5\uff0c\u6b22\u8fce\u676577\u5267\u573a\uff0c\u542c\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7814\u7a76\u5458\u4f55\u4e3d\u8001\u5e08\u5206\u4eab\u66f4\u591a\u5173\u4e8e\u8425\u517b\u7684\u8bdd\u9898\uff0c\u4ee5\u53ca\u5982\u4f55\u7834\u89e3\u8425\u517b\u548c\u98df\u7269\u65b9\u9762\u7684\u8c23\u8a00\u3002</p>\r\n\r\n<p>\u626b\u63cf\u4e8c\u7ef4\u7801\u7acb\u523b\u62a5\u540d</p>\r\n\r\n<p><img alt=\"\" src=\"https://1-im.guokr.com/udsoIpfyUNhnoNXZhEvzZOTOnvrTbPl0LGIB1XNZhyUsAgAAKQIAAEpQ.jpg\" style=\"height:119px; width:120px\" /></p>", "date_created": "2019-04-24T16:13:55.140898+08:00", "date_modified": "2019-04-24T16:24:10.633301+08:00", "date_published": "2019-04-24T16:23:29+08:00", "id": 451875, "image": "", "is_editor_recommend": false, "is_liyan_article": false, "is_published": true, "is_replyable": true, "minisite_key": "health", "replies_count": 14, "small_image": "https://1-im.guokr.com/wFlSBVwHARPpj-2Nm8V5cOSRwXa0r5b-FZzVNbdviSaABwAAOAQAAEpQ.jpg", "subject": {"articles_count": 615, "date_created": "2014-05-23T16:22:09.282129+08:00", "key": "food", "minisite_key": "health", "name": "\u98df\u7269", "sort_score": 6, "subject_type": "normal"}, "subject_key": "food", "summary": "\u5982\u4f55\u5206\u8fa8\u548c\u7834\u89e3\u98df\u7269\u76f8\u5173\u7684\u8c23\u8a00\uff0c\u8ba9\u4f55\u4e3d\u8001\u5e08\u8bb2\u7ed9\u4f60\u542c\u3002", "text": "|\u00b7\u00a0\u672c\u6587\u6765\u81ea\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u00b7|4\u670827\u65e5\u201c\u6211\u662f\u79d1\u5b66\u5bb6\u201d\u5c06\u4e3e\u529e\u7b2c\u5341\u671f\u6f14\u8bb2\u6d3b\u52a8\u300a\u5bf9\u8c23\u8a00\u8bf4\u201c\u4e0d\u201d\u300b\u672c\u671f\u6f14\u8bb2\u5609\u5bbe\u4f55\u4e3d\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7814\u7a76\u5458\u751f\u916e\u996e\u98df\u51cf\u80a5\uff1f\u78b1\u6027\u98df\u7269\u517b\u751f\uff1f\u2026\u2026\u522b\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u9a97\u4e86\uff01\u4f55\u4e3d\u91c7\u8bbf\u89c6\u9891\uff1a\u4fd7\u8bdd\u8bf4\uff0c\u6c11\u4ee5\u98df\u4e3a\u5929\u3002\u53c8\u8bf4\uff0c\u5f00\u95e8\u4e03\u4ef6\u4e8b\uff0c\u67f4\u7c73\u6cb9\u76d0\u9171\u918b\u8336\u3002\u82e5\u5403\u4e0d\u5bf9\uff0c\u95ee\u9898\u4e5f\u5927\u3002\u201c\u5728\u5bfc\u81f4\u6162\u75c5\u7684\u5371\u9669\u56e0\u7d20\u5f53\u4e2d\uff0c\u2018\u81b3\u98df\u56e0\u7d20\u2019\u975e\u5e38\u91cd\u8981\uff0c\u4e0d\u5065\u5eb7\u7684\u751f\u6d3b\u65b9\u5f0f\uff0c\u5c24\u5176\u662f\u81b3\u98df\u4e0d\u5747\u8861\uff0c\u662f\u6162\u75c5\u7684\u4e3b\u8981\u4fc3\u8fdb\u56e0\u7d20\u3002\u201d\u8bf4\u8fd9\u8bdd\u7684\u4eba\uff0c\u53eb\u4f55\u4e3d\uff0c\u662f\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7814\u7a76\u5458\u3002\u5927\u5b66\u6bd5\u4e1a\u540e\uff0c\u5979\u4ece\u4e8b\u4e867\u5e74\u4e34\u5e8a\u5de5\u4f5c\uff0c\u7136\u540e\u662f23\u5e74\u7684\u8425\u517b\u79d1\u7814\u3002\u201c\u6700\u8fd1\u51e0\u5e74\u6211\u7684\u7814\u7a76\u65b9\u5411\u4e3b\u8981\u96c6\u4e2d\u5728\u8425\u517b\u548c\u6162\u6027\u975e\u4f20\u67d3\u75c5\u7684\u9884\u9632\u548c\u63a7\u5236\uff0c\u4e3b\u8981\u7814\u7a76\u9aa8\u8d28\u758f\u677e\u3001\u7cd6\u5c3f\u75c5\u3001\u9ad8\u8840\u538b\u3001\u80a5\u80d6\u8ddf\u8425\u517b\u7684\u5173\u7cfb\u3002\u201d\u996e\u98df\uff0c\u5e73\u8861\u624d\u662f\u53ef\u53d6\u4e4b\u6cd5\u3002\u56fe\u7247\u6765\u6e90\uff1a\u56fe\u866b\u521b\u610f\u201c\u5e73\u8861\u81b3\u98df\u201d\u662f\u5979\u548c\u540c\u4e8b\u5e0c\u671b\u5ba3\u4f20\u7684\u89c2\u70b9\u3002\u53ef\u6709\u65f6\u5019\uff0c\u771f\u76f8\u8dd1\u4e0d\u8fc7\u8c23\u8a00\u3002\u201c\u5c24\u5176\u662f\uff0c\u4e00\u4e9b\u6162\u6027\u75c5\u4eba\uff0c\u4ed6\u60f3\u5065\u5eb7\u8d77\u6765\uff0c\u60f3\u6d3b\u5f97\u66f4\u4e45\u4e00\u70b9\uff0c\u5c31\u4f1a\u76f2\u76ee\u5730\u76f8\u4fe1\u7f51\u7edc\u4e0a\u4e00\u4e9b\u2018\u8c8c\u4f3c\u5f88\u5173\u5fc3\u4f60\uff0c\u7ed9\u4f60\u4e00\u4e9b\u77e5\u8bc6\u4f46\u5b9e\u9645\u4e0a\u662f\u8c23\u8a00\u2019\u7684\u4e1c\u897f\uff0c\u4f46\u5b9e\u9645\u4e0a\uff0c\u5bf9\u75be\u75c5\u7684\u6108\u540e\u8d77\u5230\u4e00\u4e2a\u4e0d\u597d\u7684\u4f5c\u7528\u3002\u201d\u201c\u6bd4\u5982\uff0c\u2018\u751f\u916e\u996e\u98df\u2019\u5728\u6700\u8fd1\u51e0\u5e74\u7279\u522b\u706b\u2014\u2014\u6211\u4eec\u4e2d\u56fd\u4eba\u73b0\u5728\u8d85\u91cd\u548c\u80a5\u80d6\u7684\u6bd4\u4f8b\u5df2\u7ecf\u8fbe\u523042%\uff0c\u4eba\u4eec\u60f3\u5065\u5eb7\u957f\u5bff\uff0c\u9700\u8981\u63a7\u5236\u4f53\u91cd\u3002\u201d\u5728\u8fd9\u79cd\u5f62\u52bf\u4e0b\uff0c\u201c\u53f7\u79f0\u4f4e\u78b3\u6c34\u3001\u53ef\u4ee5\u63a7\u5236\u4f53\u91cd\u201d\u7684\u751f\u916e\u996e\u98df\u6982\u5ff5\u5c31\u8fce\u5408\u4e86\u5f88\u591a\u4eba\u7684\u9700\u6c42\uff0c\u4e5f\u6210\u4e3a\u4e00\u4e9b\u5546\u4eba\u7684\u655b\u8d22\u5de5\u5177\u3002\u4f46\u5728\u4f55\u4e3d\u770b\u6765\uff0c\u201c\u751f\u916e\u996e\u98df\u201d\u8fd9\u79cd\u505a\u6cd5\u4e0d\u4f46\u65e0\u52a9\u4e8e\u5065\u5eb7\uff0c\u53cd\u800c\u4f1a\u5e26\u6765\u5f88\u5927\u7684\u5065\u5eb7\u9690\u60a3\u3002\u201c\u751f\u916e\u996e\u98df\u201d\u6307\u7684\u662f\u4e00\u79cd\u6bd4\u8f83\u6781\u7aef\u7684\u9ad8\u8102\u3001\u4f4e\u78b3\u6c34\u5316\u5408\u7269\u996e\u98df\u3002\u56fe\u7247\u6765\u6e90\uff1aPixabay\u201c\u751f\u916e\u996e\u98df\uff0c\u8bf4\u767d\u4e86\u5c31\u662f\uff1a\u4f60\u7684\u4e00\u65e5\u4e09\u9910\u5f53\u4e2d\uff0c\u63d0\u4f9b\u80fd\u91cf\u7684\u4e0d\u662f\u78b3\u6c34\u5316\u5408\u7269\u4e3a\u4e3b\uff0c\u800c\u662f\u7528\u8102\u80aa\u548c\u86cb\u767d\u8d28\u6765\u4f9b\u80fd\uff0c\u90a3\u5728\u8fd9\u4e2a\u4f9b\u80fd\u7684\u8fc7\u7a0b\u4e2d\u53ef\u80fd\u4f1a\u9020\u6210\u4f60\u8eab\u4f53\u5185\u7684\u916e\u4f53\u589e\u52a0\uff0c\u901a\u8fc7\u916e\u4f53\u7684\u5206\u89e3\u6765\u4f9b\u80fd\uff0c\u8fdb\u800c\u4e5f\u4f1a\u9020\u6210\u5c3f\u916e\u4f53\u589e\u52a0\uff0c\u957f\u6b64\u4ee5\u5f80\uff0c\u5bf9\u4eba\u4f53\u8fd8\u662f\u5b58\u5728\u4e00\u5b9a\u7684\u526f\u4f5c\u7528\u7684\u2014\u2014\u56e0\u4e3a\uff0c\u73b0\u5728\u6ca1\u6709\u4efb\u4f55\u7684\u6d41\u884c\u75c5\u5b66\u8bc1\u636e\u8868\u660e\u2018\u751f\u916e\u996e\u98df\u2019\u53ef\u4ee5\u8d77\u5230\u957f\u671f\u63a7\u5236\u4f53\u91cd\u3001\u957f\u671f\u964d\u4f4e\u6162\u6027\u75c5\u6bd4\u5982\u7cd6\u5c3f\u75c5\u3001\u9ad8\u8840\u538b\u7684\u60a3\u75c5\u7387\u6216\u8005\u6b7b\u4ea1\u7387\u3002\u201d\u6700\u8fd1\u5341\u591a\u5e74\uff0c\u9664\u4e86\u79d1\u7814\u5de5\u4f5c\uff0c\u4f55\u4e3d\u82b1\u4e86\u5f88\u591a\u4e1a\u4f59\u65f6\u95f4\u6295\u5165\u5230\u79d1\u666e\u3002\u628a\u4e00\u4e9b\u6666\u6da9\u96be\u61c2\u7684\u77e5\u8bc6\uff0c\u7ffb\u8bd1\u6210\u8001\u767e\u59d3\u7231\u542c\u7684\u767d\u8bdd\uff0c\u8bf4\u7ed9\u5927\u5bb6\u542c\uff0c\u5199\u7ed9\u5927\u5bb6\u770b\u3002\u201c\u6211\u89c9\u5f97\uff0c\u6700\u91cd\u8981\u7684\u5c31\u662f\u8981\u62b5\u5236\u8fd9\u4e9b\u8c23\u8a00\uff0c\u4e0d\u542c\u4e0d\u770b\u4e0d\u4f20\uff0c\u66f4\u4e0d\u8981\u53bb\u76f8\u4fe1\uff0c\u5982\u679c\u6211\u4eec\u7684\u5065\u5eb7\u7d20\u517b\u90fd\u63d0\u9ad8\u4e86\uff0c\u4e0d\u4fe1\u8fd9\u4e9b\u4e1c\u897f\uff0c\u7528\u79d1\u5b66\u7684\u5065\u5eb7\u7d20\u517b\u6765\u6b66\u88c5\u81ea\u5df1\uff0c\u6307\u5bfc\u81ea\u5df1\u7684\u5065\u5eb7\u751f\u6d3b\uff0c\u751f\u547d\u7684\u7ed3\u5c40\u4e5f\u4f1a\u662f\u4e0d\u4e00\u6837\u7684\u3002\u201d4\u670827\u65e5\uff0c\u6b22\u8fce\u676577\u5267\u573a\uff0c\u542c\u4e2d\u56fd\u75be\u75c5\u9884\u9632\u63a7\u5236\u4e2d\u5fc3\u8425\u517b\u4e0e\u5065\u5eb7\u6240\u7814\u7a76\u5458\u4f55\u4e3d\u8001\u5e08\u5206\u4eab\u66f4\u591a\u5173\u4e8e\u8425\u517b\u7684\u8bdd\u9898\uff0c\u4ee5\u53ca\u5982\u4f55\u7834\u89e3\u8425\u517b\u548c\u98df\u7269\u65b9\u9762\u7684\u8c23\u8a00\u3002\u626b\u63cf\u4e8c\u7ef4\u7801\u7acb\u523b\u62a5\u540d", "title": "\u4f55\u4e3d\uff1a\u751f\u916e\u996e\u98df\u51cf\u80a5\uff1f\u78b1\u6027\u98df\u7269\u517b\u751f\uff1f\u2026\u2026\u522b\u518d\u88ab\u8fd9\u4e9b\u8425\u517b\u8c23\u8a00\u9a97\u4e86\uff01", "ukey_author": null, "video_content": "<iframe frameborder=\"0\" src=\"https://v.qq.com/txp/iframe/player.html?vid=t0863cj0x17\" allowFullScreen=\"true\"></iframe>"}]

